INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'07667AE4BE6A79429995242E306C0A00', X'DF14076FFCF25A4584B53645FD968E00', X'DF14076FFCF25A4584B53645FD968E00', 8, NULL, NULL, 2, NULL, 0, 'Store in cool and dark place.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C7C60257EF551B4CA482516568F309BC', X'C97D012B9860214998FCF78AEB6E33DA', X'C97D012B9860214998FCF78AEB6E33DA', 0, NULL, NULL, 2, NULL, 0, 'Procedure: In a saucepan, soften the gelatin in the apple and lemon juices. To dissolve gelatin, bring to a full rolling boil and boil 2 minutes. Remove from heat. Stir in sweetener and food coloring, if desired. Fill jars, leaving 1/4 inch headspace. Wipe rims of jars with a dampened clean paper towel. Adjust lids. Do not process or freeze.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'416FBDB6452F72459A424F563B9232F7', X'C97D012B9860214998FCF78AEB6E33DA', X'C97D012B9860214998FCF78AEB6E33DA', 1, NULL, NULL, 2, NULL, 0, 'CAUTION Store in refrigerator and use within 4 weeks.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FD50DE72B12C7044B6E4C178CE3F99FF', X'C97D012B9860214998FCF78AEB6E33DA', X'C97D012B9860214998FCF78AEB6E33DA', 2, NULL, NULL, 2, NULL, 0, 'Optional: For spiced apple jelly, add 2 sticks of cinnamon and 4 whole cloves to mixture before boiling. Remove both spices before adding the sweetener and food coloring.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F6F554C4A664114E94EFE9FAFEC2367A', X'FD1A5B83569F5441963B0085E7671CE8', X'FD1A5B83569F5441963B0085E7671CE8', 0, NULL, NULL, 2, NULL, 0, 'Procedure: In a saucepan, soften the gelatin in the grape and lemon juices. Bring to a full rolling boil to dissolve gelatin. Boil 1 minute and remove from heat. Stir in sweetener. Fill hot jars quickly, leaving 1/4 inch headspace. Wipe rims of jars with a dampened clean paper towel. Adjust lids. Do not process or freeze.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'58AC61421CB22E4CB80D14E3E2040AF9', X'FD1A5B83569F5441963B0085E7671CE8', X'FD1A5B83569F5441963B0085E7671CE8', 1, NULL, NULL, 2, NULL, 0, 'CAUTION Store in refrigerator and use within 4 weeks.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9E0F735BC3C9A04B92E373B47E0C13C2', X'BD7E25ECC88C0942B4991913652556D7', X'BD7E25ECC88C0942B4991913652556D7', 0, NULL, NULL, 2, NULL, 0, 'Sweet cherries contain more pectin than sweet cherries, but will turn out best when made with additional pectin.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8909C2135635504887E6AE4C9F3790B6', X'BD7E25ECC88C0942B4991913652556D7', X'BD7E25ECC88C0942B4991913652556D7', 1, NULL, NULL, 2, NULL, 0, 'Remove stems and pits, smash or cut sour cherries into smaller pieces.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A97306D103DFB146999F67673B4405BC', X'BD7E25ECC88C0942B4991913652556D7', X'BD7E25ECC88C0942B4991913652556D7', 2, NULL, NULL, 2, NULL, 0, 'Combine sour cherries with Ball RealFruit Classic Pectin. Add  tsp butter or margarine to prevent foaming (optional). Bring mixture to a full rolling boil, stirring continuously.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C462F3C756B20047A5E4B222BB82329B', X'BD7E25ECC88C0942B4991913652556D7', X'BD7E25ECC88C0942B4991913652556D7', 3, NULL, NULL, 2, NULL, 0, 'Add sugar and stir to dissolve. Bring mixture to a full boil and boil for 1 minute, stirring continuously. Remove from heat and skim foam, if any.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FF5102C830A037409DACBA2AABDA8BCB', X'BD7E25ECC88C0942B4991913652556D7', X'BD7E25ECC88C0942B4991913652556D7', 4, NULL, NULL, 2, NULL, 0, 'Fill jam into glass jars and process for 10 minutes in a water bath canner.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'81F594D11222344D9DB3AB039EDD9E4E', X'BD7E25ECC88C0942B4991913652556D7', X'BD7E25ECC88C0942B4991913652556D7', 5, NULL, NULL, 2, NULL, 0, 'Remove jars and let stand for 12 hours.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5B4B4D2BDC9E404D9C7A32CBB77E5538', X'413A495CAB3619498502E64D9F2A9D68', X'413A495CAB3619498502E64D9F2A9D68', 0, NULL, NULL, 2, NULL, 0, 'Strawberry jam is the most popular jam in the world. It can be made from strawberries only or with other fruits like sour cherries or rhubarb. The texture and sweetness of the jam depends on the amount of sugar, the more sugar the thicker the jam.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E2B38D9ABA0C4D4CA5EDB5B05F32D81F', X'413A495CAB3619498502E64D9F2A9D68', X'413A495CAB3619498502E64D9F2A9D68', 1, NULL, NULL, 2, NULL, 0, 'Wash strawberries and remove the stems. Cut large strawberries in half, quarter the small ones.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'12DDBDEE7E38044F91168EE9B650842D', X'413A495CAB3619498502E64D9F2A9D68', X'413A495CAB3619498502E64D9F2A9D68', 2, NULL, NULL, 2, NULL, 0, 'Mix strawberries with sugar in a large bowl, cover and leave for 3 hours at room temperature.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8042FE61E741A64D8672853A54175293', X'413A495CAB3619498502E64D9F2A9D68', X'413A495CAB3619498502E64D9F2A9D68', 3, NULL, NULL, 2, NULL, 0, 'Place strawberries in a large pot, add lemon juice, cover and bring to a boil over medium heat.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A42FCBBE4F0DDE4783ECD5A104E24819', X'413A495CAB3619498502E64D9F2A9D68', X'413A495CAB3619498502E64D9F2A9D68', 4, NULL, NULL, 2, NULL, 0, 'Lower the heat, remove cover and cook for about 30 minutes stirring often. Discard foam from the top.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BC983904399D1E4CA14D784E7DE5808F', X'413A495CAB3619498502E64D9F2A9D68', X'413A495CAB3619498502E64D9F2A9D68', 5, NULL, NULL, 2, NULL, 0, 'Take the jam off the heat and perform the refrigerator test to see if it is ready.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7EA2905CEEE9EF449F415AF4ABB8B4E6', X'413A495CAB3619498502E64D9F2A9D68', X'413A495CAB3619498502E64D9F2A9D68', 6, NULL, NULL, 2, NULL, 0, 'Fill glass jars with hot jam to within  inch from the top. Remove air bubbles and wipe off the jar rim. Install and tighten the cover.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'79942DC7B3E8C048BC0C93830F47B417', X'413A495CAB3619498502E64D9F2A9D68', X'413A495CAB3619498502E64D9F2A9D68', 7, NULL, NULL, 2, NULL, 0, 'Place jars in a bath canner and bring water to boil. Cook for 10 minutes.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6769482A237C31438FEDB392E18A08BC', X'413A495CAB3619498502E64D9F2A9D68', X'413A495CAB3619498502E64D9F2A9D68', 8, NULL, NULL, 2, NULL, 0, 'Remove jars from the canner and leave undisturbed for 12 hours.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9534055CB47D194EB9AE726F34A9E0DE', X'413A495CAB3619498502E64D9F2A9D68', X'413A495CAB3619498502E64D9F2A9D68', 9, NULL, NULL, 2, NULL, 0, 'Store in cool and dark place.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7786F5B234926B4A9552824614384C9F', X'C7D251843CA0D7438409656C328740E8', X'C7D251843CA0D7438409656C328740E8', 0, NULL, NULL, 2, NULL, 0, 'Very simple and fast to make strawberry jam made using Ball RealFruit Classic Pectin.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BA8CC8482668694BA61BEE4A6E415810', X'C7D251843CA0D7438409656C328740E8', X'C7D251843CA0D7438409656C328740E8', 1, NULL, NULL, 2, NULL, 0, 'Remove caps and mash strawberries using a potato masher.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3E257DC2FEB64647BC27C8600A755308', X'C7D251843CA0D7438409656C328740E8', X'C7D251843CA0D7438409656C328740E8', 2, NULL, NULL, 2, NULL, 0, 'Mix strawberries, lemon juice and sugar in a bowl. Let stand for 10 minutes.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CAAC2187DE6C9F47A39276D0D3240DE7', X'C7D251843CA0D7438409656C328740E8', X'C7D251843CA0D7438409656C328740E8', 3, NULL, NULL, 2, NULL, 0, 'Add dry pectin to water, bring to a boil and boil for 1 minute continuing to stir.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7A3EE86B400DD243AD7832BE5F137AD5', X'C7D251843CA0D7438409656C328740E8', X'C7D251843CA0D7438409656C328740E8', 4, NULL, NULL, 2, NULL, 0, 'Pour pectin over strawberries and mix. Continue stirring for 3 minutes.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'144AEE24C6551B4794A8E1F623CB047F', X'C7D251843CA0D7438409656C328740E8', X'C7D251843CA0D7438409656C328740E8', 5, NULL, NULL, 2, NULL, 0, 'Fill jam into clean jars leaving  inch headspace. Tighten covers and let jam to set at room temperature for up to 24 hours.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6F267AFC9BAD8D459C32F7E46B6981F2', X'C7D251843CA0D7438409656C328740E8', X'C7D251843CA0D7438409656C328740E8', 6, NULL, NULL, 2, NULL, 0, 'Refrigerate up to 3 weeks or freeze up to 1 year.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'146CFB325213EC4093A7D4B52F4CC049', X'2DAA220823200B4290E5DB5297B8835A', X'2DAA220823200B4290E5DB5297B8835A', 0, NULL, NULL, 2, NULL, 0, 'Procedure: Wash and cut rhubarb into 1-inch pieces and blend or grind. Wash, stem, and crush strawberries, one layer at a time, in a saucepan. Place both fruits in a jelly bag or double layer of cheesecloth and gently squeeze out juice. Measure 3-1/2 cups of juice into a large saucepan. Add butter and sugar, thoroughly mixing into juice. Bring to a boil over high heat, stirring constantly. Immediately stir in pectin. Bring to a full rolling boil and boil hard 1 minute, stirring constantly. Remove from heat, quickly skim off foam, and fill sterile jars, leaving 1/4-inch headspace. Wipe rims of jars with a dampened clean paper towel. Adjust lids and process.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2D139537AFD78348B0199EEC11D712B6', X'90003249D4EB17469ED68E90781B65AB', X'90003249D4EB17469ED68E90781B65AB', 0, NULL, NULL, 2, NULL, 0, 'Sweet cherries are best made with added pectin and lemon juice as they contain very little natural pectin.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'08D16EA3FAAF504ABBDB535CA0C76C1F', X'90003249D4EB17469ED68E90781B65AB', X'90003249D4EB17469ED68E90781B65AB', 1, NULL, NULL, 2, NULL, 0, 'Remove stems and pits, smash or cut cherries into smaller pieces.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'24813555E586224193047E89EDEB7488', X'90003249D4EB17469ED68E90781B65AB', X'90003249D4EB17469ED68E90781B65AB', 2, NULL, NULL, 2, NULL, 0, 'Combine cherries and lemon juice, stir in Ball RealFruit Classic Pectin. Add  tsp butter or margarine to prevent foaming (optional). Bring mixture to a full rolling boil, stirring continuously.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1DAD1AFC7808C3438A204F7000BB6365', X'90003249D4EB17469ED68E90781B65AB', X'90003249D4EB17469ED68E90781B65AB', 3, NULL, NULL, 2, NULL, 0, 'Add sugar and stir to dissolve. Bring mixture to a full boil and boil for 1 minute, stirring continuously. Remove from heat and skim foam, if any.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EB822B364938594A9BC03562DA8C64DA', X'90003249D4EB17469ED68E90781B65AB', X'90003249D4EB17469ED68E90781B65AB', 4, NULL, NULL, 2, NULL, 0, 'Fill jam into glass jars and process for 10 minutes in a water bath canner.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'ED644FA1A4522745A3747C8F7A9175D5', X'90003249D4EB17469ED68E90781B65AB', X'90003249D4EB17469ED68E90781B65AB', 5, NULL, NULL, 2, NULL, 0, 'Remove jars and let stand for 12 hours.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'63276C708BEBA048B51B6A17E798223E', X'6DA106388665114A8C17BDB0BC9650AA', X'6DA106388665114A8C17BDB0BC9650AA', 0, NULL, NULL, 2, NULL, 0, 'Use Jonathan, Winesap, Stayman, Golden Delicious, Macintosh, or other tasty apple varieties for good results.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4B2BBDE7598C77498432D601505DA083', X'6DA106388665114A8C17BDB0BC9650AA', X'6DA106388665114A8C17BDB0BC9650AA', 1, NULL, NULL, 2, NULL, 0, 'Yield: About 8 to 9 pints');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7C8F35BEC208A54180ED285C4A4D291E', X'6DA106388665114A8C17BDB0BC9650AA', X'6DA106388665114A8C17BDB0BC9650AA', 2, NULL, NULL, 2, NULL, 0, 'Procedure: Wash, remove stems, quarter, and core fruit. Cook slowly in cider and vinegar until soft. Press fruit through a colander, food mill, or strainer. Cook fruit pulp with sugar and spices, stirring frequently. To test for doneness, remove a spoonful and hold it away from steam for 2 minutes. It is done if the butter remains mounded on the spoon. Another way to determine when the butter is cooked adequately is to spoon a small quantity onto a plate. When a rim of liquid does not separate around the edge of the butter, it is ready for canning. Fill hot into sterile half-pint or pint jars, leaving 1/4 inch headspace. Quart jars need not be presterilized but should be clean and kept hot until filling. Remove air bubbles and adjust headspace if needed. Wipe rims of jars with a dampened clean paper towel. Adjust lids and process.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E68871AE0888D04E846461677ACBA6AD', X'DA1A6D2A44E74D49B824213270F9C8B4', X'DA1A6D2A44E74D49B824213270F9C8B4', 0, NULL, NULL, 2, NULL, 0, 'Apple butter is made with apples, cider and aromatic spices. Many recipes include vinegar. Making apple butter and apple sauce is similar, however, butter is much thicker.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9F0F097FF0F7C6499C4974D78E255927', X'DA1A6D2A44E74D49B824213270F9C8B4', X'DA1A6D2A44E74D49B824213270F9C8B4', 1, NULL, NULL, 2, NULL, 0, '1. Remove stems, quarter and core apples. Cut apples into smaller pieces. You can peel the apples or leave the skin on. The skin will be removed during straining step.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E568F393E827C747B41F452610903FBF', X'DA1A6D2A44E74D49B824213270F9C8B4', X'DA1A6D2A44E74D49B824213270F9C8B4', 2, NULL, NULL, 2, NULL, 0, '2. Place diced apples and apple cider in pot. Cook slowly on a low heat for about one hour, until apples are soft.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E4704550C789964DBD9463F3541B31DB', X'DA1A6D2A44E74D49B824213270F9C8B4', X'DA1A6D2A44E74D49B824213270F9C8B4', 3, NULL, NULL, 2, NULL, 0, '3. Strain through a strainer. Discard skins if present.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'488B2E7331644B478476DAB44EF60FD1', X'DA1A6D2A44E74D49B824213270F9C8B4', X'DA1A6D2A44E74D49B824213270F9C8B4', 4, NULL, NULL, 2, NULL, 0, '4. Cook apple pulp with sugar, stirring frequently.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'66FA46C19ECA9D47B4D731E281332303', X'DA1A6D2A44E74D49B824213270F9C8B4', X'DA1A6D2A44E74D49B824213270F9C8B4', 5, NULL, NULL, 2, NULL, 0, '5. Add spices at the final stage of cooking.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6A0946C960771349992CCD5873D679E5', X'DA1A6D2A44E74D49B824213270F9C8B4', X'DA1A6D2A44E74D49B824213270F9C8B4', 6, NULL, NULL, 2, NULL, 0, '6. Meanwhile, sterilize canning jars.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6A7B1264CED16D40A5CD1617C44C7BDE', X'DA1A6D2A44E74D49B824213270F9C8B4', X'DA1A6D2A44E74D49B824213270F9C8B4', 7, NULL, NULL, 2, NULL, 0, '7. Test for doneness:');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0CF894D2E593E548937F5B1BF245D701', X'DA1A6D2A44E74D49B824213270F9C8B4', X'DA1A6D2A44E74D49B824213270F9C8B4', 8, NULL, NULL, 2, NULL, 0, '8. Fill hot butter into jars, leaving  headspace. Wipe jar rims and adjust lids.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7C8EB2D4B9BD584394D99C446CDE792F', X'DA1A6D2A44E74D49B824213270F9C8B4', X'DA1A6D2A44E74D49B824213270F9C8B4', 9, NULL, NULL, 2, NULL, 0, '9. Process for 5 minutes in a boiling water bath.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E5E76A2758E63C44864724E00C4612F7', X'A9197F936ED5FD42B3EE041A7CE394E9', X'A9197F936ED5FD42B3EE041A7CE394E9', 0, NULL, NULL, 2, NULL, 0, 'Apple fruit leather can be made from fresh apples or apple sauce. Canned apple sauce can be mixed with more expensive berries to make more fruit leather. It is hard to obtain liquefied apple in a blender or processor so it is best to pre-cook apples in a pot until they are soft. Then, they can be easily liquefied in a blender.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'61D1B852AF28784AA5D28F529B69E47A', X'A9197F936ED5FD42B3EE041A7CE394E9', X'A9197F936ED5FD42B3EE041A7CE394E9', 1, NULL, NULL, 2, NULL, 0, '1. Peel, core and dice apples.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FC70E431199B914DBA7F66259A3F9155', X'A9197F936ED5FD42B3EE041A7CE394E9', X'A9197F936ED5FD42B3EE041A7CE394E9', 2, NULL, NULL, 2, NULL, 0, '2. Cook apples until soft.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'352DA3E8E2E8274588F58A713E67F214', X'A9197F936ED5FD42B3EE041A7CE394E9', X'A9197F936ED5FD42B3EE041A7CE394E9', 3, NULL, NULL, 2, NULL, 0, '3. Puree apple pulp in a blender. Add sugar and cinnamon.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D5BFC5B86CA3DD40A606422CF3898F51', X'A9197F936ED5FD42B3EE041A7CE394E9', X'A9197F936ED5FD42B3EE041A7CE394E9', 4, NULL, NULL, 2, NULL, 0, '4. Pour fruit apple onto parchment sheet.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E038ADCE6C696A46B136B45B55921B8F', X'A9197F936ED5FD42B3EE041A7CE394E9', X'A9197F936ED5FD42B3EE041A7CE394E9', 5, NULL, NULL, 2, NULL, 0, '5. Dry at 140 F (40 C) until done.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'838B3A8BAE76574B85077726E2A5E4F5', X'A9197F936ED5FD42B3EE041A7CE394E9', X'A9197F936ED5FD42B3EE041A7CE394E9', 6, NULL, NULL, 2, NULL, 0, 'Testing. Test fruit leather by pressing against with your finger. Properly dried fruit leather is slightly tacky to touch, but easily peeled from the sheet. There should not be visible indentation. Cool the leather for 10 minutes, then peel away and roll. Properly dried leather will unwrap easily, the still moist spots will stick to the sheet. Wrap the fruit roll in plastic.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AFCA51DFAE702B4AB3D44EBC5D116893', X'77D447C0F3B5C1468C0958E14DA4DB9B', X'77D447C0F3B5C1468C0958E14DA4DB9B', 0, NULL, NULL, 2, NULL, 0, 'Mixing plum or plum butter with chocolate creates a wonderful combination. Addingorange flavor rounds up the flavor. Commercial chocolates are often made with plums, cherries or oranges, and such combination work well with butters as well.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2C2E519DE2489E4E92A6AF1F6FCB673C', X'77D447C0F3B5C1468C0958E14DA4DB9B', X'77D447C0F3B5C1468C0958E14DA4DB9B', 1, NULL, NULL, 2, NULL, 0, '1. Soak dry plums for 6-12 hours in water. They will absorb water and expand in volume.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9F15865897706C4D95BCF02492929F86', X'77D447C0F3B5C1468C0958E14DA4DB9B', X'77D447C0F3B5C1468C0958E14DA4DB9B', 2, NULL, NULL, 2, NULL, 0, '2. Blend soaked plums in food processor.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2FD48917B458B34B837C1BE6B274A24B', X'77D447C0F3B5C1468C0958E14DA4DB9B', X'77D447C0F3B5C1468C0958E14DA4DB9B', 3, NULL, NULL, 2, NULL, 0, '3. Add cinnamon, lemon juice, lemon zest, orange zest and cook with plum pulp on low heat stirring often. After 30 minutes test for doneness:');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5EEBD99AF0683A45BEB057F46CC0643F', X'77D447C0F3B5C1468C0958E14DA4DB9B', X'77D447C0F3B5C1468C0958E14DA4DB9B', 4, NULL, NULL, 2, NULL, 0, '4. Add orange extract and mix the butter. Pack in clean jars and process for 10 minutes in a boiling water canner.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EA5001386D60B24BB5D5E9053AD59C9E', X'77D447C0F3B5C1468C0958E14DA4DB9B', X'77D447C0F3B5C1468C0958E14DA4DB9B', 5, NULL, NULL, 2, NULL, 0, 'NOTE instead semi-sweet chocolate chips, you may add 226 g (8 oz) of Nestle pure cocoa and 100 g (3.5 oz) sugar.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C6F285AF6E5837458596155B0E5AA45F', X'A529B14FA40F8443B568F651672CE915', X'A529B14FA40F8443B568F651672CE915', 0, NULL, NULL, 2, NULL, 0, 'Peach butter is delicious and fast to make.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E2E91D749717FB4396D97B580F88AFDD', X'A529B14FA40F8443B568F651672CE915', X'A529B14FA40F8443B568F651672CE915', 1, NULL, NULL, 2, NULL, 0, 'Wash, scald, pit and peel peaches. Cut into smaller pieces.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'35F3DF327CF5A74281E18BD0EFBD0C74', X'A529B14FA40F8443B568F651672CE915', X'A529B14FA40F8443B568F651672CE915', 2, NULL, NULL, 2, NULL, 0, 'Start cooking peaches on low heat to release juice. Cook until soft.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9A7DC55DE3C1FA4FA6A0D22FBD1488A2', X'A529B14FA40F8443B568F651672CE915', X'A529B14FA40F8443B568F651672CE915', 3, NULL, NULL, 2, NULL, 0, 'Strain through a sieve.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'273A2591F07F554EA0879CF6D2027AD5', X'A529B14FA40F8443B568F651672CE915', X'A529B14FA40F8443B568F651672CE915', 4, NULL, NULL, 2, NULL, 0, 'Add sugar and cook the pulp, stirring often for about 45 minutes.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'76E1312F6923C245BB69517824EEE946', X'A529B14FA40F8443B568F651672CE915', X'A529B14FA40F8443B568F651672CE915', 5, NULL, NULL, 2, NULL, 0, 'Fill butter into sterilized jars, leaving  inch headspace.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EFEF273FAA444A45B65F3E1F5FDF196C', X'A529B14FA40F8443B568F651672CE915', X'A529B14FA40F8443B568F651672CE915', 6, NULL, NULL, 2, NULL, 0, 'Process for 5 minutes in a boiling water bath. Wipe off jar rims and tighten lids.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'199DD03C2AA9FC4CA8B045CE4A08AD44', X'A529B14FA40F8443B568F651672CE915', X'A529B14FA40F8443B568F651672CE915', 7, NULL, NULL, 2, NULL, 0, 'Remove jars and let stand for 6 hours.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8160ABF04E2F444281DEF8E02F59B90A', X'798E1D6BBA1F8147B42B5DDDD57BA1DD', X'798E1D6BBA1F8147B42B5DDDD57BA1DD', 0, NULL, NULL, 2, NULL, 0, 'Plum butter can be spread on a roll or served like a jam. Very sweet butters are usually spread thinly and those with little sugar can be served in bigger portions.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1F6DE8B3E0E19B459106C2DCF38D65A8', X'798E1D6BBA1F8147B42B5DDDD57BA1DD', X'798E1D6BBA1F8147B42B5DDDD57BA1DD', 1, NULL, NULL, 2, NULL, 0, '1. Wash plums, split and discard pits. Cut the plums into smaller pieces.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D37B8AF528DA2749AE7ED54E8F210C79', X'798E1D6BBA1F8147B42B5DDDD57BA1DD', X'798E1D6BBA1F8147B42B5DDDD57BA1DD', 2, NULL, NULL, 2, NULL, 0, '2. Add lemon juice and cook the plums until soft, stirring often. Start on very low heat until plums release juice.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E60E37AFD542954FB56C58B23D706C35', X'798E1D6BBA1F8147B42B5DDDD57BA1DD', X'798E1D6BBA1F8147B42B5DDDD57BA1DD', 3, NULL, NULL, 2, NULL, 0, '3. Strain the pulp through a strainer.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AEFA52AA1787FA428EF0D49C89F92697', X'798E1D6BBA1F8147B42B5DDDD57BA1DD', X'798E1D6BBA1F8147B42B5DDDD57BA1DD', 4, NULL, NULL, 2, NULL, 0, '4. Add sugar and cinnamon. Cook the pulp, stirring more often until proper texture is obtained.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CBB233CEA5771D40908FD447373C1261', X'798E1D6BBA1F8147B42B5DDDD57BA1DD', X'798E1D6BBA1F8147B42B5DDDD57BA1DD', 5, NULL, NULL, 2, NULL, 0, 'Test for doneness: place a small quantity of the butter on a plate. When a rim of the liquid does not separate around the edge of the butter, it is done.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6D9A0FC94CA3384A8733EB9EE53F7B28', X'798E1D6BBA1F8147B42B5DDDD57BA1DD', X'798E1D6BBA1F8147B42B5DDDD57BA1DD', 6, NULL, NULL, 2, NULL, 0, '5. Fill sterilized jars with hot prune butter and tighten the lid.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3A2F03E00FEF4448ACA49F44BA5807EC', X'798E1D6BBA1F8147B42B5DDDD57BA1DD', X'798E1D6BBA1F8147B42B5DDDD57BA1DD', 7, NULL, NULL, 2, NULL, 0, '6. Process for 10 minutes in a boiling water bath canner.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3821B1249BBE3A4CA38CF0B021256321', X'798E1D6BBA1F8147B42B5DDDD57BA1DD', X'798E1D6BBA1F8147B42B5DDDD57BA1DD', 8, NULL, NULL, 2, NULL, 0, '7. Remove jars and leave undisturbed for 12 hours.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'596902F62157A942A9B8819A4EB5A419', X'798E1D6BBA1F8147B42B5DDDD57BA1DD', X'798E1D6BBA1F8147B42B5DDDD57BA1DD', 9, NULL, NULL, 2, NULL, 0, 'NOTE plum butter does not need much sugar, taste it and decide whether it is sweet enough.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4A4213807150F048A58F6A7377798419', X'E86B853A18A1D04BA4E39DEFA0054315', X'E86B853A18A1D04BA4E39DEFA0054315', 0, NULL, NULL, 2, NULL, 0, 'Prune butter from dry prunes has a strong flavor and is faster to make than using fresh plums. Sugar is often left out as prune butter tastes delicious without it.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9B41CD306E86BA46BBF4BA2DF4D6CD1C', X'E86B853A18A1D04BA4E39DEFA0054315', X'E86B853A18A1D04BA4E39DEFA0054315', 1, NULL, NULL, 2, NULL, 0, '1. Soak the prunes for 12 hours in water. They will absorb water and expand in volume.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'845F2F7F19479545B893D9AFF99A4AAA', X'E86B853A18A1D04BA4E39DEFA0054315', X'E86B853A18A1D04BA4E39DEFA0054315', 2, NULL, NULL, 2, NULL, 0, '2. Cook the prunes until soft.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'96B25B3EA581994C86EED98B8ECFF714', X'E86B853A18A1D04BA4E39DEFA0054315', X'E86B853A18A1D04BA4E39DEFA0054315', 3, NULL, NULL, 2, NULL, 0, '3. Strain the pulp and juice through a medium size sieve.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BE455FDD499FC34F85D7A4A0F7356838', X'E86B853A18A1D04BA4E39DEFA0054315', X'E86B853A18A1D04BA4E39DEFA0054315', 4, NULL, NULL, 2, NULL, 0, '4. Place in a large pot and bring to a boil. Continue cooking at low heat, stirring often.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9577EAD698C8194DA1A31AA24E29E82A', X'E86B853A18A1D04BA4E39DEFA0054315', X'E86B853A18A1D04BA4E39DEFA0054315', 5, NULL, NULL, 2, NULL, 0, '5. Add cinnamon and keep on cooking. Test for doneness: place a small quantity of the butter on a plate. When a rim of the liquid does not separate around the edge ofthe butter, it is done.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0C332CC1F7AB5F419A482E8177F53794', X'E86B853A18A1D04BA4E39DEFA0054315', X'E86B853A18A1D04BA4E39DEFA0054315', 6, NULL, NULL, 2, NULL, 0, '6. Fill sterilized jars with hot prune butter and tighten the lid.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'65BBDEC02035E44BA954669234F243CD', X'E86B853A18A1D04BA4E39DEFA0054315', X'E86B853A18A1D04BA4E39DEFA0054315', 7, NULL, NULL, 2, NULL, 0, '7. Process for 10 minutes in a boiling water bath canner.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'ACED621C400AB24691305572D0FF84C5', X'E86B853A18A1D04BA4E39DEFA0054315', X'E86B853A18A1D04BA4E39DEFA0054315', 8, NULL, NULL, 2, NULL, 0, '8. Remove jars and leave undisturbed for 12 hours.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D13714569A59FE418C55F785D3F1B027', X'E86B853A18A1D04BA4E39DEFA0054315', X'E86B853A18A1D04BA4E39DEFA0054315', 9, NULL, NULL, 2, NULL, 0, 'NOTE adding sugar is optional. About 50 g (2 oz) is plenty.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BB72D8C2C14DF1489B6885622546854B', X'E86B853A18A1D04BA4E39DEFA0054315', X'E86B853A18A1D04BA4E39DEFA0054315', 10, NULL, NULL, 2, NULL, 0, 'A. Soak prunes for 12 hours.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'875AB8976A6EDA48827F75683C7C4398', X'E86B853A18A1D04BA4E39DEFA0054315', X'E86B853A18A1D04BA4E39DEFA0054315', 11, NULL, NULL, 2, NULL, 0, 'B. Blend prunes in a food processor.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DDB572370C89A749BED0E487E9D4C0CC', X'E86B853A18A1D04BA4E39DEFA0054315', X'E86B853A18A1D04BA4E39DEFA0054315', 12, NULL, NULL, 2, NULL, 0, 'C. Add cinnamon and cook on low heat stirring often. After 30 minutes test for doneness: place a small quantity of the butter on a plate. When a rim of the liquid does not separate around the edge of the butter, it is done.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'086D3C2BED276248833B868FA96170F1', X'E86B853A18A1D04BA4E39DEFA0054315', X'E86B853A18A1D04BA4E39DEFA0054315', 13, NULL, NULL, 2, NULL, 0, 'D. Pack in clean jars and process for 10 minutes in a boiling water canner.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'45C32E1BA8DB134BB7E1A8C876F84B42', X'D0CD093258832A4B983CA4D646F2398E', X'D0CD093258832A4B983CA4D646F2398E', 0, NULL, NULL, 2, NULL, 0, 'Strawberries make delicious fruit leather.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FF8EE2324E13B64E9B23236E73FFE238', X'D0CD093258832A4B983CA4D646F2398E', X'D0CD093258832A4B983CA4D646F2398E', 1, NULL, NULL, 2, NULL, 0, '1. Wash strawberries, remove green tops and cut strawberries into halves. Smash strawberries with potato masher.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2680CE027169154AA61B736118DA5927', X'D0CD093258832A4B983CA4D646F2398E', X'D0CD093258832A4B983CA4D646F2398E', 2, NULL, NULL, 2, NULL, 0, '2. Puree strawberries in a blender. Add sugar.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2C7ED85C63524543B484D890AE88DF69', X'D0CD093258832A4B983CA4D646F2398E', X'D0CD093258832A4B983CA4D646F2398E', 3, NULL, NULL, 2, NULL, 0, '3. Pour strawberry fruit leather onto parchment sheet.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A6A86C3850742A46A8A8BDBDEEBF211E', X'D0CD093258832A4B983CA4D646F2398E', X'D0CD093258832A4B983CA4D646F2398E', 4, NULL, NULL, 2, NULL, 0, '4. Dry at 140 F (40 C) until done. Testing. Test fruit leather by pressing against with your finger. Properly dried fruit leather is slightly tacky to touch, but easily peeled from the sheet.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3334E48C9BB91347BCA3CE34CBAA14AD', X'D0CD093258832A4B983CA4D646F2398E', X'D0CD093258832A4B983CA4D646F2398E', 5, NULL, NULL, 2, NULL, 0, 'There should not be visible indentation. Cool the leather for 10 minutes, then peel away and roll. Properly dried fruit leather will unwrap easily, the still moist spots willstick to the sheet. Wrap the fruit roll in plastic.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C7C41A3ECC19434FB08816A8E595A787', X'25ED3D1CD625514385B8658CEAF5D29C', X'25ED3D1CD625514385B8658CEAF5D29C', 0, NULL, NULL, 2, NULL, 0, 'Trim off outer leaves, split cabbage into quarters, then cut into 1 (2.5 cm) slices. Slice carrot into 1/4 (5 mm) slices.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E2184CA86CE8C84B859F18FE552339BC', X'25ED3D1CD625514385B8658CEAF5D29C', X'25ED3D1CD625514385B8658CEAF5D29C', 1, NULL, NULL, 2, NULL, 0, 'Make a brine: 3 cups salt to 4 quarts water. Brine the cabbage and carrots for 3 hours. Remove cabbage and carrots, save the brine. Rinse in fresh water, drain, repeat twice more. Drain cabbage for 30 minutes.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AA05DE779DB6D848929DED5941895EED', X'25ED3D1CD625514385B8658CEAF5D29C', X'25ED3D1CD625514385B8658CEAF5D29C', 2, NULL, NULL, 2, NULL, 0, 'Cut scallions diagonally into 1 (2.5 cm) pieces.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4413799D98CE0A42B42B1BD46E92E0ED', X'25ED3D1CD625514385B8658CEAF5D29C', X'25ED3D1CD625514385B8658CEAF5D29C', 3, NULL, NULL, 2, NULL, 0, 'Finely dice ginger and mix with red pepper powder.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'09B3F9D55229B5429CCB3C2C7C7484E2', X'25ED3D1CD625514385B8658CEAF5D29C', X'25ED3D1CD625514385B8658CEAF5D29C', 4, NULL, NULL, 2, NULL, 0, 'Mix all together.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EB89E75BACDB254B996285E7B6B50D58', X'25ED3D1CD625514385B8658CEAF5D29C', X'25ED3D1CD625514385B8658CEAF5D29C', 5, NULL, NULL, 2, NULL, 0, 'Pack the mixture in a container. Put on top outer leaves that were removed in step #1. Sprinkle with salt. Place weight on top. Add enough leftover brine to submerge the mixture. Ferment 3 days.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2B81543FB8458649A18DC0FE9986B83E', X'D09066E7BA26964996C4B7384E8F8AE2', X'D09066E7BA26964996C4B7384E8F8AE2', 0, NULL, NULL, 2, NULL, 0, 'Wash radishes, clean the skin gently with a sponge, don''t use a brush. Cut half of the radishes into 2` long sections. Make vertical criss-cross cuts at 3/4` intervals. Stop cutting at about 1/2` (1 cm) from the bottom. Cut the remaining radishes into thin 2` long Julienne strips.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'621DBC354E23054E9295FD9F306A3812', X'D09066E7BA26964996C4B7384E8F8AE2', X'D09066E7BA26964996C4B7384E8F8AE2', 1, NULL, NULL, 2, NULL, 0, 'Soften radish by placing it for 3 hours in brine, that is made by dissolving 3 cups of salt (864 g) in 4 quarts (~ 4 liters) of water. Drain, rinse in fresh water, then drain again.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B09740F046D52A4ABBF8805021A497EE', X'D09066E7BA26964996C4B7384E8F8AE2', X'D09066E7BA26964996C4B7384E8F8AE2', 2, NULL, NULL, 2, NULL, 0, 'Mix miso, honey, red pepper powder, garlic and ginger. Use garlic press to squash ginger and garlic.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'39E3E2CCEEBDA941822A42F13887037E', X'D09066E7BA26964996C4B7384E8F8AE2', X'D09066E7BA26964996C4B7384E8F8AE2', 3, NULL, NULL, 2, NULL, 0, 'Spread the cut, insert a toothpick to keep it open and fill crevices with paste. Remove the toothpick and repeat the procedure at the next cut.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AF3D559998E9A24B90CDCA145E4E85D0', X'D09066E7BA26964996C4B7384E8F8AE2', X'D09066E7BA26964996C4B7384E8F8AE2', 4, NULL, NULL, 2, NULL, 0, 'Cut the Korean pear into thin strips. Mix with dry cranberries and radish strips.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6BEB69B2B1B6A943BE39E5586A05A41A', X'D09066E7BA26964996C4B7384E8F8AE2', X'D09066E7BA26964996C4B7384E8F8AE2', 5, NULL, NULL, 2, NULL, 0, 'Place all strips on the bottom of a container and sprinkle salt over them. Mix. Place round sections of radishes on top.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CE42FA8B883F7A48B66FBBCE19C8C005', X'D09066E7BA26964996C4B7384E8F8AE2', X'D09066E7BA26964996C4B7384E8F8AE2', 6, NULL, NULL, 2, NULL, 0, 'Cover with cabbage leaves or inverted glass plate and apply some weight. You may fill a Ziplock bag with brine and put it on top.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E6BE41C0E82D2C40B33F3BCFA7C80733', X'D09066E7BA26964996C4B7384E8F8AE2', X'D09066E7BA26964996C4B7384E8F8AE2', 7, NULL, NULL, 2, NULL, 0, 'Ferment 3 days.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5504890F43BAED44AF6AABC57F94A583', X'F89D68B21CAB084E8A8DD74B3831828F', X'F89D68B21CAB084E8A8DD74B3831828F', 0, NULL, NULL, 2, NULL, 0, 'Trim off outer leaves, split cabbage into separate halves.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'53DAFE2512869843950653D0A1364C58', X'F89D68B21CAB084E8A8DD74B3831828F', X'F89D68B21CAB084E8A8DD74B3831828F', 1, NULL, NULL, 2, NULL, 0, 'Make a brine: 3 cups salt to 4 quarts water. Brine the cabbage for 3 hours. Rinse cabbage in fresh water and squeeze the water out, repeat twice more. Drain cabbage for 30 minutes.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FED55E37D2B352499D9E0706CC24EE53', X'F89D68B21CAB084E8A8DD74B3831828F', X'F89D68B21CAB084E8A8DD74B3831828F', 2, NULL, NULL, 2, NULL, 0, 'Cut cabbage into 2 (5 cm) sections.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C94278B3CCA203408C915599BF186E41', X'F89D68B21CAB084E8A8DD74B3831828F', X'F89D68B21CAB084E8A8DD74B3831828F', 3, NULL, NULL, 2, NULL, 0, 'Add a little water to red pepper powder and make paste.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B3B896E92EF48F42AFAD0BDBD512D202', X'F89D68B21CAB084E8A8DD74B3831828F', X'F89D68B21CAB084E8A8DD74B3831828F', 4, NULL, NULL, 2, NULL, 0, 'Cut scallions diagonally, dice ginger and crush garlic.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6F45A5652797FA4BA17250C085E5C6D3', X'F89D68B21CAB084E8A8DD74B3831828F', X'F89D68B21CAB084E8A8DD74B3831828F', 5, NULL, NULL, 2, NULL, 0, 'Add salt and sugar and mix all together.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'028B6B761560CE448B5BA814F561FFCD', X'F89D68B21CAB084E8A8DD74B3831828F', X'F89D68B21CAB084E8A8DD74B3831828F', 6, NULL, NULL, 2, NULL, 0, 'Pack stuffed cabbage in a container. Put on top outer leaves that were removed in step #1. Sprinkle with salt. Place weight on top. Ferment 3 days.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9E7AEDC819F56E4BAF1E14C93D22552C', X'0512A2D162CC90428E3B267B3CD3D2D2', X'0512A2D162CC90428E3B267B3CD3D2D2', 0, NULL, NULL, 2, NULL, 0, 'Beets rich chemical and mineral composition has made them a medicinal vegetable for thousands of years. Beets are rich in nitrates and when consumed regularly, they help to lower blood pressure and stimulate the immune system. Beet juice is a powerful natural colorant.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D27D823FFE8E9545809578AA033A7D33', X'0512A2D162CC90428E3B267B3CD3D2D2', X'0512A2D162CC90428E3B267B3CD3D2D2', 1, NULL, NULL, 2, NULL, 0, 'Cut off stalks and rinse beets off to get rid of dirt. Don''t scrub the outside or peel the beets.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1C01F9AFB3D1D24290186B7C0E1F883A', X'0512A2D162CC90428E3B267B3CD3D2D2', X'0512A2D162CC90428E3B267B3CD3D2D2', 2, NULL, NULL, 2, NULL, 0, 'Slice or grate beets. Cut pears into wedges.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BC39F79CC5B8F24D801EACA6A166313A', X'0512A2D162CC90428E3B267B3CD3D2D2', X'0512A2D162CC90428E3B267B3CD3D2D2', 3, NULL, NULL, 2, NULL, 0, 'Mix with salt and spices.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D42DF373CB64394783D26E945D45F8F9', X'0512A2D162CC90428E3B267B3CD3D2D2', X'0512A2D162CC90428E3B267B3CD3D2D2', 4, NULL, NULL, 2, NULL, 0, 'Pack. Place a weight on top. Beets may get foamy, this is normal.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5FE48CDE992E464D803B492BE1C83654', X'0512A2D162CC90428E3B267B3CD3D2D2', X'0512A2D162CC90428E3B267B3CD3D2D2', 5, NULL, NULL, 2, NULL, 0, 'Ferment, preferably around 68 F (20 C), for about 2 weeks. After 3 days, taste the beets and see how you like them. If you want them a little tangier, let them ferment longer. If you like what you taste, refrigerate.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9B63CB27B4A16D48A314D10F9B7A0777', X'3DCB28F665CE5E40BFF4A96F3B89955A', X'3DCB28F665CE5E40BFF4A96F3B89955A', 0, NULL, NULL, 2, NULL, 0, 'Finely grated fresh beets and horseradish is a great combination that goes well with cold meat cuts. Horseradish is a root crop, grown for the pungent roots. They contain highly volatile oils with a sharp flavor. Grind fresh horseradish in a well-ventilated room. The fumes from grinding are potent. If you use a manual grater expect to cry a lot! Beets will color your hands purple, so use gloves.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'601C1EBEDCB46D448AEB9AECFFD97014', X'3DCB28F665CE5E40BFF4A96F3B89955A', X'3DCB28F665CE5E40BFF4A96F3B89955A', 1, NULL, NULL, 2, NULL, 0, 'Wash and rinse beets to get rid of dirt but don''t scrub the outside or peel them. Slice or grate.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'32E2028281A1C74BA8F8CBDBEAC8FE25', X'3DCB28F665CE5E40BFF4A96F3B89955A', X'3DCB28F665CE5E40BFF4A96F3B89955A', 2, NULL, NULL, 2, NULL, 0, 'Do the same with horseradish.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'499212D43C7DF54BA524C9F0BAF4AAE7', X'3DCB28F665CE5E40BFF4A96F3B89955A', X'3DCB28F665CE5E40BFF4A96F3B89955A', 3, NULL, NULL, 2, NULL, 0, 'Mix everything with spices, salt and sugar.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'89977A486D5CF34E8B7499B59184375C', X'3DCB28F665CE5E40BFF4A96F3B89955A', X'3DCB28F665CE5E40BFF4A96F3B89955A', 4, NULL, NULL, 2, NULL, 0, 'Pack in a container. Place weight on top.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'420C81CC403DC144BCFB10BCD2DF35EE', X'3DCB28F665CE5E40BFF4A96F3B89955A', X'3DCB28F665CE5E40BFF4A96F3B89955A', 5, NULL, NULL, 2, NULL, 0, 'Ferment, preferably around 68 F (20 C), for about 2 weeks. Serve with lemon wedges.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'67B54E69FFAF1B4F82281348BBF808DE', X'C2D093094F9ADE49A3730D94BF606CFD', X'C2D093094F9ADE49A3730D94BF606CFD', 0, NULL, NULL, 2, NULL, 0, 'Borscht is a beet soup of Ukrainian (????) origin. In Russian it is spelled the same way (????) and in Poland it is known as barszcz. It is pronounced almost the same in most Eastern and Central European countries so there is no question where borsch comes from. Borsch was always popular among Jews who added letter t at the end and it is known as borscht in the US.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2DBAD6C7B194234D81E41DE30209565A', X'C2D093094F9ADE49A3730D94BF606CFD', X'C2D093094F9ADE49A3730D94BF606CFD', 1, NULL, NULL, 2, NULL, 0, 'There is a hot and cold version of borscht and both versions are made by cooking beets what deprives them of many beneficial nutrients. You can, however, skip cooking beets altogether and ferment them instead. Red beets ferment easily and are delicious, so it should not come as a surprise that borscht can be made without cooking, by fermentation alone.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A463D03223278B4E867C1B20A3EDE723', X'C2D093094F9ADE49A3730D94BF606CFD', X'C2D093094F9ADE49A3730D94BF606CFD', 2, NULL, NULL, 2, NULL, 0, 'This fermented beet base can be used for many dishes:');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E2BC92A83F02A94883A2A7DA22B9A7E2', X'C2D093094F9ADE49A3730D94BF606CFD', X'C2D093094F9ADE49A3730D94BF606CFD', 3, NULL, NULL, 2, NULL, 0, 'You can use it to make delicious Lithuanian Beet Cooler Soup without cooking preserving all nutrients and vitamins.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1A51F792F8CF44488FEE519885AAC698', X'C2D093094F9ADE49A3730D94BF606CFD', X'C2D093094F9ADE49A3730D94BF606CFD', 4, NULL, NULL, 2, NULL, 0, 'NOTE beets and beet juice are rich in nitrates and are credited with lowering blood pressure when consumed on a regular basis. They help to detoxify the liver.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'154C16FE39F30147A9161A18784CEAD6', X'C2D093094F9ADE49A3730D94BF606CFD', X'C2D093094F9ADE49A3730D94BF606CFD', 5, NULL, NULL, 2, NULL, 0, 'After a week fermented beets and juice have enough acidity to make uncooked borscht or delicious Lithuanian Beet Cooler Soup.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EA9BBD33218ED14AB5C0CD12454B4BF2', X'C2D093094F9ADE49A3730D94BF606CFD', X'C2D093094F9ADE49A3730D94BF606CFD', 6, NULL, NULL, 2, NULL, 0, 'The advantage of using fermented beets for making soups is that there is no cooking involved and all vitamins and nutrients are preserved. Fermented beets are softer and the juice tastes perfect. You can think of fermented beet borscht as a soup stock which can be enriched with additional ingredients.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'61F2509EACFE9C45892E3D1530F8DA34', X'C2D093094F9ADE49A3730D94BF606CFD', X'C2D093094F9ADE49A3730D94BF606CFD', 7, NULL, NULL, 2, NULL, 0, 'Make brine by dissolving salt in water.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'90C9F4314C9C9643873CFCC0ED4126A4', X'C2D093094F9ADE49A3730D94BF606CFD', X'C2D093094F9ADE49A3730D94BF606CFD', 8, NULL, NULL, 2, NULL, 0, 'Wash beets and peel off the skin. Cut into discs or dice them.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'541487FED29D7443A3DB44544B59A430', X'C2D093094F9ADE49A3730D94BF606CFD', X'C2D093094F9ADE49A3730D94BF606CFD', 9, NULL, NULL, 2, NULL, 0, 'Peel of horseradish and garlic. Cut horseradish and dill into smaller sections.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E1E87C5EBC27694F9EE6C6A6EBCF8AE5', X'C2D093094F9ADE49A3730D94BF606CFD', X'C2D093094F9ADE49A3730D94BF606CFD', 10, NULL, NULL, 2, NULL, 0, 'Place beets in a glass jar, add horseradish, garlic and dill.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'06251998E0DA0348A2A609ACB89911EC', X'C2D093094F9ADE49A3730D94BF606CFD', X'C2D093094F9ADE49A3730D94BF606CFD', 11, NULL, NULL, 2, NULL, 0, 'Fill with brine, place the lid and tighten. Let it ferment at room temperature for at least one week. You can eat itat any time, but in 4 days it develops its real character.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B0F07CA304847948AE5C98E01B0B713D', X'C2D093094F9ADE49A3730D94BF606CFD', X'C2D093094F9ADE49A3730D94BF606CFD', 12, NULL, NULL, 2, NULL, 0, 'For longer storage place fermented borscht in refrigerator.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A2ECD454313D064DBF31544398D3C397', X'CEAD674E6212DF4B8C13B6620BFB7869', X'CEAD674E6212DF4B8C13B6620BFB7869', 0, NULL, NULL, 2, NULL, 0, 'Fermented cucumbers are made from small hard cucumbers, mature dill, horseradish and salty water. Pack all ingredients tight in a jar so they will be fully covered in brine and the rest is easy.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3C0F3B40BFA1CC46ABEF96DD2334ECBF', X'CEAD674E6212DF4B8C13B6620BFB7869', X'CEAD674E6212DF4B8C13B6620BFB7869', 1, NULL, NULL, 2, NULL, 0, 'You can vary the amount of salt in brine:');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'480B99825E675042873BB19345460954', X'CEAD674E6212DF4B8C13B6620BFB7869', X'CEAD674E6212DF4B8C13B6620BFB7869', 2, NULL, NULL, 2, NULL, 0, 'Depending on your preference, fermented cucumbers are ready to consume in 3-4 days or wait longer for more acidic taste. Naturally fermented cucumbers are made without vinegar and are meant to be consumed in a relatively short time. When desired acidity is obtained, place jar in refrigerator to stop fermentation. Many people like to drink cucumber juice which is quite delicious. Naturally fermented cucumbers have little in common with loaded with vinegar commercial product.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'24ADEDAFBF9C8941A6C7F5A1BE9D4C74', X'CEAD674E6212DF4B8C13B6620BFB7869', X'CEAD674E6212DF4B8C13B6620BFB7869', 3, NULL, NULL, 2, NULL, 0, 'Wash cucumbers, scrape the ends with fingernail. Those areas contain most of bacteria. Some people cut the end off, but there is no need for that.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'11190361C0C1094A9BFB9735AF736CDA', X'CEAD674E6212DF4B8C13B6620BFB7869', X'CEAD674E6212DF4B8C13B6620BFB7869', 4, NULL, NULL, 2, NULL, 0, 'Peel off garlic and horseradish. Cut up horseradish and dill.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3AC9A2F8F2226D438761116E85B00FD6', X'CEAD674E6212DF4B8C13B6620BFB7869', X'CEAD674E6212DF4B8C13B6620BFB7869', 5, NULL, NULL, 2, NULL, 0, 'Place dill on the bottom, then fit cucumbers vertically as tight as possible. Fit garlic and dill stems into open spaces.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7B861327FCD2494482895375BA699E0E', X'CEAD674E6212DF4B8C13B6620BFB7869', X'CEAD674E6212DF4B8C13B6620BFB7869', 6, NULL, NULL, 2, NULL, 0, 'Mix salt with water and fill the jar with brine. If cucumbers are packed tight they will be completely submerged in brine.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'76CD83F3E2C7E347B9CFCA2B70C51249', X'CEAD674E6212DF4B8C13B6620BFB7869', X'CEAD674E6212DF4B8C13B6620BFB7869', 7, NULL, NULL, 2, NULL, 0, 'Ferment at room temperature. They will be ready to eat in 3 days.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1C8A03F076BE8644B3997A5885FDF5CD', X'D8235308BDA0134CB7FC35CEE809B4BD', X'D8235308BDA0134CB7FC35CEE809B4BD', 0, NULL, NULL, 2, NULL, 0, 'Fermented tomatoes can be served with sandwiches, salads or with the main dish.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E03D29933ED87B449B6020F5B6221C35', X'D8235308BDA0134CB7FC35CEE809B4BD', X'D8235308BDA0134CB7FC35CEE809B4BD', 1, NULL, NULL, 2, NULL, 0, 'Wash tomatoes and place them in clay crock or food grade plastic container.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4D5C73A5284E74478B41D17BEF31E5B0', X'D8235308BDA0134CB7FC35CEE809B4BD', X'D8235308BDA0134CB7FC35CEE809B4BD', 2, NULL, NULL, 2, NULL, 0, 'Add water and all ingredients. Cover.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B8940389E3FAF74AAF82D3FC1D8CAEAD', X'D8235308BDA0134CB7FC35CEE809B4BD', X'D8235308BDA0134CB7FC35CEE809B4BD', 3, NULL, NULL, 2, NULL, 0, 'Ferment for at least one week at room temperature.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5D0F4733D1DE1B4E8A85FC7311D5E4FD', X'D8235308BDA0134CB7FC35CEE809B4BD', X'D8235308BDA0134CB7FC35CEE809B4BD', 4, NULL, NULL, 2, NULL, 0, 'Store in refrigerator.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CA72D16BB94AF1468EFDB6BFE90143AE', X'EB5A81B4CA7C1B4285053D4D6BCFA5B5', X'EB5A81B4CA7C1B4285053D4D6BCFA5B5', 0, NULL, NULL, 2, NULL, 0, 'Peel off garlic cloves.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7A049532987A0A41AB330D7A27FF0C6F', X'EB5A81B4CA7C1B4285053D4D6BCFA5B5', X'EB5A81B4CA7C1B4285053D4D6BCFA5B5', 1, NULL, NULL, 2, NULL, 0, 'Mix miso with wine to make a paste.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A619915EA7808045A9328BC397FE2968', X'EB5A81B4CA7C1B4285053D4D6BCFA5B5', X'EB5A81B4CA7C1B4285053D4D6BCFA5B5', 2, NULL, NULL, 2, NULL, 0, 'Place some of the miso mixtrue in a jar, then add garlic.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CDF114308ACCCA4DB16014E40E7DF4EC', X'EB5A81B4CA7C1B4285053D4D6BCFA5B5', X'EB5A81B4CA7C1B4285053D4D6BCFA5B5', 3, NULL, NULL, 2, NULL, 0, 'Cover garlic with a new layer of miso.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'40E891BD5C04C44DB3311A2EEBDAE971', X'EB5A81B4CA7C1B4285053D4D6BCFA5B5', X'EB5A81B4CA7C1B4285053D4D6BCFA5B5', 4, NULL, NULL, 2, NULL, 0, 'Repeat the procedure until all garlic cloves are covered with miso.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D46C8099F697DF4C9AE5A4CC6C22FD03', X'EB5A81B4CA7C1B4285053D4D6BCFA5B5', X'EB5A81B4CA7C1B4285053D4D6BCFA5B5', 5, NULL, NULL, 2, NULL, 0, 'Close the jar and refrigerate.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'373BA4870936104D85645D6A7F258208', X'F9F75118A58DB84FA04B4A21BFEF81FE', X'F9F75118A58DB84FA04B4A21BFEF81FE', 0, NULL, NULL, 2, NULL, 0, 'Remove outer leaves.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3BEA415952434E419228D3BAA8231651', X'F9F75118A58DB84FA04B4A21BFEF81FE', X'F9F75118A58DB84FA04B4A21BFEF81FE', 1, NULL, NULL, 2, NULL, 0, 'Cut cabbage in quarters, remove cores. They may be finely grated and added as well. Cut apples into 6 wedges, discard stems and seeds.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'71B1B888DB58EF469FE9031223978ED4', X'F9F75118A58DB84FA04B4A21BFEF81FE', X'F9F75118A58DB84FA04B4A21BFEF81FE', 2, NULL, NULL, 2, NULL, 0, 'Mix cabbage, apples, cranberries, salt and pack tightly in a container.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F7DAE3CB59B97D46A5C24B7C40B867B1', X'F9F75118A58DB84FA04B4A21BFEF81FE', X'F9F75118A58DB84FA04B4A21BFEF81FE', 3, NULL, NULL, 2, NULL, 0, 'Cover with leaves and apply weight.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E44D8710D7E14D439CD87461E03F6302', X'F9F75118A58DB84FA04B4A21BFEF81FE', X'F9F75118A58DB84FA04B4A21BFEF81FE', 4, NULL, NULL, 2, NULL, 0, 'Ferment, preferably around 68 F (20 C), for about 3 weeks.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'81B3C40CF0787345BBF99AB6DE3FB0F2', X'47B982BE9377634BB8B3412300D80BE6', X'47B982BE9377634BB8B3412300D80BE6', 0, NULL, NULL, 2, NULL, 0, 'Remove 2-3 outer cabbage leaves for later use. Cut the cabbage half way from the base to the middle.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BC64AF5B9B58A340B75932BAF6ADCAA9', X'47B982BE9377634BB8B3412300D80BE6', X'47B982BE9377634BB8B3412300D80BE6', 1, NULL, NULL, 2, NULL, 0, 'Tear the rest apart.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'ECC64564AF861C4B902218823F2ED979', X'47B982BE9377634BB8B3412300D80BE6', X'47B982BE9377634BB8B3412300D80BE6', 2, NULL, NULL, 2, NULL, 0, 'Split each half into quarters.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A80F540A96157A448679CDA496D29F5D', X'47B982BE9377634BB8B3412300D80BE6', X'47B982BE9377634BB8B3412300D80BE6', 3, NULL, NULL, 2, NULL, 0, 'Sprinkle little salt into the bottom of a container.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9F64798DF50EEB4EADD73F0E5ED6A26E', X'47B982BE9377634BB8B3412300D80BE6', X'47B982BE9377634BB8B3412300D80BE6', 4, NULL, NULL, 2, NULL, 0, 'Place cabbage quarters, cut sides up. Sprinkle more salt on cut cabbage, add lemon slices, kombu, and whole hot peppers.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3709B55473F86E459F04FA8588A13F32', X'47B982BE9377634BB8B3412300D80BE6', X'47B982BE9377634BB8B3412300D80BE6', 5, NULL, NULL, 2, NULL, 0, 'Repeat until all cabbage is firmly packed. Sprinkle with remaining salt.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7EA27FBE162F0F479A1B9A9740259E42', X'47B982BE9377634BB8B3412300D80BE6', X'47B982BE9377634BB8B3412300D80BE6', 6, NULL, NULL, 2, NULL, 0, 'Place a lid or inverted dinner plate on top of cabbage, then add weight on top.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C75343D036E9E1478BAA477832D931AF', X'47B982BE9377634BB8B3412300D80BE6', X'47B982BE9377634BB8B3412300D80BE6', 7, NULL, NULL, 2, NULL, 0, 'There should be liquid over the plate the next day. Ready to eat in 3 days.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C4AB5F4620F21E47B2A718430086E7A4', X'FF26FEC4FC14DB4A92956EBA7DB587FC', X'FF26FEC4FC14DB4A92956EBA7DB587FC', 0, NULL, NULL, 2, NULL, 0, 'Remove 2-3 outer cabbage leaves for later use. Cut the cabbage into 6-8 wedges. Don''t remove the core, just trim off the tough part. The core holds the leaves together.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D74379324C4D1C4C9FB51C65E26FB35D', X'FF26FEC4FC14DB4A92956EBA7DB587FC', X'FF26FEC4FC14DB4A92956EBA7DB587FC', 1, NULL, NULL, 2, NULL, 0, 'Sprinkle a little salt into the bottom of the container or a bowl.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1048352B340A56438D85DD0A8D3C61A1', X'FF26FEC4FC14DB4A92956EBA7DB587FC', X'FF26FEC4FC14DB4A92956EBA7DB587FC', 2, NULL, NULL, 2, NULL, 0, 'Place cabbage wedges, cut sides up. Sprinkle more salt on cabbage.');
