INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F38A8B99A192064BA99BEFB27F3DDE2C', X'2EA341A8F5018C498E569FC54476C199', X'2EA341A8F5018C498E569FC54476C199', 0, NULL, NULL, 2, NULL, 0, 'Trim off outer leaves, cut the root, and split cabbage into separate halves.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EC98A1894CB9C949A44C958FE73E8540', X'2EA341A8F5018C498E569FC54476C199', X'2EA341A8F5018C498E569FC54476C199', 1, NULL, NULL, 2, NULL, 0, 'Make a brine: 3 cups salt to 4 quarts water. Brine the cabbage for 3 hours. Rinse cabbage in fresh water and squeeze the water out, repeat twice more. Drain cabbage for 30 minutes.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'63E66E22FB30A34986B14338BE909046', X'2EA341A8F5018C498E569FC54476C199', X'2EA341A8F5018C498E569FC54476C199', 2, NULL, NULL, 2, NULL, 0, 'Make red pepper paste. Cut radishes thinly into 1 (5 cm) long julienne strips. Coat strips with the paste.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'99A3E5BEA3E2D8469C9890FE0A7C0FAE', X'2EA341A8F5018C498E569FC54476C199', X'2EA341A8F5018C498E569FC54476C199', 3, NULL, NULL, 2, NULL, 0, 'Cut scallions diagonally, dice ginger and crush garlic. Drain pickled baby shrimp.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'988933DA18E23E449ED727A407E6A5C3', X'2EA341A8F5018C498E569FC54476C199', X'2EA341A8F5018C498E569FC54476C199', 4, NULL, NULL, 2, NULL, 0, 'Mix all together.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3A2057BD13F7EF45B7D0FAAB76F54946', X'2EA341A8F5018C498E569FC54476C199', X'2EA341A8F5018C498E569FC54476C199', 5, NULL, NULL, 2, NULL, 0, 'Pack the stuffing between the leaves. Wrap the stuffed cabbage with the outer layer of leaves.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'81AB713F640629488BB3FAF8088E80D3', X'2EA341A8F5018C498E569FC54476C199', X'2EA341A8F5018C498E569FC54476C199', 6, NULL, NULL, 2, NULL, 0, 'Pack stuffed cabbage in a container. Put on top outer leaves that were removed in step #1. Sprinkle with salt. Place weight on top. Ferment 3 days.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E10C2B9F5D789445B9B2A478C03EBB14', X'B0BB5BD6ED84FE49827B4366D406514D', X'B0BB5BD6ED84FE49827B4366D406514D', 0, NULL, NULL, 2, NULL, 0, 'Wash cucumbers. Cut 1/16-inch slice off blossom end and discard, but leave 1/4-inch of stem attached. Place whole cucumbers in suitable 1 gallon container. Add 1/4 cup canning or pickling salt to 2 quarts of water and bring to a boil. Pour over cucumbers. Add suitable cover and weight. Place clean towel over container and keep the temperature at about 70 F. On the third and fifth days, drain salt water and discard. Rinse cucumbers and rescald cover and weight. Return cucumbers to container. Add 1/4 cup salt to 2 quarts fresh water and boil. Pour over cucumbers. Replace cover and weight, and re-cover with clean towel. On the seventh day, drain salt water and discard. Rinse cucumbers and rescald containers, cover, and weight. Slice or strip cucumbers if desired, and return to container. Place celery seed and pickling spices in small cheesecloth bag. Combine 2 cups sugar and 4 cups vinegar in a saucepan. Add spice bag, bring to a boil and pour pickling solution over cucumbers. Add cover and weight, and re-cover with clean towel. On each of the next six days, drain syrup and spice bag and save. Add 1/2 cup sugar each day and bring to a boil in a saucepan. Remove cucumbers and rinse. Scald container, cover, and weight daily. Return cucumbers to container, add boiled syrup, cover, weight, and re-cover with towel. On the 14th day, drain syrup into saucepan. Fill hot sterile pint jars or clean hot quart jars, leaving 1/2-inch headspace. Add 1/2 cup sugar to syrup and bring to boil. Remove spice bag. Pour hot syrup over cucumbers, leaving 1/2-inch headspace. Remove air bubbles and adjust headspace if needed. Wipe rims of jars with a dampened clean paper towel. Adjust lids and process as below:');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4B5309736B99CC4697F893C84AD06C6B', X'B0BB5BD6ED84FE49827B4366D406514D', X'B0BB5BD6ED84FE49827B4366D406514D', 1, NULL, NULL, 2, NULL, 0, 'Can be canned whole, in strips, or in slices.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5F70916EB0FED942A56EC4129EE3C009', X'8071B54F9520644881DF593C2C2F6F93', X'8071B54F9520644881DF593C2C2F6F93', 0, NULL, NULL, 2, NULL, 0, 'Apple mint goes well with pork meat, for example pork chops.');
