INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C0823D5CEDC97340BAE2DB7A3118AE06', X'88DEAA3CBD55FA488775088DFFD78AAF', X'88DEAA3CBD55FA488775088DFFD78AAF', 0, NULL, NULL, 2, NULL, 0, 'Apricot (or other plums) and apple chutney goes well with cold cuts, especially liver sausages and pates.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FA6D3D84B09C614B9F364ED269A2DDBB', X'88DEAA3CBD55FA488775088DFFD78AAF', X'88DEAA3CBD55FA488775088DFFD78AAF', 1, NULL, NULL, 2, NULL, 0, '1. Spice extract. Take a small skillet and add cinnamon stick, cloves, cardamom seeds and 125 ml (1/2 cup). Bring to a boil and simmer for 10 minutes without cover. Drain.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6D7710D4B147AE42B67753499D20787B', X'88DEAA3CBD55FA488775088DFFD78AAF', X'88DEAA3CBD55FA488775088DFFD78AAF', 2, NULL, NULL, 2, NULL, 0, '2. Remove apricot pits. Add  sugar.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A24EB55809BE3D46BBF7F4040C46A75A', X'88DEAA3CBD55FA488775088DFFD78AAF', X'88DEAA3CBD55FA488775088DFFD78AAF', 3, NULL, NULL, 2, NULL, 0, '3. Peel apples, remove cores and seeds and dice finely. Squeeze lemon to release juice. Grate lemon zest. Grate or finely chop fresh ginger.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0E2EC188DE84DE45B08B1F5EF489F8C8', X'88DEAA3CBD55FA488775088DFFD78AAF', X'88DEAA3CBD55FA488775088DFFD78AAF', 4, NULL, NULL, 2, NULL, 0, '4. Heat olive oil and butter in a medium size skillet. Add onion and cook until glassy. Add apricots, apples, vinegar, and lemon juice and bring to a boil. Add spice extract, curry powder, ginger, lemon zest and remaining sugar and cook for 30 minutes over low heat. At the end of cooking add garlic.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3B79AB43F2783D4390857768715B05CE', X'88DEAA3CBD55FA488775088DFFD78AAF', X'88DEAA3CBD55FA488775088DFFD78AAF', 5, NULL, NULL, 2, NULL, 0, '5. Fill glass jars to within  inch from top, remove air bubbles and adjust lids.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E3932BD9040BDF4E9A4E981C2EAB0936', X'88DEAA3CBD55FA488775088DFFD78AAF', X'88DEAA3CBD55FA488775088DFFD78AAF', 6, NULL, NULL, 2, NULL, 0, '6. Process for 10 minutes in water bath canner.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3CCA137187BD07418E0DADE8320CC512', X'88DEAA3CBD55FA488775088DFFD78AAF', X'88DEAA3CBD55FA488775088DFFD78AAF', 7, NULL, NULL, 2, NULL, 0, '7. Remove from canner and let stand for 12 hours.');
