INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0D31E24E85FAAA439D6CA6C0F64CF187', X'9CA4ED38BDBE564590762049A6B90858', X'9CA4ED38BDBE564590762049A6B90858', 8, NULL, NULL, 2, NULL, 0, 'Switch the heat off, add horseradish and mix all together.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6E1F4BAFA66BD847B5606A7B385C6173', X'9CA4ED38BDBE564590762049A6B90858', X'9CA4ED38BDBE564590762049A6B90858', 9, NULL, NULL, 2, NULL, 0, 'Fill hot relish into 1/2 pint jars. Remove air bubbles. Tighten lids.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E6E838BEF9D6C049A1D25F6ED7FFDC18', X'9CA4ED38BDBE564590762049A6B90858', X'9CA4ED38BDBE564590762049A6B90858', 10, NULL, NULL, 2, NULL, 0, 'Place jars in a canner and bring water to a boil. Process at 212 F, 100 C for 10 minutes.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4EFDC18F43A89A42978E232CB9B7623F', X'8FEB3BEFEC8A444A823106226E1C63F6', X'8FEB3BEFEC8A444A823106226E1C63F6', 0, NULL, NULL, 2, NULL, 0, 'Wash cucumbers. Cut 1/16-inch off blossom end and discard. Cut into 3/16-inch slices. Combine cucumbers and onions in a large bowl. Add salt. Cover with 2 inches crushed or cubed ice. Refrigerate 3 to 4 hours, adding more ice as needed. Combine remaining ingredients in a large pot. Boil 10 minutes. Drain and add cucumbers and onions and slowly reheat to boiling. Fill hot pint jars with slices and cooking syrup, leaving 1/2-inch headspace. Remove air bubbles and adjust headspace if needed. Wipe rims of jars with a dampened clean paper towel. Adjust lids and process as below:');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'569D89682CB20C4AB2B170EC2F97A866', X'EC46F274B132654DA7634AFDB370C405', X'EC46F274B132654DA7634AFDB370C405', 0, NULL, NULL, 2, NULL, 0, 'Place cabbage, cucumbers, tomatoes and onions in brine (1 gal of water & 2 cups salt) overnight. Drain.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9A2BC75ED5279C4A9123E16D0AF408D2', X'EC46F274B132654DA7634AFDB370C405', X'EC46F274B132654DA7634AFDB370C405', 1, NULL, NULL, 2, NULL, 0, 'Mix all vegetables with remaining ingredients and boil for 10 minutes.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D5139A5B06C587428BA96FB930973158', X'EC46F274B132654DA7634AFDB370C405', X'EC46F274B132654DA7634AFDB370C405', 2, NULL, NULL, 2, NULL, 0, 'Pack hot into hot pint jars, leaving 1/2 inch headspace. Remove air bubbles, wipe jar rims. Place lids and screw bands. Process 10 minutes in a Boiling Water Bath.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'56802021DA622A4C9416A52CCA5FC83E', X'2874642286856F4694A39934261EA1D3', X'2874642286856F4694A39934261EA1D3', 0, NULL, NULL, 2, NULL, 0, 'Add cucumbers, peppers, onions, salt, and ice to water and let stand 4 hours. Drain and re-cover vegetables with fresh ice water for another hour. Drain again. Combine spices in a spice or cheesecloth bag. Add spices to sugar and vinegar. Heat to boiling and pour mixture over vegetables. Cover and refrigerate 24 hours. Heat mixture to boiling and fill hot into hot jars, leaving 1/2 inch headspace. Remove air bubbles and adjust headspace if needed. Wipe rims of jars with a dampened clean paper towel. Adjust lids and process.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2260617350213948A9191DC797B0B288', X'943BC19DEF9A864A88D44BA7FCE8EDE1', X'943BC19DEF9A864A88D44BA7FCE8EDE1', 0, NULL, NULL, 2, NULL, 0, 'Cucumber relish is very popular with sausages, look what carries every hotdog vendor in New York: ketchup, mustard, sauerkraut and cucumber relish.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9D55F35D95A0834A8AFB159E0EE087AD', X'943BC19DEF9A864A88D44BA7FCE8EDE1', X'943BC19DEF9A864A88D44BA7FCE8EDE1', 1, NULL, NULL, 2, NULL, 0, 'Except dill, place all ingredients in pot, mix and leave for 20 minutes.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EE946C2EC26B3C4BB5D49175AEA3D65D', X'943BC19DEF9A864A88D44BA7FCE8EDE1', X'943BC19DEF9A864A88D44BA7FCE8EDE1', 2, NULL, NULL, 2, NULL, 0, 'Bring to a boil and simmer until mixture is thicker. Add chopped dill at the end of cooking process.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'412F6224727F5741B1D5AFAB809B7492', X'943BC19DEF9A864A88D44BA7FCE8EDE1', X'943BC19DEF9A864A88D44BA7FCE8EDE1', 3, NULL, NULL, 2, NULL, 0, 'Fill hot relish into 1/2 pint jars. Remove air bubbles. Tighten lids.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1306B9A7D5247740B64CEA7251C9C8AE', X'943BC19DEF9A864A88D44BA7FCE8EDE1', X'943BC19DEF9A864A88D44BA7FCE8EDE1', 4, NULL, NULL, 2, NULL, 0, 'Place jars in a canner and bring water to a boil. Process at 212 F, 100 C for 10 minutes.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C07A2E8D37199F41B044597AC3EB251B', X'090173740072F743807E63F3D89B7D79', X'090173740072F743807E63F3D89B7D79', 0, NULL, NULL, 2, NULL, 0, 'Wash cucumbers. Cut 1/16-inch slice off blossom end and discard. Leave 1/4-inch of stem attached. Place half of dill and spices on bottom of a clean, suitable container. Add cucumbers, remaining dill, and spices. Dissolve salt in vinegar and water and pour over cucumbers. Add suitable cover and weight. Store where temperature is between 70 and 75 F for about 3 to 4 weeks while fermenting. Temperatures of 55 to 65 F are acceptable, but the fermentation will take 5 to 6 weeks. Avoid temperatures above 80 F, or pickles will become too soft during fermentation. Fermenting pickles cure slowly. Check the container several times a week and promptly remove surface scum or mold.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D98683196EED1D4EA9AD937D4E540321', X'090173740072F743807E63F3D89B7D79', X'090173740072F743807E63F3D89B7D79', 1, NULL, NULL, 2, NULL, 0, 'CAUTION If the pickles become soft, slimy, or develop a disagreeable odor, discard them. Fully fermented pickles may be stored in the original container for about 4 to 6 months, provided they are refrigerated and surface scum and molds are removed regularly. Canning fully fermented pickles is a better way to store them. To can them, pour the brine into a pan, heat slowly to a boil, and simmer 5 minutes. Filter brine through paper coffee filters to reduce cloudiness, if desired. Fill hot jar with pickles and hot brine, leaving 1/2-inch headspace. Remove air bubbles and adjust headspace if needed. Wipe rims of jars with a dampened clean paper towel. Adjust lids and process as below:');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'42E3D5990BDABD478BA0B29E81CBFAFF', X'090173740072F743807E63F3D89B7D79', X'090173740072F743807E63F3D89B7D79', 2, NULL, NULL, 2, NULL, 0, 'Use the following quantities for each gallon capacity of your container.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B568EB6672C65447BC10FEC0F104851C', X'C74F12D3A279514CA900C95E562D474E', X'C74F12D3A279514CA900C95E562D474E', 0, NULL, NULL, 2, NULL, 0, 'Lithuanian Beet Cooler Soup is made of beet stocks, beets, dill, scallions, cucumbers, plain yoghurt or sour cream. It looks very pretty and is delicious. The soup is served cold and originally was made with river crayfish. As the soup becomes famous and popular, new cooks start to put anything inside thinking they have created a new masterpiece, as long as the characteristic pink color of the soup is preserved.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A2B61BECADDC9C4E80D8ABEB75520C70', X'C74F12D3A279514CA900C95E562D474E', X'C74F12D3A279514CA900C95E562D474E', 1, NULL, NULL, 2, NULL, 0, 'NOTE Ukrainian borscht is another known beet soup.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FCE79693590A0C45A33E6E763FE4D24C', X'C74F12D3A279514CA900C95E562D474E', X'C74F12D3A279514CA900C95E562D474E', 2, NULL, NULL, 2, NULL, 0, 'Lithuanian Beet Cooler Soup can be made from Fermented Borscht.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'79A1A51FCAF1AF449F4426AB774442FA', X'C74F12D3A279514CA900C95E562D474E', X'C74F12D3A279514CA900C95E562D474E', 3, NULL, NULL, 2, NULL, 0, 'Wash and separate beets from stalks. Peel beets and dice finely. Chop stalks with leaves into  inch pieces. Use of leaves is optional, often people take only half of them.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E8B49E8B8F263045BC1B21BB7182025E', X'C74F12D3A279514CA900C95E562D474E', X'C74F12D3A279514CA900C95E562D474E', 4, NULL, NULL, 2, NULL, 0, 'Place chopped beets and stems in pot, add lemon juice, add water just over the beets level. Cook overlow heat for 15 minutes. Let it cool.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F6800325592F214C9864A6E971E0AD40', X'C74F12D3A279514CA900C95E562D474E', X'C74F12D3A279514CA900C95E562D474E', 5, NULL, NULL, 2, NULL, 0, 'Peel cucumbers and dice finely. Place into a mixing bowl. Finely cut scallions and dill. Add to cucumbers. Add yoghurt and mix together.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'401FDBBF2602C9419224019941998D32', X'C74F12D3A279514CA900C95E562D474E', X'C74F12D3A279514CA900C95E562D474E', 6, NULL, NULL, 2, NULL, 0, 'Add cooked beets and mix everything together.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'688B0AA6ECD528439DE4A3137A2FB986', X'C74F12D3A279514CA900C95E562D474E', X'C74F12D3A279514CA900C95E562D474E', 7, NULL, NULL, 2, NULL, 0, 'Pour into serving bowls and garnish with sliced hard boiled eggs.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'18124A3A46435E44A21BC4E548E31EBC', X'481CF62380CFB144807B4EB9BE3D5A7A', X'481CF62380CFB144807B4EB9BE3D5A7A', 0, NULL, NULL, 2, NULL, 0, 'Wash, peel, and separate mango flesh from seed. Chop mango flesh into chunks and puree in blender or food processor until smooth. Combine all ingredients in a 6 to 8 quart Dutch oven or stockpot and heat on medium high heat, with continuous stirring, until the mixture reaches 200 F. The mixture will sputter as it is being heated, so be sure to wear gloves or oven mitts to avoid burning skin. Fill hot sauce into hot half pint jars, leaving 1/4 inch headspace. Remove air bubbles and adjust headspace if needed. Wipe rims of jars with a dampened clean paper towel. Adjust lids and process.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2EF81807D42B944BAB864B3D43A4E246', X'481CF62380CFB144807B4EB9BE3D5A7A', X'481CF62380CFB144807B4EB9BE3D5A7A', 1, NULL, NULL, 2, NULL, 0, 'CAUTION Handling green mangoes may irritate the skin of some people in the same way as poison ivy. (They belong to the same plant family.) To avoid this reaction, wear plastic or rubber gloves while working with raw green mango. Do not touch your face, lips or eyes after touching or cutting raw green mangoes until all traces are washed away. Yield: About 6 half pint jars.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D1C92808C5766B449FCFFD8F1EF27FB8', X'5AF7D2FFA9886C40B6C96F1B178FA93E', X'5AF7D2FFA9886C40B6C96F1B178FA93E', 0, NULL, NULL, 2, NULL, 0, 'This recipe may be made with any combination of peaches, nectarines, apricots, and plums.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C50FF99CB9565D4C964B9A71C4E2DB00', X'5AF7D2FFA9886C40B6C96F1B178FA93E', X'5AF7D2FFA9886C40B6C96F1B178FA93E', 1, NULL, NULL, 2, NULL, 0, 'This recipe may be made without sugar or with up to 2 cups, according to taste or preference. Non-nutritive sweeteners may be added. If aspartame (a low-calorie nutritive sweetener) is used, the sweetening power of aspartame may be lost within 3 to 4 weeks.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F6B189CF36CC944E835EE551FF3741E4', X'5AF7D2FFA9886C40B6C96F1B178FA93E', X'5AF7D2FFA9886C40B6C96F1B178FA93E', 2, NULL, NULL, 2, NULL, 0, 'Yield: 5 to 6 half-pints');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B0A4715885EFA24384C5DD1F5808B509', X'5AF7D2FFA9886C40B6C96F1B178FA93E', X'5AF7D2FFA9886C40B6C96F1B178FA93E', 3, NULL, NULL, 2, NULL, 0, 'Procedure: Thoroughly wash 4 to 6 pounds of firm, ripe peaches. Drain well. Peel and remove pits. Grind fruit flesh with a medium or coarse blade, or crush with a fork (do not use a blender). Place ground or crushed fruit in a 2 quart saucepan. Heat slowly to release juice, stirring constantly, until fruit is tender. Place cooked fruit in a jelly bag or strainer lined with four layers of cheesecloth. Allow juice to drip about 15 minutes. Save the juice for jelly or other uses. Measure 4 cups of drained fruit pulp for making spread. Combine the 4 cups of pulp, pineapple, and lemon juice in a 4 quart saucepan. Add up to 2 cups of sugar, if desired, and mix well. Heat and boil gently for 10 to 15 minutes, stirring enough to prevent sticking. Fill hot jars quickly, leaving 1/4 inch headspace. Wipe rims of jars with a dampened clean paper towel. Adjust lids and process.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'63F84532352B064A89E2D603651A9BA9', X'E54C91373CB44845804283A91408F885', X'E54C91373CB44845804283A91408F885', 0, NULL, NULL, 2, NULL, 0, 'Procedure: Crush apples and pears in a large saucepan and stir in cinnamon. Thoroughly mix sugar and lemon juice with fruits and bring to a boil over high heat, stirring constantly. Immediately stir in pectin. Bring to a full rolling boil and boil hard 1 minute, stirring constantly. Remove from heat, quickly skim off foam, and fill sterile jars (see page 1-14) leaving 1/4-inch headspace. Wipe rims of jars with a dampened clean paper towel. Adjust lids and process.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'48D31FD612AEB44B9DA38BCD6C6EAABF', X'C33A5F80CDA3EC4C93BA97AE8488C121', X'C33A5F80CDA3EC4C93BA97AE8488C121', 0, NULL, NULL, 2, NULL, 0, 'Wash, chop, and combine vegetables with 1/2 cup salt. Cover with hot water and let stand 12 hours. Drain and press in a clean white cloth to remove all possible liquid. Tie spices loosely in a spice bag and add to combined vinegar and brown sugar and heat to a boil in a sauce pan. Add vegetables and boil gently 30 minutes or until the volume of the mixture is reduced by one half. Remove spice bag. Fill hot sterile jars with hot mixture, leaving 1/2 inch headspace. Remove air bubbles and adjust headspace if needed. Wipe rims of jars with a dampened clean paper towel. Adjust lids and process.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'33AAE3D54E0F2F4F86A3F2116D165C0D', X'C2A275E55C5E5F43B7B07C1E3532712C', X'C2A275E55C5E5F43B7B07C1E3532712C', 0, NULL, NULL, 2, NULL, 0, 'Trim off beet tops, leaving 1 inch of stem and roots to prevent bleeding of color. Wash thoroughly. Sort for size. Cover similar sizes together with boiling water and cook until tender (about 25 to 30 minutes). Caution: Drain and discard liquid. Cool beets. Trim off roots and stems and slip off skins. Slice into 1/4 inch slices. Peel and thinly slice onions. Combine vinegar, salt, sugar, and fresh water. Put spices in cheesecloth bag and add to vinegar mixture. Bring to a boil. Add beets and onions. Simmer 5 minutes. Remove spice bag. Fill hot jars with beets and onions, leaving 1/2 inch headspace. Add hot vinegar solution, allowing 1/2 inch headspace. Remove air bubbles and adjust headspace if needed. Wipe rims of jars with a dampened clean paper towel. Adjust lids and process.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'13F77CE79DB65A4084119BA4CC60A20F', X'C2A275E55C5E5F43B7B07C1E3532712C', X'C2A275E55C5E5F43B7B07C1E3532712C', 1, NULL, NULL, 2, NULL, 0, 'Variation: For pickled whole baby beets, follow above directions but use beets that are 1 to 1-1/2 inches in diameter. Pack whole; do not slice. Onions may be omitted.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0C349F2D1D7DD94E81D9C61F65A82676', X'82DD1FF08964BC44BB0A147AEC090B11', X'82DD1FF08964BC44BB0A147AEC090B11', 0, NULL, NULL, 2, NULL, 0, 'Wash and peel carrots. Cut into rounds that are approximately 1/2 inch thick. Combine vinegar, water, sugar and canning salt in an 8 quart Dutch oven or stockpot. Bring to a boil and boil 3 minutes. Add carrots and bring back to a boil. Then reduce heat to a simmer and heat until half cooked (about 10 minutes). Meanwhile, place 2 teaspoons mustard seed and 1 teaspoon celery seed into each empty hot pint jar. Fill jars with hot carrots, leaving 1 inch headspace. Fill with hot pickling liquid, leaving 1/2 inch headspace. Remove air bubbles and adjust headspace if needed. Wipe rims of jars with a dampened clean paper towel. Adjust lids and process.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'01C857360A2E0F44BEE2446C34F2467A', X'AD8EE8FBA4829944B7E708C3177AAA7D', X'AD8EE8FBA4829944B7E708C3177AAA7D', 0, NULL, NULL, 2, NULL, 0, 'The pungency of fresh horseradish fades within 1 to 2 months, even when refrigerated. Therefore, make only small quantities at a time. Wash horseradish roots thoroughly and peel off brown outer skin. The peeled roots may be grated in a food processor or cut into small cubes and put through a food grinder. Combine ingredients and fill into glass jars, leaving 1/4 inch headspace. Seal jars tightly and store in a refrigerator.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A1C87B9AC21FF749BEF4184B43E4CB4B', X'A5BFBBC0931C66428FAF870BFCCF0021', X'A5BFBBC0931C66428FAF870BFCCF0021', 0, NULL, NULL, 2, NULL, 0, 'Wash cucumbers. Cut 1/16-inch slice off blossom end and discard, but leave 1/4 inch of stem attached. Dissolve 3/4 cup salt in 2 gallons water. Pour over cucumbers and let stand 12 hours. Drain. Combine vinegar, 1/2 cup salt, sugar, and 2 quarts water. Add mixed pickling spices tied in a clean white cloth. Heat to boiling. Fill hot jars with cucumbers. Add 1 tsp mustard seed and 1-1/2 heads fresh dill per pint. Cover with boiling pickling solution, leaving 1/2-inch headspace. Remove air bubbles and adjust headspace if needed. Wipe rims of jars with a dampened clean paper towel. Adjust lids and process as below:');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4870307582EF7B42916229E26769E608', X'84DF018E528B0B4895DF45E1973A6075', X'84DF018E528B0B4895DF45E1973A6075', 0, NULL, NULL, 2, NULL, 0, 'Wash cucumbers. Cut 1/16-inch off blossom end and discard, but leave 1/4 inch of stem attached. Slice or cut in strips, if desired. Place in bowl and sprinkle with 1/3 cup salt. Cover with 2 inches of crushed or cubed ice. Refrigerate 3 to 4 hours. Add more ice as needed. Drain well. Combine sugar, vinegar, celery seed, allspice, and mustard seed in 6-quart kettle. Heat to boiling.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'202FCCD294C36E45BBB71E439DD2A53D', X'84DF018E528B0B4895DF45E1973A6075', X'84DF018E528B0B4895DF45E1973A6075', 1, NULL, NULL, 2, NULL, 0, 'Add cucumbers and heat slowly until vinegar solution returns to boil. Stir occasionally to make sure mixture heats evenly. Fill sterile jars, leaving 1/2-inch headspace.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'93CD8513C258E349A152B99D0D82009D', X'84DF018E528B0B4895DF45E1973A6075', X'84DF018E528B0B4895DF45E1973A6075', 2, NULL, NULL, 2, NULL, 0, 'Fill hot jars, leaving 1/2-inch headspace. Add hot pickling syrup, leaving 1/2-inch headspace.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1ED2E1971C73D04C958A8F51EA51DF6C', X'84DF018E528B0B4895DF45E1973A6075', X'84DF018E528B0B4895DF45E1973A6075', 3, NULL, NULL, 2, NULL, 0, 'May be canned as either strips or slices');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'570278FA10830E4FAE9B4343F87F52E9', X'84DF018E528B0B4895DF45E1973A6075', X'84DF018E528B0B4895DF45E1973A6075', 4, NULL, NULL, 2, NULL, 0, 'Remove air bubbles and adjust headspace if needed. Wipe rims of jars with a dampened clean paper towel. Adjust lids and process as below:');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0F14694B8A9B314B8FE4321B9A8F6910', X'84DF018E528B0B4895DF45E1973A6075', X'84DF018E528B0B4895DF45E1973A6075', 5, NULL, NULL, 2, NULL, 0, 'Variation for firmer pickles: Wash cucumbers. Cut 1/16-inch off blossom end and discard, but leave 1/4-inch of stem attached. Slice or strip cucumbers. Mix 1 cup pickling lime and 1/2 cup salt to 1 gallon water in a 2 to 3 gallon crock or enamelware container.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'736BFBFB99AC3F418BCBA764223F89A4', X'84DF018E528B0B4895DF45E1973A6075', X'84DF018E528B0B4895DF45E1973A6075', 6, NULL, NULL, 2, NULL, 0, 'CAUTION Avoid inhaling lime dust while mixing the lime water solution. Soak cucumber slices or strips in lime water solution for 12 to 24 hours, stirring occasionally. Remove from lime solution and rinse and resoak 1 hour in fresh cold water. Repeat the rinsing and resoaking two more times. Handle carefully because slices or strips will be brittle. Drain well.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'10A6D6C18BDE9C4A8013508C946B86B0', X'84DF018E528B0B4895DF45E1973A6075', X'84DF018E528B0B4895DF45E1973A6075', 7, NULL, NULL, 2, NULL, 0, 'NOTES');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3E6CBF942E3B6740B4D27A64E63F6237', X'0618661DEFED504AAA5792A5731E1318', X'0618661DEFED504AAA5792A5731E1318', 0, NULL, NULL, 2, NULL, 0, 'Wash cucumbers. Cut 1/16-inch slice off blossom end and discard, but leave 1/4-inch of stem attached. Place cucumbers in large container and cover with boiling water. Six to 8 hours later, and again on the second day, drain and cover with 6 quarts of fresh boiling water containing 1/4 cup salt. On the third day, drain and prick cucumbers with a table fork. Combine and bring to a boil 3 cups vinegar, 3 cups sugar, turmeric, and spices. Pour over cucumbers. Six to 8 hours later, drain and save the pickling syrup. Add another 2 cups each of sugar and vinegar and reheat to boil. Pour over pickles. On the fourth day, drain and save syrup. Add another 2 cups sugar and 1 cup vinegar. Heat to boiling and pour over pickles. Drain and save pickling syrup 6 to 8 hours later. Add 1 cup sugar and 2 tsp vanilla and heat to boiling. Fill hot sterile pint jars with pickles and cover with hot syrup, leaving 1/2-inch headspace. Remove air bubbles and adjust headspace if needed. Wipe rims of jars with a dampened clean paper towel. Adjust lids and process as below:');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C7E19680942BF94DBD9A04DEDCD6A90B', X'89BD12E6EC945045B119E311E5F237DC', X'89BD12E6EC945045B119E311E5F237DC', 0, NULL, NULL, 2, NULL, 0, 'Tomato relish goes well with dark meats, especially beef. It adds strong flavor to wild game meat and can be added to hamburgers as well.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7181490F507EB24E9554D0FBD8A8EF94', X'89BD12E6EC945045B119E311E5F237DC', X'89BD12E6EC945045B119E311E5F237DC', 1, NULL, NULL, 2, NULL, 0, 'Place tomatoes briefly in hot water, rinse with cold water and peel the skin off. Dice into small cubes.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7D68828B031C984D81249B91494E1064', X'89BD12E6EC945045B119E311E5F237DC', X'89BD12E6EC945045B119E311E5F237DC', 2, NULL, NULL, 2, NULL, 0, 'Peel apples, remove blossom ends and cores. Dice finely.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FE0A43A16B553148A542CD72F980F85D', X'89BD12E6EC945045B119E311E5F237DC', X'89BD12E6EC945045B119E311E5F237DC', 3, NULL, NULL, 2, NULL, 0, 'Dice finely red peppers and onions. Smash garlic cloves.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'91976BAF200E7043B9B5B8838F49A3DF', X'89BD12E6EC945045B119E311E5F237DC', X'89BD12E6EC945045B119E311E5F237DC', 4, NULL, NULL, 2, NULL, 0, 'Place above described materials in skillet, add vinegar and tomato sauce. Bring to a boil and cook on low heat for 30 minutes under cover.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'41607C099F288C4E9E7CF39BF0695463', X'89BD12E6EC945045B119E311E5F237DC', X'89BD12E6EC945045B119E311E5F237DC', 5, NULL, NULL, 2, NULL, 0, 'Add all remaining ingredients, bring to a boil and cook on low heat for 5 more minutes.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C24E0DBDA847F141BD9440193E126FC2', X'89BD12E6EC945045B119E311E5F237DC', X'89BD12E6EC945045B119E311E5F237DC', 6, NULL, NULL, 2, NULL, 0, 'Fill hot relish into  pint jars. Remove air bubbles. Tighten lids.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'45D71294344F4D499775BA0079971772', X'89BD12E6EC945045B119E311E5F237DC', X'89BD12E6EC945045B119E311E5F237DC', 7, NULL, NULL, 2, NULL, 0, 'Place jars in a canner and bring water to a boil. Process at 212 F, 100 C for 15 minutes.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CBBB2B7D51364A4E97F864136728EB5A', X'89BD12E6EC945045B119E311E5F237DC', X'89BD12E6EC945045B119E311E5F237DC', 8, NULL, NULL, 2, NULL, 0, 'Remove jars from the canner and let stand for 12 hours.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AD8206A5CEA4AE40B3C751AC1AC3F568', X'FD4CF41EF41AFD45A4B4580E5AB246D6', X'FD4CF41EF41AFD45A4B4580E5AB246D6', 0, NULL, NULL, 2, NULL, 0, 'White button mushroom and the large portabella mushrooms are the varieties of Agaricus bisporusspecies.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'977C6B94CE3FBE4E8D3D030DC00B32DA', X'FD4CF41EF41AFD45A4B4580E5AB246D6', X'FD4CF41EF41AFD45A4B4580E5AB246D6', 1, NULL, NULL, 2, NULL, 0, 'Wash and clean mushrooms. Cook in water for 10 minutes adding 1 teaspoon of salt. Drain.');
