INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4A577C2CA261DE4DA64230150F01EC66', X'CCB3671054E4CA47BA767806128D1D7B', X'CCB3671054E4CA47BA767806128D1D7B', 0, NULL, NULL, 2, NULL, 0, 'The same marinade may be used for pickling different types of wild mushrooms. Small mushrooms can be pickled whole, large mushrooms must be halved or quartered.');
