INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EE6D931204ADE44EB7B8D5721F7B5446', X'CCB3671054E4CA47BA767806128D1D7B', X'CCB3671054E4CA47BA767806128D1D7B', 5, NULL, NULL, 2, NULL, 0, 'Pour hot marinade into each jar to cover mushrooms. Leave 1/2 inch headspace. Remove air bubbles and adjust lids.');
