INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BDE2524FA3BF24468674C562235DFA63', X'C087FF02DABC154FA7C2A34F556424BD', X'C087FF02DABC154FA7C2A34F556424BD', 0, NULL, NULL, 2, NULL, 0, 'Use electric blender and eliminate need for pressing or sieving.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2271C125E68A8747B30D642250D27A44', X'C087FF02DABC154FA7C2A34F556424BD', X'C087FF02DABC154FA7C2A34F556424BD', 1, NULL, NULL, 2, NULL, 0, 'Yield: About 9 pints');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0BDF689BD1B4594FB1DCEDE9577D4EA9', X'C087FF02DABC154FA7C2A34F556424BD', X'C087FF02DABC154FA7C2A34F556424BD', 2, NULL, NULL, 2, NULL, 0, 'Procedure: Wash tomatoes and dip in boiling water for 30 to 60 seconds or until skins split. Then dip in cold water, slip off skins, core, and quarter. Remove seeds from peppers and slice into strips. Peel and quarter onions. Blend tomatoes, peppers, and onions at high speed for 5 seconds in electric blender. Pour into a 3- to 4-gallon stock pot or large kettle and heat. Boil gently 60 minutes, stirring frequently. Add vinegar, sugar, salt, and a spice bag containing dry mustard, red pepper, and other spices. Continue boiling and stirring until volume is reduced one-half and ketchup rounds up on a spoon with no separation of liquid and solids. Remove spice bag and fill hot jars, leaving 1/8-inch headspace. Remove air bubbles and adjust head- space if needed. Wipe rims of jars with a dampened clean paper towel. Adjust lids and follow process times for regular regular ketchup.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0F787E05C97B9B4893ED31A9145F9EA9', X'E458E4345981D043BE8B7C53ACDE69B3', X'E458E4345981D043BE8B7C53ACDE69B3', 0, NULL, NULL, 2, NULL, 0, 'Yield: About 5 pints');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'13433EDA0E76E9429E14D2F555BF0142', X'E458E4345981D043BE8B7C53ACDE69B3', X'E458E4345981D043BE8B7C53ACDE69B3', 1, NULL, NULL, 2, NULL, 0, 'CAUTION Wear plastic or rubber gloves and do not touch your face while han- dling or cutting hot peppers. If you do not wear gloves, wash hands thoroughly with soap and water before touching your face or eyes.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5DB423AFF8D42D4CA146482632D7BE52', X'E458E4345981D043BE8B7C53ACDE69B3', X'E458E4345981D043BE8B7C53ACDE69B3', 2, NULL, NULL, 2, NULL, 0, 'Procedure: Wash, trim and slice peppers and onions into rings, using a mandolin slicer or a food processor. In a 10-quart Dutch oven or stockpot, mix together all ingredients. Bring to a boil and boil 1 hour. Reduce heat slightly and simmer 1 additional hour. Turn heat off, and cool mixture slightly. Puree vegetables in a blender about 2 minutes per blender batch. Return pureed mixture to stockpot and bring carefully just to a boil. (The mixture will start to spatter as it gets close to boiling; heat slowly while stirring constantly, being careful not to get burned by splashing sauce.) Turn off heat. Fill hot sauce into hot pint jars, leaving 1/2-inch headspace. Remove air bubbles and adjust headspace if needed. Wipe rims of jars with a dampened clean paper towel. Adjust lids and process.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F48E940676D1E0449193FCCCBA3687D8', X'941E45BA2E9834439C94B5917945273E', X'941E45BA2E9834439C94B5917945273E', 0, NULL, NULL, 2, NULL, 0, 'Yield: About 6 to 8 pints');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E269A062D4FA1046A372C74AC980C605', X'941E45BA2E9834439C94B5917945273E', X'941E45BA2E9834439C94B5917945273E', 1, NULL, NULL, 2, NULL, 0, 'CAUTION Wear plastic or rubber gloves and do not touch your face while handling or cutting hot peppers. If you do not wear gloves, wash hands thoroughly with soap and water before touching your face or eyes.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CC8C727B687CD74F9C60F691D6121FAC', X'941E45BA2E9834439C94B5917945273E', X'941E45BA2E9834439C94B5917945273E', 2, NULL, NULL, 2, NULL, 0, 'Peel and prepare peppers');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5C40644A43A3D24EABE0B8B89AEBAD02', X'941E45BA2E9834439C94B5917945273E', X'941E45BA2E9834439C94B5917945273E', 3, NULL, NULL, 2, NULL, 0, 'Wash and dry peppers; slit each pepper along the side to allow steam to escape. Blister skins using one of these two methods:');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7F6AE21514C1EE45BC9738CAE5363FC2', X'941E45BA2E9834439C94B5917945273E', X'941E45BA2E9834439C94B5917945273E', 4, NULL, NULL, 2, NULL, 0, 'Oven or broiler method to blister skins - Place peppers in a hot oven (400 F) or under a broiler for 6 to 8 minutes until skins blister.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F6876A89F2CDAD4F9C9C15408A7F10B0', X'941E45BA2E9834439C94B5917945273E', X'941E45BA2E9834439C94B5917945273E', 5, NULL, NULL, 2, NULL, 0, 'Range-top method to blister skins - Cover hot burner (either gas or electric) with heavy wire mesh. Place peppers on burner for several minutes until skins blister.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'633BAB1AA49DDC4A96C07BE2CE857F94', X'941E45BA2E9834439C94B5917945273E', X'941E45BA2E9834439C94B5917945273E', 6, NULL, NULL, 2, NULL, 0, 'To peel, after blistering skins, place peppers in a pan and cover with a damp cloth. (This will make peeling the peppers easier.) Cool several minutes; peel off skins. Discard seeds and chop.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5798448B066B674F861BC9C92D64B264', X'941E45BA2E9834439C94B5917945273E', X'941E45BA2E9834439C94B5917945273E', 7, NULL, NULL, 2, NULL, 0, 'Procedure: Wash tomatoes and dip in boiling water for 30 to 60 seconds or until skins split. Dip in cold water, slip off skins, and remove cores. Coarsely chop tomatoes and combine them with chopped peppers, onions, and remaining ingredients in a large saucepan. Heat to boil, reduce heat and simmer 10 minutes. Fill hot jars, leaving 1/2-inch headspace. Remove air bubbles and adjust headspace if needed. Wipe rims of jars with a dampened clean paper towel. Adjust lids and process.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'16746FD37A046D47B861E05C7C27EEA0', X'8E6E941467824A40BA51D6B12A0C7D90', X'8E6E941467824A40BA51D6B12A0C7D90', 0, NULL, NULL, 2, NULL, 0, 'Yield: About 7 to 9 pints');
