INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'95FACBA34497954DA408D31ADB824776', X'8E6E941467824A40BA51D6B12A0C7D90', X'8E6E941467824A40BA51D6B12A0C7D90', 2, NULL, NULL, 2, NULL, 0, 'Peel and prepare peppers');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'513C9037706BD844AF3F8DBE86273DCD', X'8E6E941467824A40BA51D6B12A0C7D90', X'8E6E941467824A40BA51D6B12A0C7D90', 3, NULL, NULL, 2, NULL, 0, 'Wash and dry peppers; slit each pepper along the side to allow steam to escape. Blister skins using one of these two methods:');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'29539CEAD3D43547A8FBCD3CF751CD07', X'8E6E941467824A40BA51D6B12A0C7D90', X'8E6E941467824A40BA51D6B12A0C7D90', 4, NULL, NULL, 2, NULL, 0, 'Oven or broiler method to blister skins - Place peppers in a hot oven (400 F) or under a broiler for 6 to 8 minutes until skins blister.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'495B3AC70EFEC84AA7D3BFDD45AE7600', X'8E6E941467824A40BA51D6B12A0C7D90', X'8E6E941467824A40BA51D6B12A0C7D90', 5, NULL, NULL, 2, NULL, 0, 'Range-top method to blister skins - Cover hot burner (either gas or electric) with heavy wire mesh. Place peppers on burner for several minutes until skins blister.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EAD700C5C49EC24FACC350C81C9D082F', X'8E6E941467824A40BA51D6B12A0C7D90', X'8E6E941467824A40BA51D6B12A0C7D90', 6, NULL, NULL, 2, NULL, 0, 'To peel, after blistering skins, place peppers in a pan and cover with a damp cloth. (This will make peeling the peppers easier.) Cool several minutes; peel off skins. Discard seeds and chop.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E079EB4B7A116843A43C1D825FF96ADD', X'8E6E941467824A40BA51D6B12A0C7D90', X'8E6E941467824A40BA51D6B12A0C7D90', 7, NULL, NULL, 2, NULL, 0, 'Procedure: Wash tomatoes and dip in boiling water for 30 to 60 seconds or until skins split. Dip in cold water, slip off skins, and remove cores. Combine ingredients in a large saucepan. Heat to a boil and simmer 10 minutes. Fill hot salsa into hot pint jars, leaving 1/2-inch headspace. Remove air bubbles and adjust headspace if needed. Wipe rims of jars with a dampened clean paper towel. Adjust lids and process.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'62F828CADFF8DD4B9E5716E664FF0E92', X'FEF0501079E4F64C9D95293BCF5B13C8', X'FEF0501079E4F64C9D95293BCF5B13C8', 0, NULL, NULL, 2, NULL, 0, 'A high-quality product, ideally suited for use in soups, stews, and casseroles. This recipe is similar to that formerly referred to as Quartered Tomatoes.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AB48F6C73226264B9914BDF72A44859A', X'FEF0501079E4F64C9D95293BCF5B13C8', X'FEF0501079E4F64C9D95293BCF5B13C8', 1, NULL, NULL, 2, NULL, 0, 'Quantity: An average of 22 pounds is needed per canner load of 7 quarts; an average of 14 fresh pounds is needed per canner load of 9 pints. A bushel weighs 53 pounds and yields 17 to 20 quarts of crushed tomatoesan average of 2-3/4 pounds per quart.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'05933B65F280994992F0F733D85EE750', X'FEF0501079E4F64C9D95293BCF5B13C8', X'FEF0501079E4F64C9D95293BCF5B13C8', 2, NULL, NULL, 2, NULL, 0, 'Procedure: Wash tomatoes and dip in boiling water for 30 to 60 seconds or until skins split. Then dip in cold water, slip off skins, and remove cores. Trim off any bruised or discolored portions and quarter. Heat one-sixth of the quarters quickly in a large pot, crushing them with a wooden mallet or spoon as they are added to the pot. This will exude juice. Continue heating the tomatoes, stirring to prevent burning. Once the tomatoes are boiling, gradually add remaining quartered tomatoes, stirring constantly. These remaining tomatoes do not need to be crushed. They will soften with heating and stirring. Continue until all tomatoes are added. Then boil gently 5 minutes. Add bottled lemon juice or citric acid to jars. See acidifi- cation directions on page 3-5. Add 1 teaspoon of salt per quart to the jars, if desired. Fill hot jars immediately with hot tomatoes, leaving 1/2-inch headspace. Remove air bubbles and adjust headspace if needed. Wipe rims of jars with a dampened clean paper towel. Adjust lids and process. (Acidification is still required for the pressure canning options; follow all steps in the Procedures above for any of the processing options.)');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'061878A792355544A3DAB19D0E7CEA1F', X'4A044BA3E41F3E469969E3C106429FD2', X'4A044BA3E41F3E469969E3C106429FD2', 0, NULL, NULL, 2, NULL, 0, 'Yield: About 4 half-pints');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'91A352CF92DD804F8A86EA2345301A03', X'4A044BA3E41F3E469969E3C106429FD2', X'4A044BA3E41F3E469969E3C106429FD2', 1, NULL, NULL, 2, NULL, 0, 'CAUTION Wear plastic or rubber gloves and do not touch your face while handling or cutting hot peppers. If you do not wear gloves, wash hands thoroughly with soap and water before touching your face or eyes.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8CAE28D126521347912D9DECAA60FB03', X'4A044BA3E41F3E469969E3C106429FD2', X'4A044BA3E41F3E469969E3C106429FD2', 2, NULL, NULL, 2, NULL, 0, 'Procedure: Place mixed pickling spices in a spice bag and tie the ends firmly. Mix all ingredients in a Dutch oven or large saucepot. Bring to a boil, stirring occasionally. Simmer another 20 minutes, until tomatoes are soft. Press mixture through a food mill. Return the liquid to the stockpot, heat to boiling and boil for another 15 minutes. Fill hot sauce into hot half-pint jars, leaving 1/4-inch headspace. Remove air bubbles and adjust headspace if needed. Wipe rims of jars with a dampened clean paper towel. Adjust lids and process.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DF77CE0C4C38894B94FDBEAB0BC8AC5A', X'28FB8561383C684C95889B3C3A54715C', X'28FB8561383C684C95889B3C3A54715C', 0, NULL, NULL, 2, NULL, 0, 'Yield: About 3 pints');
