INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A6FDA4BAFF743542BFE406FE968C50C1', X'28FB8561383C684C95889B3C3A54715C', X'28FB8561383C684C95889B3C3A54715C', 4, NULL, NULL, 2, NULL, 0, 'Oven or broiler method to blister skins - Place peppers in a hot oven (400 F) or under a broiler for 6 to 8 minutes until skins blister.');
