INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1CF45F945B9C9340B42BE60D3B051DFE', X'28FB8561383C684C95889B3C3A54715C', X'28FB8561383C684C95889B3C3A54715C', 6, NULL, NULL, 2, NULL, 0, 'To peel, after blistering skins, place peppers in a pan and cover with a damp cloth. (This will make peeling the peppers easier.) Cool several minutes; peel off skins. Discard seeds and chop.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2AD39A0FBCA74449A553DA495BC94752', X'28FB8561383C684C95889B3C3A54715C', X'28FB8561383C684C95889B3C3A54715C', 7, NULL, NULL, 2, NULL, 0, 'Procedure: Wash tomatoes and dip in boiling water for 30 to 60 seconds or until skins split. Dip in cold water, slip off skins, and remove cores. Combine all ingredients in a large saucepan and heat, stirring frequently, until mixture boils. Reduce heat and simmer for 20 minutes, stirring occasionally. Fill hot salsa into hot pint jars, leaving 1/2-inch headspace. Remove air bubbles and adjust headspace if needed. Wipe rims of jars with a dampened clean paper towel. Adjust lids and process.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A068952176BB2D439526EA47E5546C41', X'7810B191E75FEC40ACE84DFB71D9DA7C', X'7810B191E75FEC40ACE84DFB71D9DA7C', 0, NULL, NULL, 2, NULL, 0, 'Yield: About 5 pints');
