INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4E40F0D36FF5274F90D1289CC186FA2B', X'7810B191E75FEC40ACE84DFB71D9DA7C', X'7810B191E75FEC40ACE84DFB71D9DA7C', 4, NULL, NULL, 2, NULL, 0, 'Oven or broiler method to blister skins - Place peppers in a hot oven (400 F) or under a broiler for 6 to 8 minutes until skins blister.');
