INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2190DB10A128EE40AB9D38813ADDE12C', X'7810B191E75FEC40ACE84DFB71D9DA7C', X'7810B191E75FEC40ACE84DFB71D9DA7C', 6, NULL, NULL, 2, NULL, 0, 'To peel, after blistering skins, place peppers in a pan and cover with a damp cloth. (This will make peeling the peppers easier.) Cool several minutes; peel off skins. Discard seeds and chop.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E35CF6736ABFC840997ADC1E1F2307A4', X'7810B191E75FEC40ACE84DFB71D9DA7C', X'7810B191E75FEC40ACE84DFB71D9DA7C', 7, NULL, NULL, 2, NULL, 0, 'Procedure: Combine all ingredients in a large saucepan and stir frequently over high heat until mixture begins to boil, then reduce heat and simmer for 20 minutes, stirring occasionally. Ladle hot salsa into hot pint jars, leaving 1/2-inch headspace. Remove air bubbles and adjust headspace if needed. Wipe rims of jars with a dampened clean paper towel. Adjust lids and process.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'857A42FE7A11B746B46381FD247172BF', X'0F89926A92166549AEAF5B00CA406EBD', X'0F89926A92166549AEAF5B00CA406EBD', 0, NULL, NULL, 2, NULL, 0, 'Quantity: An average of 23 pounds is needed per canner load of 7 quarts, or an average of 14 pounds per canner load of 9 pints. A bushel weighs 53 pounds and yields 15 to 18 quarts of juicean average of 3-1/4 pounds per quart.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2B046CE7E61816489DEB90EF07E8C5FE', X'0F89926A92166549AEAF5B00CA406EBD', X'0F89926A92166549AEAF5B00CA406EBD', 1, NULL, NULL, 2, NULL, 0, 'Procedure: Wash, remove stems, and trim off bruised or discolored portions. To prevent juice from separating, quickly cut about 1 pound of tomatoes into quarters and put directly into sauce- pan. Heat immediately to boiling while crushing. Continue to slowly add and crush freshly cut tomato quarters to the boiling mixture. Make sure the mixture boils constantly and vigorously while you add the remaining tomatoes. Simmer 5 minutes after you add all pieces.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'11AA47BECAD5264B890ECEBB2B858762', X'0F89926A92166549AEAF5B00CA406EBD', X'0F89926A92166549AEAF5B00CA406EBD', 2, NULL, NULL, 2, NULL, 0, 'If you are not concerned about juice separation, simply slice or quarter tomatoes into a large saucepan. Crush, heat, and simmer for 5 minutes before juicing.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C17A51CCDD1A264B988C9A1749F8D4EF', X'0F89926A92166549AEAF5B00CA406EBD', X'0F89926A92166549AEAF5B00CA406EBD', 3, NULL, NULL, 2, NULL, 0, 'Press both types of heated juice through a sieve or food mill to remove skins and seeds. Add bottled lemon juice or citric acid to jars. See acidification instructions on page 3-5. Heat juice again to boiling. Add 1 teaspoon of salt per quart to the jars, if desired. Fill hot jars with hot tomato juice, leaving 1/2-inch headspace. Wipe rims of jars with a dampened clean paper towel. Adjust lids and process. (Acidification is still required for the pressure canning options; follow all steps in the Procedures above for any of the processing options.)');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F8464D77D4694E449E80BDF6EF470CE4', X'6A861EBE1B230843A18509E2B035D842', X'6A861EBE1B230843A18509E2B035D842', 0, NULL, NULL, 2, NULL, 0, 'Yield: 6 to 7 pints');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9585679CF860884594838F801B7396DF', X'6A861EBE1B230843A18509E2B035D842', X'6A861EBE1B230843A18509E2B035D842', 1, NULL, NULL, 2, NULL, 0, 'Procedure: Wash tomatoes. Dip in boiling water for 30 to 60 seconds or until skins split. Dip in cold water. Slip off skins and remove cores. Quarter tomatoes into 4-gallon stockpot or a large kettle. Add onions and red pepper. Bring to boil and simmer 20 minutes, uncovered. Cover, turn off heat and let stand for 20 minutes. Combine spices in a spice bag and add to vinegar in a 2-quart saucepan. Bring to boil. Remove spice bag and combine vinegar and tomato mixture. Boil about 30 minutes. Put boiled mixture through a food mill or sieve. Return to pot. Add sugar and salt, boil gently, and stir frequently until volume is reduced by one-half or until mixture rounds up on spoon without separation. Fill hot pint jars, leaving 1/8-inch headspace. Remove air bubbles and adjust headspace if needed. Wipe rims of jars with a dampened clean paper towel. Adjust lids and process.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'64300B53E0FEF44B88B8E8D9491FD704', X'1564E8A68ED72047A8E826776FE96E91', X'1564E8A68ED72047A8E826776FE96E91', 0, NULL, NULL, 2, NULL, 0, 'Yield: About 7 quarts');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C6C8AAF03F48044EB9CF6A37C56043A2', X'1564E8A68ED72047A8E826776FE96E91', X'1564E8A68ED72047A8E826776FE96E91', 1, NULL, NULL, 2, NULL, 0, 'CAUTION Wear plastic or rubber gloves and do not touch your face while handling or cutting hot peppers. If you do not wear gloves, wash hands thoroughly with soap and water before touching your face or eyes.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'782FDBDFF5C47340903B531DD1A47A6F', X'1564E8A68ED72047A8E826776FE96E91', X'1564E8A68ED72047A8E826776FE96E91', 2, NULL, NULL, 2, NULL, 0, 'Wash and dry chiles. Slit each pepper along the side to allow steam to escape. Blister skins using one of these two methods:');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AA3D81528A33AC41905A8EC0BECC658B', X'1564E8A68ED72047A8E826776FE96E91', X'1564E8A68ED72047A8E826776FE96E91', 3, NULL, NULL, 2, NULL, 0, 'Oven or broiler method to blister skins - Place peppers in a hot oven (400 F) or under a broiler for 6 to 8 minutes until skins blister.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A3F769C0B88C3E4F99BB2E15AB29C864', X'1564E8A68ED72047A8E826776FE96E91', X'1564E8A68ED72047A8E826776FE96E91', 4, NULL, NULL, 2, NULL, 0, 'Range-top method to blister skins - Cover hot burner (either gas or electric) with heavy wire mesh. Place peppers on burner for several minutes until skins blister.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'64134A3739457C4BAE71483282D98E7C', X'1564E8A68ED72047A8E826776FE96E91', X'1564E8A68ED72047A8E826776FE96E91', 5, NULL, NULL, 2, NULL, 0, 'After blistering skins, place peppers in a pan and cover with a damp cloth. (This will make peeling the peppers easier.) Cool several minutes; peel off skins. Discard seeds and chop pep- pers. Wash tomatoes and dip in boiling water for 30 to 60 seconds or until skins split. Dip in cold water, slip off skins, and remove cores. Coarsely chop tomatoes and combine chopped peppers and remaining ingredients in large saucepan. Bring to a boil. Cover. Reduce heat and simmer 10 minutes. Fill hot jars, leaving 1-inch headspace. Remove air bubbles and adjust headspace if needed. Wipe rims of jars with a dampened clean paper towel. Adjust lids and process.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'022C5A73880AB643AB2E8324AC9C7CAC', X'0206E8FF75F38F46901A70D0FD5BEDAC', X'0206E8FF75F38F46901A70D0FD5BEDAC', 0, NULL, NULL, 2, NULL, 0, 'Quantity: An average of 12 pounds of tomatoes and 4 pounds of okra or zucchini is needed per canner load of 7 quarts. An average of 7 pounds of tomatoes and 2-1/2 pounds of okra or zucchini is needed per canner load of 9 pints. (Use about 3 pounds tomatoes to 1 pound vegetable.)');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3C63C4991072474495E178E6D9AA71BA', X'0206E8FF75F38F46901A70D0FD5BEDAC', X'0206E8FF75F38F46901A70D0FD5BEDAC', 1, NULL, NULL, 2, NULL, 0, 'Procedure: Wash tomatoes and okra or zucchini. Dip tomatoes in boiling water 30 to 60 seconds or until skins split. Then dip in cold water, slip off skins and remove cores, and quar- ter. Trim stems from okra and slice into 1-inch pieces or leave whole. Slice or cube zucchini if used. Bring tomatoes to a boil and simmer 10 minutes. Add okra or zucchini and boil gently 5 minutes. Add 1 teaspoon of salt for each quart to the jars, if desired. Fill hot jars with mix- ture, leaving 1-inch headspace. Remove air bubbles and adjust headspace if needed. Wipe rims of jars with a dampened clean paper towel. Adjust lids and process.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1651CA6CCBAE3F4AA0F095AB4D061910', X'0206E8FF75F38F46901A70D0FD5BEDAC', X'0206E8FF75F38F46901A70D0FD5BEDAC', 2, NULL, NULL, 2, NULL, 0, 'Variation: You may add four or five pear, onions or two onion slices to each jar.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A4012A841BF19048B7A77169E2C19E53', X'8BE382E5D96A0B4CA19574744794AB1F', X'8BE382E5D96A0B4CA19574744794AB1F', 0, NULL, NULL, 2, NULL, 0, 'Yield: About 7 to 9 pints');
