INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1BD201D312F3C641AED62DD3B01B2CBC', X'76E1693082455A45804970B01E2FCAA1', X'76E1693082455A45804970B01E2FCAA1', 8, NULL, NULL, 2, NULL, 0, 'Procedure: Wash tomatoes and dip in boiling water for 30 to 60 seconds or until skins split. Dip in cold water, slip off skins, and remove cores. Combine all ingredients except cumin, oregano and cilantro in a large pot and bring to a boil, stirring frequently, then reduce heat and simmer for 10 minutes. Add spices and simmer for another 20 minutes, stirring occasionally. Fill hot salsa into hot pint jars, leaving 1/2-inch headspace. Remove air bubbles and adjust headspace if needed. Wipe rims of jars with a dampened clean paper towel. Adjust lids and process.');
