INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'48A99B9BB8740C468FC2DEEBC90EBC2D', X'8C25A945AAD3D847A268DC308F56CB2B', X'8C25A945AAD3D847A268DC308F56CB2B', 7, NULL, NULL, 2, NULL, 0, 'Procedure: Wash tomatoes and dip in boiling water for 30 to 60 seconds or until skins split. Dip in cold water, slip off skins, and remove cores. Combine all ingredients in a large pot and bring to a boil, stirring frequently. Reduce heat and simmer 20 minutes, stirring occasionally. Fill hot salsa into hot pint jars, leaving 1/2-inch headspace. Remove air bubbles and adjust headspace if needed. Wipe rims of jars with a dampened clean paper towel. Adjust lids and process.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0BCB1F05D92B8C4E96FE9BB54483C6D8', X'8EED299AE097EB4E95C01D288D45E4FE', X'8EED299AE097EB4E95C01D288D45E4FE', 0, NULL, NULL, 2, NULL, 0, 'Quantity: For thin saucean average of 35 pounds is needed per canner load of 7 quarts; an average of 21 pounds is needed per canner load of 9 pints. A bushel weighs 53 pounds and yields 10 to 12 quarts of saucean average of 5 pounds per quart. For thick saucean aver- age of 46 pounds is needed per canner load of 7 quarts; an average of 28 pounds is needed per canner load of 9 pints. A bushel weighs 53 pounds and yields 7 to 9 quarts of thick saucean average of 6-1/2 pounds per quart.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'49ACE3C4F0C67344B71E9F891FBAD4A8', X'8EED299AE097EB4E95C01D288D45E4FE', X'8EED299AE097EB4E95C01D288D45E4FE', 1, NULL, NULL, 2, NULL, 0, 'Procedure: Prepare and press as for making tomato juice, see page 3-5. Simmer in large- diameter saucepan until sauce reaches desired consistency. Boil until volume is reduced by about one-third for thin sauce, or by one-half for thick sauce. Add bottled lemon juice or citric acid to jars. See acidification directions on page 3-5. Add 1 teaspoon of salt per quart to the jars, if desired. Fill hot jars, leaving 1/4-inch headspace. Remove air bubbles and adjust headspace if needed. Wipe rims of jars with a dampened clean paper towel. Adjust lids and process. (Acidification is still required for the pressure canning options; follow all steps in the Procedures above for any of the processing options.)');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'66A725CC4A53C14E8D2B4388F82C56FF', X'89D1B3A90EF2114FA6887E2C33F2F682', X'89D1B3A90EF2114FA6887E2C33F2F682', 0, NULL, NULL, 2, NULL, 0, 'Yield: About 9 pints');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B97B1D3B18A450468E9D61836815C962', X'89D1B3A90EF2114FA6887E2C33F2F682', X'89D1B3A90EF2114FA6887E2C33F2F682', 1, NULL, NULL, 2, NULL, 0, 'Procedure: To prepare tomatoes, follow directions for Spaghetti Sauce Without Meat. Saute beef or sausage until brown. Add garlic, onion, celery or green pepper, and mushrooms, if desired. Cook until vegetables are tender. Combine with tomato pulp in large saucepan. Add spices, salt, and sugar. Bring to a boil. Simmer, uncovered, until thick enough for serving. At this time initial volume will have been reduced by nearly one-half. Stir frequently to avoid burning. Fill hot jars, leaving 1-inch headspace. Remove air bubbles and adjust headspace if needed. Wipe rims of jars with a dampened clean paper towel. Adjust lids and process.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8F79BB99BC8AC249AEDF985912BF6D8A', X'AB413F8F486E7648A76F7587FD9E5154', X'AB413F8F486E7648A76F7587FD9E5154', 0, NULL, NULL, 2, NULL, 0, 'Yield: About 9 pints');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'29E31CBA5640FF49A9C6FE5B9C431A02', X'AB413F8F486E7648A76F7587FD9E5154', X'AB413F8F486E7648A76F7587FD9E5154', 1, NULL, NULL, 2, NULL, 0, 'CAUTION Do not increase the proportion of onions, peppers, or mushrooms.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0202E4011DBD044D91D70C14C025AA48', X'AB413F8F486E7648A76F7587FD9E5154', X'AB413F8F486E7648A76F7587FD9E5154', 2, NULL, NULL, 2, NULL, 0, 'Procedure: Wash tomatoes and dip in boiling water for 30 to 60 seconds or until skins split. Dip in cold water and slip off skins. Remove cores and quarter tomatoes. Boil 20 minutes, uncovered, in large saucepan. Put through food mill or sieve. Saute onions, garlic, celery or peppers, and mushrooms (if desired) in vegetable oil until tender. Combine sauteed vegetables and tomatoes and add remainder of spices, salt, and sugar. Bring to a boil. Simmer, uncovered, until thick enough for serving. At this time the initial volume will have been reduced by nearly one-half. Stir frequently to avoid burning. Fill hot jars, leaving 1-inch headspace. Remove air bubbles and adjust headspace if needed. Wipe rims of jars with a dampened clean paper towel. Adjust lids and process.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'823355E4F46C9B4497E49ABF8F69046A', X'515C0FD4B50C1046B4380A470FFC2597', X'515C0FD4B50C1046B4380A470FFC2597', 0, NULL, NULL, 2, NULL, 0, 'This recipe works best with paste tomatoes, as slicing tomatoes will yield a thin watery salsa. If you only have slicing tomatoes available, use the Tomato/Tomato Paste Salsa recipe.');
