INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0881C1F2C96F524CA8843878E99EA733', X'515C0FD4B50C1046B4380A470FFC2597', X'515C0FD4B50C1046B4380A470FFC2597', 7, NULL, NULL, 2, NULL, 0, 'To peel, after blistering skins, place peppers in a pan and cover with a damp cloth. (This will make peeling the peppers easier.) Cool several minutes; peel off skins. Discard seeds and chop.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'839016E7164FA544B424F2D3931FC916', X'515C0FD4B50C1046B4380A470FFC2597', X'515C0FD4B50C1046B4380A470FFC2597', 8, NULL, NULL, 2, NULL, 0, 'Procedure: Wash tomatoes and dip in boiling water for 30 to 60 seconds or until skins split. Dip in cold water, slip of skins, and remove cores. Combine all ingredients in a large saucepan. Bring to a boil, then reduce heat and simmer, stirring frequently until thick (about 1 hour). Fill hot sauce into hot pint jars, leaving 1/2-inch headspace. Remove air bubbles and adjust headspace if needed. Wipe rims of jars with a dampened clean paper towel. Adjust lids and process.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7D2E9F571D980D4888DB2C34791E2884', X'803C459599F62546BF0C89299B4C569B', X'803C459599F62546BF0C89299B4C569B', 0, NULL, NULL, 2, NULL, 0, 'Quantity: An average of 14 pounds is needed per canner load of 7 quarts; an average of 9 pounds is needed per canner load of 9 pints. A bushel weighs 32 pounds and yields 14 to 16 quartsan average of 2 pounds per quart.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4840085FACC4EF48B46DFC243DAA9D41', X'803C459599F62546BF0C89299B4C569B', X'803C459599F62546BF0C89299B4C569B', 1, NULL, NULL, 2, NULL, 0, 'Quality: Select unblemished firm, deep bright green tomatillos with a dry papery husk.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9D1E631178068743A607A126CC6E9D8F', X'803C459599F62546BF0C89299B4C569B', X'803C459599F62546BF0C89299B4C569B', 2, NULL, NULL, 2, NULL, 0, 'Procedure: Remove the dry outer husks entirely from the tomatillos and wash the fruit well. Leave whole; do not peel or remove seeds. Add bottled lemon juice or citric acid to jars. Follow the acidification amounts for tomatoes on page 3-5. Add enough water to cover the tomatillos in a large saucepan and boil them gently until tender, about 5 to 10 minutes. Drain and fill hot tomatillos loosely into hot jars, leaving 1/2-inch headspace. Fill hot jars with boil- ing water, leaving 1/2-inch headspace. Remove air bubbles and adjust headspace if needed. Wipe rims of jars with a dampened clean paper towel. Adjust lids and process.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F7AA5139DE1B694CAE7D6A6E712B62C5', X'803C459599F62546BF0C89299B4C569B', X'803C459599F62546BF0C89299B4C569B', 3, NULL, NULL, 2, NULL, 0, 'Process the same as Water-Packed Whole Tomatoes, Hot Pack.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3D24D1C2361CEC469696C04FF7F565E1', X'9BAF30D063C7E34BAAEA38CE7E058502', X'9BAF30D063C7E34BAAEA38CE7E058502', 0, NULL, NULL, 2, NULL, 0, 'Quantity: An average of 22 pounds of tomatoes is needed per canner load of 7 quarts. No more than 3 cups of other vegetables may be added for each 22 pounds of tomatoes.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'911D4348702E4849816143C37FB52845', X'9BAF30D063C7E34BAAEA38CE7E058502', X'9BAF30D063C7E34BAAEA38CE7E058502', 1, NULL, NULL, 2, NULL, 0, 'Procedure: Crush and simmer tomatoes as for making tomato juice (see page 3-5). Add no more than 3 cups of any combination of finely chopped celery, onions, carrots, and peppers for each 22 lbs of tomatoes. Simmer mixture 20 minutes. Press hot cooked tomatoes and vegetables through a sieve or food mill to remove skins and seeds. Add bottled lemon juice or citric acid to jars. See acidification directions on page 3-5. Add 1 teaspoon of salt per quart to the jars, if desired. Reheat tomato-vegetable juice blend to boiling and fill immediately into hot jars, leaving 1/2-inch headspace. Wipe rims of jars with a dampened clean paper towel. Adjust lids and process. (Acidification is still required for the pressure canning options; fol- low all steps in the Procedures above for any of the processing options.)');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A66BC3BEC38B064DA206DBB73CA392CB', X'05DA09571FFCBB4F9660C6CCFB574B63', X'05DA09571FFCBB4F9660C6CCFB574B63', 0, NULL, NULL, 2, NULL, 0, 'Yield: 6 to 7 pints');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'779E72B076ACEC4DB64AD05B6C178185', X'05DA09571FFCBB4F9660C6CCFB574B63', X'05DA09571FFCBB4F9660C6CCFB574B63', 1, NULL, NULL, 2, NULL, 0, 'Procedure: Follow procedure and process time for regular tomato ketchup.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1F173D5A206BC7488B6BCFC8AA7AD4C8', X'624D39DA24928B45AF0A0C1A8303B374', X'624D39DA24928B45AF0A0C1A8303B374', 0, NULL, NULL, 2, NULL, 0, 'Quantity: See whole tomatoes packed in water.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3C894884E13BED49B73ECEFB1A49C92E', X'624D39DA24928B45AF0A0C1A8303B374', X'624D39DA24928B45AF0A0C1A8303B374', 1, NULL, NULL, 2, NULL, 0, 'Procedure: Wash tomatoes. Dip in boiling water for 30 to 60 seconds or until skins split, then dip in cold water. Slip off skins and remove cores. Leave whole or halve. Add bottled lemon juice or citric acid to the jars. See acidification instructions on page 3-5. Add 1 tea- spoon of salt per quart to the jars, if desired.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E9973307A1E36F468B50770A2D0CD0A8', X'624D39DA24928B45AF0A0C1A8303B374', X'624D39DA24928B45AF0A0C1A8303B374', 2, NULL, NULL, 2, NULL, 0, 'Raw pack - Heat tomato juice in a saucepan. Fill hot jars with raw tomatoes, leaving 1/2-inch headspace. Cover tomatoes in the jars with hot tomato juice, leaving 1/2-inch headspace.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'41E858DDFCC7984C93CE3BFF260D8D1D', X'624D39DA24928B45AF0A0C1A8303B374', X'624D39DA24928B45AF0A0C1A8303B374', 3, NULL, NULL, 2, NULL, 0, 'Hot pack - Put tomatoes in a large saucepan and add enough tomato juice to completely cover them. Boil tomatoes and juice gently for 5 minutes. Fill hot jars with hot tomatoes, leaving 1/2-inch headspace. Add hot tomato juice to the jars to cover the tomatoes, leaving 1/2-inch headspace.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'ABF3FEF70F649C49A078155AF632C8F5', X'624D39DA24928B45AF0A0C1A8303B374', X'624D39DA24928B45AF0A0C1A8303B374', 4, NULL, NULL, 2, NULL, 0, 'Remove air bubbles and adjust headspace if needed. Wipe rims of jars with a dampened clean paper towel. Adjust lids and process. (Acidification is still required for the pressure canning options; follow all steps in the Procedures above for any of the processing options.)');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5A926B2572DCF245BA8E390CE6F4C186', X'884E03147DA9EE4889106071B53AD1B1', X'884E03147DA9EE4889106071B53AD1B1', 0, NULL, NULL, 2, NULL, 0, 'Quantity: An average of 21 pounds is needed per canner load of 7 quarts; an average of 13 pounds is needed per canner load of 9 pints. A bushel weighs 53 pounds and yields 15 to 21 quartsan average of 3 pounds per quart.');
INSERT INTO `VWR INTERNAL Meatsandsausages3 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B367822FB45AE342BA2758D0A2C8D9C4', X'884E03147DA9EE4889106071B53AD1B1', X'884E03147DA9EE4889106071B53AD1B1', 1, NULL, NULL, 2, NULL, 0, 'Procedure for hot or raw tomatoes filled with water in jars: Wash tomatoes. Dip in boiling water for 30 to 60 seconds or until skins split; then dip in cold water. Slip off skins and re- move cores. Leave whole or halve. Add bottled lemon juice or citric acid to jars. See acidifica- tion directions on page 3-5. Add 1 teaspoon of salt per quart to the jars, if desired. For hot pack products, add enough water to cover the tomatoes and boil them gently for 5 minutes. Fill hot jars with hot tomatoes or with raw peeled tomatoes. Add the hot cooking liquid to the hot pack, or hot water for raw pack to cover, leaving 1/2-inch headspace. Remove air bubbles and adjust headspace if needed. Wipe rims of jars with a dampened clean paper towel. Adjust lids and process. (Acidification is still required for the pressure canning op- tions; follow all steps in the Procedures above for any of the processing options.)');
