INSERT INTO `VWR INTERNAL Meatsandsausages5 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9F261DCA9F793A4C896441BA40F63962', X'85AE112DFCC0B946AF72981C7474B689', X'85AE112DFCC0B946AF72981C7474B689', 0, NULL, NULL, 2, NULL, 0, 'Salting. Take half of salt and rub into back fat on all sides. Spread salt on the bottom of container. Place back fat skin down in a container. Spread salt on top. Place another slab of back fat on top of the first one using the same procedure. Keep container under refrigeration (40-42 F, 4-6 C).');
INSERT INTO `VWR INTERNAL Meatsandsausages5 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C3516772622BB74091ECC90EF9FDAA6A', X'85AE112DFCC0B946AF72981C7474B689', X'85AE112DFCC0B946AF72981C7474B689', 1, NULL, NULL, 2, NULL, 0, 'After 7 days repeat procedure using second half of salt. Reverse order of slab placement: the slab that was previously on top goes on bottom now and vice versa. You can rub in any spices that you like for instance pepper, paprika, or coriander. Leave in a container for 1-2 weeks longer. This means that the total time is between 2 and 3 weeks.');
INSERT INTO `VWR INTERNAL Meatsandsausages5 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7248CBFC55688A4EBF0A7360A48F93EF', X'85AE112DFCC0B946AF72981C7474B689', X'85AE112DFCC0B946AF72981C7474B689', 2, NULL, NULL, 2, NULL, 0, 'Shake off excess salt from the surface of back fat and wash the slab in a lukewarm water. This removes any crystalized salt that has accumulated on the surface.');
INSERT INTO `VWR INTERNAL Meatsandsausages5 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FFC80A7F1032D243A716597F169D10C0', X'85AE112DFCC0B946AF72981C7474B689', X'85AE112DFCC0B946AF72981C7474B689', 3, NULL, NULL, 2, NULL, 0, 'Make a hanging loop and hang at cool temperature for 12 hours to dry.');
INSERT INTO `VWR INTERNAL Meatsandsausages5 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8BBCF1EBF25AA745AC69C920215E076B', X'85AE112DFCC0B946AF72981C7474B689', X'85AE112DFCC0B946AF72981C7474B689', 4, NULL, NULL, 2, NULL, 0, 'Smoke with a thin cold smoke for 2 days until back fat develops light brown or brown color.');
INSERT INTO `VWR INTERNAL Meatsandsausages5 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5717ABFB41F8D24C8EE6B2660D9BA3E5', X'85AE112DFCC0B946AF72981C7474B689', X'85AE112DFCC0B946AF72981C7474B689', 5, NULL, NULL, 2, NULL, 0, 'Keep in refrigerator.');
INSERT INTO `VWR INTERNAL Meatsandsausages5 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'ED8E4895CD894D439FA16B575A09CA33', X'154761FC9D966A4BA1EC5615AB8469FA', X'154761FC9D966A4BA1EC5615AB8469FA', 0, NULL, NULL, 2, NULL, 0, 'Immerse loin in brine and place for 2 days in refrigerator OR pump meat with 10% pump and then place it in leftover brine for 1 day only.');
INSERT INTO `VWR INTERNAL Meatsandsausages5 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'07C7D39EA0729C40A4F69DF884983E90', X'154761FC9D966A4BA1EC5615AB8469FA', X'154761FC9D966A4BA1EC5615AB8469FA', 1, NULL, NULL, 2, NULL, 0, 'Remove all bones and trim down the fat until you reach the lean meat.');
INSERT INTO `VWR INTERNAL Meatsandsausages5 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5D80416F2BB69C43AC75063D6169C61E', X'154761FC9D966A4BA1EC5615AB8469FA', X'154761FC9D966A4BA1EC5615AB8469FA', 2, NULL, NULL, 2, NULL, 0, 'Place loin into leftover brine and keep for 5 days in refrigerator.');
INSERT INTO `VWR INTERNAL Meatsandsausages5 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0CC57CCB7CAD1C4EBDEDED2F9A10BB48', X'154761FC9D966A4BA1EC5615AB8469FA', X'154761FC9D966A4BA1EC5615AB8469FA', 3, NULL, NULL, 2, NULL, 0, 'Making Brine: To make 50 brine (10 Baum) add 1.2 pounds of salt to 1 gallon of water (3.8 liters). Add 130 g (7- Tbsp) of Cure #1. Add 226 g ( lb) sugar. This amount of brine will cure about 17 lbs (7.3 kg) of meat.');
INSERT INTO `VWR INTERNAL Meatsandsausages5 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7D395D8CC27EB64291C4B4D76CF5AD20', X'154761FC9D966A4BA1EC5615AB8469FA', X'154761FC9D966A4BA1EC5615AB8469FA', 4, NULL, NULL, 2, NULL, 0, 'Remove from brine, wash and soak in cold water for 1 hour.');
INSERT INTO `VWR INTERNAL Meatsandsausages5 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F09773D9E8333C4FB515E2846F8F00EB', X'154761FC9D966A4BA1EC5615AB8469FA', X'154761FC9D966A4BA1EC5615AB8469FA', 5, NULL, NULL, 2, NULL, 0, 'Stuff into synthetic casing or stockinette and hang in a preheated to 130 F (54 C) smokehouse to dry. This should take 3-4 hours with vents wide open.');
INSERT INTO `VWR INTERNAL Meatsandsausages5 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'48CDC2C664EEF0418C76F592E3BB2030', X'154761FC9D966A4BA1EC5615AB8469FA', X'154761FC9D966A4BA1EC5615AB8469FA', 6, NULL, NULL, 2, NULL, 0, 'When loin is dry apply smoke and smoke for 3 hours at 150 F (66 C).');
INSERT INTO `VWR INTERNAL Meatsandsausages5 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C216C90BD29E1A41BAAF1F7D3C682EB5', X'154761FC9D966A4BA1EC5615AB8469FA', X'154761FC9D966A4BA1EC5615AB8469FA', 7, NULL, NULL, 2, NULL, 0, 'Increase temperature (no smoke) to 170 F (77 C) and hold until meat reaches internal temperature of 154 F (68 C).');
INSERT INTO `VWR INTERNAL Meatsandsausages5 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D345544E7EB5EE448E287C84BF8AAEFE', X'154761FC9D966A4BA1EC5615AB8469FA', X'154761FC9D966A4BA1EC5615AB8469FA', 8, NULL, NULL, 2, NULL, 0, 'Shower for 5 minutes with cold water.');
INSERT INTO `VWR INTERNAL Meatsandsausages5 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8CD07BCCAC7D054B981FD5FE60BF3A79', X'154761FC9D966A4BA1EC5615AB8469FA', X'154761FC9D966A4BA1EC5615AB8469FA', 9, NULL, NULL, 2, NULL, 0, 'Hang at room temperature until loin is dry.');
INSERT INTO `VWR INTERNAL Meatsandsausages5 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C3E8FAE9A16CBD448A0508B60631CD39', X'154761FC9D966A4BA1EC5615AB8469FA', X'154761FC9D966A4BA1EC5615AB8469FA', 10, NULL, NULL, 2, NULL, 0, 'Place in refrigerator.');
INSERT INTO `VWR INTERNAL Meatsandsausages5 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B4C6CB91D238924FB1B671D91F4845B9', X'91F512A72BC1A04A82ABE53740B20BF2', X'91F512A72BC1A04A82ABE53740B20BF2', 0, NULL, NULL, 2, NULL, 0, 'Rub in 1/2 of the mixture on all sides.');
INSERT INTO `VWR INTERNAL Meatsandsausages5 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CF72236AD05D6241B2741A4DF38E1A98', X'91F512A72BC1A04A82ABE53740B20BF2', X'91F512A72BC1A04A82ABE53740B20BF2', 1, NULL, NULL, 2, NULL, 0, 'After 5 days overhaul and rub in another half. Keep under refrigeration. The meat will be cured im 10 to 15 days.');
INSERT INTO `VWR INTERNAL Meatsandsausages5 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AB1AB46751215545B4337AF08220BFD2', X'91F512A72BC1A04A82ABE53740B20BF2', X'91F512A72BC1A04A82ABE53740B20BF2', 2, NULL, NULL, 2, NULL, 0, 'Soak bacon for one hour, brush the surface clean and hang until dry.');
INSERT INTO `VWR INTERNAL Meatsandsausages5 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E839879B7D1B8E498AEB2BE4BA58E359', X'91F512A72BC1A04A82ABE53740B20BF2', X'91F512A72BC1A04A82ABE53740B20BF2', 3, NULL, NULL, 2, NULL, 0, 'Smoke with a thin cold smoke for 1-2 days.');
INSERT INTO `VWR INTERNAL Meatsandsausages5 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6631AD62797F2F4899A0B4FEF3F2A897', X'91F512A72BC1A04A82ABE53740B20BF2', X'91F512A72BC1A04A82ABE53740B20BF2', 4, NULL, NULL, 2, NULL, 0, 'Refrigerate.');
INSERT INTO `VWR INTERNAL Meatsandsausages5 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9CD257B3FCD1464D9E7EB79C355121EF', X'91F512A72BC1A04A82ABE53740B20BF2', X'91F512A72BC1A04A82ABE53740B20BF2', 5, NULL, NULL, 2, NULL, 0, 'Bacon-Brine Cured, Brine: 60 SAL brine, 3.8 liters, 1 gallon');
INSERT INTO `VWR INTERNAL Meatsandsausages5 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3394375DEC6A0D4DB132867FF10DB9FA', X'91F512A72BC1A04A82ABE53740B20BF2', X'91F512A72BC1A04A82ABE53740B20BF2', 6, NULL, NULL, 2, NULL, 0, 'Cure #1, 81 g, 13 tsp.');
INSERT INTO `VWR INTERNAL Meatsandsausages5 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4F6ED22D498C004E90B14587C4C186BA', X'91F512A72BC1A04A82ABE53740B20BF2', X'91F512A72BC1A04A82ABE53740B20BF2', 7, NULL, NULL, 2, NULL, 0, 'Bacon is placed for 10 days in curing solution. The pieces are overhauled in the middle of the process.');
INSERT INTO `VWR INTERNAL Meatsandsausages5 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'00CA64FBE6F9D545B7CF257CAB5E1A28', X'91F512A72BC1A04A82ABE53740B20BF2', X'91F512A72BC1A04A82ABE53740B20BF2', 8, NULL, NULL, 2, NULL, 0, 'Bacon is soaked for one hour in fresh water and the surface brushed off clean.');
INSERT INTO `VWR INTERNAL Meatsandsausages5 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C30B29CB4B438E4BA3A7BBB4336CAA4B', X'91F512A72BC1A04A82ABE53740B20BF2', X'91F512A72BC1A04A82ABE53740B20BF2', 9, NULL, NULL, 2, NULL, 0, 'Bacon is hung to dry.');
INSERT INTO `VWR INTERNAL Meatsandsausages5 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'14A87FA1AB0F884CB4DEA1DC1275C948', X'91F512A72BC1A04A82ABE53740B20BF2', X'91F512A72BC1A04A82ABE53740B20BF2', 10, NULL, NULL, 2, NULL, 0, 'Bacon is smoked with cold smoke for 2 days.');
INSERT INTO `VWR INTERNAL Meatsandsausages5 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C8B6831F74171A419755B05915F633F2', X'91F512A72BC1A04A82ABE53740B20BF2', X'91F512A72BC1A04A82ABE53740B20BF2', 11, NULL, NULL, 2, NULL, 0, 'Refrigerate.');
INSERT INTO `VWR INTERNAL Meatsandsausages5 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'551BB9BCA6C58546BBDDB756239E6BBB', X'91F512A72BC1A04A82ABE53740B20BF2', X'91F512A72BC1A04A82ABE53740B20BF2', 12, NULL, NULL, 2, NULL, 0, 'Bacon - Injection Cured');
INSERT INTO `VWR INTERNAL Meatsandsausages5 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C1EF049086AD684384BE3EA681BEA401', X'91F512A72BC1A04A82ABE53740B20BF2', X'91F512A72BC1A04A82ABE53740B20BF2', 13, NULL, NULL, 2, NULL, 0, 'Bacon is needle injected with the above 60 SAL curing solution at 10% pump.');
INSERT INTO `VWR INTERNAL Meatsandsausages5 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F81E86F34C141B4E86F0739D32A9824A', X'91F512A72BC1A04A82ABE53740B20BF2', X'91F512A72BC1A04A82ABE53740B20BF2', 14, NULL, NULL, 2, NULL, 0, 'Bacon is left in for 5 days in the same strength solution. Bacon is rinsed and left to dry.');
INSERT INTO `VWR INTERNAL Meatsandsausages5 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E5B8A66B9053A9458E2213770458028C', X'91F512A72BC1A04A82ABE53740B20BF2', X'91F512A72BC1A04A82ABE53740B20BF2', 15, NULL, NULL, 2, NULL, 0, 'Bacon is smoked with cold smoke for 2 days.');
INSERT INTO `VWR INTERNAL Meatsandsausages5 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8496521E0EFF7A47866D9480703DB413', X'91F512A72BC1A04A82ABE53740B20BF2', X'91F512A72BC1A04A82ABE53740B20BF2', 16, NULL, NULL, 2, NULL, 0, 'Refrigerate.');
INSERT INTO `VWR INTERNAL Meatsandsausages5 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4635E88AA790304F9C544D2905617F26', X'52BB22022950D24CBE6ADE12540DDB06', X'52BB22022950D24CBE6ADE12540DDB06', 0, NULL, NULL, 2, NULL, 0, 'Remove all fat, connective tissue and silver skin.');
INSERT INTO `VWR INTERNAL Meatsandsausages5 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2562FE25F033BE42B04B53D06E6A26F7', X'52BB22022950D24CBE6ADE12540DDB06', X'52BB22022950D24CBE6ADE12540DDB06', 1, NULL, NULL, 2, NULL, 0, 'Mix salt, Cure #2 and all ingredients together. Take  of the mixture and rub into the meat. Place in zip-loc bag and put for 3 days into refrigerator.');
INSERT INTO `VWR INTERNAL Meatsandsausages5 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'228CAC791596994B9A781D5BCEAEF42B', X'52BB22022950D24CBE6ADE12540DDB06', X'52BB22022950D24CBE6ADE12540DDB06', 2, NULL, NULL, 2, NULL, 0, 'Remove from the bag and apply the second half of the mixture. Drain away any liquid from the bag. Place in refrigerator for 3 more days.');
INSERT INTO `VWR INTERNAL Meatsandsausages5 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D0262BF4F40783409EBD2120E20BB39D', X'52BB22022950D24CBE6ADE12540DDB06', X'52BB22022950D24CBE6ADE12540DDB06', 3, NULL, NULL, 2, NULL, 0, 'Brush off excess mixture from the meat. Stuff into synthetic or collagen casings.');
INSERT INTO `VWR INTERNAL Meatsandsausages5 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2CB33A07A6E91844AC38355133EDC499', X'52BB22022950D24CBE6ADE12540DDB06', X'52BB22022950D24CBE6ADE12540DDB06', 4, NULL, NULL, 2, NULL, 0, 'Hang for 4 weeks at 54 F (12 C) to dry and mature.');
INSERT INTO `VWR INTERNAL Meatsandsausages5 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'762D96178211AB4B836006EDD6C9A3E3', X'3001C9D96183004EAC6BB5C9FF1633C8', X'3001C9D96183004EAC6BB5C9FF1633C8', 0, NULL, NULL, 2, NULL, 0, 'Make 1 liter/quart gelatin. Add 1 Tbs white vinegar and 1 Tbs white wine.');
INSERT INTO `VWR INTERNAL Meatsandsausages5 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CDC3E249D2D3E049BDFF876A30B32C5C', X'3001C9D96183004EAC6BB5C9FF1633C8', X'3001C9D96183004EAC6BB5C9FF1633C8', 1, NULL, NULL, 2, NULL, 0, 'Pour a small amount of hot gelatin on the bottom of container, then place it in a refrigerator.');
INSERT INTO `VWR INTERNAL Meatsandsausages5 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9B188BBD2A4FC04D9F462C46192F5E8F', X'3001C9D96183004EAC6BB5C9FF1633C8', X'3001C9D96183004EAC6BB5C9FF1633C8', 2, NULL, NULL, 2, NULL, 0, 'Once the solution hardens, place a slice of orange, hard boiled egg, carrot, apple or any decoration of your choice on top of solid gelatin.');
INSERT INTO `VWR INTERNAL Meatsandsausages5 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CD980DA4723CFF408888045CCFEFC073', X'3001C9D96183004EAC6BB5C9FF1633C8', X'3001C9D96183004EAC6BB5C9FF1633C8', 3, NULL, NULL, 2, NULL, 0, 'More hot gelatin is added again to set the slices of fruit in place and the container is placed in the refrigerator again.');
INSERT INTO `VWR INTERNAL Meatsandsausages5 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EAB178CAD456884187A2561A0C3FCEE5', X'3001C9D96183004EAC6BB5C9FF1633C8', X'3001C9D96183004EAC6BB5C9FF1633C8', 4, NULL, NULL, 2, NULL, 0, 'Once the solution solidifies it is removed from the refrigerator and the whole pieces of cold chicken breast are placed into the container. 6. The container is filled with gelatin and placed in the refrigerator overnight for the jelly to solidify.');
INSERT INTO `VWR INTERNAL Meatsandsausages5 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8409154E578FC44BBE4E576E937E9080', X'3001C9D96183004EAC6BB5C9FF1633C8', X'3001C9D96183004EAC6BB5C9FF1633C8', 5, NULL, NULL, 2, NULL, 0, 'Before serving the container is flipped upside down so that the entire product drops out. It helps to dip the container first for a short while in hot water to melt a tiny amount of gelatin that is attached to the container inside.');
INSERT INTO `VWR INTERNAL Meatsandsausages5 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B723223213BB8441BC406172F83436D9', X'47F6426FC6191B40906700A4830DE362', X'47F6426FC6191B40906700A4830DE362', 0, NULL, NULL, 2, NULL, 0, 'Trim excess fat all around the butt. Keep the butt in refrigerator to keep it cold.');
INSERT INTO `VWR INTERNAL Meatsandsausages5 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7686EFDB85FF2249BA3FC0C750ABC7CB', X'47F6426FC6191B40906700A4830DE362', X'47F6426FC6191B40906700A4830DE362', 1, NULL, NULL, 2, NULL, 0, 'Mix salt, Cure #2, sugar and all spices together. This is your spice mixture. Rub the meat on all sides with half of the spice mixture. Place the butt for 9 days in refrigerator. Keep it covered with plastic wrap or a cloth to prevent drying. You can place it in a zip lock bag too.');
INSERT INTO `VWR INTERNAL Meatsandsausages5 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9F0BE57934036C44B174B354293B2D97', X'47F6426FC6191B40906700A4830DE362', X'47F6426FC6191B40906700A4830DE362', 2, NULL, NULL, 2, NULL, 0, 'After 9 days, rub the butt with the remaining spice mixture and keep in refrigerator for an additional 9 days.');
INSERT INTO `VWR INTERNAL Meatsandsausages5 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'10B2526F6306374689EB742FD7A49250', X'47F6426FC6191B40906700A4830DE362', X'47F6426FC6191B40906700A4830DE362', 3, NULL, NULL, 2, NULL, 0, 'Remove from refrigerator, rinse the surface with tap water to remove any crystallized salt. Let it dry on the refrigerators screen for 3 hours.');
INSERT INTO `VWR INTERNAL Meatsandsausages5 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D475B36EA5E42B47955E8A6FDB34C4A5', X'47F6426FC6191B40906700A4830DE362', X'47F6426FC6191B40906700A4830DE362', 4, NULL, NULL, 2, NULL, 0, 'Make another flavoring spice mixture: 1 Tablespoon of brown sugar, 1 teaspoon of black pepper, 1 teaspoon of coarsely ground fennel. Rub into coppa.');
INSERT INTO `VWR INTERNAL Meatsandsausages5 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'51CC2384F2E3764784B01F087AFEC2F7', X'47F6426FC6191B40906700A4830DE362', X'47F6426FC6191B40906700A4830DE362', 5, NULL, NULL, 2, NULL, 0, 'Stuff the butt into beef bungs or 100 mm collagen casings. Prick air pockets to allow entrapped air to escape. Do this especially at the two ends, and anywhere you see pockets of air.');
INSERT INTO `VWR INTERNAL Meatsandsausages5 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'61C3877545D2834D87855777CFF26A47', X'47F6426FC6191B40906700A4830DE362', X'47F6426FC6191B40906700A4830DE362', 6, NULL, NULL, 2, NULL, 0, 'Hold butts for 21 days at around 55 F (14 C), 70% humidity to dry and mature. Do not go over 59 F (15 C).');
INSERT INTO `VWR INTERNAL Meatsandsausages5 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8600AE305D38374397B6D8F8E69AD260', X'2943A3D21C0B8748A141070A25C4ABED', X'2943A3D21C0B8748A141070A25C4ABED', 0, NULL, NULL, 2, NULL, 0, 'Pump spray ham with 6 - 7% of it''s green weight ( 0.3 - 0.35 kg, 10 - 12 oz) of brine (salt, water and Cure # 1). About 7 - 9 syringe injections. After pumping immerse ham in the remaining brine and place in a refrigerator for 10 - 14 days.Turn over meat once every few days and keep an eye on the brine. The meat must be completely covered with brine, use weight if needed. Any symptoms of liquid going murky, accumulation of foam or slime on top or unpleasant smell, require brine change. Check the meat as well but that is very unlikely that it might be going bad. If brine''s color becomes reddish don''t worry it''s normal. If you do it first time, check brine on a daily basis, you will sleep better at night.');
INSERT INTO `VWR INTERNAL Meatsandsausages5 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B824D60D1551D64B8D16256D369C8691', X'2943A3D21C0B8748A141070A25C4ABED', X'2943A3D21C0B8748A141070A25C4ABED', 1, NULL, NULL, 2, NULL, 0, 'Once curing is done, check the meat and it''s smell and color. Place hams for 3 hours under running water. You may place them in a large pot, but you will heve to change water once in a while.This time may be shorter if smaller pieces of meat are rinsed. After one hour get a tiny piece from inside and boil or fry it to see that the taste is what you are looking for. Then wash them briefly in warm water 86 - 104 F (30 - 40 C) and clean them on outside by scrubbing with a brush or knife. That removes any accumulation of salt crystals that may prevent proper smoke penetration.');
INSERT INTO `VWR INTERNAL Meatsandsausages5 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4E36494A4CAC4844B6A12E2BA83D4015', X'2943A3D21C0B8748A141070A25C4ABED', X'2943A3D21C0B8748A141070A25C4ABED', 2, NULL, NULL, 2, NULL, 0, 'After rinsing hang hams or place them on wire screen over the sink or some other vessel. Let them drain for 12 - 24 hours.');
INSERT INTO `VWR INTERNAL Meatsandsausages5 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6FE5028563866B4AA3116B16A91CC63E', X'2943A3D21C0B8748A141070A25C4ABED', X'2943A3D21C0B8748A141070A25C4ABED', 3, NULL, NULL, 2, NULL, 0, 'Now it''s the time for the bone removal if your ham has one and getting rid of any gristle, sinew and elements that we feel are not needed. Roll up the ham and tie it with butcher twine - twice lenghtwise and every 2` (4 - 5 cm) across. Make on one end a hanging loop for smoking. Even up any irregular areas for uniform shape.');
INSERT INTO `VWR INTERNAL Meatsandsausages5 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5FD9D7572AC63A40B267DB9D3956AE49', X'2943A3D21C0B8748A141070A25C4ABED', X'2943A3D21C0B8748A141070A25C4ABED', 4, NULL, NULL, 2, NULL, 0, 'Apply warm smoke, 86 - 104 F (30 - 40 C ) for 3 - 4 hours until light yellow to light brown color develops.');
INSERT INTO `VWR INTERNAL Meatsandsausages5 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6309ED0D559C3A4792533958688E6DF8', X'2943A3D21C0B8748A141070A25C4ABED', X'2943A3D21C0B8748A141070A25C4ABED', 5, NULL, NULL, 2, NULL, 0, 'Hams are placed in boiling water and boiled for 15 min. Water temperature is then lowered to 176 - 180 F (80 - 82 C) and hams are poached at this temperature until the inside temperature is 154 - 158F ( 68 - 70 C).The estimated time is about 50 min for 1 kg of weight. This time includes 15 min of boiling.');
INSERT INTO `VWR INTERNAL Meatsandsausages5 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A5A5BB568A5E164ABFBF6A54ABACDB2A', X'2943A3D21C0B8748A141070A25C4ABED', X'2943A3D21C0B8748A141070A25C4ABED', 6, NULL, NULL, 2, NULL, 0, 'Shower with cold water until the internal temperature is reduced to 110 F (44 C). Hang at room temperature for 1 hour then place in refrigerator.');
INSERT INTO `VWR INTERNAL Meatsandsausages5 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F790952DBFFD7C4D845819D94F99398D', X'852C675D729E1646B656014E96C62706', X'852C675D729E1646B656014E96C62706', 0, NULL, NULL, 2, NULL, 0, 'Remove skin. Cut meat into 1 (25 mm) pieces. Grind skin with ? (3 mm) plate. Save fat trimmings for other sausages.');
INSERT INTO `VWR INTERNAL Meatsandsausages5 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5A6B79E94E71664EBD59B250F86ED1AE', X'852C675D729E1646B656014E96C62706', X'852C675D729E1646B656014E96C62706', 1, NULL, NULL, 2, NULL, 0, 'Dilute salt, Cure #1 and sugar in 100 ml of water and pour over meat.');
INSERT INTO `VWR INTERNAL Meatsandsausages5 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'53F41022ABC0FE4990EF21DF1E5DBEF8', X'852C675D729E1646B656014E96C62706', X'852C675D729E1646B656014E96C62706', 2, NULL, NULL, 2, NULL, 0, 'Stir meat pieces, kneading them lightly. Add spices and ground skin and mix everything together.');
INSERT INTO `VWR INTERNAL Meatsandsausages5 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C084A6B6E7B20046B038ECA5FA485A66', X'852C675D729E1646B656014E96C62706', X'852C675D729E1646B656014E96C62706', 3, NULL, NULL, 2, NULL, 0, 'Stuff meat pieces into a cooking bag tightly, trying not to trap unnecessary air.');
INSERT INTO `VWR INTERNAL Meatsandsausages5 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'660A0E36272B1643AE1E5702FABB81EA', X'852C675D729E1646B656014E96C62706', X'852C675D729E1646B656014E96C62706', 4, NULL, NULL, 2, NULL, 0, 'Place the bag inside the ham press. Apply slight pressure.');
INSERT INTO `VWR INTERNAL Meatsandsausages5 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C4D801F7D156F8429C617989694F4BB0', X'852C675D729E1646B656014E96C62706', X'852C675D729E1646B656014E96C62706', 5, NULL, NULL, 2, NULL, 0, 'Place the ham press in a refrigerator for 48 hours. This is the curing step.');
INSERT INTO `VWR INTERNAL Meatsandsausages5 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'060D4968AA57A9499107E3543B3F6220', X'852C675D729E1646B656014E96C62706', X'852C675D729E1646B656014E96C62706', 6, NULL, NULL, 2, NULL, 0, 'Apply pressure to the cover and place the ham mold in hot water. Cook for 60 minutes at 176 F (80 C). A five pound ham (2.5 kg) may need 3 hours cooking time.');
INSERT INTO `VWR INTERNAL Meatsandsausages5 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E69D396E6D09D94FAFCB2A156DE29FEE', X'852C675D729E1646B656014E96C62706', X'852C675D729E1646B656014E96C62706', 7, NULL, NULL, 2, NULL, 0, 'Place the ham press for 2-3 hours in cold water to cool and then place overnight in a refrigerator.');
INSERT INTO `VWR INTERNAL Meatsandsausages5 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9271A0E770FBF04694306D82A4E2B82F', X'852C675D729E1646B656014E96C62706', X'852C675D729E1646B656014E96C62706', 8, NULL, NULL, 2, NULL, 0, 'Remove the ham from the mold. Keep refrigerated.');
INSERT INTO `VWR INTERNAL Meatsandsausages5 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'69FCBEFDB852A648A1BEEDB068D766B2', X'531B3302EB79734C92A3C0ABCBD6E261', X'531B3302EB79734C92A3C0ABCBD6E261', 0, NULL, NULL, 2, NULL, 0, 'Remove skin. Cut meat pieces into 1 (25 mm) cubes.');
INSERT INTO `VWR INTERNAL Meatsandsausages5 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0BA0FA03DBFA754E90FF6021F7923120', X'531B3302EB79734C92A3C0ABCBD6E261', X'531B3302EB79734C92A3C0ABCBD6E261', 1, NULL, NULL, 2, NULL, 0, 'Grind all meat through 3/16 (5 mm) plate.');
INSERT INTO `VWR INTERNAL Meatsandsausages5 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7434B6CE41DFF94B88174CC4AA1AA342', X'531B3302EB79734C92A3C0ABCBD6E261', X'531B3302EB79734C92A3C0ABCBD6E261', 2, NULL, NULL, 2, NULL, 0, 'Mix meat with salt and Cure #1. Pack tightly in a container, cover with cloth and leave in a refrigerator for 2 days.');
INSERT INTO `VWR INTERNAL Meatsandsausages5 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'994401FF2651EB4F93AC9CFFEF4AC522', X'531B3302EB79734C92A3C0ABCBD6E261', X'531B3302EB79734C92A3C0ABCBD6E261', 3, NULL, NULL, 2, NULL, 0, 'Mix cured meat with all ingredients until mixture feels sticky.');
INSERT INTO `VWR INTERNAL Meatsandsausages5 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1E8A82464827BD43838FA03B45623685', X'531B3302EB79734C92A3C0ABCBD6E261', X'531B3302EB79734C92A3C0ABCBD6E261', 4, NULL, NULL, 2, NULL, 0, 'Stuff mixture into cellophane bag whose size corresponds to the size of your ham press. Place the bag into the ham press and apply pressure to the cover.');
INSERT INTO `VWR INTERNAL Meatsandsausages5 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C6651AF6CAA0404C8A2BFF7948714747', X'531B3302EB79734C92A3C0ABCBD6E261', X'531B3302EB79734C92A3C0ABCBD6E261', 5, NULL, NULL, 2, NULL, 0, 'Insert the ham press into boiling water but cook at 176 F (80 C) until meat reaches 154-160 F (68-71 C). In a small ham press, for example 4 diameter and 4-6 high, that will take about 2 hours.');
INSERT INTO `VWR INTERNAL Meatsandsausages5 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B8B8B1ED8C6FC14B8C92D03899DC031B', X'531B3302EB79734C92A3C0ABCBD6E261', X'531B3302EB79734C92A3C0ABCBD6E261', 6, NULL, NULL, 2, NULL, 0, 'Place ham press for 2-3 hours in cold water to cool and then place overnight in refrigerator.');
INSERT INTO `VWR INTERNAL Meatsandsausages5 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A43137A0EA77A942B42957115E1E06FF', X'531B3302EB79734C92A3C0ABCBD6E261', X'531B3302EB79734C92A3C0ABCBD6E261', 7, NULL, NULL, 2, NULL, 0, 'Remove ham from the press.');
INSERT INTO `VWR INTERNAL Meatsandsausages5 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1DF2DC17DE0D6F47B8BB94A07D6F2E43', X'2F7565AE111702479978D7A3DB349C73', X'2F7565AE111702479978D7A3DB349C73', 0, NULL, NULL, 2, NULL, 0, 'Making Brine: Salt: 0.6kg (1.6 lb) Cure #1: 120 g (4.2 oz) Sugar: 42 g (1.5 oz) Cold water 3.8 l (1 gal)');
INSERT INTO `VWR INTERNAL Meatsandsausages5 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'457D6464E0B88C4D91B7E44A96D9E7F1', X'2F7565AE111702479978D7A3DB349C73', X'2F7565AE111702479978D7A3DB349C73', 1, NULL, NULL, 2, NULL, 0, 'Pump ham with 60 SAL brine (combine water, salt, Cure #1 and water together). Percent injection: 7%. The weight of cover pickle: 40% in relation to meats weight.');
INSERT INTO `VWR INTERNAL Meatsandsausages5 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'72A75932D717744AB399C32336D13E43', X'2F7565AE111702479978D7A3DB349C73', X'2F7565AE111702479978D7A3DB349C73', 2, NULL, NULL, 2, NULL, 0, 'Immerse pumped ham in cover pickle for 10-14 days at refrigerator temperature. Overhaul (turn around) hams every few days.');
INSERT INTO `VWR INTERNAL Meatsandsausages5 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CC3684FD5C2C1440A0BA4212DECE07D2', X'2F7565AE111702479978D7A3DB349C73', X'2F7565AE111702479978D7A3DB349C73', 3, NULL, NULL, 2, NULL, 0, 'Soak for 3 hours in cold running water. Rinse in lukewarm water (86 - 104 F, 30 - 40 C), using a brush to clean the surface.');
INSERT INTO `VWR INTERNAL Meatsandsausages5 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FA01802E34869B459C7B762087833E9D', X'2F7565AE111702479978D7A3DB349C73', X'2F7565AE111702479978D7A3DB349C73', 4, NULL, NULL, 2, NULL, 0, 'Hang or place on a screen for 12 - 24 hours to dry.');
INSERT INTO `VWR INTERNAL Meatsandsausages5 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0A271A72FC582447BC0B692D24972D0E', X'2F7565AE111702479978D7A3DB349C73', X'2F7565AE111702479978D7A3DB349C73', 5, NULL, NULL, 2, NULL, 0, 'Stuff in netting.');
INSERT INTO `VWR INTERNAL Meatsandsausages5 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DB016C0F39071147A6C8B83E92501C75', X'2F7565AE111702479978D7A3DB349C73', X'2F7565AE111702479978D7A3DB349C73', 6, NULL, NULL, 2, NULL, 0, 'Smoke with warm smoke (86 - 104 F, 30 - 40 C), for 4 hours until skin develops light yellow - light brown color.');
INSERT INTO `VWR INTERNAL Meatsandsausages5 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'816293E1F832C746BC8169031AFA0F26', X'2F7565AE111702479978D7A3DB349C73', X'2F7565AE111702479978D7A3DB349C73', 7, NULL, NULL, 2, NULL, 0, 'Insert ham into boiling water and keep boiling for 15 minutes. Lower water temperature to (176-180 F, 80-82 C) and continue cooking until the internal meat temperature becomes 154-158 F, 68-70 C. A rule of thumb calls for 50 minutes cooking for 1 kg of ham.');
INSERT INTO `VWR INTERNAL Meatsandsausages5 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8632863241037842A0EDB1A6839A6E5D', X'2F7565AE111702479978D7A3DB349C73', X'2F7565AE111702479978D7A3DB349C73', 8, NULL, NULL, 2, NULL, 0, 'Place on shelves/screens to cool down.');
INSERT INTO `VWR INTERNAL Meatsandsausages5 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'42F3FBCE1F71D04798AC5D0B37776745', X'2F7565AE111702479978D7A3DB349C73', X'2F7565AE111702479978D7A3DB349C73', 9, NULL, NULL, 2, NULL, 0, 'Keep refrigerated.');
INSERT INTO `VWR INTERNAL Meatsandsausages5 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BEA1B1A8A1DC454D92AD41A4FF5357F5', X'8C304D3F3E087749BB307ACA5B2059C6', X'8C304D3F3E087749BB307ACA5B2059C6', 0, NULL, NULL, 2, NULL, 0, 'Mix salt, Cure #2 and sugar together. Divide into three equal parts. Rub ham on all sides with ? of the mixture. Place ham in refrigerator. Keep it covered with plastic wrap or a cloth to prevent drying. A good idea is to keep it in a zip- loc bag. This prevents the ham from drying. The ham should be kept at around 90% humidity, but the humidity in a refrigerator is only about 40%.');
INSERT INTO `VWR INTERNAL Meatsandsausages5 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'08FDC5AEA4F8804380AEBB98559F19B0', X'8C304D3F3E087749BB307ACA5B2059C6', X'8C304D3F3E087749BB307ACA5B2059C6', 1, NULL, NULL, 2, NULL, 0, 'After 3 days, rub the ham with another ? mixture and place in a refrigerator.');
INSERT INTO `VWR INTERNAL Meatsandsausages5 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'794F12F88C9C594F9BE178E34A2B5B22', X'8C304D3F3E087749BB307ACA5B2059C6', X'8C304D3F3E087749BB307ACA5B2059C6', 2, NULL, NULL, 2, NULL, 0, 'After 10 days remove the ham from the refrigerator and apply the last ? of the mixture. Place ham back in refrigerator.');
INSERT INTO `VWR INTERNAL Meatsandsausages5 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8B1AFD31CC802049AC5283916EA39D89', X'8C304D3F3E087749BB307ACA5B2059C6', X'8C304D3F3E087749BB307ACA5B2059C6', 3, NULL, NULL, 2, NULL, 0, 'After 40 days (total salting time) remove the ham from the refrigerator and briefly rinse with tap water. Brush off any salt from the surface.');
INSERT INTO `VWR INTERNAL Meatsandsausages5 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'28E495014C783C4996021BB65F80BA53', X'8C304D3F3E087749BB307ACA5B2059C6', X'8C304D3F3E087749BB307ACA5B2059C6', 4, NULL, NULL, 2, NULL, 0, 'Coat the surface of the ham with pepper and stuff the ham into the netting.');
INSERT INTO `VWR INTERNAL Meatsandsausages5 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4C85287FB142E548992540870AE01F39', X'8C304D3F3E087749BB307ACA5B2059C6', X'8C304D3F3E087749BB307ACA5B2059C6', 5, NULL, NULL, 2, NULL, 0, 'Keep for 14 days at 40-42 F (4-6 C), 75% humidity.');
INSERT INTO `VWR INTERNAL Meatsandsausages5 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F508C1D6F076D54F9FCAD3D41871E7CB', X'8C304D3F3E087749BB307ACA5B2059C6', X'8C304D3F3E087749BB307ACA5B2059C6', 6, NULL, NULL, 2, NULL, 0, 'Apply cold smoke (77 F, 25 C) for 24 hours.');
INSERT INTO `VWR INTERNAL Meatsandsausages5 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F23E6F8ED18EA34BAE5F2038568BC9B7', X'8C304D3F3E087749BB307ACA5B2059C6', X'8C304D3F3E087749BB307ACA5B2059C6', 7, NULL, NULL, 2, NULL, 0, 'Ripe (dry and mature) hams for 60 days at 77-86 F, 25-30 C), 65% humidity.');
INSERT INTO `VWR INTERNAL Meatsandsausages5 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A69A8D132F14614B8A68D298535F9F1A', X'A0A41B88DD5005468CBEA2837F8D35F2', X'A0A41B88DD5005468CBEA2837F8D35F2', 0, NULL, NULL, 2, NULL, 0, 'Remove skin. Cut meat pieces into 1 (25 mm) cubes.');
INSERT INTO `VWR INTERNAL Meatsandsausages5 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'693520BFD11F1E43B4D7C34487F1364B', X'A0A41B88DD5005468CBEA2837F8D35F2', X'A0A41B88DD5005468CBEA2837F8D35F2', 1, NULL, NULL, 2, NULL, 0, 'Add salt and Cure #1 to meat and mix together. Pack tightly in a container, cover with cloth and leave for 3 days in refrigerator. Make brine adding 2 tsp of salt to 60 ml ( cup) of water. This will make 40 SAL brine. No nitrite is needed for curing skins. Leave skins in brine for 3 days in refrigerator.');
INSERT INTO `VWR INTERNAL Meatsandsausages5 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'37A970AD3C544144A9326F33ABA88E7A', X'A0A41B88DD5005468CBEA2837F8D35F2', X'A0A41B88DD5005468CBEA2837F8D35F2', 2, NULL, NULL, 2, NULL, 0, 'Poach skins in hot water (just below boiling) for 30 minutes. Drain skins, then cool them.');
INSERT INTO `VWR INTERNAL Meatsandsausages5 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0687C562AFE3124FA9C6F2FAF0B4E919', X'A0A41B88DD5005468CBEA2837F8D35F2', X'A0A41B88DD5005468CBEA2837F8D35F2', 3, NULL, NULL, 2, NULL, 0, 'Grind skins through ? (3 mm) plate.');
INSERT INTO `VWR INTERNAL Meatsandsausages5 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A12A2E9FE545F442A80253B447EA84D7', X'A0A41B88DD5005468CBEA2837F8D35F2', X'A0A41B88DD5005468CBEA2837F8D35F2', 4, NULL, NULL, 2, NULL, 0, 'Mix cured meat pieces with pepper. Knead meat hard until feels sticky. Add ground skins and mix everything well together.');
INSERT INTO `VWR INTERNAL Meatsandsausages5 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F499E02E413A254CA6291547699926B8', X'A0A41B88DD5005468CBEA2837F8D35F2', X'A0A41B88DD5005468CBEA2837F8D35F2', 5, NULL, NULL, 2, NULL, 0, 'Stuff mixture into cellophane bag whose size corresponds to the size of your ham press. Place the bag into the ham press and apply pressure to the cover.');
INSERT INTO `VWR INTERNAL Meatsandsausages5 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'660F8C606D5732429ED6D6C2541FBBA2', X'A0A41B88DD5005468CBEA2837F8D35F2', X'A0A41B88DD5005468CBEA2837F8D35F2', 6, NULL, NULL, 2, NULL, 0, 'Insert ham press into boiling water but cook at 176 F (80 C) until meat reaches 154-160 F (68-71 C). In a small ham press, for example 4 diameter and 4-6 high, that will take about 2 hours.');
INSERT INTO `VWR INTERNAL Meatsandsausages5 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1042C5B3D296B94CAA88B662D5079866', X'A0A41B88DD5005468CBEA2837F8D35F2', X'A0A41B88DD5005468CBEA2837F8D35F2', 7, NULL, NULL, 2, NULL, 0, 'Place ham press for 2-3 hours in cold water to cool and then place overnight in refrigerator.');
INSERT INTO `VWR INTERNAL Meatsandsausages5 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0FF9B8E8F947A64F80334E1992F0FBD9', X'A0A41B88DD5005468CBEA2837F8D35F2', X'A0A41B88DD5005468CBEA2837F8D35F2', 8, NULL, NULL, 2, NULL, 0, 'Remove ham from the press.');
INSERT INTO `VWR INTERNAL Meatsandsausages5 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A1F4DB0B3F0EE84CB246161733668AE1', X'A0A41B88DD5005468CBEA2837F8D35F2', X'A0A41B88DD5005468CBEA2837F8D35F2', 9, NULL, NULL, 2, NULL, 0, 'Keep in refrigerator.');
INSERT INTO `VWR INTERNAL Meatsandsausages5 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'28E7929669FEA74E95DCD72010321789', X'977A2DCCF6B4E44C80FD00943499ED96', X'977A2DCCF6B4E44C80FD00943499ED96', 0, NULL, NULL, 2, NULL, 0, 'Pre-freeze meat so it will be easier to slice.');
INSERT INTO `VWR INTERNAL Meatsandsausages5 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'305085C9A641CE4AA16B0B185C6A572A', X'977A2DCCF6B4E44C80FD00943499ED96', X'977A2DCCF6B4E44C80FD00943499ED96', 1, NULL, NULL, 2, NULL, 0, 'Cut partially thawed meat into long slices about ` thick and an inch or two wide. For tender jerky, cut at right angles to long muscles (across the grain). Remove visible fat to help prevent off-flavors.');
INSERT INTO `VWR INTERNAL Meatsandsausages5 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'834C2E8239FE3C4F9B920CFA75E2D841', X'977A2DCCF6B4E44C80FD00943499ED96', X'977A2DCCF6B4E44C80FD00943499ED96', 2, NULL, NULL, 2, NULL, 0, 'Prepare marinade of your choice in a large sauce pan.');
INSERT INTO `VWR INTERNAL Meatsandsausages5 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FD003893398740439258C0A19A07DF86', X'977A2DCCF6B4E44C80FD00943499ED96', X'977A2DCCF6B4E44C80FD00943499ED96', 3, NULL, NULL, 2, NULL, 0, 'Pour marinade over the meat and marinate jerky 12-24 hours. Overhaul jerky pieces at least once.');
INSERT INTO `VWR INTERNAL Meatsandsausages5 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9E1AD1D385B68E458D2F19E5A53DFEFF', X'977A2DCCF6B4E44C80FD00943499ED96', X'977A2DCCF6B4E44C80FD00943499ED96', 4, NULL, NULL, 2, NULL, 0, 'Dry at 140-150 F (60-66 C) in dehydrator, oven or smoker. You can apply smoke for 30 minutes at this stage. Test for doneness by letting a piece cool. When cool, it should crack but not break when bent.');
INSERT INTO `VWR INTERNAL Meatsandsausages5 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C44F8E1851EEB94D9F5249E7E70BAD8B', X'977A2DCCF6B4E44C80FD00943499ED96', X'977A2DCCF6B4E44C80FD00943499ED96', 5, NULL, NULL, 2, NULL, 0, 'Refrigerate and when cool check again for doneness. If necessary, dry further.');
INSERT INTO `VWR INTERNAL Meatsandsausages5 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E56557B1312616479903CEDD1E9485A9', X'42BE3B3A532BC944A98ACA0C27EC65E9', X'42BE3B3A532BC944A98ACA0C27EC65E9', 0, NULL, NULL, 2, NULL, 0, 'Cut meat into strips about  (6 mm) thick.');
INSERT INTO `VWR INTERNAL Meatsandsausages5 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CE7825F3E3E56D48A66ABC1798B10094', X'42BE3B3A532BC944A98ACA0C27EC65E9', X'42BE3B3A532BC944A98ACA0C27EC65E9', 1, NULL, NULL, 2, NULL, 0, 'Bring the marinade to a full boil over medium heat.');
INSERT INTO `VWR INTERNAL Meatsandsausages5 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2A76E2BD6764C64A85FFABFBECC009B0', X'42BE3B3A532BC944A98ACA0C27EC65E9', X'42BE3B3A532BC944A98ACA0C27EC65E9', 2, NULL, NULL, 2, NULL, 0, 'Add a few meat strips, making sure there is enough marinade to cover them. Reheat to a full boil.');
INSERT INTO `VWR INTERNAL Meatsandsausages5 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'107C0313C5F8F849AB43E2F02D0F2FB4', X'42BE3B3A532BC944A98ACA0C27EC65E9', X'42BE3B3A532BC944A98ACA0C27EC65E9', 3, NULL, NULL, 2, NULL, 0, 'Remove quickly meat strips and place on a drying screen.');
INSERT INTO `VWR INTERNAL Meatsandsausages5 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AB85E0361A6B054C907FCB3E28E26C8B', X'42BE3B3A532BC944A98ACA0C27EC65E9', X'42BE3B3A532BC944A98ACA0C27EC65E9', 4, NULL, NULL, 2, NULL, 0, 'Repeat steps 3 & 4 until all meat strips have been precooked.');
INSERT INTO `VWR INTERNAL Meatsandsausages5 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7A41D64AA164A64CA95534D644115918', X'42BE3B3A532BC944A98ACA0C27EC65E9', X'42BE3B3A532BC944A98ACA0C27EC65E9', 5, NULL, NULL, 2, NULL, 0, 'Dry at 140 F (60 C) in dehydrator, oven or smoker. You can apply smoke for 30 minutes at this stage.');
INSERT INTO `VWR INTERNAL Meatsandsausages5 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A4221666D44B854A8783BDB9C273DC75', X'42BE3B3A532BC944A98ACA0C27EC65E9', X'42BE3B3A532BC944A98ACA0C27EC65E9', 6, NULL, NULL, 2, NULL, 0, 'Cool a sample piece of jerky and bend it. It should crack but not break.');
INSERT INTO `VWR INTERNAL Meatsandsausages5 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'263076EF26D34D4EBC584925966F6924', X'42BE3B3A532BC944A98ACA0C27EC65E9', X'42BE3B3A532BC944A98ACA0C27EC65E9', 7, NULL, NULL, 2, NULL, 0, 'When done, place jerky in a plastic bag and refrigerate.');
INSERT INTO `VWR INTERNAL Meatsandsausages5 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2B1BEF19C3826043B28CA927D33A2F5D', X'E5D109E106AB9748A13BB5AA23712F6B', X'E5D109E106AB9748A13BB5AA23712F6B', 0, NULL, NULL, 2, NULL, 0, 'Soak livers in cold water for 30 minutes, then grind them through ? (3 mm) grinder plate or chop them in a food processor until bubbles appear on the surface of the emulsion which may take 5-10 minutes.');
INSERT INTO `VWR INTERNAL Meatsandsausages5 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8799BEBD5918AB4687BD975A3558E76A', X'E5D109E106AB9748A13BB5AA23712F6B', X'E5D109E106AB9748A13BB5AA23712F6B', 1, NULL, NULL, 2, NULL, 0, 'Grind meats through  (6 mm) plate.');
INSERT INTO `VWR INTERNAL Meatsandsausages5 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F36C15896529CC4C80D6D1A1D5D1D655', X'E5D109E106AB9748A13BB5AA23712F6B', X'E5D109E106AB9748A13BB5AA23712F6B', 2, NULL, NULL, 2, NULL, 0, 'Dissolve gelatin in water.');
INSERT INTO `VWR INTERNAL Meatsandsausages5 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CA5DD872CF887840B6001226F8D5347B', X'E5D109E106AB9748A13BB5AA23712F6B', X'E5D109E106AB9748A13BB5AA23712F6B', 3, NULL, NULL, 2, NULL, 0, 'Mix everything together.');
INSERT INTO `VWR INTERNAL Meatsandsausages5 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7CECE8F18F0339499A69B1688AE0B778', X'E5D109E106AB9748A13BB5AA23712F6B', X'E5D109E106AB9748A13BB5AA23712F6B', 4, NULL, NULL, 2, NULL, 0, 'Stuff into a ham press.');
INSERT INTO `VWR INTERNAL Meatsandsausages5 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'102D98704C058145930B4D3B187F5F2C', X'E5D109E106AB9748A13BB5AA23712F6B', X'E5D109E106AB9748A13BB5AA23712F6B', 5, NULL, NULL, 2, NULL, 0, 'Transfer mold press to a cooking tank with hot water.');
INSERT INTO `VWR INTERNAL Meatsandsausages5 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D4C6B3BD6873E043AEA3682F720FBC40', X'E5D109E106AB9748A13BB5AA23712F6B', X'E5D109E106AB9748A13BB5AA23712F6B', 6, NULL, NULL, 2, NULL, 0, 'Let the temperature drop to 160F (72 C) and hold this temperature until internal meat temperature reaches 154 F (68 C).');
INSERT INTO `VWR INTERNAL Meatsandsausages5 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F0415C8C9FA7014DA541F93759F0BF8F', X'E5D109E106AB9748A13BB5AA23712F6B', X'E5D109E106AB9748A13BB5AA23712F6B', 7, NULL, NULL, 2, NULL, 0, 'Chill loaves in cold water and then transfer to refrigerator overnight.');
INSERT INTO `VWR INTERNAL Meatsandsausages5 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5DBC8971752E8D4DA95B63FC57384910', X'E5D109E106AB9748A13BB5AA23712F6B', X'E5D109E106AB9748A13BB5AA23712F6B', 8, NULL, NULL, 2, NULL, 0, 'Remove loaf from the mold press.');
INSERT INTO `VWR INTERNAL Meatsandsausages5 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5E4E75E7C3635D4C9A9ED189654052E7', X'E5D109E106AB9748A13BB5AA23712F6B', X'E5D109E106AB9748A13BB5AA23712F6B', 9, NULL, NULL, 2, NULL, 0, 'Keep refrigerated.');
INSERT INTO `VWR INTERNAL Meatsandsausages5 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B517EC7FB42D164A81392CBDF3FFE85D', X'16D7024273A2154BAABB0741C57588C1', X'16D7024273A2154BAABB0741C57588C1', 0, NULL, NULL, 2, NULL, 0, 'Remove all bones and trim down the fat until you reach the lean meat.');
INSERT INTO `VWR INTERNAL Meatsandsausages5 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'16A60700BAC2EA40991BFDF3A581DB22', X'16D7024273A2154BAABB0741C57588C1', X'16D7024273A2154BAABB0741C57588C1', 1, NULL, NULL, 2, NULL, 0, 'Mix salt, Cure #2 and all ingredients together.');
INSERT INTO `VWR INTERNAL Meatsandsausages5 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CA790DF3268C654FAB80882BB5B824B7', X'16D7024273A2154BAABB0741C57588C1', X'16D7024273A2154BAABB0741C57588C1', 2, NULL, NULL, 2, NULL, 0, 'Add little water to the mixture and rub the wet mixture into the loin.');
INSERT INTO `VWR INTERNAL Meatsandsausages5 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F2667B1D396557439773D2FF81464E16', X'16D7024273A2154BAABB0741C57588C1', X'16D7024273A2154BAABB0741C57588C1', 3, NULL, NULL, 2, NULL, 0, 'Place into a plastic bag and leave for 7-10 days in refrigerator.');
INSERT INTO `VWR INTERNAL Meatsandsausages5 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3631E997712FF34782674F730524D6EA', X'16D7024273A2154BAABB0741C57588C1', X'16D7024273A2154BAABB0741C57588C1', 4, NULL, NULL, 2, NULL, 0, 'Remove loin from refrigerator and brush off the excess salt. Stuff into synthetic or collagen casings.');
INSERT INTO `VWR INTERNAL Meatsandsausages5 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BE4B65E3CE668C4C94EC3122284439BD', X'16D7024273A2154BAABB0741C57588C1', X'16D7024273A2154BAABB0741C57588C1', 5, NULL, NULL, 2, NULL, 0, 'Hang loin at 46 F (8 C), 72-80% humidity for one month. This is the salt equalization stage.');
INSERT INTO `VWR INTERNAL Meatsandsausages5 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'085F414249E9C34CAE6790AF44FDB388', X'16D7024273A2154BAABB0741C57588C1', X'16D7024273A2154BAABB0741C57588C1', 6, NULL, NULL, 2, NULL, 0, 'Dry loins for 3 weeks at 68-72 F (20-22 C). Moisture removal and flavor development takes place at this stage.');
INSERT INTO `VWR INTERNAL Meatsandsausages5 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4D502534F289E440B68136097CA4BFB8', X'16D7024273A2154BAABB0741C57588C1', X'16D7024273A2154BAABB0741C57588C1', 7, NULL, NULL, 2, NULL, 0, 'Loin is ready when it loses about 40% of its original weight.');
INSERT INTO `VWR INTERNAL Meatsandsausages5 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9433516CC10F9E4B94065E4B5A60BA3E', X'F5F478FC823E784698684EC3E8635A06', X'F5F478FC823E784698684EC3E8635A06', 0, NULL, NULL, 2, NULL, 0, 'Mix salt and Cure #1 together.');
INSERT INTO `VWR INTERNAL Meatsandsausages5 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E5EFCD5A9AB5224AA3C899D856DE39A7', X'F5F478FC823E784698684EC3E8635A06', X'F5F478FC823E784698684EC3E8635A06', 1, NULL, NULL, 2, NULL, 0, 'Rub  of the mix into the belly.');
INSERT INTO `VWR INTERNAL Meatsandsausages5 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DE2291E23BCC7F4593FD7B38447B2508', X'F5F478FC823E784698684EC3E8635A06', X'F5F478FC823E784698684EC3E8635A06', 2, NULL, NULL, 2, NULL, 0, 'Place the belly in a zip lock plastic bag. Leave for 4 days in refrigerator.');
INSERT INTO `VWR INTERNAL Meatsandsausages5 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'081F2669F39EC84CB6BCAF20AA5B0E77', X'F5F478FC823E784698684EC3E8635A06', X'F5F478FC823E784698684EC3E8635A06', 3, NULL, NULL, 2, NULL, 0, 'Remove the belly from the bag. Mix all spices with the remaining half of the curing mixture (salt and cure #1). Rub the mixture into the belly. Refrigerate for 4 more days.');
INSERT INTO `VWR INTERNAL Meatsandsausages5 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B973F5E6F678154B8A06B60BFF2CA36B', X'F5F478FC823E784698684EC3E8635A06', X'F5F478FC823E784698684EC3E8635A06', 4, NULL, NULL, 2, NULL, 0, 'Wash belly with tap water and then soak it for 15 minutes in cold water.');
INSERT INTO `VWR INTERNAL Meatsandsausages5 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'27D8EE55264569489AA031B8914614E6', X'F5F478FC823E784698684EC3E8635A06', X'F5F478FC823E784698684EC3E8635A06', 5, NULL, NULL, 2, NULL, 0, 'Dry belly for 2 days at 54 F (12 C).');
INSERT INTO `VWR INTERNAL Meatsandsausages5 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'731C121BAFD96D4899A5AD390AF7F8E9', X'F5F478FC823E784698684EC3E8635A06', X'F5F478FC823E784698684EC3E8635A06', 6, NULL, NULL, 2, NULL, 0, 'Remove the skin and divide the belly in two cutting along its length.');
INSERT INTO `VWR INTERNAL Meatsandsausages5 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'788719F628EF2D498E686DA9F0900459', X'F5F478FC823E784698684EC3E8635A06', X'F5F478FC823E784698684EC3E8635A06', 7, NULL, NULL, 2, NULL, 0, 'Soak 5 (120 mm) fibrous casing for 1 hour in water. Roll each belly into a tight roll and insert into the casing. Run butcher twine around the pancetta every  and make a hanging loop. Remove any air pockets with a needle.');
INSERT INTO `VWR INTERNAL Meatsandsausages5 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4F8126E555F63A4DA8B17974E4039DD8', X'F5F478FC823E784698684EC3E8635A06', X'F5F478FC823E784698684EC3E8635A06', 8, NULL, NULL, 2, NULL, 0, 'Hang for 3 weeks at 54 F (12 C).');
INSERT INTO `VWR INTERNAL Meatsandsausages5 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'909BF19C2ED96246A2B37CCFF659BB96', X'F5F478FC823E784698684EC3E8635A06', X'F5F478FC823E784698684EC3E8635A06', 9, NULL, NULL, 2, NULL, 0, 'Refrigerate.');
INSERT INTO `VWR INTERNAL Meatsandsausages5 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'077504AC3ECB0A41B2CA0CCF289E834D', X'30C1258883CD97468D7BD0CE18BB659F', X'30C1258883CD97468D7BD0CE18BB659F', 0, NULL, NULL, 2, NULL, 0, 'Cut meat into 8 x 3 x 2` slices.');
INSERT INTO `VWR INTERNAL Meatsandsausages5 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'36EBCF05F19A5547A598F7640D4EACC0', X'30C1258883CD97468D7BD0CE18BB659F', X'30C1258883CD97468D7BD0CE18BB659F', 1, NULL, NULL, 2, NULL, 0, 'Mix salt with Cure #2 and rub the mixture into the meat.');
INSERT INTO `VWR INTERNAL Meatsandsausages5 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5F02C4402081B54E8D341DB22623D0BE', X'30C1258883CD97468D7BD0CE18BB659F', X'30C1258883CD97468D7BD0CE18BB659F', 2, NULL, NULL, 2, NULL, 0, 'Sprinkle some salt in a container and place meat slices on top of one another, separating them with salt. Place a wooden board on top of them and put a weight on top. Pastirma is a noun derived from the verb pastirmak (bastirmak in modern Turkish), which means `to press`.');
INSERT INTO `VWR INTERNAL Meatsandsausages5 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'921C6168807F2B46802D2904D40B2163', X'30C1258883CD97468D7BD0CE18BB659F', X'30C1258883CD97468D7BD0CE18BB659F', 3, NULL, NULL, 2, NULL, 0, 'Keep for 1 week in refrigerator.');
INSERT INTO `VWR INTERNAL Meatsandsausages5 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'988F448F32722C4FAE38940D8C2D7BD7', X'30C1258883CD97468D7BD0CE18BB659F', X'30C1258883CD97468D7BD0CE18BB659F', 4, NULL, NULL, 2, NULL, 0, 'Remove meat from the refrigerator and wash with water. Brush off the surface of any salt. Place again in a container for 1 more day, covering meat with a weighted board. This will drain more liquid away.');
INSERT INTO `VWR INTERNAL Meatsandsausages5 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B0A3C5E0513CC3438EDCF8C3371668A0', X'30C1258883CD97468D7BD0CE18BB659F', X'30C1258883CD97468D7BD0CE18BB659F', 5, NULL, NULL, 2, NULL, 0, 'Mix all ingredients adding 1 tsp of salt. Add a little water and make the mixture into a paste (known as emen).');
INSERT INTO `VWR INTERNAL Meatsandsausages5 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F60F092278BB1649A3A1627A98A6DA44', X'30C1258883CD97468D7BD0CE18BB659F', X'30C1258883CD97468D7BD0CE18BB659F', 6, NULL, NULL, 2, NULL, 0, 'Cover the meat with the paste and hang in a dry, well ventilated area (50-59 F, 10-15 C, 60% humidity). You can use hooks or netting. After about 5 days the paste has dried and Pastirma is ready although it may be further dried.');
INSERT INTO `VWR INTERNAL Meatsandsausages5 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D6193385EAB8A24FA8F9BFE0591F9330', X'436B2F5FA9BD3745BD61D90D6F56D501', X'436B2F5FA9BD3745BD61D90D6F56D501', 0, NULL, NULL, 2, NULL, 0, 'Curing solution: 35 degrees SAL (10 Baume), 101 g salt per 1 liter of water Salt, 90 g (5 Tbsp) Cure #1, 18 g (1 Tbsp) Sugar, 10 g (2 tsp) Water, 1 liter');
INSERT INTO `VWR INTERNAL Meatsandsausages5 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9E691D41A9C04F4F9F7B5E46CE63FB8B', X'436B2F5FA9BD3745BD61D90D6F56D501', X'436B2F5FA9BD3745BD61D90D6F56D501', 1, NULL, NULL, 2, NULL, 0, 'Spice mixture: Black pepper, cracked, 10 g (1 Tbsp) Coriander seeds, whole or cracked, 10 g (1 Tbsp) Garlic, 3.5 g (1 clove)');
INSERT INTO `VWR INTERNAL Meatsandsausages5 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2AE744C377253C47B39FAC55930B1E9A', X'436B2F5FA9BD3745BD61D90D6F56D501', X'436B2F5FA9BD3745BD61D90D6F56D501', 2, NULL, NULL, 2, NULL, 0, 'Mix brine ingredients in warm water, then cool the brine in refrigerator.');
INSERT INTO `VWR INTERNAL Meatsandsausages5 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'35AD1F2FAEBE474F9A115CC77C4FBDAC', X'436B2F5FA9BD3745BD61D90D6F56D501', X'436B2F5FA9BD3745BD61D90D6F56D501', 3, NULL, NULL, 2, NULL, 0, 'Pumping meat. Pump brisket with 10% curing solution (brisket should weigh 110% of its original weight). Add the remaining brine to cover the meat and cure for 2 days (3-4 days for 2-3 kg meat). OR');
INSERT INTO `VWR INTERNAL Meatsandsausages5 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'485F1A556D019D468EFB28B07BA850CE', X'436B2F5FA9BD3745BD61D90D6F56D501', X'436B2F5FA9BD3745BD61D90D6F56D501', 4, NULL, NULL, 2, NULL, 0, 'No pumping meat. Cover brisket with brine and cure for 4 days. Use about 4 days per 1 kg of meat.');
INSERT INTO `VWR INTERNAL Meatsandsausages5 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'95A665D1A6F6C04D997AD1179F01B630', X'436B2F5FA9BD3745BD61D90D6F56D501', X'436B2F5FA9BD3745BD61D90D6F56D501', 5, NULL, NULL, 2, NULL, 0, 'Remove brisket from brine, wash, drain and wipe dry.');
INSERT INTO `VWR INTERNAL Meatsandsausages5 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D4C31053B5925547A5806C89323DED5A', X'436B2F5FA9BD3745BD61D90D6F56D501', X'436B2F5FA9BD3745BD61D90D6F56D501', 6, NULL, NULL, 2, NULL, 0, 'Coat the brisket with spice mixture. Be generous with your rub, it should be applied in a thick coating.');
INSERT INTO `VWR INTERNAL Meatsandsausages5 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B5C92B33794DF249A695191946CBF4B5', X'436B2F5FA9BD3745BD61D90D6F56D501', X'436B2F5FA9BD3745BD61D90D6F56D501', 7, NULL, NULL, 2, NULL, 0, 'Hang in a preheated to 130 F (54 C) smokehouse (no smoke applied). With dampers fully open, hold at this temperature for about one hour, until meat surface is dry.');
INSERT INTO `VWR INTERNAL Meatsandsausages5 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A6161208830CE14FABCD198D3A97B751', X'436B2F5FA9BD3745BD61D90D6F56D501', X'436B2F5FA9BD3745BD61D90D6F56D501', 8, NULL, NULL, 2, NULL, 0, 'Re-adjust dampers to 1/4 open and apply light smoke for 2 hours. Raise temperature to 200 - 220 F (93 - 104 C). Cook until an internal temperature of 165F (74 C) is reached.');
INSERT INTO `VWR INTERNAL Meatsandsausages5 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E03241F27201F442BBAA7F8FC9F45514', X'436B2F5FA9BD3745BD61D90D6F56D501', X'436B2F5FA9BD3745BD61D90D6F56D501', 9, NULL, NULL, 2, NULL, 0, 'Hang at room temperature for 1-2 hours. Serve or refrigerate.');
INSERT INTO `VWR INTERNAL Meatsandsausages5 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D3F3357B69F9AC4988599944D71C102B', X'436B2F5FA9BD3745BD61D90D6F56D501', X'436B2F5FA9BD3745BD61D90D6F56D501', 10, NULL, NULL, 2, NULL, 0, 'Pastrami may be dry cured. For 1 kg (2.20 lb.) beef use: Salt, 35 g, 2 Tbsp. Cure #1, 5 g (1 tsp). This comes to 312 ppm of sodium nitrite. The USDA allows 625 ppm for dry curing meat. Sodium nitrite dissipates rapidly, after curing, smoking and cooking not much is left. Note: there is 4.69 g salt in Cure #1');
INSERT INTO `VWR INTERNAL Meatsandsausages5 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E857396F8726D542BEBD782605655AD0', X'436B2F5FA9BD3745BD61D90D6F56D501', X'436B2F5FA9BD3745BD61D90D6F56D501', 11, NULL, NULL, 2, NULL, 0, 'Spice mixture: Cracked black pepper, 3 tsp Coriander seeds, 3 tsp. Brown sugar, 2 tsp. Garlic, 1 clove, finely minced');
INSERT INTO `VWR INTERNAL Meatsandsausages5 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'22A7259D2A2FF7448E1E4E71C4930CE2', X'436B2F5FA9BD3745BD61D90D6F56D501', X'436B2F5FA9BD3745BD61D90D6F56D501', 12, NULL, NULL, 2, NULL, 0, 'Mix salt, cure, and spice mixture together and rub it into the meat. Massage it well and cover the meat?s surface evenly.');
INSERT INTO `VWR INTERNAL Meatsandsausages5 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4BAEC7209686224C9D6DD269CB51D44F', X'436B2F5FA9BD3745BD61D90D6F56D501', X'436B2F5FA9BD3745BD61D90D6F56D501', 13, NULL, NULL, 2, NULL, 0, 'Place meat in a Ziplock plastic bag.');
INSERT INTO `VWR INTERNAL Meatsandsausages5 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F5DFD85CA6D0EE4E90EDA17739ABBA4B', X'436B2F5FA9BD3745BD61D90D6F56D501', X'436B2F5FA9BD3745BD61D90D6F56D501', 14, NULL, NULL, 2, NULL, 0, 'Hold in a refrigerator for 5 days, turning the package daily.');
INSERT INTO `VWR INTERNAL Meatsandsausages5 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6C1C2A024014C548BABA718F9E1085C5', X'436B2F5FA9BD3745BD61D90D6F56D501', X'436B2F5FA9BD3745BD61D90D6F56D501', 15, NULL, NULL, 2, NULL, 0, 'Remove meat from the package, rinse with cold water, drain, pat dry with paper towel.');
INSERT INTO `VWR INTERNAL Meatsandsausages5 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EEDB40A7B7824144A6B4AAECB48267FF', X'436B2F5FA9BD3745BD61D90D6F56D501', X'436B2F5FA9BD3745BD61D90D6F56D501', 16, NULL, NULL, 2, NULL, 0, 'Make another spice mixture and coat the brisket with it. Be generous with your rub, it should be applied in a thick coating.');
INSERT INTO `VWR INTERNAL Meatsandsausages5 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'36470EE8FB0CD84EB4629A512989043D', X'436B2F5FA9BD3745BD61D90D6F56D501', X'436B2F5FA9BD3745BD61D90D6F56D501', 17, NULL, NULL, 2, NULL, 0, 'Smoking/cooking - follow steps 8, 9, and 10.');
INSERT INTO `VWR INTERNAL Meatsandsausages5 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'13B750F24CA3164F86CBCF7DD4724970', X'E133A18E015F494A9540AB7D61F101EA', X'E133A18E015F494A9540AB7D61F101EA', 0, NULL, NULL, 2, NULL, 0, 'Chop jerky very finely with a knife or grind in a blender to a powder.');
INSERT INTO `VWR INTERNAL Meatsandsausages5 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BAA4E7024C9988468B664E8FE2B6FB4D', X'E133A18E015F494A9540AB7D61F101EA', X'E133A18E015F494A9540AB7D61F101EA', 1, NULL, NULL, 2, NULL, 0, 'Grind/chop other ingredients and mix with pulverized jerky.');
INSERT INTO `VWR INTERNAL Meatsandsausages5 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'157F41D309905147A55E40072E9E4914', X'E133A18E015F494A9540AB7D61F101EA', X'E133A18E015F494A9540AB7D61F101EA', 2, NULL, NULL, 2, NULL, 0, 'Mix jerky powder with powdered fruits.');
INSERT INTO `VWR INTERNAL Meatsandsausages5 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1315AB23D696054B8670D341088E738D', X'E133A18E015F494A9540AB7D61F101EA', X'E133A18E015F494A9540AB7D61F101EA', 3, NULL, NULL, 2, NULL, 0, 'Cut the fat (suet, also known as tallow) into chunks and melt down over low heat, until it becomes a yellow-brown liquid. Continue to heat, stirring often, until more moisture is removed. Strain it through the filter to remove any impurities and allow to cool - it will become white.');
INSERT INTO `VWR INTERNAL Meatsandsausages5 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7FB80DE5BF7D68449D8123ED8C99FCB9', X'E133A18E015F494A9540AB7D61F101EA', X'E133A18E015F494A9540AB7D61F101EA', 4, NULL, NULL, 2, NULL, 0, 'Reheat tallow over low heat and pour over jerky-fruit mixture. The tallow should be cool enough to touch but still liquid.');
INSERT INTO `VWR INTERNAL Meatsandsausages5 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C553467AF8130F4F8EDCAD29C933E645', X'E133A18E015F494A9540AB7D61F101EA', X'E133A18E015F494A9540AB7D61F101EA', 5, NULL, NULL, 2, NULL, 0, 'Place warm pemmican into suitable molds to make blocks or bars. If pemmican solidifies too fast, you can warm it up again in a microwave.');
INSERT INTO `VWR INTERNAL Meatsandsausages5 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AB190B87142632468F5343B3EFC77550', X'E133A18E015F494A9540AB7D61F101EA', X'E133A18E015F494A9540AB7D61F101EA', 6, NULL, NULL, 2, NULL, 0, 'Wrap it up in wax paper or store in plastic bags.');
INSERT INTO `VWR INTERNAL Meatsandsausages5 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'36184A2A45A3C044A60BE344ECE10EFD', X'D908951F7ED15743BA3DDBC7D4AD2398', X'D908951F7ED15743BA3DDBC7D4AD2398', 0, NULL, NULL, 2, NULL, 0, 'Cover the meat and bones with cold water, add salt, pepper and onions and bring to a boil. Reduce heat, skim off the foam cover with lid and simmer for 1-2 hours until meat is tender.');
INSERT INTO `VWR INTERNAL Meatsandsausages5 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A0B788010DB9744298A3A0CB2FBCD9F7', X'D908951F7ED15743BA3DDBC7D4AD2398', X'D908951F7ED15743BA3DDBC7D4AD2398', 1, NULL, NULL, 2, NULL, 0, 'Remove the meat, scrape off the meat from the bones. Discard bones but save the stock.');
INSERT INTO `VWR INTERNAL Meatsandsausages5 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B01E12529BFAD447963314A9D55F6709', X'D908951F7ED15743BA3DDBC7D4AD2398', X'D908951F7ED15743BA3DDBC7D4AD2398', 2, NULL, NULL, 2, NULL, 0, 'Chop the meat very fine by hand or grind through 3/16 (4 mm) plate.');
INSERT INTO `VWR INTERNAL Meatsandsausages5 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A9855C1E7C584A499589BC96F597AAB8', X'D908951F7ED15743BA3DDBC7D4AD2398', X'D908951F7ED15743BA3DDBC7D4AD2398', 3, NULL, NULL, 2, NULL, 0, 'Place meat in stock, mix and taste. Add more salt or pepper if needed. Many recipes call for additional spices, now is the time to add them.');
INSERT INTO `VWR INTERNAL Meatsandsausages5 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AF9000FB61C0F543AAE5FDEFDCCB6330', X'D908951F7ED15743BA3DDBC7D4AD2398', X'D908951F7ED15743BA3DDBC7D4AD2398', 4, NULL, NULL, 2, NULL, 0, 'Make sure the stock with meat is hot and start slowly adding buckwheat flour stirring the mixture with a paddle. Make sure it is smooth and thick - the paddle should stand up in the pot');
INSERT INTO `VWR INTERNAL Meatsandsausages5 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'028D7C0D5893744B87EB6ACFA5AF31D2', X'D908951F7ED15743BA3DDBC7D4AD2398', X'D908951F7ED15743BA3DDBC7D4AD2398', 5, NULL, NULL, 2, NULL, 0, 'Pack the mixture into baking pans about 8 x 4 x 2 or 9 x 4 x 3. The pans should be either oiled or lined up with wax paper. Cover and place in the refrigerator for at least 4 hours.');
INSERT INTO `VWR INTERNAL Meatsandsausages5 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9F43EBA501A77240A449DE3CFC65B742', X'D908951F7ED15743BA3DDBC7D4AD2398', X'D908951F7ED15743BA3DDBC7D4AD2398', 6, NULL, NULL, 2, NULL, 0, 'Cut into 1/2 individual slices. To freeze, place the slices between pieces of butcher paper.');
INSERT INTO `VWR INTERNAL Meatsandsausages5 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C1CF12CC7480BB41885BB4DE62552FFD', X'2E5370F38EAF9A4088DA8B5C5C9A6692', X'2E5370F38EAF9A4088DA8B5C5C9A6692', 0, NULL, NULL, 2, NULL, 0, 'Pigs feet are immersed for 12 days in 1 gallon 80 SAL brine');
INSERT INTO `VWR INTERNAL Meatsandsausages5 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D2DC2795BB572C41A6560614D5A2355A', X'2E5370F38EAF9A4088DA8B5C5C9A6692', X'2E5370F38EAF9A4088DA8B5C5C9A6692', 1, NULL, NULL, 2, NULL, 0, 'Pigs feet are removed from brine, skinned and the entire foot is cooked for 3-4 hours at 200 F, 94 C (below boiling point) until meat separates from bones.');
INSERT INTO `VWR INTERNAL Meatsandsausages5 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1B109552AA9CE4408BA8C0A939515C35', X'2E5370F38EAF9A4088DA8B5C5C9A6692', X'2E5370F38EAF9A4088DA8B5C5C9A6692', 2, NULL, NULL, 2, NULL, 0, 'Feet are rinsed in cold water, then placed in a refrigerator.');
INSERT INTO `VWR INTERNAL Meatsandsausages5 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7AA33B3E9E4055489C4CED0FA7937C9E', X'2E5370F38EAF9A4088DA8B5C5C9A6692', X'2E5370F38EAF9A4088DA8B5C5C9A6692', 3, NULL, NULL, 2, NULL, 0, 'Feet are split and semi-boned.');
INSERT INTO `VWR INTERNAL Meatsandsausages5 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'331CCCCD184139468110CEC53FE994D6', X'2E5370F38EAF9A4088DA8B5C5C9A6692', X'2E5370F38EAF9A4088DA8B5C5C9A6692', 4, NULL, NULL, 2, NULL, 0, 'They are placed in jars with white vinegar. Whole peppercorns and bay leaves are added for flavoring.');
INSERT INTO `VWR INTERNAL Meatsandsausages5 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A8837B8E6967B4458E6897B8AB960463', X'B43A0EF3B9D3E14FBB926E73FAC9C73C', X'B43A0EF3B9D3E14FBB926E73FAC9C73C', 0, NULL, NULL, 2, NULL, 0, 'Mix all ingredients in 1 qt of cold water (40 F, 4 C), then spray pump pork butts to 10% of the green (original) weight.');
INSERT INTO `VWR INTERNAL Meatsandsausages5 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'20B56E662D48DC4B935566CC555B0FCA', X'B43A0EF3B9D3E14FBB926E73FAC9C73C', X'B43A0EF3B9D3E14FBB926E73FAC9C73C', 1, NULL, NULL, 2, NULL, 0, 'Leave butts in the remaining brine for 3 - 4 days in a refrigerator.');
INSERT INTO `VWR INTERNAL Meatsandsausages5 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D74184C22F5DF942A5BE8ED0A12BF452', X'B43A0EF3B9D3E14FBB926E73FAC9C73C', X'B43A0EF3B9D3E14FBB926E73FAC9C73C', 2, NULL, NULL, 2, NULL, 0, 'Take them out, wash briefly with cold water and let drain.');
INSERT INTO `VWR INTERNAL Meatsandsausages5 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'03C936DF1030704592EA2FD7004F9708', X'B43A0EF3B9D3E14FBB926E73FAC9C73C', X'B43A0EF3B9D3E14FBB926E73FAC9C73C', 3, NULL, NULL, 2, NULL, 0, 'Place butts in stockinette bag or hang them on hooks.');
INSERT INTO `VWR INTERNAL Meatsandsausages5 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'61B59A35E92E844E8FE9BB258D7950A8', X'B43A0EF3B9D3E14FBB926E73FAC9C73C', X'B43A0EF3B9D3E14FBB926E73FAC9C73C', 4, NULL, NULL, 2, NULL, 0, 'Hang them in a preheated to 130 F (54 C) smokehouse for 2-3 hours until dry.');
INSERT INTO `VWR INTERNAL Meatsandsausages5 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0B9918EE713D05418EB61C79657DC263', X'B43A0EF3B9D3E14FBB926E73FAC9C73C', X'B43A0EF3B9D3E14FBB926E73FAC9C73C', 5, NULL, NULL, 2, NULL, 0, 'Apply a medium smoke increasing temperature to 150 F for about 3 hours.');
INSERT INTO `VWR INTERNAL Meatsandsausages5 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'946009278A04E9408611BE28434B1F5C', X'B43A0EF3B9D3E14FBB926E73FAC9C73C', X'B43A0EF3B9D3E14FBB926E73FAC9C73C', 6, NULL, NULL, 2, NULL, 0, 'Raise the temperature to 160165 F and continue smoking until an internal temperature of 154 F (68 C) is obtained.');
INSERT INTO `VWR INTERNAL Meatsandsausages5 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E4CC031BDD38284C99039D2BFB4EE7E3', X'B43A0EF3B9D3E14FBB926E73FAC9C73C', X'B43A0EF3B9D3E14FBB926E73FAC9C73C', 7, NULL, NULL, 2, NULL, 0, 'Shower with cold water and hang them at room temperature to start cooling.');
INSERT INTO `VWR INTERNAL Meatsandsausages5 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C44440C2F1431A418D94BE8F8866BBB5', X'B43A0EF3B9D3E14FBB926E73FAC9C73C', X'B43A0EF3B9D3E14FBB926E73FAC9C73C', 8, NULL, NULL, 2, NULL, 0, 'Store in the refrigerator.');
INSERT INTO `VWR INTERNAL Meatsandsausages5 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BD1EDF81323F6F4CB46C82D62B70FA9A', X'4C4F0A7CFA62B242A570B3F7E4A7E3CC', X'4C4F0A7CFA62B242A570B3F7E4A7E3CC', 0, NULL, NULL, 2, NULL, 0, 'Keep lean meats separate from fat trimmings. Grind lean meats through 3/16 plate (5 mm). Refreeze and grind again. Refreeze again and grind through 1/8 (3 mm) plate. Grind fat trimmings through 1/8 (3 mm) plate, refreeze and grind again.');
INSERT INTO `VWR INTERNAL Meatsandsausages5 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D46489EED665344E98006C59652F2702', X'4C4F0A7CFA62B242A570B3F7E4A7E3CC', X'4C4F0A7CFA62B242A570B3F7E4A7E3CC', 1, NULL, NULL, 2, NULL, 0, 'Mix lean ground pork with all ingredients adding 50 ml of cold water. Add ground fat pork and 50 ml of cold water and mix well. Add fat trimmings and the last 50 ml of water and mix everything well together.');
INSERT INTO `VWR INTERNAL Meatsandsausages5 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D114631254CA614F9A52DE4E0E92C2E8', X'4C4F0A7CFA62B242A570B3F7E4A7E3CC', X'4C4F0A7CFA62B242A570B3F7E4A7E3CC', 2, NULL, NULL, 2, NULL, 0, 'Stuff firmly into 28-32 mm hog casings. Form 4-5 (10-12 cm) long links.');
INSERT INTO `VWR INTERNAL Meatsandsausages5 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'81A5DE70C9739E49AE69BE85BCE7B533', X'4C4F0A7CFA62B242A570B3F7E4A7E3CC', X'4C4F0A7CFA62B242A570B3F7E4A7E3CC', 3, NULL, NULL, 2, NULL, 0, 'Cook in hot water, at 167 F, 75 C until internal meat temperature reaches 154-158 F (68-70 C). This should take about 15 minutes.');
INSERT INTO `VWR INTERNAL Meatsandsausages5 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'584757D82B4E084EAD0F549A9F781C14', X'4C4F0A7CFA62B242A570B3F7E4A7E3CC', X'4C4F0A7CFA62B242A570B3F7E4A7E3CC', 4, NULL, NULL, 2, NULL, 0, 'Shower with cold water for 5 minutes.');
INSERT INTO `VWR INTERNAL Meatsandsausages5 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C8E2421A60C20F4FBDE19C2A6F20828A', X'4C4F0A7CFA62B242A570B3F7E4A7E3CC', X'4C4F0A7CFA62B242A570B3F7E4A7E3CC', 5, NULL, NULL, 2, NULL, 0, 'Keep in a refrigerator.');
INSERT INTO `VWR INTERNAL Meatsandsausages5 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7D84132817F2C544983010B0CA12E41B', X'BA5F7551D9D63748B238BFE179FED6AF', X'BA5F7551D9D63748B238BFE179FED6AF', 0, NULL, NULL, 2, NULL, 0, 'Grind pork through ? (10 mm) plate.');
INSERT INTO `VWR INTERNAL Meatsandsausages5 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8547EEDE86A60A4BB90B6DD4CEC22E2D', X'BA5F7551D9D63748B238BFE179FED6AF', X'BA5F7551D9D63748B238BFE179FED6AF', 1, NULL, NULL, 2, NULL, 0, 'Mix ground meat with all ingredients.');
INSERT INTO `VWR INTERNAL Meatsandsausages5 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'04EF9762341DD945A7F21C34FD1B05FD', X'BA5F7551D9D63748B238BFE179FED6AF', X'BA5F7551D9D63748B238BFE179FED6AF', 2, NULL, NULL, 2, NULL, 0, 'Stuff into pans and bake at 212 F (100 C) until the internal temperature reaches 154 F (68 C).');
INSERT INTO `VWR INTERNAL Meatsandsausages5 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C13B259D46A7B946AEB5C973854F2126', X'BA5F7551D9D63748B238BFE179FED6AF', X'BA5F7551D9D63748B238BFE179FED6AF', 3, NULL, NULL, 2, NULL, 0, 'Let cool in refrigerator before removing from the pan and slicing.');
INSERT INTO `VWR INTERNAL Meatsandsausages5 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'680B410E7A341046969A714B8A8808F6', X'957092B842E37F4AB43C8C08EEDDE2F1', X'957092B842E37F4AB43C8C08EEDDE2F1', 0, NULL, NULL, 2, NULL, 0, 'Wet cure method. Mix all ingredients with cold water. Using a needle syringe, pump the loin to 3-4 % of its green weight. That means you are taking the amount of brine that equals 3-4% of the loin meat weight. In this case it comes to 60-80 g (2.1 - 2.8 oz) or 1/3 cup. The loin then is placed in the leftover brine and placed in a refrigerator for 1 day. If meat is not pump injected, leave it in brine for 2 days, overhauling on the second day. Loins must be covered with brine, use some kind of weight on top of them.');
INSERT INTO `VWR INTERNAL Meatsandsausages5 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E43F65B97DBA564A8011BA5532992A8D', X'957092B842E37F4AB43C8C08EEDDE2F1', X'957092B842E37F4AB43C8C08EEDDE2F1', 1, NULL, NULL, 2, NULL, 0, 'Wash the outside of the loin briefly with tap water. It is wise to taste it now and if it is too salty place in cold water (refrigerator) for a few hours. If you want it saltier, stick it back into the brine for a few hours. Let it drain and dry for 6-8 hours at room temperature. Then you can rub the dry loin all over with the yolk of an egg. That will give the loin a beautiful gold color on the outside after smoking.');
INSERT INTO `VWR INTERNAL Meatsandsausages5 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'57456CD53AD84740A1798D2A8F198E5E', X'957092B842E37F4AB43C8C08EEDDE2F1', X'957092B842E37F4AB43C8C08EEDDE2F1', 2, NULL, NULL, 2, NULL, 0, 'Preheat the smoker to 130 F(54 C) and if the loin is dry, introduce smoke with dampers 1/4 closed and smoke for 1 hour at 122-140 F (50-60 C). Increase smoking temperature to 150 F (66 C) applying smoke all the time and stop when the meats internal temperature reaches?142 F (61 C). Trichinae, almost non-existent in American pork, dies at 137 F(58 C).');
INSERT INTO `VWR INTERNAL Meatsandsausages5 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7A121BF560D1D84FB865732BBB01814B', X'957092B842E37F4AB43C8C08EEDDE2F1', X'957092B842E37F4AB43C8C08EEDDE2F1', 3, NULL, NULL, 2, NULL, 0, 'Shower with cold water until the internal temperature is reduced to 110 F (44 C). Hang at room temperature for 1 hour then place in a refrigerator.');
INSERT INTO `VWR INTERNAL Meatsandsausages5 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'284E7A98597CBC4D8317E73FE53F0C5B', X'6F63CC7EBEB43E44B82B4D0FC269708C', X'6F63CC7EBEB43E44B82B4D0FC269708C', 0, NULL, NULL, 2, NULL, 0, 'Place pigs feet in cold water, add whole peppercorns, bay leaf, carrot, onion and cook pigs feet until meat separates from bones. Use enough water to cover the feet. This might take 3 hours.');
INSERT INTO `VWR INTERNAL Meatsandsausages5 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8A91071AB16DCD4EA38C03409A14BAAE', X'6F63CC7EBEB43E44B82B4D0FC269708C', X'6F63CC7EBEB43E44B82B4D0FC269708C', 1, NULL, NULL, 2, NULL, 0, 'Remove feet but save the stock. Remove fat from the top, then clarify meat stock by filtering it through cheese cloth. Add about 1.5% of vinegar which comes to about 1 Tsp per liter of stock.');
INSERT INTO `VWR INTERNAL Meatsandsausages5 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4DB7BEA3EA2F814CA33180FB190B74C7', X'6F63CC7EBEB43E44B82B4D0FC269708C', X'6F63CC7EBEB43E44B82B4D0FC269708C', 2, NULL, NULL, 2, NULL, 0, 'Separate meat from bones when feet are still hot.');
INSERT INTO `VWR INTERNAL Meatsandsausages5 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'979DFDE12ABF6646A5E7B3859CB02ED5', X'6F63CC7EBEB43E44B82B4D0FC269708C', X'6F63CC7EBEB43E44B82B4D0FC269708C', 3, NULL, NULL, 2, NULL, 0, 'Pour a little stock into the container.');
INSERT INTO `VWR INTERNAL Meatsandsausages5 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C90C7F8425B7094880B3A0101B1585E0', X'6F63CC7EBEB43E44B82B4D0FC269708C', X'6F63CC7EBEB43E44B82B4D0FC269708C', 4, NULL, NULL, 2, NULL, 0, 'Place meat on the bottom of the container.');
INSERT INTO `VWR INTERNAL Meatsandsausages5 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F2FB13880A7DBD4B9D2C401CA002414B', X'6F63CC7EBEB43E44B82B4D0FC269708C', X'6F63CC7EBEB43E44B82B4D0FC269708C', 5, NULL, NULL, 2, NULL, 0, 'Pour stock carefully into the container.');
INSERT INTO `VWR INTERNAL Meatsandsausages5 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3005AAFDD4B0514AA2B50629B7CBE5FB', X'6F63CC7EBEB43E44B82B4D0FC269708C', X'6F63CC7EBEB43E44B82B4D0FC269708C', 6, NULL, NULL, 2, NULL, 0, 'Place in refrigerator.');
INSERT INTO `VWR INTERNAL Meatsandsausages5 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EDA87F9BF5C351408C939F2016E5A705', X'507840379F0D444DA02CC47DCDFF1238', X'507840379F0D444DA02CC47DCDFF1238', 0, NULL, NULL, 2, NULL, 0, 'Cut pork butt across the grain into 1 thick strips and remove excess fat. Mix together all ingredients and coat the pork strips on all sides. Place in a container (plastic Zip-Loc bag is fine) and keep in refrigerator for 2 days. Turn pork strips over once a day or shake the bag.');
INSERT INTO `VWR INTERNAL Meatsandsausages5 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C083D931BD9F5149BFFB19C8E759AB65', X'507840379F0D444DA02CC47DCDFF1238', X'507840379F0D444DA02CC47DCDFF1238', 1, NULL, NULL, 2, NULL, 0, 'Remove the pork pieces and hang them on S-hooks for 1-2 hours until dry.');
INSERT INTO `VWR INTERNAL Meatsandsausages5 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7F0EFB932951804FB95978A1B1CCF95C', X'507840379F0D444DA02CC47DCDFF1238', X'507840379F0D444DA02CC47DCDFF1238', 2, NULL, NULL, 2, NULL, 0, 'Preheat smoker to 130 F, (55 C) and if meat feels dry apply smoke for 3 hours. If the surface of the tasso is still wet, hold it inside the smoker (no smoke) until it feels dry. Then in the last hour of smoking try to gradually increase the temperature to about 160 F, (71 C).');
INSERT INTO `VWR INTERNAL Meatsandsausages5 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'20184F97FBFEEE4B9F368C5BC775F735', X'507840379F0D444DA02CC47DCDFF1238', X'507840379F0D444DA02CC47DCDFF1238', 3, NULL, NULL, 2, NULL, 0, 'Increase temperature to 170-180 F, 77-82 C, (no smoke necessary) and cook tasso to an internal temperature of 154 F (68 C).');
INSERT INTO `VWR INTERNAL Meatsandsausages5 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CEAA89B9B747ED4BA629E39151AF9474', X'507840379F0D444DA02CC47DCDFF1238', X'507840379F0D444DA02CC47DCDFF1238', 4, NULL, NULL, 2, NULL, 0, 'Store in refrigerator.');
INSERT INTO `VWR INTERNAL Meatsandsausages5 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E13BB59242EC8F41A84EBACE2FA2C1F9', X'8707C81E5FA69D44B63B3587624A8B77', X'8707C81E5FA69D44B63B3587624A8B77', 0, NULL, NULL, 2, NULL, 0, 'Curing solution, 25 deg SAL, (6 Be): Salt, 30 g (5 tsp), Cure #1, 4.5 g (3/4 tsp), Sugar, 5 g (1 tsp), Water, 500 ml');
INSERT INTO `VWR INTERNAL Meatsandsausages5 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9115B07CF08C274DAA352AF5DB49F05A', X'8707C81E5FA69D44B63B3587624A8B77', X'8707C81E5FA69D44B63B3587624A8B77', 1, NULL, NULL, 2, NULL, 0, 'Remove any membrane and outside fat. Place 1 kg tenderloin in 0.5 l curing solution and cure for 3 days in refrigerator.');
INSERT INTO `VWR INTERNAL Meatsandsausages5 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5FBBCAB1A268C84996026BC77FE915A8', X'8707C81E5FA69D44B63B3587624A8B77', X'8707C81E5FA69D44B63B3587624A8B77', 2, NULL, NULL, 2, NULL, 0, 'Drain for 6 hours.');
INSERT INTO `VWR INTERNAL Meatsandsausages5 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'993681560DF6A2478291ED79E49A9505', X'8707C81E5FA69D44B63B3587624A8B77', X'8707C81E5FA69D44B63B3587624A8B77', 3, NULL, NULL, 2, NULL, 0, 'Make a hanging loop 3 cm (1`) at the thicker end.');
INSERT INTO `VWR INTERNAL Meatsandsausages5 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2B150165A5C39245B2A2B33536631782', X'8707C81E5FA69D44B63B3587624A8B77', X'8707C81E5FA69D44B63B3587624A8B77', 4, NULL, NULL, 2, NULL, 0, 'Crush garlic and add with paprika to 1 liter od water. Bring to a boil, then dip tenderloins for 30 seconds in the solution.');
INSERT INTO `VWR INTERNAL Meatsandsausages5 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FD493E920A04CC4BA7178110A4F038A1', X'8707C81E5FA69D44B63B3587624A8B77', X'8707C81E5FA69D44B63B3587624A8B77', 5, NULL, NULL, 2, NULL, 0, 'Hang for 1 hour to dry.');
INSERT INTO `VWR INTERNAL Meatsandsausages5 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5D441C599B878C4CA7DDBFD2659B27E2', X'8707C81E5FA69D44B63B3587624A8B77', X'8707C81E5FA69D44B63B3587624A8B77', 6, NULL, NULL, 2, NULL, 0, 'Apply hot smoke 45-60 C, 113-140 F, for 80-90 min, then bake for 20 minutes at 80-85 C, 176-185 F. The tenderloin should reach 60-70 C, 140-158 F internal meat temperature and be of the light brown color.');
INSERT INTO `VWR INTERNAL Meatsandsausages5 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'93025A8333C8984EA790FF8EC4F63247', X'8707C81E5FA69D44B63B3587624A8B77', X'8707C81E5FA69D44B63B3587624A8B77', 7, NULL, NULL, 2, NULL, 0, 'Cool in the air to < 18 C, 64 F.');
