INSERT INTO `VWR INTERNAL Meatsandsausages6 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F67F987C3BC38547A448FEA4BA3F79BD', X'1F63904C8B19AC4BB01C1DFEA9BF89B1', X'1F63904C8B19AC4BB01C1DFEA9BF89B1', 1, NULL, NULL, 2, NULL, 0, 'Fry a slice of bread in olive oil on both sides.');
INSERT INTO `VWR INTERNAL Meatsandsausages6 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'19EBEB734956C14F862885C2D96CAFCB', X'1F63904C8B19AC4BB01C1DFEA9BF89B1', X'1F63904C8B19AC4BB01C1DFEA9BF89B1', 2, NULL, NULL, 2, NULL, 0, 'Serve fillets with browned slices of bread and the dressing.');
INSERT INTO `VWR INTERNAL Meatsandsausages6 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C09DFA7A3806D848924F966AA09559C5', X'C461F5BBB4260C4A853432B7E3E413B8', X'C461F5BBB4260C4A853432B7E3E413B8', 0, NULL, NULL, 2, NULL, 0, 'Combine all ingredients except bread. Chill.');
INSERT INTO `VWR INTERNAL Meatsandsausages6 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'79A858BC21B29C4A8CF888BACB219594', X'C461F5BBB4260C4A853432B7E3E413B8', X'C461F5BBB4260C4A853432B7E3E413B8', 1, NULL, NULL, 2, NULL, 0, 'Spread bread with fish mixture; cover with another bread toast.');
INSERT INTO `VWR INTERNAL Meatsandsausages6 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FFD82FD5DA45E64E9828F53CC43237AC', X'4182F6C1A802C44C834D094869BB2159', X'4182F6C1A802C44C834D094869BB2159', 0, NULL, NULL, 2, NULL, 0, 'Mix smoked fish with all vegetables.');
INSERT INTO `VWR INTERNAL Meatsandsausages6 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'409E80562AC69743AA91DB77894394BE', X'4182F6C1A802C44C834D094869BB2159', X'4182F6C1A802C44C834D094869BB2159', 1, NULL, NULL, 2, NULL, 0, 'Blend mayonnaise, mustard and Tabasco sauce together.');
INSERT INTO `VWR INTERNAL Meatsandsausages6 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5118CFCDCF4BBD4FAEACA3B1879C5486', X'4182F6C1A802C44C834D094869BB2159', X'4182F6C1A802C44C834D094869BB2159', 2, NULL, NULL, 2, NULL, 0, 'Combine everything together.');
INSERT INTO `VWR INTERNAL Meatsandsausages6 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F98BECBDFCEB9F48A3CE11329C181B8D', X'CAF3E09C78B8804780302FF008369665', X'CAF3E09C78B8804780302FF008369665', 0, NULL, NULL, 2, NULL, 0, 'Add salt and pepper to the eel.');
INSERT INTO `VWR INTERNAL Meatsandsausages6 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'170E0BBAD417B14FBA63A7459442B853', X'CAF3E09C78B8804780302FF008369665', X'CAF3E09C78B8804780302FF008369665', 1, NULL, NULL, 2, NULL, 0, 'Roll in beaten egg and flour.');
INSERT INTO `VWR INTERNAL Meatsandsausages6 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'20C42D7BF0F6A646A244E2A5B740CE52', X'CAF3E09C78B8804780302FF008369665', X'CAF3E09C78B8804780302FF008369665', 2, NULL, NULL, 2, NULL, 0, 'Bake in hot butter to a nice brown color.');
INSERT INTO `VWR INTERNAL Meatsandsausages6 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9F62A1C8A1D4F049A7A8A06D9BC49CA8', X'9183EEC0111ECD4D8CB7061DA5D7F15F', X'9183EEC0111ECD4D8CB7061DA5D7F15F', 0, NULL, NULL, 2, NULL, 0, 'The 3 tablespoons of butter are stirred into the flour. Add the beer, all the spices, and the lemon and onion slices.');
INSERT INTO `VWR INTERNAL Meatsandsausages6 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'022DBEC53F463240AC81192B45ED94E1', X'9183EEC0111ECD4D8CB7061DA5D7F15F', X'9183EEC0111ECD4D8CB7061DA5D7F15F', 1, NULL, NULL, 2, NULL, 0, 'Cook this gravy and add the pieces of eel.');
INSERT INTO `VWR INTERNAL Meatsandsausages6 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B0DE7A29946C8D4D9FCDAE7A25295B54', X'9183EEC0111ECD4D8CB7061DA5D7F15F', X'9183EEC0111ECD4D8CB7061DA5D7F15F', 2, NULL, NULL, 2, NULL, 0, 'Boil for 20 minutes.');
INSERT INTO `VWR INTERNAL Meatsandsausages6 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7A5C21C8AF08A34784A6363775BB59FB', X'FAFD5027E02CD3418C236D41D75C7F85', X'FAFD5027E02CD3418C236D41D75C7F85', 0, NULL, NULL, 2, NULL, 0, 'Cut the eel into equal pieces, rinse, and rub each piece with salt.');
INSERT INTO `VWR INTERNAL Meatsandsausages6 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2960131648D8824A9DA576A1B61F042A', X'FAFD5027E02CD3418C236D41D75C7F85', X'FAFD5027E02CD3418C236D41D75C7F85', 1, NULL, NULL, 2, NULL, 0, 'Mix 1-1/2 quarts of water with 4 tablespoonfuls of vinegar. Scald the eel in this mix.');
INSERT INTO `VWR INTERNAL Meatsandsausages6 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'10170197C45F094FBF87D56A4C951B56', X'FAFD5027E02CD3418C236D41D75C7F85', X'FAFD5027E02CD3418C236D41D75C7F85', 2, NULL, NULL, 2, NULL, 0, 'Heat fresh water, 2 tablespoons of vinegar, peppercorns, bay leaves, cloves, onion slices, lemon slices and salt.');
INSERT INTO `VWR INTERNAL Meatsandsausages6 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'60AADF9A63C84F4CAAA822272C5C76EF', X'FAFD5027E02CD3418C236D41D75C7F85', X'FAFD5027E02CD3418C236D41D75C7F85', 3, NULL, NULL, 2, NULL, 0, 'Add the eel and cook slowly for 15 minutes.');
INSERT INTO `VWR INTERNAL Meatsandsausages6 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B7595B59DBF39F4FB9426BC566655E11', X'5F91A4320428FF48981FDFD0A9310FA4', X'5F91A4320428FF48981FDFD0A9310FA4', 0, NULL, NULL, 2, NULL, 0, 'Bring the 1-1/2 quarts of water to a boil with vinegar, peppercorns, salt, bay leaves, sliced onions, sliced lemon, and soup greens.');
INSERT INTO `VWR INTERNAL Meatsandsausages6 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D97277D2FA0C26489E07C8D96E4E9DD0', X'5F91A4320428FF48981FDFD0A9310FA4', X'5F91A4320428FF48981FDFD0A9310FA4', 1, NULL, NULL, 2, NULL, 0, 'Add the eel and simmer gently for 15 minutes.');
INSERT INTO `VWR INTERNAL Meatsandsausages6 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DB39AEEE2344CA42A3836DCF83EB1EC8', X'5F91A4320428FF48981FDFD0A9310FA4', X'5F91A4320428FF48981FDFD0A9310FA4', 2, NULL, NULL, 2, NULL, 0, 'Remove the eel and boil the broth down to 1 quart.');
INSERT INTO `VWR INTERNAL Meatsandsausages6 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D18E14BD0C01DA4EA79183821D71BD05', X'5F91A4320428FF48981FDFD0A9310FA4', X'5F91A4320428FF48981FDFD0A9310FA4', 3, NULL, NULL, 2, NULL, 0, 'Into this put the crushed egg shells, the white of egg mixed with some broth, 1/4 teaspoon of meat extract and the gelatin and cook for a minute, then set aside.');
INSERT INTO `VWR INTERNAL Meatsandsausages6 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E204F6F99EDCA548BBFD44738E51611D', X'5F91A4320428FF48981FDFD0A9310FA4', X'5F91A4320428FF48981FDFD0A9310FA4', 4, NULL, NULL, 2, NULL, 0, 'When the bouillon is clear, strain it finely.');
INSERT INTO `VWR INTERNAL Meatsandsausages6 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F995F9ECA80EDF4795949E8F7206C8A8', X'5F91A4320428FF48981FDFD0A9310FA4', X'5F91A4320428FF48981FDFD0A9310FA4', 5, NULL, NULL, 2, NULL, 0, 'Slice the two hard boiled eggs and cover the bottom of a dish with them, then pour on some jelly and fish bouillon and let it get cold.');
INSERT INTO `VWR INTERNAL Meatsandsausages6 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7ABA4D13E3F7C64E813F1D6F182B020E', X'5F91A4320428FF48981FDFD0A9310FA4', X'5F91A4320428FF48981FDFD0A9310FA4', 6, NULL, NULL, 2, NULL, 0, 'Next put the eel on and the rest of the sliced egg, then pour the rest of the stock over and let it get cold.');
INSERT INTO `VWR INTERNAL Meatsandsausages6 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5193FCECEEED0944882760571D16C3CE', X'1FCC8B4C9DA0A54986974EFC5C83175E', X'1FCC8B4C9DA0A54986974EFC5C83175E', 0, NULL, NULL, 2, NULL, 0, 'Cut the eel into 4 inch pieces.');
INSERT INTO `VWR INTERNAL Meatsandsausages6 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F5E27564A10F014CB8E2C7E1EC4C8E39', X'1FCC8B4C9DA0A54986974EFC5C83175E', X'1FCC8B4C9DA0A54986974EFC5C83175E', 1, NULL, NULL, 2, NULL, 0, 'Mix the eel, salt, vinegar, sliced onion, peppercorns, cloves, and bay leaf in water and bring to a boil.');
INSERT INTO `VWR INTERNAL Meatsandsausages6 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FB6B249522AC854A9882E774BE46C452', X'1FCC8B4C9DA0A54986974EFC5C83175E', X'1FCC8B4C9DA0A54986974EFC5C83175E', 2, NULL, NULL, 2, NULL, 0, 'When almost done, take out the eel and put in the gravy.');
INSERT INTO `VWR INTERNAL Meatsandsausages6 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1131DBC6166A33498BF8A3F8ED344AFB', X'1FCC8B4C9DA0A54986974EFC5C83175E', X'1FCC8B4C9DA0A54986974EFC5C83175E', 3, NULL, NULL, 2, NULL, 0, 'The gravy is prepared by heating the butter, stirring in the flour and cream. When smooth add salt and lemon juice and allow it to simmer for 10 minutes.');
INSERT INTO `VWR INTERNAL Meatsandsausages6 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4F7820901370CE498ECCCCE5A6EDF153', X'A9436F362140BB489A44D243ECC5A844', X'A9436F362140BB489A44D243ECC5A844', 0, NULL, NULL, 2, NULL, 0, 'Eel is a slimy fish and its skin has to be scraped with salt or sawdust to remove all traces of slime. Then it is rinsed with water. The eels are gutted, and the belly is slit beyond the vent in order to remove the kidney. All traces of blood are removed, and the eel is rinsed again. The head is left on.');
INSERT INTO `VWR INTERNAL Meatsandsausages6 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AD590681442FF84EBF0AC0EBF0AD0BAB', X'A9436F362140BB489A44D243ECC5A844', X'A9436F362140BB489A44D243ECC5A844', 1, NULL, NULL, 2, NULL, 0, 'The processing steps, eels about 1-2 lb weight:');
INSERT INTO `VWR INTERNAL Meatsandsausages6 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AAE47416710000449ECDFDAAAFC339C2', X'A9436F362140BB489A44D243ECC5A844', X'A9436F362140BB489A44D243ECC5A844', 2, NULL, NULL, 2, NULL, 0, 'Cleaned eels are brined in brine for 2 hours or they may be lightly salted instead.');
INSERT INTO `VWR INTERNAL Meatsandsausages6 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6E22923839917A49B2AC1A3C686AF44E', X'A9436F362140BB489A44D243ECC5A844', X'A9436F362140BB489A44D243ECC5A844', 3, NULL, NULL, 2, NULL, 0, 'Eels are rinsed with water. The fish is impaled on a stick inserted from the stomach cavity up to behind the head.');
INSERT INTO `VWR INTERNAL Meatsandsausages6 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CB17A9D3B1178D4498D10288545E145B', X'A9436F362140BB489A44D243ECC5A844', X'A9436F362140BB489A44D243ECC5A844', 4, NULL, NULL, 2, NULL, 0, 'The fish is dried for 1-2 hours.');
INSERT INTO `VWR INTERNAL Meatsandsausages6 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1EE3E5B66DD82249B54753C1928CB5F0', X'A9436F362140BB489A44D243ECC5A844', X'A9436F362140BB489A44D243ECC5A844', 5, NULL, NULL, 2, NULL, 0, 'Eels are hot smoked for 3 hours at 140 F (60 C) or higher. At the beginning they should be smoked at high temperature to prevent closing of the stomach cavity. After a while the fish hardens and the temperature can be lowered. To check whether the fish is cooked, press on its sides and the meat should separate from the backbone.');
INSERT INTO `VWR INTERNAL Meatsandsausages6 recipeInstruction` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'130D73CD06036748B1477F55BD4B566E', X'8ABD39A4FC0698459B1C11AB48225E6A', X'8ABD39A4FC0698459B1C11AB48225E6A', 0, NULL, NULL, 2, NULL, 0, 'Rub egg yolks through a sieve.');
