INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeIngredients` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeIngredient) VALUES (X'8ADF3779E38DE64281F4EE40AB5A3357', X'742183F250E04A4796BE9F0E1EB45E09', X'742183F250E04A4796BE9F0E1EB45E09', 4, NULL, NULL, 2, NULL, 0, 'You will need a skewer and parchment paper.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FBE38A8700424347B6B4786C182C6397', X'6AF48B38E890144DAD6FEBB9C204F08A', X'6AF48B38E890144DAD6FEBB9C204F08A', 0, NULL, NULL, 2, NULL, 0, 'Gather all the ingredients.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D03CF3CF63D1254F9F0FF65542F082F2', X'6AF48B38E890144DAD6FEBB9C204F08A', X'6AF48B38E890144DAD6FEBB9C204F08A', 1, NULL, NULL, 2, NULL, 0, 'Peel the sweet potato and cut into chunks. Cook in a pot of boiling water for about 10 minutes or until the sweet potato is tender. Mash with a potato masher and set aside to cool.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9000CE7EC98E3D4CBD8D5DC09AF93D7F', X'6AF48B38E890144DAD6FEBB9C204F08A', X'6AF48B38E890144DAD6FEBB9C204F08A', 2, NULL, NULL, 2, NULL, 0, 'Drain and rinse the chickpeas and add them to a food processor.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7079E0CBCA839D4A85EF004D1993161D', X'6AF48B38E890144DAD6FEBB9C204F08A', X'6AF48B38E890144DAD6FEBB9C204F08A', 3, NULL, NULL, 2, NULL, 0, 'Add the cooled mashed sweet potato, water, freshly squeezed lemon juice, tahini, garlic cloves, salt and pepper. Puree until smooth, scraping down the sides with a spatula as needed.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'588CBA35460DD34199CF929BA03625C1', X'6AF48B38E890144DAD6FEBB9C204F08A', X'6AF48B38E890144DAD6FEBB9C204F08A', 4, NULL, NULL, 2, NULL, 0, 'Serve with a drizzle of olive oil, if desired, alongside pita or naan bread and sliced vegetables.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B716F90BCF2E58478C39EDF29F9E9D64', X'74CBBF341625E14C8421FDECC0B48984', X'74CBBF341625E14C8421FDECC0B48984', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0210B0F523091B4282A85060B18B6A7C', X'74CBBF341625E14C8421FDECC0B48984', X'74CBBF341625E14C8421FDECC0B48984', 1, NULL, NULL, 2, NULL, 0, 'To make the labneh, stir together the Greek style yogurt and salt until thoroughly combined.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'57A4BAFAE128B84EAC5B3A0752A7B359', X'74CBBF341625E14C8421FDECC0B48984', X'74CBBF341625E14C8421FDECC0B48984', 2, NULL, NULL, 2, NULL, 0, 'Line a fine mesh strainer with cheesecloth and add the yogurt mixture. Tie the ends of the cheesecloth together and squeeze gently.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1DAC94E5D9D9D74B82A80DA9D852A5F3', X'74CBBF341625E14C8421FDECC0B48984', X'74CBBF341625E14C8421FDECC0B48984', 3, NULL, NULL, 2, NULL, 0, 'Place the strainer over a bowl but make sure the bottom of the strainer does not touch the bottom of the bowl. Leave sitting on the counter for one hour, draining off the liquid a few times. You can also do this in the refrigerator if the temperature is very hot but it will take a little longer. At the end of the hour, you''ll have a soft batch of labneh.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'74E0EA4AA6A02048923083A0E56AFEC1', X'74CBBF341625E14C8421FDECC0B48984', X'74CBBF341625E14C8421FDECC0B48984', 4, NULL, NULL, 2, NULL, 0, 'To make the cranberry sauce, add the cranberries, sugar, water, lemon zest, lemon juice, cinnamon, cardamom and salt to a heavy bottom pot. Bring to a boil, stir well and reduce the heat to low. Allow to simmer for about 15 minutes, stirring occasionally, until the cranberries have popped. Remove from the heat and let cool before serving.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C68CD54682784249B5E731C394041B5B', X'74CBBF341625E14C8421FDECC0B48984', X'74CBBF341625E14C8421FDECC0B48984', 5, NULL, NULL, 2, NULL, 0, 'Spoon the labneh onto a plate or large bowl and swirl the cooled cranberry sauce into it. Serve with crackers as an appetizer with cocktails.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B2549C7362291944BBA9DE44427657AB', X'F27BE14D14CBE340B196E75038556E63', X'F27BE14D14CBE340B196E75038556E63', 0, NULL, NULL, 2, NULL, 0, 'Make the falafel by adding the chickpeas, onion, garlic, cilantro or parsley, ground cumin, baking powder, salt, black pepper, flour, and red pepper flakes, if using. Puree until the mixture is mostly smooth but it does not need to be completely smooth. Refrigerate for at about 15 minutes.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D51C0D9DA6F17443A180FEF47C9FBFE0', X'F27BE14D14CBE340B196E75038556E63', X'F27BE14D14CBE340B196E75038556E63', 1, NULL, NULL, 2, NULL, 0, 'While the falafel base is cooling in the fridge, make the tahini by combining the sesame paste, water, lemon juice, grated garlic, salt and pepper in a bowl.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1F593CB4A6A5A7408F87B28DC4D934CF', X'F27BE14D14CBE340B196E75038556E63', X'F27BE14D14CBE340B196E75038556E63', 2, NULL, NULL, 2, NULL, 0, 'Pre-heat the oven to 400 F. Toss the onion, bell pepper, and tomatoes in the olive oil and season with salt and pepper. Spread out on a baking sheet lined with parchment paper and roast in the oven for about 30 minutes or until softened and lightly browned.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BD0B9A1CBB82C347A3575F21CBD40231', X'F27BE14D14CBE340B196E75038556E63', X'F27BE14D14CBE340B196E75038556E63', 3, NULL, NULL, 2, NULL, 0, 'Add the two tablespoons of oil to a second sheet pan lined with parchment paper. Form the falafel into balls, approximately 1 ounce each (we used a cookie scoop) and gently roll them in the oil and place on the pan. Bake in the oven for 20 to 30 minutes or until the balls are a light golden brown.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B94CC3FF7B939549B37D9A60E95431DD', X'F27BE14D14CBE340B196E75038556E63', X'F27BE14D14CBE340B196E75038556E63', 4, NULL, NULL, 2, NULL, 0, 'Prepare the kebabs by alternating pieces of vegetables with falafel balls and drizzle with the tahini sauce.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F831CA2915D8794A947C2AFC6DEB86A5', X'9D670C8EC4A3FC42986154017F9FAAB8', X'9D670C8EC4A3FC42986154017F9FAAB8', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1BD04103323D6B41802BD6C79A3FA3A1', X'9D670C8EC4A3FC42986154017F9FAAB8', X'9D670C8EC4A3FC42986154017F9FAAB8', 1, NULL, NULL, 2, NULL, 0, 'Add 2 tablespoons of olive oil to a saut pan. Heat on medium heat until hot. Add the garlic and chopped sage. Saut until the garlic is browned. Remove the pan from the heat immediately.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2D7ED741EF966842A1A884E7F69D3626', X'9D670C8EC4A3FC42986154017F9FAAB8', X'9D670C8EC4A3FC42986154017F9FAAB8', 2, NULL, NULL, 2, NULL, 0, 'Add the garbanzo beans, pumpkin pure, lemon juice, tahini, salt, pepper, cumin, and paprika to a high-speed blender or a food processor.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1D254F4C35278541A513AD65D634FD17', X'9D670C8EC4A3FC42986154017F9FAAB8', X'9D670C8EC4A3FC42986154017F9FAAB8', 3, NULL, NULL, 2, NULL, 0, 'Begin blending. As you blend, drizzle in the remaining 1/4 cup of olive oil. Blend until completely smooth. Taste and add more salt to your liking.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F672B4B0AF33E84A8928F0617DF70E23', X'9D670C8EC4A3FC42986154017F9FAAB8', X'9D670C8EC4A3FC42986154017F9FAAB8', 4, NULL, NULL, 2, NULL, 0, 'Top the hummus with more olive oil, salt, paprika, chopped sage, and roasted pumpkin seeds if you wish. Serve with pita, pita chips, or fresh veggies for dipping.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4D76C7084D89A749B6DEE0BD83DA7E52', X'31D6CA082EAA6E4D99BF2C6839F7B852', X'31D6CA082EAA6E4D99BF2C6839F7B852', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B1BCC05B24E4264FA9BC6270A5248C7B', X'31D6CA082EAA6E4D99BF2C6839F7B852', X'31D6CA082EAA6E4D99BF2C6839F7B852', 1, NULL, NULL, 2, NULL, 0, 'Pre-heat the oven to 400 degrees.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'809D263AD30B7F43BA534C7C852F756C', X'31D6CA082EAA6E4D99BF2C6839F7B852', X'31D6CA082EAA6E4D99BF2C6839F7B852', 2, NULL, NULL, 2, NULL, 0, 'Wrap the beets tightly in aluminum foil and place on a baking dish to catch any drips. Roast in the oven for 50 to 60 minutes or until the tip of a knife easily pierces the beets. Remove from the oven and allow to cool enough to handle.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'977D1B538560FA459E3139FD96488797', X'31D6CA082EAA6E4D99BF2C6839F7B852', X'31D6CA082EAA6E4D99BF2C6839F7B852', 3, NULL, NULL, 2, NULL, 0, 'Using a sharp paring knife, peel the beets (the peel should come off very easily) and cut into chunks.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'880090FFE11407499974F394DF59172D', X'31D6CA082EAA6E4D99BF2C6839F7B852', X'31D6CA082EAA6E4D99BF2C6839F7B852', 4, NULL, NULL, 2, NULL, 0, 'Add the chunks of beet to a food processor and puree. Then add the sesame paste, garlic clove, water and lemon juice. If you''d like the mixture thinner, continue adding water by the tablespoon until you''ve reached the desired consistency.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'642A5540A49459459B9379158AF795E5', X'31D6CA082EAA6E4D99BF2C6839F7B852', X'31D6CA082EAA6E4D99BF2C6839F7B852', 5, NULL, NULL, 2, NULL, 0, 'Season with salt and pepper to taste and the red pepper flakes if using.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F33C6E021BF3254DB961712816E57163', X'AF04BDC3C348484091EC0F23FC118A69', X'AF04BDC3C348484091EC0F23FC118A69', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7CC44097B8E28349B0537E1E07CBE54C', X'AF04BDC3C348484091EC0F23FC118A69', X'AF04BDC3C348484091EC0F23FC118A69', 1, NULL, NULL, 2, NULL, 0, 'In a food processor, blend all ingredients together until smooth and creamy.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0048646510A86A4F85C990834384640C', X'AF04BDC3C348484091EC0F23FC118A69', X'AF04BDC3C348484091EC0F23FC118A69', 2, NULL, NULL, 2, NULL, 0, 'Serve immediately with pita bread, pita chips, or vegetables.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1F40EAD2A3144A4DBAD207DB3B843048', X'AF04BDC3C348484091EC0F23FC118A69', X'AF04BDC3C348484091EC0F23FC118A69', 3, NULL, NULL, 2, NULL, 0, 'Enjoy!');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'77213679AC5C0F4CB673D2A59C9B5B16', X'5E6435C9AAD3D24A83B8C198A616CAD6', X'5E6435C9AAD3D24A83B8C198A616CAD6', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FD044586A0DBDD4A84744F010A65F718', X'5E6435C9AAD3D24A83B8C198A616CAD6', X'5E6435C9AAD3D24A83B8C198A616CAD6', 1, NULL, NULL, 2, NULL, 0, 'Pre-heat the oven to 400 F..');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3C4FF5ED9B393A428DB190562CA7F804', X'5E6435C9AAD3D24A83B8C198A616CAD6', X'5E6435C9AAD3D24A83B8C198A616CAD6', 2, NULL, NULL, 2, NULL, 0, 'Cut off the ends of each zucchini, slice in half, lengthwise, place on a baking sheet lined with parchment paper and drizzle on the olive oil.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6E748E6C1B919E4C9EC1A77D2BFABAAF', X'5E6435C9AAD3D24A83B8C198A616CAD6', X'5E6435C9AAD3D24A83B8C198A616CAD6', 3, NULL, NULL, 2, NULL, 0, 'Roast in the oven for 30 minutes or until the zucchinis are fully soft.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5F87A6544FA54C4F9F6F4948D5088CF8', X'5E6435C9AAD3D24A83B8C198A616CAD6', X'5E6435C9AAD3D24A83B8C198A616CAD6', 4, NULL, NULL, 2, NULL, 0, 'Allow the zucchini to cool enough to handle and then scoop the flesh out of each zucchini half.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B951537B352DEB48BC8F7BFFBC3C3CE8', X'5E6435C9AAD3D24A83B8C198A616CAD6', X'5E6435C9AAD3D24A83B8C198A616CAD6', 5, NULL, NULL, 2, NULL, 0, 'Add the flesh to a food processor along with the garlic clove, tahini and lemon juice. Puree the mixture until smooth.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B7297C25447B524BA5DA8892F337805A', X'5E6435C9AAD3D24A83B8C198A616CAD6', X'5E6435C9AAD3D24A83B8C198A616CAD6', 6, NULL, NULL, 2, NULL, 0, 'Stir in the chopped parsley, salt, ground black pepper and red pepper flakes, if using.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8883D4A879346A44B42E1ABBC6D8E569', X'5E6435C9AAD3D24A83B8C198A616CAD6', X'5E6435C9AAD3D24A83B8C198A616CAD6', 7, NULL, NULL, 2, NULL, 0, 'Serve with the toasted pine nuts alongside pita bread, naan or other flatbread for dipping.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3C17EA4FBFC2FC489A7E7EEEF02E75D7', X'834784E6B697D34B8739D245BB7382F6', X'834784E6B697D34B8739D245BB7382F6', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4A429256EB4B3B4D8FC0C9848C116E4E', X'834784E6B697D34B8739D245BB7382F6', X'834784E6B697D34B8739D245BB7382F6', 1, NULL, NULL, 2, NULL, 0, 'Slice the avocado lengthwise, remove the pit and, using a spoon, scoop out the flesh.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1CDACDDBA010EA4D94343EBBA29052E9', X'834784E6B697D34B8739D245BB7382F6', X'834784E6B697D34B8739D245BB7382F6', 2, NULL, NULL, 2, NULL, 0, 'Add the avocado flesh, rinsed and drained chickpeas, sesame paste, peeled garlic cloves, lemon juice, water, salt, and black pepper to a food processor.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FCD57F8798BC7D4C80769EEED60416FC', X'834784E6B697D34B8739D245BB7382F6', X'834784E6B697D34B8739D245BB7382F6', 3, NULL, NULL, 2, NULL, 0, 'Puree until completely smooth and adjust the salt and pepper to your liking.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4DB05A582F09B24C9CDA368A9CA58980', X'834784E6B697D34B8739D245BB7382F6', X'834784E6B697D34B8739D245BB7382F6', 4, NULL, NULL, 2, NULL, 0, 'Stir in the chopped fresh dill and parsley. Top with a sprinkling of toasted pine nuts or pumpkin seeds, if desired.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7906595EC3F3FA4D8F959A19D952C8EC', X'834784E6B697D34B8739D245BB7382F6', X'834784E6B697D34B8739D245BB7382F6', 5, NULL, NULL, 2, NULL, 0, 'Serve as a dip with warm pita bread or toasted pita chips or spread on sandwiches. Store leftovers in the refrigerator in a lidded jar or seran wrapped bowl.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'659CDA02E0EDE04A97095F1DC178B8B7', X'83D3149F23D12A4FAF0A64675CCC00CF', X'83D3149F23D12A4FAF0A64675CCC00CF', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1574CD788452444C85890DF40C1C6672', X'83D3149F23D12A4FAF0A64675CCC00CF', X'83D3149F23D12A4FAF0A64675CCC00CF', 1, NULL, NULL, 2, NULL, 0, 'Drain the chickpeas and set aside the liquid from the can. Combine the drained chickpeas, lemon juice, sesame paste, crushed garlic and salt in a blender or food processor. Add 1/4 cup of the reserved liquid from the chickpeas. Blend for 3 to 5 minutes on low until thoroughly mixed and smooth.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1A307CD5C80198489B2E6F826D12E6ED', X'83D3149F23D12A4FAF0A64675CCC00CF', X'83D3149F23D12A4FAF0A64675CCC00CF', 2, NULL, NULL, 2, NULL, 0, 'Place in a serving bowl, and create a shallow well in the center of the hummus.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C04A10869FE9AF4EAB52A17998A37A5E', X'83D3149F23D12A4FAF0A64675CCC00CF', X'83D3149F23D12A4FAF0A64675CCC00CF', 3, NULL, NULL, 2, NULL, 0, 'Add a small amount (1 to 2 tablespoons) of olive oil in the well. Garnish with parsley (optional).');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'388430BEB96FF543A1C7FA83E3A06766', X'83D3149F23D12A4FAF0A64675CCC00CF', X'83D3149F23D12A4FAF0A64675CCC00CF', 4, NULL, NULL, 2, NULL, 0, 'Serve immediately with fresh, warm or toasted pita bread, or cover and refrigerate.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'97066853ABA5594D9B2B570DE3FF3BD6', X'1C2B5D10493D6645A01EBFFDF067B9BB', X'1C2B5D10493D6645A01EBFFDF067B9BB', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'486F1FB52C556249B7E221DDD4CA8840', X'1C2B5D10493D6645A01EBFFDF067B9BB', X'1C2B5D10493D6645A01EBFFDF067B9BB', 1, NULL, NULL, 2, NULL, 0, 'In a food processor, blend the chickpeas, lemon juice, and olive oil. Add the garlic, cumin, salt and black pepper, then the sesame oil and water.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'51310719BC33F846B64F302122F6453A', X'1C2B5D10493D6645A01EBFFDF067B9BB', X'1C2B5D10493D6645A01EBFFDF067B9BB', 2, NULL, NULL, 2, NULL, 0, 'Blend until you have a smooth paste.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C167247722FC4246A22FBD6BDA567587', X'1C2B5D10493D6645A01EBFFDF067B9BB', X'1C2B5D10493D6645A01EBFFDF067B9BB', 3, NULL, NULL, 2, NULL, 0, 'Add 1 to 2 tablespoons of water if mixture is too dry for your liking.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'58D2D72B52922347A495758BF1FEC6A8', X'1C2B5D10493D6645A01EBFFDF067B9BB', X'1C2B5D10493D6645A01EBFFDF067B9BB', 4, NULL, NULL, 2, NULL, 0, 'Serve warm or at room temperature with fresh vegetables or pita bread for dipping.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DA041CD777D5CD40A313A90351684C41', X'1C2B5D10493D6645A01EBFFDF067B9BB', X'1C2B5D10493D6645A01EBFFDF067B9BB', 5, NULL, NULL, 2, NULL, 0, 'Enjoy!');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1D11E77E8F46A24AA9806C4D9B943A96', X'9A3B41A7E09D924287F896B314C2C4DC', X'9A3B41A7E09D924287F896B314C2C4DC', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8AEAB7244315A3499FF9A2E61F774DCC', X'9A3B41A7E09D924287F896B314C2C4DC', X'9A3B41A7E09D924287F896B314C2C4DC', 1, NULL, NULL, 2, NULL, 0, 'Make the falafel by adding the chickpeas, onion, garlic, cilantro, parsley, ground cumin, baking powder, salt, black pepper, flour and red pepper flakes, if using. Puree until the mixture is mostly smooth but it does not need to be completely smooth. Refrigerate for at about 15 minutes.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'021383F83CD9EF469D5F1E2EEB668A53', X'9A3B41A7E09D924287F896B314C2C4DC', X'9A3B41A7E09D924287F896B314C2C4DC', 2, NULL, NULL, 2, NULL, 0, 'While the falafel base is cooling, make the tahini by combining the sesame paste, water, lemon juice, grated garlic, salt, and pepper in a bowl.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4197FA6B0C566B44A670E5B6293BCD9C', X'9A3B41A7E09D924287F896B314C2C4DC', X'9A3B41A7E09D924287F896B314C2C4DC', 3, NULL, NULL, 2, NULL, 0, 'Add about 2- to 3-inches of the canola oil to a large pot or deep sided pan and bring to a temperature of 350 F.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E531DD31D6E5084DB3AF6C5E16052A08', X'9A3B41A7E09D924287F896B314C2C4DC', X'9A3B41A7E09D924287F896B314C2C4DC', 4, NULL, NULL, 2, NULL, 0, 'Form the falafel into balls, approximately 1 oz. each (I used a cookie scoop) and place gently into the hot oil. Allow to fry for about 2 minutes and gently rotate the balls to continue frying on all sides. The falafel balls are done when they are a golden brown color all around.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9D1E4ED65986194AA44B24A5BF3037F7', X'9A3B41A7E09D924287F896B314C2C4DC', X'9A3B41A7E09D924287F896B314C2C4DC', 5, NULL, NULL, 2, NULL, 0, 'Remove from the oil with a slotted spoon and place on a paper towel to drain.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D94D44E365351B45832651EC26FC4E67', X'9A3B41A7E09D924287F896B314C2C4DC', X'9A3B41A7E09D924287F896B314C2C4DC', 6, NULL, NULL, 2, NULL, 0, 'Prepare the sliders by placing a bit of amba sauce on the bottom of each roll, then add a falafel ball and smash it down gently with the palm of your hand. Top with some of the diced cucumbers and a drizzle of tahini sauce.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3DA614128DBB3447A228D1FED9736D9F', X'B4D70C3F5D3D8846AFB3E4CA1E45A935', X'B4D70C3F5D3D8846AFB3E4CA1E45A935', 0, NULL, NULL, 2, NULL, 0, 'First, finely chop your garlic scapes by hand or in food processor.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6F4A9471EC6BB748B4EFE4C831A44852', X'B4D70C3F5D3D8846AFB3E4CA1E45A935', X'B4D70C3F5D3D8846AFB3E4CA1E45A935', 1, NULL, NULL, 2, NULL, 0, 'Next, add all of the remaining ingredients to either the food processor or blender and pulse until you get a creamy consistency.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7466A8750C73D34CADB0E1D2AE27AD2C', X'B4D70C3F5D3D8846AFB3E4CA1E45A935', X'B4D70C3F5D3D8846AFB3E4CA1E45A935', 2, NULL, NULL, 2, NULL, 0, 'Feel free to add additional warm water if needed, in order to loosen the texture.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E7972C633DA8DC44BE6D5C3135EF8C8F', X'B4D70C3F5D3D8846AFB3E4CA1E45A935', X'B4D70C3F5D3D8846AFB3E4CA1E45A935', 3, NULL, NULL, 2, NULL, 0, 'Serve at room temperature immediately or store in refrigerator for serving in 1 to 2 days.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9A97118F7EF2F145A365AB1738C54A01', X'3224AA6DC945B14D998EFA0F0C0E6B0A', X'3224AA6DC945B14D998EFA0F0C0E6B0A', 0, NULL, NULL, 2, NULL, 0, 'To make the labneh, stir together the Greek-style yogurt, salt, dried oregano and chopped fresh parsley. Be sure the mixture is evenly incorporated to avoid pockets of salt.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'694643E6DCEB2749A3C847E235634080', X'3224AA6DC945B14D998EFA0F0C0E6B0A', X'3224AA6DC945B14D998EFA0F0C0E6B0A', 1, NULL, NULL, 2, NULL, 0, 'Pour the mixture into a piece of cheesecloth, tie the top and place over a strainer set over on a bowl. Be sure the bowl is large enough that the bottom of the strainer does not touch any dripping liquid. Leave the mixture out on the counter, at room temperature, for one hour to allow the bulk of the liquid to strain out.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'12239BD77A2C524BA4C02A908DDD5B2C', X'3224AA6DC945B14D998EFA0F0C0E6B0A', X'3224AA6DC945B14D998EFA0F0C0E6B0A', 2, NULL, NULL, 2, NULL, 0, 'Drain the bowl and then set the whole thing in the refrigerator for approximately two days. Periodically drain any liquid that forms in the bottom of the bowl.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B94B53436AEB744E9C89A9F0A999E586', X'3224AA6DC945B14D998EFA0F0C0E6B0A', X'3224AA6DC945B14D998EFA0F0C0E6B0A', 3, NULL, NULL, 2, NULL, 0, 'When the labneh is ready and firm, remove it from the cheesecloth and set over a serving plate. Add the olive oil to a skillet or saute pan and add the sliced red onion. Saute on medium heat, stirring often, for about 15 minutes or until the onions are lightly caramelized.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DCE10B9B71AFEC4D9B28957D9BEFD0B9', X'3224AA6DC945B14D998EFA0F0C0E6B0A', X'3224AA6DC945B14D998EFA0F0C0E6B0A', 4, NULL, NULL, 2, NULL, 0, 'Pour the sauteed onions over the prepared labneh cheese and serve as a dip with crackers or slices of pita bread.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0BDF6AA2E6365D4A9A997643B2A71998', X'B3085299245DE14488B6022FC2FA61A7', X'B3085299245DE14488B6022FC2FA61A7', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E61DE21148C8864FBB6D1D5EE7EF6B55', X'B3085299245DE14488B6022FC2FA61A7', X'B3085299245DE14488B6022FC2FA61A7', 1, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 375 F. Line one large or two medium baking sheets with foil.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FD8B7B9A79CA334AA42D4E5EA4CB78A3', X'B3085299245DE14488B6022FC2FA61A7', X'B3085299245DE14488B6022FC2FA61A7', 2, NULL, NULL, 2, NULL, 0, 'Stack the pita bread on a cutting board. With a sharp knife, cut the pitas into 6 even wedges. Gently open each wedge, and separate at the attached end, so that you get two pita triangles from each wedge. (You can also slice through the attached end with a sharp knife if you''d prefer.)');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E320B919AC7CDF45A356B87482F82612', X'B3085299245DE14488B6022FC2FA61A7', X'B3085299245DE14488B6022FC2FA61A7', 3, NULL, NULL, 2, NULL, 0, 'Spread the pita triangles, inside (textured side) up, in a single layer on the baking sheet(s).');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B63D867446729A4589F74665E9438F76', X'B3085299245DE14488B6022FC2FA61A7', X'B3085299245DE14488B6022FC2FA61A7', 4, NULL, NULL, 2, NULL, 0, 'Drizzle or brush the pita triangles lightly with the olive oil. Sprinkle liberally with the za''atar.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C9596933D0E4C5468E01B54AFCABC233', X'B3085299245DE14488B6022FC2FA61A7', X'B3085299245DE14488B6022FC2FA61A7', 5, NULL, NULL, 2, NULL, 0, 'Place the baking sheets into the preheated oven for about 5 minutes, or until the pita chips begin to turn crisp and lightly brown. (Watch the chips carefully if you leave them in longerthey can go from toasty to burnt in an instant.)');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9D515470789D7347901796EB75F4EAC6', X'B3085299245DE14488B6022FC2FA61A7', X'B3085299245DE14488B6022FC2FA61A7', 6, NULL, NULL, 2, NULL, 0, 'Remove the baking sheets from the oven and place on racks to cool. (The chips will get crunchier as they cool).');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AC8B4BACE41BC64297C415ADCE12992B', X'B3085299245DE14488B6022FC2FA61A7', X'B3085299245DE14488B6022FC2FA61A7', 7, NULL, NULL, 2, NULL, 0, 'Serve warm or at room temperature with dips or soft cheeses.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'14A260B5A09FA84CBABF2AC4CB431979', X'B3085299245DE14488B6022FC2FA61A7', X'B3085299245DE14488B6022FC2FA61A7', 8, NULL, NULL, 2, NULL, 0, 'Enjoy!');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E49BD7377BFF564A80D717E6409DEED2', X'84F2A85111C3C44180D265E8F0648C67', X'84F2A85111C3C44180D265E8F0648C67', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B64BD68C46CAB7478030425D7F9E01C6', X'84F2A85111C3C44180D265E8F0648C67', X'84F2A85111C3C44180D265E8F0648C67', 1, NULL, NULL, 2, NULL, 0, 'In a food processor, combine the drained beans, tahini, spinach, garlic, olive oil, and lemon juice. Blend well.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'59A87F0E3A24BD4E95A5C3BAFC3E3E54', X'84F2A85111C3C44180D265E8F0648C67', X'84F2A85111C3C44180D265E8F0648C67', 2, NULL, NULL, 2, NULL, 0, 'Add cheese and red pepper flakes and blend into a smooth and creamy dip.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1D1EB2B8417F794F9CC5287B4CD54110', X'84F2A85111C3C44180D265E8F0648C67', X'84F2A85111C3C44180D265E8F0648C67', 3, NULL, NULL, 2, NULL, 0, 'Serve with pita bread, pita chips, or fresh veggies.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B8950FA28C585C4BB49E8A9202CEE1B0', X'E424A259C783594D902236D126A0D233', X'E424A259C783594D902236D126A0D233', 0, NULL, NULL, 2, NULL, 0, 'Drain chickpeas and set aside liquid from can.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'877B94E818129847B2E4A608BF58F06F', X'E424A259C783594D902236D126A0D233', X'E424A259C783594D902236D126A0D233', 1, NULL, NULL, 2, NULL, 0, 'Combine remaining ingredients in blender or food processor.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EA6E87584FC3EE4E969C6110CCA7274A', X'E424A259C783594D902236D126A0D233', X'E424A259C783594D902236D126A0D233', 2, NULL, NULL, 2, NULL, 0, 'Add 1/4 cup of liquid from chickpeas.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6A440D3CED736F4A9844CA7033EB149F', X'E424A259C783594D902236D126A0D233', X'E424A259C783594D902236D126A0D233', 3, NULL, NULL, 2, NULL, 0, 'Blend for 3 to 5 minutes on low until thoroughly mixed and smooth.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'02C1279B58B8824BB12130DB4E25FCE1', X'507B1F7B1C1B7A4FABCC81A3F9413E7A', X'507B1F7B1C1B7A4FABCC81A3F9413E7A', 0, NULL, NULL, 2, NULL, 0, 'Place the chickpeas in a large bowl. Add enough cold water to cover the chickpeas by several inches. Cover the bowl with plastic wrap or a clean tea towel and allow to soak overnight.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4119CCB2FE2D374FAC3308277DBC2C9C', X'507B1F7B1C1B7A4FABCC81A3F9413E7A', X'507B1F7B1C1B7A4FABCC81A3F9413E7A', 1, NULL, NULL, 2, NULL, 0, 'Drain and rinse the chickpeas. Place in a large stockpot, add the baking soda, and enough cold water to cover the chickpeas by 2 inches. Bring to a boil, skimming off any foam that rises to the top. Cover the pot, reduce the heat to low, and simmer for 45 minutes to an hour, or until the chickpeas are soft enough to crush between your fingers.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7C4C14926814AA41BA75E91BCDA154A8', X'507B1F7B1C1B7A4FABCC81A3F9413E7A', X'507B1F7B1C1B7A4FABCC81A3F9413E7A', 2, NULL, NULL, 2, NULL, 0, 'Reserve about 1/2 cup of the cooking liquid, then drain and rinse the chickpeas. Reserve a tablespoon or two of the cooked chickpeas for garnish, if desired.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6CCE3242D13FF347868ED5A068AF40B0', X'507B1F7B1C1B7A4FABCC81A3F9413E7A', X'507B1F7B1C1B7A4FABCC81A3F9413E7A', 3, NULL, NULL, 2, NULL, 0, 'Put the cooked chickpeas, tahini, lemon juice, garlic, salt, pepper, and paprika in a food processor. Pure until smooth. If the hummus is too thick or dry, add a bit of the cooking liquid, more lemon juice, or tahini to taste.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E20FD5E5D1275549AB568DB660CDCB83', X'507B1F7B1C1B7A4FABCC81A3F9413E7A', X'507B1F7B1C1B7A4FABCC81A3F9413E7A', 4, NULL, NULL, 2, NULL, 0, 'To serve, place the hummus in a dish, and use the back of a spoon to make a shallow well in the center. Place any reserved chickpeas in the well, drizzle with olive oil, and garnish with freshly chopped parsley and regular or smoked paprika.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9B0F7C052B27AD44A00699799017FFCE', X'6E1377480B17DE4C9648C15F8A2B3338', X'6E1377480B17DE4C9648C15F8A2B3338', 0, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 375 F. Use a fork to poke some holes in the eggplant skin.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B6241DE642AA5A459040F58C3EA8D22D', X'6E1377480B17DE4C9648C15F8A2B3338', X'6E1377480B17DE4C9648C15F8A2B3338', 1, NULL, NULL, 2, NULL, 0, 'Place eggplant directly on the middle oven rack and roast for 30 minutes, or until the outside is crisp and the inside is soft. Allow it to cool for 20 minutes.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FF3944495E1A124A9ADB65CCAD458269', X'6E1377480B17DE4C9648C15F8A2B3338', X'6E1377480B17DE4C9648C15F8A2B3338', 2, NULL, NULL, 2, NULL, 0, 'Cut open the eggplant and scoop out the flesh into a colander; allow to drain for 10 minutes. (Removing the excess liquid helps to eliminate a bitter flavor.)');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'222B456E8BC36941A1530FC7C9894980', X'6E1377480B17DE4C9648C15F8A2B3338', X'6E1377480B17DE4C9648C15F8A2B3338', 3, NULL, NULL, 2, NULL, 0, 'Place the eggplant flesh in a medium bowl. Add the remaining ingredients except for the 1 tablespoon olive oil and mash together until the eggplant becomes paste-like. Alternatively, you can also use a food processor and pulse for about 2 minutes.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1633F1B5FA18F74E9915DAAE4D495FB2', X'6E1377480B17DE4C9648C15F8A2B3338', X'6E1377480B17DE4C9648C15F8A2B3338', 4, NULL, NULL, 2, NULL, 0, 'Slowly add the olive oil and mix quickly and thoroughly to make sure it emulsifies along with the rest of the ingredients. If using a food processor, turn it on low as you pour the olive oil through the feed tube.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F9021FF77DE8284F831C587DDAFDEBA9', X'6E1377480B17DE4C9648C15F8A2B3338', X'6E1377480B17DE4C9648C15F8A2B3338', 5, NULL, NULL, 2, NULL, 0, 'Place the eggplant dip in a serving bowl and top with the garnish of lemon juice and olive oil. Add other garnishes according to taste.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'170D873C3888BA48B0CD8F24B4362A2D', X'6E1377480B17DE4C9648C15F8A2B3338', X'6E1377480B17DE4C9648C15F8A2B3338', 6, NULL, NULL, 2, NULL, 0, 'Serve with warm or toasted pita or flatbread. Enjoy!');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CA7D0C9BBE0B77438AA77A9AFD2DE578', X'1AE43E810EBDD447B6F368D33B2532F2', X'1AE43E810EBDD447B6F368D33B2532F2', 0, NULL, NULL, 2, NULL, 0, 'In a food processor, combine all ingredients and process until smooth and creamy. If the hummus consistency is too thick, add 1 tablespoon water until desired consistency.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'071331D7AB2B6A4784CDFE26C7563BCC', X'1AE43E810EBDD447B6F368D33B2532F2', X'1AE43E810EBDD447B6F368D33B2532F2', 1, NULL, NULL, 2, NULL, 0, 'Serve sun dried tomato hummus immediately with hot pita bread, veggies, or pita chips.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AA43D1D474291C40AB94B6FF19507B3A', X'CAFDA81A692D2E46B75A5D43414A99E0', X'CAFDA81A692D2E46B75A5D43414A99E0', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9D64B0ECF2D60647AE33A31C0D0EBBAE', X'CAFDA81A692D2E46B75A5D43414A99E0', X'CAFDA81A692D2E46B75A5D43414A99E0', 1, NULL, NULL, 2, NULL, 0, 'Combine the chickpeas, warm water, peanut butter, olive oil, lemon or lime juice, crushed garlic and salt to a food processor. Blend to a very smooth, dip-like consistency. If it is too thick, add a bit more warm water, 1 tablespoon at a time, until the mixture has reached your desired level of thickness.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'61BD80F9FAFD8746BBE4C1BB1994F3DE', X'CAFDA81A692D2E46B75A5D43414A99E0', X'CAFDA81A692D2E46B75A5D43414A99E0', 2, NULL, NULL, 2, NULL, 0, 'Serve peanut butter hummus with fresh celery sticks, carrot sticks, and broccoli florets. Warm pita or french bread cubes also go very well for dipping.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'565462B5DFEFFA4CB00DA4219DAC115A', X'CAFDA81A692D2E46B75A5D43414A99E0', X'CAFDA81A692D2E46B75A5D43414A99E0', 3, NULL, NULL, 2, NULL, 0, 'Many people sprinkle roasted chickpeas on top of traditional hummus, but you can sprinkle some roasted peanuts on top of this to add some texture and also indicate the flavor.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'463F81D61D0DA74B80902EA555EA116E', X'7C76F23C7BB6CE45AD9EF07D01A49308', X'7C76F23C7BB6CE45AD9EF07D01A49308', 0, NULL, NULL, 2, NULL, 0, 'Gather seeds from watermelon and place in a colander. Be sure to use only the black seeds; do not use the small, white seeds. Rinse thoroughly in a colander to remove any excess watermelon.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'26EFDDDE648EA54ABA64D9033C169E8A', X'7C76F23C7BB6CE45AD9EF07D01A49308', X'7C76F23C7BB6CE45AD9EF07D01A49308', 1, NULL, NULL, 2, NULL, 0, 'Once clean, spread out in an even layer on a cookie sheet and allow to dry. Drying outdoors in direct sunlight is a good method. Seeds can be patted dry, but roast much better when they are completely dry.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DA3DCD9D2D3A8941ACECB1B19F87115C', X'7C76F23C7BB6CE45AD9EF07D01A49308', X'7C76F23C7BB6CE45AD9EF07D01A49308', 2, NULL, NULL, 2, NULL, 0, 'In a frying pan on the stovetop, place watermelon seeds over medium-high heat and turn them over until seeds are roasted.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B851634D39E70948ABD5E4E9AEAC7347', X'7C76F23C7BB6CE45AD9EF07D01A49308', X'7C76F23C7BB6CE45AD9EF07D01A49308', 3, NULL, NULL, 2, NULL, 0, 'Add salt to the cup of water and stir until dissolved. Pour salt water into the frying pan and let cook, stirring occasionally until water has evaporated.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'12E88910FD070C438D04A732A2C64CA8', X'7C76F23C7BB6CE45AD9EF07D01A49308', X'7C76F23C7BB6CE45AD9EF07D01A49308', 4, NULL, NULL, 2, NULL, 0, 'Once done, allow seeds to cool completely before eating.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9D6826658B75DD43B364208B10E7D3F7', X'D5099DAD2C2BA74BBD742B578AF6404F', X'D5099DAD2C2BA74BBD742B578AF6404F', 0, NULL, NULL, 2, NULL, 0, 'Gather all of the ingredients.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EAE02D0A237F5E449D2A70E96DBDE9FA', X'D5099DAD2C2BA74BBD742B578AF6404F', X'D5099DAD2C2BA74BBD742B578AF6404F', 1, NULL, NULL, 2, NULL, 0, 'In a food processor, process the garbanzo beans, minced garlic, chopped fresh spinach, tahini, and olive oil. Add the lemon juice and salt and blend until thoroughly smooth.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'07D23CBFA1A063458D9597F8EF54A0DE', X'D5099DAD2C2BA74BBD742B578AF6404F', X'D5099DAD2C2BA74BBD742B578AF6404F', 2, NULL, NULL, 2, NULL, 0, 'If your spinach hummus is too thick, try adding a tablespoon of water at a time until it is your desired consistency. The finished hummus should be smooth and creamy.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2F8D224B1C33B54D8CA058956BBE44EE', X'1D97BB29C1F4C1488A09A6C897B76F98', X'1D97BB29C1F4C1488A09A6C897B76F98', 0, NULL, NULL, 2, NULL, 0, 'In a food processor, combine chickpeas, artichokes, and tahini and blend.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'842C6354C085734CB9F7A561076102A5', X'1D97BB29C1F4C1488A09A6C897B76F98', X'1D97BB29C1F4C1488A09A6C897B76F98', 1, NULL, NULL, 2, NULL, 0, 'Slowly add olive oil, garlic, and lemon juice.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'74D97BE0712C664389060A0D3BD70C94', X'1D97BB29C1F4C1488A09A6C897B76F98', X'1D97BB29C1F4C1488A09A6C897B76F98', 2, NULL, NULL, 2, NULL, 0, 'Blend to a smooth consistency.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6DC6EB4863AD4843942036E27B2937F7', X'A23B7ED4F763054B98C5FF6D21248BF7', X'A23B7ED4F763054B98C5FF6D21248BF7', 0, NULL, NULL, 2, NULL, 0, 'In a food processor, process beans, garlic, olive oil, lemon juice, and oregano until desired consistency.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'133D9BDF798B144E9197C2D81930A0F0', X'A23B7ED4F763054B98C5FF6D21248BF7', X'A23B7ED4F763054B98C5FF6D21248BF7', 1, NULL, NULL, 2, NULL, 0, 'If hummus is too thick, simply add olive oil in small increments (1/2 teaspoon) until desired consistency.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'52754D24A833DC4982F2EF311615F785', X'A23B7ED4F763054B98C5FF6D21248BF7', X'A23B7ED4F763054B98C5FF6D21248BF7', 2, NULL, NULL, 2, NULL, 0, 'Serve roasted garlic hummus garnished with finely chopped parsley with hot pita bread, pita chips, or veggies. Roasted red pepper hummus goes very well with cubes of warm Italian bread.');
