INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E1B0A6A22774FD40A536C1C8247E527A', X'B0BA00885352684FBD296E906B566563', X'B0BA00885352684FBD296E906B566563', 1, NULL, NULL, 2, NULL, 0, 'Cut the squash in half lengthwise.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3F68E0B15E73B84DA4AAFC61B4D2196C', X'B0BA00885352684FBD296E906B566563', X'B0BA00885352684FBD296E906B566563', 2, NULL, NULL, 2, NULL, 0, 'Gather seeds from squash, separating them from the stringy flesh, and place in a colander.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2753A2AF72030B45B8E178F3CDAF6BF2', X'B0BA00885352684FBD296E906B566563', X'B0BA00885352684FBD296E906B566563', 3, NULL, NULL, 2, NULL, 0, 'Rinse the seeds thoroughly in a colander and pick away any excess squash flesh that is still clinging to the seeds.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2F100FFBF4E128439DFC032433207DA0', X'B0BA00885352684FBD296E906B566563', X'B0BA00885352684FBD296E906B566563', 4, NULL, NULL, 2, NULL, 0, 'Once clean, spread out in an even layer on a clean kitchen towel or paper towels and allow them to dry. Seeds can be patted dry, but they roast much better when they are completely dry. This is a good step to reach before you continue to prepare the main flesh of your squash in whatever recipe you prefer. The seeds can be allowed to dry for 30 minutes to several hours before you go on to the roasting step.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'95E7626BE46DFE4E9D0E69A98E500C12', X'B0BA00885352684FBD296E906B566563', X'B0BA00885352684FBD296E906B566563', 5, NULL, NULL, 2, NULL, 0, 'Preheat oven to 275 F.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C9653306BCE27F478EF0A88278336B1A', X'B0BA00885352684FBD296E906B566563', X'B0BA00885352684FBD296E906B566563', 6, NULL, NULL, 2, NULL, 0, 'Toss the dry squash seeds with salt and butter and place in a single layer onto a cookie sheet lined with aluminum foil or parchment paper.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A08E53F8988F1244BF5D58494A2A60FA', X'B0BA00885352684FBD296E906B566563', X'B0BA00885352684FBD296E906B566563', 7, NULL, NULL, 2, NULL, 0, 'Place in oven and bake for 15 to 20 minutes or until seeds are golden brown.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F38F57748CEE644D9C7762B0A8185557', X'B0BA00885352684FBD296E906B566563', X'B0BA00885352684FBD296E906B566563', 8, NULL, NULL, 2, NULL, 0, 'When you remove them from the oven, agitate the pan a bit to loosen them. Allow them to cool for 5 to 10 minutes before serving as they will become crisper as they cool.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'37124B5E06BAC742AB574DD4C3EBCAFD', X'B0BA00885352684FBD296E906B566563', X'B0BA00885352684FBD296E906B566563', 9, NULL, NULL, 2, NULL, 0, 'Serve the seeds warm or at room temperature. Save any roasted squash seeds in an air-tight container for several days. They are safe to store at room temperature.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B4A0A9B35777B7448E57B4D7A924CDCD', X'B0BA00885352684FBD296E906B566563', X'B0BA00885352684FBD296E906B566563', 10, NULL, NULL, 2, NULL, 0, 'Enjoy!');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EFA2FFADAA94DA438BF1108BD94AA29D', X'3F2B1F9153330A4795DEA7100E54234B', X'3F2B1F9153330A4795DEA7100E54234B', 0, NULL, NULL, 2, NULL, 0, 'Make the pastry dough by adding the all-purpose flour, unsalted butter, baking powder, salt and fresh thyme to a food processor. Pulse a few times until the mixture resembles coarse crumbs. With the food processor running, slowly pour the ice water through the feed tube and continue running just until a dough ball forms. Wrap in plastic and refrigerate for at least 1 hour.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4375C5CBC34FCF449FFB5E7F0DEB9BC7', X'3F2B1F9153330A4795DEA7100E54234B', X'3F2B1F9153330A4795DEA7100E54234B', 1, NULL, NULL, 2, NULL, 0, 'While the pastry is chilling, add the ground beef to a large saute pan along with the diced onions, minced garlic, ground cumin, garlic powder, fresh thyme, dried oregano, cayenne, ground black pepper and salt. Cook until the beef is browned and then stir in the beef stock and red wine. Continue cooking for another 5 minutes and then stir in the parsley. Remove from the heat and allow to cool.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C4EE0853FE7F3A448EB5779662C84970', X'3F2B1F9153330A4795DEA7100E54234B', X'3F2B1F9153330A4795DEA7100E54234B', 2, NULL, NULL, 2, NULL, 0, 'Pre-heat the oven to 400 degrees F.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E40DB5FB96521C459B46211D66E461FA', X'3F2B1F9153330A4795DEA7100E54234B', X'3F2B1F9153330A4795DEA7100E54234B', 3, NULL, NULL, 2, NULL, 0, 'Roll out the pastry to about a 10` round and cut out 8 large (approx. 4`) circles. Fill 4 of the circles with equal amounts of the cooled ground beef mixture and top with the remaining 4 circles. Use a fork to crimp the edges closed and place on a baking sheet lined with parchment paper. Bake for 40 - 45 minutes or until the pastry is a light golden brown. Note that both the pastry and beef recipes can be doubled.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'447B4008210FF7409C04C12F3FC55D14', X'EDC49DCAB66D5E48AE775BEC04EBDA8A', X'EDC49DCAB66D5E48AE775BEC04EBDA8A', 0, NULL, NULL, 2, NULL, 0, 'In a blender or food processor, process beans, garlic, jalapeno pepper, and olive oil.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5CFA3A054DB8164E821555EB0B1E225B', X'EDC49DCAB66D5E48AE775BEC04EBDA8A', X'EDC49DCAB66D5E48AE775BEC04EBDA8A', 1, NULL, NULL, 2, NULL, 0, 'Add lemon, tahini, and salt and blend well. Hummus should be smooth and creamy. If needed, add 1 teaspoon at a time until desired consistency.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2141910CF8C0C1418A72521DC8C91877', X'EDC49DCAB66D5E48AE775BEC04EBDA8A', X'EDC49DCAB66D5E48AE775BEC04EBDA8A', 2, NULL, NULL, 2, NULL, 0, 'Place in a bowl and serve with veggies, pita bread, or pita chips at room temperature. Hummus may also be stored in the refrigerator, covered.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C0F88CA24EA054458489CAF84A6BCCC9', X'328411783E1BE24C82E1550295FDEFA0', X'328411783E1BE24C82E1550295FDEFA0', 0, NULL, NULL, 2, NULL, 0, 'Drain chickpeas and set aside liquid from the can.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E06F5D156301DA46B534F1A632013536', X'328411783E1BE24C82E1550295FDEFA0', X'328411783E1BE24C82E1550295FDEFA0', 1, NULL, NULL, 2, NULL, 0, 'Combine remaining ingredients in blender or food processor. Add 1/4 cup of liquid from chickpeas. Blend for 3 to 5 minutes on low until thoroughly mixed and smooth.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4142CE2F010C444D8B7677BC30254702', X'328411783E1BE24C82E1550295FDEFA0', X'328411783E1BE24C82E1550295FDEFA0', 2, NULL, NULL, 2, NULL, 0, 'Place in serving bowl, and create a shallow well in the center of the hummus. Add a small amount (1 to 2 tablespoons) of olive oil in the well.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'92130A43A175284EA5FF16DC91A3F0B9', X'328411783E1BE24C82E1550295FDEFA0', X'328411783E1BE24C82E1550295FDEFA0', 3, NULL, NULL, 2, NULL, 0, 'Garnish with extra cilantro (optional). Serve immediately with fresh, warm or toasted pita bread, or cover and refrigerate.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FF13624C670C824B94DCDF9144459DE2', X'C4D973423975384495C7BF0B347F9038', X'C4D973423975384495C7BF0B347F9038', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients. Preheat the oven to 400 F.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F3217CD52F4EC148B5C53F4169DF7036', X'C4D973423975384495C7BF0B347F9038', X'C4D973423975384495C7BF0B347F9038', 1, NULL, NULL, 2, NULL, 0, 'Add the butter to a pot and melt on the stovetop on low heat. Stir in the honey, sugar, water, cinnamon, salt, ginger, nutmeg, cardamom, and ground cloves. Continue stirring until melted and fully combined.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D496004661272348B6BDA99BE565892D', X'C4D973423975384495C7BF0B347F9038', X'C4D973423975384495C7BF0B347F9038', 2, NULL, NULL, 2, NULL, 0, 'Stir in the walnuts, almonds, and pistachios and spread out in a single layer on a baking sheet lined with parchment paper.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9FD11CA0587BE0488E1BA28776D8F8F6', X'C4D973423975384495C7BF0B347F9038', X'C4D973423975384495C7BF0B347F9038', 3, NULL, NULL, 2, NULL, 0, 'Roast in the oven for 10 to 12 minutes, being careful not to burn the nuts.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'345B5EC195D15240A2E1944254EC1AFA', X'C4D973423975384495C7BF0B347F9038', X'C4D973423975384495C7BF0B347F9038', 4, NULL, NULL, 2, NULL, 0, 'Sprinkle with the sea salt.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'54A655E785B3314D9531DD4A8E882BD4', X'C4D973423975384495C7BF0B347F9038', X'C4D973423975384495C7BF0B347F9038', 5, NULL, NULL, 2, NULL, 0, 'Serve and enjoy!');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'111CD34DCEB01644ACA166E02DB52A09', X'D60ABE215E9E894CA285A2CAD4FD48A7', X'D60ABE215E9E894CA285A2CAD4FD48A7', 0, NULL, NULL, 2, NULL, 0, 'First, choose what type of hummus you would like to make your sandwich. Hummus is sold and can be prepared easily at home in a variety of flavors.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BC10C9DD8E27004EB201E42ADEEEF7BA', X'D60ABE215E9E894CA285A2CAD4FD48A7', X'D60ABE215E9E894CA285A2CAD4FD48A7', 1, NULL, NULL, 2, NULL, 0, 'If you are using pita bread, slice a small amount off the top, allowing the pita to be opened to stuff.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'36DD38913EF50F46B936D3E2F1DCD629', X'D60ABE215E9E894CA285A2CAD4FD48A7', X'D60ABE215E9E894CA285A2CAD4FD48A7', 2, NULL, NULL, 2, NULL, 0, 'With a butter knife spread the desired amount of hummus inside the pita pocket.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F43AA4526D3F734394E48972803C5C39', X'D60ABE215E9E894CA285A2CAD4FD48A7', X'D60ABE215E9E894CA285A2CAD4FD48A7', 3, NULL, NULL, 2, NULL, 0, 'Add chopped veggies and enjoy!');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BC326DB9B9A2F04389F452118C98663D', X'F9D6C7D85E39A147A21A8437C0377B15', X'F9D6C7D85E39A147A21A8437C0377B15', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B0C7E85CA18342478FA51CC093CE4355', X'F9D6C7D85E39A147A21A8437C0377B15', X'F9D6C7D85E39A147A21A8437C0377B15', 1, NULL, NULL, 2, NULL, 0, '?To Make the Hummus: add the rinsed and drained chickpeas, sesame paste, peeled garlic cloves, lemon juice, water, and salt and black pepper to a food processor. Puree until completely smooth and add any additional seasoning if needed. Scoop into a bowl and place in the refrigerator while you make the beef. (Note that the mixture will be quite loose but will firm up as it chills.)');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FA0D8CB6F8EA7947903469164D8A1AD0', X'F9D6C7D85E39A147A21A8437C0377B15', X'F9D6C7D85E39A147A21A8437C0377B15', 2, NULL, NULL, 2, NULL, 0, 'To Make the Spicy Beef: add the ground beef to a large skillet or cast iron pan along with the onion, garlic, cumin, coriander, dried oregano, cayenne pepper, black pepper, salt, and baharat, if using. Saute, stirring often, until the meat browns.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F36EC7DFB1E9454E854D1B3BC5F62AC2', X'F9D6C7D85E39A147A21A8437C0377B15', X'F9D6C7D85E39A147A21A8437C0377B15', 3, NULL, NULL, 2, NULL, 0, 'Stir in the beef stock to moisten and bring the mixture together. Check the seasoning and balance out the flavors if needed. Keep warm.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E734E21C0195274F8622C46C71EF08FC', X'F9D6C7D85E39A147A21A8437C0377B15', X'F9D6C7D85E39A147A21A8437C0377B15', 4, NULL, NULL, 2, NULL, 0, 'Bring the hummus to room temperature and then spoon onto a deep plate or shallow bowl, leveling it off and creating a sort of divot in the middle. Add the beef to the center and serve warm with pita bread on the side.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'18DEE1EF67BE2C41A8D75DAD053F50D7', X'9AB4C14BAAEBFD4ABA9D71E7239FFC5F', X'9AB4C14BAAEBFD4ABA9D71E7239FFC5F', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2266784BC157B848A2DFC00802910E05', X'9AB4C14BAAEBFD4ABA9D71E7239FFC5F', X'9AB4C14BAAEBFD4ABA9D71E7239FFC5F', 1, NULL, NULL, 2, NULL, 0, 'Pre-heat the oven to 400 F.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6C8DD1B33D46BA4DAE6216583007055A', X'9AB4C14BAAEBFD4ABA9D71E7239FFC5F', X'9AB4C14BAAEBFD4ABA9D71E7239FFC5F', 2, NULL, NULL, 2, NULL, 0, 'To make the spice mix, combine all the spices in a small bowl. You''ll have enough for this recipe, but I usually keep the same proportions but make a larger batch to have on hand.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'07C2C9B1C395ED4C9FACEA9617A20B72', X'9AB4C14BAAEBFD4ABA9D71E7239FFC5F', X'9AB4C14BAAEBFD4ABA9D71E7239FFC5F', 3, NULL, NULL, 2, NULL, 0, 'Add the chickpeas to a large bowl, toss with the olive oil and spice mix.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D90D865375BD474C9D7AA2EF97677B8F', X'9AB4C14BAAEBFD4ABA9D71E7239FFC5F', X'9AB4C14BAAEBFD4ABA9D71E7239FFC5F', 4, NULL, NULL, 2, NULL, 0, 'Spread out on a baking sheet lined with parchment paper and roast for 30 minutes.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'64475419968D644EAC88E629C57EA051', X'9AB4C14BAAEBFD4ABA9D71E7239FFC5F', X'9AB4C14BAAEBFD4ABA9D71E7239FFC5F', 5, NULL, NULL, 2, NULL, 0, 'Toss with the chopped herbs.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AFCCEDFAFADD0A46B081896DD90AD642', X'9AB4C14BAAEBFD4ABA9D71E7239FFC5F', X'9AB4C14BAAEBFD4ABA9D71E7239FFC5F', 6, NULL, NULL, 2, NULL, 0, 'Serve on a bed of greens, over rice or in a pita sandwich with Israeli salad.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D8D08E7EF8AF3141A621776DB8133D3F', X'65C19EB9C91A3C48BEBDB49220F4412C', X'65C19EB9C91A3C48BEBDB49220F4412C', 0, NULL, NULL, 2, NULL, 0, 'In a blender or food processor, blend the white beans.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'754385B407500A4DB487503A449A457A', X'65C19EB9C91A3C48BEBDB49220F4412C', X'65C19EB9C91A3C48BEBDB49220F4412C', 1, NULL, NULL, 2, NULL, 0, 'Add tahini, lemon juice, cumin, garlic, olive oil, and salt until the ingredients form a creamy, paste-like consistency.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2BCAF457CEF99946864D6F16DB0EF628', X'65C19EB9C91A3C48BEBDB49220F4412C', X'65C19EB9C91A3C48BEBDB49220F4412C', 2, NULL, NULL, 2, NULL, 0, 'Add small amounts of water as needed until desired texture.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'45EFEA37EAAF6E4D9B4A1DA7C5569F56', X'65C19EB9C91A3C48BEBDB49220F4412C', X'65C19EB9C91A3C48BEBDB49220F4412C', 3, NULL, NULL, 2, NULL, 0, 'Pour the mixture into a large serving bowl. Cover and refrigerate for one hour or serve immediately.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5A5C212B10B5DB44AE7BD65B61178559', X'6D91515040D20443888B7B51CACDCE6A', X'6D91515040D20443888B7B51CACDCE6A', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'644598BA0548374F99E8E8C262C7B534', X'6D91515040D20443888B7B51CACDCE6A', X'6D91515040D20443888B7B51CACDCE6A', 1, NULL, NULL, 2, NULL, 0, 'Combine all ingredients in blender or food processor and blend to a smooth, creamy consistency. If too thick, add 1 teaspoon of warm water or chickpea liquid at a time and blend until desired consistency.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'75CD4A94A1DFB441B833EFD6795322B8', X'6D91515040D20443888B7B51CACDCE6A', X'6D91515040D20443888B7B51CACDCE6A', 2, NULL, NULL, 2, NULL, 0, 'Serve almond butter hummus with pita bread, crackers, or fresh vegetables like carrots and celery.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8CF17C3E74986E46A74664E03C1DF080', X'775DFCAA4E9E5949A5F6A37CB9AEA54D', X'775DFCAA4E9E5949A5F6A37CB9AEA54D', 0, NULL, NULL, 2, NULL, 0, 'Peel and slice cucumbers into thin rounds.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1FF9E2770FBB964E98AC9BCC616F3AE5', X'775DFCAA4E9E5949A5F6A37CB9AEA54D', X'775DFCAA4E9E5949A5F6A37CB9AEA54D', 1, NULL, NULL, 2, NULL, 0, 'Place sliced cucumbers in a strainer and sprinkle with salt. Set aside.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'63C984022C29014DB2CA2602F4CE2C80', X'775DFCAA4E9E5949A5F6A37CB9AEA54D', X'775DFCAA4E9E5949A5F6A37CB9AEA54D', 2, NULL, NULL, 2, NULL, 0, 'While salted cucumber slices sit, crush garlic and mash together with fresh mint leaves using a mortar and pestle or wooden spoon.');
