INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D662EB81B56BA041AD579564C8CDC423', X'B2537381D6E61F49A87174C588CB2D5F', X'B2537381D6E61F49A87174C588CB2D5F', 3, NULL, NULL, 2, NULL, 0, 'Place the mixture into an oiled 9 x 5-inch loaf pan and cook in the oven for approximately 30 minutes or until the top is a light golden brown.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'471CBD94A5C8CB449329C1C079152077', X'B2537381D6E61F49A87174C588CB2D5F', X'B2537381D6E61F49A87174C588CB2D5F', 4, NULL, NULL, 2, NULL, 0, 'You can slice the loaf immediately if you like but, for best results, cool completely, wrap in aluminum foil, and refrigerate until firm.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6A7213820A9E334D901052C47FED59D4', X'B2537381D6E61F49A87174C588CB2D5F', X'B2537381D6E61F49A87174C588CB2D5F', 5, NULL, NULL, 2, NULL, 0, 'To reheat, add a little olive oil to a large skillet, slice the loaf very thinly and crisp up the slices in the hot pan for a few minutes.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7A2F21FC45DD35458FB79AF9A6613589', X'B2537381D6E61F49A87174C588CB2D5F', X'B2537381D6E61F49A87174C588CB2D5F', 6, NULL, NULL, 2, NULL, 0, 'Assemble the sandwiches with pita, warmed and toasted through. Spread on some tzatziki or tahini sauce, add lettuce, tomato, cucumber, onion, and top with more sauce.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'09721458F6CB6E49A501F6F9178D6DE8', X'B2537381D6E61F49A87174C588CB2D5F', X'B2537381D6E61F49A87174C588CB2D5F', 7, NULL, NULL, 2, NULL, 0, 'Serve and enjoy.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F6D578E8D99A364486806B27E11F9234', X'23D5E1D35963534BB3E6463429D2351F', X'23D5E1D35963534BB3E6463429D2351F', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AA960E1FE608B249B2F7CEE07E85A59E', X'23D5E1D35963534BB3E6463429D2351F', X'23D5E1D35963534BB3E6463429D2351F', 1, NULL, NULL, 2, NULL, 0, 'In a large bowl, combine the ground lamb, minced onion and garlic, 1 teaspoon each of the ground cumin and sumac, salt, ground black pepper, red pepper flakes, and ice water.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'05571211FB79FF439F24E80A6AF4FAD9', X'23D5E1D35963534BB3E6463429D2351F', X'23D5E1D35963534BB3E6463429D2351F', 2, NULL, NULL, 2, NULL, 0, 'Knead the mixture by hand until it turns tacky and starts sticking to the side of the bowl. Place in the refrigerator for about 30 minutes to chill.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'31960633F09D004BAB325C9BBA90C624', X'23D5E1D35963534BB3E6463429D2351F', X'23D5E1D35963534BB3E6463429D2351F', 3, NULL, NULL, 2, NULL, 0, 'Wet your hands and place a quarter of the lamb mixture onto each skewer. One easy way is to use a 1-ounce scoop to form balls, thread them onto the skewer, then mash them together.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2FF26BD603BDA942A980F73A4B4D5924', X'23D5E1D35963534BB3E6463429D2351F', X'23D5E1D35963534BB3E6463429D2351F', 4, NULL, NULL, 2, NULL, 0, 'In a small bowl, combine the remaining 1/2 teaspoon each of ground cumin and sumac.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D54D60B806544A4E95204A16BBE61737', X'23D5E1D35963534BB3E6463429D2351F', X'23D5E1D35963534BB3E6463429D2351F', 5, NULL, NULL, 2, NULL, 0, 'Grill the kebabs for approximately 12 minutes, until well charred on both sides. Sprinkle the spice mix over the kebabs while they are cooking.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'313DF6FC483F32489F538BA740284443', X'23D5E1D35963534BB3E6463429D2351F', X'23D5E1D35963534BB3E6463429D2351F', 6, NULL, NULL, 2, NULL, 0, 'Serve the kebabs on warm pita or naan bread with sliced red onions, parsley, tomatoes, and diced cucumbers. Enjoy!');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C541B5EB9E591B4FA88476AEBF96339E', X'B3BA8816B63513408B76841EBE75D9E7', X'B3BA8816B63513408B76841EBE75D9E7', 0, NULL, NULL, 2, NULL, 0, 'Combine all ingredients except for beef, fillings, and sauce ingredients to make the marinade. If it seems a little dry, add a little olive oil (a tablespoon at a time). Here is an excellent tahini recipe, if you do not have some handy.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6D3DBBA7E0AA904E9FB0783AB30973D3', X'B3BA8816B63513408B76841EBE75D9E7', X'B3BA8816B63513408B76841EBE75D9E7', 1, NULL, NULL, 2, NULL, 0, 'Add the beef, cover, and refrigerate at least 8 hours, preferably overnight.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'82158B200D9C014181D2BC5504008786', X'B3BA8816B63513408B76841EBE75D9E7', X'B3BA8816B63513408B76841EBE75D9E7', 2, NULL, NULL, 2, NULL, 0, 'In a stockpot or large saucepan, cook beef over medium heat for 45 minutes or until done. Be sure not to overcook! If beef becomes a little dry, add a few tablespoons of water throughout the cooking duration.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'776DB06B759F514AAFCF06727EBE4625', X'B3BA8816B63513408B76841EBE75D9E7', X'B3BA8816B63513408B76841EBE75D9E7', 3, NULL, NULL, 2, NULL, 0, 'While beef is cooking, prepare the sauce. Combine sauce ingredients and mix well. Set aside.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'343C1A049D276842943C48EACB48799F', X'B3BA8816B63513408B76841EBE75D9E7', X'B3BA8816B63513408B76841EBE75D9E7', 4, NULL, NULL, 2, NULL, 0, 'Take the onions, tomatoes, cucumbers and sprinkle with sumac. Add other filling ingredients in a large bowl and combine well.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4CF713E04687C942AE10FA3626427552', X'B3BA8816B63513408B76841EBE75D9E7', X'B3BA8816B63513408B76841EBE75D9E7', 5, NULL, NULL, 2, NULL, 0, 'When the beef is done, you can shred it, slice it, or leave into large pieces. As long as it is thinly cut, there is not much difference. We prefer to cut mine into wide strips.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'63AD47A629C1A34EB15B21CE607962A9', X'B3BA8816B63513408B76841EBE75D9E7', X'B3BA8816B63513408B76841EBE75D9E7', 6, NULL, NULL, 2, NULL, 0, 'To assemble the pita, place enough beef on pita to cover 1/4 of the loaf. Add veggies and pour sauce. Roll like a soft taco or burrito and you have shawarma!');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9EB43031C5BB3F4498E074843530DC90', X'8EB3983EF8F5C841986A1A069D5B8DCD', X'8EB3983EF8F5C841986A1A069D5B8DCD', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4B1B46DF52727A49AABEB8E12FB47BA1', X'8EB3983EF8F5C841986A1A069D5B8DCD', X'8EB3983EF8F5C841986A1A069D5B8DCD', 1, NULL, NULL, 2, NULL, 0, 'Add the onion, garlic, parsley, cilantro, cumin, coriander, salt, pepper, and flour to your food processor. Blend until very finely minced.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C0271E6B39D1E94FABF19407407A5FE1', X'8EB3983EF8F5C841986A1A069D5B8DCD', X'8EB3983EF8F5C841986A1A069D5B8DCD', 2, NULL, NULL, 2, NULL, 0, 'Dry the chickpeas as best as you can with paper towels. Add the chickpeas to the food processor and pulse gently until the chickpeas are just minced and blended with the spices. Do not over blend or it will become hummus!');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'58E75CDFBDD0784B90BB2625D4B8D110', X'8EB3983EF8F5C841986A1A069D5B8DCD', X'8EB3983EF8F5C841986A1A069D5B8DCD', 3, NULL, NULL, 2, NULL, 0, 'Put the falafel mixture into a bowl, cover and refrigerate for about 2 hours.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FD30E760D18F7249BC43FE9D3B52B630', X'8EB3983EF8F5C841986A1A069D5B8DCD', X'8EB3983EF8F5C841986A1A069D5B8DCD', 4, NULL, NULL, 2, NULL, 0, 'Form the falafel into 12 balls, dusting with more whole wheat flour as needed. Use a cookie scoop to form the balls. Set them on a plate as you form them. Only form as many as will fit in the air fryer. Dont form them all ahead of time to preserve their shape.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'101570D93A944D47A11DAE2CFA686F4D', X'8EB3983EF8F5C841986A1A069D5B8DCD', X'8EB3983EF8F5C841986A1A069D5B8DCD', 5, NULL, NULL, 2, NULL, 0, 'Preheat the air fryer to 350 F. Spritz the falafel balls with olive oil spray. Air fry the falafel for 15 minutes, turning half way through until lightly browned.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3BD1A87036025C418D3E8B317D476B87', X'8EB3983EF8F5C841986A1A069D5B8DCD', X'8EB3983EF8F5C841986A1A069D5B8DCD', 6, NULL, NULL, 2, NULL, 0, 'Allow to cool slightly in the air fryer basket.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B6F8A2D61B462F44A8C63F3B388744E5', X'8EB3983EF8F5C841986A1A069D5B8DCD', X'8EB3983EF8F5C841986A1A069D5B8DCD', 7, NULL, NULL, 2, NULL, 0, 'Serve with pita, tzatziki, hummus, and fresh veggies. Stuff it in a pita or serve on top of a rice bowl.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DFEF0C5A4D08744E9EB5B84FFCF1D0CC', X'D0FB20D0FFA8154C89F5218C8A55EE06', X'D0FB20D0FFA8154C89F5218C8A55EE06', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F14390C60960ED45A4EF7B0C4889D8EF', X'D0FB20D0FFA8154C89F5218C8A55EE06', X'D0FB20D0FFA8154C89F5218C8A55EE06', 1, NULL, NULL, 2, NULL, 0, 'Slice the cooked lamb shank as thinly as possible.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5590B7905CD84744AD102F2F22BF242D', X'D0FB20D0FFA8154C89F5218C8A55EE06', X'D0FB20D0FFA8154C89F5218C8A55EE06', 2, NULL, NULL, 2, NULL, 0, 'Add the butter to a large saut pan or cast iron skillet. Saut the lamb slices in the butter until warmed through.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AFF3F289495E6849A7101B0F7EB383F7', X'D0FB20D0FFA8154C89F5218C8A55EE06', X'D0FB20D0FFA8154C89F5218C8A55EE06', 3, NULL, NULL, 2, NULL, 0, 'Place the warmed lamb over the pita or naan bread.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'749A2E00C91BBE42BA422DD447D5A3E2', X'D0FB20D0FFA8154C89F5218C8A55EE06', X'D0FB20D0FFA8154C89F5218C8A55EE06', 4, NULL, NULL, 2, NULL, 0, 'Add the seeded and diced tomatoes to the pan with the butter and saut for a minute.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'40C4023B79FDAF428DFF576A48903C5B', X'D0FB20D0FFA8154C89F5218C8A55EE06', X'D0FB20D0FFA8154C89F5218C8A55EE06', 5, NULL, NULL, 2, NULL, 0, 'Stir in the tomato sauce until warmed through.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'356F94BE9467FA4483D805268D3E847B', X'D0FB20D0FFA8154C89F5218C8A55EE06', X'D0FB20D0FFA8154C89F5218C8A55EE06', 6, NULL, NULL, 2, NULL, 0, 'Season with salt and pepper to taste');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B8A163DA34EEC048BF53B3DFC26815AD', X'D0FB20D0FFA8154C89F5218C8A55EE06', X'D0FB20D0FFA8154C89F5218C8A55EE06', 7, NULL, NULL, 2, NULL, 0, 'Pour the tomato mixture over the lamb');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2C3D693EEA853640A91237EA440605E3', X'D0FB20D0FFA8154C89F5218C8A55EE06', X'D0FB20D0FFA8154C89F5218C8A55EE06', 8, NULL, NULL, 2, NULL, 0, 'Top with Greek-style yogurt and serve warm.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A4CC11477CF4A24A9E5F337FB80BD3A7', X'D0FB20D0FFA8154C89F5218C8A55EE06', X'D0FB20D0FFA8154C89F5218C8A55EE06', 9, NULL, NULL, 2, NULL, 0, 'Enjoy!');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5F925DEF8998724690393081D43E4E1A', X'F4883C5712FE574586CC64FD08FF2FBE', X'F4883C5712FE574586CC64FD08FF2FBE', 0, NULL, NULL, 2, NULL, 0, 'In a large bowl, add the ground lamb, diced onion, minced garlic, chopped parsley, ground cumin, ground coriander, ground cinnamon, salt, and pepper. Mix together until thoroughly combined and form four individual patties.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FAA4420BF4D6BE4E8A490E2EC1111E34', X'F4883C5712FE574586CC64FD08FF2FBE', X'F4883C5712FE574586CC64FD08FF2FBE', 1, NULL, NULL, 2, NULL, 0, 'Heat up your grill or a grill pan if cooking indoors. Place the burgers on the grill and cook for 4 minutes, flip and cook for an additional 4 minutes. That will give you a medium-rare burger and an internal temperature of about 160 degrees Fahrenheit. Cook for an additional minute or two if you''d like your burgers more well done.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6CE40961DDC21844924EE68D956190B9', X'F4883C5712FE574586CC64FD08FF2FBE', X'F4883C5712FE574586CC64FD08FF2FBE', 2, NULL, NULL, 2, NULL, 0, 'Toast or grill the buns until lightly golden brown (optional) and spread about a tablespoon of labneh on the bottom half of each bun. Place a cooked burger over the labneh, top with the lettuce or green of your choice and sprinkle the crumbled feta cheese over it. top with the remaining half of the bun and serve hot.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EF80442104C719459D6979DCC7B591E9', X'F2846CA83BFDAD439A1DE0EE17B323D5', X'F2846CA83BFDAD439A1DE0EE17B323D5', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'370EE659CEF8B64C86956B71A6AC9C4E', X'F2846CA83BFDAD439A1DE0EE17B323D5', X'F2846CA83BFDAD439A1DE0EE17B323D5', 1, NULL, NULL, 2, NULL, 0, 'Bring a large pot of salted water to a boil and add the pasta. Cook according to the package directions.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2F5B541E6C57644B8EA5BB48F81A54A1', X'F2846CA83BFDAD439A1DE0EE17B323D5', X'F2846CA83BFDAD439A1DE0EE17B323D5', 2, NULL, NULL, 2, NULL, 0, 'In a bowl, whisk together the sesame paste, water, soy sauce, lemon juice, honey, grated ginger, garlic, and crushed red pepper flakes until smooth.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9E41826235AF0044B7DD6C78348263B8', X'F2846CA83BFDAD439A1DE0EE17B323D5', X'F2846CA83BFDAD439A1DE0EE17B323D5', 3, NULL, NULL, 2, NULL, 0, 'Drain the cooked noodles and toss with the tahini sauce. Top with the chopped scallions and sesame seeds.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'63DD586B1715F64CBF80CAE141A711C5', X'F2846CA83BFDAD439A1DE0EE17B323D5', X'F2846CA83BFDAD439A1DE0EE17B323D5', 4, NULL, NULL, 2, NULL, 0, 'Serve and enjoy!');
