INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DD6F1A99921EFB458BFBA086607F4888', X'F54761E9E3B6CF45A886E170F7DDA886', X'F54761E9E3B6CF45A886E170F7DDA886', 2, NULL, NULL, 2, NULL, 0, 'To prepare the chicken, heat the tablespoon of olive oil in a large skillet and add the chicken, skin side down. Season with salt and pepper and cook until lightly golden brown on both sides, about 10 minutes. Remove from the pan and set aside.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EE34A96647FD90478BEF75A0EFB03A58', X'F54761E9E3B6CF45A886E170F7DDA886', X'F54761E9E3B6CF45A886E170F7DDA886', 3, NULL, NULL, 2, NULL, 0, 'Add the remaining tablespoon of olive oil to the pan along with the diced onion. Saute until the onion becomes translucent. Stir in the garlic and continue cooking for a few more seconds.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8E299812DE41634BAB9FD818DBFD9AA9', X'F54761E9E3B6CF45A886E170F7DDA886', X'F54761E9E3B6CF45A886E170F7DDA886', 4, NULL, NULL, 2, NULL, 0, 'Stir in the tomato paste and then add the water, salt, black pepper, turmeric, and 2 tablespoons of the saffron water.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8B286A1B1434F54F97264B6F8DE44F56', X'F54761E9E3B6CF45A886E170F7DDA886', X'F54761E9E3B6CF45A886E170F7DDA886', 5, NULL, NULL, 2, NULL, 0, 'Add the chicken back into the pan, cover, reduce the heat to medium-low and cook for approximately 30 to 40 minutes, until the chicken is tender and cooked through.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1D39190B325FA148A5DB3F4CA71CA5A8', X'F54761E9E3B6CF45A886E170F7DDA886', X'F54761E9E3B6CF45A886E170F7DDA886', 6, NULL, NULL, 2, NULL, 0, 'To make the saffron rice, add the rice and water to a pot and bring to a boil. Cover, reduce the heat to low and cook for approximately 15 minutes or until all the water has been absorbed. Remove from the heat, season with salt and the remaining tablespoon of the saffron water. Stir in the dried cranberries.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'19002CB395CA0F4AB256C0F4347CD205', X'F54761E9E3B6CF45A886E170F7DDA886', X'F54761E9E3B6CF45A886E170F7DDA886', 7, NULL, NULL, 2, NULL, 0, 'Serve the chicken and sauce over a bed of the rice.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2A74FB29CD61F740B11C6973BFD8BF10', X'43C63D770156C548A939EB8D5DAD86BE', X'43C63D770156C548A939EB8D5DAD86BE', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'815FFF412DB4E947BE8F49ADBAEA717D', X'43C63D770156C548A939EB8D5DAD86BE', X'43C63D770156C548A939EB8D5DAD86BE', 1, NULL, NULL, 2, NULL, 0, 'Slice the eggplant into rounds and fry in the oil for about 5 to 7 minutes on each side or until golden brown.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'850C277BCC7E6042A9A3ADED2AC9639D', X'43C63D770156C548A939EB8D5DAD86BE', X'43C63D770156C548A939EB8D5DAD86BE', 2, NULL, NULL, 2, NULL, 0, 'Assemble the Israeli salad and stir together the tahini ingredients.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E13EE2541166EF479DB0B91134CEC174', X'43C63D770156C548A939EB8D5DAD86BE', X'43C63D770156C548A939EB8D5DAD86BE', 3, NULL, NULL, 2, NULL, 0, 'To assemble the sandwiches, spread the hummus evenly into the pita pockets and then stuff with the fried eggplant rounds and the sliced hard-boiled eggs. Spoon in the Israeli salad and drizzle with the tahini sauce and amba sauce.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3D4696522C520547A359C082F0887A64', X'50CEBCCD6B87034EB4AAE29716E3C8F6', X'50CEBCCD6B87034EB4AAE29716E3C8F6', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients. Pre-heat the oven to 350 F.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C8B809C74E638F41ABDDD6C4CAB89E7E', X'50CEBCCD6B87034EB4AAE29716E3C8F6', X'50CEBCCD6B87034EB4AAE29716E3C8F6', 1, NULL, NULL, 2, NULL, 0, 'Add the broccoli rabe to a large pot of boiling water and cook for 2 minutes. Drain and set aside.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'664CE7B5CE22F44690B6D1F2B4D96E4E', X'50CEBCCD6B87034EB4AAE29716E3C8F6', X'50CEBCCD6B87034EB4AAE29716E3C8F6', 2, NULL, NULL, 2, NULL, 0, 'Add the tablespoon of olive oil along with the diced onions to a large oven-proof skillet or cast iron pan. Cook on medium heat for about 10 minutes or until the onions become translucent.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A2790A3E13C17F4BAA637A467256EED9', X'50CEBCCD6B87034EB4AAE29716E3C8F6', X'50CEBCCD6B87034EB4AAE29716E3C8F6', 3, NULL, NULL, 2, NULL, 0, 'Add the minced garlic and continue cooking for about another minute.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'10DF4981A3DEBD438408E2DC90EF4460', X'50CEBCCD6B87034EB4AAE29716E3C8F6', X'50CEBCCD6B87034EB4AAE29716E3C8F6', 4, NULL, NULL, 2, NULL, 0, 'Cut the par-cooked broccoli rabe into small pieces and stir into the onion and garlic mixture. Cook for a couple of minutes, then stir in the baby spinach leaves. Continue cooking for another couple of minutes, stirring often, until the spinach begins to wilt.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7E3BC4217FA37E4E83E6FF7687FDBBE9', X'50CEBCCD6B87034EB4AAE29716E3C8F6', X'50CEBCCD6B87034EB4AAE29716E3C8F6', 5, NULL, NULL, 2, NULL, 0, 'Stir in the ground cumin, salt, ground black pepper, and milk.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C7ECF5665A1E954AB0B4627B4F1007DB', X'50CEBCCD6B87034EB4AAE29716E3C8F6', X'50CEBCCD6B87034EB4AAE29716E3C8F6', 6, NULL, NULL, 2, NULL, 0, 'Make four wells in the mixture and crack an egg into each well, being careful not to break the yolks. Note that it is easier to crack each egg into a small bowl and then transfer to the pan.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1826A0176D084449B3758FC6BE52593E', X'50CEBCCD6B87034EB4AAE29716E3C8F6', X'50CEBCCD6B87034EB4AAE29716E3C8F6', 7, NULL, NULL, 2, NULL, 0, 'Place the pan with the eggs into the pre-heated oven and cook for 10 to 15 minutes until the eggs are are set to your liking.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6FCA30D84885CD449DECB12BE613D3CF', X'50CEBCCD6B87034EB4AAE29716E3C8F6', X'50CEBCCD6B87034EB4AAE29716E3C8F6', 8, NULL, NULL, 2, NULL, 0, 'Sprinkle the cooked eggs with a dash of flaky sea salt and a pinch of red pepper flakes.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0E17366C94CC8644843C22393B8ED131', X'50CEBCCD6B87034EB4AAE29716E3C8F6', X'50CEBCCD6B87034EB4AAE29716E3C8F6', 9, NULL, NULL, 2, NULL, 0, 'Serve with crusty bread or warmed slices of pita or naan.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DF6972968B2C5344BB0F3AC254A2C138', X'6E797D7E85277342AB5F18D2C696B0B9', X'6E797D7E85277342AB5F18D2C696B0B9', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6DC741D34619134EA8D507710C9C8D7F', X'6E797D7E85277342AB5F18D2C696B0B9', X'6E797D7E85277342AB5F18D2C696B0B9', 1, NULL, NULL, 2, NULL, 0, 'Place dried chickpeas in a bowl and cover with cold water. Allow to soak overnight.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BB2CE2FA352D1A40A8F001766D8F7385', X'6E797D7E85277342AB5F18D2C696B0B9', X'6E797D7E85277342AB5F18D2C696B0B9', 2, NULL, NULL, 2, NULL, 0, 'Drain the chickpeas and place them in a pot with fresh water. Bring to a boil.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'469342734EB43845AE9837D4D96BED21', X'6E797D7E85277342AB5F18D2C696B0B9', X'6E797D7E85277342AB5F18D2C696B0B9', 3, NULL, NULL, 2, NULL, 0, 'Allow to boil for 5 minutes, then reduce the heat and let simmer on low for about 1 hour. Being careful not to overcook them. They should still be firm.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3715F60814304D4184AD090C32DC1A4C', X'6E797D7E85277342AB5F18D2C696B0B9', X'6E797D7E85277342AB5F18D2C696B0B9', 4, NULL, NULL, 2, NULL, 0, 'Drain and allow to cool in the strainer for 15 minutes.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2CD44F0030356D47A22B8325D37D6471', X'6E797D7E85277342AB5F18D2C696B0B9', X'6E797D7E85277342AB5F18D2C696B0B9', 5, NULL, NULL, 2, NULL, 0, 'Add 2 inches of oil to a heavy-bottomed large pot and heat to 350 F.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FF526B0B8233704185067338DEED1A2B', X'6E797D7E85277342AB5F18D2C696B0B9', X'6E797D7E85277342AB5F18D2C696B0B9', 6, NULL, NULL, 2, NULL, 0, 'Combine the drained chickpeas, onion, garlic, parsley, cumin, coriander, salt, and pepper (if using) in a medium bowl. Add the flour.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C6C8D70B00BAEF4E8C39053F660CAE59', X'6E797D7E85277342AB5F18D2C696B0B9', X'6E797D7E85277342AB5F18D2C696B0B9', 7, NULL, NULL, 2, NULL, 0, 'Mash the chickpeas, mixing all of the ingredients together. Alternatively, you can combine the ingredients in a food processor. You want the result to be a thick paste.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'38A0EB052568D84FBC0B747E73BDB51B', X'6E797D7E85277342AB5F18D2C696B0B9', X'6E797D7E85277342AB5F18D2C696B0B9', 8, NULL, NULL, 2, NULL, 0, 'Form the mixture into balls about the size of a ping pong ball. Slightly flatten.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6916A75FA58DDB42B2F48774024CEFA6', X'6E797D7E85277342AB5F18D2C696B0B9', X'6E797D7E85277342AB5F18D2C696B0B9', 9, NULL, NULL, 2, NULL, 0, 'Fry the falafel balls for 5 to 7 minutes until golden brown. Drain.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'47AC2A6296CF3B4ABFD9D4E6B96E4031', X'6E797D7E85277342AB5F18D2C696B0B9', X'6E797D7E85277342AB5F18D2C696B0B9', 10, NULL, NULL, 2, NULL, 0, 'Serve hot.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B1D4A0000BF91340AD0BF51636E945DD', X'2F651402BEF2D24C965BCBAE0DEE1F13', X'2F651402BEF2D24C965BCBAE0DEE1F13', 0, NULL, NULL, 2, NULL, 0, 'Gather ingredients.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6235FD6F23114C4DB83E41F2487D2E5D', X'2F651402BEF2D24C965BCBAE0DEE1F13', X'2F651402BEF2D24C965BCBAE0DEE1F13', 1, NULL, NULL, 2, NULL, 0, 'Combine chickpeas, garlic, onion, parsley, coriander, cumin, salt, and pepper (to taste) in a medium bowl. Add flour and combine well.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D93F53E971B86E43A9F379CDB212B229', X'2F651402BEF2D24C965BCBAE0DEE1F13', X'2F651402BEF2D24C965BCBAE0DEE1F13', 2, NULL, NULL, 2, NULL, 0, 'Mash chickpeas, making sure to mix ingredients together. You can also combine ingredients in a food processor. You want the result to be a thick paste.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'30B8D2C407C26345BC52E52B79B10847', X'2F651402BEF2D24C965BCBAE0DEE1F13', X'2F651402BEF2D24C965BCBAE0DEE1F13', 3, NULL, NULL, 2, NULL, 0, 'Form the mixture into the size of a ping-pong ball. Slightly flatten.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AD75308802634F4F85E33E10EFEE1F32', X'2F651402BEF2D24C965BCBAE0DEE1F13', X'2F651402BEF2D24C965BCBAE0DEE1F13', 4, NULL, NULL, 2, NULL, 0, 'Fry in 2 inches of oil at 350 F until golden brown, about 2 to 5 minutes. Remove from the pan and drain on paper towels.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0F5FA75A619BA744BCE14127F620670B', X'2F651402BEF2D24C965BCBAE0DEE1F13', X'2F651402BEF2D24C965BCBAE0DEE1F13', 5, NULL, NULL, 2, NULL, 0, 'Serve falafel by itself, or with warm pita bread with veggies, or tahini sauce.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'84D8CA323C022D439D785E94D6A54964', X'168D6B43D1BA704FA0B8FEF1934CBB23', X'168D6B43D1BA704FA0B8FEF1934CBB23', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'00725CF8F39A11419F7D37ADD35627FE', X'168D6B43D1BA704FA0B8FEF1934CBB23', X'168D6B43D1BA704FA0B8FEF1934CBB23', 1, NULL, NULL, 2, NULL, 0, 'Heat a large frying pan or griddle to medium-high heat. Coat with olive oil and heat the pita rounds for 2 minutes on each side. The pita may begin to brown a little.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'01F0E4805288AD449BC34A9AAE982F1B', X'168D6B43D1BA704FA0B8FEF1934CBB23', X'168D6B43D1BA704FA0B8FEF1934CBB23', 2, NULL, NULL, 2, NULL, 0, 'Heat up all the pita rounds and cut the top part to reveal the pocket.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4F4FBFF978982347ADCA1AD6EFE64E30', X'168D6B43D1BA704FA0B8FEF1934CBB23', X'168D6B43D1BA704FA0B8FEF1934CBB23', 3, NULL, NULL, 2, NULL, 0, 'Stuff each pita round with 3 falafel balls and a spoonful each of diced tomatoes and cucumber. Top with sliced onion, chopped parsley, the optional pickles, and a generous drizzle of tahini sauce.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5B72718D451E3340A61883BAC0D563B0', X'168D6B43D1BA704FA0B8FEF1934CBB23', X'168D6B43D1BA704FA0B8FEF1934CBB23', 4, NULL, NULL, 2, NULL, 0, 'Serve and enjoy.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'421F1B6AC643C84CB2C17A04F1534E49', X'89ABADB78208C94F947BBA27BF0E9D93', X'89ABADB78208C94F947BBA27BF0E9D93', 0, NULL, NULL, 2, NULL, 0, 'Add the 2 tablespoons of olive oil to a large pot or Dutch oven. Add the cubed stew meat and brown on all sides.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A9A91B76FACE014E8002B87D651112EE', X'89ABADB78208C94F947BBA27BF0E9D93', X'89ABADB78208C94F947BBA27BF0E9D93', 1, NULL, NULL, 2, NULL, 0, 'Add in the chopped onion and minced garlic.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7A144500D1DB9E4EB9A0065886D039CE', X'89ABADB78208C94F947BBA27BF0E9D93', X'89ABADB78208C94F947BBA27BF0E9D93', 2, NULL, NULL, 2, NULL, 0, 'Add the crushed tomatoes, stirring them in well with the meat, garlic, and onion.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0905455A2F96264C97B599636F486972', X'89ABADB78208C94F947BBA27BF0E9D93', X'89ABADB78208C94F947BBA27BF0E9D93', 3, NULL, NULL, 2, NULL, 0, 'Add the ground cumin, ground coriander, ground allspice, salt, and pepper.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B3D347570FADCA45A8E5DE81E4C43923', X'89ABADB78208C94F947BBA27BF0E9D93', X'89ABADB78208C94F947BBA27BF0E9D93', 4, NULL, NULL, 2, NULL, 0, 'Pour in the water and the tomato puree. Stir and combine well. Add the green beans and bring to a boil. Reduce the heat to low and simmer for 2 hours, or until meat is tender and done.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1F7AD718D411804D842FDA3CC2B019D7', X'89ABADB78208C94F947BBA27BF0E9D93', X'89ABADB78208C94F947BBA27BF0E9D93', 5, NULL, NULL, 2, NULL, 0, 'The sauce should thicken slightly as it cooks. If it does not or if you would like a thicker sauce, whisk in a slurry comprised of 1/4 cup flour mixed with a little water until well combined. Then cook the sauce on high for a few minutes to allow the raw flour to cook out and the sauce to thicken.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8025D0CC3243174D8857B2A02643B2C4', X'89ABADB78208C94F947BBA27BF0E9D93', X'89ABADB78208C94F947BBA27BF0E9D93', 6, NULL, NULL, 2, NULL, 0, 'Serve the stew over white rice and alongside a green salad.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'63C22C64FC8C2F40BEBA312FC7F2DAAB', X'EBA8B3CCB3394149A02B08050E0C4671', X'EBA8B3CCB3394149A02B08050E0C4671', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D8A9B93999F0F84983100ECD71E8B772', X'EBA8B3CCB3394149A02B08050E0C4671', X'EBA8B3CCB3394149A02B08050E0C4671', 1, NULL, NULL, 2, NULL, 0, 'Up to 24 hours before grilling, combine marinade ingredients and pour into freezer bag.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0D7ACDBBA3742346A4903595116CAD46', X'EBA8B3CCB3394149A02B08050E0C4671', X'EBA8B3CCB3394149A02B08050E0C4671', 2, NULL, NULL, 2, NULL, 0, 'Place lamb cubes in marinade and refrigerate until ready to grill. Be sure all pieces of lamb are coated well.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'ACA6E3B3A564ED42B1019895EB63B133', X'EBA8B3CCB3394149A02B08050E0C4671', X'EBA8B3CCB3394149A02B08050E0C4671', 3, NULL, NULL, 2, NULL, 0, 'Prepare vegetables. Wash and dry veggies. Cut green peppers and onions into 1-inch chunks. Set aside.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'502C724388FBED469F5BB33F7AF7E52C', X'EBA8B3CCB3394149A02B08050E0C4671', X'EBA8B3CCB3394149A02B08050E0C4671', 4, NULL, NULL, 2, NULL, 0, 'Prepare skewers by spraying a light coat of cooking oil on them to prevent sticking.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9FAF11A823AC324FBDA55BBFE1D7B0CB', X'EBA8B3CCB3394149A02B08050E0C4671', X'EBA8B3CCB3394149A02B08050E0C4671', 5, NULL, NULL, 2, NULL, 0, 'Place lamb and vegetables on skewers.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'54F15DAB42FB914BA5CB5F0C470219DF', X'EBA8B3CCB3394149A02B08050E0C4671', X'EBA8B3CCB3394149A02B08050E0C4671', 6, NULL, NULL, 2, NULL, 0, 'Grill spicy lamb kebabs for about 5-7 minutes on each side or until done as desired.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'40688DD85995E8448895C3B9002D63C0', X'EBA8B3CCB3394149A02B08050E0C4671', X'EBA8B3CCB3394149A02B08050E0C4671', 7, NULL, NULL, 2, NULL, 0, 'Serve with hummus, baba ghannouj, or turkish cacik, a cucumber yogurt sauce for dipping.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1CDE72D892702844A0CA4BEA7CC7F71D', X'264A172EB5186A4B950615FE707A0BA0', X'264A172EB5186A4B950615FE707A0BA0', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7E3A68FDDD980A459602CD42F407D696', X'264A172EB5186A4B950615FE707A0BA0', X'264A172EB5186A4B950615FE707A0BA0', 1, NULL, NULL, 2, NULL, 0, 'Preheat pan or grill to 375 F.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0D8015480B588A488E093E498A19213E', X'264A172EB5186A4B950615FE707A0BA0', X'264A172EB5186A4B950615FE707A0BA0', 2, NULL, NULL, 2, NULL, 0, 'In a bowl, combine lamb, parsley, salt, pepper, and paprika and mix well. (Roll up the sleeves, take off your rings, and do this by hand. The results are better this way.)');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'719284C63184F04996A2C690D95475DA', X'264A172EB5186A4B950615FE707A0BA0', X'264A172EB5186A4B950615FE707A0BA0', 3, NULL, NULL, 2, NULL, 0, 'Form meat and spice mixture into 4 patties -- 1/2 to 3/4 inches thick.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FD3DF82EDD00744AB1C59C26A868EBD6', X'264A172EB5186A4B950615FE707A0BA0', X'264A172EB5186A4B950615FE707A0BA0', 4, NULL, NULL, 2, NULL, 0, 'Put in heated pan or grill and cook for about 4 to 5 minutes each side. The flavor of lamb is best at a medium-well, so cook until you see a faint pink color in the center. You can certainly cook longer, should you choose.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F80791A6283CDE489AF315EABA110A45', X'264A172EB5186A4B950615FE707A0BA0', X'264A172EB5186A4B950615FE707A0BA0', 5, NULL, NULL, 2, NULL, 0, 'Place desired cheese on top and allow it to melt for about 30 seconds to one minute.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B0378CDCBB59B04689F903FE73025FDF', X'264A172EB5186A4B950615FE707A0BA0', X'264A172EB5186A4B950615FE707A0BA0', 6, NULL, NULL, 2, NULL, 0, 'Splash the pan with some cool water to create steam and put a stainless steel mixing bowl over the pan for about 30 seconds. The result is a perfectly melted cheese that hugs the burger.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A8F2647C014F21449ADBEC693B0CE98E', X'264A172EB5186A4B950615FE707A0BA0', X'264A172EB5186A4B950615FE707A0BA0', 7, NULL, NULL, 2, NULL, 0, 'Serve and enjoy!');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B9306E705A401C4DAC2E676A7D13FE3E', X'F7B7757682760543B56D98FFECBEB381', X'F7B7757682760543B56D98FFECBEB381', 0, NULL, NULL, 2, NULL, 0, 'Arrange chicken in a single layer in a glass baking dish.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'27BD087D7799A8459910E89D943FFE37', X'F7B7757682760543B56D98FFECBEB381', X'F7B7757682760543B56D98FFECBEB381', 1, NULL, NULL, 2, NULL, 0, 'In a small bowl, whisk together cola, ketchup, apricot jam, onion, garlic, salt, pepper, and ginger to form the marinade.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'00A76870C401F549AE5AA2B4D869E40A', X'F7B7757682760543B56D98FFECBEB381', X'F7B7757682760543B56D98FFECBEB381', 2, NULL, NULL, 2, NULL, 0, 'Pour the marinade over the chicken. Cover. Refrigerate for at least 2 hours or overnight.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'030D0C37F5A96C4FAF32B7651F3ACECC', X'F7B7757682760543B56D98FFECBEB381', X'F7B7757682760543B56D98FFECBEB381', 3, NULL, NULL, 2, NULL, 0, 'When ready to cook, heat the oven to 375 F/190 C.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EE9B2A4B984F414BB4D18DF5B82E6CB3', X'F7B7757682760543B56D98FFECBEB381', X'F7B7757682760543B56D98FFECBEB381', 4, NULL, NULL, 2, NULL, 0, 'Bake, covered, for 1 hour, basting after 30 minutes.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1717D3C0BDE184408363D98292251331', X'F7B7757682760543B56D98FFECBEB381', X'F7B7757682760543B56D98FFECBEB381', 5, NULL, NULL, 2, NULL, 0, 'Remove the foil. Bake uncovered for another 20 to 30 minutes, until the chicken is browned and cooked through.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FB0157FDA2C059429B8D557CE8998B38', X'F7B7757682760543B56D98FFECBEB381', X'F7B7757682760543B56D98FFECBEB381', 6, NULL, NULL, 2, NULL, 0, 'Serve with rice and enjoy.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6ED0E10583166646A5304DA0A5F665B1', X'E14E5F00A40C534F8325D884D5592B0E', X'E14E5F00A40C534F8325D884D5592B0E', 0, NULL, NULL, 2, NULL, 0, 'In a bowl, combine lamb, olive oil, lemon juice, pepper, salt, and red pepper. Allow to marinate at least 2 to 3 hours prior to cooking.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2E1C0EEE5841CF458F864ACDA90ED81C', X'E14E5F00A40C534F8325D884D5592B0E', X'E14E5F00A40C534F8325D884D5592B0E', 1, NULL, NULL, 2, NULL, 0, 'Place marinated beef on skewers (about 6 cubes per skewer). Be sure to apply a light coat of oil on the skewer prior to threading the meat.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'402DEE14D537B340BFABC25D78A9A24C', X'E14E5F00A40C534F8325D884D5592B0E', X'E14E5F00A40C534F8325D884D5592B0E', 2, NULL, NULL, 2, NULL, 0, 'Place vegetables on separate skewers, alternating type of vegetable. The meat and veggies are cooked on different skewers because the meat will take longer to grill.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1FA5A74A0F0FBB4E91789070A15D557D', X'E14E5F00A40C534F8325D884D5592B0E', X'E14E5F00A40C534F8325D884D5592B0E', 3, NULL, NULL, 2, NULL, 0, 'Cook lamb shashlik skewers on the grill or under the broiler for 10 to 12 minutes, or until desired doneness. Turn to ensure even cooking.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'405E57A48C33AE438F33F5F77D570A0B', X'E14E5F00A40C534F8325D884D5592B0E', X'E14E5F00A40C534F8325D884D5592B0E', 4, NULL, NULL, 2, NULL, 0, 'Grill vegetable skewers for last 5 minutes of grilling. Turn. The vegetables should be crisp, yet tender. Be careful not to overcook.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'21FBC9B7C5E4B343AA3DDBFCEA68DF5F', X'3CCB5361AB648040BA00746A357D860B', X'3CCB5361AB648040BA00746A357D860B', 0, NULL, NULL, 2, NULL, 0, 'In a medium bowl, combine all chicken ingredients together and allow to marinate at least two hours.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'729C16FC2EEBD841A9C0D0766A8477E8', X'3CCB5361AB648040BA00746A357D860B', X'3CCB5361AB648040BA00746A357D860B', 1, NULL, NULL, 2, NULL, 0, 'Remove chicken from marinade and slice about 1/4 to 1/2-inch thin.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E0CFCB4B6F0CEF43AC38FDAACD6610A7', X'3CCB5361AB648040BA00746A357D860B', X'3CCB5361AB648040BA00746A357D860B', 2, NULL, NULL, 2, NULL, 0, 'Pour 3 to 4 tablespoon of olive oil in a medium frying pan. Heat to a medium-high and add chicken. Cook chicken for about 8 minutes, stirring occasionally for even cooking.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'33F97BE88BEE334CB2BB755E446B2794', X'3CCB5361AB648040BA00746A357D860B', X'3CCB5361AB648040BA00746A357D860B', 3, NULL, NULL, 2, NULL, 0, 'In a medium bowl, combine chicken with veggies. Add desired sauce or condiment. Use desired amount.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FBD59DBED9519046882CECE4944724C9', X'3CCB5361AB648040BA00746A357D860B', X'3CCB5361AB648040BA00746A357D860B', 4, NULL, NULL, 2, NULL, 0, 'Heat pita loaves in oven or microwave. Stuff pita with chicken and veggies.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'45C6DBC29458CD46BB1869AB474DBFFF', X'3CCB5361AB648040BA00746A357D860B', X'3CCB5361AB648040BA00746A357D860B', 5, NULL, NULL, 2, NULL, 0, 'Serve immediately with french fries.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'47C9145467496E42824F1265AA220475', X'6EF9AB3D4E88784D8A6E6BFF2D6D51FB', X'6EF9AB3D4E88784D8A6E6BFF2D6D51FB', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1FEB55120ED5814DB9D5100B42A9D505', X'6EF9AB3D4E88784D8A6E6BFF2D6D51FB', X'6EF9AB3D4E88784D8A6E6BFF2D6D51FB', 1, NULL, NULL, 2, NULL, 0, '2 hours prior to grilling, prepare fish by rinsing with cold water. Pat dry with paper towel. Cut into 1-inch cubes. Set aside.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CFCFE3002A392447BE26680F43FE8217', X'6EF9AB3D4E88784D8A6E6BFF2D6D51FB', X'6EF9AB3D4E88784D8A6E6BFF2D6D51FB', 2, NULL, NULL, 2, NULL, 0, 'Mix olive oil, lemon juice, dill, and vinegar to make the marinade. Pour marinade into a large freezer bag and add fish. Make sure fish is well coated with marinade. Place freezer bag in the refrigerator for two hours.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6B211CE840EC28419A3CD7254013CD2A', X'6EF9AB3D4E88784D8A6E6BFF2D6D51FB', X'6EF9AB3D4E88784D8A6E6BFF2D6D51FB', 3, NULL, NULL, 2, NULL, 0, 'While fish is marinating, chop vegetables. Half the cucumber lengthwise and slice into 1/2 inch pieces. Cut the onions into eights. Cut red pepper into 1/3 chunks. Set aside.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'57262960ACE6FD4CAB6C5FBC03E16656', X'6EF9AB3D4E88784D8A6E6BFF2D6D51FB', X'6EF9AB3D4E88784D8A6E6BFF2D6D51FB', 4, NULL, NULL, 2, NULL, 0, 'Remove fish from marinade. Discard marinade.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CFD3CBC73277574BB97E6E1E0709D875', X'6EF9AB3D4E88784D8A6E6BFF2D6D51FB', X'6EF9AB3D4E88784D8A6E6BFF2D6D51FB', 5, NULL, NULL, 2, NULL, 0, 'Assemble kebabs according to taste. Many people like a combination of alternating pieces of meat and vegetables. This combination is entirely a matter of personal choice. It does not affect the cooking.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'381996D98569564BA4F45CA884503CB7', X'6EF9AB3D4E88784D8A6E6BFF2D6D51FB', X'6EF9AB3D4E88784D8A6E6BFF2D6D51FB', 6, NULL, NULL, 2, NULL, 0, 'If you are using steel skewers, be sure to put a light coat of oil on them before adding the fish and vegetables. They will slide on and off much easier.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CAFEFA9FFB80134FA6FD863AEB48D81A', X'6EF9AB3D4E88784D8A6E6BFF2D6D51FB', X'6EF9AB3D4E88784D8A6E6BFF2D6D51FB', 7, NULL, NULL, 2, NULL, 0, 'Spray the grill lightly with cooking oil and place the fish kebabs on the grill. Cook for about 4-6 minutes each side or until the fish flakes easily with a fork.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'22982F68D4DE8441A84181F0F01DACA1', X'6EF9AB3D4E88784D8A6E6BFF2D6D51FB', X'6EF9AB3D4E88784D8A6E6BFF2D6D51FB', 8, NULL, NULL, 2, NULL, 0, 'Serve over white rice.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1CF971D0F59FF846AF465208679961C2', X'C2DD1E4DC18B6C4BA9631B049C0EC967', X'C2DD1E4DC18B6C4BA9631B049C0EC967', 0, NULL, NULL, 2, NULL, 0, 'In a mixing bowl, combine all ingredients. Combine well using a wooden spoon, or with your hands. Mixing with your hands allows the mixture to combine better.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'887496E73971AB40BA1A8ACA9730B1B9', X'C2DD1E4DC18B6C4BA9631B049C0EC967', X'C2DD1E4DC18B6C4BA9631B049C0EC967', 1, NULL, NULL, 2, NULL, 0, 'Take about 1/4 meat mixture and make into a ball. Place ball through flat skewers, and shape into square cigar-like shapes. Repeat with remaining meat.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E7FDD5BD3D4D5A4DAFEA15630F007A46', X'C2DD1E4DC18B6C4BA9631B049C0EC967', X'C2DD1E4DC18B6C4BA9631B049C0EC967', 2, NULL, NULL, 2, NULL, 0, 'Place on grill and cook about 4 to 6 minutes each side, until meat is no longer pink and is done.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D0E67CC04209B34C9F23C4954387854B', X'C2DD1E4DC18B6C4BA9631B049C0EC967', X'C2DD1E4DC18B6C4BA9631B049C0EC967', 3, NULL, NULL, 2, NULL, 0, 'Serve over a bed of white rice and pita bread. Serve with a salad, like a fattoush or tabbouleh.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BD5176B2BDCB3A45A576663F1C3B1EBF', X'0F0D048CD7C1D041A70C70F90A1D148C', X'0F0D048CD7C1D041A70C70F90A1D148C', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'90C3EF726CFB0C4C95FAF1E347A5A2CF', X'0F0D048CD7C1D041A70C70F90A1D148C', X'0F0D048CD7C1D041A70C70F90A1D148C', 1, NULL, NULL, 2, NULL, 0, 'Soak the fava beans overnight in a large bowl of water.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B2AB352FA0025544A79752AA6092F3DF', X'0F0D048CD7C1D041A70C70F90A1D148C', X'0F0D048CD7C1D041A70C70F90A1D148C', 2, NULL, NULL, 2, NULL, 0, 'Drain the beans, add them to a large saucepan and cover them with fresh water. Bring the water to a boil and simmer on low for 45 minutes to 1 hour, or until the beans are tender.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E0AAAB680181C142A198DB23F167DBCF', X'0F0D048CD7C1D041A70C70F90A1D148C', X'0F0D048CD7C1D041A70C70F90A1D148C', 3, NULL, NULL, 2, NULL, 0, 'Drain and place in a medium bowl. Add the crushed garlic, lemon juice, olive oil and cumin and toss well to combine and coat. It''s common to mash the beans together with the other ingredients but you can also leave them whole and well mixed.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7609918598CB544EB0C1EEC52C1E077C', X'0F0D048CD7C1D041A70C70F90A1D148C', X'0F0D048CD7C1D041A70C70F90A1D148C', 4, NULL, NULL, 2, NULL, 0, 'Serve hot with a fried egg and pita bread.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C40E9BCA0A7DC24A8F9C2109017E5206', X'0F0D048CD7C1D041A70C70F90A1D148C', X'0F0D048CD7C1D041A70C70F90A1D148C', 5, NULL, NULL, 2, NULL, 0, 'Enjoy!');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F1433B5E6AD0354FA5353E8829AF0E83', X'22BB248F30771D4DB1A474B9C1EDF8C8', X'22BB248F30771D4DB1A474B9C1EDF8C8', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7B2806042729A14FA5480EE04DEA5C29', X'22BB248F30771D4DB1A474B9C1EDF8C8', X'22BB248F30771D4DB1A474B9C1EDF8C8', 1, NULL, NULL, 2, NULL, 0, 'Put flour in a shallow bowl.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A217AD98F87E6B448E28B3C621CB2E99', X'22BB248F30771D4DB1A474B9C1EDF8C8', X'22BB248F30771D4DB1A474B9C1EDF8C8', 2, NULL, NULL, 2, NULL, 0, 'Beat eggs in a second shallow bowl.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'08AEE1F44BBF5F48A1C359AB4E5A77FD', X'22BB248F30771D4DB1A474B9C1EDF8C8', X'22BB248F30771D4DB1A474B9C1EDF8C8', 3, NULL, NULL, 2, NULL, 0, 'Mix bread crumbs with spices in a third shallow bowl.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E11998938A4AF943AAB7D120582C3408', X'22BB248F30771D4DB1A474B9C1EDF8C8', X'22BB248F30771D4DB1A474B9C1EDF8C8', 4, NULL, NULL, 2, NULL, 0, 'Beat chicken breasts to flatten.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6F2BC8BC6639804E9F675DC016407636', X'22BB248F30771D4DB1A474B9C1EDF8C8', X'22BB248F30771D4DB1A474B9C1EDF8C8', 5, NULL, NULL, 2, NULL, 0, 'Dip chicken in flour, shaking off excess.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FB4FF371D37D9E46A25CF56E9C24F219', X'22BB248F30771D4DB1A474B9C1EDF8C8', X'22BB248F30771D4DB1A474B9C1EDF8C8', 6, NULL, NULL, 2, NULL, 0, 'Then dip in eggs, shaking off excess.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'039B63EE2D191B48888AFA288F38F547', X'22BB248F30771D4DB1A474B9C1EDF8C8', X'22BB248F30771D4DB1A474B9C1EDF8C8', 7, NULL, NULL, 2, NULL, 0, 'Then dip in seasoned crumbs.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E1CE538D51B7994494485AF2FC0F28FE', X'22BB248F30771D4DB1A474B9C1EDF8C8', X'22BB248F30771D4DB1A474B9C1EDF8C8', 8, NULL, NULL, 2, NULL, 0, 'Heat oil in a large frying pan over medium heat.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1188BF4C451C1D4E86BD83E8E0DF1818', X'22BB248F30771D4DB1A474B9C1EDF8C8', X'22BB248F30771D4DB1A474B9C1EDF8C8', 9, NULL, NULL, 2, NULL, 0, 'Fry chicken in hot oil on both sides. Fry for 1 to 2 minutes per side or until golden brown.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0FA2C9CA569AA542ACE306EDFC7BD790', X'22BB248F30771D4DB1A474B9C1EDF8C8', X'22BB248F30771D4DB1A474B9C1EDF8C8', 10, NULL, NULL, 2, NULL, 0, 'Serve and enjoy!');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'776FDBE60C537F48B1E3CDAEADFB5FFB', X'01D3B3D70801DC4AAD98B6BEB790B266', X'01D3B3D70801DC4AAD98B6BEB790B266', 0, NULL, NULL, 2, NULL, 0, 'Soak beans overnight in water. Drain and rinse. Preheat oil to 350 F. 2 inches of oil in a frying pan will suffice. We prefer to use vegetable or canola oil, but olive oil can be used also.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C9A194F8ECF74B41AC3A85E31BCEEE8E', X'01D3B3D70801DC4AAD98B6BEB790B266', X'01D3B3D70801DC4AAD98B6BEB790B266', 1, NULL, NULL, 2, NULL, 0, 'Place beans in food processor and blend. Add remaining ingredients to form a thick paste-like consistency. If too thick, add 1 tablespoon of water at a time until desired consistency.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'80369E527E5D2542B9F5043B8A31C181', X'01D3B3D70801DC4AAD98B6BEB790B266', X'01D3B3D70801DC4AAD98B6BEB790B266', 2, NULL, NULL, 2, NULL, 0, 'Remove falafel mixture from processor. Spoon falafel into hot oil by the tablespoon. Fry for about 2 minutes or until falafel is a golden brown color. Serve falafel by itself, or with hot pita bread with veggies, hummus, or tahini sauce.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6BE253F8DD990A4297E6BF7BFCC2F7E0', X'461DFA75D86940419199A2AF1FDE6A27', X'461DFA75D86940419199A2AF1FDE6A27', 0, NULL, NULL, 2, NULL, 0, 'Cut the fish into 2x5-inch long pieces or fillets may be cut lengthwise in half. Set aside.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'51D47258307B46458E481FED931F926E', X'461DFA75D86940419199A2AF1FDE6A27', X'461DFA75D86940419199A2AF1FDE6A27', 1, NULL, NULL, 2, NULL, 0, 'Heat the oil in a large frying pan with 2-inch sides over medium-low heat. Add the garlic and onions and cook for 5 minutes.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DFEF601C24D32A45B1B7EF93C68776CF', X'461DFA75D86940419199A2AF1FDE6A27', X'461DFA75D86940419199A2AF1FDE6A27', 2, NULL, NULL, 2, NULL, 0, 'Add the red, orange, and yellow peppers and cook for another 4 minutes.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FB143C29EEBD304284786E4F6F054809', X'461DFA75D86940419199A2AF1FDE6A27', X'461DFA75D86940419199A2AF1FDE6A27', 3, NULL, NULL, 2, NULL, 0, 'Stir in the paprika, salt, and black pepper to taste.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D48342D42AF1AB478CE5992375D02EC3', X'461DFA75D86940419199A2AF1FDE6A27', X'461DFA75D86940419199A2AF1FDE6A27', 4, NULL, NULL, 2, NULL, 0, 'Stir in the chili powder or heat of choice. Add the water, increase the heat to medium-high, and bring to a boil.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E4DCF449699685499F5AD206AC47E883', X'461DFA75D86940419199A2AF1FDE6A27', X'461DFA75D86940419199A2AF1FDE6A27', 5, NULL, NULL, 2, NULL, 0, 'Reduce the heat to low, place the fish slices on top of the garlic, onions, and peppers, cover, and cook for 5 minutes.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AD76FFFA8D6E7140B4D2DB7D7AC2A680', X'461DFA75D86940419199A2AF1FDE6A27', X'461DFA75D86940419199A2AF1FDE6A27', 6, NULL, NULL, 2, NULL, 0, 'Use a fork to pick up some of the peppers and onions and place them on top of the fish slices. Cover and cook for another 5 minutes. Taste the sauce and add more salt if necessary.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D734A5058E37F64BACA8246952E1BE56', X'461DFA75D86940419199A2AF1FDE6A27', X'461DFA75D86940419199A2AF1FDE6A27', 7, NULL, NULL, 2, NULL, 0, 'Sprinkle with the cilantro and serve. Serve warm or at room temperature.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1D2FBBF454B7424B951D04AEB379344C', X'E2D8B969AAD6E74094520E1DF5CDFA52', X'E2D8B969AAD6E74094520E1DF5CDFA52', 0, NULL, NULL, 2, NULL, 0, 'Heat oven to 350 F.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F7DD9CC8B551D647966CE7C24058CF1C', X'E2D8B969AAD6E74094520E1DF5CDFA52', X'E2D8B969AAD6E74094520E1DF5CDFA52', 1, NULL, NULL, 2, NULL, 0, 'In a food processor, combine the red bell pepper, parsley, garlic, pepper, and onion until thoroughly mixed. Ingredients can be used as listed but tend to hold better when mixed with the chicken.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8C851D48AEF0C847A35C36935738CB33', X'E2D8B969AAD6E74094520E1DF5CDFA52', X'E2D8B969AAD6E74094520E1DF5CDFA52', 2, NULL, NULL, 2, NULL, 0, 'Pour the mixture into a large bowl and add chicken and mix well.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'54C4DD91F20DBE4BA534CD64F7C8B7FA', X'E2D8B969AAD6E74094520E1DF5CDFA52', X'E2D8B969AAD6E74094520E1DF5CDFA52', 3, NULL, NULL, 2, NULL, 0, 'Wet hands with water and form the mixture into balls, patties, or cigar-shaped pieces, whichever you desire.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E5C1526D1615B642A1E0E49850F20D88', X'E2D8B969AAD6E74094520E1DF5CDFA52', X'E2D8B969AAD6E74094520E1DF5CDFA52', 4, NULL, NULL, 2, NULL, 0, 'Place on greased cookie sheet or shallow baking dish in oven and cook for twenty minutes.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'14AB15E5209F6D49A6CD4BACEEDA16FC', X'E2D8B969AAD6E74094520E1DF5CDFA52', X'E2D8B969AAD6E74094520E1DF5CDFA52', 5, NULL, NULL, 2, NULL, 0, 'Chicken kofta can also be cooked on a grill, either on skewers or alone, or pan-fried for 8 to 10 minutes, turning halfway through cook time.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'26DAD87E43F565479C8AF8FC9354426F', X'3A0782E0763FC540AC215834360C5DE3', X'3A0782E0763FC540AC215834360C5DE3', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients. Heat oven to 425 F.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1DB695500A64BA4E84CCF9F06E797EED', X'3A0782E0763FC540AC215834360C5DE3', X'3A0782E0763FC540AC215834360C5DE3', 1, NULL, NULL, 2, NULL, 0, 'Bake sweet potatoes for one hour or until they can be pierced easily with a fork. Alternatively, you can microwave the sweet potatoes (usually 20 minutes for two large sweet potatoes, depending on your microwave). Allow sweet potatoes to cool completely.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D25B9AB0FFF5954E8EB51F928D414406', X'3A0782E0763FC540AC215834360C5DE3', X'3A0782E0763FC540AC215834360C5DE3', 2, NULL, NULL, 2, NULL, 0, 'Peel the skins off the cooled sweet potatoes and discard.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'46F106C677519046AECF65A3919D2B86', X'3A0782E0763FC540AC215834360C5DE3', X'3A0782E0763FC540AC215834360C5DE3', 3, NULL, NULL, 2, NULL, 0, 'Place cooled sweet potatoes in a large bowl and mash with a fork.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9B6245EE70074347A2244C55259A17E0', X'3A0782E0763FC540AC215834360C5DE3', X'3A0782E0763FC540AC215834360C5DE3', 4, NULL, NULL, 2, NULL, 0, 'Add flour, chickpeas, onion, garlic, parsley, cumin, coriander, lemon juice, and salt.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0CEAA1AC8D3D8344966BCC52AF16BB21', X'3A0782E0763FC540AC215834360C5DE3', X'3A0782E0763FC540AC215834360C5DE3', 5, NULL, NULL, 2, NULL, 0, 'Continue to mash with a fork until well combined and smooth in consistency. Alternatively, pulse all of the ingredients in a food processor for quick mashing.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E6E41D6760BAC04CB7C05B698FC981DF', X'3A0782E0763FC540AC215834360C5DE3', X'3A0782E0763FC540AC215834360C5DE3', 6, NULL, NULL, 2, NULL, 0, 'Lightly grease a baking sheet with nonstick cooking spray. Using your hands, a melon baller, cookie scoop, or tablespoon, form the sweet potato mixture into small balls and place them 2 inches apart on the pan.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2A5FA328663835499EF380C4D20123C1', X'3A0782E0763FC540AC215834360C5DE3', X'3A0782E0763FC540AC215834360C5DE3', 7, NULL, NULL, 2, NULL, 0, 'Sprinkle with the desired amount of sesame seeds.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E37D629A04A0CF4F97E4E813760672DC', X'3A0782E0763FC540AC215834360C5DE3', X'3A0782E0763FC540AC215834360C5DE3', 8, NULL, NULL, 2, NULL, 0, 'Chill the falafel balls in the refrigerator for about 45 minutes.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2A477FCBAF5AD0419CD9C1C9BA00A43F', X'3A0782E0763FC540AC215834360C5DE3', X'3A0782E0763FC540AC215834360C5DE3', 9, NULL, NULL, 2, NULL, 0, 'Preheat oven to 375 F. Remove the chilled falafel balls from the refrigerator and bake for about 10 to 12 minutes or until golden and crispy.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1359A9525B177F4F9B418C7B5BD9745E', X'3A0782E0763FC540AC215834360C5DE3', X'3A0782E0763FC540AC215834360C5DE3', 10, NULL, NULL, 2, NULL, 0, 'Serve and enjoy.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5663DDF9E8FD164CBB66C664799A7ED0', X'CD420BEA61EDA24BAE7B5EB66E83EDA6', X'CD420BEA61EDA24BAE7B5EB66E83EDA6', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D806E33EA2DBDE4EADF8A1BC436272D2', X'CD420BEA61EDA24BAE7B5EB66E83EDA6', X'CD420BEA61EDA24BAE7B5EB66E83EDA6', 1, NULL, NULL, 2, NULL, 0, 'Lightly toast the pita round in a toaster or oven and cut into bite size pieces.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EADA43E0C28C8740BB4E642FA0C8AA88', X'CD420BEA61EDA24BAE7B5EB66E83EDA6', X'CD420BEA61EDA24BAE7B5EB66E83EDA6', 2, NULL, NULL, 2, NULL, 0, 'Add the olive oil and minced garlic to a skillet or cast iron pan and saut for a minute, just until the garlic becomes slightly translucent. Be careful not to burn it.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A0E33610E96BFB45BE537405601B12D2', X'CD420BEA61EDA24BAE7B5EB66E83EDA6', X'CD420BEA61EDA24BAE7B5EB66E83EDA6', 3, NULL, NULL, 2, NULL, 0, 'Add the diced tomato and continue cooking on medium low heat for another minute.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'28C1126CC8A51B40AA244C38DB82BB20', X'CD420BEA61EDA24BAE7B5EB66E83EDA6', X'CD420BEA61EDA24BAE7B5EB66E83EDA6', 4, NULL, NULL, 2, NULL, 0, 'If the asparagus stems are thick, peel them with a vegetable peeler, then slice into thin diagonal pieces. Add to the pan and continue cooking for another minute.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CDEA4AF2977C8A4CA54782E4DA9104C8', X'CD420BEA61EDA24BAE7B5EB66E83EDA6', X'CD420BEA61EDA24BAE7B5EB66E83EDA6', 5, NULL, NULL, 2, NULL, 0, 'Season with the sumac, salt, and pepper. Stir in the toasted pita bread pieces and cook for an additional minute or two until the pita absorbs the flavors.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6267A42E6FA6CA498FDAC4C330B5C410', X'CD420BEA61EDA24BAE7B5EB66E83EDA6', X'CD420BEA61EDA24BAE7B5EB66E83EDA6', 6, NULL, NULL, 2, NULL, 0, 'Finish with the lemon juice and serve warm.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CF7215A6B3814B4985BC4EAABC7E725B', X'BBF6BB7B5C678F4DA2B94562EF839B24', X'BBF6BB7B5C678F4DA2B94562EF839B24', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AF7671E02F06F743AEE0E40CD33CA8CF', X'BBF6BB7B5C678F4DA2B94562EF839B24', X'BBF6BB7B5C678F4DA2B94562EF839B24', 1, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 400 F.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'ADD880633D334A4698DC167C8E88E3A5', X'BBF6BB7B5C678F4DA2B94562EF839B24', X'BBF6BB7B5C678F4DA2B94562EF839B24', 2, NULL, NULL, 2, NULL, 0, 'In a small bowl, toss the chickpeas with the tablespoon of olive oil and the 1/2 teaspoon of Za''atar. Spread out on a baking sheet lined with parchment paper. You can make the pita bowls at the same time.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A7CF685CEA758A428417A9CD1BA05D4A', X'BBF6BB7B5C678F4DA2B94562EF839B24', X'BBF6BB7B5C678F4DA2B94562EF839B24', 3, NULL, NULL, 2, NULL, 0, 'Rub both pitas with the tablespoon of olive oil and sprinkle evenly with the 1/2 teaspoon of Za''atar. Microwave for 20 seconds to make them pliable.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A97409D9055A8D4F921911A1A9744184', X'BBF6BB7B5C678F4DA2B94562EF839B24', X'BBF6BB7B5C678F4DA2B94562EF839B24', 4, NULL, NULL, 2, NULL, 0, 'To form the bowl, drape them over a small, oven-safe bowl or even the back of a muffin tin cup. If you have nothing like that, you can also form balls out of aluminum foil and drape the pitas over them. Bake both the chickpeas and pitas for 12 minutes.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'47174790B60792429BAD530C9CF14E50', X'BBF6BB7B5C678F4DA2B94562EF839B24', X'BBF6BB7B5C678F4DA2B94562EF839B24', 5, NULL, NULL, 2, NULL, 0, 'While the pita and chickpeas are in the oven, make the dressing by whisking together the olive oil, lemon juice, tahini, and Za''atar.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'678A39A079363F4BBC939D7D9AFD5B76', X'BBF6BB7B5C678F4DA2B94562EF839B24', X'BBF6BB7B5C678F4DA2B94562EF839B24', 6, NULL, NULL, 2, NULL, 0, 'When the pita bowls have cooled off enough to handle, assemble the salads by adding equal amounts of the baby spinach, tomatoes, cucumbers, red onion and roasted chickpeas to each pita bowl. Top with the vinaigrette.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2ABB327797771A4092A1E1116158D8BE', X'BBF6BB7B5C678F4DA2B94562EF839B24', X'BBF6BB7B5C678F4DA2B94562EF839B24', 7, NULL, NULL, 2, NULL, 0, 'Serve and enjoy!');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'20DFA03AED4B0F46844CCE4C5E0A9014', X'DCEF8660122B3F41AD851ED9234B4CAE', X'DCEF8660122B3F41AD851ED9234B4CAE', 0, NULL, NULL, 2, NULL, 0, 'Finely chop the onion and parsley. Time-saving Tip: Use a food processor.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CAA0FDE75EB02C4C8345CEB7B74FEA62', X'DCEF8660122B3F41AD851ED9234B4CAE', X'DCEF8660122B3F41AD851ED9234B4CAE', 1, NULL, NULL, 2, NULL, 0, 'Add the ground beef to a large bowl and stir in the onions and parsley.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'237D04D0A2FDA44680511D7168A097BF', X'DCEF8660122B3F41AD851ED9234B4CAE', X'DCEF8660122B3F41AD851ED9234B4CAE', 2, NULL, NULL, 2, NULL, 0, 'Season with salt and pepper.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BF0E311339CDFE469C129E621863A442', X'DCEF8660122B3F41AD851ED9234B4CAE', X'DCEF8660122B3F41AD851ED9234B4CAE', 3, NULL, NULL, 2, NULL, 0, 'Form the beef mixture into small balls and place four or five balls onto a metal skewer.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7C72ABE3E433764D91E279109F83F8F6', X'DCEF8660122B3F41AD851ED9234B4CAE', X'DCEF8660122B3F41AD851ED9234B4CAE', 4, NULL, NULL, 2, NULL, 0, 'Shape the meat into a cigar shape on the skewer. Note: if you use wooden skewers, be sure to soak them in water for 30 minutes first to avoid them burning.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0AEF07A5663D6F469BA3CC397A960B7B', X'DCEF8660122B3F41AD851ED9234B4CAE', X'DCEF8660122B3F41AD851ED9234B4CAE', 5, NULL, NULL, 2, NULL, 0, 'Pre-heat the oven to 350 F.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4E0D90113DA65347BB4D0E26EA33B1AB', X'DCEF8660122B3F41AD851ED9234B4CAE', X'DCEF8660122B3F41AD851ED9234B4CAE', 6, NULL, NULL, 2, NULL, 0, 'Bake the skewers for 45 minutes.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6A39C9B17DF0934AADF7143C6BFA6129', X'DCEF8660122B3F41AD851ED9234B4CAE', X'DCEF8660122B3F41AD851ED9234B4CAE', 7, NULL, NULL, 2, NULL, 0, 'Or if grilling, grill for 20 to 25 minutes or until done.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BECFAB0CDC271446902FC3D1BFECB4BF', X'DCEF8660122B3F41AD851ED9234B4CAE', X'DCEF8660122B3F41AD851ED9234B4CAE', 8, NULL, NULL, 2, NULL, 0, 'Serve over a bed of white rice.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A6229AB291B4FC4CA98914921A707378', X'AB03A0242053F44A99062D01AB21084B', X'AB03A0242053F44A99062D01AB21084B', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C62943B0B9EF7E45A6C7A4516510F34F', X'AB03A0242053F44A99062D01AB21084B', X'AB03A0242053F44A99062D01AB21084B', 1, NULL, NULL, 2, NULL, 0, 'Trim the lamb of all excess fat and cut it into 1-inch cubes. Cut the bell peppers and onions into 1-inch pieces.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'016D1665D891BB49B256FF21C2CFB549', X'AB03A0242053F44A99062D01AB21084B', X'AB03A0242053F44A99062D01AB21084B', 2, NULL, NULL, 2, NULL, 0, 'In a small plastic or glass bowl, combine the marinade ingredients (olive oil, lemon juice, sea salt, cumin, grated ginger, and black pepper).');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2E583257134C124A99189730992C2FD2', X'AB03A0242053F44A99062D01AB21084B', X'AB03A0242053F44A99062D01AB21084B', 3, NULL, NULL, 2, NULL, 0, 'In a resealable container (a large zip-top bag works best), place the lamb pieces with the marinade. Massage them to mix and seal bag. Refrigerate the meat in the marinade for 2 to 24 hours.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'63A0929EEED3E344A70E9FF82B014081', X'AB03A0242053F44A99062D01AB21084B', X'AB03A0242053F44A99062D01AB21084B', 4, NULL, NULL, 2, NULL, 0, 'Preheat the grill to high heat. Thread lamb, onion, and pepper pieces onto skewers, intermixing the pieces or making skewers of just meat and just vegetables (see Recipe Tip below).');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6699AB0340365240A2C38304918DC994', X'AB03A0242053F44A99062D01AB21084B', X'AB03A0242053F44A99062D01AB21084B', 5, NULL, NULL, 2, NULL, 0, 'Place the kebabs on the hot grill and cook for about 8 to 10 minutes, uncovered. Starting at the 2-minute mark, turn the skewers to cook another side of the cubes. Do this every 2 minutes until all four sides of the cubes have been grilled. Cook longer if lamb chunks are larger than 1-inch.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'41F6EFD6FDA5644EADE944B9746CACCC', X'AB03A0242053F44A99062D01AB21084B', X'AB03A0242053F44A99062D01AB21084B', 6, NULL, NULL, 2, NULL, 0, 'Once cooked to your desired doneness, remove the kebabs from the grill.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6DF64D391BE29E4DA5F08A2FC20C82E0', X'AB03A0242053F44A99062D01AB21084B', X'AB03A0242053F44A99062D01AB21084B', 7, NULL, NULL, 2, NULL, 0, 'Serve on or off of the skewers, with lemon and lime slices.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C18B5818BFE3DE4CA508A76077301123', X'A09CAD9DDD78504F93F8A077C16E1724', X'A09CAD9DDD78504F93F8A077C16E1724', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'92042149069CD1459802AB0738A21E6A', X'A09CAD9DDD78504F93F8A077C16E1724', X'A09CAD9DDD78504F93F8A077C16E1724', 1, NULL, NULL, 2, NULL, 0, 'Prepare the couscous. In a medium-sized saucepan over medium-high heat, heat 2 tablespoons olive oil. Add the couscous and cook, stirring occasionally, for about 3 to 4 minutes or until fragrant and lightly toasted.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'235E0BA81D2E2546A2D493426446308D', X'A09CAD9DDD78504F93F8A077C16E1724', X'A09CAD9DDD78504F93F8A077C16E1724', 2, NULL, NULL, 2, NULL, 0, 'Add the vegetable broth, bring the mixture to a boil, and then turn down the heat and simmer for 10 to 12 minutes or until the liquid is absorbed and the couscous is tender. Set aside.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1B2FB48155526F4E9CC58FF980031717', X'A09CAD9DDD78504F93F8A077C16E1724', X'A09CAD9DDD78504F93F8A077C16E1724', 3, NULL, NULL, 2, NULL, 0, 'While the couscous simmers, heat the remaining 1 tablespoon olive oil in a saucepan over medium-high heat. Add the garlic, mushrooms, and white wine and cook, stirring often, for about 5 minutes.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E1819D046AD7804D8AB579FE8A64D083', X'A09CAD9DDD78504F93F8A077C16E1724', X'A09CAD9DDD78504F93F8A077C16E1724', 4, NULL, NULL, 2, NULL, 0, 'Add the dried thyme, fresh parsley, and salt, stirring to combine, and cook for about 3 to 4 minutes more, or until most of the moisture from the mushrooms has evaporated.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B8EDE7441516834A8EA8E1E0D6C8ABD2', X'A09CAD9DDD78504F93F8A077C16E1724', X'A09CAD9DDD78504F93F8A077C16E1724', 5, NULL, NULL, 2, NULL, 0, 'Add the cooked Israeli couscous to the mushroom mixture, tossing well to combine. Add salt and pepper to taste.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7161696B41803E47BEEED6017B0AE83F', X'9967713D295F154B80D64C5715E8CA01', X'9967713D295F154B80D64C5715E8CA01', 0, NULL, NULL, 2, NULL, 0, 'Combine the yogurt, salt, black pepper, turmeric, mustard, curry powder, cardamom, lemon juice, and vinegar in a large glass or plastic bowl.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D869897F2E6A6340AB4A5BC0883268DC', X'9967713D295F154B80D64C5715E8CA01', X'9967713D295F154B80D64C5715E8CA01', 1, NULL, NULL, 2, NULL, 0, 'Add the cubed chicken into the bowl, toss to combine, cover with plastic wrap, and place in the refrigerator for 1 to 4 hours.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9180E14EC0317349A62765365AC5BD21', X'9967713D295F154B80D64C5715E8CA01', X'9967713D295F154B80D64C5715E8CA01', 2, NULL, NULL, 2, NULL, 0, 'Preheat the grill to medium-high heat.');
