INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2F7AC6583F0C3D4885C389F35CF47168', X'7DBB459ADA250B418FAA3A7A901D20BF', X'7DBB459ADA250B418FAA3A7A901D20BF', 3, NULL, NULL, 2, NULL, 0, 'Raise the heat and add the chicken stock, then the quartered potato, cauliflower florets, and cumin.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0F1E6E75B3A646428E86600FD6AA0E2A', X'7DBB459ADA250B418FAA3A7A901D20BF', X'7DBB459ADA250B418FAA3A7A901D20BF', 4, NULL, NULL, 2, NULL, 0, 'Bring to a boil, cover, reduce the heat to low and simmer for 25 minutes.');
