INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9F8FF6011FA39441908438F772E712C7', X'7DBB459ADA250B418FAA3A7A901D20BF', X'7DBB459ADA250B418FAA3A7A901D20BF', 6, NULL, NULL, 2, NULL, 0, 'While the soup is cooking, combined the drained and rinsed chickpeas with the olive oil, cumin and garlic powder.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E0DF44D154FE8642BCCBF0300CE18991', X'7DBB459ADA250B418FAA3A7A901D20BF', X'7DBB459ADA250B418FAA3A7A901D20BF', 7, NULL, NULL, 2, NULL, 0, 'You can either spread them out on a baking sheet and roast in the oven at 400 degrees for 20 minutes or, as I did, use a cast iron pan on the stovetop and stir occasionally.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DE73DAD12A100F4AAB08213825E9A70D', X'7DBB459ADA250B418FAA3A7A901D20BF', X'7DBB459ADA250B418FAA3A7A901D20BF', 8, NULL, NULL, 2, NULL, 0, 'Season with salt and pepper and serve on top of the cooked soup.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A4179A9DD9E29C4399EEE98548E62134', X'7DBB459ADA250B418FAA3A7A901D20BF', X'7DBB459ADA250B418FAA3A7A901D20BF', 9, NULL, NULL, 2, NULL, 0, 'Enjoy!');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'93112100109DA14FB3EB63C3A5FCAD92', X'E3AF80B322F69A48BCC76D24D3A74584', X'E3AF80B322F69A48BCC76D24D3A74584', 0, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 375 F.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CA3938219AC5AE499C1913569E4B5D06', X'E3AF80B322F69A48BCC76D24D3A74584', X'E3AF80B322F69A48BCC76D24D3A74584', 1, NULL, NULL, 2, NULL, 0, 'Season each lamb chop with an equal amount of the za''atar on both sides.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D3B986B08BF8CA4A86AD46DB350CD694', X'E3AF80B322F69A48BCC76D24D3A74584', X'E3AF80B322F69A48BCC76D24D3A74584', 2, NULL, NULL, 2, NULL, 0, 'Heat the olive oil in a large skillet or cast iron pan.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A9083B9E96B9DD4DAB549AD9EA79DD96', X'E3AF80B322F69A48BCC76D24D3A74584', X'E3AF80B322F69A48BCC76D24D3A74584', 3, NULL, NULL, 2, NULL, 0, 'When hot, add the lamb chops and sear on high heat for 2 minutes on each side.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'611628D5695C0F46B0151F02A8021928', X'E3AF80B322F69A48BCC76D24D3A74584', X'E3AF80B322F69A48BCC76D24D3A74584', 4, NULL, NULL, 2, NULL, 0, 'Place in the oven for 5 minutes, flip chops and continue cooking for an additional 5 minutes. Chops will be medium-rare at an internal temperature of 145 degrees, medium at 160 F and well done at 170 F. Continue cooking until you''ve reached your desired temperature.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'241792A4E480854B906413BF5FFF8FDD', X'E3AF80B322F69A48BCC76D24D3A74584', X'E3AF80B322F69A48BCC76D24D3A74584', 5, NULL, NULL, 2, NULL, 0, 'Remove the pan from the oven and move the cooked chops to a plate and cover with aluminum foil to keep warm while you make the sauce.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B1BE5873AE2BD945815445242EE222E9', X'E3AF80B322F69A48BCC76D24D3A74584', X'E3AF80B322F69A48BCC76D24D3A74584', 6, NULL, NULL, 2, NULL, 0, 'Place the pan on medium-low heat on the stovetop and add the tablespoon of butter.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'24972A6FF63DD9428028E8CD4B5260E1', X'E3AF80B322F69A48BCC76D24D3A74584', X'E3AF80B322F69A48BCC76D24D3A74584', 7, NULL, NULL, 2, NULL, 0, 'Whisk in the flour until fully coated and then whisk in the beef stock.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'38F082B69304424E9EA6CDE521DF0C39', X'E3AF80B322F69A48BCC76D24D3A74584', X'E3AF80B322F69A48BCC76D24D3A74584', 8, NULL, NULL, 2, NULL, 0, 'Continue cooking until the sauce has thickened.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B369C7AD09591A4099B19D866C55A6C1', X'E3AF80B322F69A48BCC76D24D3A74584', X'E3AF80B322F69A48BCC76D24D3A74584', 9, NULL, NULL, 2, NULL, 0, 'Season with additional salt and pepper, if needed, and pour over the cooked lamb.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'94DB3A30A99D4343801923B70A576FDD', X'BEF85B3600875748AFEABD48A5ACE1C4', X'BEF85B3600875748AFEABD48A5ACE1C4', 0, NULL, NULL, 2, NULL, 0, 'Preheat your oven to 400 F. Once the oven has preheated, arrange the 3 cloves garlic and red onion onto a small baking dish and roast for about 27 minutes, until fork-tender. Remove from the oven, but do not turn the oven off. Allow the garlic and red onion to cool for about 10 minutes.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EF0DCA7EB9E0AE46BF0D2E7DEA542500', X'BEF85B3600875748AFEABD48A5ACE1C4', X'BEF85B3600875748AFEABD48A5ACE1C4', 1, NULL, NULL, 2, NULL, 0, 'Place the roasted garlic and red onion into the bowl of a food processor along with the cooked chickpeas, ground coriander, ground cumin, and salt. Puree the chickpeas until smooth, stopping every 30 seconds or so to scrape the bowl of the food processor, or as needed. The mixture should be the consistency of a thick hummus after blending.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'139641922551984DB3DD689E6246A820', X'BEF85B3600875748AFEABD48A5ACE1C4', X'BEF85B3600875748AFEABD48A5ACE1C4', 2, NULL, NULL, 2, NULL, 0, 'Transfer the chickpea puree to a large bowl. Add the baking powder and chickpea flour and fold in gently to combine and distribute throughout the chickpea mixture. Fold in the chopped parsley and cilantro. Using very clean and dry hands, work the mixture into a dough, adding more chickpea flour as needed. Divide the dough into 2 inch balls and then press gently to shape into flattened pucks, about 1 1/2 inches wide.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'472B834164A10442BA77149B18815B8B', X'BEF85B3600875748AFEABD48A5ACE1C4', X'BEF85B3600875748AFEABD48A5ACE1C4', 3, NULL, NULL, 2, NULL, 0, 'Arrange the unbaked falafel onto a parchment paper covered cookie sheet and lightly brush the tops of the falafel patties with 1/2 of the amount of cooking oil called for. Flip over and brush the other side of the falafel with the remaining oilyou may not need to use all of the oil called for. Sprinkle one side of the falafel patties with ground black pepper.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'ABE788AD954C394591F8CF5B1C208D63', X'BEF85B3600875748AFEABD48A5ACE1C4', X'BEF85B3600875748AFEABD48A5ACE1C4', 4, NULL, NULL, 2, NULL, 0, 'Bake in the preheated oven for 15 minutes, then flip the falafel over and bake an additional 15 to 18 minutes, or until golden brown, fragrant, and baked throughout.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C3B0C35D364FC040A55F806E75628270', X'BEF85B3600875748AFEABD48A5ACE1C4', X'BEF85B3600875748AFEABD48A5ACE1C4', 5, NULL, NULL, 2, NULL, 0, 'Enjoy stuffed into a pita, on top of a salad, or as a quick and easy lunch on the go.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DD0C8FF944E9914AB6F952D4E4C8662C', X'A7810EA0D05C724FA30A74CB539C3844', X'A7810EA0D05C724FA30A74CB539C3844', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8747C2066158FF4798018FD789D1E775', X'A7810EA0D05C724FA30A74CB539C3844', X'A7810EA0D05C724FA30A74CB539C3844', 1, NULL, NULL, 2, NULL, 0, 'Drain lamb on a paper towel for 15 minutes.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A66D22A0CAA2F24CAA6E7C0BE4905A6F', X'A7810EA0D05C724FA30A74CB539C3844', X'A7810EA0D05C724FA30A74CB539C3844', 2, NULL, NULL, 2, NULL, 0, 'Preheat grill.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1FCC9CAA31DBE4409AA677C8BD11AC65', X'A7810EA0D05C724FA30A74CB539C3844', X'A7810EA0D05C724FA30A74CB539C3844', 3, NULL, NULL, 2, NULL, 0, 'Mix all ingredients together.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'064007BBA0076A45B41F4A325FBFAC47', X'A7810EA0D05C724FA30A74CB539C3844', X'A7810EA0D05C724FA30A74CB539C3844', 4, NULL, NULL, 2, NULL, 0, 'Mold mixture around long skewers.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F7BAD2F79ABAC34BBB858BD3CF188FF1', X'A7810EA0D05C724FA30A74CB539C3844', X'A7810EA0D05C724FA30A74CB539C3844', 5, NULL, NULL, 2, NULL, 0, 'Or, form the mixture into long thin sausages and thread onto skewers. The latter option is a bit more difficult for most people, but you select the option that best works for you. Note: only the ends of the skewers should be visible.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7A76F7134EDB5A42800F91A1DA0965C7', X'A7810EA0D05C724FA30A74CB539C3844', X'A7810EA0D05C724FA30A74CB539C3844', 6, NULL, NULL, 2, NULL, 0, 'Place on grill and brush with oil and cook until done. The safe temperature for ground lamb is 165 F or 75 C.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'142AA6D8A25AB447AFE9473DDB3486A9', X'A7810EA0D05C724FA30A74CB539C3844', X'A7810EA0D05C724FA30A74CB539C3844', 7, NULL, NULL, 2, NULL, 0, 'Remove kofta from the skewers and serve.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C16C578425DE764E9D0DE149D9AFCADD', X'A7810EA0D05C724FA30A74CB539C3844', X'A7810EA0D05C724FA30A74CB539C3844', 8, NULL, NULL, 2, NULL, 0, 'Enjoy!');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'63CD79D50217024F93209DC44F059B0D', X'6A0EBA1ABC6AD24089C8FCFBB3A3DFEF', X'6A0EBA1ABC6AD24089C8FCFBB3A3DFEF', 0, NULL, NULL, 2, NULL, 0, 'Add the ground beef, ground cumin, garlic powder, ground coriander, dried oregano, black pepper, salt, and baharat, if using, to a bowl and mix well (clean hands work best for this).');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'71A3BC008C49B54DAEBCB8DEF2119A43', X'6A0EBA1ABC6AD24089C8FCFBB3A3DFEF', X'6A0EBA1ABC6AD24089C8FCFBB3A3DFEF', 1, NULL, NULL, 2, NULL, 0, 'Form into 4 equal-sized patties.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'979EC57A9D1C83459D70F03511A7BF78', X'6A0EBA1ABC6AD24089C8FCFBB3A3DFEF', X'6A0EBA1ABC6AD24089C8FCFBB3A3DFEF', 2, NULL, NULL, 2, NULL, 0, 'Place the patties on your grill pan (indoor or outdoor) and cook the burgers to your desired doneness.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C4EAD7334A01A54FBB39DBE59CFA5912', X'6A0EBA1ABC6AD24089C8FCFBB3A3DFEF', X'6A0EBA1ABC6AD24089C8FCFBB3A3DFEF', 3, NULL, NULL, 2, NULL, 0, 'Assemble the burgers on the buns and top with an equal number of the greens, crumbled feta cheese, red onion and tahini sauce.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4819DE6750D15A49884DD492337EAFB8', X'F57A4E5329B14D478134830C1A32A879', X'F57A4E5329B14D478134830C1A32A879', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8B5079E9DBF82444A784CF2415F4C2A5', X'F57A4E5329B14D478134830C1A32A879', X'F57A4E5329B14D478134830C1A32A879', 1, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 400 F.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'404BB2A4D0A60E48B1C26F810E26A867', X'F57A4E5329B14D478134830C1A32A879', X'F57A4E5329B14D478134830C1A32A879', 2, NULL, NULL, 2, NULL, 0, 'Combine 1 tablespoon of the olive oil with the teaspoon of cumin.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'06AC3DB0047C2D40A841F4DD3211F016', X'F57A4E5329B14D478134830C1A32A879', X'F57A4E5329B14D478134830C1A32A879', 3, NULL, NULL, 2, NULL, 0, 'Peel and slice the carrots and toss them in the cumin oil.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C4756AFBF2C8AA4484C684CCBCED1999', X'F57A4E5329B14D478134830C1A32A879', X'F57A4E5329B14D478134830C1A32A879', 4, NULL, NULL, 2, NULL, 0, 'Place on a baking sheet lined with parchment paper, season with salt and pepper and roast in the oven for 30 minutes.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9FA57A31A8C0BD4BB5448D34AFBB7ECF', X'F57A4E5329B14D478134830C1A32A879', X'F57A4E5329B14D478134830C1A32A879', 5, NULL, NULL, 2, NULL, 0, 'Bring a large pot of salted water to a boil.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'91744752C217CA4AB621FBAB02AF4941', X'F57A4E5329B14D478134830C1A32A879', X'F57A4E5329B14D478134830C1A32A879', 6, NULL, NULL, 2, NULL, 0, 'Add the farro, cover, reduce the heat to low and simmer for 20 minutes.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EDCFB3619D152A4EB4ABF80864B997F4', X'F57A4E5329B14D478134830C1A32A879', X'F57A4E5329B14D478134830C1A32A879', 7, NULL, NULL, 2, NULL, 0, 'Drain and return to the pot.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5D58C039B264F7479CE4EA37775D5F71', X'F57A4E5329B14D478134830C1A32A879', X'F57A4E5329B14D478134830C1A32A879', 8, NULL, NULL, 2, NULL, 0, 'Add the remaining tablespoon of olive oil to a skillet or cast iron pan along with the sliced onion.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'91B763CE12046B4EBCA9DE8F195A393B', X'F57A4E5329B14D478134830C1A32A879', X'F57A4E5329B14D478134830C1A32A879', 9, NULL, NULL, 2, NULL, 0, 'Saute for a few minutes until the onion is soft and a light golden brown.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EAD878B771FCE84CACA3BB33988C9A80', X'F57A4E5329B14D478134830C1A32A879', X'F57A4E5329B14D478134830C1A32A879', 10, NULL, NULL, 2, NULL, 0, 'Stir the sauteed onion into the cooked farro.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E7E38067B351BA4391BFC208D8A17E28', X'F57A4E5329B14D478134830C1A32A879', X'F57A4E5329B14D478134830C1A32A879', 11, NULL, NULL, 2, NULL, 0, 'Make the cumin tahini sauce by combining the sesame paste, water, minced garlic, lemon juice and cumin in a bowl.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'25387E906DCE6A4A8AD8FD9587A22B6F', X'F57A4E5329B14D478134830C1A32A879', X'F57A4E5329B14D478134830C1A32A879', 12, NULL, NULL, 2, NULL, 0, 'Whisk until smooth and season with salt and pepper to taste.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'62679527FDB56949A6947A94B0A61910', X'F57A4E5329B14D478134830C1A32A879', X'F57A4E5329B14D478134830C1A32A879', 13, NULL, NULL, 2, NULL, 0, 'To serve, spoon the farro and onion mixture onto a platter, top with the roasted carrots and drizzle with the tahini sauce. Enjoy!');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'99AC404EA055B34FB1A42FB4173DC026', X'BBB97CF29A0B6A4AA7A325E39D2E8D57', X'BBB97CF29A0B6A4AA7A325E39D2E8D57', 0, NULL, NULL, 2, NULL, 0, 'Toss the chicken strips in the canola oil and cumin. Place on a grill pan (or outdoor grill) and cook for a few minutes on each side until cooked through. Season with salt and pepper.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E1D738D09FBAE74E97B09B2297234FB0', X'BBB97CF29A0B6A4AA7A325E39D2E8D57', X'BBB97CF29A0B6A4AA7A325E39D2E8D57', 1, NULL, NULL, 2, NULL, 0, 'Peel, seed and dice the cucumber or you can use an English seedless cucumber which does not require peeling or seeding. Peel and julienne the carrots, dice the tomatoes and peel and slice the red onion. Toss in a bowl with the red pepper flakes and parsley or cilantro.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'31E35D588B1B8946A3B17FC408716ABB', X'BBB97CF29A0B6A4AA7A325E39D2E8D57', X'BBB97CF29A0B6A4AA7A325E39D2E8D57', 2, NULL, NULL, 2, NULL, 0, 'Make the dressing by combining the sesame paste, water, lemon juice, garlic powder and sumac. Stir until fully combined and season with salt and pepper.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E95B3380AACDFA4B9D95CA7127570F01', X'BBB97CF29A0B6A4AA7A325E39D2E8D57', X'BBB97CF29A0B6A4AA7A325E39D2E8D57', 3, NULL, NULL, 2, NULL, 0, 'To assemble the tacos, divide the grilled chicken strips equally among the four pita rounds. Top with an equal amount of the vegetables and drizzle the tahini sauce on top.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'191E8FDD2144764AA974B645A94C8706', X'4E73250834BD894A8A1271800EC25F07', X'4E73250834BD894A8A1271800EC25F07', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E49C848A811D3F4F8A2655ECD32D3A6B', X'4E73250834BD894A8A1271800EC25F07', X'4E73250834BD894A8A1271800EC25F07', 1, NULL, NULL, 2, NULL, 0, 'Preheat oven to 400 F.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F4343A1642A9CE4A994242234EB3B5EC', X'4E73250834BD894A8A1271800EC25F07', X'4E73250834BD894A8A1271800EC25F07', 2, NULL, NULL, 2, NULL, 0, 'Lightly oil a baking pan and scatter the sliced onion across it. Lay the fillets side-by-side on the pan, on top of the onions, and sprinkle with the herbs and seasoning. Drizzle with olive oil and lemon.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7F180E8DEF72DB4999F68F5600F4AD34', X'4E73250834BD894A8A1271800EC25F07', X'4E73250834BD894A8A1271800EC25F07', 3, NULL, NULL, 2, NULL, 0, 'Bake the fish for 10 minutes, or until it flakes easily with a fork.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'11ECF49A6476E3409D45B38620A03A42', X'4E73250834BD894A8A1271800EC25F07', X'4E73250834BD894A8A1271800EC25F07', 4, NULL, NULL, 2, NULL, 0, 'While the fish is baking, whisk together the tahini and 1/4 cup of hot water. If the mixture is too thick, add additional water 1 tablespoon at a time until it is thick but creamy.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E903145E550643489A284165F1CB9DE9', X'4E73250834BD894A8A1271800EC25F07', X'4E73250834BD894A8A1271800EC25F07', 5, NULL, NULL, 2, NULL, 0, 'Add the garlic, olive oil and lemon juice and whisk thoroughly. If necessary, add warm water until the sauce reaches desired consistency. Add the parsley and seasonings to taste.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F17465B4081E0E4FA931D1D92B1CD12F', X'4E73250834BD894A8A1271800EC25F07', X'4E73250834BD894A8A1271800EC25F07', 6, NULL, NULL, 2, NULL, 0, '?When the fish is cooked, top it with the sauce and serve immediately.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'481C8A3A42877A4BB82CDB525BD4F3BA', X'8A7AB314226A5B45A4451930922219AE', X'8A7AB314226A5B45A4451930922219AE', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3E2643FF720A5743B06A5C9066360F4A', X'8A7AB314226A5B45A4451930922219AE', X'8A7AB314226A5B45A4451930922219AE', 1, NULL, NULL, 2, NULL, 0, 'Make the garam masala by combining the spices until very fine, using a blender or a coffee grinder. Set aside.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6FBD3D7811639149BE62257077580FE8', X'8A7AB314226A5B45A4451930922219AE', X'8A7AB314226A5B45A4451930922219AE', 2, NULL, NULL, 2, NULL, 0, 'Put the ginger, onions, and garlic into a blender, and mix to a paste, adding a tablespoon of water if necessary. Set aside.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1D90E2109B241A42902DAB6459F5CDEF', X'8A7AB314226A5B45A4451930922219AE', X'8A7AB314226A5B45A4451930922219AE', 3, NULL, NULL, 2, NULL, 0, 'Put the peppercorns, cloves, bay leaves, and cardamom pods in one bowl.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E903F70179D8F34282FED948B9FCEDE9', X'8A7AB314226A5B45A4451930922219AE', X'8A7AB314226A5B45A4451930922219AE', 4, NULL, NULL, 2, NULL, 0, 'Put the ground cumin, ground coriander, cayenne, and 1 teaspoon of the salt in another bowl.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7DCA2575CAA4D440820D3E8920E95DC4', X'8A7AB314226A5B45A4451930922219AE', X'8A7AB314226A5B45A4451930922219AE', 5, NULL, NULL, 2, NULL, 0, 'Put the yogurt into another bowl.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3F39AA770699B341829CBF6B52911C0A', X'8A7AB314226A5B45A4451930922219AE', X'8A7AB314226A5B45A4451930922219AE', 6, NULL, NULL, 2, NULL, 0, 'Wash and chop and drain the fresh spinach, have it ready to add into the dish. (Or defrost the frozen spinach.)');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A5EE5BA2B92B5E4EA0478ADDD3D4EAB5', X'8A7AB314226A5B45A4451930922219AE', X'8A7AB314226A5B45A4451930922219AE', 7, NULL, NULL, 2, NULL, 0, 'Put the remaining teaspoon of salt into another bowl.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B5794783028CFD4DBCAD77ADA7A5622F', X'8A7AB314226A5B45A4451930922219AE', X'8A7AB314226A5B45A4451930922219AE', 8, NULL, NULL, 2, NULL, 0, 'Heat the vegetable oil in a large Dutch oven.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E3BD079DE25B9F40B93CBE329B470554', X'8A7AB314226A5B45A4451930922219AE', X'8A7AB314226A5B45A4451930922219AE', 9, NULL, NULL, 2, NULL, 0, 'When hot, put in the peppercorns, cloves, bay leaves, and cardamom pods.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CA74CDF89372F94AAF73808953F31295', X'8A7AB314226A5B45A4451930922219AE', X'8A7AB314226A5B45A4451930922219AE', 10, NULL, NULL, 2, NULL, 0, 'Stir for a second, put in the onions, garlic, and ginger. Stir and fry until the paste starts to develop brown specks.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D369769CC570324B94972132B52DEEAE', X'8A7AB314226A5B45A4451930922219AE', X'8A7AB314226A5B45A4451930922219AE', 11, NULL, NULL, 2, NULL, 0, 'Add the ground cumin, ground coriander, cayenne, and one teaspoon of the salt. Stir together, then add the meat. Stir and fry for a minute.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F01B48373A6BED4BB3761560CF651379', X'8A7AB314226A5B45A4451930922219AE', X'8A7AB314226A5B45A4451930922219AE', 12, NULL, NULL, 2, NULL, 0, 'Next, add the yogurt one teaspoon at a time, until the yogurt has been mixed thoroughly with the other ingredients. Keep on frying until the meat has a slightly browned look.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5CD567D22329634ABD31E3BBD00E84D0', X'8A7AB314226A5B45A4451930922219AE', X'8A7AB314226A5B45A4451930922219AE', 13, NULL, NULL, 2, NULL, 0, 'Add the spinach, bit by bit, stirring in more as the spinach in the pan wilts. Keep stirring and cooking until the spinach wilts completely. It should give up its juices, to cook the meat.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BC09802B29D6CF4AAB8883626BFA9268', X'8A7AB314226A5B45A4451930922219AE', X'8A7AB314226A5B45A4451930922219AE', 14, NULL, NULL, 2, NULL, 0, 'Put the entire mixture in the slow cooker, and allow it to simmer for 6 to 8 hours on high or 8 to 10 hours on low until meat is tender.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2902FB4A4C003245BE96349DCE8DC738', X'8A7AB314226A5B45A4451930922219AE', X'8A7AB314226A5B45A4451930922219AE', 15, NULL, NULL, 2, NULL, 0, 'When done, sprinkle the garam masala over the meat, and stir in, then simmer for about 5 minutes longer. If there is too much liquid, simmer uncovered for 5 to 10 minutes until sauce is thicker.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AF8FECA946A36E47860ACCCF762579CA', X'8A7AB314226A5B45A4451930922219AE', X'8A7AB314226A5B45A4451930922219AE', 16, NULL, NULL, 2, NULL, 0, 'Enjoy.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'79BF8EB9176E12449A819634B3E867CB', X'39777A08CB1A9B44A95D1344A2F2109B', X'39777A08CB1A9B44A95D1344A2F2109B', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3999E9F4735A75489BFACF9A3A6961BC', X'39777A08CB1A9B44A95D1344A2F2109B', X'39777A08CB1A9B44A95D1344A2F2109B', 1, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 400 F.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8F771BC0A1CBE84BA895F33A02B6F8A6', X'39777A08CB1A9B44A95D1344A2F2109B', X'39777A08CB1A9B44A95D1344A2F2109B', 2, NULL, NULL, 2, NULL, 0, 'Spread the hummus evenly on the pita or naan bread. Sprinkle with the tomatoes, crumbled feta cheese, and za''atar.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AE1A00A024D80744893AF39FF1FEE8F8', X'39777A08CB1A9B44A95D1344A2F2109B', X'39777A08CB1A9B44A95D1344A2F2109B', 3, NULL, NULL, 2, NULL, 0, 'Place the pizza on a baking sheet lined with parchment paper and bake for 15 to 20 minutes or until the bread is a light golden brown.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'979150836D52724F89B665107A014BDB', X'39777A08CB1A9B44A95D1344A2F2109B', X'39777A08CB1A9B44A95D1344A2F2109B', 4, NULL, NULL, 2, NULL, 0, 'Remove from the oven and sprinkle with the chopped spinach.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'546BD80BCBB9D4488F4642ADE16401C3', X'39777A08CB1A9B44A95D1344A2F2109B', X'39777A08CB1A9B44A95D1344A2F2109B', 5, NULL, NULL, 2, NULL, 0, 'Serve and enjoy!');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7F4F26D386F0944AADCEB20CE9540DFF', X'B63E0A2177399447A16D3573A17A997E', X'B63E0A2177399447A16D3573A17A997E', 0, NULL, NULL, 2, NULL, 0, 'Place stew beef, onions, garlic, salt, and pepper in a 3 quart pot. Add water to cover. Bring to a boil, reduce heat to simmer and cover. Simmer until meat is tender, about 30 minutes.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'68CDE5E426A428419DF998E7DBF0F0AE', X'B63E0A2177399447A16D3573A17A997E', X'B63E0A2177399447A16D3573A17A997E', 1, NULL, NULL, 2, NULL, 0, 'When meat is tender, add okra.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BA8074EFE63CFD4BB171953B40268ACC', X'B63E0A2177399447A16D3573A17A997E', X'B63E0A2177399447A16D3573A17A997E', 2, NULL, NULL, 2, NULL, 0, 'Once okra begins to become tender, about 10 minutes, add 1 can of crushed tomato/tomato sauce and mix well. Sauce will thicken as it cooks.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'119674726A94934F934AB00E48E1ED29', X'B63E0A2177399447A16D3573A17A997E', X'B63E0A2177399447A16D3573A17A997E', 3, NULL, NULL, 2, NULL, 0, 'Continue to simmer about an hour on low.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1D35230F177E5F418C7CC701B88023E8', X'B63E0A2177399447A16D3573A17A997E', X'B63E0A2177399447A16D3573A17A997E', 4, NULL, NULL, 2, NULL, 0, 'Serve over cooked white rice, along with a salad, and pita bread.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CF56AB5267F6594B9B5953E9CAC45D98', X'D9616A8E8036F8478F74731614F12619', X'D9616A8E8036F8478F74731614F12619', 0, NULL, NULL, 2, NULL, 0, 'Prepare falafel, but do not fry yet.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E222B7E12B8526439B7945E4BE14080E', X'D9616A8E8036F8478F74731614F12619', X'D9616A8E8036F8478F74731614F12619', 1, NULL, NULL, 2, NULL, 0, 'Chop and rinse vegetables. Toss together. Allow it to dry on a paper towel for a few minutes, while you fry your falafel.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'232D089C7A03154A89CF48A338939C59', X'D9616A8E8036F8478F74731614F12619', X'D9616A8E8036F8478F74731614F12619', 2, NULL, NULL, 2, NULL, 0, 'Fry falafel and allow to drain on paper towel.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7F7F5F4B7EE3264C9AFE87B78CE58E80', X'D9616A8E8036F8478F74731614F12619', X'D9616A8E8036F8478F74731614F12619', 3, NULL, NULL, 2, NULL, 0, 'Lay a bed of lettuce/veggies on each serving plate.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'024B1D80E7F47B409152BBCA1F247B58', X'D9616A8E8036F8478F74731614F12619', X'D9616A8E8036F8478F74731614F12619', 4, NULL, NULL, 2, NULL, 0, 'Add the desired amount of falafel rounds on top of lettuce.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C50ADF6F35E6ED44BD8F96BC23486B91', X'D9616A8E8036F8478F74731614F12619', X'D9616A8E8036F8478F74731614F12619', 5, NULL, NULL, 2, NULL, 0, 'Sprinkle with tahini and falafel hot sauce.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'463BB6A86584AB4DB0317250A59FA3C1', X'44EC2705DBCD974CACF4F754013143EC', X'44EC2705DBCD974CACF4F754013143EC', 0, NULL, NULL, 2, NULL, 0, 'Turn on the broiler. Soak the bread in milk until all milk is absorbed, then mash it into a paste');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F52640693A968D4BAC6C8E32048A5035', X'44EC2705DBCD974CACF4F754013143EC', X'44EC2705DBCD974CACF4F754013143EC', 1, NULL, NULL, 2, NULL, 0, 'Process the parsley in a food processor and add to a large bowl with ground lamb and set aside.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B565DD5303C99147A1D58E4566FED4E5', X'44EC2705DBCD974CACF4F754013143EC', X'44EC2705DBCD974CACF4F754013143EC', 2, NULL, NULL, 2, NULL, 0, 'Process onion and garlic until very finely chopped. Add to bowl with bread and lamb mixture.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F0ABDCE6835A5F488D4E8F525E0C9A63', X'44EC2705DBCD974CACF4F754013143EC', X'44EC2705DBCD974CACF4F754013143EC', 3, NULL, NULL, 2, NULL, 0, 'Add the salt, cumin, sumac, cayenne pepper, allspice, pepper and mace and mix gently until the ingredients are well-combined.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8A5670BCC1C6B74CA7B78DC640BFCD5F', X'44EC2705DBCD974CACF4F754013143EC', X'44EC2705DBCD974CACF4F754013143EC', 4, NULL, NULL, 2, NULL, 0, 'Put the mixture into a casserole dish and place under the broiler until browned, about 15 minutes.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D57373169DF6734EA7F42B75C324E6ED', X'44EC2705DBCD974CACF4F754013143EC', X'44EC2705DBCD974CACF4F754013143EC', 5, NULL, NULL, 2, NULL, 0, 'Remove from oven and reduce heat to 375 F.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'20CA39441FC9EB4293A5BF7B960D6122', X'44EC2705DBCD974CACF4F754013143EC', X'44EC2705DBCD974CACF4F754013143EC', 6, NULL, NULL, 2, NULL, 0, 'Slice the potato and tomato into pieces about 1/4-inch thick and add to the meat mixture, first a layer of potatoes and then of tomatoes.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0BCF046E84929141A4D0270BDCD59D30', X'44EC2705DBCD974CACF4F754013143EC', X'44EC2705DBCD974CACF4F754013143EC', 7, NULL, NULL, 2, NULL, 0, 'Cover the casserole with aluminum foil or a lid and cook on the middle rack of the oven for 40 minutes.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E2A61E9C94304C4F9896AA52A06DAD78', X'5CD486AA2865D0469ACDCA17775DB06D', X'5CD486AA2865D0469ACDCA17775DB06D', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 350 F. Lightly grease a loaf pan.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'893AA6BF531155408895A93E53CCCFC1', X'5CD486AA2865D0469ACDCA17775DB06D', X'5CD486AA2865D0469ACDCA17775DB06D', 1, NULL, NULL, 2, NULL, 0, 'In a large bowl, combine lamb, beef, eggs, and breadcrumbs. Mix well. (Hands work best.)');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6B847D6FB9EDA244BED323F38D6E35B3', X'5CD486AA2865D0469ACDCA17775DB06D', X'5CD486AA2865D0469ACDCA17775DB06D', 2, NULL, NULL, 2, NULL, 0, 'Add onion, green pepper, garlic, salt, pepper, cumin, basil, and cinnamon. Mix well, make sure spices are thoroughly spread throughout the mixture.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5B85C96F5E6C024D83989015CCC28072', X'5CD486AA2865D0469ACDCA17775DB06D', X'5CD486AA2865D0469ACDCA17775DB06D', 3, NULL, NULL, 2, NULL, 0, 'Transfer mixture into prepared loaf pan. Bake for 1 1/2 hours or until cooked through.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'00C42847A8F42A44BA39D3B4C06566FC', X'5CD486AA2865D0469ACDCA17775DB06D', X'5CD486AA2865D0469ACDCA17775DB06D', 4, NULL, NULL, 2, NULL, 0, 'Remove from oven and let stand approximately 15 minutes. Sprinkle with fresh, chopped parsley.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3F07FBAE49B4CB4BA600C54787BC955D', X'80153184C7721A40874C59EAA3FF5F5C', X'80153184C7721A40874C59EAA3FF5F5C', 0, NULL, NULL, 2, NULL, 0, 'Place the beef, chopped onion, minced garlic, salt, pepper and ground cumin, if using, into a 3-quart pot or Dutch oven. Add enough beef stock or water to cover the beef, bring to a boil, cover and reduce the heat to low. Simmer for about 30 to 45 minutes or until the meat is fork tender.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'72C44C5449803B479904580599555454', X'80153184C7721A40874C59EAA3FF5F5C', X'80153184C7721A40874C59EAA3FF5F5C', 1, NULL, NULL, 2, NULL, 0, 'When the meat is tender, add in the frozen spinach. No need to drain it. Once the spinach loosens up in the stew, add the crushed tomatoes or tomato sauce and mix well. Continue to simmer for an additional hour on low heat.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'21F53621D5B18D438EFFC3FF03964FAD', X'80153184C7721A40874C59EAA3FF5F5C', X'80153184C7721A40874C59EAA3FF5F5C', 2, NULL, NULL, 2, NULL, 0, 'Serve the stew over cooked white rice, pasta or mashed potatoes alongside a simple salad.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B616633222298E4F8CF8364C7EEF4AD3', X'4342C784C800934789AA180BB1143879', X'4342C784C800934789AA180BB1143879', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'47C5513B7B7A524A9E98BD9D579B4B8E', X'4342C784C800934789AA180BB1143879', X'4342C784C800934789AA180BB1143879', 1, NULL, NULL, 2, NULL, 0, 'In a large saucepan, brown meat with olive oil. Add onions and garlic.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4E8B4B00AF127A45A694FE66E8B69830', X'4342C784C800934789AA180BB1143879', X'4342C784C800934789AA180BB1143879', 2, NULL, NULL, 2, NULL, 0, 'Add crushed tomatoes, stirring well with meat, garlic and onion. Add cumin, coriander, salt and pepper, and allspice. Add water and tomato puree. Stir and combine well.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'258BBAA3CD54CD438B449959ACFD6491', X'4342C784C800934789AA180BB1143879', X'4342C784C800934789AA180BB1143879', 3, NULL, NULL, 2, NULL, 0, 'Add okra and bring to a boil.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3834EDD86CDF694B92CF409D0D24A052', X'4342C784C800934789AA180BB1143879', X'4342C784C800934789AA180BB1143879', 4, NULL, NULL, 2, NULL, 0, 'Reduce heat to low and simmer for 2 hours, or until meat is tender and done.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C53B063061E02649B0EBD89DCAB8C713', X'4342C784C800934789AA180BB1143879', X'4342C784C800934789AA180BB1143879', 5, NULL, NULL, 2, NULL, 0, 'The sauce should thicken as it cooks. If it does not, add 1/2 cup all-purpose flour.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'47F4EA4230977F4D9E374B2C921BB50B', X'4342C784C800934789AA180BB1143879', X'4342C784C800934789AA180BB1143879', 6, NULL, NULL, 2, NULL, 0, 'Serve bamia with white rice and a salad. Enjoy!');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B54EEAA3AFC52742BB28E08B0D78E021', X'08F8219AC1EF6F43A13C482B7663CCB7', X'08F8219AC1EF6F43A13C482B7663CCB7', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'17BD96E44E94384DB66F2E1A941443B4', X'08F8219AC1EF6F43A13C482B7663CCB7', X'08F8219AC1EF6F43A13C482B7663CCB7', 1, NULL, NULL, 2, NULL, 0, 'Combine the ground black pepper, ground coriander, ground cumin, lemon juice and oil in a medium bowl.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D4F2CFAFE557B14A9821FDF040A71CC8', X'08F8219AC1EF6F43A13C482B7663CCB7', X'08F8219AC1EF6F43A13C482B7663CCB7', 2, NULL, NULL, 2, NULL, 0, 'Add the lamb chops, cover and marinate in the refrigerator for at least 1 hour, but up to 24 hours.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'670969E1271AE44391BEDD4841AE4F15', X'08F8219AC1EF6F43A13C482B7663CCB7', X'08F8219AC1EF6F43A13C482B7663CCB7', 3, NULL, NULL, 2, NULL, 0, 'Cook the lamb chops for 5 to 7 minutes on each side on the grill or under the broiler in the oven. Season with salt.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'31F1A31DEDDAD949BB7FC7ECDFF7C4F0', X'08F8219AC1EF6F43A13C482B7663CCB7', X'08F8219AC1EF6F43A13C482B7663CCB7', 4, NULL, NULL, 2, NULL, 0, 'Serve with white rice, salad, and pita bread. You can also serve with fried potatoes for an informal meal or cookout.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C9A6ACC709B9E94C97E8F388420B03C9', X'08F8219AC1EF6F43A13C482B7663CCB7', X'08F8219AC1EF6F43A13C482B7663CCB7', 5, NULL, NULL, 2, NULL, 0, 'Enjoy!');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3CB9C57159305248B771949D1336C8A7', X'70FE9EA485B8AD41AB70C3E1FC0D55E0', X'70FE9EA485B8AD41AB70C3E1FC0D55E0', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BA45D2373E3EF64694BA0D2872F6EFB3', X'70FE9EA485B8AD41AB70C3E1FC0D55E0', X'70FE9EA485B8AD41AB70C3E1FC0D55E0', 1, NULL, NULL, 2, NULL, 0, 'In a saucepan, bring water to a boil.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B60F29C9C87C484AA7C5E076D111395F', X'70FE9EA485B8AD41AB70C3E1FC0D55E0', X'70FE9EA485B8AD41AB70C3E1FC0D55E0', 2, NULL, NULL, 2, NULL, 0, 'Add salt and stir. Add couscous and cover. Remove from heat and allow to sit for about 5 to 6 minutes.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C4BAE426011C66488DF041F680B2D705', X'70FE9EA485B8AD41AB70C3E1FC0D55E0', X'70FE9EA485B8AD41AB70C3E1FC0D55E0', 3, NULL, NULL, 2, NULL, 0, 'Allow the couscous to absorb the water and then fluff with a fork. Couscous should be light and fluffy, not gummy.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1CA31FFACC595F4A8DF506AF1D14F1B1', X'70FE9EA485B8AD41AB70C3E1FC0D55E0', X'70FE9EA485B8AD41AB70C3E1FC0D55E0', 4, NULL, NULL, 2, NULL, 0, 'Serve as the base for a stew or as a side dish. Enjoy!');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'596191C2781C9B44A6FD6CDD1ECB360E', X'9C59B94CB735E64EBC349A2876401E91', X'9C59B94CB735E64EBC349A2876401E91', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients. Preheat the oven to 350 F.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A860C7BFCDE41C4FBE762696539C4C4E', X'9C59B94CB735E64EBC349A2876401E91', X'9C59B94CB735E64EBC349A2876401E91', 1, NULL, NULL, 2, NULL, 0, 'Oil a 9 x 13 2-inch baking dish or a similarly-sized (3 to 4-quart) casserole dish.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'364F647327A25A4E82A2D04174611C60', X'9C59B94CB735E64EBC349A2876401E91', X'9C59B94CB735E64EBC349A2876401E91', 2, NULL, NULL, 2, NULL, 0, 'Line 2 large, rimmed baking sheets with parchment paper. Divide the challah cubes between the baking sheets.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'88520B9C84833E43A5D52191535A2911', X'9C59B94CB735E64EBC349A2876401E91', X'9C59B94CB735E64EBC349A2876401E91', 3, NULL, NULL, 2, NULL, 0, 'Drizzle the bread with a little olive oil, toss with clean hands, and spread evenly into a single layer. Bake, turning once or twice while cooking, until dry and toasted, about 15 to 20 minutes. Set aside to cool, but leave the oven on.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'45CE69EA33D0824D9294285ADBFCE663', X'9C59B94CB735E64EBC349A2876401E91', X'9C59B94CB735E64EBC349A2876401E91', 4, NULL, NULL, 2, NULL, 0, 'In a large skillet or pan set over medium-high heat, warm the oil. Add the onion, garlic, fennel, and celery, and saute until the onion is soft and translucent, about 5 minutes.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D36EF4D11870B54F8DE105F9681E59CB', X'9C59B94CB735E64EBC349A2876401E91', X'9C59B94CB735E64EBC349A2876401E91', 5, NULL, NULL, 2, NULL, 0, 'Add apple and continue to saute until warmed through, about 5 minutes more. Stir in the sage and lemon zest and saute until fragrant, about 1 minute. Remove from the heat.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'ED39AC07D4B08548BB352B7920EDA90F', X'9C59B94CB735E64EBC349A2876401E91', X'9C59B94CB735E64EBC349A2876401E91', 6, NULL, NULL, 2, NULL, 0, 'Place the toasted challah cubes in a large bowl. Add the sauteed vegetables and toss to coat.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9A8C9FD03A5D99468AD46A1F2EEA9FD1', X'9C59B94CB735E64EBC349A2876401E91', X'9C59B94CB735E64EBC349A2876401E91', 7, NULL, NULL, 2, NULL, 0, 'Pour the vegetable broth evenly over the mixture and stir well. Season to taste with salt and pepper, if desired.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'94EB529998648C4B85E2F6733F15EF97', X'9C59B94CB735E64EBC349A2876401E91', X'9C59B94CB735E64EBC349A2876401E91', 8, NULL, NULL, 2, NULL, 0, 'Pour the stuffing mixture into the prepared baking dish and smooth the top. Drizzle with a little olive oil.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'77ECC69FD9675D4DB28EBDBF5DB8AB5C', X'9C59B94CB735E64EBC349A2876401E91', X'9C59B94CB735E64EBC349A2876401E91', 9, NULL, NULL, 2, NULL, 0, 'Cover with foil and bake in the preheated oven for 30 minutes.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7D66AE646CDDA3469EC6F002E3601831', X'9C59B94CB735E64EBC349A2876401E91', X'9C59B94CB735E64EBC349A2876401E91', 10, NULL, NULL, 2, NULL, 0, 'Carefully remove the foil and bake for 10 to 20 minutes more, or until the top is golden, and the stuffing reaches your preferred consistency.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A298AEB5B8D3AD438049CAF14EF304DB', X'9C59B94CB735E64EBC349A2876401E91', X'9C59B94CB735E64EBC349A2876401E91', 11, NULL, NULL, 2, NULL, 0, 'Serve and enjoy!');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FCCBB01FD611274A9E68D40BCA550602', X'BA8DC1A8E458E846B95FDAA3577044F1', X'BA8DC1A8E458E846B95FDAA3577044F1', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2A946B7ADF08664DB51F0AB2A6F020C7', X'BA8DC1A8E458E846B95FDAA3577044F1', X'BA8DC1A8E458E846B95FDAA3577044F1', 1, NULL, NULL, 2, NULL, 0, 'Lightly grease a tube pan or 9 x 9 x 2-inch baking pan with oil or non-stick cooking spray. Preheat the oven to 350 F (180 C).');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'25CFB16D43C58C41BEC79E4EBAAE5F69', X'BA8DC1A8E458E846B95FDAA3577044F1', X'BA8DC1A8E458E846B95FDAA3577044F1', 2, NULL, NULL, 2, NULL, 0, 'In a large pot, bring water to a boil. Add the noodles, and cook according to the package directions, taking care not to overcook.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1544679B3336204184BC0C783638089C', X'BA8DC1A8E458E846B95FDAA3577044F1', X'BA8DC1A8E458E846B95FDAA3577044F1', 3, NULL, NULL, 2, NULL, 0, 'Drain, return to the pot, and stir in 1 tablespoon of oil to prevent the noodles from sticking. Then, set it aside.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B183A01D2CE2434BA5C9CE14FDA69BCE', X'BA8DC1A8E458E846B95FDAA3577044F1', X'BA8DC1A8E458E846B95FDAA3577044F1', 4, NULL, NULL, 2, NULL, 0, 'Place the oil and sugar in a light-colored, heavy-bottomed saucepan, which will allow you to see the color of the melting sugar. Adjust the stove to medium heat, and stir continuously with a wooden spoon. Slowly the sugar will start to turn yellowish-brown, combining with the oil. If the sugar turns dark brown too quickly, turn the heat down.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A07D4C261376824190A7A67E0AD45BB5', X'BA8DC1A8E458E846B95FDAA3577044F1', X'BA8DC1A8E458E846B95FDAA3577044F1', 5, NULL, NULL, 2, NULL, 0, 'Stir until a bubbly, liquid caramel has formed. Then immediately pour caramel over cooked noodles with the salt and pepper, and continue stirring until thoroughly blended.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8BC270D085C8D54EAFDB9B97D2A2D16F', X'BA8DC1A8E458E846B95FDAA3577044F1', X'BA8DC1A8E458E846B95FDAA3577044F1', 6, NULL, NULL, 2, NULL, 0, 'Let cool for a few minutes. Then add eggs one at a time, mixing after each addition.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0192068AEBE0F54CB71614DC8652B2BE', X'BA8DC1A8E458E846B95FDAA3577044F1', X'BA8DC1A8E458E846B95FDAA3577044F1', 7, NULL, NULL, 2, NULL, 0, 'Pour into prepared pan. Bake for 1 to 1 1/2 hours in the preheated oven until nicely browned.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2E3250F8F266394EB1367D7DAFE53482', X'BA8DC1A8E458E846B95FDAA3577044F1', X'BA8DC1A8E458E846B95FDAA3577044F1', 8, NULL, NULL, 2, NULL, 0, 'When done, remove immediately from pan. This kugel can be served hot, warm, or cold.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DAC78E9A19D0B34483D5D1769D1665A4', X'90A44AEABFD22D4A93067C605A8D85B4', X'90A44AEABFD22D4A93067C605A8D85B4', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B2F2ED21C6C7F84B837DADC521C1E735', X'90A44AEABFD22D4A93067C605A8D85B4', X'90A44AEABFD22D4A93067C605A8D85B4', 1, NULL, NULL, 2, NULL, 0, 'Preheat oven to 375 F. Spray a cookie sheet or shallow baking dish (9x13) with your favorite cooking spray. For easy cleanup, you can line your baking sheet or pan with aluminum foil and then spray with cooking spray to prevent sticking.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3B332545A6BF104DBCB2D373D2439CDF', X'90A44AEABFD22D4A93067C605A8D85B4', X'90A44AEABFD22D4A93067C605A8D85B4', 2, NULL, NULL, 2, NULL, 0, 'Next, begin to slice and cut vegetables. There is no rule to follow here, but we prefer to slice the carrots, zucchini, squash, onions, and peppers. Potatoes are delicious in cubes or for small white potatoes, in quarters.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7CC8C5DB1DC4CD4289AAB3ABB9A59FBD', X'90A44AEABFD22D4A93067C605A8D85B4', X'90A44AEABFD22D4A93067C605A8D85B4', 3, NULL, NULL, 2, NULL, 0, 'After chopping and slicing, place vegetables in a single layer on a baking sheet or in ??a dish. Drizzle with olive oil. Add the desired amount of salt and pepper. Sprinkle with garlic powder, thyme, and cumin.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'95021DAD3AF2764D9C7CE7FF9DFCDBD2', X'90A44AEABFD22D4A93067C605A8D85B4', X'90A44AEABFD22D4A93067C605A8D85B4', 4, NULL, NULL, 2, NULL, 0, 'Place in heated over and cook for 45 minutes, or until vegetables can be pierced with a fork. Serve immediately.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EDB285E0ED387745A1EF45CF898F5943', X'90A44AEABFD22D4A93067C605A8D85B4', X'90A44AEABFD22D4A93067C605A8D85B4', 5, NULL, NULL, 2, NULL, 0, 'Enjoy!');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D947810602135544AC742F5EB85B5F1E', X'F63732AFD4E6654C854521F16634BCDE', X'F63732AFD4E6654C854521F16634BCDE', 0, NULL, NULL, 2, NULL, 0, 'Rub the chicken breasts with the olive oil and grill for 5 to 6 minutes on each side until cooked all the way through. You can also do this on a grill pan on the stove top.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DED0D50BD1511B48A966DA312F1E06A7', X'F63732AFD4E6654C854521F16634BCDE', X'F63732AFD4E6654C854521F16634BCDE', 1, NULL, NULL, 2, NULL, 0, 'While the chicken is grilling, make the sauce by adding the olive oil and diced onion to a saute pan. Saute on medium heat for about 10 minutes or until the onion is translucent. Stir in the tomato sauce, ground cumin, ground coriander, garlic powder, cinnamon, turmeric, cayenne, salt, and black pepper.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5F38DB39EB3DA7428FC2D2F8794D6A13', X'F63732AFD4E6654C854521F16634BCDE', X'F63732AFD4E6654C854521F16634BCDE', 2, NULL, NULL, 2, NULL, 0, 'Thinly slice or shred the cooked chicken breasts and add to the sauce. Simmer on low heat for 10 minutes.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BA64BE207307954F9498C1582040674B', X'F63732AFD4E6654C854521F16634BCDE', X'F63732AFD4E6654C854521F16634BCDE', 3, NULL, NULL, 2, NULL, 0, 'Serve on small slider buns with sliced red onions or coleslaw.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2C93EDFDEF52AE4EA526A4356B26A35E', X'53BB4D5CFF7C2949A6C3785341028885', X'53BB4D5CFF7C2949A6C3785341028885', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2DD7EE72D485F2439D6CE6B84B2FD1E4', X'53BB4D5CFF7C2949A6C3785341028885', X'53BB4D5CFF7C2949A6C3785341028885', 1, NULL, NULL, 2, NULL, 0, 'Rinse the rice until the water runs clear. Drain in a colander and set aside.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1DE0F437D0292247BAED7DFA6BCB5659', X'53BB4D5CFF7C2949A6C3785341028885', X'53BB4D5CFF7C2949A6C3785341028885', 2, NULL, NULL, 2, NULL, 0, 'In a medium saucepan, bring the 2 cups of water to a boil. Add the salt, stir, and then add the rinsed and drained rice. Stir with a fork.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C48419D676246C4BB168CED20FF6B79F', X'53BB4D5CFF7C2949A6C3785341028885', X'53BB4D5CFF7C2949A6C3785341028885', 3, NULL, NULL, 2, NULL, 0, 'Reduce the heat, cover the rice, and let it simmer for 20 minutes. Check it after 15 minutes to see if all the water has evaporated. If it has, the rice is ready. If not, replace the lid and let the rice simmer an additional 5 minutes.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'68C4658C16172641B59F692D95CA98A9', X'53BB4D5CFF7C2949A6C3785341028885', X'53BB4D5CFF7C2949A6C3785341028885', 4, NULL, NULL, 2, NULL, 0, 'Remove from the heat, fluff with a fork, and transfer into a serving dish.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BEA3A0F3F7396C4D83F78C376B93EE11', X'6FB8E6B6ED9D52469ADE69FB925A4DDC', X'6FB8E6B6ED9D52469ADE69FB925A4DDC', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2A04B85075822D40B574D690F15BEC7B', X'6FB8E6B6ED9D52469ADE69FB925A4DDC', X'6FB8E6B6ED9D52469ADE69FB925A4DDC', 1, NULL, NULL, 2, NULL, 0, 'Toast the pita bread in a pre-heated 400 F oven for about 5 minutes.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3258306BD471944A9D5AE2D186874D76', X'6FB8E6B6ED9D52469ADE69FB925A4DDC', X'6FB8E6B6ED9D52469ADE69FB925A4DDC', 2, NULL, NULL, 2, NULL, 0, 'Then tear into 1-inch pieces.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'354C7F6D92F347439685BE35B3375F3E', X'8E71B8EA083EC34BBAB3964B2C4F6ADC', X'8E71B8EA083EC34BBAB3964B2C4F6ADC', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4B30A2DC2F14F24792036CA6732D2D5D', X'8E71B8EA083EC34BBAB3964B2C4F6ADC', X'8E71B8EA083EC34BBAB3964B2C4F6ADC', 1, NULL, NULL, 2, NULL, 0, 'Place the couscous in a dry saut pan over medium heat and cook for 3 to 4 minutes, shaking the pan occasionally, until the couscous is lightly toasted.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'433BFBD8EEE49940BE09ED501F606217', X'8E71B8EA083EC34BBAB3964B2C4F6ADC', X'8E71B8EA083EC34BBAB3964B2C4F6ADC', 2, NULL, NULL, 2, NULL, 0, 'Place the couscous in a saucepan, add 1 1/2 cups water and bring to a boil over high heat. Reduce the heat to low and cover the pan. Cook for 8 to 9 minutes or until all the water has been absorbed and spoon it into a bowl.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'25072F94DB47EB45A21964C5BB057124', X'8E71B8EA083EC34BBAB3964B2C4F6ADC', X'8E71B8EA083EC34BBAB3964B2C4F6ADC', 3, NULL, NULL, 2, NULL, 0, 'While the couscous is cooking, toast the almonds in the saut pan over medium heat for 4 to 5 minutes or until they are lightly browned. Add to the couscous.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BC07C8A332A55045A41E4BA030537A45', X'8E71B8EA083EC34BBAB3964B2C4F6ADC', X'8E71B8EA083EC34BBAB3964B2C4F6ADC', 4, NULL, NULL, 2, NULL, 0, 'Stir in the cranberries and scallions, distributing them evenly.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F080D92BBE67A34C839BA202B90F557C', X'8E71B8EA083EC34BBAB3964B2C4F6ADC', X'8E71B8EA083EC34BBAB3964B2C4F6ADC', 5, NULL, NULL, 2, NULL, 0, 'Mix the olive oil, vinegar, and orange peel in a small bowl and pour over the couscous.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2E72F313491EF042B5E2E8156C916D3E', X'8E71B8EA083EC34BBAB3964B2C4F6ADC', X'8E71B8EA083EC34BBAB3964B2C4F6ADC', 6, NULL, NULL, 2, NULL, 0, 'Toss the ingredients and season to taste with salt and pepper.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A167E7A92E94FA488BED35958F2E3B23', X'8E71B8EA083EC34BBAB3964B2C4F6ADC', X'8E71B8EA083EC34BBAB3964B2C4F6ADC', 7, NULL, NULL, 2, NULL, 0, 'Enjoy!');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FDF631C530305844B86076DA4146FFE4', X'2D1ACA8A1B3C144DB70B83D2EC745631', X'2D1ACA8A1B3C144DB70B83D2EC745631', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DE480A0899945E46B5EB7C0E614260EE', X'2D1ACA8A1B3C144DB70B83D2EC745631', X'2D1ACA8A1B3C144DB70B83D2EC745631', 1, NULL, NULL, 2, NULL, 0, 'Soak the bulghur wheat in cold water for 1 1/2 to 2 hours until soft.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'94664817D59C4F4D9B96F51298A1A2E4', X'2D1ACA8A1B3C144DB70B83D2EC745631', X'2D1ACA8A1B3C144DB70B83D2EC745631', 2, NULL, NULL, 2, NULL, 0, 'While the bulghur wheat is soaking, prepare all the other ingredients. Chop the fresh parsley and the fresh mint, being sure to discard the stems.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'799F2850ED305C48B4D52CA8817BC677', X'2D1ACA8A1B3C144DB70B83D2EC745631', X'2D1ACA8A1B3C144DB70B83D2EC745631', 3, NULL, NULL, 2, NULL, 0, 'Finely chop the medium onion and dice the tomatoes.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B6D277AF5D162D4A95C7A2DF4B5BC009', X'2D1ACA8A1B3C144DB70B83D2EC745631', X'2D1ACA8A1B3C144DB70B83D2EC745631', 4, NULL, NULL, 2, NULL, 0, 'After soaking, squeeze out excess water from the bulghur wheat using your hands and/or a paper towel.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F0B646380673E045B2650DE82F72A002', X'2D1ACA8A1B3C144DB70B83D2EC745631', X'2D1ACA8A1B3C144DB70B83D2EC745631', 5, NULL, NULL, 2, NULL, 0, 'Combine all the ingredients in a bowl, except for the salt, pepper, lemon juice, and olive oil.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2B933122C1AA3E4FAB13CA4FCE3196BE', X'2D1ACA8A1B3C144DB70B83D2EC745631', X'2D1ACA8A1B3C144DB70B83D2EC745631', 6, NULL, NULL, 2, NULL, 0, 'Line the serving bowl with grape leaves or romaine lettuce and add the mixed salad.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'03C4BF3F3D9DCA4CAD02C1B49C6E67B8', X'2D1ACA8A1B3C144DB70B83D2EC745631', X'2D1ACA8A1B3C144DB70B83D2EC745631', 7, NULL, NULL, 2, NULL, 0, 'Sprinkle the olive oil, lemon juice, salt, and pepper on top.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7082856E2ED3B0428C1A0E531B0DF206', X'2D1ACA8A1B3C144DB70B83D2EC745631', X'2D1ACA8A1B3C144DB70B83D2EC745631', 8, NULL, NULL, 2, NULL, 0, 'You can serve this immediately or, ideally, chill in the refrigerator for up to 2 hours before serving. This will give the flavors time to infuse.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'534A63AC47E11640812EEA77E4D793D5', X'D65331FB7368D6468FAC5665BFDBBFBC', X'D65331FB7368D6468FAC5665BFDBBFBC', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'844703F372833242B0290FB1669C24BA', X'D65331FB7368D6468FAC5665BFDBBFBC', X'D65331FB7368D6468FAC5665BFDBBFBC', 1, NULL, NULL, 2, NULL, 0, 'Coat a large bowl with oil. Set aside.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9AEF01F77135C4478BB7351FBE4D5780', X'D65331FB7368D6468FAC5665BFDBBFBC', X'D65331FB7368D6468FAC5665BFDBBFBC', 2, NULL, NULL, 2, NULL, 0, 'In a measuring cup, combine yeast, water, and sugar. Mix until yeast is dissolved. Set aside.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EE492171623A9945B76D6AA40BF2A46B', X'D65331FB7368D6468FAC5665BFDBBFBC', X'D65331FB7368D6468FAC5665BFDBBFBC', 3, NULL, NULL, 2, NULL, 0, 'In a large mixing bowl, combine flour and salt.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'11E8399914C3944380764CD73710A41E', X'D65331FB7368D6468FAC5665BFDBBFBC', X'D65331FB7368D6468FAC5665BFDBBFBC', 4, NULL, NULL, 2, NULL, 0, 'Add yeast-water-sugar mixture and form a dough.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7A6A6DE9B8CBBA418F8D8FC1AECCFBB9', X'D65331FB7368D6468FAC5665BFDBBFBC', X'D65331FB7368D6468FAC5665BFDBBFBC', 5, NULL, NULL, 2, NULL, 0, 'Knead dough for 10 to 15 minutes by hand or 5 to 8 minutes using a dough hook in a stand mixer.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F3F41592DCD33D4093152102E88B3338', X'D65331FB7368D6468FAC5665BFDBBFBC', X'D65331FB7368D6468FAC5665BFDBBFBC', 6, NULL, NULL, 2, NULL, 0, 'Once the dough is kneaded, place the ball of dough in the oiled bowl. Roll the dough around the bowl to coat it with oil.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'89C0D57C028B52469289F4F566119097', X'D65331FB7368D6468FAC5665BFDBBFBC', X'D65331FB7368D6468FAC5665BFDBBFBC', 7, NULL, NULL, 2, NULL, 0, 'Cover and let rise for 1 to 1 1/2 hours, or until the dough doubles in size.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DEE81E777DAA1E4ABEB72A1E63260F63', X'D65331FB7368D6468FAC5665BFDBBFBC', X'D65331FB7368D6468FAC5665BFDBBFBC', 8, NULL, NULL, 2, NULL, 0, 'Once the dough has doubled, punch it down to release air.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D019BE44ED05D54BB8F8B2798268D5A4', X'D65331FB7368D6468FAC5665BFDBBFBC', X'D65331FB7368D6468FAC5665BFDBBFBC', 9, NULL, NULL, 2, NULL, 0, 'Continue to knead for about 5 minutes.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8EC9AE180F3D174C9D8009E3DD980B69', X'D65331FB7368D6468FAC5665BFDBBFBC', X'D65331FB7368D6468FAC5665BFDBBFBC', 10, NULL, NULL, 2, NULL, 0, 'Divide dough into 8 separate balls of dough.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'129522D822BDF144886EDD7C41719DCD', X'D65331FB7368D6468FAC5665BFDBBFBC', X'D65331FB7368D6468FAC5665BFDBBFBC', 11, NULL, NULL, 2, NULL, 0, 'Cover and allow to rise for 30 minutes.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F08BD870ABF171488989180BBF667CA5', X'D65331FB7368D6468FAC5665BFDBBFBC', X'D65331FB7368D6468FAC5665BFDBBFBC', 12, NULL, NULL, 2, NULL, 0, 'Heat oven to 400 F.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B950CE74EAE2F04B8878FA7EAF7A53B6', X'D65331FB7368D6468FAC5665BFDBBFBC', X'D65331FB7368D6468FAC5665BFDBBFBC', 13, NULL, NULL, 2, NULL, 0, 'Once the balls of dough have risen, roll them out into thin rectangles, about 12-by-10-inches for large lavash or 8-by-6-inches for small flatbreads. They should be as thin as pizza dough.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4DBF149BF3026C4E8157FA0E4A550CEE', X'D65331FB7368D6468FAC5665BFDBBFBC', X'D65331FB7368D6468FAC5665BFDBBFBC', 14, NULL, NULL, 2, NULL, 0, 'Transfer to parchment-lined baking sheets. Puncture the entire surface of the rectangles with the tines of a fork.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6788BB49755BF2459E4A57C76D416B99', X'D65331FB7368D6468FAC5665BFDBBFBC', X'D65331FB7368D6468FAC5665BFDBBFBC', 15, NULL, NULL, 2, NULL, 0, 'Brush dough with water and sprinkle with sesame seeds and poppy seeds if desired.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B3F0DB44A42EC54AB793579751C11C05', X'D65331FB7368D6468FAC5665BFDBBFBC', X'D65331FB7368D6468FAC5665BFDBBFBC', 16, NULL, NULL, 2, NULL, 0, 'Bake for 20 to 25 minutes or until golden brown.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DCE46D442F5D49418B6A4821A440B076', X'D65331FB7368D6468FAC5665BFDBBFBC', X'D65331FB7368D6468FAC5665BFDBBFBC', 17, NULL, NULL, 2, NULL, 0, 'Remove from oven and eat while still warm or when cool.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'75755F402D10B6428AE311BEF3B2FDCB', X'D65331FB7368D6468FAC5665BFDBBFBC', X'D65331FB7368D6468FAC5665BFDBBFBC', 18, NULL, NULL, 2, NULL, 0, 'Enjoy!');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'787F241E0B51C543B930358190545587', X'58D3B7345FE56941B101F52DD9577369', X'58D3B7345FE56941B101F52DD9577369', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E3985D9A03960A4E9FDF3274C3D08B33', X'58D3B7345FE56941B101F52DD9577369', X'58D3B7345FE56941B101F52DD9577369', 1, NULL, NULL, 2, NULL, 0, 'First, heat the chopped onions in olive oil, butter or margarine stovetop in a large skillet. Allow the onions to cook, stirring occasionally, for about 5 to 7 minutes until they are softened.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EED3FC4D3BF73D479C940449CF42A5E0', X'58D3B7345FE56941B101F52DD9577369', X'58D3B7345FE56941B101F52DD9577369', 2, NULL, NULL, 2, NULL, 0, 'Next, add the Israeli couscous to the skillet, and toast the grains, stirring, for just a minute or two, until they are lightly browned.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E055111E4AF87F4FBE25A693D0F350D7', X'58D3B7345FE56941B101F52DD9577369', X'58D3B7345FE56941B101F52DD9577369', 3, NULL, NULL, 2, NULL, 0, 'Once your Israeli couscous is lightly toasted, add about half of the vegetable broth and bring to a simmer. Allow to cook for about two to three minutes, stirring occasionally, then add in the other half of the vegetable broth. Allow to cook, stirring, for an additional 2 to 3 minutes.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'294B1229D0AD014F9A054F82DF4C6BED', X'58D3B7345FE56941B101F52DD9577369', X'58D3B7345FE56941B101F52DD9577369', 4, NULL, NULL, 2, NULL, 0, 'Finally, add in the dry white wine, stirring to combine. Heat, stirring, for another 3 to 5 minutes, then add in the fresh spinach.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1E4512DA60D1704585A3A6B1EE155A1E', X'58D3B7345FE56941B101F52DD9577369', X'58D3B7345FE56941B101F52DD9577369', 5, NULL, NULL, 2, NULL, 0, 'Reduce the heat to medium-low, cover, and allow to heat until spinach has wilted about 2 minutes, then turn off the heat and stir in the Parmesan cheese and season generously with sea salt or kosher salt and a bit of black pepper, to taste.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F492712C194C8844B0F912D709C88C8F', X'58D3B7345FE56941B101F52DD9577369', X'58D3B7345FE56941B101F52DD9577369', 6, NULL, NULL, 2, NULL, 0, 'As soon as Parmesan cheese has melted, plate and serve immediately. Garnish individual servings with a bit of extra Parmesan cheese, if you''d like.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'776269A7C17DE54F8ECF53B56756F5C2', X'9F2D31847D8D074080AF3603275F6CEC', X'9F2D31847D8D074080AF3603275F6CEC', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'83DE24068FA07E4082CAD498AD3E7C0E', X'9F2D31847D8D074080AF3603275F6CEC', X'9F2D31847D8D074080AF3603275F6CEC', 1, NULL, NULL, 2, NULL, 0, 'In a small saucepan, combine the cocoa powder and cold water, and whisk together until smooth.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9D0F609D750D6445A2AB5710CB7C8739', X'9F2D31847D8D074080AF3603275F6CEC', X'9F2D31847D8D074080AF3603275F6CEC', 2, NULL, NULL, 2, NULL, 0, 'Without putting over heat, add the sugar and torn mint leaves to the cocoa and water mixture.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'29CC0337BD67EA40B66323A5773DF18C', X'9F2D31847D8D074080AF3603275F6CEC', X'9F2D31847D8D074080AF3603275F6CEC', 3, NULL, NULL, 2, NULL, 0, 'Now place the saucepan over medium heat.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7A95F49B3756F44AA4EA16C5FEAD25B4', X'9F2D31847D8D074080AF3603275F6CEC', X'9F2D31847D8D074080AF3603275F6CEC', 4, NULL, NULL, 2, NULL, 0, 'Bring the mixture to a boil, stirring constantly to melt the sugar. As soon as the syrup begins to boil, reduce the heat and simmer for 3 to 5 minutes. The mixture should begin to thicken in texture and have a glossy tint. Be careful as to not burn the mixture.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'62789B97513358419FD88E202987E4D7', X'9F2D31847D8D074080AF3603275F6CEC', X'9F2D31847D8D074080AF3603275F6CEC', 5, NULL, NULL, 2, NULL, 0, 'Once simmered to a thick consistency, remove from the heat and allow to cool.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5DBCA681CED8634BBB695FB7AAFA9293', X'9F2D31847D8D074080AF3603275F6CEC', X'9F2D31847D8D074080AF3603275F6CEC', 6, NULL, NULL, 2, NULL, 0, 'When the syrup has cooled to near room temperature it is ready to be strained.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'27BC3A29EAB1DB438DD2B0F53975DFDB', X'9F2D31847D8D074080AF3603275F6CEC', X'9F2D31847D8D074080AF3603275F6CEC', 7, NULL, NULL, 2, NULL, 0, 'Use a fine mesh sieve to strain the syrup into a clean glass jar, carefully to remove any ingredients that may have clumped during the heating and cooling process. (If you do not have a sieve or want the authentic taste of an inconsistent texture, simply skip this process.)');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'57800E61A8DD3843BD8785E45903DF92', X'9F2D31847D8D074080AF3603275F6CEC', X'9F2D31847D8D074080AF3603275F6CEC', 8, NULL, NULL, 2, NULL, 0, 'Lastly, cover the jar and store in the refrigerator for up to three weeks. If the sugar starts to crystallize it may be time to make a new batch. To make it last simply reheat before using.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'335796D4ED84604D8DD544928A017545', X'9F2D31847D8D074080AF3603275F6CEC', X'9F2D31847D8D074080AF3603275F6CEC', 9, NULL, NULL, 2, NULL, 0, 'When ready to use drizzle over your favorite desserts, or make your traditional hot chocolate with a twist, or simply add to your baked goods for a minty surprise.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A9AC2DB50D491D4DBCCA91F55EB8A204', X'9F2D31847D8D074080AF3603275F6CEC', X'9F2D31847D8D074080AF3603275F6CEC', 10, NULL, NULL, 2, NULL, 0, 'Enjoy!');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3449E961E5D3BB4C91B62ECA64DB6A75', X'E404B8A3FDEC744C95DAC1A36915E0A1', X'E404B8A3FDEC744C95DAC1A36915E0A1', 0, NULL, NULL, 2, NULL, 0, 'In a food processor, combine beans, garlic, lemon juice, tahini (if desired) and yogurt. Blend well. Add salt, and blend until smooth and creamy.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D71842F28312F04EB796D348337984D3', X'E404B8A3FDEC744C95DAC1A36915E0A1', X'E404B8A3FDEC744C95DAC1A36915E0A1', 1, NULL, NULL, 2, NULL, 0, 'To thin the hummus (if desired), add a little of the chickpea liquid about a teaspoon at a time. You can also mix in warm water or olive oil to loosen the spread even more.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'864C79CD3EEF3C41963F1DF9DA4F0F4B', X'FA5FF589B353CF4DB24782AEF015D5E8', X'FA5FF589B353CF4DB24782AEF015D5E8', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'325EF327684FA14BA88B5B5BBDB4EE7A', X'FA5FF589B353CF4DB24782AEF015D5E8', X'FA5FF589B353CF4DB24782AEF015D5E8', 1, NULL, NULL, 2, NULL, 0, 'Coarsely chop the pulp and put it into a large pot along with enough water to cover the fruit. Bring to a boil over high heat, then reduce the heat and simmer until the fruit pulp is losing its color and flavor to the liquid, about 15 minutes.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A97D267DCA89B840A84F20A1A180687C', X'FA5FF589B353CF4DB24782AEF015D5E8', X'FA5FF589B353CF4DB24782AEF015D5E8', 2, NULL, NULL, 2, NULL, 0, 'Strain the liquid through a jelly bag, several layers of cheesecloth in a colander, or a cloth produce bag.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DA499A6540F58E44AF35C4C9A9E8B1B9', X'FA5FF589B353CF4DB24782AEF015D5E8', X'FA5FF589B353CF4DB24782AEF015D5E8', 3, NULL, NULL, 2, NULL, 0, 'Measure the strained liquid. You should have about 4 cups. Return the prickly pear liquid to the pot (wash the pot first if there are any seeds sticking to it).');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DE8E9A1CCFF4C347AC16476F724B7206', X'FA5FF589B353CF4DB24782AEF015D5E8', X'FA5FF589B353CF4DB24782AEF015D5E8', 4, NULL, NULL, 2, NULL, 0, 'Add the sugar and lemon juice. Bring the mixture to a boil over medium-high heat, stirring to dissolve the sugar.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'07679418B916FB4088FC520F100AC59A', X'FA5FF589B353CF4DB24782AEF015D5E8', X'FA5FF589B353CF4DB24782AEF015D5E8', 5, NULL, NULL, 2, NULL, 0, 'A foamy scum usually forms on the surface of the prickly pear syrup while it is coming to a boil. Skim as much of this off as possible and discard it.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D242E9805960DF4695F9D180C093F740', X'FA5FF589B353CF4DB24782AEF015D5E8', X'FA5FF589B353CF4DB24782AEF015D5E8', 6, NULL, NULL, 2, NULL, 0, 'Pour the still-hot prickly pear syrup into clean pint or half-pint canning jars (it is not necessary to sterilize the jars for this recipe). Be sure to leave half an inch of headspace between the surface of the syrup and the rims of the jars. Wipe the rims of the jars clean with a damp paper towel or a clean dishcloth.?');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'33236F9A7D536448982E00B45F3AB046', X'FA5FF589B353CF4DB24782AEF015D5E8', X'FA5FF589B353CF4DB24782AEF015D5E8', 7, NULL, NULL, 2, NULL, 0, 'Screw on canning lids and process in a boiling water bath for 10 minutes (adjust the canning time if you live at a high altitude). Alternatively, skip the canning process and refrigerate your prickly pear syrup for up to 1 month.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0E58CC79D6B776478B3483C8EF8C05C5', X'E244DF69F2F08844A7A8E629DD279C7E', X'E244DF69F2F08844A7A8E629DD279C7E', 0, NULL, NULL, 2, NULL, 0, 'Place the colander over a large bowl. Line the colander with the cheesecloth, muslin bag, or paper coffee filters. Spoon in the yogurt.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E308943C89D15F41AFE6F6F0341A70CA', X'E244DF69F2F08844A7A8E629DD279C7E', X'E244DF69F2F08844A7A8E629DD279C7E', 1, NULL, NULL, 2, NULL, 0, 'Leave the yogurt to drain for 24 hours, refrigerated for a milder flavor or at room temperature for a tangier labneh.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A532557E705E004280EB957FDB093D4B', X'E244DF69F2F08844A7A8E629DD279C7E', X'E244DF69F2F08844A7A8E629DD279C7E', 2, NULL, NULL, 2, NULL, 0, 'The liquid that separates out into the bowl is whey. You can use it to kickstart lacto-fermented pickles, especially if you want to reduce or eliminate the amount of salt in them. Whey will keep in the refrigerator for up to a month.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'05FF176DD5B41040AA1570A0A23A1C54', X'E244DF69F2F08844A7A8E629DD279C7E', X'E244DF69F2F08844A7A8E629DD279C7E', 3, NULL, NULL, 2, NULL, 0, 'Mix the salt, pepper, and herbs (if using) into the yogurt.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C9B5CCA6E42A22459C4FEF32B7A0614E', X'E244DF69F2F08844A7A8E629DD279C7E', X'E244DF69F2F08844A7A8E629DD279C7E', 4, NULL, NULL, 2, NULL, 0, 'Spoon the labneh as is into jars or containers and store it in the refrigerator for up to 1 week. For longer storage, use the traditional oil-preserved method below.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4C67FB7ECE64384B8F2A68659AC8609D', X'E244DF69F2F08844A7A8E629DD279C7E', X'E244DF69F2F08844A7A8E629DD279C7E', 5, NULL, NULL, 2, NULL, 0, 'Lightly coat your hands with olive oil. Scoop up a scant tablespoon of labneh and gently shape it into a ball. The labneh will be soft, but you should still be able to shape it into a ball that holds together.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'80347C03760D2E4E946427F0603B1FE8', X'E244DF69F2F08844A7A8E629DD279C7E', X'E244DF69F2F08844A7A8E629DD279C7E', 6, NULL, NULL, 2, NULL, 0, 'Place the labneh balls carefully into clean, dry glass jars. Pour the extra-virgin olive oil over the labneh. For decoration and extra flavor, you can tuck in a few sprigs of fresh herbs as you add the labneh and oil.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E6B892088070EE4AA6AA92ED985328F3', X'E244DF69F2F08844A7A8E629DD279C7E', X'E244DF69F2F08844A7A8E629DD279C7E', 7, NULL, NULL, 2, NULL, 0, 'Lightly tap the jars on the side to release any air bubbles. Cover and store in the refrigerator for up to 2 months.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1F37B7D7F50FC34EABB546683E70A41E', X'91F4520FC970034FAFF8F3E937F5B78F', X'91F4520FC970034FAFF8F3E937F5B78F', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 375 F.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'357EA44A18A062419DDE97D189A186D7', X'91F4520FC970034FAFF8F3E937F5B78F', X'91F4520FC970034FAFF8F3E937F5B78F', 1, NULL, NULL, 2, NULL, 0, 'In a large mixing bowl, combine the yeast, nigella seeds, and baking powder. Stir until well mixed. Add in the flour and repeat mixing until all ingredients are well combined.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CD76CD29C8BE77408DFA08C467C40602', X'91F4520FC970034FAFF8F3E937F5B78F', X'91F4520FC970034FAFF8F3E937F5B78F', 2, NULL, NULL, 2, NULL, 0, 'Set aside and warm the cup of milk in a small saucepan until lukewarm only. It should be close to room temperature. It is not important to be exact, just so long as the milk is not specifically hot or cold.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B555AC9A0F7D9D40B96CBBC37C48144B', X'91F4520FC970034FAFF8F3E937F5B78F', X'91F4520FC970034FAFF8F3E937F5B78F', 3, NULL, NULL, 2, NULL, 0, 'Then, in a separate small mixing bowl place the egg, yogurt, milk, and melted ghee. Once combined, pour into the spice and flour mixture.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B8296B3734CC094DB032B4086853F501', X'91F4520FC970034FAFF8F3E937F5B78F', X'91F4520FC970034FAFF8F3E937F5B78F', 4, NULL, NULL, 2, NULL, 0, 'Mix together until a ball of dough forms. Knead the dough until it is elastic enough to build loaves with. Separate the dough into 8 equal parts.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'78D4037A0A76BA46A39047D45F91DB69', X'91F4520FC970034FAFF8F3E937F5B78F', X'91F4520FC970034FAFF8F3E937F5B78F', 5, NULL, NULL, 2, NULL, 0, 'Create oval shapes with the dough and flatten until about 1/2 inch thick.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1EBF4D1419847940BB2B4FAB389211B2', X'91F4520FC970034FAFF8F3E937F5B78F', X'91F4520FC970034FAFF8F3E937F5B78F', 6, NULL, NULL, 2, NULL, 0, 'Place 2 to 3 on a baking sheet at a time. Place in the oven for 15 minutes on the middle rack. If the top does not appear brown at this time, place the baking sheet on the top rack for 2 minutes.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'65F82C1FC143CC45B2BF5FF4BEED4B06', X'91F4520FC970034FAFF8F3E937F5B78F', X'91F4520FC970034FAFF8F3E937F5B78F', 7, NULL, NULL, 2, NULL, 0, 'Remove from the oven and drizzle lightly with olive oil. Sprinkle a pinch of Za''atar on each loaf and place in a bread basket to keep warm. Repeat the cooking process with the additional uncooked loaves.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B8A0CBDAD34F37469A595E3D8852D00E', X'91F4520FC970034FAFF8F3E937F5B78F', X'91F4520FC970034FAFF8F3E937F5B78F', 8, NULL, NULL, 2, NULL, 0, 'Once all loaves are cooked serve with fresh hummus or Taziki.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'08CC0A6D24C14346817D5A9CD905FA1E', X'0A2F05ECE6BFD543A81620BB4549F817', X'0A2F05ECE6BFD543A81620BB4549F817', 0, NULL, NULL, 2, NULL, 0, 'Bring a large pot of water to a boil.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FF607698D92FD24F94EBE66BB05DD0D2', X'0A2F05ECE6BFD543A81620BB4549F817', X'0A2F05ECE6BFD543A81620BB4549F817', 1, NULL, NULL, 2, NULL, 0, 'Add the green beans and cook until bright green and crisp-tender, about 3 to 4 minutes.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3D02AE9BDD36364390C2B7612B100B1F', X'0A2F05ECE6BFD543A81620BB4549F817', X'0A2F05ECE6BFD543A81620BB4549F817', 2, NULL, NULL, 2, NULL, 0, 'Drain and set aside.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7405AD2E31E9A84BBB218F6A09685983', X'0A2F05ECE6BFD543A81620BB4549F817', X'0A2F05ECE6BFD543A81620BB4549F817', 3, NULL, NULL, 2, NULL, 0, 'In a chef''s pan or large skillet, warm the olive oil over medium-high heat.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6B1D18B34EBBDD42ACD224EDD9A4D825', X'0A2F05ECE6BFD543A81620BB4549F817', X'0A2F05ECE6BFD543A81620BB4549F817', 4, NULL, NULL, 2, NULL, 0, 'Add the garlic and saut until fragrant, about 30 to 60 seconds.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2D14C5EA6F7E2C4B83F7ABB2E0F6176C', X'0A2F05ECE6BFD543A81620BB4549F817', X'0A2F05ECE6BFD543A81620BB4549F817', 5, NULL, NULL, 2, NULL, 0, 'Add the green beans and pecans and saut to coat with the oil.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3E88A4D3C2145440BD5DABD3DDBF4608', X'0A2F05ECE6BFD543A81620BB4549F817', X'0A2F05ECE6BFD543A81620BB4549F817', 6, NULL, NULL, 2, NULL, 0, 'Drizzle with the date syrup, toss together quickly, and remove from the heat.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BCDB2C1A94156D4889FC193C9B8477C0', X'0A2F05ECE6BFD543A81620BB4549F817', X'0A2F05ECE6BFD543A81620BB4549F817', 7, NULL, NULL, 2, NULL, 0, 'Transfer to a serving platter or bowl.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B0181E97CEFB974F987BCAA8A91A8DF4', X'0A2F05ECE6BFD543A81620BB4549F817', X'0A2F05ECE6BFD543A81620BB4549F817', 8, NULL, NULL, 2, NULL, 0, 'Drizzle with additional date syrup if desired.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B70FFC00CA30334E8FFCA1CBC19C7D30', X'0A2F05ECE6BFD543A81620BB4549F817', X'0A2F05ECE6BFD543A81620BB4549F817', 9, NULL, NULL, 2, NULL, 0, 'Serve warm or at room temperature. Enjoy!');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6491A80DCCAC494EBC3924CD5EF90BD3', X'C4AAE4449538744CB5600AB650C841E1', X'C4AAE4449538744CB5600AB650C841E1', 0, NULL, NULL, 2, NULL, 0, 'Rinse the rice in a fine mesh colander until the water runs clear. Drain well.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1A04AB10D10EC64C9815ED6ABDFA8049', X'C4AAE4449538744CB5600AB650C841E1', X'C4AAE4449538744CB5600AB650C841E1', 1, NULL, NULL, 2, NULL, 0, 'Warm the oil in a heavy-bottomed saucepan, set over medium-high heat. Stir in the rice, turmeric, and curry powder, mixing well to coat the rice with the spices and oil. Add the raisins, vegetable stock and soy sauce and bring to a boil.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'35B025FD370ADE4CA4AC6264E8207C9F', X'C4AAE4449538744CB5600AB650C841E1', X'C4AAE4449538744CB5600AB650C841E1', 2, NULL, NULL, 2, NULL, 0, 'Reduce the heat to low, cover the pot, and simmer for 18 minutes. (Do not lift the lid while cooking or the rice will become sticky.)');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'96DA7C451A94374D9F175DAAEA21E0D1', X'C4AAE4449538744CB5600AB650C841E1', X'C4AAE4449538744CB5600AB650C841E1', 3, NULL, NULL, 2, NULL, 0, 'Remove the rice from the heat, stir to fluff, then replace the cover and allow the rice to rest for 10 minutes.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'59CFAED75033574DADF217B9E66860E7', X'C4AAE4449538744CB5600AB650C841E1', X'C4AAE4449538744CB5600AB650C841E1', 4, NULL, NULL, 2, NULL, 0, 'Mound the rice onto a platter and serve. Enjoy!');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'636F086A983CF54E9D7170E84EC39DB2', X'D6BF3E5CD2FF874F9B65828057951376', X'D6BF3E5CD2FF874F9B65828057951376', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5E3C8CD3AC02CC4B8AE9DA81F1CE7669', X'D6BF3E5CD2FF874F9B65828057951376', X'D6BF3E5CD2FF874F9B65828057951376', 1, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 400 F.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C393F446A5E25E4B917999E923D8955C', X'D6BF3E5CD2FF874F9B65828057951376', X'D6BF3E5CD2FF874F9B65828057951376', 2, NULL, NULL, 2, NULL, 0, 'Slice each acorn squash in half, lengthwise, and scoop out the seeds. You should have four boat-shaped pieces. Place on a baking sheet lined with parchment paper and roast in the oven for 20 minutes or until the squash is fork tender. Set aside.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B12CD681CD861B4192B9B9209A1F6B4E', X'D6BF3E5CD2FF874F9B65828057951376', X'D6BF3E5CD2FF874F9B65828057951376', 3, NULL, NULL, 2, NULL, 0, 'Spread the bread cubes out on the baking sheet. Bake in the oven for about 10 minutes or until the cubes are a lightly toasted.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EF180209825DC5478FADC9C4E6B95328', X'D6BF3E5CD2FF874F9B65828057951376', X'D6BF3E5CD2FF874F9B65828057951376', 4, NULL, NULL, 2, NULL, 0, 'Add two tablespoons of the butter to a large skillet or cast iron pan. Add the diced onion and, on medium heat, saute for about 10 minutes until translucent.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6CAFE3B22407BC4D854119EF0460A9F6', X'D6BF3E5CD2FF874F9B65828057951376', X'D6BF3E5CD2FF874F9B65828057951376', 5, NULL, NULL, 2, NULL, 0, 'While the onion is sauteing, add the chicken stock and the remaining 6 tablespoons of butter to a pot and heat, allowing the butter to melt.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5752D40C9E94874AA58165E9EA3FC843', X'D6BF3E5CD2FF874F9B65828057951376', X'D6BF3E5CD2FF874F9B65828057951376', 6, NULL, NULL, 2, NULL, 0, 'Scoop the cooked flesh out of the acorn squash, but leave enough in the skin to use as a bowl.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'57D57E4FBA272C4EB163AD6A7D5D5370', X'D6BF3E5CD2FF874F9B65828057951376', X'D6BF3E5CD2FF874F9B65828057951376', 7, NULL, NULL, 2, NULL, 0, 'Add the squash and toasted bread cubes to a large bowl.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'212567BE6A2F7A41A254181377B06D22', X'D6BF3E5CD2FF874F9B65828057951376', X'D6BF3E5CD2FF874F9B65828057951376', 8, NULL, NULL, 2, NULL, 0, 'Stir in the cooked onion and ladle in the warm chicken and butter stock. Fluff with a fork to combine and make sure the bread absorbs the liquid.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'29D9F00D6DFD3B45BCDE280AD41325B3', X'D6BF3E5CD2FF874F9B65828057951376', X'D6BF3E5CD2FF874F9B65828057951376', 9, NULL, NULL, 2, NULL, 0, 'Stir in the rosemary, thyme, sage, parsley and slivered almonds. Season with the za''atar, salt, and pepper.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F13E7CBC9A736441A9185B6CAF6ECC92', X'D6BF3E5CD2FF874F9B65828057951376', X'D6BF3E5CD2FF874F9B65828057951376', 10, NULL, NULL, 2, NULL, 0, 'Scoop the stuffing into each of the hollowed out acorn squash shells and either refrigerate until ready to use or return to the oven for about 15 minutes for the flavors to meld together and the stuffing to heat through.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D22D4EE5F9A6D9479E6848E00B1BDEC1', X'D6BF3E5CD2FF874F9B65828057951376', X'D6BF3E5CD2FF874F9B65828057951376', 11, NULL, NULL, 2, NULL, 0, 'You can also forgo the squash bowls and just scoop into a large casserole dish or individual ramekins.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E47DEEF9C611E647AE17A82D3AC9D091', X'A255F06804BDCB43BFD9E05A3EB360BE', X'A255F06804BDCB43BFD9E05A3EB360BE', 0, NULL, NULL, 2, NULL, 0, 'In a medium sauce pot, bring chicken broth, lemon juice, and lemon zest to a boil.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F43ECC9C82C7244788413A554DDA266E', X'A255F06804BDCB43BFD9E05A3EB360BE', X'A255F06804BDCB43BFD9E05A3EB360BE', 1, NULL, NULL, 2, NULL, 0, 'In a medium bowl, combine couscous and salt.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D2836655484A2F42B6846A7BE907473F', X'A255F06804BDCB43BFD9E05A3EB360BE', X'A255F06804BDCB43BFD9E05A3EB360BE', 2, NULL, NULL, 2, NULL, 0, 'Pour boiling broth over couscous and allow to sit, covered for 10 minutes, or until the broth is absorbed.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0B41BA6BF406EF44905C5518678D558C', X'A255F06804BDCB43BFD9E05A3EB360BE', X'A255F06804BDCB43BFD9E05A3EB360BE', 3, NULL, NULL, 2, NULL, 0, 'Fluff couscous with a fork and serve immediately.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'345830E5CDF7AD47A4AC028A2329646D', X'3776AC90B56B774DBDBA05C47B1B4203', X'3776AC90B56B774DBDBA05C47B1B4203', 0, NULL, NULL, 2, NULL, 0, 'Wash and spin dry the spinach leaves and layer them on a serving platter.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'68CCC866FF9BFE49BA5D9BE78AD7C86B', X'3776AC90B56B774DBDBA05C47B1B4203', X'3776AC90B56B774DBDBA05C47B1B4203', 1, NULL, NULL, 2, NULL, 0, 'Peel the blood orange and cut into slices approximately 1/4` thick. Layer the orange slices over the spinach.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A79035BE99DCFF42AD75BA2887ED7CEA', X'3776AC90B56B774DBDBA05C47B1B4203', X'3776AC90B56B774DBDBA05C47B1B4203', 2, NULL, NULL, 2, NULL, 0, 'Sprinkle the crumbled feta cheese over the salad.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8BB92954D6F63E459D5946AA04CEA99A', X'3776AC90B56B774DBDBA05C47B1B4203', X'3776AC90B56B774DBDBA05C47B1B4203', 3, NULL, NULL, 2, NULL, 0, 'Whisk together the olive oil, blood orange juice, ground sumac, salt and pepper and drizzle over the salad.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'510BA7BBDD5BF042ABFADDD310804DD1', X'E742E765D0F08E47B77AB79C6F08CF70', X'E742E765D0F08E47B77AB79C6F08CF70', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FED9D7E0B4D87A4395D528AA07108FD9', X'E742E765D0F08E47B77AB79C6F08CF70', X'E742E765D0F08E47B77AB79C6F08CF70', 1, NULL, NULL, 2, NULL, 0, 'In large bowl, mix together yeast, water, sugar, and salt.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A819F75ABF1C024C85138B30BEAC8904', X'E742E765D0F08E47B77AB79C6F08CF70', X'E742E765D0F08E47B77AB79C6F08CF70', 2, NULL, NULL, 2, NULL, 0, 'Stir in vegetable oil and eggs.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D7A13F7D8FF1DE4688F1B08F155632D6', X'E742E765D0F08E47B77AB79C6F08CF70', X'E742E765D0F08E47B77AB79C6F08CF70', 3, NULL, NULL, 2, NULL, 0, 'Mix in enough bread flour to make a thick dough that can be kneaded by hand.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'704678AA9F7B9C4ABE02629F04DBB326', X'E742E765D0F08E47B77AB79C6F08CF70', X'E742E765D0F08E47B77AB79C6F08CF70', 4, NULL, NULL, 2, NULL, 0, 'Turn dough out onto board and knead for 8 minutes.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C854ECF4EE63D149A1FAEC054951D684', X'E742E765D0F08E47B77AB79C6F08CF70', X'E742E765D0F08E47B77AB79C6F08CF70', 5, NULL, NULL, 2, NULL, 0, 'Put dough in greased bowl and turn dough over in the bowl so that the top is also lightly greased. Cover with clean kitchen towel and let rise for 1 hour in a warm, draft-free place.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4B3DDA8DA9941046AB81CCF4F4DAAD75', X'E742E765D0F08E47B77AB79C6F08CF70', X'E742E765D0F08E47B77AB79C6F08CF70', 6, NULL, NULL, 2, NULL, 0, 'Punch down.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B1B9CC0CA6D089419BADD9FEFC0BE4AB', X'E742E765D0F08E47B77AB79C6F08CF70', X'E742E765D0F08E47B77AB79C6F08CF70', 7, NULL, NULL, 2, NULL, 0, 'Turn dough out onto lightly floured board and knead out bubbles for 5 minutes.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'42D400A1B534704CA0890973FB1ADD60', X'E742E765D0F08E47B77AB79C6F08CF70', X'E742E765D0F08E47B77AB79C6F08CF70', 8, NULL, NULL, 2, NULL, 0, 'Divide dough into 6 equal pieces. Set 3 pieces aside.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DEA7FC25A0B2004DA07F759312FA9D2C', X'E742E765D0F08E47B77AB79C6F08CF70', X'E742E765D0F08E47B77AB79C6F08CF70', 9, NULL, NULL, 2, NULL, 0, 'Roll out the remaining 3 pieces of dough between your hands, making 3 strips, about 11- to 12-inches long.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'10B3953790F4D94594A9FD952F4FDFAF', X'E742E765D0F08E47B77AB79C6F08CF70', X'E742E765D0F08E47B77AB79C6F08CF70', 10, NULL, NULL, 2, NULL, 0, 'Line strips side-by-side on floured board and pinch the top ends together.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'012124D528538640A0AEEBF8BE78B4F2', X'E742E765D0F08E47B77AB79C6F08CF70', X'E742E765D0F08E47B77AB79C6F08CF70', 11, NULL, NULL, 2, NULL, 0, 'Braid strips and pinch bottom ends together.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'938BCAF78D29C94B98A878A2E03385A0', X'E742E765D0F08E47B77AB79C6F08CF70', X'E742E765D0F08E47B77AB79C6F08CF70', 12, NULL, NULL, 2, NULL, 0, 'Set braided loaf on greased baking sheet and repeat steps for the remaining 3 pieces of dough.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'635490E617ADE142B6FE0BB6ABBC4DC4', X'E742E765D0F08E47B77AB79C6F08CF70', X'E742E765D0F08E47B77AB79C6F08CF70', 13, NULL, NULL, 2, NULL, 0, 'Cover loaves with kitchen towel and let rise in a warm, draft-free place for 30 minutes or until doubled in bulk.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'879FB0F640E46644891483848B702F9A', X'E742E765D0F08E47B77AB79C6F08CF70', X'E742E765D0F08E47B77AB79C6F08CF70', 14, NULL, NULL, 2, NULL, 0, 'Uncover bread and brush on egg yolk. Sprinkle with poppy seeds.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A7B5051BC73FE44CAD3566C9DF5716EB', X'E742E765D0F08E47B77AB79C6F08CF70', X'E742E765D0F08E47B77AB79C6F08CF70', 15, NULL, NULL, 2, NULL, 0, 'Bake at 400 F for 40 minutes or until bread sounds hollow when tapped on.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B2EF9E8DA101204A865D4AD032DE0BCF', X'E742E765D0F08E47B77AB79C6F08CF70', X'E742E765D0F08E47B77AB79C6F08CF70', 16, NULL, NULL, 2, NULL, 0, 'Let bread cool on rack.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'46723CB3DA69C2479AF512F4A7660EC3', X'E742E765D0F08E47B77AB79C6F08CF70', X'E742E765D0F08E47B77AB79C6F08CF70', 17, NULL, NULL, 2, NULL, 0, 'Serve warm.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'139888C935892C41A6914465F07EB972', X'006812168B3FB9408C310392111B84FD', X'006812168B3FB9408C310392111B84FD', 0, NULL, NULL, 2, NULL, 0, 'Soak grape leaves in cold water for 2 to 3 hours. Jarred grape leaves are very salty from the brine solution.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AC02D61E1944F943B958524CF0E1C80E', X'006812168B3FB9408C310392111B84FD', X'006812168B3FB9408C310392111B84FD', 1, NULL, NULL, 2, NULL, 0, 'Rinse and drain in a colander. Set aside.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5E396D7C98ADBD449443FE4578D6FCD4', X'006812168B3FB9408C310392111B84FD', X'006812168B3FB9408C310392111B84FD', 2, NULL, NULL, 2, NULL, 0, 'In a mixing bowl, combine remaining ingredients. Mix well with hands.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'44581F792F042D4F8586E6743A69B2B1', X'006812168B3FB9408C310392111B84FD', X'006812168B3FB9408C310392111B84FD', 3, NULL, NULL, 2, NULL, 0, 'Place a grape leaf side down. Place about 1 to 2 tablespoons of mixture in the middle.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D900844E802B1944B51450F293BDA572', X'006812168B3FB9408C310392111B84FD', X'006812168B3FB9408C310392111B84FD', 4, NULL, NULL, 2, NULL, 0, 'Fold in the sides and roll upward. Think of rolling a burrito, only smaller.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EDEC64014D815041941D453017073A5D', X'006812168B3FB9408C310392111B84FD', X'006812168B3FB9408C310392111B84FD', 5, NULL, NULL, 2, NULL, 0, 'When you have rolled the last leaf, place a heavy plate on top of the stuffed grape leaves in your pan. We use a ceramic plate. This helps keep the grape leaves in place during cooking so they don''t shift around.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'170D9D04C79ECA46AE6CA3133B02A9F3', X'006812168B3FB9408C310392111B84FD', X'006812168B3FB9408C310392111B84FD', 6, NULL, NULL, 2, NULL, 0, 'Fill pan with water and bring to a boil. Reduce heat to low and simmer for about 30 to 45 minutes until rice and meat are done.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5349F1A7E46EFD4EA6B9658F3356EA36', X'6E098556F6632B4491117B9FD11E5875', X'6E098556F6632B4491117B9FD11E5875', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E8EE59325045D1478359DAEF203EB188', X'6E098556F6632B4491117B9FD11E5875', X'6E098556F6632B4491117B9FD11E5875', 1, NULL, NULL, 2, NULL, 0, 'Slice the eggplant in half.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BDE7C55CE0037F43BC4F84729A19D1B2', X'6E098556F6632B4491117B9FD11E5875', X'6E098556F6632B4491117B9FD11E5875', 2, NULL, NULL, 2, NULL, 0, 'On a baking sheet, roast the eggplant in a 400 F oven for approximately 45 minutes, or until soft.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'89BE2679D5EAD449B7B45021E0C475CD', X'6E098556F6632B4491117B9FD11E5875', X'6E098556F6632B4491117B9FD11E5875', 3, NULL, NULL, 2, NULL, 0, 'Remove the eggplant from the oven and allow it to cool slightly, then scoop out inside of eggplant, leaving skin behind.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'70E71669F6D72C408FDEFE99A94BE1BC', X'6E098556F6632B4491117B9FD11E5875', X'6E098556F6632B4491117B9FD11E5875', 4, NULL, NULL, 2, NULL, 0, 'In a blender or food processor, combine eggplant, chickpeas, garlic, lemon juice, tahini, and sea salt until smooth. The mixture will be somewhat thick. Slowly incorporate the olive oil until well mixed.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BB91DC852863054C8B2B5F38D9049708', X'6E098556F6632B4491117B9FD11E5875', X'6E098556F6632B4491117B9FD11E5875', 5, NULL, NULL, 2, NULL, 0, 'Mix in the optional fresh chopped parsley by hand.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3C32652EF5DCB942BC887E3DDA000358', X'6E098556F6632B4491117B9FD11E5875', X'6E098556F6632B4491117B9FD11E5875', 6, NULL, NULL, 2, NULL, 0, 'Enjoy your homemade baba ghanoush hummus served with veggies, pita (or any other kind of flatbread), or as a sandwich spread. To serve, drizzle with a little bit of extra olive oil (use the best quality you have) and sprinkle with chili powder, paprika or cumin for a bit of color. Spread some baba ghanoush in a flour tortilla and add lettuce, tomatoes, and veggies for a vegan sandwich wrap.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B20669DE92E501488D8D162EED5B9121', X'00A1D8EECE8FCE4CB561C4C9D31C69E5', X'00A1D8EECE8FCE4CB561C4C9D31C69E5', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'920E27F3DBE3C44E8C9FF62738BD4E48', X'00A1D8EECE8FCE4CB561C4C9D31C69E5', X'00A1D8EECE8FCE4CB561C4C9D31C69E5', 1, NULL, NULL, 2, NULL, 0, 'In a large bowl, toss together the chopped tomatoes, cucumbers, pepper, and scallions.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0DA01B71CF171A41A0F8F09D68CB3E51', X'00A1D8EECE8FCE4CB561C4C9D31C69E5', X'00A1D8EECE8FCE4CB561C4C9D31C69E5', 2, NULL, NULL, 2, NULL, 0, 'Immediately before serving, season with olive oil, lemon juice, salt, and pepper. Toss well, taste, and adjust the seasonings if desired.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2000BD8305CFF94F97E2F490A807B2E3', X'00A1D8EECE8FCE4CB561C4C9D31C69E5', X'00A1D8EECE8FCE4CB561C4C9D31C69E5', 3, NULL, NULL, 2, NULL, 0, 'Leftover salad will keep, covered in the refrigerator, for 2 to 3 days.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FDD3C3DDC65CD843A1EB08276979BECC', X'6D6888F87733F0448E325DFE056C9DE5', X'6D6888F87733F0448E325DFE056C9DE5', 0, NULL, NULL, 2, NULL, 0, 'Drain chickpeas and rinse thoroughly and set aside.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4BADF999D67BAB4E99218713DE1BE1B0', X'6D6888F87733F0448E325DFE056C9DE5', X'6D6888F87733F0448E325DFE056C9DE5', 1, NULL, NULL, 2, NULL, 0, 'In a large saucepan, saut onion and garlic in olive oil until soft and a light golden color.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'06549038AE655E4E92B62DF6902E196C', X'6D6888F87733F0448E325DFE056C9DE5', X'6D6888F87733F0448E325DFE056C9DE5', 2, NULL, NULL, 2, NULL, 0, 'Add chicken stock or broth and bring to a boil.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2AF42E5F163588498CAF1CCA2B517063', X'6D6888F87733F0448E325DFE056C9DE5', X'6D6888F87733F0448E325DFE056C9DE5', 3, NULL, NULL, 2, NULL, 0, 'Stir in uncooked rice, drained chickpeas, salt, and pepper. Cover and reduce heat. Simmer on low heat for twenty minutes.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C128D5B59910654CA70427B08110DB1F', X'6D6888F87733F0448E325DFE056C9DE5', X'6D6888F87733F0448E325DFE056C9DE5', 4, NULL, NULL, 2, NULL, 0, 'After twenty minutes, remove from heat and let sit, covered, for 3 to 5 minutes.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'772A82AD3BB00445AEBA7B4F67FFFB40', X'6D6888F87733F0448E325DFE056C9DE5', X'6D6888F87733F0448E325DFE056C9DE5', 5, NULL, NULL, 2, NULL, 0, 'Remove lid and fluff with a fork. Serve immediately.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'65280101CFC1574C8E7EFD52C01325BC', X'C9F8C3E7381C96469A1B79F071DDAB8F', X'C9F8C3E7381C96469A1B79F071DDAB8F', 0, NULL, NULL, 2, NULL, 0, 'Rinse the chickpeas and drain well to remove all the excess liquid. Place in a large bowl.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D9EF8793BEACD24DAC1719B8AAEF7F00', X'C9F8C3E7381C96469A1B79F071DDAB8F', X'C9F8C3E7381C96469A1B79F071DDAB8F', 1, NULL, NULL, 2, NULL, 0, 'Cut the stem end off each Brussels sprout and then slice in half. Place in a food processor and pulse a few times until shredded but not pureed. Add to the chickpeas.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0CDCA43D56D06D4F81B719C4244353BF', X'C9F8C3E7381C96469A1B79F071DDAB8F', X'C9F8C3E7381C96469A1B79F071DDAB8F', 2, NULL, NULL, 2, NULL, 0, 'Make the lemon tahini dressing by combining the sesame paste with the water and lemon juice. Grate in the garlic, using a fine grater, and season with salt and pepper to taste. If you''d like a slightly thinner consistency, add an additional tablespoon of water.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'17B3EA95C82F084EB110B02CB16EAEDD', X'C9F8C3E7381C96469A1B79F071DDAB8F', X'C9F8C3E7381C96469A1B79F071DDAB8F', 3, NULL, NULL, 2, NULL, 0, 'Toss the chickpeas and Brussels sprouts with half of the lemon tahini dressing (or more as desired) and the za''atar.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0B747C78C5B91840B38F54AF7EBED7A1', X'0C4C3D395409A048BBEA6C7D44BFDE8F', X'0C4C3D395409A048BBEA6C7D44BFDE8F', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'538A2E5BE331794C84B1526F6C045035', X'0C4C3D395409A048BBEA6C7D44BFDE8F', X'0C4C3D395409A048BBEA6C7D44BFDE8F', 1, NULL, NULL, 2, NULL, 0, 'Finely chop tomatoes, cucumbers, and onions.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'380B561BE755C34ABBDC3D1470D484B7', X'0C4C3D395409A048BBEA6C7D44BFDE8F', X'0C4C3D395409A048BBEA6C7D44BFDE8F', 2, NULL, NULL, 2, NULL, 0, 'Toss with remaining ingredients and gently mix well.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8018747BCB95FC499BC64E1C99023E01', X'0C4C3D395409A048BBEA6C7D44BFDE8F', X'0C4C3D395409A048BBEA6C7D44BFDE8F', 3, NULL, NULL, 2, NULL, 0, 'Serve chilled.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'06EE67CDEBCA8A46BBC6A3D31499DA4D', X'EB0070FE2756B3489FBBFE4C9BA97BCF', X'EB0070FE2756B3489FBBFE4C9BA97BCF', 0, NULL, NULL, 2, NULL, 0, 'Bring a large pot of lightly salted water to a boil. Shell the peas and add them to the boiling water, reduce the heat to medium and cook for about 5 minutes. Drain and set aside.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F59915ADDAE650489AEAE07B10AC8512', X'EB0070FE2756B3489FBBFE4C9BA97BCF', X'EB0070FE2756B3489FBBFE4C9BA97BCF', 1, NULL, NULL, 2, NULL, 0, 'Add the olive oil and minced garlic to a large saute pan or skillet. Saute for about a minute, just until the garlic is translucent but be careful not to burn it.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'68526023FD10A8489D3FE2BA7C69D1AE', X'EB0070FE2756B3489FBBFE4C9BA97BCF', X'EB0070FE2756B3489FBBFE4C9BA97BCF', 2, NULL, NULL, 2, NULL, 0, 'Add the cooked peas and toss to coat and warm the peas. Season with the sumac, salt, and pepper.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6549502081EEDA49BD2FE40C01DBCFDA', X'EB0070FE2756B3489FBBFE4C9BA97BCF', X'EB0070FE2756B3489FBBFE4C9BA97BCF', 3, NULL, NULL, 2, NULL, 0, 'Serve warm with the crumbled feta cheese on top.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CD981F2FD48A02439C0A18D3CBEFDAD2', X'B35E16DB298AA74FBAB0D9F824B82DD9', X'B35E16DB298AA74FBAB0D9F824B82DD9', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'619CA24B6800D3438B08F9C421C60214', X'B35E16DB298AA74FBAB0D9F824B82DD9', X'B35E16DB298AA74FBAB0D9F824B82DD9', 1, NULL, NULL, 2, NULL, 0, '?Rinse and dry the arugula or spinach and place on a large serving plate or board. Slice the cherry tomatoes into halves or quarters, depending on size. Leave some just sliced in half on the side for dipping into the hummus. Arrange the remaining tomato slices on top of the greens.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'33032867A4FDF543AB680D8DDC925B84', X'B35E16DB298AA74FBAB0D9F824B82DD9', X'B35E16DB298AA74FBAB0D9F824B82DD9', 2, NULL, NULL, 2, NULL, 0, 'Rinse and dry the chickpeas and spread them out over the greens.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'88B7DE1E6688A748A9DAA7AEA2D3FBF0', X'B35E16DB298AA74FBAB0D9F824B82DD9', X'B35E16DB298AA74FBAB0D9F824B82DD9', 3, NULL, NULL, 2, NULL, 0, 'Wash and thinly slice the cucumber. If you''re using an English (seedless) cucumber, there''s no need to peel or seed. Arrange the slices on the salad but leave a few in a pile on the side for dipping into hummus.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'75B9A95787FCB240A9C2B01A9F3BD128', X'B35E16DB298AA74FBAB0D9F824B82DD9', X'B35E16DB298AA74FBAB0D9F824B82DD9', 4, NULL, NULL, 2, NULL, 0, 'Sprinkle the sumac over the salad and season with salt and pepper.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9B8CA26C6C8A7F4F9C86474973B40447', X'B35E16DB298AA74FBAB0D9F824B82DD9', X'B35E16DB298AA74FBAB0D9F824B82DD9', 5, NULL, NULL, 2, NULL, 0, 'Add the hummus to a bowl on the side of the platter along with the warmed pita rounds. Drizzle the salad with some of the tahini dressing but leave the rest in a bowl on the side for people to add more if they like.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'62333BCD91D9CC489E10666C9CD384CE', X'F0D326A20CE07A46923B7668CDD51D2F', X'F0D326A20CE07A46923B7668CDD51D2F', 0, NULL, NULL, 2, NULL, 0, 'If you buy store bought stuffing bread cubes they should already be toasted. But if you''re using homemade, spread the cubes out on a baking sheet lined with parchment paper and bake in a 400 F oven for 10 to 15 minutes until lightly golden bread. The toasted bread will absorb the liquid better.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2E706916F0F9084A86F6B26621E56DE7', X'F0D326A20CE07A46923B7668CDD51D2F', X'F0D326A20CE07A46923B7668CDD51D2F', 1, NULL, NULL, 2, NULL, 0, 'Add 2 tablespoons of butter to a large skillet or cast iron pan along with the onion. Saut on medium heat for about 15 minutes, stirring occasionally, until the onions are translucent or a light golden brown.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DA6AF3920F0485428457A42863D408B6', X'F0D326A20CE07A46923B7668CDD51D2F', X'F0D326A20CE07A46923B7668CDD51D2F', 2, NULL, NULL, 2, NULL, 0, 'Stir in the almonds and apples and continue sauting and stirring for another 5 to 10 minutes. Stir in the parsley and thyme.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'51063CC83F5FF94DB7B3F1C6C5687F25', X'F0D326A20CE07A46923B7668CDD51D2F', X'F0D326A20CE07A46923B7668CDD51D2F', 3, NULL, NULL, 2, NULL, 0, 'While the onion is sauting, add the chicken stock (you can use vegetable stock if you prefer) and cardamom to a pot and simmer.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'34A5E4D1D15AA34E9E4A29126604C1BC', X'F0D326A20CE07A46923B7668CDD51D2F', X'F0D326A20CE07A46923B7668CDD51D2F', 4, NULL, NULL, 2, NULL, 0, 'Add the toasted bread cubes to a large bowl. Stir in the sauted onion mixture and pour on the warm stock. Stir to combine and to make sure the bread is absorbing the liquid but try not to mash it too much.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'29EFD659AFC89342B130181E9E23C32F', X'F0D326A20CE07A46923B7668CDD51D2F', X'F0D326A20CE07A46923B7668CDD51D2F', 5, NULL, NULL, 2, NULL, 0, 'Fluff a bit with a fork and transfer to a baking dish.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'407B3812DB9D6448B04AAB2D960E3F5C', X'F0D326A20CE07A46923B7668CDD51D2F', X'F0D326A20CE07A46923B7668CDD51D2F', 6, NULL, NULL, 2, NULL, 0, 'When ready to serve, bake in a 350 F oven for 20 minutes.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AE37A54BAC717640B0F5E54BA2028D47', X'9AA93E22543071419885350A4DEAD8AA', X'9AA93E22543071419885350A4DEAD8AA', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3C9FBE1C20A0EB448D2C9BB571DE6EEA', X'9AA93E22543071419885350A4DEAD8AA', X'9AA93E22543071419885350A4DEAD8AA', 1, NULL, NULL, 2, NULL, 0, 'Pre-heat the oven to 400 degrees.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'44B96CCA83860C4F9DAC9B9C3A3D13F4', X'9AA93E22543071419885350A4DEAD8AA', X'9AA93E22543071419885350A4DEAD8AA', 2, NULL, NULL, 2, NULL, 0, 'Slice the heirloom tomatoes and place on a baking sheet lined with parchment paper. Drizzle with two tablespoons of olive oil and sprinkle with the za''atar. Roast in the oven for approximately 30 minutes or until the tomatoes are starting to get a little color but not burned.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3B3701B93D2CB94591DF2EC99B1EDC2B', X'9AA93E22543071419885350A4DEAD8AA', X'9AA93E22543071419885350A4DEAD8AA', 3, NULL, NULL, 2, NULL, 0, 'Brush the slices of Italian bread with the remaining tablespoon of olive oil and grill or toast in the oven for approximately 10 minutes or until lightly golden brown.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CF3FBB2165F655498B19DD58F130CCD9', X'9AA93E22543071419885350A4DEAD8AA', X'9AA93E22543071419885350A4DEAD8AA', 4, NULL, NULL, 2, NULL, 0, 'Spread each slice with the softened feta cheese and top with the roasted heirloom tomatoes. Cut each slice in half and serve warm as an appetizer with a cocktail or glass of wine.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D2E02886230FC240A315EDFCDCAE84B6', X'C91A46589AAF3C40964E3C8CA5D55DAA', X'C91A46589AAF3C40964E3C8CA5D55DAA', 0, NULL, NULL, 2, NULL, 0, 'Add the pasta to a large pot of boiling salted water and cook according to the package directions. Drain the cooked pasta and return to the pot. Stir in the rinsed and drained chickpeas as well as the baby spinach leaves. Note that the residual heat of the pasta will cook the spinach down a bit.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FA2EC763A4583B428AB174E403CB417F', X'C91A46589AAF3C40964E3C8CA5D55DAA', X'C91A46589AAF3C40964E3C8CA5D55DAA', 1, NULL, NULL, 2, NULL, 0, 'Make the dressing by whisking together the chopped garlic scapes or minced garlic, olive oil, fresh lemon juice, sesame paste, water, red pepper flakes, dried oregano, ground black pepper and salt.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B886EC7C077EE147B97B5004FD5282B8', X'C91A46589AAF3C40964E3C8CA5D55DAA', X'C91A46589AAF3C40964E3C8CA5D55DAA', 2, NULL, NULL, 2, NULL, 0, 'Toss the pasta, spinach and chickpeas with the dressing and serve at room temperature.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8B46CA9C1BD7B246985AA92CB48AFB96', X'9629002F189A47489FBF95A2D7836F81', X'9629002F189A47489FBF95A2D7836F81', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E1AF557F75D8FA45B37F0F73ECB65981', X'9629002F189A47489FBF95A2D7836F81', X'9629002F189A47489FBF95A2D7836F81', 1, NULL, NULL, 2, NULL, 0, 'You can grate the shallot, potato, and squash by hand on a box grater but it''s easier to use the grating attachment on a food processor. Begin with the shallot, then the squash and then the potato.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5ABA8427D2ADCE45BD13C95EED2CBB5D', X'9629002F189A47489FBF95A2D7836F81', X'9629002F189A47489FBF95A2D7836F81', 2, NULL, NULL, 2, NULL, 0, 'Transfer the grated vegetables to a large bowl and mix in the egg, flour, salt, za''atar and pepper.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'21092885B39E2F41874BF6F5576BDA04', X'9629002F189A47489FBF95A2D7836F81', X'9629002F189A47489FBF95A2D7836F81', 3, NULL, NULL, 2, NULL, 0, 'Heat the canola oil in a large skillet or cast iron pan.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5A059D2F60903248BCC1BBAA0148B119', X'9629002F189A47489FBF95A2D7836F81', X'9629002F189A47489FBF95A2D7836F81', 4, NULL, NULL, 2, NULL, 0, 'Drop spoonfuls of the batter into the pan. I made mine about 3 inches but you can make them smaller or larger. Fry on medium heat for about 5 to 7 minutes on each side or until the pancakes are a light golden brown. Repeat until you''ve used all the batter.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'461DA215A6F5704CA60712B8E3BAD9B9', X'9629002F189A47489FBF95A2D7836F81', X'9629002F189A47489FBF95A2D7836F81', 5, NULL, NULL, 2, NULL, 0, 'Top with a dollop of labneh and an additional sprinkling of za''atar.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E10937D3257CDD4B99484153395DDB1F', X'9629002F189A47489FBF95A2D7836F81', X'9629002F189A47489FBF95A2D7836F81', 6, NULL, NULL, 2, NULL, 0, 'Serve and enjoy!');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E4329FCCF082594B85E8D09390E49759', X'2648BCA414B62348BBF7C79B0FA7C71F', X'2648BCA414B62348BBF7C79B0FA7C71F', 0, NULL, NULL, 2, NULL, 0, 'Make the tahini sauce by combining the garlic, sesame paste, lemon juice, Greek yogurt and water in the bowl of a small food processor. Puree until fully combined and season with salt and pepper.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6FF5C7FA29EA394199D5D19F702EACBC', X'2648BCA414B62348BBF7C79B0FA7C71F', X'2648BCA414B62348BBF7C79B0FA7C71F', 1, NULL, NULL, 2, NULL, 0, 'Remove the skin from the chicken and shred or dice the meat.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D1F38A43B5E0C240BC987A8982CCDAC8', X'2648BCA414B62348BBF7C79B0FA7C71F', X'2648BCA414B62348BBF7C79B0FA7C71F', 2, NULL, NULL, 2, NULL, 0, 'Add the chicken to a large bowl along with the shredded carrots and toss with the tahini sauce.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FF3EEE4A52F45A47BDC7985180C37759', X'2648BCA414B62348BBF7C79B0FA7C71F', X'2648BCA414B62348BBF7C79B0FA7C71F', 3, NULL, NULL, 2, NULL, 0, 'Stir in the sliced almonds, chopped cilantro and za''atar.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2F2398B80D5F4441A0E38BFB3907572F', X'2648BCA414B62348BBF7C79B0FA7C71F', X'2648BCA414B62348BBF7C79B0FA7C71F', 4, NULL, NULL, 2, NULL, 0, 'Season with additional salt and pepper as desired.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B35CB473FED41E4694DE249A427319D3', X'2648BCA414B62348BBF7C79B0FA7C71F', X'2648BCA414B62348BBF7C79B0FA7C71F', 5, NULL, NULL, 2, NULL, 0, 'Serve on pita or naan bread with greens such as baby spinach.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'571D9AB31D3EC242893561746E0FC6BC', X'1EAF029A547E2442999EAA67AFC92D15', X'1EAF029A547E2442999EAA67AFC92D15', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2598DCA2255AC14FB04E8738B6793ABE', X'1EAF029A547E2442999EAA67AFC92D15', X'1EAF029A547E2442999EAA67AFC92D15', 1, NULL, NULL, 2, NULL, 0, 'Add the olive oil and diced onion to a large skillet.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'88004DB398F051488BC10CFCFF53CBAC', X'1EAF029A547E2442999EAA67AFC92D15', X'1EAF029A547E2442999EAA67AFC92D15', 2, NULL, NULL, 2, NULL, 0, 'Saute over medium low heat for a few minutes, until the onions become translucent.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4E0FB67DCFB8B84C9884D0F4B5CD69FE', X'1EAF029A547E2442999EAA67AFC92D15', X'1EAF029A547E2442999EAA67AFC92D15', 3, NULL, NULL, 2, NULL, 0, 'Peel the butternut squash and cut in half with a sharp knife.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A7E8CC0CF7C85A43BE7D2911CF23A9C8', X'1EAF029A547E2442999EAA67AFC92D15', X'1EAF029A547E2442999EAA67AFC92D15', 4, NULL, NULL, 2, NULL, 0, 'Scoop out the seeds with a spoon and then cut the squash into small chunks.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'13158EA05E019449A95B2DE8284F071A', X'1EAF029A547E2442999EAA67AFC92D15', X'1EAF029A547E2442999EAA67AFC92D15', 5, NULL, NULL, 2, NULL, 0, 'Working in batches, add a few of the chunks to a food processor and pulse a few times until you get a rice-like size and consistency.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2C2C32D36765D14890F5CCE3906940C9', X'1EAF029A547E2442999EAA67AFC92D15', X'1EAF029A547E2442999EAA67AFC92D15', 6, NULL, NULL, 2, NULL, 0, 'Add the riced butternut squash to the pan with the onion and continue sauteing for a few minutes until the squash softens a bit but doesn''t become mushy.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BCA9A71315868E45BE5F110681BF124C', X'1EAF029A547E2442999EAA67AFC92D15', X'1EAF029A547E2442999EAA67AFC92D15', 7, NULL, NULL, 2, NULL, 0, 'Season with the garlic powder, cumin, salt and pepper.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C19EEBAEDC04B542A677D83935E2281F', X'1EAF029A547E2442999EAA67AFC92D15', X'1EAF029A547E2442999EAA67AFC92D15', 8, NULL, NULL, 2, NULL, 0, 'Stir in the parsley and top with the slivered almonds.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C94734CB44329B4BB68C80C9A32E9184', X'1EAF029A547E2442999EAA67AFC92D15', X'1EAF029A547E2442999EAA67AFC92D15', 9, NULL, NULL, 2, NULL, 0, 'Serve and enjoy!');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'44EC0AF609D87C408F9836CFDBF04C8D', X'A7D1630C842EA04DB93B90738B1F40CE', X'A7D1630C842EA04DB93B90738B1F40CE', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D4CA277F41C1CC48A488202699F4A924', X'A7D1630C842EA04DB93B90738B1F40CE', X'A7D1630C842EA04DB93B90738B1F40CE', 1, NULL, NULL, 2, NULL, 0, '?In a medium bowl, whisk together the eggs and canola oil.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B53E74A35968A74C96297019CC921BCF', X'A7D1630C842EA04DB93B90738B1F40CE', X'A7D1630C842EA04DB93B90738B1F40CE', 2, NULL, NULL, 2, NULL, 0, 'Stir in the matzo meal, salt, za''atar and baking powder until well combined and then stir in the water. Note that the mixture will be very loose but will thicken as it stands. Refrigerate for at least 30 minutes.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'20C8CF3E75FA8B4D8C4172FB8269D6EE', X'A7D1630C842EA04DB93B90738B1F40CE', X'A7D1630C842EA04DB93B90738B1F40CE', 3, NULL, NULL, 2, NULL, 0, 'Bring a large pot of water to a boil. Scoop approximately 1 ounce ball of the matzo batter into the water. Cover, reduce the heat to low and let simmer for 30 minutes.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E32CCC2FA2220D45B562ABEDB1539DB3', X'A7D1630C842EA04DB93B90738B1F40CE', X'A7D1630C842EA04DB93B90738B1F40CE', 4, NULL, NULL, 2, NULL, 0, 'Remove the cooked matzo balls with a slotted spoon or spider strainer and add to soup.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1686B2A32879AF4D8BC953113E2D997E', X'A7D1630C842EA04DB93B90738B1F40CE', X'A7D1630C842EA04DB93B90738B1F40CE', 5, NULL, NULL, 2, NULL, 0, 'Serve and enjoy!');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'227D9C1FBD55774EAAE3B53F70E1CA6F', X'BC53889495D4E74288C4F73BB6ABB1A6', X'BC53889495D4E74288C4F73BB6ABB1A6', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'ED6274A840BB644B99EBA07B1C1DD9DA', X'BC53889495D4E74288C4F73BB6ABB1A6', X'BC53889495D4E74288C4F73BB6ABB1A6', 1, NULL, NULL, 2, NULL, 0, 'Trim the ends of the green beans and add to a pot of boiling water. Cook on high heat for approximately 10 minutes or until the beans are fork tender. Drain.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E15D9295F9179845BB589208F1FA1235', X'BC53889495D4E74288C4F73BB6ABB1A6', X'BC53889495D4E74288C4F73BB6ABB1A6', 2, NULL, NULL, 2, NULL, 0, 'While the beans are cooking, Add 2 tablespoons of the olive oil to a skillet or cast iron pan along with the peeled and sliced onions. Saute for about 10 minutes until the onions are lightly caramelized.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4E6ACA3760B17148977D755C51FFEB93', X'BC53889495D4E74288C4F73BB6ABB1A6', X'BC53889495D4E74288C4F73BB6ABB1A6', 3, NULL, NULL, 2, NULL, 0, 'When the onions are done, remove from the pan and add to the cooked and drained green beans. Add the remaining 2 tablespoons of olive oil to the pan along with the breadcrumbs and za''atar. Saute for about 2 minutes, stirring often, until lightly browned.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8D90C17D26F4FA4FA89DA79F7277A73E', X'BC53889495D4E74288C4F73BB6ABB1A6', X'BC53889495D4E74288C4F73BB6ABB1A6', 4, NULL, NULL, 2, NULL, 0, 'Toss the green beans and onions with the breadcrumbs and season with salt and pepper to taste.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'07C221AAC5869245A580BC8C244E1DA0', X'5A626D95F2AEB847B9C49FCEC0EFB50F', X'5A626D95F2AEB847B9C49FCEC0EFB50F', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'859873FB04A17C4D93D5FF83B485E2A4', X'5A626D95F2AEB847B9C49FCEC0EFB50F', X'5A626D95F2AEB847B9C49FCEC0EFB50F', 1, NULL, NULL, 2, NULL, 0, 'Wash the radishes. Slice off the stem ends and tips. If using watermelon or daikon radishes, peel them. Leave cherry belle and other rosy-skinned radishes unpeeled.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'823ED6F1902F144CA61D83457063BB8F', X'5A626D95F2AEB847B9C49FCEC0EFB50F', X'5A626D95F2AEB847B9C49FCEC0EFB50F', 2, NULL, NULL, 2, NULL, 0, 'Slice the radishes into 1/8-inch-thick rounds. Smaller radishes simply can be cut in half and very small ones (less than 1 inch in diameter) can be left whole.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'65D964F1C1A5D144945465D05D6564E9', X'5A626D95F2AEB847B9C49FCEC0EFB50F', X'5A626D95F2AEB847B9C49FCEC0EFB50F', 3, NULL, NULL, 2, NULL, 0, 'Divide the dill, garlic, bay leaves, mustard seeds, cumin seeds, and optional pepper flakes evenly between two glass pint jars. It is not necessary to sterilize the jars for this recipe but they should be scrupulously clean.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EC8D3D31D104AE4DAB661206D1647B14', X'5A626D95F2AEB847B9C49FCEC0EFB50F', X'5A626D95F2AEB847B9C49FCEC0EFB50F', 4, NULL, NULL, 2, NULL, 0, 'Pack the radishes in with the spices.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F83013A1F37469469FFA6F87BA1838AD', X'5A626D95F2AEB847B9C49FCEC0EFB50F', X'5A626D95F2AEB847B9C49FCEC0EFB50F', 5, NULL, NULL, 2, NULL, 0, 'To make the brine, bring the water, vinegar, honey, and salt to boil in a small saucepan, stirring to dissolve the honey and salt.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'65610EA96AA1E14FBBD58170E3C038E9', X'5A626D95F2AEB847B9C49FCEC0EFB50F', X'5A626D95F2AEB847B9C49FCEC0EFB50F', 6, NULL, NULL, 2, NULL, 0, 'Skim off any foam and discard.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9BCFB95C8F489E41BE305E0B82D61250', X'5A626D95F2AEB847B9C49FCEC0EFB50F', X'5A626D95F2AEB847B9C49FCEC0EFB50F', 7, NULL, NULL, 2, NULL, 0, 'Pour the hot brine over the radishes and seasonings. The radishes should be completely immersed in the brine.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'31BD433B020BDE438C72AD4BAAC1BA9C', X'5A626D95F2AEB847B9C49FCEC0EFB50F', X'5A626D95F2AEB847B9C49FCEC0EFB50F', 8, NULL, NULL, 2, NULL, 0, 'Tightly cover and store in the refrigerator. Pickled radishes will be ready to eat in 24 hours but will be even better if you can wait a week before serving them.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D381DBCB591D1F428C387E2C35211E80', X'5A626D95F2AEB847B9C49FCEC0EFB50F', X'5A626D95F2AEB847B9C49FCEC0EFB50F', 9, NULL, NULL, 2, NULL, 0, 'Serve pickled radishes as a condiment or chop them and add to grain-based salads like tabbouleh.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EC08FB2A28A71F45A7EE8CBFAE98B04E', X'E0F63EE71ABC5D4D8AE096B37CD1B6EC', X'E0F63EE71ABC5D4D8AE096B37CD1B6EC', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F84F5BACF8E20D48B1F4D899440629EB', X'E0F63EE71ABC5D4D8AE096B37CD1B6EC', X'E0F63EE71ABC5D4D8AE096B37CD1B6EC', 1, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 350 F.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A7B7A7B91B4D00498C518A0ED0590CC1', X'E0F63EE71ABC5D4D8AE096B37CD1B6EC', X'E0F63EE71ABC5D4D8AE096B37CD1B6EC', 2, NULL, NULL, 2, NULL, 0, 'Add the feta cheese, cream cheese, za''atar, and dried oregano to a food processor along with one tablespoon of the milk. Puree until smooth, adding an additional tablespoon of milk if needed to get a creamy texture. Note that the mixture will be quite salty but it will also provide the seasoning for the pastry and tomatoes.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8117B40D6A2446409165203294CE4CD7', X'E0F63EE71ABC5D4D8AE096B37CD1B6EC', X'E0F63EE71ABC5D4D8AE096B37CD1B6EC', 3, NULL, NULL, 2, NULL, 0, 'Gently brush a layer of fillo pastry with the melted butter or olive oil, top with another layer and repeat until all the layers are done.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'02DA6CD63A449844B04B3FF33E7E4439', X'E0F63EE71ABC5D4D8AE096B37CD1B6EC', X'E0F63EE71ABC5D4D8AE096B37CD1B6EC', 4, NULL, NULL, 2, NULL, 0, 'Spread the feta cheese mixture over the top layer of fillo, leaving about a 1 1/2 inch border on all sides.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0924023D344B7D4C852C7740EACB8850', X'E0F63EE71ABC5D4D8AE096B37CD1B6EC', X'E0F63EE71ABC5D4D8AE096B37CD1B6EC', 5, NULL, NULL, 2, NULL, 0, 'Slice or quarter the tomatoes, depending on their size and spread on top of the feta cheese mixture. Fold up the sides of the filo to create a rectangular tart with an edge. Make sure to brush any exposed dough with some of the melted butter or olive oil.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'391EFB57C0785846AA8E5F411FF5DB70', X'E0F63EE71ABC5D4D8AE096B37CD1B6EC', X'E0F63EE71ABC5D4D8AE096B37CD1B6EC', 6, NULL, NULL, 2, NULL, 0, 'Place on a baking sheet lined with parchment paper and bake for 45 minutes or until the pastry is a light golden brown. Finish by topping with the chopped fresh parsley.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'81E827010C737D4D983F7F70526F410B', X'E0F63EE71ABC5D4D8AE096B37CD1B6EC', X'E0F63EE71ABC5D4D8AE096B37CD1B6EC', 7, NULL, NULL, 2, NULL, 0, 'Serve and enjoy!');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5DBBFC192CBB054DA27E579451F0910B', X'40295D193D5EF54A88DBF98F44F72E73', X'40295D193D5EF54A88DBF98F44F72E73', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EBB96BB55807194E986BA1FCD95BC30F', X'40295D193D5EF54A88DBF98F44F72E73', X'40295D193D5EF54A88DBF98F44F72E73', 1, NULL, NULL, 2, NULL, 0, 'Set up the boiling water bath that you will process your jars of asparagus pickles in, and turn the heat on high to bring the water to a boil.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EC9799385433DB4890EB9876DA253100', X'40295D193D5EF54A88DBF98F44F72E73', X'40295D193D5EF54A88DBF98F44F72E73', 2, NULL, NULL, 2, NULL, 0, 'Wash the asparagus. Hold each spear at either end and bend it until it snaps. From the point at which it snapped to the pointy tip of the spear is the tender portion that you will pickle.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3E11DF93A44EA94AB2FBDD799DA93F57', X'40295D193D5EF54A88DBF98F44F72E73', X'40295D193D5EF54A88DBF98F44F72E73', 3, NULL, NULL, 2, NULL, 0, 'If necessary, trim the bottom ends of the tender asparagus spears so that none of them is longer than 6 inches.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4EFC0ACAA62C754694C9518677919765', X'40295D193D5EF54A88DBF98F44F72E73', X'40295D193D5EF54A88DBF98F44F72E73', 4, NULL, NULL, 2, NULL, 0, 'Slice off the ends of the onion and peel it. Cut the onion in half lengthwise and then slice the halves into slivers.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'27F71844F410F24A940050328F295DFA', X'40295D193D5EF54A88DBF98F44F72E73', X'40295D193D5EF54A88DBF98F44F72E73', 5, NULL, NULL, 2, NULL, 0, 'Peel and smash the garlic.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D6CF854F78A192409977539E8197BADF', X'40295D193D5EF54A88DBF98F44F72E73', X'40295D193D5EF54A88DBF98F44F72E73', 6, NULL, NULL, 2, NULL, 0, 'Pierce fresh chile peppers with the tip of a knife, or break dried chile peppers in half.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E3ED326C7A926640B52A749874250486', X'40295D193D5EF54A88DBF98F44F72E73', X'40295D193D5EF54A88DBF98F44F72E73', 7, NULL, NULL, 2, NULL, 0, 'Combine the vinegar, water, sugar or honey, salt, and spices in a medium pot. Bring to a boil over high heat.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'942E746793D7A64E8EF9D77803FEF6CC', X'40295D193D5EF54A88DBF98F44F72E73', X'40295D193D5EF54A88DBF98F44F72E73', 8, NULL, NULL, 2, NULL, 0, 'Reduce the heat and simmer for 10 minutes to release the flavors of the spices.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6D1DB5556AB8434D93B093A61DC499E3', X'40295D193D5EF54A88DBF98F44F72E73', X'40295D193D5EF54A88DBF98F44F72E73', 9, NULL, NULL, 2, NULL, 0, 'While the vinegar and spice brine is simmering, load the jars. Divide the onion slivers, hot peppers and garlic between two quart-sized canning jars. It is not necessary to sterilize the jars for this recipe, but they should be scrupulously clean.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B82BE600ECC5B14EA7F0295CF63AC39E', X'40295D193D5EF54A88DBF98F44F72E73', X'40295D193D5EF54A88DBF98F44F72E73', 10, NULL, NULL, 2, NULL, 0, 'Place one of the jars on its side (it''s easier to tightly load in the asparagus spears that way). Lay the asparagus in with the pointy tip end facing the rim of the jar. Keep adding more spears until it is impossible to fit in even one more: the asparagus will shrink a little during canning, and packing the spears in tightly keeps them from floating up out of the brine.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A0985E0B7325B544A33E98B7AC87A622', X'40295D193D5EF54A88DBF98F44F72E73', X'40295D193D5EF54A88DBF98F44F72E73', 11, NULL, NULL, 2, NULL, 0, 'Repeat with the other jar.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A8C2E55494F9A74A91A00AD51B5A2838', X'40295D193D5EF54A88DBF98F44F72E73', X'40295D193D5EF54A88DBF98F44F72E73', 12, NULL, NULL, 2, NULL, 0, 'Pour the hot brine over the asparagus spears. They should be completely covered by the liquid but still have at least 1/2-inch of space between the surface of the brine and the rims of the jars.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'302937CC596AA642B58D944F90569C7A', X'40295D193D5EF54A88DBF98F44F72E73', X'40295D193D5EF54A88DBF98F44F72E73', 13, NULL, NULL, 2, NULL, 0, 'Screw on canning lids and process in the boiling water bath for 20 minutes.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E9270DD0340C2245A409074A9BD34843', X'40295D193D5EF54A88DBF98F44F72E73', X'40295D193D5EF54A88DBF98F44F72E73', 14, NULL, NULL, 2, NULL, 0, 'Wait at least a week before tasting. It takes that long for the flavors to combine and mellow.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D1D2887BB15B1D44AAD41456F38FAC1C', X'A4C139BFFE6CFE4BAC0C996E29C587DA', X'A4C139BFFE6CFE4BAC0C996E29C587DA', 0, NULL, NULL, 2, NULL, 0, 'In a large pot, cover the lentils with an ample amount of water and bring to a boil. Add salt and pepper. Once the lentils have boiled, reduce heat to a simmer and let sit for 45 minutes.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C7C5261A47A91A4696FC739EFB1A6F4A', X'A4C139BFFE6CFE4BAC0C996E29C587DA', X'A4C139BFFE6CFE4BAC0C996E29C587DA', 1, NULL, NULL, 2, NULL, 0, 'About 15 minutes before the lentils being done, saute onion and garlic in 2 tablespoons of olive oil until soft. Keep on low until lentils are finished cooking.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3B8B8F137C652B49AA6339BDA62556A5', X'A4C139BFFE6CFE4BAC0C996E29C587DA', X'A4C139BFFE6CFE4BAC0C996E29C587DA', 2, NULL, NULL, 2, NULL, 0, 'Once lentils are done, drain excess water and allow to sit uncovered for 5 minutes to cool.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DCF9D7C181A87B46A7BA21AD3FF1C9FB', X'A4C139BFFE6CFE4BAC0C996E29C587DA', X'A4C139BFFE6CFE4BAC0C996E29C587DA', 3, NULL, NULL, 2, NULL, 0, 'Add onions and garlic and combine well. Be careful not to crush the lentils!');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'65412AC9457B454A8C1E3B4A57491B3C', X'A4C139BFFE6CFE4BAC0C996E29C587DA', X'A4C139BFFE6CFE4BAC0C996E29C587DA', 4, NULL, NULL, 2, NULL, 0, 'Add olive oil, lemon juice, and parsley. Serve immediately or allow to chill and serve. Great the next day also!');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E593935994B7F0409573EB3170857654', X'ACE0999CCB4960438A004C179DACBCD5', X'ACE0999CCB4960438A004C179DACBCD5', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BF0DE02AE9558241BB1BAB621BD8E2BA', X'ACE0999CCB4960438A004C179DACBCD5', X'ACE0999CCB4960438A004C179DACBCD5', 1, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 400 F.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4CDF8E5A164AE94F8E0E419B7DE02066', X'ACE0999CCB4960438A004C179DACBCD5', X'ACE0999CCB4960438A004C179DACBCD5', 2, NULL, NULL, 2, NULL, 0, 'Wrap the beets tightly in aluminum foil, place on a baking sheet to catch any drips and roast in the oven for 45 minutes to an hour or until the beets can be easily pierced with the tip of a knife. Remove from the oven and allow to cool enough to be handled. Peel the beets (the peel will come off easily with a paring knife) and dice.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'75422853A996A94C9822C11B473EB141', X'ACE0999CCB4960438A004C179DACBCD5', X'ACE0999CCB4960438A004C179DACBCD5', 3, NULL, NULL, 2, NULL, 0, 'Make the dressing by whisking together the olive oil, lemon juice, mayonnaise or mustard (to help emulsify the dressing), honey and sumac in a bowl. Season with salt and pepper to taste.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'61D80B88A0491D429D768B7785FF6467', X'ACE0999CCB4960438A004C179DACBCD5', X'ACE0999CCB4960438A004C179DACBCD5', 4, NULL, NULL, 2, NULL, 0, 'To assemble the salads, divide the salad greens evenly on four plates, top with an equal amount of sliced red onions, diced beets, and crumbled goat cheese.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3B7FEA0171CDEC4ABA141DFC3C7DEEB0', X'ACE0999CCB4960438A004C179DACBCD5', X'ACE0999CCB4960438A004C179DACBCD5', 5, NULL, NULL, 2, NULL, 0, 'Drizzle with the dressing and serve with slices of pita bread.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BCFC097C17B6BA47B04B3B7EE26194A8', X'ACE0999CCB4960438A004C179DACBCD5', X'ACE0999CCB4960438A004C179DACBCD5', 6, NULL, NULL, 2, NULL, 0, 'Enjoy!');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'ECABAED8B260654CBE06DBB66F045B8B', X'CDA5EBEAB2038949815C0D7445CBC967', X'CDA5EBEAB2038949815C0D7445CBC967', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7608125DC8A4A94B99991CA9EAD6D596', X'CDA5EBEAB2038949815C0D7445CBC967', X'CDA5EBEAB2038949815C0D7445CBC967', 1, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 400 F.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'233688B42EB55940BF859D585FA0ADE1', X'CDA5EBEAB2038949815C0D7445CBC967', X'CDA5EBEAB2038949815C0D7445CBC967', 2, NULL, NULL, 2, NULL, 0, 'Split the squash in half lengthwise, scoop out the seeds, and rub with 1 tablespoon of the olive oil.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0FE6BAAACD603F498EB69BEE42AFED3F', X'CDA5EBEAB2038949815C0D7445CBC967', X'CDA5EBEAB2038949815C0D7445CBC967', 3, NULL, NULL, 2, NULL, 0, 'Place on a baking sheet lined with parchment paper and roast for 45 minutes or until the squash is tender.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1D5AAE59744679418D25D2FEBCD8CCEB', X'CDA5EBEAB2038949815C0D7445CBC967', X'CDA5EBEAB2038949815C0D7445CBC967', 4, NULL, NULL, 2, NULL, 0, 'Toss the chickpeas with the remaining tablespoon of olive oil.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B726DDD425838644AEDD20679A9571E6', X'CDA5EBEAB2038949815C0D7445CBC967', X'CDA5EBEAB2038949815C0D7445CBC967', 5, NULL, NULL, 2, NULL, 0, 'When the squash has cooked for 25 minutes, add the chickpeas to the baking sheet for the remaining 20 minutes.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'29AAE6A7F950264AADC9F71C3473C2ED', X'CDA5EBEAB2038949815C0D7445CBC967', X'CDA5EBEAB2038949815C0D7445CBC967', 6, NULL, NULL, 2, NULL, 0, 'Make the tahini sauce by adding the garlic clove, sesame paste, Greek yogurt, lemon juice and water to a small food processor. Puree until completely smooth and season with salt and black pepper to taste. If you''d like the dressing a little thinner, continue adding water, 1 teaspoon at a time, until you have the desired consistency.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2E1F18A34CBB2548A9226BD9C887E5E5', X'CDA5EBEAB2038949815C0D7445CBC967', X'CDA5EBEAB2038949815C0D7445CBC967', 7, NULL, NULL, 2, NULL, 0, 'Serve each half of the roasted squash with half the chickpeas. Sprinkle with the za''atar and drizzle on the Greek yogurt tahini sauce. Serve over a bed of baby spinach or cooked rice.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A31811417BF1554FA6E7179B20C877C2', X'34B9C5B44ADE2248A710D31874820EF7', X'34B9C5B44ADE2248A710D31874820EF7', 0, NULL, NULL, 2, NULL, 0, 'In a medium saucepan, combine the pumpkin puree (be sure to use plain pumpkin and not pumpkin pie filling), clove, cinnamon, ginger, allspice, sugar, lemon juice, salt, and vanilla extract on medium-high heat, stirring constantly. Bring the mixture to a boil and then reduce the heat to low. Allow the mixture to simmer for about 45 to 60 minutes. Check and stir frequently to prevent it from sticking to the bottom of the pan. If this occurs, lower the heat even further. When making jams and jellies, it is best to use a non-stick pan instead of stainless steel or copper bottom pans to avoid a reaction with the metals.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9F7A2108DDF1C7488947395101F4F5CF', X'34B9C5B44ADE2248A710D31874820EF7', X'34B9C5B44ADE2248A710D31874820EF7', 1, NULL, NULL, 2, NULL, 0, 'After 45 to 60 minutes, the mixture will be thick and a clear orange color?almost translucent. Taste the jam to see if it needs any additional salt. Then pour it into sterilized jars and seal once cooled. Note that if you are not preserving, you can also place the jam in unsterilized containers and store in the refrigerator but be sure to use within a week or two.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D2B640D469A50943ACAFB812749AA105', X'34B9C5B44ADE2248A710D31874820EF7', X'34B9C5B44ADE2248A710D31874820EF7', 2, NULL, NULL, 2, NULL, 0, 'If you are a first-time canner, it is important that you follow canning safety procedures very carefully. If not, you may put yourself or others at risk of food poisoning and also risking premature spoilage.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9CB441BE74357F4B84C00B0872493F6B', X'34B9C5B44ADE2248A710D31874820EF7', X'34B9C5B44ADE2248A710D31874820EF7', 3, NULL, NULL, 2, NULL, 0, 'Note that if using fresh pumpkin, choose one that is anywhere from 2 to 3 lbs. and roast for an hour in a 400 F oven until tender. Scoop out the seeds and then spoon the flesh into the pot as you would with canned pumpkin puree.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4A63B603F84C354E9C1236C724EECD71', X'34B9C5B44ADE2248A710D31874820EF7', X'34B9C5B44ADE2248A710D31874820EF7', 4, NULL, NULL, 2, NULL, 0, 'For some fun variations, try substituting pure maple syrup for some of the granulated sugar for a more autumnal flavor.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F473D8AAB305174FA90F9F3347E98106', X'4EFCBA599E20394DBDE21ED41AB1E566', X'4EFCBA599E20394DBDE21ED41AB1E566', 0, NULL, NULL, 2, NULL, 0, 'Add the potatoes to a large pot of salted boiling water and cook for about 10 minutes or until just fork tender.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'96F39C3889FDB0419B0BB2DFB2FEB1CA', X'4EFCBA599E20394DBDE21ED41AB1E566', X'4EFCBA599E20394DBDE21ED41AB1E566', 1, NULL, NULL, 2, NULL, 0, 'If you''re using the small potatoes, there''s no need to peel them. Or you can choose to peel them after they''ve cooked and cooled as the peel will come off easier then.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'73315A23468DB84B8367F741C01EABBE', X'4EFCBA599E20394DBDE21ED41AB1E566', X'4EFCBA599E20394DBDE21ED41AB1E566', 2, NULL, NULL, 2, NULL, 0, 'Drain the potatoes and slice in half or quarters depending on the size.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DCC2772FE2A40B4FBCACF97E56A90851', X'4EFCBA599E20394DBDE21ED41AB1E566', X'4EFCBA599E20394DBDE21ED41AB1E566', 3, NULL, NULL, 2, NULL, 0, 'Add the two tablespoons of olive oil and minced garlic to a cast iron pan and saute for a minute, just until the garlic is slightly translucent. Do not allow the garlic to burn or it will become bitter.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'54E795B834B9CE488E56B841E42C4ECC', X'4EFCBA599E20394DBDE21ED41AB1E566', X'4EFCBA599E20394DBDE21ED41AB1E566', 4, NULL, NULL, 2, NULL, 0, 'Add the cooked potatoes to the pan and stir to coat them in the olive oil and garlic.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'44F37A11F5BA2F4D92A8C43240A77689', X'4EFCBA599E20394DBDE21ED41AB1E566', X'4EFCBA599E20394DBDE21ED41AB1E566', 5, NULL, NULL, 2, NULL, 0, 'Saute on medium heat for about 15 minutes, stirring occasionally.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F21853231D45D94FB831AB9BEDFEF043', X'4EFCBA599E20394DBDE21ED41AB1E566', X'4EFCBA599E20394DBDE21ED41AB1E566', 6, NULL, NULL, 2, NULL, 0, 'Stir in the chili paste and continue sauteing for an additional 15 minutes.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D06E8F42076AD745B375C920A647162E', X'4EFCBA599E20394DBDE21ED41AB1E566', X'4EFCBA599E20394DBDE21ED41AB1E566', 7, NULL, NULL, 2, NULL, 0, 'Stir in the cilantro and season with additional salt, if needed.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'571501AA8594CD41BD4609B0A4AF21F1', X'C59029FB8C78624D883A3FB6233860E6', X'C59029FB8C78624D883A3FB6233860E6', 0, NULL, NULL, 2, NULL, 0, 'To make a basic Greek yogurt style tahini sauce, add the garlic clove, sesame paste, Greek yogurt, lemon juice, and water to a small food processor and puree until smooth.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6F2B75DFA9046347996538C8FD47E2B6', X'C59029FB8C78624D883A3FB6233860E6', X'C59029FB8C78624D883A3FB6233860E6', 1, NULL, NULL, 2, NULL, 0, 'Season with salt and pepper to taste. If you''d like the dressing a little thinner, continue adding water, 1 teaspoon at a time, until you have the desired consistency.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'55CBD7234E089340A49351EE091355A2', X'C59029FB8C78624D883A3FB6233860E6', X'C59029FB8C78624D883A3FB6233860E6', 2, NULL, NULL, 2, NULL, 0, 'To make a cilantro version, add 1 to 2 teaspoons of chopped cilantro to the food processor.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1326273932BCB04F97EC682656BFEF4B', X'F9A6D17AEC289E4895ADB19D583097A5', X'F9A6D17AEC289E4895ADB19D583097A5', 0, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 400 degrees.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FA83021B4A3FBD48A74C88ABBC830699', X'F9A6D17AEC289E4895ADB19D583097A5', X'F9A6D17AEC289E4895ADB19D583097A5', 1, NULL, NULL, 2, NULL, 0, 'Add the beet, chickpeas, garlic, tahini sauce, flour, parsley and cumin to a food processor. Pulse until the mixture comes together but it doesn''t need to be a smooth paste. Season with salt and pepper.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2F511346995A0A489686A3EE3EFCB48C', X'F9A6D17AEC289E4895ADB19D583097A5', X'F9A6D17AEC289E4895ADB19D583097A5', 2, NULL, NULL, 2, NULL, 0, 'Form 1 oz. balls (use can use a scoop) and place on a baking sheet lined with parchment paper. Bake for 30 minutes.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8DD65C2E73AD12498B284C88044A60FD', X'F9A6D17AEC289E4895ADB19D583097A5', X'F9A6D17AEC289E4895ADB19D583097A5', 3, NULL, NULL, 2, NULL, 0, 'Serve over greens, drizzled with tahini sauce. You can also serve this in a pita with salad or over pasta instead of meatballs.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FC177A6CFE6C734385E71CA78F557C43', X'CACF297EE0FA02479743ED8842228295', X'CACF297EE0FA02479743ED8842228295', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C56485D5B022C94C8A7F47A267220C57', X'CACF297EE0FA02479743ED8842228295', X'CACF297EE0FA02479743ED8842228295', 1, NULL, NULL, 2, NULL, 0, 'Add the unsalted butter and the vermicelli to a large saut pan or pot that has a tight-fitting lid. Brown the noodles on medium-high heat, stirring often, and be careful not to burn them.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D035B77463F0004BA14BBED1E6C44797', X'CACF297EE0FA02479743ED8842228295', X'CACF297EE0FA02479743ED8842228295', 2, NULL, NULL, 2, NULL, 0, 'Once the noodles are browned, add the rice and water to the pot and bring to a boil. Cover, reduce the heat down to low, and simmer for about 20 minutes or until the water has been absorbed and the rice is tender.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1F9F6D922228E840B5129E2F1697BD05', X'CACF297EE0FA02479743ED8842228295', X'CACF297EE0FA02479743ED8842228295', 3, NULL, NULL, 2, NULL, 0, 'Fluff with a fork, season with salt and pepper, and add in chopped parsley or toasted pine nuts, if using. Serve as a side dish to fish, chicken, or beef.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'97CFFC9FE2ECFB40AA6AEFA609B4345A', X'371DB9120F75AA4B936E9F230E5C3881', X'371DB9120F75AA4B936E9F230E5C3881', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7919E90C10BD1246B27DA3ABC5657B67', X'371DB9120F75AA4B936E9F230E5C3881', X'371DB9120F75AA4B936E9F230E5C3881', 1, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 350 F.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'ABC5A53E8E952C4AB582DF112B3F1D13', X'371DB9120F75AA4B936E9F230E5C3881', X'371DB9120F75AA4B936E9F230E5C3881', 2, NULL, NULL, 2, NULL, 0, 'Make the cheesecake filling by combining the cream cheese with the sugar, egg, lemon juice, sour cream (or Greek yogurt), flour and salt until fully mixed.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'90710AAD05FC2E4AB5CC1A512840766D', X'371DB9120F75AA4B936E9F230E5C3881', X'371DB9120F75AA4B936E9F230E5C3881', 3, NULL, NULL, 2, NULL, 0, 'Spread a layer of phyllo dough over each pan and brush with the melted butter. Add 8 more sheets to each pan, with melted butter brushed over each sheet. Allow the dough sheets to overhang the pan.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'25AB6C32ABC912469A784D4E5DB0E516', X'371DB9120F75AA4B936E9F230E5C3881', X'371DB9120F75AA4B936E9F230E5C3881', 4, NULL, NULL, 2, NULL, 0, 'Pour the cheesecake filling equally into each pan and sprinkle on the chopped pistachios. Fold the edges of the phyllo dough over the cheesecake. Don''t worry about making it perfect. It will crumple up naturally. Brush on a little more melted butter and place on a baking sheet. Bake in the oven for 20 minutes, then cover the browned phyllo crust loosely with aluminum foil and continue baking for an additional 20 minutes.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1B96FCB49B734A418A73F45F2616F0F5', X'371DB9120F75AA4B936E9F230E5C3881', X'371DB9120F75AA4B936E9F230E5C3881', 5, NULL, NULL, 2, NULL, 0, 'Make the syrup by adding the sugar, water, honey and lemon juice to a pot and simmering gently until the sugar dissolves. When the cheesecakes are done, remove from the oven and let cool just until you can remove them from the spring-form. Pour the honey syrup over each one and let sit for a few minutes to let it soak in.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F42D09244C54594DBF4D4B77ABBE8C79', X'371DB9120F75AA4B936E9F230E5C3881', X'371DB9120F75AA4B936E9F230E5C3881', 6, NULL, NULL, 2, NULL, 0, 'Serve and enjoy!');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9FD6FDCDC9319E419C9FB8E520F01007', X'795393B8A3B622409598211B719A9930', X'795393B8A3B622409598211B719A9930', 0, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 400 F.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E5979D6A5EC6184DAD8D3536E410E6FE', X'795393B8A3B622409598211B719A9930', X'795393B8A3B622409598211B719A9930', 1, NULL, NULL, 2, NULL, 0, 'Unroll the puff pastry sheets and place them on greased baking sheets or ones lined with parchment paper. Bake for 15 minutes. You want the puff pastry to be golden and puffed. Remove from oven but do not turn the oven off.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A2BAB8261ABAE34D87ACB8B5BEADF154', X'795393B8A3B622409598211B719A9930', X'795393B8A3B622409598211B719A9930', 2, NULL, NULL, 2, NULL, 0, 'Break the puff pastry into small pieces (about 1 to 2 inches). Place the broken pieces into a large bowl along with the nuts and coconut flakes. Be sure that they are mixed well and evenly distributed. Place the nuts and pastry mixture into a greased 9 x 12 baking dish.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4A9389BBE602D646BC7E4221C4E6F642', X'795393B8A3B622409598211B719A9930', X'795393B8A3B622409598211B719A9930', 3, NULL, NULL, 2, NULL, 0, 'In a saucepan, heat the sweetened condensed milk, vanilla, and water on medium heat. Allow to cook for about 3 to 5 minutes. Remove from the heat and allow to cool.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CA7D6D7FC28A664B88B185DB1D188CE6', X'795393B8A3B622409598211B719A9930', X'795393B8A3B622409598211B719A9930', 4, NULL, NULL, 2, NULL, 0, 'Pour the milk mixture over the puff pastry nut mixture. Pour the cream on top and bake, uncovered, for 15 minutes.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'994198F17216C74A86FA98238492837F', X'795393B8A3B622409598211B719A9930', X'795393B8A3B622409598211B719A9930', 5, NULL, NULL, 2, NULL, 0, 'Serve warm or at room temperature.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8CDCD96B06936841BEC7A3DBF6712E46', X'5351F583806F6042BE99D037A1A4D90F', X'5351F583806F6042BE99D037A1A4D90F', 0, NULL, NULL, 2, NULL, 0, 'Pre-heat the oven to 350 F and butter and flour a bundt or tube style pan.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4EB05E80BBABF543ACA54ADBCE9B8188', X'5351F583806F6042BE99D037A1A4D90F', X'5351F583806F6042BE99D037A1A4D90F', 1, NULL, NULL, 2, NULL, 0, 'Using a hand or stand mixer, beat together the eggs and sugar until lightened in color.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'930ACC26C08D7A43824224C30A5C7E93', X'5351F583806F6042BE99D037A1A4D90F', X'5351F583806F6042BE99D037A1A4D90F', 2, NULL, NULL, 2, NULL, 0, 'Beat in the butter, vanilla, and yogurt. Stir in the food coloring, if using.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9D5B4F911D839945AA6470BCEE5E0B83', X'5351F583806F6042BE99D037A1A4D90F', X'5351F583806F6042BE99D037A1A4D90F', 3, NULL, NULL, 2, NULL, 0, 'Add the pistachios to a food processor and pulse until ground');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'921222A8C221AC4F83FBE932EB31C5F9', X'5351F583806F6042BE99D037A1A4D90F', X'5351F583806F6042BE99D037A1A4D90F', 4, NULL, NULL, 2, NULL, 0, 'In a separate bowl, sift together the flour, ground pistachios, baking powder, baking soda, and salt.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4840F3253802324AB4BF9CDCB0B19D75', X'5351F583806F6042BE99D037A1A4D90F', X'5351F583806F6042BE99D037A1A4D90F', 5, NULL, NULL, 2, NULL, 0, 'Beat the dry ingredients into the wet and pour the batter into the prepared pan.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'806CB68D9101CC4C88B6EBFBC25D0F27', X'5351F583806F6042BE99D037A1A4D90F', X'5351F583806F6042BE99D037A1A4D90F', 6, NULL, NULL, 2, NULL, 0, 'Bake for 50 to 55 minutes or until a toothpick comes out clean.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'044B6FB33C6C3C4C955DB14F641066B8', X'5351F583806F6042BE99D037A1A4D90F', X'5351F583806F6042BE99D037A1A4D90F', 7, NULL, NULL, 2, NULL, 0, 'Make the glaze by whisking together the powdered sugar, milk, cardamom, and salt until smooth.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'45FF9628DFFAEC4AB19036482BC9AE0B', X'5351F583806F6042BE99D037A1A4D90F', X'5351F583806F6042BE99D037A1A4D90F', 8, NULL, NULL, 2, NULL, 0, 'Drizzle over the cooled cake and sprinkle on some additional chopped pistachios, if desired.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C4CB765BEBD3064EAE0C2221A8F41051', X'52F2D5171AC1804582AD95DA34987538', X'52F2D5171AC1804582AD95DA34987538', 0, NULL, NULL, 2, NULL, 0, 'Pre-heat the oven to 350 F and butter and flour a 9-inch cake pan. You can also bake this in a standard loaf pan.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1758817FED777342841700C1A0AA96E1', X'52F2D5171AC1804582AD95DA34987538', X'52F2D5171AC1804582AD95DA34987538', 1, NULL, NULL, 2, NULL, 0, 'Using a hand or stand mixer, cream together the eggs, sugar, and honey until fluffy and lightened in color. Beat in the butter, buttermilk, and vanilla.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CC8FC58B85648E429C9EA1EB03A8621D', X'52F2D5171AC1804582AD95DA34987538', X'52F2D5171AC1804582AD95DA34987538', 2, NULL, NULL, 2, NULL, 0, 'In a separate bowl, sift together the all-purpose flour, baking soda, baking powder, cinnamon, and salt. Mix the dry ingredients into the wet batter.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'77E60DD6ED6D054C83B9E409E51F9321', X'52F2D5171AC1804582AD95DA34987538', X'52F2D5171AC1804582AD95DA34987538', 3, NULL, NULL, 2, NULL, 0, 'Peel, core, and dice the apple. Tip: Toss the apple pieces with a teaspoon of flour to prevent them from sinking to the bottom of the cake.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'340EBCD5D597B04CBFD7C4F51D9D8A0E', X'52F2D5171AC1804582AD95DA34987538', X'52F2D5171AC1804582AD95DA34987538', 4, NULL, NULL, 2, NULL, 0, 'Fold the apple pieces into the batter and pour into the prepared cake pan. Bake for approximately 30 minutes or until a cake tester or toothpick comes out clean. Allow cooling before removing from the pan.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'92755C7826C9E34BBDACCA192E356948', X'BF7ACFC615028141BBCE39C5E2067F49', X'BF7ACFC615028141BBCE39C5E2067F49', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7738091E700A5E4FB04DD4B6B95C6F64', X'BF7ACFC615028141BBCE39C5E2067F49', X'BF7ACFC615028141BBCE39C5E2067F49', 1, NULL, NULL, 2, NULL, 0, 'Preheat oven to 375 F. Spray a 9x13-inch cake pan with non-stick cooking spray.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'177586991473024898724D3BB4243D15', X'BF7ACFC615028141BBCE39C5E2067F49', X'BF7ACFC615028141BBCE39C5E2067F49', 2, NULL, NULL, 2, NULL, 0, 'In a bowl, combine butter, egg yolks, coffee, sugar, and flour with your hands until the mixture is crumbly.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'01E194B5BB08CE46AADE336955C1E0D0', X'BF7ACFC615028141BBCE39C5E2067F49', X'BF7ACFC615028141BBCE39C5E2067F49', 3, NULL, NULL, 2, NULL, 0, 'Press the mixture into the bottom of the cake pan. This forms the base for the cake.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0CFE545402F7BB4194BC2ECA9273AA35', X'BF7ACFC615028141BBCE39C5E2067F49', X'BF7ACFC615028141BBCE39C5E2067F49', 4, NULL, NULL, 2, NULL, 0, 'In a separate bowl, combine eggs, white cheese, sugar, vanilla sugar, and flour.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'850B6F3F0B35DA4EB5D82D03C58A7777', X'BF7ACFC615028141BBCE39C5E2067F49', X'BF7ACFC615028141BBCE39C5E2067F49', 5, NULL, NULL, 2, NULL, 0, 'Pour this mixture on top of the cake base.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F129874025FC3C4DB2F13AFA368EC5B7', X'BF7ACFC615028141BBCE39C5E2067F49', X'BF7ACFC615028141BBCE39C5E2067F49', 6, NULL, NULL, 2, NULL, 0, 'Bake for 30 minutes or until the cheese does not wiggle. Remove cake and cool for 30 minutes.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B6DC68E6C39C3A4AA9EFA31F2850FDF1', X'BF7ACFC615028141BBCE39C5E2067F49', X'BF7ACFC615028141BBCE39C5E2067F49', 7, NULL, NULL, 2, NULL, 0, 'Turn oven temperature down to 200 F.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4D07B9BA61C7714EAFF6ACBBEA7B606A', X'BF7ACFC615028141BBCE39C5E2067F49', X'BF7ACFC615028141BBCE39C5E2067F49', 8, NULL, NULL, 2, NULL, 0, 'In a small bowl, mix sour cream with vanilla sugar and sugar. Spread evenly over the cooled cake.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9A61E33012BAF54C8B1BD234AFA85192', X'BF7ACFC615028141BBCE39C5E2067F49', X'BF7ACFC615028141BBCE39C5E2067F49', 9, NULL, NULL, 2, NULL, 0, 'Bake again for 20 minutes.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'23565951BC64D0439144F2C8336563D3', X'BF7ACFC615028141BBCE39C5E2067F49', X'BF7ACFC615028141BBCE39C5E2067F49', 10, NULL, NULL, 2, NULL, 0, 'Refrigerate until fully cooled and firm and then serve!');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7E61F9BCFA15FF488BBEDE851047EF93', X'913A0E61407BFB4AA4FEB1014802D777', X'913A0E61407BFB4AA4FEB1014802D777', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'418FA4CE4648D6418F98D44509ECF553', X'913A0E61407BFB4AA4FEB1014802D777', X'913A0E61407BFB4AA4FEB1014802D777', 1, NULL, NULL, 2, NULL, 0, 'Combine the sesame paste, butter, vanilla, confectioner''s sugar and salt in a bowl and mix until thoroughly combined.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DD6E227F866DFA489CF5E67BD80D27A5', X'913A0E61407BFB4AA4FEB1014802D777', X'913A0E61407BFB4AA4FEB1014802D777', 2, NULL, NULL, 2, NULL, 0, 'Use a small scoop (approximately 1 ounce) or your hands to form balls and place them on a dish lined with parchment paper.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2ACD8481986E55429576F8D628CF36AD', X'913A0E61407BFB4AA4FEB1014802D777', X'913A0E61407BFB4AA4FEB1014802D777', 3, NULL, NULL, 2, NULL, 0, 'Insert a toothpick into each ball to make them easier to dip in the chocolate.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4FB672B6E2DB3243925497EE3EC16AD6', X'913A0E61407BFB4AA4FEB1014802D777', X'913A0E61407BFB4AA4FEB1014802D777', 4, NULL, NULL, 2, NULL, 0, 'Place the formed candies in the freezer to harden while you make the coating.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D562887E822D204C88D2511BB146D315', X'913A0E61407BFB4AA4FEB1014802D777', X'913A0E61407BFB4AA4FEB1014802D777', 5, NULL, NULL, 2, NULL, 0, 'Place the chocolate chips and butter in a bowl set over a pot of simmering water and stir occasionally until melted.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D19A76602A36E245B42B46BC74ECAEC6', X'913A0E61407BFB4AA4FEB1014802D777', X'913A0E61407BFB4AA4FEB1014802D777', 6, NULL, NULL, 2, NULL, 0, 'Stir in the butter.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9347411E61E54D48A108A48B0CC51DBC', X'913A0E61407BFB4AA4FEB1014802D777', X'913A0E61407BFB4AA4FEB1014802D777', 7, NULL, NULL, 2, NULL, 0, 'At this point, the chocolate may seize up a little. If so, add the tablespoon of milk or cream and continue stirring until it''s smoothed out again.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'684A44C0C341A5429B7627006738805C', X'913A0E61407BFB4AA4FEB1014802D777', X'913A0E61407BFB4AA4FEB1014802D777', 8, NULL, NULL, 2, NULL, 0, 'Remove from the heat and allow to cool for about 10 minutes.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F6BF1D3F5AC836499D2DE41050FB1414', X'913A0E61407BFB4AA4FEB1014802D777', X'913A0E61407BFB4AA4FEB1014802D777', 9, NULL, NULL, 2, NULL, 0, 'Remove the peanut butter balls from the freezer and, gripping by the toothpick, dip each one about 3/4 into the chocolate.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8737B9895F852547B9C2D87C124BBAE4', X'913A0E61407BFB4AA4FEB1014802D777', X'913A0E61407BFB4AA4FEB1014802D777', 10, NULL, NULL, 2, NULL, 0, 'Place them back on the parchment paper, sprinkle on a bit of sea salt and refrigerate for about an hour for the chocolate to set up.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6018D79CCB635D46AE430D1E2EB0CCE9', X'913A0E61407BFB4AA4FEB1014802D777', X'913A0E61407BFB4AA4FEB1014802D777', 11, NULL, NULL, 2, NULL, 0, 'Serve them with or without the toothpicks.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'17E28B06F5A18240AC52C5E7023E0F38', X'913A0E61407BFB4AA4FEB1014802D777', X'913A0E61407BFB4AA4FEB1014802D777', 12, NULL, NULL, 2, NULL, 0, 'Enjoy!');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5F0810A9ADD7F54886B74FF0BFB7296C', X'E4034D5BB1063743ADA73DC90DADE2D1', X'E4034D5BB1063743ADA73DC90DADE2D1', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4F83F5BD5B962A499DDBED894309069C', X'E4034D5BB1063743ADA73DC90DADE2D1', X'E4034D5BB1063743ADA73DC90DADE2D1', 1, NULL, NULL, 2, NULL, 0, 'Cut the tips of the fig stems off, slice in half and add to a blender or small food processor.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'188A8DCA0654C64C8243E54D159A85BD', X'E4034D5BB1063743ADA73DC90DADE2D1', X'E4034D5BB1063743ADA73DC90DADE2D1', 2, NULL, NULL, 2, NULL, 0, 'Add the banana half, Greek-style yogurt, honey, salt, and 1/4 cup of the milk to the blender.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4E80132071B8D54F9C13785C69B9CA40', X'E4034D5BB1063743ADA73DC90DADE2D1', X'E4034D5BB1063743ADA73DC90DADE2D1', 3, NULL, NULL, 2, NULL, 0, 'Puree until smooth and continue adding milk if you''d like a thinner consistency.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'71C7F205710B304793874D8BF86CA6DE', X'AE4C84B23C3236449BC22250C3200ECB', X'AE4C84B23C3236449BC22250C3200ECB', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'666700D5727E6244B7A36E8A41914139', X'AE4C84B23C3236449BC22250C3200ECB', X'AE4C84B23C3236449BC22250C3200ECB', 1, NULL, NULL, 2, NULL, 0, 'Heat the honey in a heavy-bottomed saucepan over medium heat until it reaches 240 F or the soft ball stage. (The soft ball stage is when syrup is dropped into cold water and forms a soft, flexible ball.)');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D02BC07845D5A84BA5D4B7F11D4813A2', X'AE4C84B23C3236449BC22250C3200ECB', X'AE4C84B23C3236449BC22250C3200ECB', 2, NULL, NULL, 2, NULL, 0, 'Allow the honey to cool slightly and add the vanilla and nuts.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4CF55DC8C39B8741B64FD7FADA9EEF4A', X'AE4C84B23C3236449BC22250C3200ECB', X'AE4C84B23C3236449BC22250C3200ECB', 3, NULL, NULL, 2, NULL, 0, 'Gently fold in the tahini and stir until the mixture is well blended.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'224C30C69B66BA42ACCFFD26DF07B4C2', X'AE4C84B23C3236449BC22250C3200ECB', X'AE4C84B23C3236449BC22250C3200ECB', 4, NULL, NULL, 2, NULL, 0, 'Lightly oil a 6-cup mold, loaf, or cake pan.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6F4C469B3854284888B558187D3BA88D', X'AE4C84B23C3236449BC22250C3200ECB', X'AE4C84B23C3236449BC22250C3200ECB', 5, NULL, NULL, 2, NULL, 0, 'Pour the mixture into the pan and cool completely.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A48135C5A6D8BA48A20400A82A9B7B3C', X'AE4C84B23C3236449BC22250C3200ECB', X'AE4C84B23C3236449BC22250C3200ECB', 6, NULL, NULL, 2, NULL, 0, 'Wrap the halvah well and refrigerate it for 24 to 36 hours so the halvahs characteristic crystallized texture can fully develop.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B3DBD48BAF651D4381ED0E991245CD71', X'AE4C84B23C3236449BC22250C3200ECB', X'AE4C84B23C3236449BC22250C3200ECB', 7, NULL, NULL, 2, NULL, 0, 'Cut the halvah while its cold but serve at room temperature. The halvah will keep in the refrigerator for months.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6263AC1DBFF3F64393108B8699403AC1', X'AE4C84B23C3236449BC22250C3200ECB', X'AE4C84B23C3236449BC22250C3200ECB', 8, NULL, NULL, 2, NULL, 0, 'Enjoy!');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'85B27DD861372F46AA522ED9DA55CFDC', X'3B062953D2610746BF2C3BA6F821A381', X'3B062953D2610746BF2C3BA6F821A381', 0, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 350 F / 180 C. Line a baking sheet with parchment paper.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'32B80E89406BD6469B730FB254638C10', X'3B062953D2610746BF2C3BA6F821A381', X'3B062953D2610746BF2C3BA6F821A381', 1, NULL, NULL, 2, NULL, 0, 'In a bowl, mix the egg, cheese, parsley, garlic salt, and pepper.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'90C796D1FC560E45B71C3AB8DE6E0B02', X'3B062953D2610746BF2C3BA6F821A381', X'3B062953D2610746BF2C3BA6F821A381', 2, NULL, NULL, 2, NULL, 0, 'Using a sharp knife, cut the puff pastry into 5-inch squares.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F21F8911A43A38419F1392041DE3BC33', X'3B062953D2610746BF2C3BA6F821A381', X'3B062953D2610746BF2C3BA6F821A381', 3, NULL, NULL, 2, NULL, 0, 'Place a heaping tablespoon of cheese filling in each square.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C822CE2D07C6C342980C3500991E509C', X'3B062953D2610746BF2C3BA6F821A381', X'3B062953D2610746BF2C3BA6F821A381', 4, NULL, NULL, 2, NULL, 0, 'Dampen the edges of the squares with water, and fold in half diagonally to form triangular pastries.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'85188400CA636A43BE1EE5CE8C3F492D', X'3B062953D2610746BF2C3BA6F821A381', X'3B062953D2610746BF2C3BA6F821A381', 5, NULL, NULL, 2, NULL, 0, 'Pinch the edges together to seal the filling inside.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'82B2FA2B6939E443B92EC54238026CAF', X'3B062953D2610746BF2C3BA6F821A381', X'3B062953D2610746BF2C3BA6F821A381', 6, NULL, NULL, 2, NULL, 0, 'Brush the tops of the bourekas with beaten egg and sprinkle with sesame seeds.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E0A10513385DAC4E993747B6DE2BA21A', X'3B062953D2610746BF2C3BA6F821A381', X'3B062953D2610746BF2C3BA6F821A381', 7, NULL, NULL, 2, NULL, 0, 'Bake in the preheated oven for 30 minutes, or until the pastries are puffed and golden, and the filling is cooked through. Serve immediately.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'11B95F282D79564C9A6050221B71D08A', X'838D137B33649944AD7CBC4C3D75D7DB', X'838D137B33649944AD7CBC4C3D75D7DB', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'41FE7E5AA502E6408FD2BC136928AE56', X'838D137B33649944AD7CBC4C3D75D7DB', X'838D137B33649944AD7CBC4C3D75D7DB', 1, NULL, NULL, 2, NULL, 0, 'Add the sugar to a heavy bottom pot and heat on low until it melts and starts to brown.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2521757994D3254F95ED55AA3314D0BF', X'838D137B33649944AD7CBC4C3D75D7DB', X'838D137B33649944AD7CBC4C3D75D7DB', 2, NULL, NULL, 2, NULL, 0, 'Once the sugar is bubbling and a very light golden brown, pour in the heavy cream. (Note that the mixture will bubble up.) Begin stirring it until the cream is fully incorporated and then stir in the butter. You may find that the mixture has hard lumps. Just continue stirring, on low, until they melt and you have a smooth sauce.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AF8562CACD505E40BB8AE192F157EBCD', X'838D137B33649944AD7CBC4C3D75D7DB', X'838D137B33649944AD7CBC4C3D75D7DB', 3, NULL, NULL, 2, NULL, 0, 'Stir in the tahini (sesame paste) and season with sea salt to taste. Allow the mixture to cool and then store in a lidded jar in the refrigerator for up to a week.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A0D962738DA9D741A3B76F05E6074693', X'838D137B33649944AD7CBC4C3D75D7DB', X'838D137B33649944AD7CBC4C3D75D7DB', 4, NULL, NULL, 2, NULL, 0, 'Enjoy!');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'28C7AA6B0BDEF745A8917F1236E92EC8', X'A7CA190FA337D9428336C26EAC8A2308', X'A7CA190FA337D9428336C26EAC8A2308', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D267C9037C2EF84891533DF87AD92C2B', X'A7CA190FA337D9428336C26EAC8A2308', X'A7CA190FA337D9428336C26EAC8A2308', 1, NULL, NULL, 2, NULL, 0, 'Make the tart dough by adding the all-purpose flour, sugar, salt, and butter to the bowl of a food processor. Pulse 10 to 15 times just until the mixture resembles coarse crumbs.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'914324A56A51A143B3655DD34918E03C', X'A7CA190FA337D9428336C26EAC8A2308', X'A7CA190FA337D9428336C26EAC8A2308', 2, NULL, NULL, 2, NULL, 0, 'With the machine running, pour the ice water through the feed tube and continue running until the mixture forms a dough ball.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D82904848DFA0F48B9B956DE7AC099FB', X'A7CA190FA337D9428336C26EAC8A2308', X'A7CA190FA337D9428336C26EAC8A2308', 3, NULL, NULL, 2, NULL, 0, 'Wrap the dough in plastic and refrigerate for at least one hour.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6EA676F4771F754D89597178655339F3', X'A7CA190FA337D9428336C26EAC8A2308', X'A7CA190FA337D9428336C26EAC8A2308', 4, NULL, NULL, 2, NULL, 0, 'When ready to bake, preheat the oven to 350 F.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D578855E62240045925C2636A93DB404', X'A7CA190FA337D9428336C26EAC8A2308', X'A7CA190FA337D9428336C26EAC8A2308', 5, NULL, NULL, 2, NULL, 0, 'Roll out the dough, on a well-floured surface, to about a 10-inch round.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B1A5BF29BE6FA5418DA31A4BFDFDAAD1', X'A7CA190FA337D9428336C26EAC8A2308', X'A7CA190FA337D9428336C26EAC8A2308', 6, NULL, NULL, 2, NULL, 0, 'Drape the dough over your rolling pin and transfer to a 9-inch tart pan, preferably with a removable bottom.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7AEB1781A2F7224E9918AFCBBEDB0086', X'A7CA190FA337D9428336C26EAC8A2308', X'A7CA190FA337D9428336C26EAC8A2308', 7, NULL, NULL, 2, NULL, 0, 'To make the filling, combine the mascarpone cheese, sugar, lemon juice and salt in a large bowl. Using a whisk or hand mixer, stir until the sugar dissolves and the mixture is combined.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'70AF8C18DB80D34B86DCE654D2185EE8', X'A7CA190FA337D9428336C26EAC8A2308', X'A7CA190FA337D9428336C26EAC8A2308', 8, NULL, NULL, 2, NULL, 0, 'Pour the batter into the prepared tart pan.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A5BB3B5F1B5B0C4889C8DFFFFE2E37D2', X'A7CA190FA337D9428336C26EAC8A2308', X'A7CA190FA337D9428336C26EAC8A2308', 9, NULL, NULL, 2, NULL, 0, 'Place on a baking sheet and bake for 40 to 45 minutes or until the cheese is set and the dough is a light golden brown. Allow to cool thoroughly before removing from the tart pan.?');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'09ED4E2AD219374D830C672858051360', X'A7CA190FA337D9428336C26EAC8A2308', X'A7CA190FA337D9428336C26EAC8A2308', 10, NULL, NULL, 2, NULL, 0, 'To make the topping, slice the figs and place them on top of the tart and drizzle with the honey. Serve with additional honey for people to drizzle if they like.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'43632276ADEA0442BEDF4610353487A2', X'F699B4FB758C2A43BA09495D6A6D3B4C', X'F699B4FB758C2A43BA09495D6A6D3B4C', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DB18202DC0AD8E419A10A6055DE8031B', X'F699B4FB758C2A43BA09495D6A6D3B4C', X'F699B4FB758C2A43BA09495D6A6D3B4C', 1, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 350 F/180 C. Line a baking sheet with baking paper. Set aside.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8DED0F106FA86D42B312A2AF8371F197', X'F699B4FB758C2A43BA09495D6A6D3B4C', X'F699B4FB758C2A43BA09495D6A6D3B4C', 2, NULL, NULL, 2, NULL, 0, 'Cream the butter and sugar until light and fluffy.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BC4CA27D6B853844BDCB3DEFF33F7F7C', X'F699B4FB758C2A43BA09495D6A6D3B4C', X'F699B4FB758C2A43BA09495D6A6D3B4C', 3, NULL, NULL, 2, NULL, 0, 'Sift together the flour and cocoa powder and mix into butter mixture with a wooden spoon.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'07F492CF2C07F14DBB9F4F10D3485705', X'F699B4FB758C2A43BA09495D6A6D3B4C', X'F699B4FB758C2A43BA09495D6A6D3B4C', 4, NULL, NULL, 2, NULL, 0, 'Fold in cornflakes and don''t worry if they crumble.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3F0DBFDB307E824EB00F946AEA960B61', X'F699B4FB758C2A43BA09495D6A6D3B4C', X'F699B4FB758C2A43BA09495D6A6D3B4C', 5, NULL, NULL, 2, NULL, 0, 'Roll or press 1 1/2 teaspoons of the dough into balls and flatten them slightly. Place them about 2 inches apart on the baking sheet.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DF01BA195A6F5D41879D9A5CB3601CC4', X'F699B4FB758C2A43BA09495D6A6D3B4C', X'F699B4FB758C2A43BA09495D6A6D3B4C', 6, NULL, NULL, 2, NULL, 0, 'Bake in the oven for 10 to 15 minutes. Remove from oven, and cool on a wire rack.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1702D8212AFDB64683BD8006F9AB3461', X'F699B4FB758C2A43BA09495D6A6D3B4C', X'F699B4FB758C2A43BA09495D6A6D3B4C', 7, NULL, NULL, 2, NULL, 0, 'Prepare the icing by combining the icing sugar, unsweetened cocoa powder, and water in a bowl.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AFB1477954FD6047A770B48BF63870FC', X'F699B4FB758C2A43BA09495D6A6D3B4C', X'F699B4FB758C2A43BA09495D6A6D3B4C', 8, NULL, NULL, 2, NULL, 0, 'Mix well until the mixture is free of lumps and of a creamy consistency.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8323F566F8ED27498797C53CA0ED6D32', X'F699B4FB758C2A43BA09495D6A6D3B4C', X'F699B4FB758C2A43BA09495D6A6D3B4C', 9, NULL, NULL, 2, NULL, 0, 'Spoon a little icing on each cookie, and decorate with flaked almonds.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DEF1E66EB297F448BC70BDDB09035D0F', X'BC9C2B818097044BB380766B756446B2', X'BC9C2B818097044BB380766B756446B2', 0, NULL, NULL, 2, NULL, 0, 'In a food processor, grind the biscuit cookies into finely crushed crumbs.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'862A635FCC6B4D4F8AA6F0BD0E31ECB0', X'BC9C2B818097044BB380766B756446B2', X'BC9C2B818097044BB380766B756446B2', 1, NULL, NULL, 2, NULL, 0, 'Place the butter or margarine and chocolate in the top of a double boiler set over simmering water, stirring gently until melted. Or, melt in the microwave, heating in 30-second intervals, stirring between each, until smooth. Remove from the heat.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0FAEBE2F78556E45AB56B0FE794361CE', X'BC9C2B818097044BB380766B756446B2', X'BC9C2B818097044BB380766B756446B2', 2, NULL, NULL, 2, NULL, 0, 'In a large bowl, use an electric mixer or sturdy spoon to combine the biscuit crumbs, melted chocolate mixture, sugar, cocoa powder and coffee granules.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'042AE4DDD195D346B5E83106DF8C9188', X'BC9C2B818097044BB380766B756446B2', X'BC9C2B818097044BB380766B756446B2', 3, NULL, NULL, 2, NULL, 0, 'While continuing to stir, slowly add the milk or soy milk until the batter is moist enough to hold together when shaped into balls.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B4C8D3B7F5212C46A63A1B844C1DE253', X'BC9C2B818097044BB380766B756446B2', X'BC9C2B818097044BB380766B756446B2', 4, NULL, NULL, 2, NULL, 0, 'Using clean hands, roll the mixture into walnut-sized balls. (Use about 1 tablespoon of the chocolate mixture per ball.)');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A997EB26DB4FE443ABD18786111DC9B5', X'BC9C2B818097044BB380766B756446B2', X'BC9C2B818097044BB380766B756446B2', 5, NULL, NULL, 2, NULL, 0, 'Roll each ball into powdered sugar (or a mix of powdered sugar, cocoa, and cinnamon), coconut flakes, sprinkles, or sanding sugar.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D01A7114F227EC4CB41153F8310FF02D', X'BC9C2B818097044BB380766B756446B2', X'BC9C2B818097044BB380766B756446B2', 6, NULL, NULL, 2, NULL, 0, 'Place on a plate, cover, and chill in the refrigerator until firm.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'356E4F3F49C8654995791EDA2CDFEB39', X'BC9C2B818097044BB380766B756446B2', X'BC9C2B818097044BB380766B756446B2', 7, NULL, NULL, 2, NULL, 0, 'Store leftover truffles in an airtight container in the refrigerator for 4 to 5 days.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0A2B9E6883E2D64380F849C48FBD70AB', X'FA5C02A4037B024A89F946E79784E071', X'FA5C02A4037B024A89F946E79784E071', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'20ECDBFFC9C91945812F1F24A947A435', X'FA5C02A4037B024A89F946E79784E071', X'FA5C02A4037B024A89F946E79784E071', 1, NULL, NULL, 2, NULL, 0, 'In a large bowl, whisk together the whole milk, heavy cream, honey, granulated sugar, vanilla, and salt.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AFB733CBC0B41D4FACC07E966EBED591', X'FA5C02A4037B024A89F946E79784E071', X'FA5C02A4037B024A89F946E79784E071', 2, NULL, NULL, 2, NULL, 0, 'Add the roasted pistachios to a food processor and pulse a few times until the nuts are very broken down but not powder or paste. Add the crushed pistachios to the bowl.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'44B04077A0001945ACFED9F00CE4711B', X'FA5C02A4037B024A89F946E79784E071', X'FA5C02A4037B024A89F946E79784E071', 3, NULL, NULL, 2, NULL, 0, 'Pour the mixture into your ice cream maker and follow the manufacturer''s instructions for proper use. Churn the mixture for 35 to 40 minutes.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D7B58AFFAF65D84C8A818CFA189C3A99', X'FA5C02A4037B024A89F946E79784E071', X'FA5C02A4037B024A89F946E79784E071', 4, NULL, NULL, 2, NULL, 0, 'Transfer to a freezer-safe container and freeze for at least 4 hours before serving.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8064C1592AD58B458E60747F0FD5D245', X'FA5C02A4037B024A89F946E79784E071', X'FA5C02A4037B024A89F946E79784E071', 5, NULL, NULL, 2, NULL, 0, 'Scoop and serve.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1DD14F5DE0EF4043B0CB6606A683BF08', X'43485FD6AA17354A94600D5F46804D7F', X'43485FD6AA17354A94600D5F46804D7F', 0, NULL, NULL, 2, NULL, 0, 'If your ice cream maker requires pre-freezing the bowl, do so.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F7D563EFE0E7B946AB5A61DB9300A240', X'43485FD6AA17354A94600D5F46804D7F', X'43485FD6AA17354A94600D5F46804D7F', 1, NULL, NULL, 2, NULL, 0, 'In a large bowl, whisk together the whole milk, heavy cream, granulated sugar, vanilla, salt, and sesame paste (tahini).');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B206734BD6928C41B5891BD865218303', X'43485FD6AA17354A94600D5F46804D7F', X'43485FD6AA17354A94600D5F46804D7F', 2, NULL, NULL, 2, NULL, 0, 'Pour the mixture into your ice cream maker and follow the manufacturer''s instructions to churn for 35 to 40 minutes.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4D32E4DD6E92B3469722DB1D2704B498', X'43485FD6AA17354A94600D5F46804D7F', X'43485FD6AA17354A94600D5F46804D7F', 3, NULL, NULL, 2, NULL, 0, 'Freeze for at least 4 hours before serving and garnish with the crumbled halva pieces if desired for a fun texture and flavor boost!');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0647133AD52DE148B62636DB4A438764', X'9C46C86B1F7E764D830FB96DE97D041E', X'9C46C86B1F7E764D830FB96DE97D041E', 0, NULL, NULL, 2, NULL, 0, 'Melt the semi-sweet chocolate in the microwave or a bowl set over a pot of simmering water. Allow it to cool slightly.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'150ABBD303720C4B80AA445ECCF824B9', X'9C46C86B1F7E764D830FB96DE97D041E', X'9C46C86B1F7E764D830FB96DE97D041E', 1, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 350 F and butter and flour an 8-inch square pan. Lining it with parchment paper will also help remove the brownies when they''re done.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'436685EE88D327419D5751348F7B8C13', X'9C46C86B1F7E764D830FB96DE97D041E', X'9C46C86B1F7E764D830FB96DE97D041E', 2, NULL, NULL, 2, NULL, 0, 'Add the sugar and butter to a large bowl and, using a stand or hand mixer, cream them together until the mixture has lightened in color. Beat in the eggs, vanilla and melted and cooled chocolate.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'85127C34F302884B862274B16BD6ED9C', X'9C46C86B1F7E764D830FB96DE97D041E', X'9C46C86B1F7E764D830FB96DE97D041E', 3, NULL, NULL, 2, NULL, 0, 'In a separate bowl, sift together the unsweetened cocoa powder, flour, baking powder, and salt. Add the dry ingredients into the wet and stir to combine.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FB5A43D2829DCD41A60644F7E7EAE80E', X'9C46C86B1F7E764D830FB96DE97D041E', X'9C46C86B1F7E764D830FB96DE97D041E', 4, NULL, NULL, 2, NULL, 0, 'Cut the halva into 1/2-inch pieces and sprinkle them evenly across the bottom of the prepared baking pan. Pour the brownie batter over the halva and bake for approximately 40 to 45 minutes or until the tops of the brownies are set. Cool before slicing and removing from the pan.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'452CCF6126C18748B7FE5C06DE476FA4', X'9FDB6AC0FCA4C14B92C503BDC0B5EF62', X'9FDB6AC0FCA4C14B92C503BDC0B5EF62', 0, NULL, NULL, 2, NULL, 0, 'Pre-heat the oven to 350 F. Butter a standard loaf pan and line it with a strip of parchment paper for easy removal of the bread after baking.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E8FEE5EF5EF2BF4A8C7074BDF062A39C', X'9FDB6AC0FCA4C14B92C503BDC0B5EF62', X'9FDB6AC0FCA4C14B92C503BDC0B5EF62', 1, NULL, NULL, 2, NULL, 0, 'In a large bowl combine the eggs, sugar, and honey. Using a stand or hand mixer, beat together until the sugar is dissolved. Add the butter, vanilla and orange zest and beat until fully incorporated.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'40B5DE433A85DB4D954223CB43CFF99E', X'9FDB6AC0FCA4C14B92C503BDC0B5EF62', X'9FDB6AC0FCA4C14B92C503BDC0B5EF62', 2, NULL, NULL, 2, NULL, 0, 'In a separate bowl, sift together the flour, cinnamon, ginger, nutmeg, baking powder, baking soda, and salt. Add the dry ingredients into the wet and mix just until combined.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BFB25C2996FEAB44BCD3DCC593F4B376', X'9FDB6AC0FCA4C14B92C503BDC0B5EF62', X'9FDB6AC0FCA4C14B92C503BDC0B5EF62', 3, NULL, NULL, 2, NULL, 0, 'Pour the batter into the prepared loaf pan and bake for approximately 40 minutes or until a cake tester comes out clean.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'88D23BA800069946B1B1B592673047F8', X'9FDB6AC0FCA4C14B92C503BDC0B5EF62', X'9FDB6AC0FCA4C14B92C503BDC0B5EF62', 4, NULL, NULL, 2, NULL, 0, 'Make the glaze by whisking together the powdered sugar, honey, milk, melted butter, cinnamon, ginger, and salt. Drizzle over the warm cake.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1697E75F5104DB4CAE803A2E7D745508', X'C69143D764A9BC4C9635A02AF6E5F8B2', X'C69143D764A9BC4C9635A02AF6E5F8B2', 0, NULL, NULL, 2, NULL, 0, 'Add the frozen banana, Greek style yogurt, dates, ginger, milk, cardamom and salt to a blender.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'79803B259E562E479F9CFB324FE57463', X'C69143D764A9BC4C9635A02AF6E5F8B2', X'C69143D764A9BC4C9635A02AF6E5F8B2', 1, NULL, NULL, 2, NULL, 0, 'Puree on high until completely smooth, pour into chilled glasses and serve.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A2448AA9B4349842A9C4F8EFAEFB251A', X'C69143D764A9BC4C9635A02AF6E5F8B2', X'C69143D764A9BC4C9635A02AF6E5F8B2', 2, NULL, NULL, 2, NULL, 0, 'Note: Peel and quarter ripe bananas and place in plastic zip top baggies in the freezer for easy smoothies and frozen yogurt uses.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EC4A7C97CCEBDD45AE28D3044901474C', X'CB24DBF6C4847242881E2C8132D75359', X'CB24DBF6C4847242881E2C8132D75359', 0, NULL, NULL, 2, NULL, 0, 'Using a stand or hand mixer, cream together the eggs and sugar until lightened in color. Beat in the butter, sesame paste and vanilla.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'00251A318A70014580612830F16707DA', X'CB24DBF6C4847242881E2C8132D75359', X'CB24DBF6C4847242881E2C8132D75359', 1, NULL, NULL, 2, NULL, 0, 'In a separate bowl, sift together the flour, salt, baking powder and baking soda. Mix the dry ingredients into the wet and then stir in the chocolate chips. Wrap in plastic and refrigerate for about 8 hours or overnight.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9D1D7060D0D3AC4BA76356CA2A408FDC', X'CB24DBF6C4847242881E2C8132D75359', X'CB24DBF6C4847242881E2C8132D75359', 2, NULL, NULL, 2, NULL, 0, 'Pre-heat the oven to 325 degrees.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'57B627A0CE9B904494EEF816E01CBD41', X'CB24DBF6C4847242881E2C8132D75359', X'CB24DBF6C4847242881E2C8132D75359', 3, NULL, NULL, 2, NULL, 0, 'Use a cookie scoop to form balls of dough and place them on a baking sheet lined with parchment paper. Keep the dough balls at least 3` apart because the cookies will spread. I used a 2 oz. scoop and got about 14 large cookies.)');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CF2D3F83B970FC4883C9FB2C4CE4CC2C', X'CB24DBF6C4847242881E2C8132D75359', X'CB24DBF6C4847242881E2C8132D75359', 4, NULL, NULL, 2, NULL, 0, 'Bake for 15 - 16 minutes until the edges are lightly browned. Sprinkle with sea salt and then allow the cookies to cool thoroughly before removing them from the baking sheet. The cookies will be very soft when they first come out and will break if you try to move them. Once cool, they will be firm enough to move but remain chewy in the center for a long time.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A07D23BA831821459FC2026B8CD73B34', X'9240F2D9EAEEC741BF2C7C715B8ADDC7', X'9240F2D9EAEEC741BF2C7C715B8ADDC7', 0, NULL, NULL, 2, NULL, 0, 'Add the pitted dates to a bowl and fill with the cup of hot water. Allow them to soak for at least 30 minutes. When ready, add the dates and about 1/4 cup of the water to a food processor along with the honey and salt. Puree until completely smooth.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'229443CE948BFC4CADF387682B57F137', X'9240F2D9EAEEC741BF2C7C715B8ADDC7', X'9240F2D9EAEEC741BF2C7C715B8ADDC7', 1, NULL, NULL, 2, NULL, 0, 'To make the cookies, sift together the flour, sugar, and salt in a large bowl.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3DCB283A42D61F44ADAB244A05D8B1B2', X'9240F2D9EAEEC741BF2C7C715B8ADDC7', X'9240F2D9EAEEC741BF2C7C715B8ADDC7', 2, NULL, NULL, 2, NULL, 0, 'Cut in the butter using a pastry cutter or fork. You can also do this in a stand mixer.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9673865183232B4DAA81C855CEDB75F5', X'9240F2D9EAEEC741BF2C7C715B8ADDC7', X'9240F2D9EAEEC741BF2C7C715B8ADDC7', 3, NULL, NULL, 2, NULL, 0, 'Add the vanilla and buttermilk and stir until combined. Note that the batter will probably be too thick for a mixer and you will need to do this by hand.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E67184EA9D7B7C4EB39B7D7E6C54489B', X'9240F2D9EAEEC741BF2C7C715B8ADDC7', X'9240F2D9EAEEC741BF2C7C715B8ADDC7', 4, NULL, NULL, 2, NULL, 0, 'Turn the dough out onto a floured board and form into a disk. Wrap in plastic and refrigerate for at least 30 minutes.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BE8EAF83980AD54FA1D03A2C829C7D83', X'9240F2D9EAEEC741BF2C7C715B8ADDC7', X'9240F2D9EAEEC741BF2C7C715B8ADDC7', 5, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 350 F. Roll out the cookie dough, on a well-floured surface, to about 1/4` thick.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E1A70DB26F0C0242BAD75E25F4F180CD', X'9240F2D9EAEEC741BF2C7C715B8ADDC7', X'9240F2D9EAEEC741BF2C7C715B8ADDC7', 6, NULL, NULL, 2, NULL, 0, 'Use a cookie cutter (mine was about 3`) to cut out rounds and place them on a baking sheet lined with parchment paper.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A9EA1D949EDDC24999BAF2EC4C12B51A', X'9240F2D9EAEEC741BF2C7C715B8ADDC7', X'9240F2D9EAEEC741BF2C7C715B8ADDC7', 7, NULL, NULL, 2, NULL, 0, 'Bake for 12 to 14 minutes and allow to cool for at least 15 minutes before moving. The cookies will be very soft when you take them out of the oven but will harden after cooling.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EAA67D92DD1190479F10FB17B515D158', X'9240F2D9EAEEC741BF2C7C715B8ADDC7', X'9240F2D9EAEEC741BF2C7C715B8ADDC7', 8, NULL, NULL, 2, NULL, 0, 'Place a tablespoon of honey date filling in the center of one cookie and top with a second cookie to spread the filling. A 3` cutter will yield about 16 cookies which will be 8 sandwiched cookies.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'422B5ED9252FB3439FECA99C19EAFFAF', X'F0E96F8C61C5BB46999BB1A33117B69D', X'F0E96F8C61C5BB46999BB1A33117B69D', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
