INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8E54F830A7281B4688C98FC07E1094C3', X'F0E96F8C61C5BB46999BB1A33117B69D', X'F0E96F8C61C5BB46999BB1A33117B69D', 2, NULL, NULL, 2, NULL, 0, 'Beat in the yogurt, goat cheese, vanilla, and salt.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7384D8EDD755A04EAFFFCE9D9A532304', X'F0E96F8C61C5BB46999BB1A33117B69D', X'F0E96F8C61C5BB46999BB1A33117B69D', 3, NULL, NULL, 2, NULL, 0, 'Pour equally into 4 jars or dessert glasses and refrigerate for about an hour to firm up.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FE3EBF31B42C834AB198907C5B181426', X'F0E96F8C61C5BB46999BB1A33117B69D', X'F0E96F8C61C5BB46999BB1A33117B69D', 4, NULL, NULL, 2, NULL, 0, 'Add the sugar, honey, salt, and water to a saucepan and heat on medium just until the sugar dissolves.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'643B91311DA3A7499EB71A6F1EB542B1', X'F0E96F8C61C5BB46999BB1A33117B69D', X'F0E96F8C61C5BB46999BB1A33117B69D', 5, NULL, NULL, 2, NULL, 0, 'Distribute the halved figs evenly into the 4 servings and pour the honey glaze over the figs.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'29585B2DF7BF0047B1D569AC6020BDB7', X'5D24FF02A67D764BB5B35834BDB4A84C', X'5D24FF02A67D764BB5B35834BDB4A84C', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'412AD899A943DA4EA57F0195556443BF', X'5D24FF02A67D764BB5B35834BDB4A84C', X'5D24FF02A67D764BB5B35834BDB4A84C', 1, NULL, NULL, 2, NULL, 0, 'Add the butter and sugar to a bowl and, using a stand or hand mixer, cream together. Mix in the vanilla and milk.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'00E761A3C1E6834D95550E6A309E2AF6', X'5D24FF02A67D764BB5B35834BDB4A84C', X'5D24FF02A67D764BB5B35834BDB4A84C', 2, NULL, NULL, 2, NULL, 0, 'In a separate bowl, sift together the flour, salt and cardamom. Stir the dry ingredients into the wet and fold in half the chopped pistachios. Form into a ball, wrap in plastic and refrigerate for at least one hour.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3772B87BCBF2414790EB648B092BBF2D', X'5D24FF02A67D764BB5B35834BDB4A84C', X'5D24FF02A67D764BB5B35834BDB4A84C', 3, NULL, NULL, 2, NULL, 0, 'When ready to make the cookies, pre-heat the oven to 350 F.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F8F9D7C9AA93214EB3DAF5C31304F6CC', X'5D24FF02A67D764BB5B35834BDB4A84C', X'5D24FF02A67D764BB5B35834BDB4A84C', 4, NULL, NULL, 2, NULL, 0, 'Roll the dough out on a floured surface till it''s approximately an 8 inch circle. Use a 2 1/2 inch cookie or biscuit cutter to cut out rounds.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C3A13742FD8F514B9BAA2A33EBCA36E8', X'5D24FF02A67D764BB5B35834BDB4A84C', X'5D24FF02A67D764BB5B35834BDB4A84C', 5, NULL, NULL, 2, NULL, 0, 'Place the remainder of the chopped pistachios in a bowl and gently dip the edges of the rounds into the nuts.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'32C6365E798C2C42814CBA42AB495C92', X'5D24FF02A67D764BB5B35834BDB4A84C', X'5D24FF02A67D764BB5B35834BDB4A84C', 6, NULL, NULL, 2, NULL, 0, 'Place on a baking sheet lined with parchment paper and bake for 20 to 22 minutes.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6EB0F9D116C9944C96FCF223F1118117', X'5D24FF02A67D764BB5B35834BDB4A84C', X'5D24FF02A67D764BB5B35834BDB4A84C', 7, NULL, NULL, 2, NULL, 0, 'After baking, allow the cookies to cool and firm up before removing them from the baking sheet.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'808FAEC236E14C4A9DB43B601CE186E0', X'5D24FF02A67D764BB5B35834BDB4A84C', X'5D24FF02A67D764BB5B35834BDB4A84C', 8, NULL, NULL, 2, NULL, 0, 'Serve and enjoy!');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CB0A3A3203A65747BABEFE5E49F1856E', X'05F30A0407B9584181753EB81F77AAF1', X'05F30A0407B9584181753EB81F77AAF1', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'840C5266BABC824EA3A526804034DEED', X'05F30A0407B9584181753EB81F77AAF1', X'05F30A0407B9584181753EB81F77AAF1', 1, NULL, NULL, 2, NULL, 0, 'Heat the oven to 350 F.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0EAC4C1A54C4C94897C8687A209D4178', X'05F30A0407B9584181753EB81F77AAF1', X'05F30A0407B9584181753EB81F77AAF1', 2, NULL, NULL, 2, NULL, 0, 'Using a stand or hand mixer, cream together the eggs and sugar until lightened in color. Beat in the butter and almond extract.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AA426A761BD4814E89B4D731D97B44B6', X'05F30A0407B9584181753EB81F77AAF1', X'05F30A0407B9584181753EB81F77AAF1', 3, NULL, NULL, 2, NULL, 0, 'In a separate bowl, sift together the salt, baking powder, and all-purpose flour (or, if making the Passover version, the potato starch and cake meal).');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'03472CD37605584080F5682EC6BF2667', X'05F30A0407B9584181753EB81F77AAF1', X'05F30A0407B9584181753EB81F77AAF1', 4, NULL, NULL, 2, NULL, 0, 'Fold the dry ingredients into the wet, stir in the sliced almonds, and turn out onto a baking sheet lined with parchment paper. The dough will be very sticky and you''ll need additional flour (or cake meal) to shape it into a loaf, approximately 3 inches high.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8BD466EAB7F92C4885CDFE0207D00B2C', X'05F30A0407B9584181753EB81F77AAF1', X'05F30A0407B9584181753EB81F77AAF1', 5, NULL, NULL, 2, NULL, 0, 'Bake for 45 minutes.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'487180FA97A1A449AA209B70BCF9E49F', X'05F30A0407B9584181753EB81F77AAF1', X'05F30A0407B9584181753EB81F77AAF1', 6, NULL, NULL, 2, NULL, 0, 'With a serrated knife, slice while the loaf is still warm.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'287C0503F7A77E41B9971D1C2315E7D8', X'93C6AA3713BDD9468FB4547630F5BAA5', X'93C6AA3713BDD9468FB4547630F5BAA5', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CEDDEC8337C92D42B9B243A5CE34EB7A', X'93C6AA3713BDD9468FB4547630F5BAA5', X'93C6AA3713BDD9468FB4547630F5BAA5', 1, NULL, NULL, 2, NULL, 0, 'In a small saucepan, bring the sugar and water to a boil.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C5410CA5346E3C40AFB8123CA2678EEF', X'93C6AA3713BDD9468FB4547630F5BAA5', X'93C6AA3713BDD9468FB4547630F5BAA5', 2, NULL, NULL, 2, NULL, 0, 'Once the sugar is dissolved, add the saffron and rosewater. You will have a syrup like texture. Remove from heat.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'143D3E71AC11FE42B15408C5791461DC', X'93C6AA3713BDD9468FB4547630F5BAA5', X'93C6AA3713BDD9468FB4547630F5BAA5', 3, NULL, NULL, 2, NULL, 0, 'In a medium saucepan, melt the butter on medium heat.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BEB202472D09884BB05D7493EFFD434B', X'93C6AA3713BDD9468FB4547630F5BAA5', X'93C6AA3713BDD9468FB4547630F5BAA5', 4, NULL, NULL, 2, NULL, 0, 'Add the flour slowly and mix with the butter to make a paste.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E49C70C38D9B1D408AA338824DECF223', X'93C6AA3713BDD9468FB4547630F5BAA5', X'93C6AA3713BDD9468FB4547630F5BAA5', 5, NULL, NULL, 2, NULL, 0, 'Slowly add the sugar syrup and blend together. Remove from the heat.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E4B0059729BE9C4E9CE2C3A864B7610B', X'93C6AA3713BDD9468FB4547630F5BAA5', X'93C6AA3713BDD9468FB4547630F5BAA5', 6, NULL, NULL, 2, NULL, 0, 'Immediately pour the mixture into individual ramekins or onto a serving plate.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'22739CAF0771EA47BB808D706B85A215', X'93C6AA3713BDD9468FB4547630F5BAA5', X'93C6AA3713BDD9468FB4547630F5BAA5', 7, NULL, NULL, 2, NULL, 0, 'Allow to cool and either remove the halva from the ramekins or cut into small serving pieces.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F29B40C0061DB34F9DCD2BFA0037B247', X'93C6AA3713BDD9468FB4547630F5BAA5', X'93C6AA3713BDD9468FB4547630F5BAA5', 8, NULL, NULL, 2, NULL, 0, 'Serve with coffee or tea.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B3A73BACC209214A92E223F6364342CC', X'CC65BA3C32261E4A98B83223155AF194', X'CC65BA3C32261E4A98B83223155AF194', 0, NULL, NULL, 2, NULL, 0, 'Rinse the rice well with cold water to remove any starchy coating from the grains.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'172101AF0C2F0343ABD4403A76E63526', X'CC65BA3C32261E4A98B83223155AF194', X'CC65BA3C32261E4A98B83223155AF194', 1, NULL, NULL, 2, NULL, 0, 'In a large saucepan over medium-high heat, combine rice and milk. Bring the liquid to a low boil and then reduce the heat to a simmer.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0FD6BA14112C86488223AC5F9C398F81', X'CC65BA3C32261E4A98B83223155AF194', X'CC65BA3C32261E4A98B83223155AF194', 2, NULL, NULL, 2, NULL, 0, 'Simmer until creamy, approximately 45 minutes. Stir the mixture occassionally to ensure the rice isn''t sticking to the bottom of the pan.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'27FC7F5D7E579D4CA78BD43D340B0BF5', X'CC65BA3C32261E4A98B83223155AF194', X'CC65BA3C32261E4A98B83223155AF194', 3, NULL, NULL, 2, NULL, 0, 'Add remaining 3 tablespoons of milk, sugar, cardamom, salt, and cornstarch to rice and stir thoroughly. Add rosewater and bring to a boil for 1 minute.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8728ADCC1E87FD418B1C1C06BC50D69F', X'CC65BA3C32261E4A98B83223155AF194', X'CC65BA3C32261E4A98B83223155AF194', 4, NULL, NULL, 2, NULL, 0, 'Remove from heat and allow to sit for 5 minutes to thicken and transfer to serving bowls.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0A1F17D3F6745A448C527D8BAF950937', X'CC65BA3C32261E4A98B83223155AF194', X'CC65BA3C32261E4A98B83223155AF194', 5, NULL, NULL, 2, NULL, 0, 'Serve immediately or refrigerate for at least one hour.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EB403E66CD7B34448C9319584D47AEE9', X'80D3E64AD3427B47913E001C297B2113', X'80D3E64AD3427B47913E001C297B2113', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B6835C01BD62E548891A6430FFCC1103', X'80D3E64AD3427B47913E001C297B2113', X'80D3E64AD3427B47913E001C297B2113', 1, NULL, NULL, 2, NULL, 0, 'Add the egg and butter to a large bowl and cream together using a hand or stand mixer.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BF3038073ECB834880B3957B97A4872C', X'80D3E64AD3427B47913E001C297B2113', X'80D3E64AD3427B47913E001C297B2113', 2, NULL, NULL, 2, NULL, 0, 'Beat in the sugar, orange zest, and orange juice.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E345A696FED6BE488B1207B5A020A54A', X'80D3E64AD3427B47913E001C297B2113', X'80D3E64AD3427B47913E001C297B2113', 3, NULL, NULL, 2, NULL, 0, 'In a separate bowl, sift together the flour, baking powder, and salt.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D275E956F409BB47AA51F6674607B3F9', X'80D3E64AD3427B47913E001C297B2113', X'80D3E64AD3427B47913E001C297B2113', 4, NULL, NULL, 2, NULL, 0, 'Fold the dry ingredients into the wet, turn out onto a floured surface to form a dough ball, wrap in plastic and refrigerate for at least 1 hour.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'903991A2EFC6054CAF3921E1768C9E6B', X'80D3E64AD3427B47913E001C297B2113', X'80D3E64AD3427B47913E001C297B2113', 5, NULL, NULL, 2, NULL, 0, 'While the dough is chilling, make the filling by adding the dates to a bowl with the hot water and let soak for 30 minutes.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'792FF68F470E2B40A9C4139C589A97A4', X'80D3E64AD3427B47913E001C297B2113', X'80D3E64AD3427B47913E001C297B2113', 6, NULL, NULL, 2, NULL, 0, 'Add the soaked dates and about 1/4 cup of the water to a food processor with the honey and salt. Puree until completely smooth.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EE4BD54041637346961C10EA8E17D4F1', X'80D3E64AD3427B47913E001C297B2113', X'80D3E64AD3427B47913E001C297B2113', 7, NULL, NULL, 2, NULL, 0, 'Pre-heat the oven to 375 F.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B3E39A9B5751074EA6406E9B6E043430', X'80D3E64AD3427B47913E001C297B2113', X'80D3E64AD3427B47913E001C297B2113', 8, NULL, NULL, 2, NULL, 0, 'Roll the dough out to between 1/8- to 1/4-inch thick (I like thicker cookies). Use a 3 1/2-inch round cutter to cut circles out of the dough.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2DD822BF619E6A47883DAFEAB7157FD3', X'80D3E64AD3427B47913E001C297B2113', X'80D3E64AD3427B47913E001C297B2113', 9, NULL, NULL, 2, NULL, 0, 'Place about a teaspoon of honey date filling in the center of each dough round and fold into the classic triangular shape (see an easy tutorial here).');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FEAA5F36BC26CF4492B6DD06E1A3E0C9', X'80D3E64AD3427B47913E001C297B2113', X'80D3E64AD3427B47913E001C297B2113', 10, NULL, NULL, 2, NULL, 0, 'Place on a baking sheet lined with parchment paper and bake for 12 to 15 minutes.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C0757808135EA8439405C05D6C36AC35', X'80D3E64AD3427B47913E001C297B2113', X'80D3E64AD3427B47913E001C297B2113', 11, NULL, NULL, 2, NULL, 0, 'Cool and enjoy!');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'36FC1411BFB18046B96EB04F67437B99', X'AA877D66EDD88D44BA543B8DF22E0F9E', X'AA877D66EDD88D44BA543B8DF22E0F9E', 0, NULL, NULL, 2, NULL, 0, 'In a large bowl, sift together the all purpose flour, salt and baking powder.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'90557FAE2936894CB9B069486CBFC54E', X'AA877D66EDD88D44BA543B8DF22E0F9E', X'AA877D66EDD88D44BA543B8DF22E0F9E', 1, NULL, NULL, 2, NULL, 0, 'Add the olive oil and mix together until all the flour is coated with the oil.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B8DACE581047B842AD7E9E062518CEE5', X'AA877D66EDD88D44BA543B8DF22E0F9E', X'AA877D66EDD88D44BA543B8DF22E0F9E', 2, NULL, NULL, 2, NULL, 0, 'Add the cold water and continue mixing to form a dough ball.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'485CA7D18B360E4F895032AA475BB3B4', X'AA877D66EDD88D44BA543B8DF22E0F9E', X'AA877D66EDD88D44BA543B8DF22E0F9E', 3, NULL, NULL, 2, NULL, 0, 'Wrap in plastic and refrigerate for at least 30 minutes.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FBEBE7D9AA8E694AB0844022E10B58E0', X'AA877D66EDD88D44BA543B8DF22E0F9E', X'AA877D66EDD88D44BA543B8DF22E0F9E', 4, NULL, NULL, 2, NULL, 0, 'While the dough is chilling, add the beets to a pot of boiling water and cook on high heat for 5 minutes.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5DA1D9493127AC478432F23E870B995E', X'AA877D66EDD88D44BA543B8DF22E0F9E', X'AA877D66EDD88D44BA543B8DF22E0F9E', 5, NULL, NULL, 2, NULL, 0, 'Drain and allow to cool enough to peel and slice thinly. Note that a mandoline works best for this but you can also use a sharp knife.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'85ED82FB701E6B4BBAB0836D2F0486B8', X'AA877D66EDD88D44BA543B8DF22E0F9E', X'AA877D66EDD88D44BA543B8DF22E0F9E', 6, NULL, NULL, 2, NULL, 0, 'Pre-heat the oven to 400 degrees.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'52D8683518205E4BAEE141A4EB7AEAB2', X'AA877D66EDD88D44BA543B8DF22E0F9E', X'AA877D66EDD88D44BA543B8DF22E0F9E', 7, NULL, NULL, 2, NULL, 0, 'Roll out the dough, on a floured surface, to about an 8 inch round.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E7428958F4CC1E49870294BA4F0565B8', X'AA877D66EDD88D44BA543B8DF22E0F9E', X'AA877D66EDD88D44BA543B8DF22E0F9E', 8, NULL, NULL, 2, NULL, 0, 'Place layers of the sliced beets about 1` in from the edge of the dough.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EE5A0A58A9DC8449BFE7DC7A4ECCAB28', X'AA877D66EDD88D44BA543B8DF22E0F9E', X'AA877D66EDD88D44BA543B8DF22E0F9E', 9, NULL, NULL, 2, NULL, 0, 'Sprinkle some za''atar and a bit of the crumbled feta cheese on each layer of beets.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'01E760EF99EA074C8347BB43EB939E43', X'AA877D66EDD88D44BA543B8DF22E0F9E', X'AA877D66EDD88D44BA543B8DF22E0F9E', 10, NULL, NULL, 2, NULL, 0, 'Roll up the edges of the dough and pinch together. No need to make it perfect.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A4769E4AFE3F6E488BAC0CB75BAD0339', X'AA877D66EDD88D44BA543B8DF22E0F9E', X'AA877D66EDD88D44BA543B8DF22E0F9E', 11, NULL, NULL, 2, NULL, 0, 'Place on a sheet pan lined with parchment paper and bake for 40 - 45 minutes or until the dough is a light golden brown.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'50B9DF5C4C0CE0449380E455C2F55BA1', X'751EB671F91504478355245B588315A1', X'751EB671F91504478355245B588315A1', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 350 degrees F.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C4821D720A7ABB409A09F1E95D8AD15F', X'751EB671F91504478355245B588315A1', X'751EB671F91504478355245B588315A1', 1, NULL, NULL, 2, NULL, 0, 'In a medium bowl, combine flour and semolina. Set aside.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'727C9CABEEF5274D94C7F03EA0C0D388', X'751EB671F91504478355245B588315A1', X'751EB671F91504478355245B588315A1', 2, NULL, NULL, 2, NULL, 0, 'In a smaller bowl, beat together butter for 30 to 45 seconds. Slowly add in flour mixture until combined.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'ACBB42CB1F6CEB4C89588B2651C52582', X'751EB671F91504478355245B588315A1', X'751EB671F91504478355245B588315A1', 3, NULL, NULL, 2, NULL, 0, 'Stir in milk, allspice, and orange blossom water.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1887D530349CA64A851D12A6AEEEEFD7', X'751EB671F91504478355245B588315A1', X'751EB671F91504478355245B588315A1', 4, NULL, NULL, 2, NULL, 0, 'Roll dough into 12 pieces.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'26F73B25E7CD02419D4388DC453DDBB0', X'751EB671F91504478355245B588315A1', X'751EB671F91504478355245B588315A1', 5, NULL, NULL, 2, NULL, 0, 'In a small bowl, combine nuts and sugar. Set aside.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'82C05FF5EF196F448FF1230E1DEEA61E', X'751EB671F91504478355245B588315A1', X'751EB671F91504478355245B588315A1', 6, NULL, NULL, 2, NULL, 0, 'Grab your dough and push 2-inch circles into it, then place onto parchment lined cookie sheet.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D981E0A78691D14DB348C94EF4A0329D', X'751EB671F91504478355245B588315A1', X'751EB671F91504478355245B588315A1', 7, NULL, NULL, 2, NULL, 0, 'Place about 1 teaspoon of nut mixture on top of dough. Fold and press dough together to seal the filling. Repeat with remaining dough and nut mixture.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6621182D0B71A24F8657294247820774', X'751EB671F91504478355245B588315A1', X'751EB671F91504478355245B588315A1', 8, NULL, NULL, 2, NULL, 0, 'Bake maamoul for 20 to 25 minutes until golden brown.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FCB7456B1D8F10489A016DE7B74B24B0', X'7DEEBB8CC5CE7741A8DA1DA366E43FDB', X'7DEEBB8CC5CE7741A8DA1DA366E43FDB', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 350 F. Butter a 15 by 10 by 1-inch baking pan.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C5E749F7032FA44C9AB84016EED17BA9', X'7DEEBB8CC5CE7741A8DA1DA366E43FDB', X'7DEEBB8CC5CE7741A8DA1DA366E43FDB', 1, NULL, NULL, 2, NULL, 0, 'In a bowl, combine pistachio nuts and butter.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'491DF41F650BF442AF5A8884AA704762', X'7DEEBB8CC5CE7741A8DA1DA366E43FDB', X'7DEEBB8CC5CE7741A8DA1DA366E43FDB', 2, NULL, NULL, 2, NULL, 0, 'Take eight sheets of phyllo dough and layer them in the pan, spreading each sheet with melted butter as you go.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'06F6CE0E2126084C968892D2B9FE98A3', X'7DEEBB8CC5CE7741A8DA1DA366E43FDB', X'7DEEBB8CC5CE7741A8DA1DA366E43FDB', 3, NULL, NULL, 2, NULL, 0, 'Spread about half of the nut mixture over the phyllo dough.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'35A9D3E95EA3B14F87B33B6627F8D417', X'7DEEBB8CC5CE7741A8DA1DA366E43FDB', X'7DEEBB8CC5CE7741A8DA1DA366E43FDB', 4, NULL, NULL, 2, NULL, 0, 'Cover with a buttered sheet of phyllo dough.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'541E05CA96ED8F4580A87B038A2EF813', X'7DEEBB8CC5CE7741A8DA1DA366E43FDB', X'7DEEBB8CC5CE7741A8DA1DA366E43FDB', 5, NULL, NULL, 2, NULL, 0, 'Layer five more sheets of phyllo dough, buttering each sheet as you layer.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'563D1D9A8FCB1B4A88CFC53D04A4C1EC', X'7DEEBB8CC5CE7741A8DA1DA366E43FDB', X'7DEEBB8CC5CE7741A8DA1DA366E43FDB', 6, NULL, NULL, 2, NULL, 0, 'Add remaining nut mixture and layer with remaining sheets of phyllo dough, buttering each sheet.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'443BC5B1D0061B4AB61FE29EFE0A2699', X'7DEEBB8CC5CE7741A8DA1DA366E43FDB', X'7DEEBB8CC5CE7741A8DA1DA366E43FDB', 7, NULL, NULL, 2, NULL, 0, 'Using a knife, cut the baklava into 1 1/2-inch diamonds.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'609EC250EB8E9A4597A38800752C6CDB', X'7DEEBB8CC5CE7741A8DA1DA366E43FDB', X'7DEEBB8CC5CE7741A8DA1DA366E43FDB', 8, NULL, NULL, 2, NULL, 0, 'Bake for 50 minutes or until golden brown.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F57B9699E7D757409EEA4566BEABA218', X'7DEEBB8CC5CE7741A8DA1DA366E43FDB', X'7DEEBB8CC5CE7741A8DA1DA366E43FDB', 9, NULL, NULL, 2, NULL, 0, 'About 30 minutes before baklava is finished, combine sugar, honey, cinnamon, and lemon juice for glaze in a small saucepan.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6419266957E0A341B26F2348BAF599FB', X'7DEEBB8CC5CE7741A8DA1DA366E43FDB', X'7DEEBB8CC5CE7741A8DA1DA366E43FDB', 10, NULL, NULL, 2, NULL, 0, 'Bring to a boil, making sure that the sugar is completely dissolved.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'19A85D3126A5064B8B668BC3F074E730', X'7DEEBB8CC5CE7741A8DA1DA366E43FDB', X'7DEEBB8CC5CE7741A8DA1DA366E43FDB', 11, NULL, NULL, 2, NULL, 0, 'Reduce heat and allow the mixture to simmer for 5 minutes.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'555E8235867D164CA51CB113339EBEBF', X'7DEEBB8CC5CE7741A8DA1DA366E43FDB', X'7DEEBB8CC5CE7741A8DA1DA366E43FDB', 12, NULL, NULL, 2, NULL, 0, 'When baklava is finished cooking, drizzle syrup over top.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'95EA1836EF16E841B035F761D84F7713', X'7DEEBB8CC5CE7741A8DA1DA366E43FDB', X'7DEEBB8CC5CE7741A8DA1DA366E43FDB', 13, NULL, NULL, 2, NULL, 0, 'Let cool.');
