INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'74337C1668E21640B4F14ED17AE7C137', X'68C315F6CB564747A29669280476D181', X'68C315F6CB564747A29669280476D181', 1, NULL, NULL, 2, NULL, 0, 'Preheat oven to 350 F.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A38EB4921533DE4F8958D06AA815240A', X'68C315F6CB564747A29669280476D181', X'68C315F6CB564747A29669280476D181', 2, NULL, NULL, 2, NULL, 0, 'In a mixing bowl combine semolina, flour, turmeric, and baking powder. Stir to combine well.');
