INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'814397B960A590448C5A7CA332872DE6', X'68C315F6CB564747A29669280476D181', X'68C315F6CB564747A29669280476D181', 6, NULL, NULL, 2, NULL, 0, 'Allow to cool for 15 to 20 minutes and serve with your favorite coffee or tea.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5C814CF4093F65418E13A9BC5AAAD5BE', X'742183F250E04A4796BE9F0E1EB45E09', X'742183F250E04A4796BE9F0E1EB45E09', 0, NULL, NULL, 2, NULL, 0, 'First, line a large baking sheet with parchment paper. After dipping your dates in chocolate, you will place them on the sheet to set.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'51BCC659E5EF734593A80A22D74A2389', X'742183F250E04A4796BE9F0E1EB45E09', X'742183F250E04A4796BE9F0E1EB45E09', 1, NULL, NULL, 2, NULL, 0, 'Stuff each date with the nut of your choice. Melt chocolate in microwave safe bowl for about 3 minutes, stirring halfway through cooking time.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A553D97E3D25E346851C7BB77511EDC4', X'742183F250E04A4796BE9F0E1EB45E09', X'742183F250E04A4796BE9F0E1EB45E09', 2, NULL, NULL, 2, NULL, 0, 'Using a skewer, dip each date in chocolate. Allow the excess chocolate to fall off by carefully rotating the skewer. Place dipped date on parchment and sprinkle with coconut flakes. Repeat with remaining dates.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'36F0FB7DF4CF90468D2674F25156B97D', X'742183F250E04A4796BE9F0E1EB45E09', X'742183F250E04A4796BE9F0E1EB45E09', 3, NULL, NULL, 2, NULL, 0, 'Allow dates to set in the refrigerator for about 20 to 30 minutes before serving.');
