INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'16E5AC349EBF3046BA34BD6873CC24C9', X'0931A3DD2535A34A9E69D2AF668758E1', X'0931A3DD2535A34A9E69D2AF668758E1', 0, NULL, NULL, 2, NULL, 0, 'In a small saucepan, add the dried herbs to the water and bring to a boilCook for 5 minutes. Over a bowl pour the water through a strainer to remove the herbs and leave the water to cool to room temperature. Discard the herbs.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'72E57770DC12C541BFEB42B787837892', X'0931A3DD2535A34A9E69D2AF668758E1', X'0931A3DD2535A34A9E69D2AF668758E1', 1, NULL, NULL, 2, NULL, 0, 'In a large bowl, knead the salt into the butter with your hands. Add the strained, cooled water and continue kneading the mixture to ensure all of the butter is making contact with the water.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B69031F9B45490428D07B18284C9227E', X'0931A3DD2535A34A9E69D2AF668758E1', X'0931A3DD2535A34A9E69D2AF668758E1', 2, NULL, NULL, 2, NULL, 0, 'Strain the water from the butter, squeezing the butter to remove any excess. Place the butter in a clean glass or ceramic jar, pressing and packing it down to ensure there are no air pockets. Cover the jar.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D67B8B90B663B640AEAD00790FE27360', X'0931A3DD2535A34A9E69D2AF668758E1', X'0931A3DD2535A34A9E69D2AF668758E1', 3, NULL, NULL, 2, NULL, 0, 'Leave the jar undisturbed in a cool, dark place (a cupboard is fine) for 30 days, or longer if you prefer more pungency. After opening, store the smen in the refrigerator.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A985A290BB38B84D945BA0605B8E82BB', X'A20B874CE0C7244C9CC4916B71E4A7B0', X'A20B874CE0C7244C9CC4916B71E4A7B0', 0, NULL, NULL, 2, NULL, 0, 'Ahead of time, place the dried fava beans in a large bowl and cover with a generous amount of cold water. Set aside to soak overnight or all day.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'05E28960E1DFB4458FA0C121E229B92A', X'A20B874CE0C7244C9CC4916B71E4A7B0', X'A20B874CE0C7244C9CC4916B71E4A7B0', 1, NULL, NULL, 2, NULL, 0, 'When ready to cook, drain the beans and peel them if they weren''t already skinned and split.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C1C3BF18BF68F24EA0F9337CD40241EF', X'A20B874CE0C7244C9CC4916B71E4A7B0', X'A20B874CE0C7244C9CC4916B71E4A7B0', 2, NULL, NULL, 2, NULL, 0, 'Transfer the peeled fava beans to a medium-sized pot and cover with a generous amount of water. Bring to a boil over high heat, then reduce the heat to medium and simmer the beans until tender, one hour or longer.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C1124A03097B36468B273B79BCBCB5EE', X'A20B874CE0C7244C9CC4916B71E4A7B0', X'A20B874CE0C7244C9CC4916B71E4A7B0', 3, NULL, NULL, 2, NULL, 0, 'Drain the beans, reserving the liquid.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1C183C9F822FF541A1AC4ECB2C1FA641', X'A20B874CE0C7244C9CC4916B71E4A7B0', X'A20B874CE0C7244C9CC4916B71E4A7B0', 4, NULL, NULL, 2, NULL, 0, 'Transfer the drained beans to a food processor, and add the garlic, olive oil, lemon juice, two tablespoons of the reserved liquid, and spices. Process on high speed until smooth, adding additional liquid if necessary to thin the bessara. At a minimum, it should be thin enough to pour out onto a plate, but may be thinned even more if a soup-like consistency is preferred.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'71A0ABEA4B686A47AF9A39DDAFBA0A1A', X'A20B874CE0C7244C9CC4916B71E4A7B0', X'A20B874CE0C7244C9CC4916B71E4A7B0', 5, NULL, NULL, 2, NULL, 0, 'Taste the bessara and adjust the seasoning as desired.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'56801187CBA73047876887694BD9DE65', X'A20B874CE0C7244C9CC4916B71E4A7B0', X'A20B874CE0C7244C9CC4916B71E4A7B0', 6, NULL, NULL, 2, NULL, 0, 'Reheat the bessara and serve warm, preferably in a warm dish so that the bessara doesn''t cool rapidly.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'59BEAEC42EAA5F4D980FF96AC214D615', X'A20B874CE0C7244C9CC4916B71E4A7B0', X'A20B874CE0C7244C9CC4916B71E4A7B0', 7, NULL, NULL, 2, NULL, 0, 'Garnish with any of the following: ground cumin, paprika, salt, harissa or cayenne, olive oil, and/or chopped parsley.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7A0CBA4C9D21A74C84BB44C36F9FF7D6', X'05BE79C025A06A478CE3BE867D391E86', X'05BE79C025A06A478CE3BE867D391E86', 0, NULL, NULL, 2, NULL, 0, 'Scrub the zucchini, but do not peel them. Cut off the ends, and chop the zucchini into tiny cubes.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'46B3C51629559047896EA345D48E1CF2', X'05BE79C025A06A478CE3BE867D391E86', X'05BE79C025A06A478CE3BE867D391E86', 1, NULL, NULL, 2, NULL, 0, 'Place the cubed zucchini in a pot with the olive oil, garlic, spices, and water. Cover and cook over medium heat stirring occasionally, for about 7 to 8 minutes, or until the zucchini are crisp-tender.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9552437B8800E447AD521D7A3458FCA3', X'05BE79C025A06A478CE3BE867D391E86', X'05BE79C025A06A478CE3BE867D391E86', 2, NULL, NULL, 2, NULL, 0, 'Add the lemon juice, parsley, and cumin and continue simmering, uncovered, until the liquids have reduced and the zucchini is tender but not mushy. Taste, and adjust the seasoning if desired.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D052349556F9904A8E68CBA773970B3E', X'05BE79C025A06A478CE3BE867D391E86', X'05BE79C025A06A478CE3BE867D391E86', 3, NULL, NULL, 2, NULL, 0, 'Serve warm or cold. Cooked zucchini salad can be eaten as a dip, or served with a fork or spoon.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'21E7416B174F86448DE461956B423921', X'5701DBEF928EAA43AEDABDBBF387C6F7', X'5701DBEF928EAA43AEDABDBBF387C6F7', 0, NULL, NULL, 2, NULL, 0, 'Place the tomatoes, garlic, and peppers in a heavy-bottomed 3-quart saucepan.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'07EE4F3CE908E14FB45103B59934D747', X'5701DBEF928EAA43AEDABDBBF387C6F7', X'5701DBEF928EAA43AEDABDBBF387C6F7', 1, NULL, NULL, 2, NULL, 0, 'Set over medium heat and cook, uncovered, for 20 minutes, stirring frequently with a spoon.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'927BAFD99F1E6B45868A0A96F37731AD', X'5701DBEF928EAA43AEDABDBBF387C6F7', X'5701DBEF928EAA43AEDABDBBF387C6F7', 2, NULL, NULL, 2, NULL, 0, 'When the vegetables are nice and soft, add the oil, paprika, salt, and pepper.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8E215B29C494CD408C0252DF643EBBEC', X'5701DBEF928EAA43AEDABDBBF387C6F7', X'5701DBEF928EAA43AEDABDBBF387C6F7', 3, NULL, NULL, 2, NULL, 0, 'Cook, stirring frequently, for another 10 minutes.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E102C4DD2019574C8383BB865A597D9C', X'5701DBEF928EAA43AEDABDBBF387C6F7', X'5701DBEF928EAA43AEDABDBBF387C6F7', 4, NULL, NULL, 2, NULL, 0, 'When the mixture is well blended and most of the liquid has evaporated, the matbucha is ready.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CA959C75F433BC499C5997A8EB3342D1', X'5701DBEF928EAA43AEDABDBBF387C6F7', X'5701DBEF928EAA43AEDABDBBF387C6F7', 5, NULL, NULL, 2, NULL, 0, 'Cool and store in a covered container in the refrigerator.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AC1CBB8702D68449BD364AA25B2FA018', X'E0B341FA57FFC541A3C418726B015751', X'E0B341FA57FFC541A3C418726B015751', 0, NULL, NULL, 2, NULL, 0, 'Soak the olives in a bowl of water for a few hours or overnight; drain. (Or, if the olives are hard, soften them by soaking in boiling water for 15 minutes.) For better infusion of the marinade, make one or two small incisions in each olive (or gently crush them to crack the flesh of the olive).');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'929339938C1B5E45A2B42C8CF9D15F4A', X'E0B341FA57FFC541A3C418726B015751', X'E0B341FA57FFC541A3C418726B015751', 1, NULL, NULL, 2, NULL, 0, 'While the olives are soaking, make the harissa if it''s not already prepared:');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2803EBDBC997B34FBFC77EE6B7DDEEF5', X'E0B341FA57FFC541A3C418726B015751', X'E0B341FA57FFC541A3C418726B015751', 2, NULL, NULL, 2, NULL, 0, 'Seed the preserved lemon and cut it into thin strips.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'18BF9AD9DA4FB54FB8291047B9A297A6', X'E0B341FA57FFC541A3C418726B015751', X'E0B341FA57FFC541A3C418726B015751', 3, NULL, NULL, 2, NULL, 0, 'Combine the preserved lemon with the drained olives, parsley. lemon juice and as much harissa as desired.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'273A6A215FEEC240A3EAE4D37BB32FAA', X'E0B341FA57FFC541A3C418726B015751', X'E0B341FA57FFC541A3C418726B015751', 4, NULL, NULL, 2, NULL, 0, 'If you like, a little additional olive oil or garlic can be mixed in to the olives.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'64DD9C357074604982544A900088B235', X'E0B341FA57FFC541A3C418726B015751', X'E0B341FA57FFC541A3C418726B015751', 5, NULL, NULL, 2, NULL, 0, 'Cover and refrigerate for six to eight hours (overnight is best) before serving.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2D1051D6DA421241B33B867F0190944F', X'DB7FA159A395374ABFD5973C0DD829AF', X'DB7FA159A395374ABFD5973C0DD829AF', 0, NULL, NULL, 2, NULL, 0, 'Preheat your oven to 375 F / 190 C. Wash the almonds if they feel gritty (they usually are in Morocco) and drain.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'66046F4321F4FF4994B15CDB999D0427', X'DB7FA159A395374ABFD5973C0DD829AF', X'DB7FA159A395374ABFD5973C0DD829AF', 1, NULL, NULL, 2, NULL, 0, 'Spread the almonds on a baking pan and roast for about 15 minutes, longer if necessary, until the almonds are crunchy and darkened but not burnt.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'097FA03F13F730429FCBC22C5C69CA06', X'DB7FA159A395374ABFD5973C0DD829AF', X'DB7FA159A395374ABFD5973C0DD829AF', 2, NULL, NULL, 2, NULL, 0, 'Allow the almonds to cool a bit and then pass them through a meat grinder several times to make a smooth, oily-looking paste. Or, grind the toasted almonds to a paste in a food processor on high speed.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'90609465B9F8FF4886F8544DEBC976F0', X'DB7FA159A395374ABFD5973C0DD829AF', X'DB7FA159A395374ABFD5973C0DD829AF', 3, NULL, NULL, 2, NULL, 0, 'Be sure to continue processing until the almonds grind to a glistening, the smooth mass that can almost be poured. This can take several minutes.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'67AD6124E0DFD345B58A8DC785D5CBEA', X'DB7FA159A395374ABFD5973C0DD829AF', X'DB7FA159A395374ABFD5973C0DD829AF', 4, NULL, NULL, 2, NULL, 0, 'Next, gradually blend the argan oil into the almond paste, a spoonful at a time or in a very slow trickle. You can do this by hand stirring vigorously, or preferably with the food processor on the lowest speed. Use only as much argan oil as you need to to make the amlou as thick or thin as you like.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D0CE6E2EC1D75441BC2ED27295ED7AA3', X'DB7FA159A395374ABFD5973C0DD829AF', X'DB7FA159A395374ABFD5973C0DD829AF', 5, NULL, NULL, 2, NULL, 0, 'Next, gradually add the warm honey, sugar, and salt in the same manner. Taste the amlou and adjust the sweetness if desired.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'04DA7778965C89478BB2F22F27F1F299', X'DB7FA159A395374ABFD5973C0DD829AF', X'DB7FA159A395374ABFD5973C0DD829AF', 6, NULL, NULL, 2, NULL, 0, 'Serve amlou on a plate or shallow dish with bread for dipping.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1EDB09711431B846A24118D5789DE0C6', X'28CF57A4FDBA1B47B0E890A4B5689233', X'28CF57A4FDBA1B47B0E890A4B5689233', 0, NULL, NULL, 2, NULL, 0, 'Mix the ground meat with the onion and spices. Melt the butter in a large skillet or frying pan and add the ground meat. Cook over medium heat, stirring to break up the meat into fine pieces until the meat is well browned and thoroughly cooked.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FED393EFFE4848429A90E881F5C19096', X'28CF57A4FDBA1B47B0E890A4B5689233', X'28CF57A4FDBA1B47B0E890A4B5689233', 1, NULL, NULL, 2, NULL, 0, 'Stir in the parsley and the three eggs, and cook over medium-high heat just until the liquids are absorbed. You should be able to pack and mold the meat. Set the kefta filling aside.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3795EC62E0DADF489346131EEBF2DF85', X'28CF57A4FDBA1B47B0E890A4B5689233', X'28CF57A4FDBA1B47B0E890A4B5689233', 2, NULL, NULL, 2, NULL, 0, 'Fold the briouats. See How to Fold Briouats. With scissors, cut the pastry dough into strips about two-and-a-half inches wide. Place a single strip of warqa or spring roll wrapper  or two layered strips of phyllo dough  on your work surface. (If the strip of warqa is less than 10 inches long, use two layers.) Lightly brush butter on the bottom two-thirds of the dough. Place a large tablespoon of filling towards the bottom of the strip, and fold the bottom up to enclose the filling.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8625E384189C6E42B7875E5DFC945EBC', X'28CF57A4FDBA1B47B0E890A4B5689233', X'28CF57A4FDBA1B47B0E890A4B5689233', 3, NULL, NULL, 2, NULL, 0, 'Fold the bottom left corner of the enclosed dough upwards to the right, aligning it with the right edge of the strip. Then flip the bottom right corner upwards to the left, aligning it with the left edge of the dough. You should see a triangle take shape. Continue folding the triangle, flipping right and then left in this manner, until you reach the end of the dough.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'015B69A481DFF84B8A1CA573B4953770', X'28CF57A4FDBA1B47B0E890A4B5689233', X'28CF57A4FDBA1B47B0E890A4B5689233', 4, NULL, NULL, 2, NULL, 0, 'Trim any excess length off the edge of the dough, dab it with a little egg yolk, and tuck the end of the dough into the `pocket` formed by the open edge of dough on the briouat. You can use the tip of a butter knife or your scissors to help tuck in the flap of dough.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'255F7F13E9C86349B4C92D0417C2BD66', X'28CF57A4FDBA1B47B0E890A4B5689233', X'28CF57A4FDBA1B47B0E890A4B5689233', 5, NULL, NULL, 2, NULL, 0, 'Cook or freeze the briouats. Deep fry the briouats in hot oil until light golden brown. Drain and serve. Briouats stay warm for a long time, but if frying them well in advance of serving, you can reheat them in a 350 degree F oven for five to 10 minutes.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DB99CED3D5B78240A6786D7096867EBD', X'CE3E2E89DFE259458591A08C2FFDACB6', X'CE3E2E89DFE259458591A08C2FFDACB6', 0, NULL, NULL, 2, NULL, 0, 'Rinse the olives and remove the pits. Combine the olives with the capers.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A2E2E66296EAE048871D28FCCC104099', X'CE3E2E89DFE259458591A08C2FFDACB6', X'CE3E2E89DFE259458591A08C2FFDACB6', 1, NULL, NULL, 2, NULL, 0, 'Chop the olives and capers as finely as you can by hand, or place them in a food processor and pulse several times until the mixture is almost a paste. If using a processor, be careful not to overdo the blending as you want the tapenade to retain some texture.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F500FF8EB7B01448ADF255AF590AF555', X'CE3E2E89DFE259458591A08C2FFDACB6', X'CE3E2E89DFE259458591A08C2FFDACB6', 2, NULL, NULL, 2, NULL, 0, 'Mix the chopped olives with the remaining ingredients by hand, or in the food processor (again by pulsing several times, just until combined).');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AFD0973D7D99F2408BE6F9909319AF11', X'CE3E2E89DFE259458591A08C2FFDACB6', X'CE3E2E89DFE259458591A08C2FFDACB6', 3, NULL, NULL, 2, NULL, 0, 'Taste and adjust harissa, garlic, and lemon juice as desired.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'17AE997E6BEFB94B9B1E82A48C53E506', X'CE3E2E89DFE259458591A08C2FFDACB6', X'CE3E2E89DFE259458591A08C2FFDACB6', 4, NULL, NULL, 2, NULL, 0, 'For a thinner tapenade, increase the amount of olive oil, stirring in an additional teaspoon or two at a time, until you achieve the desired consistency.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5C70E14F3BCB3547A1E0A5FABCB15388', X'AAA32D27D5668247A5FB8E3E42A746D4', X'AAA32D27D5668247A5FB8E3E42A746D4', 0, NULL, NULL, 2, NULL, 0, 'Place the artichoke bottoms in a small pot.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'460DD1B197823F4199F65A5268E809DB', X'AAA32D27D5668247A5FB8E3E42A746D4', X'AAA32D27D5668247A5FB8E3E42A746D4', 1, NULL, NULL, 2, NULL, 0, 'Add about 2 cups of water (enough to barely cover the artichokes), the olive oil, spices, parsley and preserved lemon.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5D068CF36618034FA982243DE5578C94', X'AAA32D27D5668247A5FB8E3E42A746D4', X'AAA32D27D5668247A5FB8E3E42A746D4', 2, NULL, NULL, 2, NULL, 0, 'Cover and simmer the artichokes 20 to 30 minutes, until tender.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D45D58DE58EFC54FBFA22A725EDF7C12', X'AAA32D27D5668247A5FB8E3E42A746D4', X'AAA32D27D5668247A5FB8E3E42A746D4', 3, NULL, NULL, 2, NULL, 0, 'Remove the artichoke bottoms.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FDC6D0C1D0D8764E9640CFB8E77EE2F3', X'AAA32D27D5668247A5FB8E3E42A746D4', X'AAA32D27D5668247A5FB8E3E42A746D4', 4, NULL, NULL, 2, NULL, 0, 'Cut them into bite-size pieces if serving as a salad, or coarsely chop them if serving as a dip.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F22F598CAB9624439AE1F8CEC906BB3C', X'AAA32D27D5668247A5FB8E3E42A746D4', X'AAA32D27D5668247A5FB8E3E42A746D4', 5, NULL, NULL, 2, NULL, 0, 'Return the cut-up artichoke bottoms to the pot with the olives and adjust seasoning to taste.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2B5EAAD49080474F85657658571938F3', X'AAA32D27D5668247A5FB8E3E42A746D4', X'AAA32D27D5668247A5FB8E3E42A746D4', 6, NULL, NULL, 2, NULL, 0, 'Simmer, uncovered, for a few minutes longer to reduce the liquids to a thick sauce.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'41163072D2936143AFF2FA11A84A55DE', X'AAA32D27D5668247A5FB8E3E42A746D4', X'AAA32D27D5668247A5FB8E3E42A746D4', 7, NULL, NULL, 2, NULL, 0, 'Serve the salad room temperature or chilled.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'389916DA121881498D0EECE5DF713DE1', X'DB1FC5B6A30CA54DBD563582844EE8B3', X'DB1FC5B6A30CA54DBD563582844EE8B3', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5C64C784D84AAB45BAF132F6C97E9F98', X'DB1FC5B6A30CA54DBD563582844EE8B3', X'DB1FC5B6A30CA54DBD563582844EE8B3', 1, NULL, NULL, 2, NULL, 0, 'Melt the butter over low heat in a small saucepan. Increase the heat and bring the melted butter to a simmer. Simmer gently for 30 to 45 minutes, or until the milk solids on the bottom of the pot have turned a light amber.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BA5C7DDDA3BC5F4BA4AD2A492C8E82F8', X'DB1FC5B6A30CA54DBD563582844EE8B3', X'DB1FC5B6A30CA54DBD563582844EE8B3', 2, NULL, NULL, 2, NULL, 0, 'Place a piece of cheesecloth over a bowl and carefully pour the clarified butter (not the solids) through a cheesecloth. Repeat this straining several times if necessary to remove all traces of milk solids. You want only perfectly clear clarified butter for smen.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D0D99808C7D03149A286C163716A5A85', X'DB1FC5B6A30CA54DBD563582844EE8B3', X'DB1FC5B6A30CA54DBD563582844EE8B3', 3, NULL, NULL, 2, NULL, 0, 'Stir the salt into the strained clarified butter, then pour it into a jar. Cover, and store in a cool, dark place (a cupboard is fine) for a month or longer.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D3F621AC9FF1D044817258B1550C80D1', X'DB1FC5B6A30CA54DBD563582844EE8B3', X'DB1FC5B6A30CA54DBD563582844EE8B3', 4, NULL, NULL, 2, NULL, 0, 'After opening, store the smen in the refrigerator.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CE77338075CF084C93F3D4E5999BFA57', X'DB1FC5B6A30CA54DBD563582844EE8B3', X'DB1FC5B6A30CA54DBD563582844EE8B3', 5, NULL, NULL, 2, NULL, 0, 'Enjoy!');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D33C9ED96C9C0E4D8E59A63520337CC5', X'F501DA9B18BC4C4AA171117F754ADA1B', X'F501DA9B18BC4C4AA171117F754ADA1B', 0, NULL, NULL, 2, NULL, 0, 'Wash and dry the fish.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'09696A7B75BA5143AC7A8A4498E2D1E8', X'F501DA9B18BC4C4AA171117F754ADA1B', X'F501DA9B18BC4C4AA171117F754ADA1B', 1, NULL, NULL, 2, NULL, 0, 'Mix all of the Chermoula ingredients, adding a little more lemon juice or vegetable oil if necessary to thin the marinade.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BCF98BB2091D7242899379312045A3DE', X'F501DA9B18BC4C4AA171117F754ADA1B', X'F501DA9B18BC4C4AA171117F754ADA1B', 2, NULL, NULL, 2, NULL, 0, 'Taste and adjust the seasoning so that the chermoula is as salty, lemony, and spicy as you like.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'91776FFEEA246A429E3050E2E2DEC0A0', X'F501DA9B18BC4C4AA171117F754ADA1B', X'F501DA9B18BC4C4AA171117F754ADA1B', 3, NULL, NULL, 2, NULL, 0, 'Reserve a little more than half of the chermoula, and use the remaining chermoula to marinate the fish, rubbing the chermoula over the outside of the fish and inside the cavity.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FBDCE7537431684F86D32FCBF2482D50', X'F501DA9B18BC4C4AA171117F754ADA1B', X'F501DA9B18BC4C4AA171117F754ADA1B', 4, NULL, NULL, 2, NULL, 0, 'Cover the fish and leave to marinate while proceeding with the recipe. (Or, refrigerate the fish and leave to marinate at least several hours or even overnight.) Bring the fish to room temperature while proceeding with the recipe.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'03FD1769D26474489688593FE9BA85B3', X'F501DA9B18BC4C4AA171117F754ADA1B', X'F501DA9B18BC4C4AA171117F754ADA1B', 5, NULL, NULL, 2, NULL, 0, 'Preheat an oven to 425 F (220 C).');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0ED833797C938C47AE0694EAE3C4C088', X'F501DA9B18BC4C4AA171117F754ADA1B', X'F501DA9B18BC4C4AA171117F754ADA1B', 6, NULL, NULL, 2, NULL, 0, 'Lightly oil a baking dish with olive oil.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'465D51711A2FF049B598206476187B69', X'F501DA9B18BC4C4AA171117F754ADA1B', X'F501DA9B18BC4C4AA171117F754ADA1B', 7, NULL, NULL, 2, NULL, 0, 'Distribute the carrots over the bottom of the dish, crossing them to create a bed for the potatoes and fish.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E32C25550E610C43A387024D00749C15', X'F501DA9B18BC4C4AA171117F754ADA1B', X'F501DA9B18BC4C4AA171117F754ADA1B', 8, NULL, NULL, 2, NULL, 0, 'Add the potato slices in a single layer and season them to taste with salt, ginger, and pepper.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D0F7F4FC7D36BA4E853C773E3FC6FCA7', X'F501DA9B18BC4C4AA171117F754ADA1B', X'F501DA9B18BC4C4AA171117F754ADA1B', 9, NULL, NULL, 2, NULL, 0, 'Place the fish in the baking dish and arrange the tomato slices on and around the fish.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E39C78584C0E6741801BA474051536B5', X'F501DA9B18BC4C4AA171117F754ADA1B', X'F501DA9B18BC4C4AA171117F754ADA1B', 10, NULL, NULL, 2, NULL, 0, 'Thin the reserved chermoula with 1/4 cup of water and several tablespoons of olive oil, and spoon the mixture over the fish and vegetables.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1F59A686743EEC49AD7BDCBAC293288D', X'F501DA9B18BC4C4AA171117F754ADA1B', X'F501DA9B18BC4C4AA171117F754ADA1B', 11, NULL, NULL, 2, NULL, 0, 'Top the fish with the green pepper, lemon slices, chili peppers and olives.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'82ED94E74D78F74197F2E71CCE6A524E', X'F501DA9B18BC4C4AA171117F754ADA1B', X'F501DA9B18BC4C4AA171117F754ADA1B', 12, NULL, NULL, 2, NULL, 0, 'Cover the fish with aluminum foil and bake for 25 minutes.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'62D2689F46F2ED4B844ECEF6C1CCF1D9', X'F501DA9B18BC4C4AA171117F754ADA1B', X'F501DA9B18BC4C4AA171117F754ADA1B', 13, NULL, NULL, 2, NULL, 0, 'Remove the foil and continue baking for another 20 to 30 minutes, until the fish and vegetables are tender.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'864602F434B7A449B7F630210F3EBE0D', X'F501DA9B18BC4C4AA171117F754ADA1B', X'F501DA9B18BC4C4AA171117F754ADA1B', 14, NULL, NULL, 2, NULL, 0, 'If the liquids in the dish have not reduced to a thick sauce during the baking, you''ll want to do this on the stove. Carefully pour out the liquids into a saucepan and cover the fish to keep warm.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'64C8934C3D47B149A7C02951BBCC5E6F', X'F501DA9B18BC4C4AA171117F754ADA1B', X'F501DA9B18BC4C4AA171117F754ADA1B', 15, NULL, NULL, 2, NULL, 0, 'Reduce the liquids to a thick sauce over medium to medium-high heat and pour immediately back into the baking dish.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'25A110048CC91B478BF5B730E3F8B1CD', X'F501DA9B18BC4C4AA171117F754ADA1B', X'F501DA9B18BC4C4AA171117F754ADA1B', 16, NULL, NULL, 2, NULL, 0, 'Garnish with the chopped parsley and serve.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5547E9D8B0435048A040458FDDE5374C', X'2674B4F4F464E84DBA68E339C38233C9', X'2674B4F4F464E84DBA68E339C38233C9', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F349D36D26B46943A7A19ACB948E9C6A', X'2674B4F4F464E84DBA68E339C38233C9', X'2674B4F4F464E84DBA68E339C38233C9', 1, NULL, NULL, 2, NULL, 0, 'Mix all ingredients in a bowl.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A592425AA228F545AD743C1480317829', X'2674B4F4F464E84DBA68E339C38233C9', X'2674B4F4F464E84DBA68E339C38233C9', 2, NULL, NULL, 2, NULL, 0, 'The chermoula is now ready for marinating meats and veggies, dressing potatoes before baking, or mixing into rice, quinoa, or couscous.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'510A2BDF27858B4DA7BC151DE1AA31C2', X'2674B4F4F464E84DBA68E339C38233C9', X'2674B4F4F464E84DBA68E339C38233C9', 3, NULL, NULL, 2, NULL, 0, 'Enjoy!');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AC967B3797147743BECF7D2404144F60', X'5BAA803AFC3A1F418A88A5B0E1A9A2ED', X'5BAA803AFC3A1F418A88A5B0E1A9A2ED', 0, NULL, NULL, 2, NULL, 0, 'Pour the olive oil into the base of a tagine or the bottom of a wide pot or Dutch oven. Distribute the sliced onion over the bottom.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B7EBF4B3E2F107438958DCB66CDA3D58', X'5BAA803AFC3A1F418A88A5B0E1A9A2ED', X'5BAA803AFC3A1F418A88A5B0E1A9A2ED', 1, NULL, NULL, 2, NULL, 0, 'In a bowl, toss the chicken with the chopped onion, garlic, ground nigella, whole nigella and spices. Arrange the chicken bone side down in the center of the tagine or pot, and distribute the chopped onions all around.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'178AF76D7EB9F542A060E5BA37EF9C0D', X'5BAA803AFC3A1F418A88A5B0E1A9A2ED', X'5BAA803AFC3A1F418A88A5B0E1A9A2ED', 2, NULL, NULL, 2, NULL, 0, 'Swirl 1/3 cup of water in the bowl to clean it of the spices, then add the water to the tagine or pot. If using smen, add it to the liquids.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'07C95EEFEE46044E8DB0E16273AF53B1', X'5BAA803AFC3A1F418A88A5B0E1A9A2ED', X'5BAA803AFC3A1F418A88A5B0E1A9A2ED', 3, NULL, NULL, 2, NULL, 0, 'Garnish the tagine with the cilantro bouquet, lemon and olives. Follow one of the cooking methods below.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D9963A325B050E4784DFD9EBC6AD1AB6', X'0B901116C6044D46B3B988632DF4D075', X'0B901116C6044D46B3B988632DF4D075', 0, NULL, NULL, 2, NULL, 0, 'In a mixing bowl, blend together the fine semolina, sugar, baking powder, and salt. Add the butter, and blend with your hands or a wooden spoon just until the mixture is the consistency of sand and the semolina grains have all been moistened.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BAB0C97FE75B0E478057E56D72BC34A8', X'0B901116C6044D46B3B988632DF4D075', X'0B901116C6044D46B3B988632DF4D075', 1, NULL, NULL, 2, NULL, 0, 'Add 1/2 cup milk and mix until dough forms. It should be quite moist, wet almost, and easily packed into a large mound. Add additional milk if necessary to achieve this consistency.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'07EACCA0B4608844AB9A8BC0AC9562D8', X'0B901116C6044D46B3B988632DF4D075', X'0B901116C6044D46B3B988632DF4D075', 2, NULL, NULL, 2, NULL, 0, 'Shape the dough into balls any size that you like and leave the dough to rest a few minutes.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4801D5922099A347874E1A1DF06F9639', X'0B901116C6044D46B3B988632DF4D075', X'0B901116C6044D46B3B988632DF4D075', 3, NULL, NULL, 2, NULL, 0, 'Preheat a griddle or frying pan over medium-low heat. While the griddle is heating, roll the balls in the coarse semolina (if using) and flatten each ball into a disc about 1/4-inch thick, or a bit thicker if you like.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1AEFE739275F3D4C90DB5584375316B1', X'0B901116C6044D46B3B988632DF4D075', X'0B901116C6044D46B3B988632DF4D075', 4, NULL, NULL, 2, NULL, 0, 'Cook the harcha over fairly low heat, about 5 to 10 minutes on each side, until they turn a pale to medium golden color. Flip only once, and check occasionally to be sure the harcha aren''t coloring too quickly, as they need some time to cook all the way through.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'07B20BCE046F8348895A91193C806CAA', X'0B901116C6044D46B3B988632DF4D075', X'0B901116C6044D46B3B988632DF4D075', 5, NULL, NULL, 2, NULL, 0, 'Serve immediately with jam, cheese, or butter.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3530E33ABF09BD40881F0FDC781C185F', X'E251B6F0E536754A840B7EE2F16D246B', X'E251B6F0E536754A840B7EE2F16D246B', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'917814AB75C54A42B1861CB469C68F8B', X'E251B6F0E536754A840B7EE2F16D246B', X'E251B6F0E536754A840B7EE2F16D246B', 1, NULL, NULL, 2, NULL, 0, 'Rinse the beetroots. Place in a pot or pressure cooker and cover with cold water. Boil or pressure cook the beets until tender. Test by piercing the beet with a sharp knife; if it can be inserted into the center, the beet is cooked. This can take up to an hour and a half when boiling, and up to 40 minutes when pressure cookinghow long depends on how large the beets are. If the beets you''re cooking are different sizes, simply check the cooking at intervals and remove the beets one by one as they''re done, drain the beets, rinse under cold running water and slide the skins off while the beets are still warm.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DFFEA6351D191B4DBF120759A91D2588', X'E251B6F0E536754A840B7EE2F16D246B', X'E251B6F0E536754A840B7EE2F16D246B', 2, NULL, NULL, 2, NULL, 0, 'Allow the beets to cool.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'42B8A0E0F2457A40A8868D8964F5AF7A', X'E251B6F0E536754A840B7EE2F16D246B', X'E251B6F0E536754A840B7EE2F16D246B', 3, NULL, NULL, 2, NULL, 0, 'Cut the beets into 1/4-inch to 1/2-inch slices of uniform thickness, and then cut each slice into uniformly sized cubes.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C801B116B7204644AFE39137AC372B10', X'E251B6F0E536754A840B7EE2F16D246B', X'E251B6F0E536754A840B7EE2F16D246B', 4, NULL, NULL, 2, NULL, 0, 'Transfer the cubed beets to a large bowl and combine with the remaining ingredients, seasoning to taste with salt and pepper. Cover tightly and refrigerate at least an hour or overnight.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E129E0371B0679438FA5AC6AE847CB14', X'E251B6F0E536754A840B7EE2F16D246B', X'E251B6F0E536754A840B7EE2F16D246B', 5, NULL, NULL, 2, NULL, 0, 'Prior to serving, taste the salad and adjust the seasoning if desired. The salad may be garnished with freshly slivered onion or a little more parsley.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'49F64C640090BD498BFF27DAD30EADD1', X'0EC7CBEE803BB141A23ECE9C79A13B2B', X'0EC7CBEE803BB141A23ECE9C79A13B2B', 0, NULL, NULL, 2, NULL, 0, 'Wash the mallow very well several times in a large bowl of water. Drain thoroughly. Finely chop the leaves, place them in a steamer, and steam over simmering water for 15 to 20 minutes, until the leaves darken and are tender.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D7D43D2A44B20F43986F351E5FC28FC8', X'0EC7CBEE803BB141A23ECE9C79A13B2B', X'0EC7CBEE803BB141A23ECE9C79A13B2B', 1, NULL, NULL, 2, NULL, 0, 'Squeeze out any excess liquid from the steamed mallow, and transfer the mallow to a skillet. Add the remaining ingredients (except for the olives), stir to mix, and saute over medium heat for 5 to 10 minutes until the parsley is wilted and the flavors have blended. Adjust seasoning if desired.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'09B1E728701C1349843A4F3E9796B0E5', X'0EC7CBEE803BB141A23ECE9C79A13B2B', X'0EC7CBEE803BB141A23ECE9C79A13B2B', 2, NULL, NULL, 2, NULL, 0, 'Serve warm or at room temperature, garnishing the salad with the olives and preserved lemon rinds.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'28980F4F623784479E70EC2518B49754', X'F2A583C5BF2F6946A95F01DB3D7EF090', X'F2A583C5BF2F6946A95F01DB3D7EF090', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4A0F745152BE2B498959208E857B5A53', X'F2A583C5BF2F6946A95F01DB3D7EF090', X'F2A583C5BF2F6946A95F01DB3D7EF090', 1, NULL, NULL, 2, NULL, 0, 'Peel the carrots and grate them into very fine strands (my preference) or to a pulp.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F14DF6B54FCBA14599C5276C439115F5', X'F2A583C5BF2F6946A95F01DB3D7EF090', X'F2A583C5BF2F6946A95F01DB3D7EF090', 2, NULL, NULL, 2, NULL, 0, 'Peel the oranges and remove as much of the pith as possible by scraping the orange with a knife Separate the oranges into slices, then cut the slices into 1/2-inch pieces.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9CDF04826C1A3745AF7CF50390D04CA8', X'F2A583C5BF2F6946A95F01DB3D7EF090', X'F2A583C5BF2F6946A95F01DB3D7EF090', 3, NULL, NULL, 2, NULL, 0, 'Mix the carrots and oranges with the remaining ingredients in a bowl. Taste and adjust sugar, lemon juice, and orange flower water as desired.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'30663388FB77AA4783A1026E1BE5744B', X'F2A583C5BF2F6946A95F01DB3D7EF090', X'F2A583C5BF2F6946A95F01DB3D7EF090', 4, NULL, NULL, 2, NULL, 0, 'Cover the salad and refrigerate for a 1/2 hour or more to allow the salad to macerate.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C3E64E75F801FA40ACF9454D2796E877', X'F2A583C5BF2F6946A95F01DB3D7EF090', X'F2A583C5BF2F6946A95F01DB3D7EF090', 5, NULL, NULL, 2, NULL, 0, 'Check on the salad before serving. If it''s too dry for your liking, mix in a little additional orange juice.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1E6A62611732044D8DCBD2A446FEBA2D', X'F2A583C5BF2F6946A95F01DB3D7EF090', X'F2A583C5BF2F6946A95F01DB3D7EF090', 6, NULL, NULL, 2, NULL, 0, 'Spoon the salad and juice into individual serving bowls and serve.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'79857B6BCACDE74EAEEF24366A9C5283', X'3423B34B938FEB4488E90F64ED296447', X'3423B34B938FEB4488E90F64ED296447', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CB871EFFA259B84BABAA0E0E86C12251', X'3423B34B938FEB4488E90F64ED296447', X'3423B34B938FEB4488E90F64ED296447', 1, NULL, NULL, 2, NULL, 0, 'Trim or snap off the ends from the green beans, removing any stringy fibers which may run the length of the bean. Not all beans will have this fiber. If the beans are excessively long, they can be cut or broken in half for easier eating.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0880442C9D4E21479ECDF340F6CCE49A', X'3423B34B938FEB4488E90F64ED296447', X'3423B34B938FEB4488E90F64ED296447', 2, NULL, NULL, 2, NULL, 0, 'Wash and drain the beans.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E355CC756C2EA544AD2605A89E7E8D44', X'3423B34B938FEB4488E90F64ED296447', X'3423B34B938FEB4488E90F64ED296447', 3, NULL, NULL, 2, NULL, 0, 'Boil or steam the beans just until crisp-tender, about 5 to 7 minutes.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FB271CB102C5FF4E88DF5A39FF64D684', X'3423B34B938FEB4488E90F64ED296447', X'3423B34B938FEB4488E90F64ED296447', 4, NULL, NULL, 2, NULL, 0, 'If boiling, drain and submerge the beans in an ice-water bath for a minute or two, and drain again.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'444480536E25D5478B1A673B4B02DB30', X'3423B34B938FEB4488E90F64ED296447', X'3423B34B938FEB4488E90F64ED296447', 5, NULL, NULL, 2, NULL, 0, 'Melt the butter or heat the olive oil in a sauté pan or skillet over medium-low heat. Add the garlic and sauté just for 1 minute until fragrant; do not burn.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'68E4AAC578DCFC48B7534ECC23039682', X'3423B34B938FEB4488E90F64ED296447', X'3423B34B938FEB4488E90F64ED296447', 6, NULL, NULL, 2, NULL, 0, 'Add the green beans to the pan and season to taste with salt, black pepper, cumin, and cayenne pepper. Toss gently and cook just until the beans are heated through.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'ECCF598F3B17CB429BD45BEB8E2BA5D5', X'3423B34B938FEB4488E90F64ED296447', X'3423B34B938FEB4488E90F64ED296447', 7, NULL, NULL, 2, NULL, 0, 'If using parsley, gently stir it in and sprinkle over the top as a garnish.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5071D917B784A944BEB0AF3598A9095F', X'3423B34B938FEB4488E90F64ED296447', X'3423B34B938FEB4488E90F64ED296447', 8, NULL, NULL, 2, NULL, 0, 'Serve immediately.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'582CBA4CF42A8F4EA6338910D87C518D', X'D9B2B716AD2BE14C82A74AE8B4E27385', X'D9B2B716AD2BE14C82A74AE8B4E27385', 0, NULL, NULL, 2, NULL, 0, 'Activate the yeast by combining it with 1/4 cup of the warm water and a teaspoon of the sugar. Set the mixture aside until it''s frothy, about 5 to 10 minutes.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'412CE45CB574764FAE9BB0EE605159B4', X'D9B2B716AD2BE14C82A74AE8B4E27385', X'D9B2B716AD2BE14C82A74AE8B4E27385', 1, NULL, NULL, 2, NULL, 0, 'In a large bowl, combine the flours, remaining sugar, and salt in a mixing bowl. Add the yeast mixture, the oil, and the rest of the water, and mix to form a soft, manageable dough.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D621D6B92CF0D84191238DA0B756D78B', X'D9B2B716AD2BE14C82A74AE8B4E27385', X'D9B2B716AD2BE14C82A74AE8B4E27385', 2, NULL, NULL, 2, NULL, 0, 'Knead the dough in a mixer with a dough hook, or by hand on a lightly floured surface, for about 10 minutes or until smooth and elastic. The dough should be quite soft but not sticky. If it''s too sticky to work with, add a little flour one tablespoon at a time. If the dough feels a bit stiff, work in additional water one tablespoon at a time.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5F988E65CF8D65499406F7ADF5FE0AC9', X'D9B2B716AD2BE14C82A74AE8B4E27385', X'D9B2B716AD2BE14C82A74AE8B4E27385', 3, NULL, NULL, 2, NULL, 0, 'Divide the dough into smooth balls the size of small plums and let them rest, covered, on a lightly floured surface for about 10 minutes.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DCB5F5C5EC651C4FB94CE9378E68228C', X'D9B2B716AD2BE14C82A74AE8B4E27385', X'D9B2B716AD2BE14C82A74AE8B4E27385', 4, NULL, NULL, 2, NULL, 0, 'Roll out each ball into a thin circle about 1/8 inch thick. Set the rounds of dough on a clean, dry towel and cover. Leave to rise for about 1 to 1 1/2 hours, until light and puffy.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4E905358344A0E4FA57F28EA280A447C', X'D9B2B716AD2BE14C82A74AE8B4E27385', X'D9B2B716AD2BE14C82A74AE8B4E27385', 5, NULL, NULL, 2, NULL, 0, 'Heat a very lightly oiled cast-iron skillet, griddle or other nonstick pan over medium heat. Allow the pan to get quite hot.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'40D52D452D665F4C9620EEE82CCCD3EA', X'D9B2B716AD2BE14C82A74AE8B4E27385', X'D9B2B716AD2BE14C82A74AE8B4E27385', 6, NULL, NULL, 2, NULL, 0, 'Cook the batbout in batches, turning several times, until golden brown on both sides. The browning will be a bit uneven since the bread puffs up as it cooks, but that''s okay.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5C2B1E8FFACCEA4595F25E7847C8E52E', X'D9B2B716AD2BE14C82A74AE8B4E27385', X'D9B2B716AD2BE14C82A74AE8B4E27385', 7, NULL, NULL, 2, NULL, 0, 'Transfer the cooked batbout to a rack or towel-lined basket to cool. It''s fine to stack them while they''re warm.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7DBE5191B0EA8744A92C89A0BB68BD78', X'D9B2B716AD2BE14C82A74AE8B4E27385', X'D9B2B716AD2BE14C82A74AE8B4E27385', 8, NULL, NULL, 2, NULL, 0, 'Batbout will keep fresh for two days at room temperature. They freeze well and can be heated directly from the freezer in a microwave oven just until thawed. Avoid overheating or they will dry out.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'ACB516BD395B9F4DB390EDEB719CA582', X'017E2CB078A0B24DA5040622D8D93B61', X'017E2CB078A0B24DA5040622D8D93B61', 0, NULL, NULL, 2, NULL, 0, 'Prepare two baking sheets either by oiling the centers or by dusting the pans with coarse semolina.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0478A02A10E12746B5D70C4271168108', X'017E2CB078A0B24DA5040622D8D93B61', X'017E2CB078A0B24DA5040622D8D93B61', 1, NULL, NULL, 2, NULL, 0, 'In a large bowl combine the fine semolina or durum flour, white flour, sugar, and salt in a bowl. Make a large well in the center of the flour mixture, and add the yeast.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EC4DBAC369EC8643BB44075B39E887F9', X'017E2CB078A0B24DA5040622D8D93B61', X'017E2CB078A0B24DA5040622D8D93B61', 2, NULL, NULL, 2, NULL, 0, 'Add the oil and 1 1/2 cups warm water to the well, mixing to dissolve the yeast first, and then stirring the entire contents of the bowl to incorporate the water into the flour.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DC8C7850B7DF9C4D9A0C94C7EA6DAC35', X'017E2CB078A0B24DA5040622D8D93B61', X'017E2CB078A0B24DA5040622D8D93B61', 3, NULL, NULL, 2, NULL, 0, 'Turn the dough out onto a floured surface and begin kneading the dough. If necessary, add flour or water in very small amounts to make the dough soft and pliable, but not sticky. Continue kneading for 5 to 10 minutes, or until the dough is very smooth and elastic.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8E79B28EE26F5B4E965F82DD932AEDC1', X'017E2CB078A0B24DA5040622D8D93B61', X'017E2CB078A0B24DA5040622D8D93B61', 4, NULL, NULL, 2, NULL, 0, 'Divide the dough in half and shape each portion into a smooth circular mound. Roll the mounds in some coarse or fine semolina, pressing the grains gently into the surface of the dough.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FBE1BE243C1E8F4884F8646D85272596', X'017E2CB078A0B24DA5040622D8D93B61', X'017E2CB078A0B24DA5040622D8D93B61', 5, NULL, NULL, 2, NULL, 0, 'Place the dough on the prepared pans and cover with a towel. Allow the dough to rest for 10 minutes.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EA1BD993AE37E24CB759FE813890F896', X'017E2CB078A0B24DA5040622D8D93B61', X'017E2CB078A0B24DA5040622D8D93B61', 6, NULL, NULL, 2, NULL, 0, 'After the dough has rested, use the palm of your hand to flatten the dough into large, flat rounds about 1/4` thick. Cover the loaves with a towel, and leave to rise about one hour or longer, until the dough springs back when pressed lightly with a finger.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9694A596E3FF4741A61A42F94A1E76F8', X'017E2CB078A0B24DA5040622D8D93B61', X'017E2CB078A0B24DA5040622D8D93B61', 7, NULL, NULL, 2, NULL, 0, 'Preheat an oven to 435 F/225 C.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'17F5E6C695BC5347ADD21FEB1D0ECDEC', X'017E2CB078A0B24DA5040622D8D93B61', X'017E2CB078A0B24DA5040622D8D93B61', 8, NULL, NULL, 2, NULL, 0, 'Poke the dough with a fork in several places to create steam vents. Bake the bread for about 20 minutes  rotate the pans about halfway through the baking time  or until the loaves are nicely colored and sound hollow when tapped. Transfer the bread to a rack or towel-lined basket to cool.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'41D470928532884D9F3275787735E284', X'017E2CB078A0B24DA5040622D8D93B61', X'017E2CB078A0B24DA5040622D8D93B61', 9, NULL, NULL, 2, NULL, 0, 'Moroccan bread will keep only a day at room temperature, so plan to freeze leftovers. Thaw at room temperature, then reheat in the oven for just-baked freshness.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7584D64329152940924CF01468DA2E3F', X'BE0BC1AB0890E04F9FDF99DE32EFCCE3', X'BE0BC1AB0890E04F9FDF99DE32EFCCE3', 0, NULL, NULL, 2, NULL, 0, 'Preheat your oven to 400 F / 200 C.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8482160731B73542A6B9000C7D9D8F38', X'BE0BC1AB0890E04F9FDF99DE32EFCCE3', X'BE0BC1AB0890E04F9FDF99DE32EFCCE3', 1, NULL, NULL, 2, NULL, 0, 'Peel, core and thinly slice the apples.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'173A243CC74CF241AA012B784AC46D07', X'BE0BC1AB0890E04F9FDF99DE32EFCCE3', X'BE0BC1AB0890E04F9FDF99DE32EFCCE3', 2, NULL, NULL, 2, NULL, 0, 'In a 10-inch skillet, melt the butter over medium heat. Add the sugar and cinnamon and stir to distribute evenly.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2B53F3DB5E638C45BFBE5CE646266B37', X'BE0BC1AB0890E04F9FDF99DE32EFCCE3', X'BE0BC1AB0890E04F9FDF99DE32EFCCE3', 3, NULL, NULL, 2, NULL, 0, 'Add the sliced apples, cover, and cook for about 5 minutes, or until the apples are tender and a rich syrup has formed. Stir gently once or twice during cooking.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DD3EF66A39293A4E95B4E09E0437B29D', X'BE0BC1AB0890E04F9FDF99DE32EFCCE3', X'BE0BC1AB0890E04F9FDF99DE32EFCCE3', 4, NULL, NULL, 2, NULL, 0, 'Remove the pan from the heat. Push the apples to distribute them evenly over the bottom of the pan. If you like, you can take the time to arrange them in a pattern.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0CC5BAC181935446897525AD97595780', X'BE0BC1AB0890E04F9FDF99DE32EFCCE3', X'BE0BC1AB0890E04F9FDF99DE32EFCCE3', 5, NULL, NULL, 2, NULL, 0, 'In a medium-sized bowl, combine the flour with the sugar, baking powder, and salt. Add the egg yolks and milk, whisking until smooth.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'06C9EE2DC0E51546873DA8526CA36221', X'BE0BC1AB0890E04F9FDF99DE32EFCCE3', X'BE0BC1AB0890E04F9FDF99DE32EFCCE3', 6, NULL, NULL, 2, NULL, 0, 'With an electric mixer, beat the egg whites to soft peaks. Gently fold the egg whites into the flour mixture, then spread the pancake batter over the cooked apples. Place the pan in the preheated oven and bake for 10 to 15 minutes, or until golden and puffy.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'441BDE1ABE013E4B9E2EBB0921CEA8A0', X'BE0BC1AB0890E04F9FDF99DE32EFCCE3', X'BE0BC1AB0890E04F9FDF99DE32EFCCE3', 7, NULL, NULL, 2, NULL, 0, 'Remove the pan from the oven and use a rubber spatula to loosen the pancake from the sides of the pan. Invert a serving plate over the frying pan then carefully hold the pan and plate together and flip them over so that the cake releases onto the plate.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DCBC19A7D9E2D8478581C5C3EDB86DD4', X'BE0BC1AB0890E04F9FDF99DE32EFCCE3', X'BE0BC1AB0890E04F9FDF99DE32EFCCE3', 8, NULL, NULL, 2, NULL, 0, 'Serve warm or at room temperature. For best texture, the pancake is best eaten the same day.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FB93EA6B12FEEE40BF4451A129CA5692', X'09B6C93BA9654C44AFB4DFA1BCD68369', X'09B6C93BA9654C44AFB4DFA1BCD68369', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E1984405CEEF80468401788850F96654', X'09B6C93BA9654C44AFB4DFA1BCD68369', X'09B6C93BA9654C44AFB4DFA1BCD68369', 1, NULL, NULL, 2, NULL, 0, 'Peel the avocado and cut in half. Discard the pit.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0A053B83C74ABB4288764897AF2446B7', X'09B6C93BA9654C44AFB4DFA1BCD68369', X'09B6C93BA9654C44AFB4DFA1BCD68369', 2, NULL, NULL, 2, NULL, 0, 'Put the avocado and the sugar in a blender with 1 cup of milk. Blend until very creamy and smooth.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C0C262B67BE47647A819BADDACE9A3C4', X'09B6C93BA9654C44AFB4DFA1BCD68369', X'09B6C93BA9654C44AFB4DFA1BCD68369', 3, NULL, NULL, 2, NULL, 0, 'Gradually add another cup or two of milk to make the shake as thin or as thick as you like. How much milk you need will depend on the size and ripeness of the avocado.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E81445A808CAFA4D82FCC061025B76A1', X'09B6C93BA9654C44AFB4DFA1BCD68369', X'09B6C93BA9654C44AFB4DFA1BCD68369', 4, NULL, NULL, 2, NULL, 0, 'Add a handful of ice to the blender if you like your shake well-chilled, and blend for another minute. Taste and adjust sugar if necessary.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EFDBC3B2E5283D48A31686AD827B75B9', X'09B6C93BA9654C44AFB4DFA1BCD68369', X'09B6C93BA9654C44AFB4DFA1BCD68369', 5, NULL, NULL, 2, NULL, 0, 'Pour into glasses and serve.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9524413A1B054E48929F2BC2BD227AC0', X'9CE39DEB80BF7D4D88AF1DDB293E0A15', X'9CE39DEB80BF7D4D88AF1DDB293E0A15', 0, NULL, NULL, 2, NULL, 0, 'By hand or with a mixer, beat the eggs, sugar, and oil until thick. Beat in the vanilla (or zest) and salt, and stir in the cornflour until smooth. Mix in enough flour to form a somewhat stiff but pliable dough.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'ADD4B61A6A8D1045941BA50CF15B0C57', X'9CE39DEB80BF7D4D88AF1DDB293E0A15', X'9CE39DEB80BF7D4D88AF1DDB293E0A15', 1, NULL, NULL, 2, NULL, 0, 'Line two large baking trays with parchment paper. Feed the dough through a large fluted tip attached to a manual meat grinder, pastry bag or cookie press. Use scissors to cut the dough into 2-inch strips (I like to gently twist the dough) and arrange the sticks fairly close together on the lined trays.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9EC34255C40F794A83A3CD0B18DDE427', X'9CE39DEB80BF7D4D88AF1DDB293E0A15', X'9CE39DEB80BF7D4D88AF1DDB293E0A15', 2, NULL, NULL, 2, NULL, 0, 'Preheat your oven to 350 F (180 C). Bake the cookies, one pan at a time, for about 15 minutes, or until lightly colored. Carefully transfer the parchment paper with cookies to a rack to cool.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F386D34DD90D174C977B5ADAAE314DA4', X'8E7E1942EBCDA145810DD57670D1B2CC', X'8E7E1942EBCDA145810DD57670D1B2CC', 0, NULL, NULL, 2, NULL, 0, 'Preheat your oven to 350 F (180 C). Grease and flour a Bundt or small tube pan.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'553B03DC284DD14D88FF25229A9505CE', X'8E7E1942EBCDA145810DD57670D1B2CC', X'8E7E1942EBCDA145810DD57670D1B2CC', 1, NULL, NULL, 2, NULL, 0, 'If desired, separate the eggs. In a large bowl, beat the egg whites with an electric mixer until stiff. Set aside. (This step is optional; you can add whole eggs in the next step if you prefer.)');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'550E5041E91F1140977C5686F50C934C', X'8E7E1942EBCDA145810DD57670D1B2CC', X'8E7E1942EBCDA145810DD57670D1B2CC', 2, NULL, NULL, 2, NULL, 0, 'In another large bowl, beat together by hand or with a mixer the yogurt (reserve the empty container to use as your measure), the vegetable oil, sugar and egg yolks (or whole eggs, if you chose not to separate the eggs).');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2196F5D6FBBF6A47B892FA171B639F29', X'8E7E1942EBCDA145810DD57670D1B2CC', X'8E7E1942EBCDA145810DD57670D1B2CC', 3, NULL, NULL, 2, NULL, 0, 'Stir in the vanilla, flour, baking powder and salt; beat briskly by hand or with an electric mixer until smooth.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'ED447FE308A378439A2828DA11DA112A', X'8E7E1942EBCDA145810DD57670D1B2CC', X'8E7E1942EBCDA145810DD57670D1B2CC', 4, NULL, NULL, 2, NULL, 0, 'If you separated the eggs, gently fold the beaten egg whites into the cake batter, being careful to incorporate all of the whites evenly.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6DED9C334B16024DA5E2536B9A8FF8AB', X'8E7E1942EBCDA145810DD57670D1B2CC', X'8E7E1942EBCDA145810DD57670D1B2CC', 5, NULL, NULL, 2, NULL, 0, 'Pour the batter into the prepared cake and bake for 40 to 45 minutes, or until the cake tests done.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'94A76809D2AD29488126F1D7B4E4DE38', X'8E7E1942EBCDA145810DD57670D1B2CC', X'8E7E1942EBCDA145810DD57670D1B2CC', 6, NULL, NULL, 2, NULL, 0, 'Allow the cake to cool in the pan for five to 10 minutes before inverting onto a plate. The cake may be served warm, but it will improve in flavor and texture as it sits. Allow it to cool completely before storing in an airtight container.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'452DF14560191043A15AF528A1033EA9', X'03B59568160FB54AA9373177847DFB46', X'03B59568160FB54AA9373177847DFB46', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'65615EFDC2A0CA438F0A9184366C0A2B', X'03B59568160FB54AA9373177847DFB46', X'03B59568160FB54AA9373177847DFB46', 1, NULL, NULL, 2, NULL, 0, 'Line a large baking sheet or pan with aluminum foil or parchment paper. Set aside.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9ED22DBBAF103C47B2EB1D935A1B11F4', X'03B59568160FB54AA9373177847DFB46', X'03B59568160FB54AA9373177847DFB46', 2, NULL, NULL, 2, NULL, 0, 'In a heavy-bottomed skillet, combine the peanuts, sugar, and water. Cook over medium heat, stirring constantly until the mixture thickens into a syrup.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'300D604D0A4D164688D9FF77599F838D', X'03B59568160FB54AA9373177847DFB46', X'03B59568160FB54AA9373177847DFB46', 3, NULL, NULL, 2, NULL, 0, 'Continue cooking and stirring for about 10 minutes, or until the liquids evaporate and a sandy-textured sugar mixture coats the peanuts.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'206B94738C00764DB018167AE403BB91', X'03B59568160FB54AA9373177847DFB46', X'03B59568160FB54AA9373177847DFB46', 4, NULL, NULL, 2, NULL, 0, 'Lower the heat a bit and continue stirring as the excess sugar in the pan begins to melt. This will take several minutes. Once the syrup begins to form, it will change in color from clear to golden to amber. Stir constantly, and be careful that the heat is not so high as to burn or darken the syrup too much.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'99601579E6C9454EABCBEB618BAEEB3D', X'03B59568160FB54AA9373177847DFB46', X'03B59568160FB54AA9373177847DFB46', 5, NULL, NULL, 2, NULL, 0, 'When the syrup is a light-to-medium-amber color and the sugary coating on the peanuts has glazed, remove the skillet from the heat.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A05859FD06C114479F7D498329102DF8', X'03B59568160FB54AA9373177847DFB46', X'03B59568160FB54AA9373177847DFB46', 6, NULL, NULL, 2, NULL, 0, 'Sprinkle the salt and orange flower water (if using) over the peanuts and stir.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C2807C090661594786761B970349AAC8', X'03B59568160FB54AA9373177847DFB46', X'03B59568160FB54AA9373177847DFB46', 7, NULL, NULL, 2, NULL, 0, 'Turn the peanuts out onto your prepared pan and quickly spread them in a single layer. Allow them to cool and harden before serving.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DD36939D7F4CE14BB08DA95E0942BCD4', X'03B59568160FB54AA9373177847DFB46', X'03B59568160FB54AA9373177847DFB46', 8, NULL, NULL, 2, NULL, 0, 'Be sure the caramelized peanuts have cooled completely before storing in an airtight container.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FB597DC6B1EE5149B92A497CD5107FE8', X'03B59568160FB54AA9373177847DFB46', X'03B59568160FB54AA9373177847DFB46', 9, NULL, NULL, 2, NULL, 0, 'Enjoy!');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B3CD16CE460B3C4DB687B4F48EC5705F', X'71C291F31E983A48BB22901C6437513C', X'71C291F31E983A48BB22901C6437513C', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2000DE062195E447A64CDD2C844D9F0E', X'71C291F31E983A48BB22901C6437513C', X'71C291F31E983A48BB22901C6437513C', 1, NULL, NULL, 2, NULL, 0, 'Preheat an oven to 350 F / 180 C.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'764CEC479C2DD9478306A8C74A76FCA0', X'71C291F31E983A48BB22901C6437513C', X'71C291F31E983A48BB22901C6437513C', 2, NULL, NULL, 2, NULL, 0, 'Sift together the flour, salt, and cinnamon and set aside. In another bowl, with an electric mixer, beat the butter and sugar until creamy. Beat in the egg yolks and vanilla.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D7A4282C076EC1408804D661B98ED1E3', X'71C291F31E983A48BB22901C6437513C', X'71C291F31E983A48BB22901C6437513C', 3, NULL, NULL, 2, NULL, 0, 'Gradually mix in the flour mixture to form a thick, stiff dough. (Note: An electric mixer with a paddle attachment will help pull together the dough, which may initially be crumbly. Or, use your hands to warm the ingredients and work the dough, squeezing and kneading, to help combine the ingredients into a manageable dough.) If using the ground nuts, mix them in as well.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'04654BE4B6EEB34980241E0C98AACB9D', X'71C291F31E983A48BB22901C6437513C', X'71C291F31E983A48BB22901C6437513C', 4, NULL, NULL, 2, NULL, 0, 'Shape the dough into small balls about 1` (3 cm) in diameter. Place the balls on ungreased baking sheets (you can line the pans with parchment paper if you like) and flatten them by pressing with a fork in a criss-cross pattern. Brush the tops lightly with egg white (not too much).');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FAD06CA911CDF148817DB0EF6574D0AC', X'71C291F31E983A48BB22901C6437513C', X'71C291F31E983A48BB22901C6437513C', 5, NULL, NULL, 2, NULL, 0, 'Bake the cookies 8 to 9 minutes, or until barely colored for a chewy cookie; bake for 10 to 12 minutes, or until golden for a crispy cookie.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'58FF4EA9BF6CA54FA0E478CA9BEBB37A', X'71C291F31E983A48BB22901C6437513C', X'71C291F31E983A48BB22901C6437513C', 6, NULL, NULL, 2, NULL, 0, 'Allow the cookies to cool on the baking sheet for a minute before transferring them to a rack to cool completely.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'70F885C967C85C4CB44E211041F05688', X'754763AD9B90CB40BDD0AB26F82BB5C8', X'754763AD9B90CB40BDD0AB26F82BB5C8', 0, NULL, NULL, 2, NULL, 0, 'First, prepare the tarator dipping sauce by combing the yogurt, bread crumbs, crushed walnuts, garlic, lemon juice and salt in a chopper or food processor. Set the sauce aside to set.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D129D3C78A2B2443A5F85AAB18FB198F', X'754763AD9B90CB40BDD0AB26F82BB5C8', X'754763AD9B90CB40BDD0AB26F82BB5C8', 1, NULL, NULL, 2, NULL, 0, 'Next, prepare the mussels. Make sure the mussels have no remaining pieces of beard or debris. Rinse them under very cold water and pat them dry with paper towels.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DD3533ED43F6944FA1C4CEE3711735E9', X'754763AD9B90CB40BDD0AB26F82BB5C8', X'754763AD9B90CB40BDD0AB26F82BB5C8', 2, NULL, NULL, 2, NULL, 0, 'Dip each mussel in flour and shake off the excess.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A96AD97F12EE5346A6E90DFB73BA9DF6', X'754763AD9B90CB40BDD0AB26F82BB5C8', X'754763AD9B90CB40BDD0AB26F82BB5C8', 3, NULL, NULL, 2, NULL, 0, 'Put three floured mussels on each bamboo skewer leaving space at one end for holding.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'91C226210D514B44AFDC8152A0E29E92', X'754763AD9B90CB40BDD0AB26F82BB5C8', X'754763AD9B90CB40BDD0AB26F82BB5C8', 4, NULL, NULL, 2, NULL, 0, 'In a deep skillet or saucepan add the oil and heat it for frying.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E8CF6BCCDE47A946B75338C65CDAC3F8', X'754763AD9B90CB40BDD0AB26F82BB5C8', X'754763AD9B90CB40BDD0AB26F82BB5C8', 5, NULL, NULL, 2, NULL, 0, 'While the oil heats up, whisk together the club soda, flour, salt and egg yolk until smooth in a large mixing bowl.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'ADBC4B0AEA2430409585EB7B244E3BA1', X'754763AD9B90CB40BDD0AB26F82BB5C8', X'754763AD9B90CB40BDD0AB26F82BB5C8', 6, NULL, NULL, 2, NULL, 0, 'When it thickens to resemble the consistency of cake batter, stop whisking and set it aside.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3FE726ED0A09D645B36E9651F8F16644', X'754763AD9B90CB40BDD0AB26F82BB5C8', X'754763AD9B90CB40BDD0AB26F82BB5C8', 7, NULL, NULL, 2, NULL, 0, 'In a separate bowl, stir together the water and baking soda until well combined. Set it aside.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'02DCB3FAA0E78649B863D937791A7E6C', X'754763AD9B90CB40BDD0AB26F82BB5C8', X'754763AD9B90CB40BDD0AB26F82BB5C8', 8, NULL, NULL, 2, NULL, 0, 'Dip the mussels on each skewer in the batter and turn it until all the mussels are well covered.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1398A077ECFDA74F8CE793055DC88E1B', X'754763AD9B90CB40BDD0AB26F82BB5C8', X'754763AD9B90CB40BDD0AB26F82BB5C8', 9, NULL, NULL, 2, NULL, 0, 'After the batter, immediately dip the mussels in the baking soda and water mixture and put it in the hot oil to fry.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'070AAC71C3E4B24A8E61A84F8AAFCB99', X'754763AD9B90CB40BDD0AB26F82BB5C8', X'754763AD9B90CB40BDD0AB26F82BB5C8', 10, NULL, NULL, 2, NULL, 0, 'Turn the mussels on the skewer as they cook with thongs so they brown evenly on all sides.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'136DD5BE64AEED458D94224360DC8838', X'754763AD9B90CB40BDD0AB26F82BB5C8', X'754763AD9B90CB40BDD0AB26F82BB5C8', 11, NULL, NULL, 2, NULL, 0, 'Put a few hot skewers on a plate with a dolop of tarator sauce for dipping. You can also spread some tarator sauce between the crusts of a hunk of Turkish bread.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3B30C9AFE4EE1445A0BD57769774066F', X'754763AD9B90CB40BDD0AB26F82BB5C8', X'754763AD9B90CB40BDD0AB26F82BB5C8', 12, NULL, NULL, 2, NULL, 0, 'Then use the bread to hold the mussels and slide them off the skewer.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'00089DC58C7AF74480F3E4BEBE4EACBC', X'93EE9888017B7F41AADB2C0A8969A736', X'93EE9888017B7F41AADB2C0A8969A736', 0, NULL, NULL, 2, NULL, 0, 'Drain the chickpeas and rinse them under cold water in a wire strainer until the water runs clear. Put the olive oil in a pan and saute the crushed garlic on low heat until tender, but not browned.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E22A5FA7983D1B4189A8303841F2413A', X'93EE9888017B7F41AADB2C0A8969A736', X'93EE9888017B7F41AADB2C0A8969A736', 1, NULL, NULL, 2, NULL, 0, 'Add the drained chickpeas and enough water to just cover them. Bring to a boil, reduce the heat and cover, leaving the lid on a crack. Let the chickpeas simmer gently until the water disappears, usually 10 to 15 minutes.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EEF4BF70AEFD524E81D41BF9C4CE8904', X'93EE9888017B7F41AADB2C0A8969A736', X'93EE9888017B7F41AADB2C0A8969A736', 2, NULL, NULL, 2, NULL, 0, 'When the chickpeas are falling apart, transfer them, together with any remaining liquid, to a food processor. Add the lemon juice, lemon zest, tahini and spices and process them on high until you have a very smooth paste.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B4CA3EC2CD7C8F4F8EC64A2187F1AC88', X'93EE9888017B7F41AADB2C0A8969A736', X'93EE9888017B7F41AADB2C0A8969A736', 3, NULL, NULL, 2, NULL, 0, 'Stop a few times during processing to scrape the sides and add some water. Continue processing and adding water until you have the consistency you want. Some people prefer thick hummus while other prefer it softer. You decide.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AF84DD395CBFB54D9B3401E8D86A33C6', X'93EE9888017B7F41AADB2C0A8969A736', X'93EE9888017B7F41AADB2C0A8969A736', 4, NULL, NULL, 2, NULL, 0, 'Empty the hummus into a clay or other oven-proof baking dish. Cover the top lightly with aluminum foil. Bake it in a 350°F oven until it''s warmed well through. While the hummus is baking, fry your pastirma or other meat in a small skillet until crispy. Remove the hummus from the oven and sprinkle the top with the crumbled meat and drizzle it with some olive oil.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9335EF79E311E944889A2D5224186EB3', X'93EE9888017B7F41AADB2C0A8969A736', X'93EE9888017B7F41AADB2C0A8969A736', 5, NULL, NULL, 2, NULL, 0, 'You can serve your warm hummus the Turkish way, along with other appetizers, or with slivers of pita bread or crackers as a spread or dip. If you plan to serve the hummus later that day, cover it immediately to prevent the top and edges from drying and darkening. If you refrigerate your hummus, keep it air tight and make sure to give it time to warm up to room temperature again before serving.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2D484A353BBE364A95D71E48EA3F7D0E', X'AEFEE82D39B023428560FF9E0A84ABA1', X'AEFEE82D39B023428560FF9E0A84ABA1', 0, NULL, NULL, 2, NULL, 0, 'If youre using round yufka sheets, use a pizza cutter or sharp knife to cut them into triangles. Divide each circle into eight to ten triangular slices like you are slicing a pizza. The wide base of each triangle should be about four to five inches wide.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8647FE5B2F5B6645B07CB702FB92821E', X'AEFEE82D39B023428560FF9E0A84ABA1', X'AEFEE82D39B023428560FF9E0A84ABA1', 1, NULL, NULL, 2, NULL, 0, 'If you are using frozen phyllo, thaw it gently according to the package directions. Here are some tips on working with frozen phyllo.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'15B8908A378C6A4982A136603781A5C0', X'AEFEE82D39B023428560FF9E0A84ABA1', X'AEFEE82D39B023428560FF9E0A84ABA1', 2, NULL, NULL, 2, NULL, 0, 'Using two phyllo sheets, one on top of the other, begin at the center point of one of the short edges. Cut diagonally down to the left bottom corner. Starting again from the top center point, cut diagonally down to the right bottom corner. This will give you three pastry triangles from each rectangular sheet.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6B9044BE1BD7EB4694E5ED2B4BA1CB9F', X'AEFEE82D39B023428560FF9E0A84ABA1', X'AEFEE82D39B023428560FF9E0A84ABA1', 3, NULL, NULL, 2, NULL, 0, 'In a small bowl, crumble the white cheese or feta with your fingers. Add the chopped parsley and salt and pepper to taste.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'02C6EF16FEB98947A8ABF92FC30BF459', X'AEFEE82D39B023428560FF9E0A84ABA1', X'AEFEE82D39B023428560FF9E0A84ABA1', 4, NULL, NULL, 2, NULL, 0, 'Take a pastry triangle and position it so the wide end is at the bottom and the point is pointing away from you. Spread about a tablespoon of the filling in a thin line near the bottom edge, leaving about one-half inch empty on each side.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E304182CB3452A40B4CE6FC4E97CD601', X'AEFEE82D39B023428560FF9E0A84ABA1', X'AEFEE82D39B023428560FF9E0A84ABA1', 5, NULL, NULL, 2, NULL, 0, 'Before you begin rolling, fold the bottom right and left corners of the pastry toward the center to cover the edges of the filling. This will trap the filling inside as you roll the pastry up.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1B37291FE18239469055889192911C33', X'AEFEE82D39B023428560FF9E0A84ABA1', X'AEFEE82D39B023428560FF9E0A84ABA1', 6, NULL, NULL, 2, NULL, 0, 'Using your fingers, gently roll the pastry away from you toward the point of the triangle. Try to tuck in any open edges as you go along. When you get to the end, wet the inside of the point of the triangle and stick it to the roll. This will seal it keep it closed as it fries.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6E572789445E4B41B2113E755F0C649D', X'AEFEE82D39B023428560FF9E0A84ABA1', X'AEFEE82D39B023428560FF9E0A84ABA1', 7, NULL, NULL, 2, NULL, 0, 'Heat about two inches of oil to frying temperature. Add several pastries to the pan leaving some space in between them to prevent them from sticking to each other.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EBB0703E2FE8704CAEF685C191B9575E', X'AEFEE82D39B023428560FF9E0A84ABA1', X'AEFEE82D39B023428560FF9E0A84ABA1', 8, NULL, NULL, 2, NULL, 0, 'Using prongs or a fork, move them around as they fry for a more even color. When the ends begin to brown, flip them over and let the other side cook. When the pastries are done, they should be golden brown all over with darker ends.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E727964D6D9CC749A74643C7E5E07196', X'AEFEE82D39B023428560FF9E0A84ABA1', X'AEFEE82D39B023428560FF9E0A84ABA1', 9, NULL, NULL, 2, NULL, 0, 'Drain the pastries on a thick layer of paper towels before serving.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B7A96B736EA0A6459F494E683D9F96E4', X'90E13F707370A248BB8F115EB288F5FD', X'90E13F707370A248BB8F115EB288F5FD', 0, NULL, NULL, 2, NULL, 0, 'In a large bowl, melt the butter in the microwave. Next, stir in the milk, salt, and pepper. Microwave again on high until the milk is warm but not scalding.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2F9312111290F7429259B12061B73D4E', X'90E13F707370A248BB8F115EB288F5FD', X'90E13F707370A248BB8F115EB288F5FD', 1, NULL, NULL, 2, NULL, 0, 'In a separate bowl, toss together the crumbled white cheese and chopped parsley.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F9AAA8AB1B8CE841A5C378DAEF6D2435', X'90E13F707370A248BB8F115EB288F5FD', X'90E13F707370A248BB8F115EB288F5FD', 2, NULL, NULL, 2, NULL, 0, 'Butter the bottom and sides of a large, oven-proof baking tray. Drizzle a spoonful or two of the milk mixture over the bottom. Next, take the first sheet of yufka and place it in an uneven, wrinkly fashion to cover the bottom of the tray. Spoon about 1/6 of the milk mixture all over the yufka. Let it run into the cracks and crevices and around the edges.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'20FC362C8D24044E9D608E14034A1DC3', X'90E13F707370A248BB8F115EB288F5FD', X'90E13F707370A248BB8F115EB288F5FD', 3, NULL, NULL, 2, NULL, 0, 'Divide the cheese mixture roughly into five parts. Sprinkle 1/5 of the cheese mixture over the yufka. Now, do the same with the next layer. Repeat until you have five layers finished. Cover the top of your pastry completely with the last piece of yufka. Drizzle the last portion of the milk mixture over the top, wetting all of it.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1B1A23820AC99048A8E5BA5B54C96E5C', X'90E13F707370A248BB8F115EB288F5FD', X'90E13F707370A248BB8F115EB288F5FD', 4, NULL, NULL, 2, NULL, 0, 'Sprinkle the top of your pastry with sesame seeds or nigella seeds. Bake it in a 185 F/365 C oven for about 45 minutes, or until the top is nicely browned and the center is firm.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0098397C92F7B84DBC842C4BC3ABBE3C', X'90E13F707370A248BB8F115EB288F5FD', X'90E13F707370A248BB8F115EB288F5FD', 5, NULL, NULL, 2, NULL, 0, 'When your pastry is cooked, remove the pan from oven and sprinkle one or two tablespoons of cold water over the top, then wrap the whole thing in clean butcher''s paper or a clean towel. This helps to soften the top layer. Let the pastry rest for about 20 minutes before cutting it into squares.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2DF8D20E206A38488384F8DC23945C92', X'3247AE86B1DEC048844B524E970196FC', X'3247AE86B1DEC048844B524E970196FC', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8A984CA6A9A31B4ABD0C65467E5EADE1', X'3247AE86B1DEC048844B524E970196FC', X'3247AE86B1DEC048844B524E970196FC', 1, NULL, NULL, 2, NULL, 0, 'Bring the water to a rolling boil in a covered saucepan. Add the dry fava beans, reduce the heat and cover. Let the mixture simmer slowly until the fava beans are very soft and falling apart, and the water is reduced, about 20 minutes.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9EC0AEE44F401A46BD69EC7B8B3F1C04', X'3247AE86B1DEC048844B524E970196FC', X'3247AE86B1DEC048844B524E970196FC', 2, NULL, NULL, 2, NULL, 0, 'Once the beans are soft enough to fall apart when you touch them with a wooden spoon, add the salt, pepper, sugar, and olive oil to the pan and stir until combined.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E214DBBDB26C234CAF7A06801DBAE744', X'3247AE86B1DEC048844B524E970196FC', X'3247AE86B1DEC048844B524E970196FC', 3, NULL, NULL, 2, NULL, 0, 'Place a large, fine wire strainer over another pan or large bowl. Pour some of the pasty bean mixture into the strainer and press it through using a wooden spoon. Repeat until you''ve pressed through all the mixture. Don''t forget to remove the extra from the bottom of the strainer.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'40ABE1EEAAA06943B8DBB3E7FA4DF90C', X'3247AE86B1DEC048844B524E970196FC', X'3247AE86B1DEC048844B524E970196FC', 4, NULL, NULL, 2, NULL, 0, 'You should have a thick, pasty but smooth mixture with no lumps. If your paste seems too thick and dry, stir in about 1 tablespoon more olive oil.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'72F8A3D15889204B95EE352DFE6FD8C6', X'3247AE86B1DEC048844B524E970196FC', X'3247AE86B1DEC048844B524E970196FC', 5, NULL, NULL, 2, NULL, 0, 'While the paste is still warm, spoon it into a shallow rectangular, square, or circle-shaped serving dish about 3 inches deep. Glass works best. We like to use a 9-inch round glass oven dish. Smooth the top of the paste with the bottom of a spoon. Set it aside to cool and set.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1966177682CA044481A1D055F6A81916', X'3247AE86B1DEC048844B524E970196FC', X'3247AE86B1DEC048844B524E970196FC', 6, NULL, NULL, 2, NULL, 0, 'Once it''s cooled down to room temperature, cover it with plastic wrap and refrigerate it for several hours.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6EC0E80AF9F91045A316E8D01744ED6A', X'3247AE86B1DEC048844B524E970196FC', X'3247AE86B1DEC048844B524E970196FC', 7, NULL, NULL, 2, NULL, 0, 'When you remove it from the refrigerator, the paste should have set and be firm enough to cut. Wet the blade of a sharp knife and cut the fava into squares or diamonds. Make sure to re-wet the blade often to prevent sticking.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DE94AE1908C4034994C890C207517D95', X'3247AE86B1DEC048844B524E970196FC', X'3247AE86B1DEC048844B524E970196FC', 8, NULL, NULL, 2, NULL, 0, 'Remove the blocks of fava gently and arrange them on your serving plate. Drizzle them with a little extra-virgin olive oil and sprinkle the chopped, fresh dill weed over the top.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CA4F2615FAA23A49960D11CE33AF17DA', X'92962933CE9F5848A094EA8B07A1546E', X'92962933CE9F5848A094EA8B07A1546E', 0, NULL, NULL, 2, NULL, 0, 'Handle your squash flowers very gently to avoid bruising or tearing. Do not wash them as this will cause the delicate petals to stick together.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A27B3CD5BC66324B9C39162C31482C3A', X'92962933CE9F5848A094EA8B07A1546E', X'92962933CE9F5848A094EA8B07A1546E', 1, NULL, NULL, 2, NULL, 0, 'Prepare your flowers for stuffing by gently removing any green leaves at the base. Set them aside.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3ADE6BDCADE75F4B9B71D634B628254A', X'92962933CE9F5848A094EA8B07A1546E', X'92962933CE9F5848A094EA8B07A1546E', 2, NULL, NULL, 2, NULL, 0, 'Now it''s time to prepare the stuffing. First, put a shallow pan on a medium flame and fry the onions in the olive oil until they are soft and reduced.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1F74448DFED7454D94B1BB7B023ADCD9', X'92962933CE9F5848A094EA8B07A1546E', X'92962933CE9F5848A094EA8B07A1546E', 3, NULL, NULL, 2, NULL, 0, 'Add the pine nuts and brown them.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CB1B46CD8631484D805FC6E2F03CBEF3', X'92962933CE9F5848A094EA8B07A1546E', X'92962933CE9F5848A094EA8B07A1546E', 4, NULL, NULL, 2, NULL, 0, 'Add all the other dry ingredients and mix well.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4CBB092BCEEC5C489EA46D5C72653404', X'92962933CE9F5848A094EA8B07A1546E', X'92962933CE9F5848A094EA8B07A1546E', 5, NULL, NULL, 2, NULL, 0, 'Stir in the water, then bring to a boil. Cover and simmer over low heat until the water is almost absorbed. You''ll know it''s ready when you have a fragrant mixture of half-cooked rice.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C7AF39E4D376C341B3BE807E03DF7C7B', X'92962933CE9F5848A094EA8B07A1546E', X'92962933CE9F5848A094EA8B07A1546E', 6, NULL, NULL, 2, NULL, 0, 'Once your filling cools down enough to handle, you can begin stuffing your flowers. Handling each flower gently, fill the center of each using a small spoon and fold the petal tips over the top of the stuffing, so they overlap, covering the filling completely.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1B29B3673C64D3428D59D1386BA91A23', X'92962933CE9F5848A094EA8B07A1546E', X'92962933CE9F5848A094EA8B07A1546E', 7, NULL, NULL, 2, NULL, 0, 'Do your best not to overstuff the flowers. You need to leave enough room for the rice to expand while they cook. Don''t worry, it takes some practice to get it just right, but once your fingers get used to it, stuffing is easy!');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F00D20346C503641BCB6CF0AD0F74C6B', X'92962933CE9F5848A094EA8B07A1546E', X'92962933CE9F5848A094EA8B07A1546E', 8, NULL, NULL, 2, NULL, 0, 'As you stuff each flower, place them side by sidelining the bottom of another shallow pan. You can start a second layer if necessary, but keeping them to a single layer is always best.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'216C9BD26EEA234DB5615472500407AF', X'92962933CE9F5848A094EA8B07A1546E', X'92962933CE9F5848A094EA8B07A1546E', 9, NULL, NULL, 2, NULL, 0, 'Add enough water to the pan to cover the stuffed flowers, but no more. Drizzle about 2 tablespoons of extra-virgin olive over the mixture and add some extra salt to the water.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1ED290E05C328043B87008076D7AF623', X'92962933CE9F5848A094EA8B07A1546E', X'92962933CE9F5848A094EA8B07A1546E', 10, NULL, NULL, 2, NULL, 0, 'Bring the pan to a soft boil, then immediately reduce the heat to low and cover. The flowers should simmer very gently with the cover on until all the water is absorbed.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'745DEF10B6AEE94D8BD64809ECF3F9C7', X'92962933CE9F5848A094EA8B07A1546E', X'92962933CE9F5848A094EA8B07A1546E', 11, NULL, NULL, 2, NULL, 0, 'Remove the pan from the heat and let it continue to steam until it cools to room temperature. If too much condensation forms on the lid, cover the pan with paper towels and close the lid over them letting the flowers continue to steam.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0CF938523FEBA44D80479327658CCF0B', X'92962933CE9F5848A094EA8B07A1546E', X'92962933CE9F5848A094EA8B07A1546E', 12, NULL, NULL, 2, NULL, 0, 'When the flowers have cooled down, they will become firmer and easier to remove from the pan. Remove each one gently with your fingers being careful not to damage it, or the other flowers around it.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0C02DF9161B21D419F40C8B34E65488C', X'92962933CE9F5848A094EA8B07A1546E', X'92962933CE9F5848A094EA8B07A1546E', 13, NULL, NULL, 2, NULL, 0, 'Place each ''dolma'' on a decorative serving dish creating a random pile, drizzle them with some more olive oil and garnish the plate with a few fresh zucchini flowers, if desired.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'48BA2A527455214DB56AEB8E5AF6E111', X'8B9FE3DE32D93C46A5582B60510AF1FA', X'8B9FE3DE32D93C46A5582B60510AF1FA', 0, NULL, NULL, 2, NULL, 0, 'First cut off any roots from the purslane. Wash and drain it twice to make sure there is no soil or grit left on the leaves.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'880B910DCF8C7048BCB2E81A0C25C8E5', X'8B9FE3DE32D93C46A5582B60510AF1FA', X'8B9FE3DE32D93C46A5582B60510AF1FA', 1, NULL, NULL, 2, NULL, 0, 'Separate the leaves from the stems. You can save the stems for use in other recipes if you wish. Spin the leaves in a salad spinner to remove any extra moisture.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D609E8247DB8E4438001435E038FF242', X'8B9FE3DE32D93C46A5582B60510AF1FA', X'8B9FE3DE32D93C46A5582B60510AF1FA', 2, NULL, NULL, 2, NULL, 0, 'In a separate bowl, whip together the other ingredients using a wire whisk until smooth and creamy. Adjust the amount of salt and pepper to your taste.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A5BA61306A354B44B121BA847B7E426C', X'8B9FE3DE32D93C46A5582B60510AF1FA', X'8B9FE3DE32D93C46A5582B60510AF1FA', 3, NULL, NULL, 2, NULL, 0, 'Add the purslane leaves and mix together well. Make sure all the leaves are covered with the yogurt mixture.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4155F70F6F077B439B09D99335E0F76F', X'8B9FE3DE32D93C46A5582B60510AF1FA', X'8B9FE3DE32D93C46A5582B60510AF1FA', 4, NULL, NULL, 2, NULL, 0, 'Serve immediately, or cover and chill it for several hours or overnight. The flavor actually gets better if the salad rests for a while. Gently stir it again before serving.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E05982D82468BD4EB16FEA5A0C2F16DA', X'DD539EC64696D4458F808AAC89BD17A8', X'DD539EC64696D4458F808AAC89BD17A8', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'06BD6148BD58AF4C9A9EAEAD5F76AEFF', X'DD539EC64696D4458F808AAC89BD17A8', X'DD539EC64696D4458F808AAC89BD17A8', 1, NULL, NULL, 2, NULL, 0, 'In a mixing bowl, stir together the feta cheese, egg, and parsley.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AD7FA2B847AA2342B82684AAC794F672', X'DD539EC64696D4458F808AAC89BD17A8', X'DD539EC64696D4458F808AAC89BD17A8', 2, NULL, NULL, 2, NULL, 0, 'Add the oil to a large pot or deep skillet and heat it to 350 F.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B7CBD7E39747AD46B2CFC30052FF8B11', X'DD539EC64696D4458F808AAC89BD17A8', X'DD539EC64696D4458F808AAC89BD17A8', 3, NULL, NULL, 2, NULL, 0, 'While the oil is heating, remove the phyllo dough from the refrigerator. (Be sure to have a damp towel on hand to cover the phyllo you are not working with. Phyllo dough dries out quickly if left uncovered.)');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4F1E71DD01CF4047A01B252324AD9759', X'DD539EC64696D4458F808AAC89BD17A8', X'DD539EC64696D4458F808AAC89BD17A8', 4, NULL, NULL, 2, NULL, 0, 'Take one sheet of phyllo and cut it into fourths. Each piece should be about 4 x 8 inches.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F1908E6FEBDC234DB0C069DC3F87E2FA', X'DD539EC64696D4458F808AAC89BD17A8', X'DD539EC64696D4458F808AAC89BD17A8', 5, NULL, NULL, 2, NULL, 0, 'Brush each piece with the melted butter. Place about 1 tablespoon of cheese mixture at the base of the dough strip and roll up. Seal by adding a little melted butter.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A1CA6027319B8A449BAA11989CC3ECE9', X'DD539EC64696D4458F808AAC89BD17A8', X'DD539EC64696D4458F808AAC89BD17A8', 6, NULL, NULL, 2, NULL, 0, 'Repeat with the remaining phyllo, butter and cheese mixture.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'029CD3591560B749966DC68364AE07ED', X'DD539EC64696D4458F808AAC89BD17A8', X'DD539EC64696D4458F808AAC89BD17A8', 7, NULL, NULL, 2, NULL, 0, 'Fry in about 2 inches of oil for 2-4 minutes, or until golden brown.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BAE461CA139D3F499383239A893E507E', X'DD539EC64696D4458F808AAC89BD17A8', X'DD539EC64696D4458F808AAC89BD17A8', 8, NULL, NULL, 2, NULL, 0, 'Use a sieve to remove from the oil and drain on a paper towel.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'79B7DE92EF9C8842ABC3809D87E0EAB5', X'DD539EC64696D4458F808AAC89BD17A8', X'DD539EC64696D4458F808AAC89BD17A8', 9, NULL, NULL, 2, NULL, 0, 'Serve immediately and enjoy!');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AEBA4FCEA9205342B1BB6EB257EB6501', X'899049BF367960429D18672FADED6A74', X'899049BF367960429D18672FADED6A74', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FAFE2044E192E241AF75FE6C934B5F07', X'899049BF367960429D18672FADED6A74', X'899049BF367960429D18672FADED6A74', 1, NULL, NULL, 2, NULL, 0, 'Place chicken in a large, covered saucepan. Fill the pan with water to just cover the chicken. Add the salt, pepper, peeled onion, and carrot whole. Bring to a boil, cover and reduce the heat. Let the chicken simmer slowly until the meat is falling off the bones.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C0379F8634C5894EA055BC607A560783', X'899049BF367960429D18672FADED6A74', X'899049BF367960429D18672FADED6A74', 2, NULL, NULL, 2, NULL, 0, 'While your chicken is cooking, you can crush your walnuts. The traditional way is to crush them between two layers of wax paper using a rolling pin. This allows the natural oil from the walnuts to blend with the mixture for the best flavor and consistency. If you''re in a hurry, you can process the walnuts in a food processor to make a fine powder.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A33A938024091E4B8DB559E1016415DC', X'899049BF367960429D18672FADED6A74', X'899049BF367960429D18672FADED6A74', 3, NULL, NULL, 2, NULL, 0, 'Once the chicken is thoroughly cooked, keep the pan covered and let it cool down. When the chicken is cool enough to handle, remove it from the broth and put it on a separate platter. Remove the onion and carrot and discard them.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E5EA5ECF8C03B748A9004B3ECE0C144A', X'899049BF367960429D18672FADED6A74', X'899049BF367960429D18672FADED6A74', 4, NULL, NULL, 2, NULL, 0, 'Wearing rubber gloves, separate all the meat from the bones, and discard all the bones, skin, and gristle. Then pull the large chunks of meat into small strips.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7AAA865DD4D8AC4788E60042CC893C02', X'899049BF367960429D18672FADED6A74', X'899049BF367960429D18672FADED6A74', 5, NULL, NULL, 2, NULL, 0, 'While the broth is still warm, strain it through a fine wire strainer into a large bowl. Add your stale bread to the broth in the bowl and break it up with your fingers so there are no large pieces of bread.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3B71D6134A202E4D9606DC77E525ADFC', X'899049BF367960429D18672FADED6A74', X'899049BF367960429D18672FADED6A74', 6, NULL, NULL, 2, NULL, 0, 'Add the crushed walnuts, crushed garlic and paprika and mix together well. Lastly, add the shredded chicken and mix until combined. Adjust the salt to your taste. Cover and refrigerate for a few hours before serving.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C8BA334F826AD944A5509AAFA5AA6634', X'899049BF367960429D18672FADED6A74', X'899049BF367960429D18672FADED6A74', 7, NULL, NULL, 2, NULL, 0, 'To prepare your dish for serving, spread the mixture in a shallow serving dish. Decorate the top with walnut halves. Mix one teaspoon of paprika with one tablespoon of olive oil and drizzle the ''red'' oil over the top for the final touch.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'79998D4608BD38449B0CBBA4680363CC', X'899049BF367960429D18672FADED6A74', X'899049BF367960429D18672FADED6A74', 8, NULL, NULL, 2, NULL, 0, 'Serve and enjoy!');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'386BD71BA26AE641B01EB1440DE2D9D3', X'5E1E453F69B8984A867A538A4F76CE17', X'5E1E453F69B8984A867A538A4F76CE17', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EFBDE6D26D131A429A9DCAA486A6058E', X'5E1E453F69B8984A867A538A4F76CE17', X'5E1E453F69B8984A867A538A4F76CE17', 1, NULL, NULL, 2, NULL, 0, 'In a mixing bowl, combine the grated cucumbers and minced garlic. Add salt to taste.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C136E66C04DEE74DB5592C6D117AACAC', X'5E1E453F69B8984A867A538A4F76CE17', X'5E1E453F69B8984A867A538A4F76CE17', 2, NULL, NULL, 2, NULL, 0, 'Mix in the yogurt with a wooden spoon, and transfer to a serving bowl. Garnish with the mint or dill, and drizzle olive oil on top. Serve immediately or cover with plastic wrap and refrigerate.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8E1DBD5B5F05C843B41BCDB674F4C95D', X'5E1E453F69B8984A867A538A4F76CE17', X'5E1E453F69B8984A867A538A4F76CE17', 3, NULL, NULL, 2, NULL, 0, 'Enjoy!');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2CC758FA93578C459F170DC6FCC4A92E', X'62ECA1B2C452804A9BFD0A82B1DA0068', X'62ECA1B2C452804A9BFD0A82B1DA0068', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0AABFE51978EE4429DEE0B4E7E3406D5', X'62ECA1B2C452804A9BFD0A82B1DA0068', X'62ECA1B2C452804A9BFD0A82B1DA0068', 1, NULL, NULL, 2, NULL, 0, 'Rinse the chicken in cold water. Blot the meat dry on paper towels.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DF804A8F943E5F46A8B48BC6E8D1D401', X'62ECA1B2C452804A9BFD0A82B1DA0068', X'62ECA1B2C452804A9BFD0A82B1DA0068', 2, NULL, NULL, 2, NULL, 0, 'Cut the meat into bite-sized cubes about the size of large dice.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FC9391E7E3B44743BEA4584072AF0DFA', X'62ECA1B2C452804A9BFD0A82B1DA0068', X'62ECA1B2C452804A9BFD0A82B1DA0068', 3, NULL, NULL, 2, NULL, 0, 'Using the finest grater possible, make a pulp from the onion and garlic cloves.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A13D9A581518D647A5397710CF1BA83F', X'62ECA1B2C452804A9BFD0A82B1DA0068', X'62ECA1B2C452804A9BFD0A82B1DA0068', 4, NULL, NULL, 2, NULL, 0, 'Pour this pulp and juice into a very fine mesh strainer and, using a wooden spoon, press out the juice into a separate bowl.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'17807BC5F5888142A5102AC1BCE71D8E', X'62ECA1B2C452804A9BFD0A82B1DA0068', X'62ECA1B2C452804A9BFD0A82B1DA0068', 5, NULL, NULL, 2, NULL, 0, 'Discard the onion and garlic pulp.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A5BF5B1D044EAE4CA56F88EA34D97C89', X'62ECA1B2C452804A9BFD0A82B1DA0068', X'62ECA1B2C452804A9BFD0A82B1DA0068', 6, NULL, NULL, 2, NULL, 0, 'In a glass or ceramic bowl, combine the onion-garlic juice, yogurt or milk, oil, tomato paste, black pepper, paprika, and salt.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BB3805E717F67140AF3ACFE453AA1566', X'62ECA1B2C452804A9BFD0A82B1DA0068', X'62ECA1B2C452804A9BFD0A82B1DA0068', 7, NULL, NULL, 2, NULL, 0, 'Add the cubed chicken and toss to coat.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FBBBB18D22B8B24B9D20A1E5A3B82CE3', X'62ECA1B2C452804A9BFD0A82B1DA0068', X'62ECA1B2C452804A9BFD0A82B1DA0068', 8, NULL, NULL, 2, NULL, 0, 'Cover the bowl and refrigerate it for at least 4 hours or overnight for the best results.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'67B3B4406A578146BBC3564DB9016E5A', X'62ECA1B2C452804A9BFD0A82B1DA0068', X'62ECA1B2C452804A9BFD0A82B1DA0068', 9, NULL, NULL, 2, NULL, 0, 'If using bamboo skewers, soak them in water for a few hours so they don''t ignite on the grill.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'049FB99962D09F418659FD0BE69B0F3A', X'62ECA1B2C452804A9BFD0A82B1DA0068', X'62ECA1B2C452804A9BFD0A82B1DA0068', 10, NULL, NULL, 2, NULL, 0, 'Preheat a grill to medium heat.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'390885EB8208374AB54812112201D784', X'62ECA1B2C452804A9BFD0A82B1DA0068', X'62ECA1B2C452804A9BFD0A82B1DA0068', 11, NULL, NULL, 2, NULL, 0, 'Remove the pieces from the marinade and thread them onto small metal or bamboo kebab skewers. The chunks of chicken can touch each other, but dont put them too close together to ensure they cook through. Discard any remaining marinade.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9757AD67DD19C940ACD1F7E3BE45AA4D', X'62ECA1B2C452804A9BFD0A82B1DA0068', X'62ECA1B2C452804A9BFD0A82B1DA0068', 12, NULL, NULL, 2, NULL, 0, 'Sprinkle the kebabs with salt and put them on the grill. Grill them evenly on all sides, about 12 minutes total. You can sprinkle Turkish spices like oregano, sumac, and paprika over the hot kebabs to add extra flavor.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8F52113988268B4993B5DB484E035F5B', X'62ECA1B2C452804A9BFD0A82B1DA0068', X'62ECA1B2C452804A9BFD0A82B1DA0068', 13, NULL, NULL, 2, NULL, 0, 'Serve and enjoy.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3E94577661D5494EA37BF89AE5A79181', X'C75CD453D4E9834B81A935AC9E593FA4', X'C75CD453D4E9834B81A935AC9E593FA4', 0, NULL, NULL, 2, NULL, 0, 'Heat the grill to medium.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'407D9AAB977F3E4EB00E055625F3F232', X'C75CD453D4E9834B81A935AC9E593FA4', X'C75CD453D4E9834B81A935AC9E593FA4', 1, NULL, NULL, 2, NULL, 0, 'Put all the ingredients in a large mixing bowl. If you are using bread slices, wet them and squeeze out the water before adding them to the bowl.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2DB8B071A28BEE42B6A65B3B72EAE5C9', X'C75CD453D4E9834B81A935AC9E593FA4', X'C75CD453D4E9834B81A935AC9E593FA4', 2, NULL, NULL, 2, NULL, 0, 'Briskly knead together all the ingredients for several minutes until well-blended. Set aside to rest for a few minutes.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'649D79E1D525A5408F725E70A5A0B217', X'C75CD453D4E9834B81A935AC9E593FA4', X'C75CD453D4E9834B81A935AC9E593FA4', 3, NULL, NULL, 2, NULL, 0, 'Break off an apricot-sized piece of the meat mixture and form the desired shape with your hands. Flattened meatballs or patties cook up the best.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'87DFB51F1A086746AB6FBE60B66EB22D', X'C75CD453D4E9834B81A935AC9E593FA4', X'C75CD453D4E9834B81A935AC9E593FA4', 4, NULL, NULL, 2, NULL, 0, 'Place each patty on the grill. Grill patties, turning once until browned on both sides.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4031F0A7129CD64C994207699684AC44', X'4E91D586A66A6C43BEBAE9CB06AF035A', X'4E91D586A66A6C43BEBAE9CB06AF035A', 0, NULL, NULL, 2, NULL, 0, 'Place the ½ chicken in a pot and fill it with enough water to cover the chicken by one inch. Peel the carrot and the onion and place them in the pot. Bring the water to a boil, then reduce the heat and cover the pot. Let the chicken simmer gently until the meat is falling off the bones, about 30 minutes. Set it aside to cool.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C4F4FD964E24A545A5CFB50FFCC0D7B8', X'4E91D586A66A6C43BEBAE9CB06AF035A', X'4E91D586A66A6C43BEBAE9CB06AF035A', 1, NULL, NULL, 2, NULL, 0, 'When the chicken is cool enough to handle, discard the onion and carrot. Remove the chicken from the broth and separate all the meat from the bones, Discard the bones, skin, and gristle. Strain the broth through a fine wire strainer and set it aside.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'455E6D88E4213A478E545239E9ACC053', X'4E91D586A66A6C43BEBAE9CB06AF035A', X'4E91D586A66A6C43BEBAE9CB06AF035A', 2, NULL, NULL, 2, NULL, 0, 'Drain and rinse the chickpeas and set them aside. In a large, shallow pan, melt the butter together with the oil. Add the rice and work the oils through all the grains with a wooden spoon. On low heat, continue to fry the dry rice for a few minutes.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EF251DBFE9D2F84FB162C6E2373D5ECD', X'4E91D586A66A6C43BEBAE9CB06AF035A', X'4E91D586A66A6C43BEBAE9CB06AF035A', 3, NULL, NULL, 2, NULL, 0, 'Add the chickpeas, chicken broth, water, and spices. Bring the liquid to a boil, then reduce the heat and cover. Let the rice simmer very gently on low heat until all the liquid is absorbed.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3B39ABB37216FB4CB1CFC491DB8EB0DB', X'4E91D586A66A6C43BEBAE9CB06AF035A', X'4E91D586A66A6C43BEBAE9CB06AF035A', 4, NULL, NULL, 2, NULL, 0, 'Turn off the heat. Open the top of the pan and arrange the chicken pieces over the top of the cooked rice. Do this quickly as you dont want to lose too much steam. Replace the cover and let the rice continue to steam for about 10 minutes more.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4D7A9A3C57E13F4AA3BAECB252B843DA', X'4E91D586A66A6C43BEBAE9CB06AF035A', X'4E91D586A66A6C43BEBAE9CB06AF035A', 5, NULL, NULL, 2, NULL, 0, 'When you are ready to serve it, remove the lid. Using a large bowl as a mold, arrange the chicken pieces in the bottom and up the sides of the bowl. Using the wooden spoon, fill the bowl with the hot rice and gently pack it down to make it firm. When the bowl is full, turn it upside down on your serving plate. You can garnish the top with a sprinkle of chopped fresh herbs if you wish.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'828F00F422DA5D40B4D826584AD17098', X'4E91D586A66A6C43BEBAE9CB06AF035A', X'4E91D586A66A6C43BEBAE9CB06AF035A', 6, NULL, NULL, 2, NULL, 0, 'Serve the rice piping hot with a glass of ice cold Turkish yogurt drink, called ayran (ai-RAHN).');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A2EE221C7E8D6E4E9AE5D299B7502291', X'8D5B955F0A40AD42996E729DF2DC048F', X'8D5B955F0A40AD42996E729DF2DC048F', 0, NULL, NULL, 2, NULL, 0, 'First, fill a medium saucepan with water. Add about one teaspoon of salt and bring it to a boil. Add the frozen or fresh shrimp. Boil for 1 to 2 minutes only. Drain the shrimp and run them under cold water to keep them from cooking too much.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'73EDB5EBB0A40E47844BC27FC1578771', X'8D5B955F0A40AD42996E729DF2DC048F', X'8D5B955F0A40AD42996E729DF2DC048F', 1, NULL, NULL, 2, NULL, 0, 'Peel and dice the onion and garlic cloves. Peel the tomatoes and dice them. Clean the green peppers and dice them about the same size as the tomatoes. Drain or clean the mushrooms.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3BF944E5D3E3014D905CD61CC941BD19', X'8D5B955F0A40AD42996E729DF2DC048F', X'8D5B955F0A40AD42996E729DF2DC048F', 2, NULL, NULL, 2, NULL, 0, 'Heat the olive oil in a saucepan and fry the onions and garlic until they soften and become transparent. Add the green peppers and cook a few minutes more. Add the diced tomato, mushrooms, tomato paste, and spices and let the mixture simmer until most of the liquid is gone.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A4674B5A9FD0E1459CA4B0677E2A9527', X'8D5B955F0A40AD42996E729DF2DC048F', X'8D5B955F0A40AD42996E729DF2DC048F', 3, NULL, NULL, 2, NULL, 0, 'Add the cooked shrimp and gently turn the mixture with a wooden spoon to evenly distribute the shrimp throughout. Transfer the mixture into one large or several small clay pots or oven-proof dishes. Cover the top(s) generously with grated cheese.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1D23B94EA65ED44899E09C72EAC4A241', X'8D5B955F0A40AD42996E729DF2DC048F', X'8D5B955F0A40AD42996E729DF2DC048F', 4, NULL, NULL, 2, NULL, 0, 'Put the casserole(s) in the oven set on the broil setting on a rack near the top. Cook them until the cheese is bubbly and browns nicely. Remove the casserole and serve it immediately while it''s still piping hot. Serve it with crusty bread to dip in the juice.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'99A37BB3F271BF4281958EA42D291608', X'31C943CF91E02240BD0E305F8561F1F6', X'31C943CF91E02240BD0E305F8561F1F6', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8896DD661F2EE342B6D4A0B31C7D30D4', X'31C943CF91E02240BD0E305F8561F1F6', X'31C943CF91E02240BD0E305F8561F1F6', 1, NULL, NULL, 2, NULL, 0, 'Wash the tomatoes and peel them using a small, sharp knife. Place the peeled tomatoes in a food processor and puree them, or finely grate them using a hand grater.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1A06AF910997CE4A862AE98BB2220DC6', X'31C943CF91E02240BD0E305F8561F1F6', X'31C943CF91E02240BD0E305F8561F1F6', 2, NULL, NULL, 2, NULL, 0, 'Rinse the bulgur under cold water in a wire strainer for a few minutes. Drain it and put it in a large mixing bowl along with the tomato puree. Mix together well, cover the bowl, and set it aside for about 1 hour until the bulgur is softened.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'526F9F80F5883B45960EA1176F180601', X'31C943CF91E02240BD0E305F8561F1F6', X'31C943CF91E02240BD0E305F8561F1F6', 3, NULL, NULL, 2, NULL, 0, 'Peel and coarsely chop the onions and garlic and put them in the food processor along with the stale bread and the walnut halves. Pulverize on high speed until you have a fine powder.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'376CEEF59A64F54AA5A242FDF5B0CC42', X'31C943CF91E02240BD0E305F8561F1F6', X'31C943CF91E02240BD0E305F8561F1F6', 4, NULL, NULL, 2, NULL, 0, 'Add this mixture to the tomato and bulgur along with the rest of the ingredients. Wearing rubber gloves, knead the mixture until all the ingredients are well combined.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9199E624DF6B9248B97E7EFFAC28AF9F', X'31C943CF91E02240BD0E305F8561F1F6', X'31C943CF91E02240BD0E305F8561F1F6', 5, NULL, NULL, 2, NULL, 0, 'Divide the mixture into portions small enough to fit inside the food processor. Process each portion on medium speed until smooth. Combine all the processed portions together in a large bowl and continue to knead until it has a smooth consistency.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F8B5EE658433E040BB8AE1A1CC710C42', X'31C943CF91E02240BD0E305F8561F1F6', X'31C943CF91E02240BD0E305F8561F1F6', 6, NULL, NULL, 2, NULL, 0, 'Place in a bowl and cover with an airtight seal; set aside for at least 5 hours or overnight.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1FC8ECEAA4B84743976C3565C757427B', X'31C943CF91E02240BD0E305F8561F1F6', X'31C943CF91E02240BD0E305F8561F1F6', 7, NULL, NULL, 2, NULL, 0, 'The next morning, break off bite-sized pieces of the mixture and shape them into the fingerprint shapes. Arrange them on a serving plate with leaves of Romaine lettuce and slivers of fresh lemon for squeezing.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5999D86CE687E5428EF23B0BC4C81202', X'7E0B19E963F8014CA868E7E0C5CF483C', X'7E0B19E963F8014CA868E7E0C5CF483C', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D1722EFE4E6E414E9FE7B147F939FCAF', X'7E0B19E963F8014CA868E7E0C5CF483C', X'7E0B19E963F8014CA868E7E0C5CF483C', 1, NULL, NULL, 2, NULL, 0, 'First, put the rice in a covered saucepan with 3 cups of water and bring to a boil. Reduce the heat, cover, and cook the rice until it softens.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'493BDCF3061AAB42B095C233FE8D836A', X'7E0B19E963F8014CA868E7E0C5CF483C', X'7E0B19E963F8014CA868E7E0C5CF483C', 2, NULL, NULL, 2, NULL, 0, 'In a separate bowl, beat the egg, plain yogurt, flour, and 1 cup of water briskly with a wire whisk until well blended and creamy. Make sure you have no lumps of flour or yogurt.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E1E2148802D2EF49B9514A92E2A80B81', X'7E0B19E963F8014CA868E7E0C5CF483C', X'7E0B19E963F8014CA868E7E0C5CF483C', 3, NULL, NULL, 2, NULL, 0, 'Using your wire whisk, stir the rice-water mixture and add the yogurt-water mixture while gently whisking. Then, add salt and white pepper. Allow the mixture to heat through until steaming. Continue whisking gently without letting the soup come to a boil. If the soup becomes too thick for your liking, add a little more water.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C51A6F4C1D18D540A278009A5C47FD2F', X'7E0B19E963F8014CA868E7E0C5CF483C', X'7E0B19E963F8014CA868E7E0C5CF483C', 4, NULL, NULL, 2, NULL, 0, 'In a separate pan, melt the butter and add the dried mint. Stir thoroughly and remove from the heat quickly. Add the butter and mint to the soup and continue whisking until well combined. Garnish each bowl of soup with a sprig of fresh mint leaves.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2E0EEE6A8D930042A1A9403F45FF4A83', X'7E0B19E963F8014CA868E7E0C5CF483C', X'7E0B19E963F8014CA868E7E0C5CF483C', 5, NULL, NULL, 2, NULL, 0, 'If you wish, you can also keep the butter and mint separate and drizzle it over the top of the soup just before serving. If you want to spice up your soup a little more, optionally add about 1 teaspoon hot red pepper flakes to the butter along with the mint.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B4B88F23E1954844946FFC1DD0361989', X'7E0B19E963F8014CA868E7E0C5CF483C', X'7E0B19E963F8014CA868E7E0C5CF483C', 6, NULL, NULL, 2, NULL, 0, 'Serve and enjoy!');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'582FC0D38CD6B748B23D6B48C4E0CB04', X'A47BB920DBFC8043A9ACF3907493276B', X'A47BB920DBFC8043A9ACF3907493276B', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'089EC94D7FE315429142EAA0649BC6AD', X'A47BB920DBFC8043A9ACF3907493276B', X'A47BB920DBFC8043A9ACF3907493276B', 1, NULL, NULL, 2, NULL, 0, 'Peel and dice the onion.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E2AB932B8457074C80ACCD851324394F', X'A47BB920DBFC8043A9ACF3907493276B', X'A47BB920DBFC8043A9ACF3907493276B', 2, NULL, NULL, 2, NULL, 0, 'Peel the tomatoes with a sharp knife and dice them, saving the juice.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0BD25DBB81EDF243905307588178F011', X'A47BB920DBFC8043A9ACF3907493276B', X'A47BB920DBFC8043A9ACF3907493276B', 3, NULL, NULL, 2, NULL, 0, 'Remove the seeds from the peppers and dice them as well.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CD49CA96BDAC22449B5757D06F9518EC', X'A47BB920DBFC8043A9ACF3907493276B', X'A47BB920DBFC8043A9ACF3907493276B', 4, NULL, NULL, 2, NULL, 0, 'Melt the butter in a large skillet. Sauté the onion until tender and translucent.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6D1505FFBF8340468876A0594CD2FFA7', X'A47BB920DBFC8043A9ACF3907493276B', X'A47BB920DBFC8043A9ACF3907493276B', 5, NULL, NULL, 2, NULL, 0, 'When the onions are cooked, add all the diced vegetables and spices and stir to combine.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0D0488DFDDA4F24EA41DB77C740D4EF6', X'A47BB920DBFC8043A9ACF3907493276B', X'A47BB920DBFC8043A9ACF3907493276B', 6, NULL, NULL, 2, NULL, 0, 'Bring the mixture to a boil, then cover and reduce the heat. Let the mixture simmer gently until the tomatoes and peppers soften and release their natural juice.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'891354391834C54DA1643AF8D23C7C31', X'A47BB920DBFC8043A9ACF3907493276B', X'A47BB920DBFC8043A9ACF3907493276B', 7, NULL, NULL, 2, NULL, 0, 'Remove the lid and continue to simmer, letting some of the liquid evaporate.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A0DA571936F94A439E0D4B33134EBE42', X'A47BB920DBFC8043A9ACF3907493276B', X'A47BB920DBFC8043A9ACF3907493276B', 8, NULL, NULL, 2, NULL, 0, 'Lightly salt and scramble the eggs in a separate bowl.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3DD9953972BBA842B561F994BD0F092F', X'A47BB920DBFC8043A9ACF3907493276B', X'A47BB920DBFC8043A9ACF3907493276B', 9, NULL, NULL, 2, NULL, 0, 'Then pour them in the center of the pan. Don''t stir the mixture. Let the eggs travel through the vegetables on their own. You can help them by moving the skillet around.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'79BF84A33BECB0478294BA5512CAD0BC', X'A47BB920DBFC8043A9ACF3907493276B', X'A47BB920DBFC8043A9ACF3907493276B', 10, NULL, NULL, 2, NULL, 0, 'Let the mixture continue to simmer until the eggs are cooked. If the tops of the eggs still seem raw, cover the pan for a few minutes.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DC05A01961A1B54B858B12DF81FD7BF4', X'A47BB920DBFC8043A9ACF3907493276B', X'A47BB920DBFC8043A9ACF3907493276B', 11, NULL, NULL, 2, NULL, 0, 'Your menemen should be fully cooked but still very juicy. You can drizzle some extra melted butter over the top before serving if you wish.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4FC5B9A32D741C4DA2C2AB8BB01F6303', X'A47BB920DBFC8043A9ACF3907493276B', X'A47BB920DBFC8043A9ACF3907493276B', 12, NULL, NULL, 2, NULL, 0, 'Serve and enjoy!');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EA56F9DC30D0E44AAC60AD87F3596BF7', X'249782FF229B80458741981F447D5E47', X'249782FF229B80458741981F447D5E47', 0, NULL, NULL, 2, NULL, 0, 'Squeeze the extra liquid out of the grated onion and place it in a covered saucepan along with the meat and the grated tomatoes. Add the salt, pepper, and sugar. Stir the ingredients until combined.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'49E22375A63E2146B1901C09AF1E9E97', X'249782FF229B80458741981F447D5E47', X'249782FF229B80458741981F447D5E47', 1, NULL, NULL, 2, NULL, 0, 'Start the mixture on high heat, then reduce the heat to low and cover. Let the meat simmer very slowly, stirring occasionally until it''s very tender and the tomato and onion are reduced to a thick sauce, about 1 hour.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D50E2CC0E01AC84793FB2127241B2C00', X'249782FF229B80458741981F447D5E47', X'249782FF229B80458741981F447D5E47', 2, NULL, NULL, 2, NULL, 0, 'While the meat is cooking, prepare the eggplant mash. If you are using fresh eggplant, roast them and extract the flesh.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'503A50C3B19AF940B678D18132F70E94', X'249782FF229B80458741981F447D5E47', X'249782FF229B80458741981F447D5E47', 3, NULL, NULL, 2, NULL, 0, 'If you are using jarred eggplant, drain the contents in a fine wire strainer and rinse with a little water. Press the eggplant flesh in the strainer to remove the extra liquid.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1305BAAF87AA7846A8C7EF477D3CCA08', X'249782FF229B80458741981F447D5E47', X'249782FF229B80458741981F447D5E47', 4, NULL, NULL, 2, NULL, 0, 'Melt the butter in a large shallow saucepan or skillet. Add the flour and stir for a minute or two without allowing the flour to burn. Add the milk and stir with a wire whisk to form a smooth bechamel.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A488DD8168E6094F81E99F762AC5360E', X'249782FF229B80458741981F447D5E47', X'249782FF229B80458741981F447D5E47', 5, NULL, NULL, 2, NULL, 0, 'Add the salt, pepper, and optional garlic and continue stirring with the whisk over low heat. Don''t allow the mixture to boil. Add your eggplant and cheese and continue stirring until you have a smooth mixture with no large pieces of eggplant.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A0C56E3822697A4980C2A25D062A06E4', X'249782FF229B80458741981F447D5E47', X'249782FF229B80458741981F447D5E47', 6, NULL, NULL, 2, NULL, 0, 'Turn the heat to the lowest possible setting, cover the pan and let it gently bubble for about 5 minutes. When your eggplant mash is ready, give it a final stir. Then cover the bottom of your serving platter generously with the hot mash.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E60B15457D83814BAC8310F28E6C9653', X'249782FF229B80458741981F447D5E47', X'249782FF229B80458741981F447D5E47', 7, NULL, NULL, 2, NULL, 0, 'Arrange the hot beef stew on top of the mash at the center of the platter, leaving a ring of mash all around the meat. Garnish with fresh rosemary sprigs just before serving.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'90DB045909C8984A9E5DC77FF10F6B86', X'34FE563CC49A1F488DF4F4F7CD77A245', X'34FE563CC49A1F488DF4F4F7CD77A245', 0, NULL, NULL, 2, NULL, 0, 'Cube the liver with a sharp knife. The cubes should be about the size of dice. Place the cubed liver in a colander and rinse under very cold water washing away any extra blood.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AF5DAD6E3B80C94290B42F84A347B4D3', X'34FE563CC49A1F488DF4F4F7CD77A245', X'34FE563CC49A1F488DF4F4F7CD77A245', 1, NULL, NULL, 2, NULL, 0, 'Set aside to drain for a few minutes, then turn the washed liver cubes out on paper towels to remove the extra moisture.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D8130FD3BCF9D1449398832BA55A71EB', X'34FE563CC49A1F488DF4F4F7CD77A245', X'34FE563CC49A1F488DF4F4F7CD77A245', 2, NULL, NULL, 2, NULL, 0, 'In a clean plastic bag, shake together the salt, pepper, flour, and paprika. Add liver cubes and shake them inside the bag until all are lightly covered with the flour mixture.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9B5519A51DAC75418BFB594E23390ECD', X'34FE563CC49A1F488DF4F4F7CD77A245', X'34FE563CC49A1F488DF4F4F7CD77A245', 3, NULL, NULL, 2, NULL, 0, 'Melt the butter and oil together in a large skillet. When hot enough for frying, add the floured liver cubes all at once. Gently arrange them with a wooden spoon, so all are in contact with the oil evenly.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'00D62815C555D84EB89C1216C11B3CF6', X'34FE563CC49A1F488DF4F4F7CD77A245', X'34FE563CC49A1F488DF4F4F7CD77A245', 4, NULL, NULL, 2, NULL, 0, 'When one side is browned, gently turn spoonfuls of the cubes and brown the other side. While browning you can arrange the cubes with your spoon to turn the uncooked sides, but never stir them too vigorously, or you''ll risk losing the flour coating.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F5557157EE25A240BF6D3BFCF930DB0F', X'34FE563CC49A1F488DF4F4F7CD77A245', X'34FE563CC49A1F488DF4F4F7CD77A245', 5, NULL, NULL, 2, NULL, 0, 'While the liver is cooking, peel and thinly slice the red onion, then cut the slices into quarters. Separate the layers and toss them together with the sumac and chopped parsley, and season with salt.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C3136D3937344B41B6C9BAD88AC6A7A4', X'34FE563CC49A1F488DF4F4F7CD77A245', X'34FE563CC49A1F488DF4F4F7CD77A245', 6, NULL, NULL, 2, NULL, 0, 'To serve, you can make a bed out the onion mixture and spoon the cooked liver on top, or you can serve the onions on the side in a separate bowl. The best side dish to go along with the liver are cubed potatoes that are baked or fried.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E607FA6FC05784429E6B8E5870D993A8', X'A97BB97EFE8F1346B1D4C80F62B5BEAD', X'A97BB97EFE8F1346B1D4C80F62B5BEAD', 0, NULL, NULL, 2, NULL, 0, 'Begin by peeling your eggplants. Using a vegetable peeler, begin at one end and remove a strip of peel from end to end. Leave a strip of peel about the same width, then peel another strip from end to end.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8EB96E46C5407744A009AFCEB67918A9', X'A97BB97EFE8F1346B1D4C80F62B5BEAD', X'A97BB97EFE8F1346B1D4C80F62B5BEAD', 1, NULL, NULL, 2, NULL, 0, 'Repeat this until your eggplant is peeled in a striped pattern. Peel all eggplant this way.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AAD39E09FEE9864C97A7566BB7228FB6', X'A97BB97EFE8F1346B1D4C80F62B5BEAD', X'A97BB97EFE8F1346B1D4C80F62B5BEAD', 2, NULL, NULL, 2, NULL, 0, 'Slice the eggplants about 1/4 inch thick. Put all the eggplant slices in a large bowl of salty water and let them soak for about 30 minutes. This removes the bitterness.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EB62BE1A44362E499BC1A55AB728947F', X'A97BB97EFE8F1346B1D4C80F62B5BEAD', X'A97BB97EFE8F1346B1D4C80F62B5BEAD', 3, NULL, NULL, 2, NULL, 0, 'Slice the green pepper into thin rings. Grate the tomato. Peel and chop the onion finely and fry it in 1 tablespoon of olive oil until tender.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9314208C8A408B458FB5948C0AEC619E', X'A97BB97EFE8F1346B1D4C80F62B5BEAD', X'A97BB97EFE8F1346B1D4C80F62B5BEAD', 4, NULL, NULL, 2, NULL, 0, 'Add the green pepper slices and continue frying until soft. Add the ground beef and fry until browned. Add the grated tomato, tomato paste, and spices.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'066CBC35DC6E914AAF64F17397A2A7BF', X'A97BB97EFE8F1346B1D4C80F62B5BEAD', X'A97BB97EFE8F1346B1D4C80F62B5BEAD', 5, NULL, NULL, 2, NULL, 0, 'Cover the pan and let the meat simmer for about 10 minutes. Remove from the heat.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FFE1095D590FEC438DD6CB1975AF07DB', X'A97BB97EFE8F1346B1D4C80F62B5BEAD', X'A97BB97EFE8F1346B1D4C80F62B5BEAD', 6, NULL, NULL, 2, NULL, 0, 'Grate the other three tomatoes and put them in a small saucepan.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B5B0FE8459ABFE4BBEF3469E11EC5FA1', X'A97BB97EFE8F1346B1D4C80F62B5BEAD', X'A97BB97EFE8F1346B1D4C80F62B5BEAD', 7, NULL, NULL, 2, NULL, 0, 'Add the tomato paste, salt, pepper, oregano, and sugar and let the sauce simmer for about 10 minutes. Remove from the heat.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C1A58042755EC946929DFF00583AD35B', X'A97BB97EFE8F1346B1D4C80F62B5BEAD', X'A97BB97EFE8F1346B1D4C80F62B5BEAD', 8, NULL, NULL, 2, NULL, 0, 'Remove the eggplant slices from the water and blot them dry with paper towels.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6B9F6B890A6EB44DBF45683E4385739D', X'A97BB97EFE8F1346B1D4C80F62B5BEAD', X'A97BB97EFE8F1346B1D4C80F62B5BEAD', 9, NULL, NULL, 2, NULL, 0, 'Fry them on both sides in vegetable oil until tender and golden brown on both sides. Drain them well on paper towels.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'786A228914FB0445A82D6AA827615700', X'A97BB97EFE8F1346B1D4C80F62B5BEAD', X'A97BB97EFE8F1346B1D4C80F62B5BEAD', 10, NULL, NULL, 2, NULL, 0, 'Line the bottom of a large baking tray with the fried eggplant slices to make a single layer. Make sure there are no gaps between the pieces.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'28B67FD114E3DB4B81F4252B0F7FEB99', X'A97BB97EFE8F1346B1D4C80F62B5BEAD', X'A97BB97EFE8F1346B1D4C80F62B5BEAD', 11, NULL, NULL, 2, NULL, 0, 'Spread the tomato sauce over the eggplant. Next, cover the tomato sauce layer with the meat mixture.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BA500B6F01C96441ABB379F2B7EA6E91', X'A97BB97EFE8F1346B1D4C80F62B5BEAD', X'A97BB97EFE8F1346B1D4C80F62B5BEAD', 12, NULL, NULL, 2, NULL, 0, 'In a large pan, melt the butter over high heat. Add the flour and turn it around a few times with a wooden spoon.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AC108FF0A6CC5E4CBEDB6D74442ACEA2', X'A97BB97EFE8F1346B1D4C80F62B5BEAD', X'A97BB97EFE8F1346B1D4C80F62B5BEAD', 13, NULL, NULL, 2, NULL, 0, 'Add the milk and seasonings and use a wire whisk to make the mixture smooth as it thickens. Your bechamel sauce is ready when its the consistency of pudding.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F3E865EC08F7A64A9C03BBBC01FB5BED', X'A97BB97EFE8F1346B1D4C80F62B5BEAD', X'A97BB97EFE8F1346B1D4C80F62B5BEAD', 14, NULL, NULL, 2, NULL, 0, 'Pour the hot bechamel sauce over the meat layer, covering it completely. Sprinkle the grated cheese evenly over the top of the casserole.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1EB295BF7AF6CA4A943A46A30C1CAEA5', X'A97BB97EFE8F1346B1D4C80F62B5BEAD', X'A97BB97EFE8F1346B1D4C80F62B5BEAD', 15, NULL, NULL, 2, NULL, 0, 'Bake the casserole in a 360 F/180 C oven until the top turns golden brown.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E22BC295BB0374478A9F628F49210924', X'A97BB97EFE8F1346B1D4C80F62B5BEAD', X'A97BB97EFE8F1346B1D4C80F62B5BEAD', 16, NULL, NULL, 2, NULL, 0, 'Remove the casserole from the oven and let it rest for at least 10 minutes before cutting it into squares.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'32F8AF1CFBF69B4C8349F51201C4778E', X'A97BB97EFE8F1346B1D4C80F62B5BEAD', X'A97BB97EFE8F1346B1D4C80F62B5BEAD', 17, NULL, NULL, 2, NULL, 0, 'Serve your musakka with Turkish-style rice pilaf.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B1CA97EB6667094F8F2AB5C7245DC545', X'3B4885CFEBAEE74184E917B3BAF423D6', X'3B4885CFEBAEE74184E917B3BAF423D6', 0, NULL, NULL, 2, NULL, 0, 'Prepare the eggplant. Using a vegetable peeler or sharp knife, peel alternate strips from the eggplant from end to end leaving a striped pattern. Let the eggplants soak in heavily salted water while you prepare the other ingredients.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1933BB92511B354CB55FAB33CEA562D1', X'3B4885CFEBAEE74184E917B3BAF423D6', X'3B4885CFEBAEE74184E917B3BAF423D6', 1, NULL, NULL, 2, NULL, 0, 'Prepare the filling. Put the olive oil in a large skillet. Fry the onions until they become tender and reduced. Add the ground beef and brown it thoroughly.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FB7814782DC7844BB1ECFC6D39D297DF', X'3B4885CFEBAEE74184E917B3BAF423D6', X'3B4885CFEBAEE74184E917B3BAF423D6', 2, NULL, NULL, 2, NULL, 0, 'Using a sharp paring knife, peel five of the tomatoes and dice them into cubes. Once the meat is nicely browned, add the cubed tomato and continue stirring. You can also substitute fresh tomatoes with canned, diced tomatoes. Drain off the juice before adding them to the meat.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E2387781BDFB1047B5A91BBD2782CE9E', X'3B4885CFEBAEE74184E917B3BAF423D6', X'3B4885CFEBAEE74184E917B3BAF423D6', 3, NULL, NULL, 2, NULL, 0, 'Add spices and garlic and stir well. Add the chopped parsley Stir the mixture about three minutes more then turn off the heat and let it rest.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D2BA2BD72E907B4CAEF23D71C95DA814', X'3B4885CFEBAEE74184E917B3BAF423D6', X'3B4885CFEBAEE74184E917B3BAF423D6', 4, NULL, NULL, 2, NULL, 0, 'Drain the eggplants and blot them dry with paper towels. In another skillet, heat the vegetable oil and fry the eggplants whole, turning them to cook evenly on all sides. When the eggplants soften, remove them from the oil and drain them on paper towels.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B958C21E00CC464983AAC0E269B305F3', X'3B4885CFEBAEE74184E917B3BAF423D6', X'3B4885CFEBAEE74184E917B3BAF423D6', 5, NULL, NULL, 2, NULL, 0, 'Line up the fried eggplants side by side in an oven-proof baking dish. Using your paring knife, cut a slit from end to end of each eggplant and gently open up each one with your fingers.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A0A0CB026D087F46AB17690248CB335D', X'3B4885CFEBAEE74184E917B3BAF423D6', X'3B4885CFEBAEE74184E917B3BAF423D6', 6, NULL, NULL, 2, NULL, 0, 'Fill the center of each eggplant with the meat mixture. Slice the two extra tomatoes thinly, cut each slice in half and place the slices over the filling. Do the same with the peppers.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F949673917FEB14D873BA356503AD578', X'3B4885CFEBAEE74184E917B3BAF423D6', X'3B4885CFEBAEE74184E917B3BAF423D6', 7, NULL, NULL, 2, NULL, 0, 'Mix the water with the tomato paste and pour it into the baking dish. Cook the eggplant in a 375 F/200 C oven for about 30 minutes. Remove the dish from the oven and let it rest for a few minutes before serving.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8135F4E11D0AA5428048246457CDCB61', X'B8876529F7D67A4EBE1C1D254FE3D8EF', X'B8876529F7D67A4EBE1C1D254FE3D8EF', 0, NULL, NULL, 2, NULL, 0, 'To make the meat cups, add the ground beef, egg and spices to a mixing bowl. Gently remove the crusts from the stale bread and wet it, squeezing out the extra water. Add the moistened bread to the bowl.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0D73FC5C3249B2459938FE37595A174B', X'B8876529F7D67A4EBE1C1D254FE3D8EF', X'B8876529F7D67A4EBE1C1D254FE3D8EF', 1, NULL, NULL, 2, NULL, 0, 'Wearing rubber gloves, knead all the ingredients together well until it thickens and stiffens, similar to dough. If you have time, cover the top with plastic wrap and let it rest about 15 minutes.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4884591C126D7C459C56C6328487408B', X'B8876529F7D67A4EBE1C1D254FE3D8EF', X'B8876529F7D67A4EBE1C1D254FE3D8EF', 2, NULL, NULL, 2, NULL, 0, 'To make a meat cup, tear off a ball of the meat mixture the size of a golf ball. Make a hole in the center with your finger, then as if you were shaping pottery by hand, use your fingers to lengthen the sides upward and enlarge the hole. Your meat cup should be able to stand up on its own when you place it in the baking pan.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8A5660A7BE9C414E8EC44E272CD94DB7', X'B8876529F7D67A4EBE1C1D254FE3D8EF', X'B8876529F7D67A4EBE1C1D254FE3D8EF', 3, NULL, NULL, 2, NULL, 0, 'Set your meat cup in a round or rectangular baking tray. Repeat these steps with rest of the meat mixture until you have filled your baking tray.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DEF0C764213A3C409DAA518C9353D3F2', X'B8876529F7D67A4EBE1C1D254FE3D8EF', X'B8876529F7D67A4EBE1C1D254FE3D8EF', 4, NULL, NULL, 2, NULL, 0, 'To make the mashed potatoes, peel your potatoes and boil them until very soft. Drain the excess water and mash them with a hand masher while they are still hot. Add the butter and spices and continue to mash.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3376E74E589CA24DBCAE3ACAFA277080', X'B8876529F7D67A4EBE1C1D254FE3D8EF', X'B8876529F7D67A4EBE1C1D254FE3D8EF', 5, NULL, NULL, 2, NULL, 0, 'Beat them with an electric mixer for several minutes to get rid of the lumps. Slowly add the milk while you continue to beat them until you have a still potato mixture that will hold peaks.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4BC160EF952E5149BBC1DEB4858F508F', X'B8876529F7D67A4EBE1C1D254FE3D8EF', X'B8876529F7D67A4EBE1C1D254FE3D8EF', 6, NULL, NULL, 2, NULL, 0, 'Using a dessert spoon, fill the centers of each meat cup with a generous portion of the mashed potato. You can also use a cake icing decorator. Make sure you pile the potato high to make peaks on top of each meat cup.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1E89137E6D5B11459145BECA37F0955D', X'B8876529F7D67A4EBE1C1D254FE3D8EF', X'B8876529F7D67A4EBE1C1D254FE3D8EF', 7, NULL, NULL, 2, NULL, 0, 'Sprinkle the top of each potato mound with grated cheese. Bake the köfte in a 390 F/200 C oven until the meatballs are cooked and the tops of the potatoes are slightly browned, about 20 minutes.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A3D78CB83D495F46AA4036B0ED0F4817', X'B8876529F7D67A4EBE1C1D254FE3D8EF', X'B8876529F7D67A4EBE1C1D254FE3D8EF', 8, NULL, NULL, 2, NULL, 0, 'Serve your çanak köftesi hot along with Turkish rice pilaf with orzo and a salad like Turkish shepherds salad.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'58112589956DBE4698F821BD2BE78B92', X'91D6A8FAA7197F45A977BE14BCA68BB3', X'91D6A8FAA7197F45A977BE14BCA68BB3', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'85894ABA243A434C86A382CEC45F4D64', X'91D6A8FAA7197F45A977BE14BCA68BB3', X'91D6A8FAA7197F45A977BE14BCA68BB3', 1, NULL, NULL, 2, NULL, 0, 'Melt the butter together with the oil in a covered skillet or shallow pan. Fry the drained grated onion until tender but not brown - about 5 minutes.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2F7D8D91AF6EAD4593B91484E47160BB', X'91D6A8FAA7197F45A977BE14BCA68BB3', X'91D6A8FAA7197F45A977BE14BCA68BB3', 2, NULL, NULL, 2, NULL, 0, 'Add the grated tomato and juice and the grated pepper and continue to fry until the vegetables have softened and the liquid is reduced.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D6CAF7DD3500424092D16FCE13961635', X'91D6A8FAA7197F45A977BE14BCA68BB3', X'91D6A8FAA7197F45A977BE14BCA68BB3', 3, NULL, NULL, 2, NULL, 0, 'Add the bulgur and stir well to combine using a wooden spoon.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'688534F399B1D14480D555F9D92118CE', X'91D6A8FAA7197F45A977BE14BCA68BB3', X'91D6A8FAA7197F45A977BE14BCA68BB3', 4, NULL, NULL, 2, NULL, 0, 'Add the salt, pepper, tomato paste, sugar, and broth and stir until combined. Bring the mixture to a boil, then cover and reduce the heat to low.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FC2BC2198B95FE4D9D12042C2BB1ED3C', X'91D6A8FAA7197F45A977BE14BCA68BB3', X'91D6A8FAA7197F45A977BE14BCA68BB3', 5, NULL, NULL, 2, NULL, 0, 'Let the bulgur simmer gently until all the liquid has been absorbed - about 15 to 20 minutes. Keeping the pan covered, remove it from the heat and set it aside to cool down.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'005E3354257D9F43B8131F3D5C10378B', X'91D6A8FAA7197F45A977BE14BCA68BB3', X'91D6A8FAA7197F45A977BE14BCA68BB3', 6, NULL, NULL, 2, NULL, 0, 'Gently stir the pilaf before serving to make sure all the ingredients are well blended.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A5F94C4BFAABA941BB040282BB16B532', X'91D6A8FAA7197F45A977BE14BCA68BB3', X'91D6A8FAA7197F45A977BE14BCA68BB3', 7, NULL, NULL, 2, NULL, 0, 'Garnish each serving with a piece of fresh or grilled pepper, tomato, and some fresh Italian parsley.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8A1073C99F0DFC43B84BDAEB29CC1ADE', X'91D6A8FAA7197F45A977BE14BCA68BB3', X'91D6A8FAA7197F45A977BE14BCA68BB3', 8, NULL, NULL, 2, NULL, 0, 'Serve and enjoy!');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0FFF0AEC1067A3448DF2417F1657105A', X'DE272C0903374A439616E65D4AAD29DE', X'DE272C0903374A439616E65D4AAD29DE', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A78D9DCCE0BE7746A50F954925723CD5', X'DE272C0903374A439616E65D4AAD29DE', X'DE272C0903374A439616E65D4AAD29DE', 1, NULL, NULL, 2, NULL, 0, 'In a copper sahan or frying pan, melt the butter completely. Continue to allow the butter to bubble for a few minutes without letting it burn.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F95E33475B5B9742B07EBD98454C7F79', X'DE272C0903374A439616E65D4AAD29DE', X'DE272C0903374A439616E65D4AAD29DE', 2, NULL, NULL, 2, NULL, 0, 'Add the cornmeal and work the butter all the way through with a large wooden spoon.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7C8F0CCEEB09644094C9A4D4691D21DB', X'DE272C0903374A439616E65D4AAD29DE', X'DE272C0903374A439616E65D4AAD29DE', 3, NULL, NULL, 2, NULL, 0, 'Stir the cornmeal gently over the heat for several minutes until it changes color to a deep golden brown.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CA613D56AD65B544AA2A6B9F5AB994FC', X'DE272C0903374A439616E65D4AAD29DE', X'DE272C0903374A439616E65D4AAD29DE', 4, NULL, NULL, 2, NULL, 0, 'When the oil from the butter begins to separate out, add the water and bring it to a boil.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E626073577CEF24AB06373937D43780C', X'DE272C0903374A439616E65D4AAD29DE', X'DE272C0903374A439616E65D4AAD29DE', 5, NULL, NULL, 2, NULL, 0, 'Once the water boils, slowly add the grated cheese.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2B5E704AE474D64FBA43FB9E192DBF7F', X'DE272C0903374A439616E65D4AAD29DE', X'DE272C0903374A439616E65D4AAD29DE', 6, NULL, NULL, 2, NULL, 0, 'Stir the mixture well each time to allow the cheese to melt and the mixture to become smooth each time.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F32661E9DBB61643B770B9685F8B2AAD', X'DE272C0903374A439616E65D4AAD29DE', X'DE272C0903374A439616E65D4AAD29DE', 7, NULL, NULL, 2, NULL, 0, 'As you stir, the melted cheese will combine with the cornmeal.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'499EBE5D711F2245964353A04A87660F', X'DE272C0903374A439616E65D4AAD29DE', X'DE272C0903374A439616E65D4AAD29DE', 8, NULL, NULL, 2, NULL, 0, 'Let it cook over a low flame, stirring occasionally until you see the butter appear on the top.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'75FB5E4D33208E45A228DFF32858AE07', X'DE272C0903374A439616E65D4AAD29DE', X'DE272C0903374A439616E65D4AAD29DE', 9, NULL, NULL, 2, NULL, 0, 'Serve hot with fresh, crusty bread to scoop it with and enjoy.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1EBB1066BA2ABE41A255E7F9A839B1D1', X'BDB7C07EC4A89F43A3910EC2AFEA11D0', X'BDB7C07EC4A89F43A3910EC2AFEA11D0', 0, NULL, NULL, 2, NULL, 0, 'First, cook the pasta in salted water for eight to ten minutes until tender. Drain it and rinse under cold water. Toss it with the vegetable oil the first two cups of the grated cheese in a large mixing bowl. The cheese should have enough salt to season the pasta, but you can add salt and pepper to your taste.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F0FE7B6F7C42A941BAF692F170060CC8', X'BDB7C07EC4A89F43A3910EC2AFEA11D0', X'BDB7C07EC4A89F43A3910EC2AFEA11D0', 1, NULL, NULL, 2, NULL, 0, 'Butter the bottom and sides of a large glass or ceramic baking dish and add the pasta mixture.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'629AD40511A41949B52D2F62B6ED2D56', X'BDB7C07EC4A89F43A3910EC2AFEA11D0', X'BDB7C07EC4A89F43A3910EC2AFEA11D0', 2, NULL, NULL, 2, NULL, 0, 'To prepare the Bechamel sauce, melt the butter or margarine in a small saucepan. Add the flour and stir until bubbly. Don''t let the flour darken or burn.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E7C0B5DDDE7BED49B0EEC53A8107E283', X'BDB7C07EC4A89F43A3910EC2AFEA11D0', X'BDB7C07EC4A89F43A3910EC2AFEA11D0', 3, NULL, NULL, 2, NULL, 0, 'Turn the heat to low, add the milk and whisk until smooth. Whisk in the salt and pepper and continue whisking until the mixture scalds and begins to thicken. Don''t let it boil. Continue until you have a smooth, white sauce. Adjust the salt to your taste.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D35B1AA15BD0F74C993BAFF992151BC7', X'BDB7C07EC4A89F43A3910EC2AFEA11D0', X'BDB7C07EC4A89F43A3910EC2AFEA11D0', 4, NULL, NULL, 2, NULL, 0, 'Pour the hot Bechamel over the pasta and gently turn over the pasta to combine them, if needed. Cover the top of the pasta with the remaining two cups of grated cheese. Bake in a 350 F/ 170 C oven until the dish is firm and the top is golden brown.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'59C683DCEF8C7540A275AE58BCE6B89A', X'BDB7C07EC4A89F43A3910EC2AFEA11D0', X'BDB7C07EC4A89F43A3910EC2AFEA11D0', 5, NULL, NULL, 2, NULL, 0, 'Remove the dish from the oven and let it rest about ten minutes before serving. Cut it squares or serve it with a spoon.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'ABC27175F9CC9947A63892C2BB440070', X'A099874EC3E5C041BB97B419CD39A0B5', X'A099874EC3E5C041BB97B419CD39A0B5', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B4F3BD64F34D624D9B572D297FF0A7A5', X'A099874EC3E5C041BB97B419CD39A0B5', X'A099874EC3E5C041BB97B419CD39A0B5', 1, NULL, NULL, 2, NULL, 0, 'In a shallow saucepan, melt the butter and oil together. Once melted, add the orzo and stir continuously over medium heat until the pasta turns a dark golden color. Be careful and keep stirring so the butter and orzo don''t burn.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D248E8BD2BA799409D3C75CD9641E4E4', X'A099874EC3E5C041BB97B419CD39A0B5', X'A099874EC3E5C041BB97B419CD39A0B5', 2, NULL, NULL, 2, NULL, 0, 'Add the uncooked rice and stir thoroughly until the oil has worked its way through all the grains. Add your chicken broth and spices and bring to a boil. If you are using chicken bouillon in place of stock, cut down or omit the salt.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0FEEFC6780B5A246BA903A4DA50DEDDD', X'A099874EC3E5C041BB97B419CD39A0B5', X'A099874EC3E5C041BB97B419CD39A0B5', 3, NULL, NULL, 2, NULL, 0, 'Cover and reduce the heat. Let the rice slowly simmer until the liquid disappears.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'45D89267C5CDBA4D96F04746E8C7035C', X'A099874EC3E5C041BB97B419CD39A0B5', X'A099874EC3E5C041BB97B419CD39A0B5', 4, NULL, NULL, 2, NULL, 0, 'Without removing the cover, take the pan from the heat and put it aside to cool down. Keep the top on at all times while the rice cools. Note: If too much condensation forms on the inside of the lid, open it and cover the top of the pan with a few paper towels, then close the lid over them. This will absorb excess moisture and allow the rice to continue steaming at an even pace.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E67FDEB84510ED4983785C4EC0DD3BBC', X'A099874EC3E5C041BB97B419CD39A0B5', X'A099874EC3E5C041BB97B419CD39A0B5', 5, NULL, NULL, 2, NULL, 0, 'Before serving, open the lid and gently stir the rice to loosen the bottom and separate the grains.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3C66550ABC8D6047B3DC405EC77CB3EC', X'A099874EC3E5C041BB97B419CD39A0B5', X'A099874EC3E5C041BB97B419CD39A0B5', 6, NULL, NULL, 2, NULL, 0, 'You can use a small dessert bowl as a mold to ?give it that signature rice pilaf look. Fill the bowl and pack in the warm rice with the back of a spoon. Invert the bowl onto a serving plate. The pilaf will pop out in the shape of the bowl. Enjoy!');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7E6112F62D01EB4BBF5E14255CDBCDFC', X'758995BF34322B4AAA054BB3EC4E002B', X'758995BF34322B4AAA054BB3EC4E002B', 0, NULL, NULL, 2, NULL, 0, 'Roast your eggplants on the grill, and remove the pulp. Once you''ve extracted the flesh and discarded the skin and seeds, use it right away. If not, let the pulp sit in a bowl with some water and lemon juice in it. This prevents the eggplant from darkening.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FF35ECB3F409A848AABD50556B46C97E', X'758995BF34322B4AAA054BB3EC4E002B', X'758995BF34322B4AAA054BB3EC4E002B', 1, NULL, NULL, 2, NULL, 0, 'Put your eggplant into a fine wire strainer, and squeeze out any excess liquid. Transfer the pulp to a mixing bowl.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D61E7852A08DD5489FBC00E872323C5E', X'758995BF34322B4AAA054BB3EC4E002B', X'758995BF34322B4AAA054BB3EC4E002B', 2, NULL, NULL, 2, NULL, 0, 'Add the olive oil, salt, pepper, and optional crushed garlic.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F7A41FBFE070C349A04567A917B7235A', X'758995BF34322B4AAA054BB3EC4E002B', X'758995BF34322B4AAA054BB3EC4E002B', 3, NULL, NULL, 2, NULL, 0, 'With a fork, whip the mixture until it becomes homogenous, light, and fluffy with no large chunks or strings of eggplant. This can take awhile. You may have to take several breaks in between to rest your arm.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8A37F3620D38A842BF367AC2BADA0072', X'758995BF34322B4AAA054BB3EC4E002B', X'758995BF34322B4AAA054BB3EC4E002B', 4, NULL, NULL, 2, NULL, 0, 'Once the puree is ready, you can gently stir in your optional mayonnaise or yogurt. Chill before serving.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'405B6C3B3DEE1D40A1A935B81BD21279', X'D14C8857566A9D468D34E86A3BEEAB45', X'D14C8857566A9D468D34E86A3BEEAB45', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'39B4E18381B93248990760362DAAE828', X'D14C8857566A9D468D34E86A3BEEAB45', X'D14C8857566A9D468D34E86A3BEEAB45', 1, NULL, NULL, 2, NULL, 0, 'Place flour and dry yeast in a large bowl, mix to combine.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5D225225FDBF634788D0D1BD67B4B048', X'D14C8857566A9D468D34E86A3BEEAB45', X'D14C8857566A9D468D34E86A3BEEAB45', 2, NULL, NULL, 2, NULL, 0, 'Add the warm water and milk, and combine lightly with a fork.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'04558B4845417C42B5433BCC3B12F194', X'D14C8857566A9D468D34E86A3BEEAB45', X'D14C8857566A9D468D34E86A3BEEAB45', 3, NULL, NULL, 2, NULL, 0, 'Add the softened butter or margarine and olive oil and combine, as well as the sugar and salt.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1B27139DB4DC594E8278993DABD6AFE7', X'D14C8857566A9D468D34E86A3BEEAB45', X'D14C8857566A9D468D34E86A3BEEAB45', 4, NULL, NULL, 2, NULL, 0, 'Knead the dough. You can knead with a mixer or by hand. If you are using a mixer, process the dough for about 10 minutes using the dough kneading hook. If you choose to knead by hand, knead the dough in the bowl for 15 minutes.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AA64405B67A85A4B982396BB3D590680', X'D14C8857566A9D468D34E86A3BEEAB45', X'D14C8857566A9D468D34E86A3BEEAB45', 5, NULL, NULL, 2, NULL, 0, 'Turn the dough out onto a floured surface and continue to knead for about five minutes more. The dough should be very soft. If its too firm, knead in another tablespoon of olive oil until smooth.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FEEA8469C9F7B94FA330533EE4EFAB62', X'D14C8857566A9D468D34E86A3BEEAB45', X'D14C8857566A9D468D34E86A3BEEAB45', 6, NULL, NULL, 2, NULL, 0, 'Transfer the dough to a plate or bowl, cover with a damp cloth and set aside in a warm place. Allow to rise for about two hours.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2EC349952E5F064A97E6D7348D4C7C80', X'D14C8857566A9D468D34E86A3BEEAB45', X'D14C8857566A9D468D34E86A3BEEAB45', 7, NULL, NULL, 2, NULL, 0, 'After the dough has doubled in size, fill a shallow metal baking tray or cookie sheet with water and place it in the bottom rack of the oven. Preheat oven to 430 F/225 C.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BDFB4E7AFEB95A4C9168613D54B5163E', X'D14C8857566A9D468D34E86A3BEEAB45', X'D14C8857566A9D468D34E86A3BEEAB45', 8, NULL, NULL, 2, NULL, 0, 'After two hours, the dough should be about double in size. Remove the cloth and turn it out onto a floured surface. Divide the dough into two pieces with a dough cutter or sharp carving knife.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CDF3C0400445E44CB5E45D3EC7F3DFFC', X'D14C8857566A9D468D34E86A3BEEAB45', X'D14C8857566A9D468D34E86A3BEEAB45', 9, NULL, NULL, 2, NULL, 0, 'Shape your 2 pide loaves using your fingers. Make each loaf flat and either oval or round, about 1/2 inch thick.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'33297F9852A035458D0829238B40EABD', X'D14C8857566A9D468D34E86A3BEEAB45', X'D14C8857566A9D468D34E86A3BEEAB45', 10, NULL, NULL, 2, NULL, 0, 'Place each loaf on a metal baking tray sprinkled with flour.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9B3700C5DE032C4BB7F264D44B262B3A', X'D14C8857566A9D468D34E86A3BEEAB45', X'D14C8857566A9D468D34E86A3BEEAB45', 11, NULL, NULL, 2, NULL, 0, 'Using a sharp knife dipped in flour, first cut about 1 inch in from the edge to make a circle all the way around the loaf. Then, inside the circle, make diagonal cuts in opposite directions to make a diamond pattern. Do the same with the other loaf.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'79753473831C7541B4830BE2670D5DEC', X'D14C8857566A9D468D34E86A3BEEAB45', X'D14C8857566A9D468D34E86A3BEEAB45', 12, NULL, NULL, 2, NULL, 0, 'In a bowl, whisk together the yogurt and egg yolk. Using a pastry brush, coat the top of the loaves generously with the yogurt-egg mixture.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F1D84B409C5F4648BC991802DA2FE487', X'D14C8857566A9D468D34E86A3BEEAB45', X'D14C8857566A9D468D34E86A3BEEAB45', 13, NULL, NULL, 2, NULL, 0, 'Sprinkle the sesame and nigella seeds evenly over the tops.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'341324576E26104D99E98111908DEFBE', X'D14C8857566A9D468D34E86A3BEEAB45', X'D14C8857566A9D468D34E86A3BEEAB45', 14, NULL, NULL, 2, NULL, 0, 'Bake in the preheated oven at 430 F/225 C oven with the water tray for about 30 minutes or until the top becomes deep, golden brown. Serve the pide hot out of the oven.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C718DC7442C4F74FB69C80D800870443', X'DCA665FDE9B42B4891B0592DBAACEFE8', X'DCA665FDE9B42B4891B0592DBAACEFE8', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C28E5F73057A5B449DA8A6F2FA5A21FE', X'DCA665FDE9B42B4891B0592DBAACEFE8', X'DCA665FDE9B42B4891B0592DBAACEFE8', 1, NULL, NULL, 2, NULL, 0, 'The night before, put the dry beans in a large bowl and cover them generously with water. Let them soak overnight. The next morning, drain the beans and put them in a pot with fresh water. Let them boil softly until tender. Allow them to cool in the water, then drain them well.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E9397F15A557E14697FABD573A69D8DB', X'DCA665FDE9B42B4891B0592DBAACEFE8', X'DCA665FDE9B42B4891B0592DBAACEFE8', 2, NULL, NULL, 2, NULL, 0, 'If you want to save time, substitute the dry beans with canned Navy beans. Drain them and rinse them under cold water before you use them.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C646F2C31FCB2F4695888CB989BA417B', X'DCA665FDE9B42B4891B0592DBAACEFE8', X'DCA665FDE9B42B4891B0592DBAACEFE8', 3, NULL, NULL, 2, NULL, 0, 'Put the drained beans in a large bowl. Drizzle the vinegar over the top. Cover the bowl with a lid or plastic wrap and let it rest for about one hour.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3F34A8B6D1A90A4D8E1C5BB0DB7C7FF5', X'DCA665FDE9B42B4891B0592DBAACEFE8', X'DCA665FDE9B42B4891B0592DBAACEFE8', 4, NULL, NULL, 2, NULL, 0, 'In the meantime, chop the tomatoes into small cubes, cut the pepper into rings and chop the parsley. Peel the onion, cut it in half, and cut it into half-circle slices. Wearing rubber gloves, rub the onion slices between your hands or on a wooden cutting board to soften them.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'578B2CB8CCA65C47BBAAFC73A011506C', X'DCA665FDE9B42B4891B0592DBAACEFE8', X'DCA665FDE9B42B4891B0592DBAACEFE8', 5, NULL, NULL, 2, NULL, 0, 'Drain the extra vinegar from the beans. Toss the chopped vegetables and parsley in with your fingers. Put the beans on a large, flat serving plate or distribute them on individual salad plates. Spread the onion slices evenly over the top.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A421E06640EEAC4DA61E8255E25876D9', X'DCA665FDE9B42B4891B0592DBAACEFE8', X'DCA665FDE9B42B4891B0592DBAACEFE8', 6, NULL, NULL, 2, NULL, 0, 'For the dressing: mix the lemon juice, olive oil, and salt and pepper together then drizzle it over the salad.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EBAD2602224ED44AA6D76AABE974FC21', X'DCA665FDE9B42B4891B0592DBAACEFE8', X'DCA665FDE9B42B4891B0592DBAACEFE8', 7, NULL, NULL, 2, NULL, 0, 'Garnish the top with black olives, slivers of hard-boiled egg, lemon wedges and parsley sprigs. Sprinkle with hot pepper flakes, if desired.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6B4E096291ECE1439476AC1CFF656969', X'DCA665FDE9B42B4891B0592DBAACEFE8', X'DCA665FDE9B42B4891B0592DBAACEFE8', 8, NULL, NULL, 2, NULL, 0, 'Serve and enjoy!');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3F4A9AC226235C4985EA16B92485D257', X'6530338AF61E3945BF24D384AE32B43F', X'6530338AF61E3945BF24D384AE32B43F', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F9D60FE70627AF438858E5790FBBEBB9', X'6530338AF61E3945BF24D384AE32B43F', X'6530338AF61E3945BF24D384AE32B43F', 1, NULL, NULL, 2, NULL, 0, 'Wash and drain all your vegetables and herbs well. Cut the peppers in half and remove the stems, seeds, and white inner membranes. With a sharp dicing blade, dice the peppers as finely as you can.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8207E8981C738540AB8DF9DF6DB01567', X'6530338AF61E3945BF24D384AE32B43F', X'6530338AF61E3945BF24D384AE32B43F', 2, NULL, NULL, 2, NULL, 0, 'Peel the tomatoes and remove the watery seed portions from the inside and discard them. Dice the pulp portions in the same manner as the peppers. Do the same with the parsley leaves, onion, and garlic cloves.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'ABEF115206D92742AEFC66B8ACACBAA1', X'6530338AF61E3945BF24D384AE32B43F', X'6530338AF61E3945BF24D384AE32B43F', 3, NULL, NULL, 2, NULL, 0, 'Between two sheets of wax paper, crush the walnuts into as fine a powder using a rolling pin.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D0D957F894524B4F921DF9F1294E6582', X'6530338AF61E3945BF24D384AE32B43F', X'6530338AF61E3945BF24D384AE32B43F', 4, NULL, NULL, 2, NULL, 0, 'In a large, shallow dish or tray, mix all the ingredients and spices above together with your fingers. Adjust the salt to your taste. Using a fork, continue to crush the mixture little by little until it is well mashed and blended.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EA63ECC79DB45C4D97F399B02483DA38', X'6530338AF61E3945BF24D384AE32B43F', X'6530338AF61E3945BF24D384AE32B43F', 5, NULL, NULL, 2, NULL, 0, 'Transfer the mash to your serving dish and garnish it with a sprinkle of sumac powder and fresh sprigs of Italian parsley.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'63A146357614BF44A8E56FEEFECECE7B', X'6530338AF61E3945BF24D384AE32B43F', X'6530338AF61E3945BF24D384AE32B43F', 6, NULL, NULL, 2, NULL, 0, 'Enjoy!');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5FCB88916743AE42B4C2A5B3D731F51E', X'97D766B96A41D0479F678A83BE4DD1D8', X'97D766B96A41D0479F678A83BE4DD1D8', 0, NULL, NULL, 2, NULL, 0, 'Clean the vegetables and put them into a large saucepan with the water. Boil them gently until very soft. Drain them, then add the cooked chickpeas and puree the mixture in a blender or food processor. If the mixture seems too watery, let it sit in a very fine wire strainer for a few minutes to let excess water drip away.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1ADFF4F2486AF24FA34E0930845317A9', X'97D766B96A41D0479F678A83BE4DD1D8', X'97D766B96A41D0479F678A83BE4DD1D8', 1, NULL, NULL, 2, NULL, 0, 'Put the warm vegetable puree into a large mixing bowl. Add the yogurt and mix thoroughly. Mix in the active dry yeast and spices.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'268CD7F2303BA94D886E4E560F2643C7', X'97D766B96A41D0479F678A83BE4DD1D8', X'97D766B96A41D0479F678A83BE4DD1D8', 2, NULL, NULL, 2, NULL, 0, 'Now begin adding flour very slowly and working it completely into the mixture with your hands. Continue this process until all the flour is mixed in evenly and you have a smooth batter.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'23E27428E452604AB3B4547AD8162876', X'97D766B96A41D0479F678A83BE4DD1D8', X'97D766B96A41D0479F678A83BE4DD1D8', 3, NULL, NULL, 2, NULL, 0, 'Cover the top of the bowl with some cling wrap and a towel and set it in a warm place for 5 days. Check the batter every day and stir it to keep the fermentation even.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'25FB522FFA7A6D4A81AFD72A719660E3', X'97D766B96A41D0479F678A83BE4DD1D8', X'97D766B96A41D0479F678A83BE4DD1D8', 4, NULL, NULL, 2, NULL, 0, 'On the 5th day, remove the towel and cling wrap and stir the mixture well. It should have a sour aroma and be bright orange in color.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5E93AB9553D0B743A67458C75D35CBC7', X'97D766B96A41D0479F678A83BE4DD1D8', X'97D766B96A41D0479F678A83BE4DD1D8', 5, NULL, NULL, 2, NULL, 0, 'Divide the batter up by placing large spoonfuls on baking sheets covered with non-stick baking parchment. Leave the sheets in a dry place until one side of the patties are completely dry. Flip them over and allow the other side to dry.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'18482CBDA50D6842BA3B3C135F4688DD', X'97D766B96A41D0479F678A83BE4DD1D8', X'97D766B96A41D0479F678A83BE4DD1D8', 6, NULL, NULL, 2, NULL, 0, 'After the patties look dry, you can begin to break them apart with your fingers. The centers will still be moist, so allow more time for the coarsely crumbled tarhana to dry further.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'20D8FF222DBD464D967CD65C7E2D7092', X'97D766B96A41D0479F678A83BE4DD1D8', X'97D766B96A41D0479F678A83BE4DD1D8', 7, NULL, NULL, 2, NULL, 0, 'Repeat the crumbling and drying process several times until the pieces are small enough to put through a food processor or fine sieve.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B9684B3DB04A014F9702082CDFF38822', X'97D766B96A41D0479F678A83BE4DD1D8', X'97D766B96A41D0479F678A83BE4DD1D8', 8, NULL, NULL, 2, NULL, 0, 'Once you have a lot of fine powder, spread it out on the trays and continue to dry it, shifting the powder with your hands occasionally.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0ADA7B1A61AAC245BC0745BB713FE3B0', X'97D766B96A41D0479F678A83BE4DD1D8', X'97D766B96A41D0479F678A83BE4DD1D8', 9, NULL, NULL, 2, NULL, 0, 'In the end, you should have a fine pulse that is completely dry. As long as youre sure its dried thoroughly, you can store the pulse in glass containers for more than 1 year with no refrigeration needed.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'202B82F31D1DBD4E99CC5539F9567064', X'97D766B96A41D0479F678A83BE4DD1D8', X'97D766B96A41D0479F678A83BE4DD1D8', 10, NULL, NULL, 2, NULL, 0, 'To make tarhana soup, simply cook a few tablespoons of the pulse in hot milk or water until it thickens, and add butter, salt, and spices to taste.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1EE1ECADE542714BABB47F7F7BCB37EA', X'F12026CB2A9DEE459F1CCF5D37DF4639', X'F12026CB2A9DEE459F1CCF5D37DF4639', 0, NULL, NULL, 2, NULL, 0, 'Prepare the eggplant: cut off both ends and using the edge of a vegetable peeler or a paring knife, remove the skin in alternating strips going from top to bottom. Do this gently, saving as much flesh as you can.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1FAC7653B6AF2C4C98ABEEAAD680693A', X'F12026CB2A9DEE459F1CCF5D37DF4639', X'F12026CB2A9DEE459F1CCF5D37DF4639', 1, NULL, NULL, 2, NULL, 0, 'Cut the eggplant into bite-sized rectangular chunks about 2 inches long. Put the chunks of eggplant in a large mesh strainer or colander and salt well, turning with your hands to salt all sides. Let the salted eggplant rest for about 15 minutes while you prepare the other vegetables. This removes the bitterness.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F3675B57A08C1C4F9E9E119D7FDC68B2', X'F12026CB2A9DEE459F1CCF5D37DF4639', X'F12026CB2A9DEE459F1CCF5D37DF4639', 2, NULL, NULL, 2, NULL, 0, 'Coarsely dice the peppers, red onion, and squash.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'579AAF29DB00AC46A1EA1F8CD95F9CFD', X'F12026CB2A9DEE459F1CCF5D37DF4639', X'F12026CB2A9DEE459F1CCF5D37DF4639', 3, NULL, NULL, 2, NULL, 0, 'Once the vegetables are ready, pour about two inches of oil in a deep frying pan or saucepan and turn the heat on high. While the oil is heating, wash the eggplant in the strainer with cold water, turning it with your hands, until the salt is removed. Drain it well and blot off the extra water with paper towels before you fry them.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DF4E4645026D4E469687726E1AB3ACB3', X'F12026CB2A9DEE459F1CCF5D37DF4639', X'F12026CB2A9DEE459F1CCF5D37DF4639', 4, NULL, NULL, 2, NULL, 0, 'Fry the eggplant until tender and just beginning to brown at the edges. Do the same with the squash. Strain out the fried vegetables and drain them on a thick layer of paper towels. Let them sit for several minutes to make sure as much oil as possible drains away.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A503A13F773728498C0AE2027229ED43', X'F12026CB2A9DEE459F1CCF5D37DF4639', X'F12026CB2A9DEE459F1CCF5D37DF4639', 5, NULL, NULL, 2, NULL, 0, 'In the meantime, put two tablespoons of olive oil in a skillet and fry the onions, garlic, and peppers until tender.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D453111C99C9474888FA8A51A2456B0A', X'F12026CB2A9DEE459F1CCF5D37DF4639', X'F12026CB2A9DEE459F1CCF5D37DF4639', 6, NULL, NULL, 2, NULL, 0, 'Add the tomatoes, tomato paste, sugar, salt, and pepper. Cover and reduce the heat to low. Let the sauce simmer until the tomatoes are soft and begin to break down.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'578750F6D473B441A258068CBED61D9E', X'F12026CB2A9DEE459F1CCF5D37DF4639', X'F12026CB2A9DEE459F1CCF5D37DF4639', 7, NULL, NULL, 2, NULL, 0, 'Remove the lid, stir it well, and let the sauce simmer until most of the liquid is reduced. It should be the consistency of jarred pasta sauce.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'509254314BE5CF46933F71C62D0122AF', X'F12026CB2A9DEE459F1CCF5D37DF4639', X'F12026CB2A9DEE459F1CCF5D37DF4639', 8, NULL, NULL, 2, NULL, 0, 'In a mixing bowl, add the eggplant, squash, and tomato sauce and stir together well. Put the saksuka in a serving dish and chill for several hours. Just before serving, spoon generous amounts of garlic yogurt over the top.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A21F5EE51BB2A24AA9D4EE7E0AC704E1', X'1AC21C7DF1AA88489F791D962984438F', X'1AC21C7DF1AA88489F791D962984438F', 0, NULL, NULL, 2, NULL, 0, 'If you are using fresh okra, trim the rough stems from the ends of the pods and remove the thin membrane covering the stem end. Handle them gently as not to damage them. Rinse them several times in a wire strainer under ice cold water, turning them with your hands.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4B28DB1B86086B44AD0516431C5BEE91', X'1AC21C7DF1AA88489F791D962984438F', X'1AC21C7DF1AA88489F791D962984438F', 1, NULL, NULL, 2, NULL, 0, 'Drain them and let them soak in one cup of vinegar for about 30 minutes. Wash them again to remove all the vinegar. Repeat this process once more.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F258BFEB47161B4695D45DEFF5192750', X'1AC21C7DF1AA88489F791D962984438F', X'1AC21C7DF1AA88489F791D962984438F', 2, NULL, NULL, 2, NULL, 0, 'After your okra soaks in vinegar and is rinsed the second time, it''s ready to use. If you''re using frozen okra, it''s ready to use right out of the bag.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'11AF094E88FA6445B491DDA3190CCD9F', X'1AC21C7DF1AA88489F791D962984438F', X'1AC21C7DF1AA88489F791D962984438F', 3, NULL, NULL, 2, NULL, 0, 'Peel the onion and coarsely chop it. Heat the two tablespoons of olive oil in a medium saucepan and fry the onions until tender and reduced. Add the fresh or frozen okra.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B990984E15F94544ADFD2DA3925F3FCA', X'1AC21C7DF1AA88489F791D962984438F', X'1AC21C7DF1AA88489F791D962984438F', 4, NULL, NULL, 2, NULL, 0, 'Wash and peel your tomato. Dice it into small cubes and add it to the pan. Add the lemon juice, salt, pepper, and sugar and turn the mixture around gently with a large wooden spoon.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D01A74937B63D8448CF72280F98C13F6', X'1AC21C7DF1AA88489F791D962984438F', X'1AC21C7DF1AA88489F791D962984438F', 5, NULL, NULL, 2, NULL, 0, 'Add the water, bring it to a boil, reduce the heat to low and cover. Let the okra simmer gently. The amount of liquid in the pan will increase as the vegetables release their own juice; you should simmer them until the okra are very tender and the liquid is almost gone.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FA986AE248D4864D810EAD05EA5B0264', X'1AC21C7DF1AA88489F791D962984438F', X'1AC21C7DF1AA88489F791D962984438F', 6, NULL, NULL, 2, NULL, 0, 'Test your okra a few times while cooking. If they are tender but there is still a lot of liquid, remove the lid so the liquid evaporates more quickly.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E8566E4D8B36ED44A5EF24EB939FD596', X'1AC21C7DF1AA88489F791D962984438F', X'1AC21C7DF1AA88489F791D962984438F', 7, NULL, NULL, 2, NULL, 0, 'When the okra is tender and the liquid is almost gone, turn off the heat and let the pan cool down to room temperature. Transfer the okra to your serving platter.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BDFBEEA33F586C4F81ED2764477CF0D6', X'1AC21C7DF1AA88489F791D962984438F', X'1AC21C7DF1AA88489F791D962984438F', 8, NULL, NULL, 2, NULL, 0, 'Drizzle the 1/3 cup of olive oil over the top of the dish. You can serve the okra room temperature or cover and refrigerate it before serving.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'30D3A491CE035049A028A96049BCEFA8', X'8F0675A1B75FF74DA6DDC2ACF98F90AC', X'8F0675A1B75FF74DA6DDC2ACF98F90AC', 0, NULL, NULL, 2, NULL, 0, 'In a large bowl, melt the butter in the microwave. Next, stir in the milk, salt, and pepper. Microwave again on high until the milk is warm but not scalding.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'34E84135E56D2C419C5CCA17DEF3A2B2', X'8F0675A1B75FF74DA6DDC2ACF98F90AC', X'8F0675A1B75FF74DA6DDC2ACF98F90AC', 1, NULL, NULL, 2, NULL, 0, 'In a separate bowl, toss together the crumbled white cheese and chopped parsley.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'328E96B7C47D36499D08624CC91F4790', X'8F0675A1B75FF74DA6DDC2ACF98F90AC', X'8F0675A1B75FF74DA6DDC2ACF98F90AC', 2, NULL, NULL, 2, NULL, 0, 'Butter the bottom and sides of a large, oven-proof baking tray. Drizzle a spoonful or two of the milk mixture over the bottom. Next, take the first sheet of yufka and place it in an uneven, wrinkly fashion to cover the bottom of the tray. Spoon about 1/6 of the milk mixture all over the yufka. Let it run into the cracks and crevices and around the edges.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'68A3E58916FC4240AE009DDDE8E53FD0', X'8F0675A1B75FF74DA6DDC2ACF98F90AC', X'8F0675A1B75FF74DA6DDC2ACF98F90AC', 3, NULL, NULL, 2, NULL, 0, 'Divide the cheese mixture roughly into five parts. Sprinkle 1/5 of the cheese mixture over the yufka. Now, do the same with the next layer. Repeat until you have five layers finished. Cover the top of your pastry completely with the last piece of yufka. Drizzle the last portion of the milk mixture over the top, wetting all of it.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7BBCC8DEFE3621439D2E1051C2D12FD8', X'8F0675A1B75FF74DA6DDC2ACF98F90AC', X'8F0675A1B75FF74DA6DDC2ACF98F90AC', 4, NULL, NULL, 2, NULL, 0, 'Sprinkle the top of your pastry with sesame seeds or nigella seeds. Bake it in a 185 F/365 C oven for about 45 minutes, or until the top is nicely browned and the center is firm.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BB82F9B89ACEE343BB45596A2071EAA8', X'8F0675A1B75FF74DA6DDC2ACF98F90AC', X'8F0675A1B75FF74DA6DDC2ACF98F90AC', 5, NULL, NULL, 2, NULL, 0, 'When your pastry is cooked, remove the pan from oven and sprinkle one or two tablespoons of cold water over the top, then wrap the whole thing in clean butcher''s paper or a clean towel. This helps to soften the top layer. Let the pastry rest for about 20 minutes before cutting it into squares.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'73433A965A753949B5374228E39F2F15', X'9C6D45DDC679B54F9984DE158680A5B5', X'9C6D45DDC679B54F9984DE158680A5B5', 0, NULL, NULL, 2, NULL, 0, 'First, peel and dice your onion and garlic and put them in a covered saucepan along with 3 tbsp. of olive oil. Saute the onion and garlic until they''re soft and reduced.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8157179336F6E341A6B7B941E1BE5074', X'9C6D45DDC679B54F9984DE158680A5B5', X'9C6D45DDC679B54F9984DE158680A5B5', 1, NULL, NULL, 2, NULL, 0, 'Add the beans, carrots, grated tomato, tomato paste, sugar, and spices. Add enough water to just cover the beans and stir until combined.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FDF6CD816538AF4890C3C760EDE98225', X'9C6D45DDC679B54F9984DE158680A5B5', X'9C6D45DDC679B54F9984DE158680A5B5', 2, NULL, NULL, 2, NULL, 0, 'Bring the mixture to a boil, then cover and reduce the heat to low. Let the beans simmer gently until much of the water is reduced and the beans and carrots are tender.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BA45E028E0F247408DD32570A323C1D0', X'9C6D45DDC679B54F9984DE158680A5B5', X'9C6D45DDC679B54F9984DE158680A5B5', 3, NULL, NULL, 2, NULL, 0, 'When the beans are tender, turn off the heat and let the pan cool down. After it cools, spoon the beans gently onto your serving plate. Drizzle the olive oil evenly over the top.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B03A4E43105D464A97CB05F99D1406C7', X'9C6D45DDC679B54F9984DE158680A5B5', X'9C6D45DDC679B54F9984DE158680A5B5', 4, NULL, NULL, 2, NULL, 0, 'Cover with cling wrap and chill until ready to serve.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E4C3F59080BD3A49BCD8F64A036EBD32', X'9C6D45DDC679B54F9984DE158680A5B5', X'9C6D45DDC679B54F9984DE158680A5B5', 5, NULL, NULL, 2, NULL, 0, 'Just before serving, sprinkle the top with your chopped Italian parsley leaves for garnish.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1BD220F0F1CE5042BDDFF6F8B52899E3', X'464753A15557294C8E0EABA292DBD2DC', X'464753A15557294C8E0EABA292DBD2DC', 0, NULL, NULL, 2, NULL, 0, 'First, place the softened butter or margarine in a large mixing bowl and whip with an electric mixer on the lowest setting until creamy.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4F887155B7D15141A992B728038F3ED9', X'464753A15557294C8E0EABA292DBD2DC', X'464753A15557294C8E0EABA292DBD2DC', 1, NULL, NULL, 2, NULL, 0, 'Add the flour. Wearing rubber gloves, work the flour into the butter with your fingers until combined. Add the powdered sugar, vanilla, baking powder, and semolina and continue working them in with your fingers until the dough has a smooth consistency. The warmth from your hands will help melt the butter and soften the mixture as you work.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'915408000ACA074789959D18A1ADB9CD', X'464753A15557294C8E0EABA292DBD2DC', X'464753A15557294C8E0EABA292DBD2DC', 2, NULL, NULL, 2, NULL, 0, 'In the meantime, start the syrup. Add 4 cups of boiling water to a saucepan. Add the granulated sugar and the half lemon. Bring to a boil then gently simmer uncovered for about 10 minutes, stirring occasionally. Remove from the heat and let it cool down just a bit.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'04B476B2602CA743A2868EADB4A5E293', X'464753A15557294C8E0EABA292DBD2DC', X'464753A15557294C8E0EABA292DBD2DC', 3, NULL, NULL, 2, NULL, 0, 'Next, add the eggs to the dough mixture and knead them into the dough until smooth.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0468DF54DFCE91438A87C84E15163C40', X'464753A15557294C8E0EABA292DBD2DC', X'464753A15557294C8E0EABA292DBD2DC', 4, NULL, NULL, 2, NULL, 0, 'With your hands, pinch off a piece of dough and roll it into a ball about the size of an apricot. Place it on an ungreased metal oven tray. Continue making even-sized balls and lining them up side by side, leaving some space in between.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8C6EC59466010D428AED2A39BBDDC23C', X'464753A15557294C8E0EABA292DBD2DC', X'464753A15557294C8E0EABA292DBD2DC', 5, NULL, NULL, 2, NULL, 0, 'Gently press down on the top of each ball to flatten it slightly. Press an almond or a hazelnut into the center of each.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0D18FB9BA4BDC44F88F58CEA90E9603C', X'464753A15557294C8E0EABA292DBD2DC', X'464753A15557294C8E0EABA292DBD2DC', 6, NULL, NULL, 2, NULL, 0, 'Bake in a 160C or 320F oven for 20 to 25 minutes until the tops of your cookies are golden brown.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DD86BA62F97E0F43A4B268FAC58EC791', X'464753A15557294C8E0EABA292DBD2DC', X'464753A15557294C8E0EABA292DBD2DC', 7, NULL, NULL, 2, NULL, 0, 'Remove your tray from the oven and, while it is still hot, pour the warm syrup evenly over the top.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7A124A07C1F70F4CA679AF7778DB0701', X'464753A15557294C8E0EABA292DBD2DC', X'464753A15557294C8E0EABA292DBD2DC', 8, NULL, NULL, 2, NULL, 0, 'Let the tray cool down to room temperature. As they cool, the cookies will absorb the syrup and get soft.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2B34EDB3A79DD14088BEECF331F7223E', X'464753A15557294C8E0EABA292DBD2DC', X'464753A15557294C8E0EABA292DBD2DC', 9, NULL, NULL, 2, NULL, 0, 'When the sekerpare are cool, they are ready to serve. Serve two or three on a decorative plate and garnish them with a spoonful of Turkish clotted cream, called `kaymak` if you wish.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F0F8D723F5AFD74689290C946D60D105', X'25B2F3EF1850C74CAD93F0B98F90E92A', X'25B2F3EF1850C74CAD93F0B98F90E92A', 0, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 350 F (175 C). Grease a 9-inch by 13-inch baking pan.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FA457FBC4E312744BBD22358EB582197', X'25B2F3EF1850C74CAD93F0B98F90E92A', X'25B2F3EF1850C74CAD93F0B98F90E92A', 1, NULL, NULL, 2, NULL, 0, 'Before you start making the cake, prepare the syrup to give it time to cool down. Begin by mixing the sugar and water in a medium saucepan. Turn the heat on high and bring the mixture to a boil while continuously stirring it.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'46583149DBD3B542A948D7AE6575AF27', X'25B2F3EF1850C74CAD93F0B98F90E92A', X'25B2F3EF1850C74CAD93F0B98F90E92A', 2, NULL, NULL, 2, NULL, 0, 'Once it boils, reduce the heat to low and let the syrup boil gently for about 10 minutes with the cover off. Add the lemon juice toward the end. Turn off the heat and let the syrup cool down while you make the rest of the recipe.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'24D8E760EA91A547B5C14E7E9B77F48A', X'25B2F3EF1850C74CAD93F0B98F90E92A', X'25B2F3EF1850C74CAD93F0B98F90E92A', 3, NULL, NULL, 2, NULL, 0, 'In a large mixing bowl, whisk together the eggs and sugar. Whisk the mixture briskly for several minutes until the sugar dissolves. The more you whisk, the better your revani will turn out.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'316A2D9E659F9C4CA44B3D03791E9F8D', X'25B2F3EF1850C74CAD93F0B98F90E92A', X'25B2F3EF1850C74CAD93F0B98F90E92A', 4, NULL, NULL, 2, NULL, 0, 'Add the oil, lemon zest, vanilla, and yogurt and whisk for several minutes more.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C8458808BD3A864CBFE0ED5787E351E2', X'25B2F3EF1850C74CAD93F0B98F90E92A', X'25B2F3EF1850C74CAD93F0B98F90E92A', 5, NULL, NULL, 2, NULL, 0, 'Add the dry ingredients and mix together well until you have a smooth batter.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F2B791B1EB2AD849941800C91C3866A9', X'25B2F3EF1850C74CAD93F0B98F90E92A', X'25B2F3EF1850C74CAD93F0B98F90E92A', 6, NULL, NULL, 2, NULL, 0, 'Pour the batter into the greased baking pan. Bake until the top is nicely browned and a toothpick inserted in the center comes out clean, about 30 to 40 minutes. Once you remove the pan from the oven, let it rest for about 5 minutes.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4A6EAC9F2AB06C4C87726976D0905DE4', X'25B2F3EF1850C74CAD93F0B98F90E92A', X'25B2F3EF1850C74CAD93F0B98F90E92A', 7, NULL, NULL, 2, NULL, 0, 'Cut the cake into portion-sized squares or rectangles while still in the pan.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'860A324783A0FD469E14AF27C157DF25', X'25B2F3EF1850C74CAD93F0B98F90E92A', X'25B2F3EF1850C74CAD93F0B98F90E92A', 8, NULL, NULL, 2, NULL, 0, 'Using a large spoon, slowly drizzle all of the cooled syrup over the cake and let it soak in. Cover the cake with foil and refrigerate it for several hours.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C3157B4D7B324F489DF7B05815C33FC9', X'25B2F3EF1850C74CAD93F0B98F90E92A', X'25B2F3EF1850C74CAD93F0B98F90E92A', 9, NULL, NULL, 2, NULL, 0, 'Before serving, garnish each square of cake with a pinch of coconut and ground nuts.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5899950A038EEE4486711D629B73D84B', X'93AC2536E1E1EA4EB656F5A630BCB8CA', X'93AC2536E1E1EA4EB656F5A630BCB8CA', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'42E1F12ED2EE624293853ADE1F1C3FB9', X'93AC2536E1E1EA4EB656F5A630BCB8CA', X'93AC2536E1E1EA4EB656F5A630BCB8CA', 1, NULL, NULL, 2, NULL, 0, 'Make the syrup. In a saucepan, stir the 3 1/2 cups and 3 cups sugar water together. Bring the mixture to a boil and stir until the sugar is dissolved.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9E8300F196AF0F4FA06E8CA15F675A9E', X'93AC2536E1E1EA4EB656F5A630BCB8CA', X'93AC2536E1E1EA4EB656F5A630BCB8CA', 2, NULL, NULL, 2, NULL, 0, 'Reduce the heat and let the syrup simmer gently for about 15 minutes. Add the lemon juice and simmer for 1 minute longer. Turn off the heat and let it cool.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B07857DDA3E1EB4485A7C87C78A2DD69', X'93AC2536E1E1EA4EB656F5A630BCB8CA', X'93AC2536E1E1EA4EB656F5A630BCB8CA', 3, NULL, NULL, 2, NULL, 0, 'Make the batter. In a saucepan, add the 2 cups water, butter, and 2 tablespoons sugar. Turn on the heat and stir continuously until the butter is melted.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C299D6C104D97A44ACFD937CC0A8EC06', X'93AC2536E1E1EA4EB656F5A630BCB8CA', X'93AC2536E1E1EA4EB656F5A630BCB8CA', 4, NULL, NULL, 2, NULL, 0, 'Stir in the flour using a wooden spoon until a loose dough forms. When the dough begins to collect around the spoon, remove the pan from the heat.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CB1C665AFB57EF428D21360B7EB074FD', X'93AC2536E1E1EA4EB656F5A630BCB8CA', X'93AC2536E1E1EA4EB656F5A630BCB8CA', 5, NULL, NULL, 2, NULL, 0, 'Break one egg at a time into the dough and stir them in with the wooden spoon.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F34EB0DEC35DA44E9C08EA274D5B8FF3', X'93AC2536E1E1EA4EB656F5A630BCB8CA', X'93AC2536E1E1EA4EB656F5A630BCB8CA', 6, NULL, NULL, 2, NULL, 0, 'Add the semolina and cornstarch, combining well with the wooden spoon.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'643CC1E9DEA2C24194A4471CE218B087', X'93AC2536E1E1EA4EB656F5A630BCB8CA', X'93AC2536E1E1EA4EB656F5A630BCB8CA', 7, NULL, NULL, 2, NULL, 0, 'Transfer batter to a pastry bag fitted with a star tip.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'62A48FDECEB5174CA1E7202D57D9B265', X'93AC2536E1E1EA4EB656F5A630BCB8CA', X'93AC2536E1E1EA4EB656F5A630BCB8CA', 8, NULL, NULL, 2, NULL, 0, 'Pour about 3 inches of vegetable oil into a deep frying pan and heat to 350 F, checking it with a thermometer.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'42AAC39D8B93984093462932BFC9F06B', X'93AC2536E1E1EA4EB656F5A630BCB8CA', X'93AC2536E1E1EA4EB656F5A630BCB8CA', 9, NULL, NULL, 2, NULL, 0, 'Once the oil is hot, squeeze out small lines of dough directly into the hot oil. Use a strainer or spoon to turn the pieces over and around so they brown evenly.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'43D0424F31B9D8428CF7724B9C57159E', X'93AC2536E1E1EA4EB656F5A630BCB8CA', X'93AC2536E1E1EA4EB656F5A630BCB8CA', 10, NULL, NULL, 2, NULL, 0, 'When the tulumba are evenly browned, remove them from the oil and drain on paper towels.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8B6D4B04E466B4429B3264BD76756557', X'93AC2536E1E1EA4EB656F5A630BCB8CA', X'93AC2536E1E1EA4EB656F5A630BCB8CA', 11, NULL, NULL, 2, NULL, 0, 'Add them to the cool syrup and let them soak it up for a few minutes.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7B755F3F78619A4EABB81292823478F6', X'93AC2536E1E1EA4EB656F5A630BCB8CA', X'93AC2536E1E1EA4EB656F5A630BCB8CA', 12, NULL, NULL, 2, NULL, 0, 'Arrange the syrupy sweets on a serving plate and pour some extra syrup over the top. You can garnish the tulumba with ground nuts if you wish.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4D777E9470E04B498E8E3AE281D075D5', X'7CAAA463334A3F4DA7562AF644965434', X'7CAAA463334A3F4DA7562AF644965434', 0, NULL, NULL, 2, NULL, 0, 'The most difficult part of this recipe is the first step?trimming your pumpkin. To begin, cut the top off your pumpkin like a small cap. In the same fashion, cut off the stem at the very bottom.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'119BC9B486DDFA419BD9DCA99C5BC3CD', X'7CAAA463334A3F4DA7562AF644965434', X'7CAAA463334A3F4DA7562AF644965434', 1, NULL, NULL, 2, NULL, 0, 'Using a sturdy, sharp carving knife, cut the pumpkin from top to bottom making slivers about 2 inches wide at the center.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5407360ECC94BC4DAF8DF29E18A283FD', X'7CAAA463334A3F4DA7562AF644965434', X'7CAAA463334A3F4DA7562AF644965434', 2, NULL, NULL, 2, NULL, 0, 'Clean out the seeds and soft middle portions and cut off the outer skin of each sliver. Now you should have several crescent-shaped slivers of pumpkin.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2EE477801CF4CF4498005E9181534470', X'7CAAA463334A3F4DA7562AF644965434', X'7CAAA463334A3F4DA7562AF644965434', 3, NULL, NULL, 2, NULL, 0, 'Depending on their length, cut each sliver into two or three pieces. Your goal should be pieces three to four inches long.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D4238F77BB928F47B93CA562CA206893', X'7CAAA463334A3F4DA7562AF644965434', X'7CAAA463334A3F4DA7562AF644965434', 4, NULL, NULL, 2, NULL, 0, 'Line the bottom of a large, shallow covered skillet or saucepan with the pumpkin.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9A35C9F0C26116468AA4B43EF667D41C', X'7CAAA463334A3F4DA7562AF644965434', X'7CAAA463334A3F4DA7562AF644965434', 5, NULL, NULL, 2, NULL, 0, 'Pour the sugar evenly over the top. Sprinkle the pinch of salt over the top, add the cinnamon sticks and cover the pan. Let the pan rest overnight.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'718925741559A445B7B06E1450A19E0E', X'7CAAA463334A3F4DA7562AF644965434', X'7CAAA463334A3F4DA7562AF644965434', 6, NULL, NULL, 2, NULL, 0, 'The next morning, you''ll see that the pumpkin has released all its juices. The pumpkin pieces should be nearly covered in their own juice. There should be no need to add any extra water.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BD50007D892A2545810ADBF9750227DE', X'7CAAA463334A3F4DA7562AF644965434', X'7CAAA463334A3F4DA7562AF644965434', 7, NULL, NULL, 2, NULL, 0, 'Place the plan still covered on the stove and bring it to a boil, then reduce the heat.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0361A64F3396A24EB9744F359C3B3E1A', X'7CAAA463334A3F4DA7562AF644965434', X'7CAAA463334A3F4DA7562AF644965434', 8, NULL, NULL, 2, NULL, 0, 'Let the pumpkin simmer until it becomes very soft and translucent and the juice and sugar are reduced to a thick, syrupy consistency. This can take from one to even two hours. Check the pan frequently to prevent the sugar from burning.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FF351EEBDC1ABC42AA632B206BE27FBC', X'7CAAA463334A3F4DA7562AF644965434', X'7CAAA463334A3F4DA7562AF644965434', 9, NULL, NULL, 2, NULL, 0, 'Once the pumpkin is `candied,` let it cool down in the pan for several minutes. Remove the pieces gently and arrange them on your serving platter.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D25FE39F56F8F64C9DC3075899B637BC', X'7CAAA463334A3F4DA7562AF644965434', X'7CAAA463334A3F4DA7562AF644965434', 10, NULL, NULL, 2, NULL, 0, 'Discard the cinnamon sticks. Drizzle the extra syrup over the top and refrigerate for several hours.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D61C44D2EDBB0647BECDF084DD0EE00F', X'7CAAA463334A3F4DA7562AF644965434', X'7CAAA463334A3F4DA7562AF644965434', 11, NULL, NULL, 2, NULL, 0, 'Just before serving, garnish your pumpkin pieces with crushed walnuts and a dollop of Turkish ''kaymak,'' or clotted cream.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'961D1E6715922E4A9F65E5E0B9E4EFF1', X'7CAAA463334A3F4DA7562AF644965434', X'7CAAA463334A3F4DA7562AF644965434', 12, NULL, NULL, 2, NULL, 0, 'If you want to spice up your candied pumpkin, you can add whole cloves, cardamom, pieces of ginger and nutmeg to the juice along with the cinnamon sticks before cooking.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A19C01F69445D446BFBC745B3DEB1561', X'CE84CCFDC4ECDC41AB359A34B1EE1EE4', X'CE84CCFDC4ECDC41AB359A34B1EE1EE4', 0, NULL, NULL, 2, NULL, 0, 'Put the water in a saucepan and bring it to a boil. Add the sugar and stir until dissolved.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'39A1B2AE936A9B4F8240B6C6C22A37F3', X'CE84CCFDC4ECDC41AB359A34B1EE1EE4', X'CE84CCFDC4ECDC41AB359A34B1EE1EE4', 1, NULL, NULL, 2, NULL, 0, 'Add the citric acid and continue to boil the sugar until it darkens and stiffens to the consistency of thick honey. Remove the pan from the heat.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9B0DD7686DA4B24C9E9FF79B98212164', X'CE84CCFDC4ECDC41AB359A34B1EE1EE4', X'CE84CCFDC4ECDC41AB359A34B1EE1EE4', 2, NULL, NULL, 2, NULL, 0, 'Add the tahini to the thickened sugar. Using a wooden spoon, stir the mixture by scraping the sides and bring it to the center.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FE991E841B043D4D9EEBB814E7456F67', X'CE84CCFDC4ECDC41AB359A34B1EE1EE4', X'CE84CCFDC4ECDC41AB359A34B1EE1EE4', 3, NULL, NULL, 2, NULL, 0, 'Repeat this motion evenly all around the pot, always kneading the mixture toward the center. When you have a smooth texture, add the optional nuts and mix through.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'44C1277685D5A14AA91E45126679B0F8', X'CE84CCFDC4ECDC41AB359A34B1EE1EE4', X'CE84CCFDC4ECDC41AB359A34B1EE1EE4', 4, NULL, NULL, 2, NULL, 0, 'Press the mixture into a square or rectangular dish. Let it cool down to room temperature. Cut the helva into squares and serve. You can garnish each serving with additional chopped nuts if you like.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C931E78FBABE504098A4BA87E69777D8', X'1656A3FA1CC7A94EBCAC0AF621B18275', X'1656A3FA1CC7A94EBCAC0AF621B18275', 0, NULL, NULL, 2, NULL, 0, 'Before you begin, you must boil your chestnuts until tender. To do this, using a sturdy, sharp knife, slice into the outer hull crosswise each chestnut making a slit deep enough to penetrate the inner hull as well.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8DBA3A66D0669E448B3FB61ADC9B62F4', X'1656A3FA1CC7A94EBCAC0AF621B18275', X'1656A3FA1CC7A94EBCAC0AF621B18275', 1, NULL, NULL, 2, NULL, 0, 'Put the chestnuts in a large saucepan and fill it with water to cover the chestnuts well. Bring to a boil and let the chestnuts boil with a cracked lid for about 20 minutes or until they are very tender.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6D60738DF230394BB279F931ADF62CAB', X'1656A3FA1CC7A94EBCAC0AF621B18275', X'1656A3FA1CC7A94EBCAC0AF621B18275', 2, NULL, NULL, 2, NULL, 0, 'When the chestnuts are tender, drain them and run them under cold water until they are cool enough to handle. Use the paring knife to remove the outer shell and the inner brown membrane. Be careful to remove all the membrane not leaving any small pieces.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'59A8A30CC8B39D469473757EFBAA1F70', X'1656A3FA1CC7A94EBCAC0AF621B18275', X'1656A3FA1CC7A94EBCAC0AF621B18275', 3, NULL, NULL, 2, NULL, 0, 'First , put two cups of boiled, peeled chestnuts, powdered sugar and 2 tbsp. of heavy cream in a food processor and pulse on high until well blended.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'91C2596D5D44A94FBEB591D210C5EB30', X'1656A3FA1CC7A94EBCAC0AF621B18275', X'1656A3FA1CC7A94EBCAC0AF621B18275', 4, NULL, NULL, 2, NULL, 0, 'Continue to add cream little by little, processing each time, until you have a dough-like puree that doesn''t stick to your hands. Remove the puree from the mixer, form it into a ball and let it rest in the refrigerator for 30 minutes.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'18C4EF00FFCA9A42AADB3D302BAA2035', X'1656A3FA1CC7A94EBCAC0AF621B18275', X'1656A3FA1CC7A94EBCAC0AF621B18275', 5, NULL, NULL, 2, NULL, 0, 'Melt the bitter chocolate in a double boiler or in a microwave oven. Gently whisk one tbsp. of heavy cream into to melted chocolate.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2C5E9F66F1EA5B41870CF9B68F3F289A', X'1656A3FA1CC7A94EBCAC0AF621B18275', X'1656A3FA1CC7A94EBCAC0AF621B18275', 6, NULL, NULL, 2, NULL, 0, 'Break off pieces of the chestnut puree and form them into balls the size of chestnuts. Roll them in the melted chocolate to cover all sides with the help of a fork. Place each ball on a tray covered with wax paper. Garnish one side or the top of each ball with ground pistachio nuts before the chocolate hardens.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'96F594BFFDCF0744934EDE03D40C355C', X'1656A3FA1CC7A94EBCAC0AF621B18275', X'1656A3FA1CC7A94EBCAC0AF621B18275', 7, NULL, NULL, 2, NULL, 0, 'Place the tray in the refrigerator for at least 20 minutes before serving to allow the chocolate to set.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F92895EDA5232148B1959D57D9053655', X'1656A3FA1CC7A94EBCAC0AF621B18275', X'1656A3FA1CC7A94EBCAC0AF621B18275', 8, NULL, NULL, 2, NULL, 0, 'If you don''t like working with melted chocolate, you can also roll your karyoka in cocoa powder to coat them.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'178EEB33F9B8174484829E3134DB9792', X'1656A3FA1CC7A94EBCAC0AF621B18275', X'1656A3FA1CC7A94EBCAC0AF621B18275', 9, NULL, NULL, 2, NULL, 0, 'For serving, you can place each candy in a small paper confection cup or arrange them on a fancy serving dish. A chocolate chestnut or two goes very well with coffee and boxes of them make wonderful gifts.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E0383BBA92DAC443B38E624E09031228', X'53D1D91830F60044B238A60E33D4BA8E', X'53D1D91830F60044B238A60E33D4BA8E', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'47B9B4090B62E947B6F5075387D157AA', X'53D1D91830F60044B238A60E33D4BA8E', X'53D1D91830F60044B238A60E33D4BA8E', 1, NULL, NULL, 2, NULL, 0, 'Grease the sides and bottom of a 9-inch baking pan with butter or shortening. Line with wax paper and then grease the wax paper as well.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'32F92316460AA9449525D228C0C97CC1', X'53D1D91830F60044B238A60E33D4BA8E', X'53D1D91830F60044B238A60E33D4BA8E', 2, NULL, NULL, 2, NULL, 0, 'In a saucepan, combine 1 1/2 cups of water, the granulated sugar, and lemon juice over medium heat. Stir constantly until the sugar dissolves.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0C9E1B81AA089746A6CDA8A0288885E9', X'53D1D91830F60044B238A60E33D4BA8E', X'53D1D91830F60044B238A60E33D4BA8E', 3, NULL, NULL, 2, NULL, 0, 'Allow the mixture to boil. Then reduce the heat to low and allow it to simmer until the mixture reaches 240 F on a candy thermometer. Remove from the heat and set aside.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C80A2A1305D33843BB35723E7B1ED803', X'53D1D91830F60044B238A60E33D4BA8E', X'53D1D91830F60044B238A60E33D4BA8E', 4, NULL, NULL, 2, NULL, 0, 'Combine 1 cup of cornstarch, cream of tartar, and the remaining 2 3/4 cups water in a saucepan over medium heat. Stir until all the lumps are gone and the mixture begins to boil. Stop stirring when the mixture has a glue-like consistency.?');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E0A0C01FFC8E3749AE61BFDE5F8D048E', X'53D1D91830F60044B238A60E33D4BA8E', X'53D1D91830F60044B238A60E33D4BA8E', 5, NULL, NULL, 2, NULL, 0, 'Stir in the sugar and lemon juice mixture. Stir constantly for about 5 minutes. Reduce the heat to low, and allow the mixture to simmer for 1 hour, stirring frequently.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'54DDF2A1F067E242B4557748259B104C', X'53D1D91830F60044B238A60E33D4BA8E', X'53D1D91830F60044B238A60E33D4BA8E', 6, NULL, NULL, 2, NULL, 0, 'Once the mixture has become a golden color, stir in the rosewater. Pour the mixture into the prepared pan. Spread evenly and allow to cool overnight.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4A006369FE147E40A570D8DA45A69AF5', X'53D1D91830F60044B238A60E33D4BA8E', X'53D1D91830F60044B238A60E33D4BA8E', 7, NULL, NULL, 2, NULL, 0, 'Once it has cooled overnight, sift together the confectioners'' sugar and the remaining 1/4 cup cornstarch.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CA8C652AA054734B859772F5C3763790', X'53D1D91830F60044B238A60E33D4BA8E', X'53D1D91830F60044B238A60E33D4BA8E', 8, NULL, NULL, 2, NULL, 0, 'Turn over the baking pan containing the Turkish delight onto a clean counter or table and cut with an oiled knife into one-inch pieces.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FADE157C34FB6648AB0EC91B5732FE5F', X'53D1D91830F60044B238A60E33D4BA8E', X'53D1D91830F60044B238A60E33D4BA8E', 9, NULL, NULL, 2, NULL, 0, 'Coat with the confectioners'' sugar mixture. Serve or store in an airtight container in layers separated with wax or parchment paper.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'51AAC0686A74274FA8606893556DD8EC', X'65A90FDD9DCBC946B8165DAB1CD5F896', X'65A90FDD9DCBC946B8165DAB1CD5F896', 0, NULL, NULL, 2, NULL, 0, 'If you are using ready `ekmek kadayifi,` you have to wet it down first with boiling water to soften it. Begin by placing the `kadayif` browned-side-down in a shallow, round tray of the same size. Pour enough boiling water over the bread to cover it, and let it rest for 15 minutes.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C68017A5DCEBB94396BE677E8BD3F3F8', X'65A90FDD9DCBC946B8165DAB1CD5F896', X'65A90FDD9DCBC946B8165DAB1CD5F896', 1, NULL, NULL, 2, NULL, 0, 'When time is up, remove the wet bread, and place it on a clean towel or a thick layer of folded paper towels.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9CFB8657E858EA4093AED6598B339E01', X'65A90FDD9DCBC946B8165DAB1CD5F896', X'65A90FDD9DCBC946B8165DAB1CD5F896', 2, NULL, NULL, 2, NULL, 0, 'Wring out the extra water by pressing down on the bread evenly. Let it rest on the towel for a few minutes. Discard the water in the tray and put the moistened bread back inside.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4878432329B69146AC3BF4ABA9B11743', X'65A90FDD9DCBC946B8165DAB1CD5F896', X'65A90FDD9DCBC946B8165DAB1CD5F896', 3, NULL, NULL, 2, NULL, 0, 'Using a sharp knife, cut a large cross shape at the center of the moistened bread. This will help the syrup penetrate. Place the metal tray on the stove over low heat. Slowly drizzle the syrup over the top. Turn the tray around over the burner to help the bread absorb the syrup evenly.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D3930AECD3B98748B74B4AEC58955B6E', X'65A90FDD9DCBC946B8165DAB1CD5F896', X'65A90FDD9DCBC946B8165DAB1CD5F896', 4, NULL, NULL, 2, NULL, 0, 'Put the sugar in a large pan, and let it melt slowly over medium heat. Let it caramelize until it turns a dark honey color. Add the water, and bring it to a boil.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2D3C240296DA924F973B89CE2BAACBA2', X'65A90FDD9DCBC946B8165DAB1CD5F896', X'65A90FDD9DCBC946B8165DAB1CD5F896', 5, NULL, NULL, 2, NULL, 0, 'Once it boils, add the lemon juice. Let the syrup simmer gently for about 10 minutes then turn off the heat. The syrup should be thick and the color of light honey.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5DA098F222115641BA4102AEA704AF3C', X'65A90FDD9DCBC946B8165DAB1CD5F896', X'65A90FDD9DCBC946B8165DAB1CD5F896', 6, NULL, NULL, 2, NULL, 0, 'All the bread should become saturated with the syrup which will cause it to darken. When all the syrup is completely absorbed, remove the tray from the heat, and let it cool down.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1719711CD46FB94F91B7AC1DC49D464E', X'65A90FDD9DCBC946B8165DAB1CD5F896', X'65A90FDD9DCBC946B8165DAB1CD5F896', 7, NULL, NULL, 2, NULL, 0, 'To serve, cut the `ekmek kadayifi` in squares or triangles. The traditional way to garnish it is to put a thick layer of clotted cream between two layers of bread, but you can also put it on top instead. Sprinkle the top of the dessert with crushed nuts.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'32390729A321BB49A1864583D062998B', X'30E045BB55BB9C49AD48506F44373863', X'30E045BB55BB9C49AD48506F44373863', 0, NULL, NULL, 2, NULL, 0, 'Combine the sugar, milk, and water in a saucepan and bring it to a boil. Stir it occasionally until the sugar is dissolved then turn off the heat. If you decide to add optional flavoring like vanilla, rose water or lemon zest, stir it into the hot milk mixture.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'095778F053A96A42A2242385996F268F', X'30E045BB55BB9C49AD48506F44373863', X'30E045BB55BB9C49AD48506F44373863', 1, NULL, NULL, 2, NULL, 0, 'Melt the butter in a large, shallow pan. When the butter is bubbly, add the flour all at once. Work the butter all the way through the flour using a wooden spoon.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DC845327650F5C44A212BABB206CFF76', X'30E045BB55BB9C49AD48506F44373863', X'30E045BB55BB9C49AD48506F44373863', 2, NULL, NULL, 2, NULL, 0, 'Reduce the heat to low. Stir the flour and butter mixture by turning it over with the wooden spoon. Do this continuously over very low heat for at least 15 minutes. The flour should slowly turn golden brown and take on a toasted aroma. Make sure to turn the mixture over evenly and be care not to let it brown too quickly.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E0594CE135248245B039FA41E8CBA211', X'30E045BB55BB9C49AD48506F44373863', X'30E045BB55BB9C49AD48506F44373863', 3, NULL, NULL, 2, NULL, 0, 'When the flour is ready, turn off the heat. Add the milk and sugar mixture all at once and let it bubble up. Stir the liquid through the flour as it''s bubbling. Continue to stir the mixture as it cools down. The helva will begin to bind together and form a ball. It should be soft but firm and take on a shiny texture.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EB7A8BFBA6D10C47B92182F0A7101F90', X'30E045BB55BB9C49AD48506F44373863', X'30E045BB55BB9C49AD48506F44373863', 4, NULL, NULL, 2, NULL, 0, 'While the ball of helva is still hot, you can shape it as you wish. You can use a large spoon to make oval-shaped individual helva, or you can press the helva down in a shallow serving dish. When it''s cool, you can cut it into squares.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'40809D309249DA40AEE2A2A185C9C037', X'30E045BB55BB9C49AD48506F44373863', X'30E045BB55BB9C49AD48506F44373863', 5, NULL, NULL, 2, NULL, 0, 'Toast the pine nuts by turning them over in a small, non-stick pan over high heat. Press a pine nut on the top of each ''spoon'' helva, or press them evenly in a pattern over the top of flat helva.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FB756CA03DC3D44288A1231888B225B9', X'5FC69B7A2B5D0641ACB039C14467FB67', X'5FC69B7A2B5D0641ACB039C14467FB67', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients. Preheat the oven to 350 F / 180 C.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FAFF391F6898E849B5BAEF95C5BD0900', X'5FC69B7A2B5D0641ACB039C14467FB67', X'5FC69B7A2B5D0641ACB039C14467FB67', 1, NULL, NULL, 2, NULL, 0, 'In a colander, rinse the rice until the water runs clear.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1008AE91071FCD4696496FC2991F8786', X'5FC69B7A2B5D0641ACB039C14467FB67', X'5FC69B7A2B5D0641ACB039C14467FB67', 2, NULL, NULL, 2, NULL, 0, 'In a large saucepan, add the rice and enough water to cover it by about 1/2 inch.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B1AC8DBF6C383740B7DEC1F15959B736', X'5FC69B7A2B5D0641ACB039C14467FB67', X'5FC69B7A2B5D0641ACB039C14467FB67', 3, NULL, NULL, 2, NULL, 0, 'Bring to a boil, then reduce the heat. Let the rice simmer gently until it''s softened, about 5 minutes.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1602FAD5A28A20449EB63DE3EE35444C', X'5FC69B7A2B5D0641ACB039C14467FB67', X'5FC69B7A2B5D0641ACB039C14467FB67', 4, NULL, NULL, 2, NULL, 0, 'Add 3 3/4 cups milk, the sugar, and vanilla extract to the rice. Bring the mixture to a boil. Reduce the heat and let it boil very gently for about 10 minutes.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C8E427EB636AA54BA9537E9E18D068B1', X'5FC69B7A2B5D0641ACB039C14467FB67', X'5FC69B7A2B5D0641ACB039C14467FB67', 5, NULL, NULL, 2, NULL, 0, 'In a small bowl, use a whisk to mix 1 cup of milk and the cornstarch until smooth.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D8CA7234803F2B458A02E3C45DF8D3C7', X'5FC69B7A2B5D0641ACB039C14467FB67', X'5FC69B7A2B5D0641ACB039C14467FB67', 6, NULL, NULL, 2, NULL, 0, 'While stirring, gradually pour the milk-cornstarch mixture into the rice.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8B91F1A0DBAD7F46BB72740BD036B90F', X'5FC69B7A2B5D0641ACB039C14467FB67', X'5FC69B7A2B5D0641ACB039C14467FB67', 7, NULL, NULL, 2, NULL, 0, 'Turn up the heat and continue stirring the pudding until scalding.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'352EA208BB5D2E44976E916662C265EE', X'5FC69B7A2B5D0641ACB039C14467FB67', X'5FC69B7A2B5D0641ACB039C14467FB67', 8, NULL, NULL, 2, NULL, 0, 'When the pudding thickens, continue to stir and cook for about 2 minutes more.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'99EEEB615A31D54C9A40D9F8103578C5', X'5FC69B7A2B5D0641ACB039C14467FB67', X'5FC69B7A2B5D0641ACB039C14467FB67', 9, NULL, NULL, 2, NULL, 0, 'Remove from the heat and fill small, ovenproof dessert cups, clay cups, or disposable aluminum pudding cups with the hot mixture.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4B9572BE2F821A41ABC05D2142C56509', X'5FC69B7A2B5D0641ACB039C14467FB67', X'5FC69B7A2B5D0641ACB039C14467FB67', 10, NULL, NULL, 2, NULL, 0, 'In a small bowl, beat the egg yolk with the remaining 3 tablespoons of milk.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4F046E766189D646B9ABEFB7341633BE', X'5FC69B7A2B5D0641ACB039C14467FB67', X'5FC69B7A2B5D0641ACB039C14467FB67', 11, NULL, NULL, 2, NULL, 0, 'Using a spoon, drizzle a small amount of the egg-milk mixture into the center of each dessert cup.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8D211CDCC880ED459A321411951307C1', X'5FC69B7A2B5D0641ACB039C14467FB67', X'5FC69B7A2B5D0641ACB039C14467FB67', 12, NULL, NULL, 2, NULL, 0, 'With the back of the spoon, use a light, circular motion to swirl the egg yolk from the center to the edges so it spreads evenly over the top of each cup.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'59EC4ACAEE09434E98E7D6E7AEB14EC2', X'5FC69B7A2B5D0641ACB039C14467FB67', X'5FC69B7A2B5D0641ACB039C14467FB67', 13, NULL, NULL, 2, NULL, 0, 'Bake the pudding cups until the tops are nicely browned, about 20 minutes.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DDBF774A255A224C8C92338D607390F0', X'5FC69B7A2B5D0641ACB039C14467FB67', X'5FC69B7A2B5D0641ACB039C14467FB67', 14, NULL, NULL, 2, NULL, 0, 'Let the cups cool at room temperature and then refrigerate for several hours before serving. Enjoy.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6748A7AF1724D943BCE81C0FA1A98659', X'711B1759E03C924487CA95923889DE56', X'711B1759E03C924487CA95923889DE56', 0, NULL, NULL, 2, NULL, 0, 'Begin by washing the rice in a wire strainer under cold, running water for several minutes until the water runs clear. Once it''s washed and drained, put the rice in a large saucepan. Add water to the pan to cover the rice about ½ inch. Bring the water to a boil, cover the pan, turn the heat to low and cook the rice for about 20 minutes.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'ACB4636ABEF29D49928933C2E4CB08AA', X'711B1759E03C924487CA95923889DE56', X'711B1759E03C924487CA95923889DE56', 1, NULL, NULL, 2, NULL, 0, 'Put the 4 ½ cups water in a separate saucepan. Add the saffron and let it soak for about 15 minutes. After it softens, use the edge of a wooden spoon to crush the saffron and release the yellow color.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CCC7A1C190590149B22775624308036F', X'711B1759E03C924487CA95923889DE56', X'711B1759E03C924487CA95923889DE56', 2, NULL, NULL, 2, NULL, 0, 'Add the cooked rice, sugar and turmeric to the saffron and water and bring it to a boil. Add the rose water and cornstarch and cook, stirring continuously for about 20 minutes until the pudding thickens and becomes a clear yellow color.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C5F680239E24B747B0701A4BC4455707', X'711B1759E03C924487CA95923889DE56', X'711B1759E03C924487CA95923889DE56', 3, NULL, NULL, 2, NULL, 0, 'Turn off the heat and leave the pudding to cool in the pan about 10 minutes.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DD24C7F982ABEB40BE9F34CFD836493A', X'711B1759E03C924487CA95923889DE56', X'711B1759E03C924487CA95923889DE56', 4, NULL, NULL, 2, NULL, 0, 'Fill your dessert cups or a large serving bowl with the pudding. Let it cool to room temperature. Refrigerate for several hours.');
INSERT INTO `VWR INTERNAL MiddleEasternRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DFB75F52E6445941BDEC777829C56FAA', X'711B1759E03C924487CA95923889DE56', X'711B1759E03C924487CA95923889DE56', 5, NULL, NULL, 2, NULL, 0, 'Serve your ''zerde'' cold. Garnish it with more pine nuts, and dried currants that have been soaked for a few minutes in hot water. You can also use other toppings like ground coconut, crushed pistachio nuts, and fresh pomegranate seeds.');
