INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3906400113C8844E90F644A19D932645', X'8ADA55AF4DF2444198916570281DD257', X'8ADA55AF4DF2444198916570281DD257', 0, NULL, NULL, 2, NULL, 0, 'Place the cashews in a bowl and cover with room temperature water. Let soak for at least 30 minutes or up to 3 hours. Drain.');
