INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'175A66B0F8DD0D4B9321ADCA7CD0D9BD', X'8ADA55AF4DF2444198916570281DD257', X'8ADA55AF4DF2444198916570281DD257', 3, NULL, NULL, 2, NULL, 0, 'Add the drained cashews, onion mixture, almond milk, nutritional yeast, lemon juice, and mustard to a blender. Season with salt and pepper and secure the lid.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3CB279A41AB93C49BE054A619A6DF35B', X'8ADA55AF4DF2444198916570281DD257', X'8ADA55AF4DF2444198916570281DD257', 4, NULL, NULL, 2, NULL, 0, 'Blend until very smooth, adding water 1/4 cup at a time until desired consistency is reached. Taste for seasoning and adjust as needed.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'25B35A34873F3F4090F20130EE235F6B', X'8ADA55AF4DF2444198916570281DD257', X'8ADA55AF4DF2444198916570281DD257', 5, NULL, NULL, 2, NULL, 0, 'Add the blended sauce to a saucepot and warm over low heat, stirring constantly. Add a splash or two of water if it thickens up too much.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DE91DC0F0F261A4BB59F37F0339FA112', X'8ADA55AF4DF2444198916570281DD257', X'8ADA55AF4DF2444198916570281DD257', 6, NULL, NULL, 2, NULL, 0, 'On a platter or individual plates, pile up half of the chips and top with cheese sauce, beans, and salsa. Top with the other half of the chips and repeat, ending with a mound of guacamole. Serve immediately.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A0F6D75D70E8FC43A83C223E0420E30A', X'B6B5CD23DC50E74394BABED9FC91C3A4', X'B6B5CD23DC50E74394BABED9FC91C3A4', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 350 F. Prepare the muffin tin by lining with baking cups.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5944C69DBDC9D54A98331292D052B86E', X'B6B5CD23DC50E74394BABED9FC91C3A4', X'B6B5CD23DC50E74394BABED9FC91C3A4', 1, NULL, NULL, 2, NULL, 0, 'In the mixing bowl of a stand mixer, combine cake mix, Margarita mix, egg whites, oil and lime zest. Blend on low for 30 seconds then medium for 2 minutes.');
