INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'36CAB1EB954CF644B2E394845E357A70', X'B6B5CD23DC50E74394BABED9FC91C3A4', X'B6B5CD23DC50E74394BABED9FC91C3A4', 3, NULL, NULL, 2, NULL, 0, 'Bake 22 to 24 minutes or until a toothpick inserted comes out clean.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'64952FF341F4704A9AEAC4AC7E69D508', X'B6B5CD23DC50E74394BABED9FC91C3A4', X'B6B5CD23DC50E74394BABED9FC91C3A4', 4, NULL, NULL, 2, NULL, 0, 'Cool in pans for 5 minutes and then remove from pans and cool completely before frosting.');
