INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F07D6E9EBC75B94F82ADF771245E4FE2', X'B6B5CD23DC50E74394BABED9FC91C3A4', X'B6B5CD23DC50E74394BABED9FC91C3A4', 6, NULL, NULL, 2, NULL, 0, 'Gradually add sugar 1 cup at a time, beating well after each addition.');
