INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DB9D968CCCFA2945B95A064AE2AB408F', X'B6B5CD23DC50E74394BABED9FC91C3A4', X'B6B5CD23DC50E74394BABED9FC91C3A4', 8, NULL, NULL, 2, NULL, 0, 'Spread icing onto cupcakes. Sprinkle green sanding sugar around the edge of the cupcakes and garnish with additional lime zest.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CE31F351E368A743B1A12015BB0F1576', X'B6B5CD23DC50E74394BABED9FC91C3A4', X'B6B5CD23DC50E74394BABED9FC91C3A4', 9, NULL, NULL, 2, NULL, 0, 'Cut a small opening at the tip of the Shot Tops Infuser. Place the cut opening into the tequila and squeeze the top to fill. Insert the cut end into the cupcakes. When ready to eat, squeeze the top of Shot Tops Infuser again to infuse cake with tequila.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A8AEE2CE4CB68145ACB7A2529691CD0D', X'A8A8A94EB7B12040827A50BF89FDE940', X'A8A8A94EB7B12040827A50BF89FDE940', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6185CAA06940FB48A79BB563D29B53B5', X'A8A8A94EB7B12040827A50BF89FDE940', X'A8A8A94EB7B12040827A50BF89FDE940', 1, NULL, NULL, 2, NULL, 0, 'In a small dish or bowl, combine the soymilk and vinegar and allow to rest for 3-5 minutes or until thickened slightly. Mix in cornstarch or arrowroot, stirring to dissolve. Set aside.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E3D24170D78925478B9715E5264BEFD2', X'A8A8A94EB7B12040827A50BF89FDE940', X'A8A8A94EB7B12040827A50BF89FDE940', 2, NULL, NULL, 2, NULL, 0, 'In a blender or food processor, process the cashews until finely ground. Set aside.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'447DDE4034733A4481E9F6FA17C85195', X'A8A8A94EB7B12040827A50BF89FDE940', X'A8A8A94EB7B12040827A50BF89FDE940', 3, NULL, NULL, 2, NULL, 0, 'In a small saucepan over medium heat, combine water, lemon juice, nutritional yeast, spices, and tahini. Stir in ground cashews and soymilk mixture until well blended. Stir in prepared salsa and salt. Cook until desired consistency, keeping in mind that mixture will thicken slightly as it cools. Salt to taste.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2F3EA82297B07B498CBC311F29CAB4F0', X'A8A8A94EB7B12040827A50BF89FDE940', X'A8A8A94EB7B12040827A50BF89FDE940', 4, NULL, NULL, 2, NULL, 0, 'Serve warm, at room temperature or cold with tortilla or pita chips, or use as a sauce for tacos, quesadillas or burritos.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E148F74CEE48C946892D5063E3E9156E', X'6ECA1FF5ED57FE4D8D17DB1C28EAA72E', X'6ECA1FF5ED57FE4D8D17DB1C28EAA72E', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BFAB0999E59F30489E46ACC590ED6A2C', X'6ECA1FF5ED57FE4D8D17DB1C28EAA72E', X'6ECA1FF5ED57FE4D8D17DB1C28EAA72E', 1, NULL, NULL, 2, NULL, 0, 'Select the sauté button on the Instant Pot and add about half of the oil to the inner pot. When the oil is hot and shimmering, add about half of the beef. Cook until well browned on all sides. Remove the beef to a plate. Repeat with the remaining oil and beef. Remove the beef to the plate.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'26D85F83EFBF7B46BB00E25AFC88620B', X'6ECA1FF5ED57FE4D8D17DB1C28EAA72E', X'6ECA1FF5ED57FE4D8D17DB1C28EAA72E', 2, NULL, NULL, 2, NULL, 0, 'In a bowl, combine the stock, vinegar, lime juice, peppers, adobo sauce, cumin, oregano, salt, pepper, and cloves; stir until blended. Add the liquids and seasonings to the pot and stir, scraping up any browned bits in the bottom of the pot.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EE75FDAA58F6B4499327C6BEAEB951EF', X'6ECA1FF5ED57FE4D8D17DB1C28EAA72E', X'6ECA1FF5ED57FE4D8D17DB1C28EAA72E', 3, NULL, NULL, 2, NULL, 0, 'Add the beef back to the Instant Pot along with the bay leaves.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8073F2E2CAA5A8459456D4E9D424455C', X'6ECA1FF5ED57FE4D8D17DB1C28EAA72E', X'6ECA1FF5ED57FE4D8D17DB1C28EAA72E', 4, NULL, NULL, 2, NULL, 0, 'Place the lid on the Instant Pot and lock it in place. Turn the valve to the sealing position and select pressure cook or manual setting (high pressure). Set the time for 45 minutes.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BEEB5B15D48CAF46B994D1C83D0A41A6', X'6ECA1FF5ED57FE4D8D17DB1C28EAA72E', X'6ECA1FF5ED57FE4D8D17DB1C28EAA72E', 5, NULL, NULL, 2, NULL, 0, 'When the time is up, let the pressure come down naturally for about 10 minutes. Carefully turn the valve to the venting position to release the remaining pressure.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2FEC1A94F00E86459E3FAF3DFD1A0C54', X'6ECA1FF5ED57FE4D8D17DB1C28EAA72E', X'6ECA1FF5ED57FE4D8D17DB1C28EAA72E', 6, NULL, NULL, 2, NULL, 0, 'Remove the beef to a pan or bowl and shred it with two forks. Add the liquids to the beef. If you aren''t serving immediately, add the shredded beef back to the pot and keep it on the warm setting.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DEEF565EF1EC144CA339AED398E67900', X'6ECA1FF5ED57FE4D8D17DB1C28EAA72E', X'6ECA1FF5ED57FE4D8D17DB1C28EAA72E', 7, NULL, NULL, 2, NULL, 0, 'Serve the beef in soft or crisp corn tortillas with fresh salsa, lime wedges, and chopped cilantro.');
