INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4F6E91FD95464F42867D888BED4F6D1C', X'44017979C1DB5947BDDCE66435D1F12F', X'44017979C1DB5947BDDCE66435D1F12F', 3, NULL, NULL, 2, NULL, 0, 'Heat oven to 425 F.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'60164225573375459D3E8F63307C8B06', X'44017979C1DB5947BDDCE66435D1F12F', X'44017979C1DB5947BDDCE66435D1F12F', 4, NULL, NULL, 2, NULL, 0, 'In a large bowl or in an electric blender, combine the eggs, flour, milk, Dijon mustard, kosher salt, and pepper. Whisk or process until the batter is smooth.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F171BB725DDBA34CAD4CDDCBA455FB3E', X'44017979C1DB5947BDDCE66435D1F12F', X'44017979C1DB5947BDDCE66435D1F12F', 5, NULL, NULL, 2, NULL, 0, 'Wipe the heavy 10-inch skillet out, and add 3 tablespoons of butter. Place the skillet over medium heat, and swirl the butter so that it coats the bottom and sides. When the foaming subsides, pour the batter into the skillet.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AC29B5E4C227F94A9C6576F1AD56127E', X'44017979C1DB5947BDDCE66435D1F12F', X'44017979C1DB5947BDDCE66435D1F12F', 6, NULL, NULL, 2, NULL, 0, 'Combine the shredded cheese and cooked bacon, and sprinkle over the center of the egg batter, leaving an edge of about 1 inch to allow the batter to puff as it bakes.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9D19423667CD354FB1E32FF8FFB916CE', X'44017979C1DB5947BDDCE66435D1F12F', X'44017979C1DB5947BDDCE66435D1F12F', 7, NULL, NULL, 2, NULL, 0, 'Bake for about 15 to 17 minutes, or until the pancake is browned and puffed around the edge. Turn the oven temperature down to 325 F, and let it cook for about 3 to 5 minutes longer, or until set.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'494C1EBB3088D74B9937BF70DA8CDFE5', X'44017979C1DB5947BDDCE66435D1F12F', X'44017979C1DB5947BDDCE66435D1F12F', 8, NULL, NULL, 2, NULL, 0, 'Remove the pancake from the oven, and garnish with chives or parsley. Slice into wedges, and serve hot.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'464E3A05F2BB85439B0BC7CCBB110931', X'12555A6A71EAE7459AD9D2EBC8004932', X'12555A6A71EAE7459AD9D2EBC8004932', 0, NULL, NULL, 2, NULL, 0, 'Gather your ingredients.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'33A5A66E64E7B248A79E28A72E97BB70', X'12555A6A71EAE7459AD9D2EBC8004932', X'12555A6A71EAE7459AD9D2EBC8004932', 1, NULL, NULL, 2, NULL, 0, 'Wipe the strawberries with paper towels and remove the green stem with a kitchen knife. Slice the strawberries into quarters.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C7E5DB0FD4E3824E85DC3F56C9122012', X'12555A6A71EAE7459AD9D2EBC8004932', X'12555A6A71EAE7459AD9D2EBC8004932', 2, NULL, NULL, 2, NULL, 0, 'Place the sliced strawberries in a mixing bowl and add the pepper, sugar, and lemon zest. Stir gently, cover with a clean dish towel, and leave for 1 hour in the refrigerator.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1B8F276A4C5F5549B372444BA4F531F8', X'12555A6A71EAE7459AD9D2EBC8004932', X'12555A6A71EAE7459AD9D2EBC8004932', 3, NULL, NULL, 2, NULL, 0, 'Remove the strawberries from the fridge and transfer to a food processor, or you can use a stick blender, and blitz until smooth and foamy. Set aside, cover again with a dish towel, and leave to settle while you make the cream.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0DDFE9336662E54BBD8197965115B436', X'12555A6A71EAE7459AD9D2EBC8004932', X'12555A6A71EAE7459AD9D2EBC8004932', 4, NULL, NULL, 2, NULL, 0, 'Whisk the mascarpone cheese with the vanilla and powdered sugar until light and airy. Pop the bowl into the fridge to chill slightly, along with four bowls, for 5 minutes.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'53824D1A0F65764BBE4A493BC7224BF1', X'12555A6A71EAE7459AD9D2EBC8004932', X'12555A6A71EAE7459AD9D2EBC8004932', 5, NULL, NULL, 2, NULL, 0, 'To Serve: Carefully ladle the strawberry soup into the chilled bowls — any large bubbles will dissipate with a gentle tap on the side of the bowl. Use a dessert spoon to scoop up a ball of the mascarpone mixture, or if you want a more professional presentation, using two dessert spoons, you can roll the mascarpone into a quenelle.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'69253F047C595240A42DB109B7F9E24F', X'12555A6A71EAE7459AD9D2EBC8004932', X'12555A6A71EAE7459AD9D2EBC8004932', 6, NULL, NULL, 2, NULL, 0, 'Dot the soup with the balsamic glaze, decorate with small leaves of basil, and serve immediately.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3F3858F8DD02164392EF2844522458D2', X'C2506253E7954C41AE8988275E358924', X'C2506253E7954C41AE8988275E358924', 0, NULL, NULL, 2, NULL, 0, 'Start by greasing and lining the sides and base of a 20cm / 8` diameter springform cake tin with greaseproof paper.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1D24CB70B86FC644A9256F3049111EC7', X'63C72D58AA60AE4D95D47F9A7125992D', X'63C72D58AA60AE4D95D47F9A7125992D', 0, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 400F/200C Gas 6');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E2345620202CDB4EBF9AC1C82BA3C0E7', X'63C72D58AA60AE4D95D47F9A7125992D', X'63C72D58AA60AE4D95D47F9A7125992D', 1, NULL, NULL, 2, NULL, 0, 'Start by chilling a large mixing bowl in the freezer for 10 minutes or 30 in the fridge.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0B2BFFFC35058843B316998A9B3EE3CE', X'63C72D58AA60AE4D95D47F9A7125992D', X'63C72D58AA60AE4D95D47F9A7125992D', 2, NULL, NULL, 2, NULL, 0, 'Sieve the flour and baking powder together into the large mixing bowl. Add the butter and rub into the flour working as quickly until the mixture resembles breadcrumbs, do this as fast as possible, so the mixture does not become warm.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'049EFF6E2B69124299829EC145F8F8A0', X'63C72D58AA60AE4D95D47F9A7125992D', X'63C72D58AA60AE4D95D47F9A7125992D', 3, NULL, NULL, 2, NULL, 0, 'Add the caster sugar and the lavender, stir with a metal spoon.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F1BAFDB034562C458A1854D2DC14F4F6', X'63C72D58AA60AE4D95D47F9A7125992D', X'63C72D58AA60AE4D95D47F9A7125992D', 4, NULL, NULL, 2, NULL, 0, 'Reserve 2 tablespoons of egg (for glazing) then add to the mixture with 3/4 of the milk. Using a dinner knife (not your hands) mix to create a soft, dough?if the dough feels dry add a little more milk but take care not to make the dough too sticky.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6514F790B921B04B8D41DD67052A6B6F', X'63C72D58AA60AE4D95D47F9A7125992D', X'63C72D58AA60AE4D95D47F9A7125992D', 5, NULL, NULL, 2, NULL, 0, 'Tip the dough onto a lightly floured surface and knead gently and lightly to help smoothen taking care not to be too heavy or knead too long.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'ADA742D3658F2A48984C2391F6E09FCB', X'63C72D58AA60AE4D95D47F9A7125992D', X'63C72D58AA60AE4D95D47F9A7125992D', 6, NULL, NULL, 2, NULL, 0, 'Roll?again carefully?into a circle at least 2 cm deep.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4865B060E465BF4FA8DB12989FEC7F62', X'63C72D58AA60AE4D95D47F9A7125992D', X'63C72D58AA60AE4D95D47F9A7125992D', 7, NULL, NULL, 2, NULL, 0, 'Dip a 6 cm cutter into your bag of flour and cut out the scones avoiding twisting the cutter (this can prevent the scones from rising). Reform the dough trimmings into a ball and repeat as above until you have used it all up.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FD536F279F68DE429CCE9AC981A09638', X'63C72D58AA60AE4D95D47F9A7125992D', X'63C72D58AA60AE4D95D47F9A7125992D', 8, NULL, NULL, 2, NULL, 0, 'Place close together but not touching on a lightly greased baking sheet and brush the tops with the beaten egg. Bake in the center of the preheated oven for approximately 10 to 12 minutes or until golden and risen. Check the base of the scones to make sure it also is golden.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D906CD501190EE4F8F4AB8A9241F172C', X'63C72D58AA60AE4D95D47F9A7125992D', X'63C72D58AA60AE4D95D47F9A7125992D', 9, NULL, NULL, 2, NULL, 0, 'Place on a cooling rack.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E22EBB1F1C97F24A9BD498CEC0AC8432', X'AEC4DD67123CFC449DD5D333DFF545C3', X'AEC4DD67123CFC449DD5D333DFF545C3', 0, NULL, NULL, 2, NULL, 0, 'Gather your ingredients.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0AF8774CBF82BE43A3D0C1F51F7975AD', X'AEC4DD67123CFC449DD5D333DFF545C3', X'AEC4DD67123CFC449DD5D333DFF545C3', 1, NULL, NULL, 2, NULL, 0, 'Place the graham crackers and sugar in a food processor and process until completely crumbled. Drizzle in the melted butter.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BAE895FCB0BC8149AF648BACB12F887C', X'AEC4DD67123CFC449DD5D333DFF545C3', X'AEC4DD67123CFC449DD5D333DFF545C3', 2, NULL, NULL, 2, NULL, 0, 'Press the graham cracker crust into 6 greased mini cheesecake molds. If you are using a muffin tin, make sure to use liners. Bake for 5 to 10 minutes at 350 F or until lightly browned.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2AD76E588AB2BD42902E1C9927E1E630', X'AEC4DD67123CFC449DD5D333DFF545C3', X'AEC4DD67123CFC449DD5D333DFF545C3', 3, NULL, NULL, 2, NULL, 0, 'Beat the softened cream cheese with the sugar and vanilla extract until combined and fluffy.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'42D34E9479A84744858ED0CED4812943', X'AEC4DD67123CFC449DD5D333DFF545C3', X'AEC4DD67123CFC449DD5D333DFF545C3', 4, NULL, NULL, 2, NULL, 0, 'Fold the whipped cream topping into the cream cheese mixture.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B9D1DD6A7A15CB4CB5131714F4027D1C', X'AEC4DD67123CFC449DD5D333DFF545C3', X'AEC4DD67123CFC449DD5D333DFF545C3', 5, NULL, NULL, 2, NULL, 0, 'Fill the prepared molds with the cream cheese filling. Fill the molds a little bit at a time. Then press down on the top of the filling with a spoon or spatula so they completely fill the mold and there are no gaps or pockets of air.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C11FB4BDA178504BAF0F09CEB1A7617B', X'AEC4DD67123CFC449DD5D333DFF545C3', X'AEC4DD67123CFC449DD5D333DFF545C3', 6, NULL, NULL, 2, NULL, 0, 'Cover and refrigerate for 2 hours, or until completely chilled and firm.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6FD40DB853A6DC47B9486C6A7165B09B', X'AEC4DD67123CFC449DD5D333DFF545C3', X'AEC4DD67123CFC449DD5D333DFF545C3', 7, NULL, NULL, 2, NULL, 0, 'Remove the cheesecakes from the molds, top with fresh berries and serve! They can also be kept chilled for up to 2 days before serving!');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C6F1DC67CF1F5F42B2CF5D1A2059809E', X'D33BA2915B26F44AA33EF98DFFD2DAF0', X'D33BA2915B26F44AA33EF98DFFD2DAF0', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AF12FF3E03816B40861A1457AEA99ED3', X'D33BA2915B26F44AA33EF98DFFD2DAF0', X'D33BA2915B26F44AA33EF98DFFD2DAF0', 1, NULL, NULL, 2, NULL, 0, 'Whisk the buttermilk, eggs, 1 teaspoon of the salt, and 1 teaspoon of the pepper in a large bowl. Add the cubed steaks to the bowl and cover the bowl and refrigerate for at least one hour but up to overnight. Make sure to submerge the steaks in the buttermilk mixture.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'79242A9A5D8E44469F857D8974ED626C', X'D33BA2915B26F44AA33EF98DFFD2DAF0', X'D33BA2915B26F44AA33EF98DFFD2DAF0', 2, NULL, NULL, 2, NULL, 0, 'Whisk together the flour, remaining salt, pepper, onion powder, and paprika in a shallow dish. Set aside 2 tablespoons of the seasoned flour for the gravy.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'26E56C8C214B694597DB7952FC5B348C', X'D33BA2915B26F44AA33EF98DFFD2DAF0', X'D33BA2915B26F44AA33EF98DFFD2DAF0', 3, NULL, NULL, 2, NULL, 0, 'Remove 1 steak from the buttermilk mixture and dredge it in the flour mixture, coating both sides. Place the steak back into the buttermilk mixture, coating both sides. Then place the steak back into the flour mixture, coating once more. Repeat with the remaining steaks.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'07FB89313AF52E4E8398953F4440E31D', X'D33BA2915B26F44AA33EF98DFFD2DAF0', X'D33BA2915B26F44AA33EF98DFFD2DAF0', 4, NULL, NULL, 2, NULL, 0, 'Place the steaks on top of a cooling rack over a sheet pan and refrigerate for 30 minutes.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'277515D7697E9441BC50EB2CE0CCADE8', X'D33BA2915B26F44AA33EF98DFFD2DAF0', X'D33BA2915B26F44AA33EF98DFFD2DAF0', 5, NULL, NULL, 2, NULL, 0, 'Add the oil to a large cast-iron skillet or other heavy-bottomed pan. Heat the oil to 350 F. Add the steaks, frying for 3 to 4 minutes per side. Remove the steaks and place them onto another clean cooling rack over a baking sheet. Keep warm in a 200 F oven.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1B27F449FA0A0E45BBCD873ED5E2517F', X'D33BA2915B26F44AA33EF98DFFD2DAF0', X'D33BA2915B26F44AA33EF98DFFD2DAF0', 6, NULL, NULL, 2, NULL, 0, 'Gather the ingredients for the gravy.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'56DC52A8DE3C9A46A7C04FD3D67B2934', X'D33BA2915B26F44AA33EF98DFFD2DAF0', X'D33BA2915B26F44AA33EF98DFFD2DAF0', 7, NULL, NULL, 2, NULL, 0, 'Add the butter to the remaining oil and then add the 2 tablespoons of reserved flour. Whisk until the flour is incorporated into the oil. Cook for 2 to 3 minutes or until the flour is lightly browned.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D343A15AC232074B87ECF7B3FAD3839C', X'D33BA2915B26F44AA33EF98DFFD2DAF0', X'D33BA2915B26F44AA33EF98DFFD2DAF0', 8, NULL, NULL, 2, NULL, 0, 'Pour the beef broth and hot sauce into the pan while whisking. Bring the gravy to a boil and then turn the heat to low and simmer, whisking occasionally for another 3 minutes.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5A45C2CDC245704489A35D423196BBEE', X'D33BA2915B26F44AA33EF98DFFD2DAF0', X'D33BA2915B26F44AA33EF98DFFD2DAF0', 9, NULL, NULL, 2, NULL, 0, 'Remove the steaks form the warm oven, top them with the gravy, and serve alongside your favorite sides.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3963341EAF39CF44A526E8327EC267A5', X'FEED5A75F3321E4FAEB94FEBDAB2014F', X'FEED5A75F3321E4FAEB94FEBDAB2014F', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'96BC87393C7F38498B7D25827C874CAE', X'FEED5A75F3321E4FAEB94FEBDAB2014F', X'FEED5A75F3321E4FAEB94FEBDAB2014F', 1, NULL, NULL, 2, NULL, 0, 'Cut the pointy end of a strawberry off so that the berry can stand up on its own.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D872172305B1A2488B117EAA769923F5', X'FEED5A75F3321E4FAEB94FEBDAB2014F', X'FEED5A75F3321E4FAEB94FEBDAB2014F', 2, NULL, NULL, 2, NULL, 0, 'Hull the strawberry. Then cut a hole in the top of the berry to hold the filling, being careful not to cut all the way through to the bottom. Repeat with the remaining strawberries.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C7A873AB1173644687260F3619F38C3D', X'FEED5A75F3321E4FAEB94FEBDAB2014F', X'FEED5A75F3321E4FAEB94FEBDAB2014F', 3, NULL, NULL, 2, NULL, 0, 'In a medium mixing bowl, combine the cream cheese, powdered sugar, heavy cream, lemon juice, and vanilla extract. Beat with a hand mixer until well mixed and creamy.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D8419E6BADDDA647AA1881287C6BEA40', X'FEED5A75F3321E4FAEB94FEBDAB2014F', X'FEED5A75F3321E4FAEB94FEBDAB2014F', 4, NULL, NULL, 2, NULL, 0, 'In a small bowl, combine the graham cracker crumbs, melted butter, and sugar. Mix to combine.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DE81F70EC1EA1946AEA5B616EB7C757B', X'FEED5A75F3321E4FAEB94FEBDAB2014F', X'FEED5A75F3321E4FAEB94FEBDAB2014F', 5, NULL, NULL, 2, NULL, 0, 'Add the cheesecake filling to a piping bag and pipe it into the center of each strawberry. If you don’t have a piping bag you can fill a ziplock bag and cut off a corner and use that instead. You can also fill with a small spoon if needed.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'00AC441BC56152428D4293E949A1F71F', X'FEED5A75F3321E4FAEB94FEBDAB2014F', X'FEED5A75F3321E4FAEB94FEBDAB2014F', 6, NULL, NULL, 2, NULL, 0, 'Top with a dollop of whipped cream, if using. Sprinkle the top of each strawberry with the graham cracker crumbs. Chill, covered, in the refrigerator for 30 minutes to allow the cream cheese filling to firm up a bit, or up to 24 hours.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'38B27C12573C6C469161DF2CB020780F', X'FEED5A75F3321E4FAEB94FEBDAB2014F', X'FEED5A75F3321E4FAEB94FEBDAB2014F', 7, NULL, NULL, 2, NULL, 0, 'Serve and enjoy!');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3052055BBB74204F8D2C05B954C206B8', X'900971B92BA67E40959429E8F2E9CF9E', X'900971B92BA67E40959429E8F2E9CF9E', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C931A9D2004548458FD472B1F5671FA7', X'900971B92BA67E40959429E8F2E9CF9E', X'900971B92BA67E40959429E8F2E9CF9E', 1, NULL, NULL, 2, NULL, 0, 'Position the oven rack to the lowest position in your oven. Preheat to 425 F. Slice the potatoes to 1/8 inch thick using a mandoline or a very sharp knife.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D6F87E7D6614E64F8BEA97D8530F289A', X'900971B92BA67E40959429E8F2E9CF9E', X'900971B92BA67E40959429E8F2E9CF9E', 2, NULL, NULL, 2, NULL, 0, 'Place the sliced potatoes into a bowl of cold water. Stir the potatoes around and allow to sit for 10 minutes.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BE7362BAA1D95F4DBF7A4293C9F2F62E', X'900971B92BA67E40959429E8F2E9CF9E', X'900971B92BA67E40959429E8F2E9CF9E', 3, NULL, NULL, 2, NULL, 0, 'Remove the potatoes from the water and place in a colander to drain. Spread the potatoes onto a paper towel-lined baking sheet and pat dry. Allow to sit for another 15 minutes or so, until as dry as possible.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3063B93DB7F3E743A4C789B211CC176D', X'900971B92BA67E40959429E8F2E9CF9E', X'900971B92BA67E40959429E8F2E9CF9E', 4, NULL, NULL, 2, NULL, 0, 'While the potatoes are drying, add 2 tablespoons of the butter to a 10-inch cast-iron skillet. Heat on medium-high heat and add the sliced onion and 1 teaspoon of salt. Saute until soft and caramelized, about 12 minutes. Remove the onions from the pan and set aside.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FFF629B2BFD03B4B94B8F2BEA8F8844C', X'900971B92BA67E40959429E8F2E9CF9E', X'900971B92BA67E40959429E8F2E9CF9E', 5, NULL, NULL, 2, NULL, 0, 'Whisk together 7 tablespoons of the butter and the roasted garlic, cornstarch, garlic powder, remaining 1/2 teaspoon salt, and black pepper in a large bowl.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F06B1C634209734A9D0015F6EDBD73DE', X'900971B92BA67E40959429E8F2E9CF9E', X'900971B92BA67E40959429E8F2E9CF9E', 6, NULL, NULL, 2, NULL, 0, 'Toss the potatoes with the butter and garlic mixture until they are completely coated.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1D76017A295D2C4B8DFE8390F579F646', X'900971B92BA67E40959429E8F2E9CF9E', X'900971B92BA67E40959429E8F2E9CF9E', 7, NULL, NULL, 2, NULL, 0, 'Add the remaining 1 tablespoon of butter to the same cast-iron skillet. Swirl the butter to coat the bottom of the pan completely.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D027A811716BA448A939712750F87F88', X'900971B92BA67E40959429E8F2E9CF9E', X'900971B92BA67E40959429E8F2E9CF9E', 8, NULL, NULL, 2, NULL, 0, 'Place one slice of potato in the center of you skillet, working in a circle, start adding more potato slices, slightly overlapping them until you have a full circle formed over the bottom of the pan.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'380343188E12904596A84CEB5BE21227', X'900971B92BA67E40959429E8F2E9CF9E', X'900971B92BA67E40959429E8F2E9CF9E', 9, NULL, NULL, 2, NULL, 0, 'Repeat with another circular layer of potatoes and then more cheese and onions. Continue until you have used all of your potatoes and cheese. Try to keep the layers as even as possible in thickness.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'81E8FE14C0FF6F49B54BFB1F669213B9', X'900971B92BA67E40959429E8F2E9CF9E', X'900971B92BA67E40959429E8F2E9CF9E', 10, NULL, NULL, 2, NULL, 0, 'Heat the skillet over medium-high heat for five minutes. Coat the top of the potatoes with nonstick cooking spray and place a piece of parchment paper or aluminum foil over the top so it completely covers the surface. Add another pan on top and weigh it down with beans, weights, or even bricks.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6FCCBD653B598D428E0747803C43EF48', X'900971B92BA67E40959429E8F2E9CF9E', X'900971B92BA67E40959429E8F2E9CF9E', 11, NULL, NULL, 2, NULL, 0, 'Place the skillet on the lowest rack of the oven and bake for 25 minutes.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'610041D2E1BC8E479786655AF8E203C1', X'900971B92BA67E40959429E8F2E9CF9E', X'900971B92BA67E40959429E8F2E9CF9E', 12, NULL, NULL, 2, NULL, 0, 'Remove the top pan, weights, and foil from the potatoes. Return the skillet to the oven and cook for another 15 minutes on a rack close to the top. The tops of the potatoes should be golden brown in color and then potatoes should feel tender when pricked with a fork.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C3D6577D58AFDC4DBBADC23DBB0CF0DB', X'900971B92BA67E40959429E8F2E9CF9E', X'900971B92BA67E40959429E8F2E9CF9E', 13, NULL, NULL, 2, NULL, 0, 'Place the skillet on the stovetop one more time. Heat to medium-high heat and cook for about 2 minutes, gently shaking the pan to release the sides of the galette.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D7D5437B8A89B24F9FAAF51B91F31344', X'900971B92BA67E40959429E8F2E9CF9E', X'900971B92BA67E40959429E8F2E9CF9E', 14, NULL, NULL, 2, NULL, 0, 'Sprinkle more cheese on top, cut and serve with rosemary, sour cream, and more cheese.');
