INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BF67540434F5E448945D03CE0F6F66F8', X'D53B25A6DB62294899A3A5B396A9FAA9', X'D53B25A6DB62294899A3A5B396A9FAA9', 1, NULL, NULL, 2, NULL, 0, 'Set aside the slices of bread. (They can even be left out overnight and will actually absorb more of the egg mixture if they are slightly stale.) Whisk together the eggs, whole milk, sugar, and vanilla extract in a medium-sized bowl. Pick a size that will fit the bread easily when you are ready to dip them.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'215C54CF0956AE4D9886DA19203B4381', X'D53B25A6DB62294899A3A5B396A9FAA9', X'D53B25A6DB62294899A3A5B396A9FAA9', 2, NULL, NULL, 2, NULL, 0, 'Take a slice of bread and smear it with about a fourth of the Nutella. Top that slice with another piece of bread. Repeat with the remaining slices until you have four Nutella sandwiches.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D48FBAD7AD6D4D4D991A64F358CC3F06', X'D53B25A6DB62294899A3A5B396A9FAA9', X'D53B25A6DB62294899A3A5B396A9FAA9', 3, NULL, NULL, 2, NULL, 0, 'Heat a griddle or large non-stick pan on medium-high heat and grease with the butter.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'22E574490BE5BC4D88F090DDDE4DA17E', X'D53B25A6DB62294899A3A5B396A9FAA9', X'D53B25A6DB62294899A3A5B396A9FAA9', 4, NULL, NULL, 2, NULL, 0, 'Dip each sandwich into the egg mixture, coating both sides of the sandwich completely. Dip again if desired.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'49DC5555D5BA22448AE6761F1EDEBE14', X'D53B25A6DB62294899A3A5B396A9FAA9', X'D53B25A6DB62294899A3A5B396A9FAA9', 5, NULL, NULL, 2, NULL, 0, 'Place the sandwiches on the griddle and turn the heat down to medium-low. Cook for about 2 minutes or until golden brown. Flip and cook for another two minutes on the other side until golden brown and all the egg has been cooked through.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5974F36CE4E0A848A93D84491E354079', X'D53B25A6DB62294899A3A5B396A9FAA9', X'D53B25A6DB62294899A3A5B396A9FAA9', 6, NULL, NULL, 2, NULL, 0, 'Top with whipped cream, powdered sugar, and raspberries.');
