INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'13CABBB21EC35040BD8B2A5CDA02D8C2', X'25FE79FBA3BC4B4C85379D96BEEE7379', X'25FE79FBA3BC4B4C85379D96BEEE7379', 6, NULL, NULL, 2, NULL, 0, 'Combine chicken, quartered strawberries, walnuts, red onion, and feta cheese in a large bowl. Toss liberally. Top onto bed of mixed greens.');
