INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4D8DBDCB6463364588BB951364A50B0A', X'BD1B517D9ABEAB48B1171C30604E57AC', X'BD1B517D9ABEAB48B1171C30604E57AC', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'80C3F8A226336943AED6DD72BED48F4D', X'BD1B517D9ABEAB48B1171C30604E57AC', X'BD1B517D9ABEAB48B1171C30604E57AC', 1, NULL, NULL, 2, NULL, 0, 'Whisk together the flour, sugar, baking powder, and salt in a medium-sized bowl.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8E85F186F4A04343A00B57F4E61849AC', X'BD1B517D9ABEAB48B1171C30604E57AC', X'BD1B517D9ABEAB48B1171C30604E57AC', 2, NULL, NULL, 2, NULL, 0, 'Whisk together the milk, melted butter, eggs, and vanilla extract in a measuring cup and then add it to the dry ingredients. Make sure the butter has cooled slightly before adding the egg so it doesn’t start to curdle.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9B3D8CFC8D464C45A58367ABB9FB2076', X'BD1B517D9ABEAB48B1171C30604E57AC', X'BD1B517D9ABEAB48B1171C30604E57AC', 3, NULL, NULL, 2, NULL, 0, 'Generously grease the bottom and sides of the Instant Pot insert with cooking spray, butter, or cooking oil. Set the Instant Pot to sauté and heat for 2 minutes.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F3EA844C2C85AA48B6AB87CD606CCA34', X'BD1B517D9ABEAB48B1171C30604E57AC', X'BD1B517D9ABEAB48B1171C30604E57AC', 4, NULL, NULL, 2, NULL, 0, 'Pour the batter into the Instant Pot and allow to cook for 3 minutes to set the bottom. This will prevent the pancake from sticking. Cover and set on the low setting for 40 minutes.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3C08994999B2E649BD7900B9577054F1', X'BD1B517D9ABEAB48B1171C30604E57AC', X'BD1B517D9ABEAB48B1171C30604E57AC', 5, NULL, NULL, 2, NULL, 0, 'Allow any steam to release (there will be little to no build up because of the low setting), open the Instant Pot, then check to make sure the center of the pancake comes out clean when you stick it with a toothpick.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0A1C885FCAA3CE4D9138A279C1D13FCE', X'BD1B517D9ABEAB48B1171C30604E57AC', X'BD1B517D9ABEAB48B1171C30604E57AC', 6, NULL, NULL, 2, NULL, 0, 'Press the sauté button and cook for another two minutes. Do not cook for any longer or the bottom of the pancake will burn.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'91C747572FA7494A98E89FB5864D389D', X'BD1B517D9ABEAB48B1171C30604E57AC', X'BD1B517D9ABEAB48B1171C30604E57AC', 7, NULL, NULL, 2, NULL, 0, 'Place a large plate over the top of the Instant Pot insert and carefully flip the pancake over onto the plate. Slice and serve with immediately hot maple syrup and butter.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'69D94373466624498A376792560095CC', X'F84705C29214314CB55344C8F4D1D676', X'F84705C29214314CB55344C8F4D1D676', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4F1BBEE35CC6BD429C1B4F24BE010135', X'F84705C29214314CB55344C8F4D1D676', X'F84705C29214314CB55344C8F4D1D676', 1, NULL, NULL, 2, NULL, 0, 'In a cocktail shaker filled with ice cubes, pour the gin (or Cognac), simple syrup, and lemon juice.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D9712A367A548C4B93351D5C9BD7B015', X'F84705C29214314CB55344C8F4D1D676', X'F84705C29214314CB55344C8F4D1D676', 2, NULL, NULL, 2, NULL, 0, 'Shake well.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5BB0F8A0A8A026499768710530A7FAB6', X'F84705C29214314CB55344C8F4D1D676', X'F84705C29214314CB55344C8F4D1D676', 3, NULL, NULL, 2, NULL, 0, 'Strain into a chilled Champagne flute that is at least half full of ice.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3B1349A2AA190E41B5659EDB3DDF6103', X'F84705C29214314CB55344C8F4D1D676', X'F84705C29214314CB55344C8F4D1D676', 4, NULL, NULL, 2, NULL, 0, 'Slowly fill with Champagne.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DA9E9983A18B194A908EDC0D65354AC0', X'5E89F93F7B34504D90073CF26563008F', X'5E89F93F7B34504D90073CF26563008F', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2D65CE683ECCEB42A4A65E5C02C86102', X'5E89F93F7B34504D90073CF26563008F', X'5E89F93F7B34504D90073CF26563008F', 1, NULL, NULL, 2, NULL, 0, 'Use a sharp knife to slice off the woody ends of the asparagus and any tough parts on the broccolini stems. Place the trimmed veggies into a large bowl. Drizzle with the extra virgin olive oil and a light sprinkle of salt.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'39D1810131555144A4A48676F8609C35', X'5E89F93F7B34504D90073CF26563008F', X'5E89F93F7B34504D90073CF26563008F', 2, NULL, NULL, 2, NULL, 0, 'Heat a griddle pan on the stove over medium heat. Add the asparagus and the broccolini and grill for 6 minutes, turning the vegetables frequently, to make sure they don''t scorch. Once they are cooked through, but still firm, remove from the pan and leave to drain and cool on paper towels.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'286353E291677440B342A83E6819FFF2', X'5E89F93F7B34504D90073CF26563008F', X'5E89F93F7B34504D90073CF26563008F', 3, NULL, NULL, 2, NULL, 0, 'Meanwhile, make the dressing. Cut the avocado in half, remove the pit, and scoop out the flesh into a food processor or blender. Add the sour cream, lime juice, cilantro, and a tiny pinch of salt. Blitz to create a silky dressing. If it feels thick or stiff, add a little water to loosen it.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7479095A74808A4DB37397190C843B10', X'5E89F93F7B34504D90073CF26563008F', X'5E89F93F7B34504D90073CF26563008F', 4, NULL, NULL, 2, NULL, 0, 'Cook your fresh or frozen peas by either steaming or dropping into lightly boiling water for a few minutes, taking care not to overcook—they should remain firm and bright green.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EFC0673D4D0D444D8981688E5A053534', X'5E89F93F7B34504D90073CF26563008F', X'5E89F93F7B34504D90073CF26563008F', 5, NULL, NULL, 2, NULL, 0, 'To assemble the salad, pile the asparagus and broccolini into the center of the plate. Sprinkle with the peas and the crumbled feta. Drizzle the dressing on or around the salad or serve on the side.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8BEE7BDEC3DEAD4B9A25587E251515C0', X'0DDB057DC83DB24C91E277DBCE4DCFDE', X'0DDB057DC83DB24C91E277DBCE4DCFDE', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5E368B5C827A5B45AF175D8696D786D4', X'0DDB057DC83DB24C91E277DBCE4DCFDE', X'0DDB057DC83DB24C91E277DBCE4DCFDE', 1, NULL, NULL, 2, NULL, 0, 'Build the tomato juice and lemon juice into a highball glass over ice cubes.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BEB7D39A68C5ED4692B2EC7A2DCEFD08', X'0DDB057DC83DB24C91E277DBCE4DCFDE', X'0DDB057DC83DB24C91E277DBCE4DCFDE', 2, NULL, NULL, 2, NULL, 0, 'Mix well.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3C0C9961C4B4B84F945AACEBBAF692BF', X'0DDB057DC83DB24C91E277DBCE4DCFDE', X'0DDB057DC83DB24C91E277DBCE4DCFDE', 3, NULL, NULL, 2, NULL, 0, 'Add the Worcester sauce, salt, pepper, and hot sauce to taste.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8FE2E301ACCBC54BBFA4A2AFD1589EBF', X'0DDB057DC83DB24C91E277DBCE4DCFDE', X'0DDB057DC83DB24C91E277DBCE4DCFDE', 4, NULL, NULL, 2, NULL, 0, 'Garnish with the celery stalk or pickle spear if using.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A8EF9BF680AF6A448F5E147CA9511A6E', X'0DDB057DC83DB24C91E277DBCE4DCFDE', X'0DDB057DC83DB24C91E277DBCE4DCFDE', 5, NULL, NULL, 2, NULL, 0, 'Enjoy.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FAA429BFA7A7A143B06AEDEAC78E05B5', X'0115BE8FEB304F4BA4D0C39190A1084C', X'0115BE8FEB304F4BA4D0C39190A1084C', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients. Heat the oven to 350 F. Grease an 8-inch square baking pan and set aside.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5117BC9C69DE7F409D737C26460657F4', X'0115BE8FEB304F4BA4D0C39190A1084C', X'0115BE8FEB304F4BA4D0C39190A1084C', 1, NULL, NULL, 2, NULL, 0, 'In a large bowl, combine the melted butter, sugar and vanilla extract.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'176B67163DF439438A14CDF18BF5F00C', X'0115BE8FEB304F4BA4D0C39190A1084C', X'0115BE8FEB304F4BA4D0C39190A1084C', 2, NULL, NULL, 2, NULL, 0, 'Add the eggs and beat well to combine.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'637B766BA4CD8E4DA24CA4733EECE4EB', X'0115BE8FEB304F4BA4D0C39190A1084C', X'0115BE8FEB304F4BA4D0C39190A1084C', 3, NULL, NULL, 2, NULL, 0, 'In another large bowl combine the flour, unsweetened cocoa powder, baking powder, and salt.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B442B41C38B213449395375A71A99C15', X'0115BE8FEB304F4BA4D0C39190A1084C', X'0115BE8FEB304F4BA4D0C39190A1084C', 4, NULL, NULL, 2, NULL, 0, 'Gradually add the flour mixture to the egg mixture, beating until well blended.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6CEC9947F341C842B0F55939BF93A447', X'0115BE8FEB304F4BA4D0C39190A1084C', X'0115BE8FEB304F4BA4D0C39190A1084C', 5, NULL, NULL, 2, NULL, 0, 'Spread the brownie batter evenly into the prepared baking pan.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E54BA95C1C733D4097367087B35F916C', X'0115BE8FEB304F4BA4D0C39190A1084C', X'0115BE8FEB304F4BA4D0C39190A1084C', 6, NULL, NULL, 2, NULL, 0, 'Sprinkle the chopped fresh strawberries evenly on top of the batter. Bake for 40 to 45 minutes or until brownies begin to pull away from sides of pan.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AAC95FB5BF4B29408DB545A7ED466AC6', X'0115BE8FEB304F4BA4D0C39190A1084C', X'0115BE8FEB304F4BA4D0C39190A1084C', 7, NULL, NULL, 2, NULL, 0, 'Cool completely in the pan on a wire rack.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D06F28D45EE90444B27A91FF503C47D0', X'0115BE8FEB304F4BA4D0C39190A1084C', X'0115BE8FEB304F4BA4D0C39190A1084C', 8, NULL, NULL, 2, NULL, 0, 'Once cooled, melt the white chocolate and drizzle over the brownies.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EC832B0022B21F4CB487594695FCECF1', X'1B76A3A580E3E149AD6EA3EF33E7951F', X'1B76A3A580E3E149AD6EA3EF33E7951F', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'536DE01CE12AA2498108E47CE573A946', X'1B76A3A580E3E149AD6EA3EF33E7951F', X'1B76A3A580E3E149AD6EA3EF33E7951F', 1, NULL, NULL, 2, NULL, 0, 'Warm the milk to between 105 F and 115 F. In a small bowl, combine the warm milk, yeast, and 1 teaspoon of the sugar, stirring to dissolve yeast. Let stand 5 minutes to allow it to foam.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'972F44084C8B794CB7839BCC4D9C8043', X'1B76A3A580E3E149AD6EA3EF33E7951F', X'1B76A3A580E3E149AD6EA3EF33E7951F', 2, NULL, NULL, 2, NULL, 0, 'In the bowl of a stand mixer fitted with the dough hook attachment, combine the flour, 1/4 cup of the butter, salt, orange zest, eggs, and the remaining 1/4 cup sugar in bowl of heavy-duty electric stand mixer. Add the milk mixture and beat at low speed 1 to 2 minutes or until blended. Increase speed to medium-low and beat 5 minutes or until the dough is smooth. The dough will be soft and slightly sticky.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F20730D262555F49A77BD445E74F6F4F', X'1B76A3A580E3E149AD6EA3EF33E7951F', X'1B76A3A580E3E149AD6EA3EF33E7951F', 3, NULL, NULL, 2, NULL, 0, 'Place the dough in a greased bowl, turning over once to grease the top. Cover and let rise in a warm place, free from drafts, 45 minutes to 1 hour or until doubled in bulk.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3EBB6CB83D366C44B3738E589D93A46E', X'1B76A3A580E3E149AD6EA3EF33E7951F', X'1B76A3A580E3E149AD6EA3EF33E7951F', 4, NULL, NULL, 2, NULL, 0, 'Meanwhile, make the filling. In a small bowl, combine the orange marmalade, 3/4 cup sugar, and 1/3 cup melted butter.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3E70F2931F56AB478ACE028488B52635', X'1B76A3A580E3E149AD6EA3EF33E7951F', X'1B76A3A580E3E149AD6EA3EF33E7951F', 5, NULL, NULL, 2, NULL, 0, 'When the dough is ready, turn out on a lightly floured surface. Roll into a 15 x 12-inch rectangle.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AEE4265E6130F84A8B905AC6CF7480A7', X'1B76A3A580E3E149AD6EA3EF33E7951F', X'1B76A3A580E3E149AD6EA3EF33E7951F', 6, NULL, NULL, 2, NULL, 0, 'Spread the marmalade mixture over the dough, leaving a 1/2-inch border.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EEFF74D8FFED3A43986B46FFA35217CF', X'1B76A3A580E3E149AD6EA3EF33E7951F', X'1B76A3A580E3E149AD6EA3EF33E7951F', 7, NULL, NULL, 2, NULL, 0, 'Roll up, jelly-roll style, starting at the long side.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'97E1E2C9257FA1489B73D920E3958F97', X'1B76A3A580E3E149AD6EA3EF33E7951F', X'1B76A3A580E3E149AD6EA3EF33E7951F', 8, NULL, NULL, 2, NULL, 0, 'Cut the dough into 12 slices, and place in a well-greased 13 x 9-inch pan. To create a clean cut without compressing the individual rolls, slice each piece using unflavored dental floss rather than a knife. First, score the roll to ensure evenly-sized orange rolls. Slide a long piece of dental floss under the log of dough. When you have the floss where you’d like to make the cut, cross the two ends over the top of the dough and pull them in opposite directions, slicing through the dough.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D1D41FE992F0EC42A2F5EF5D6DD6BB0F', X'1B76A3A580E3E149AD6EA3EF33E7951F', X'1B76A3A580E3E149AD6EA3EF33E7951F', 9, NULL, NULL, 2, NULL, 0, 'Cover and let rise 45 minutes or until almost doubled in bulk.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A71093FE99502E4097C4962B3A1B8BFD', X'1B76A3A580E3E149AD6EA3EF33E7951F', X'1B76A3A580E3E149AD6EA3EF33E7951F', 10, NULL, NULL, 2, NULL, 0, 'While the rolls are baking, make the glaze. In a large bowl, combine the cream cheese and butter until light and fluffy.');
