INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D20AC22116D1B84C8020202013DC947F', X'9BCA70F6A2FED34EA8978B1837C568E5', X'9BCA70F6A2FED34EA8978B1837C568E5', 1, NULL, NULL, 2, NULL, 0, 'Build the liquid ingredients in a highball glass over ice cubes. Start by pouring the vodka, and then add the tomato juice, lemon juice, and the dash of Worcheshire sauce.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9E0359D2319C0943ADBA3570BB3E12A1', X'9BCA70F6A2FED34EA8978B1837C568E5', X'9BCA70F6A2FED34EA8978B1837C568E5', 2, NULL, NULL, 2, NULL, 0, 'Add the celery salt, pepper, and hot sauce to taste. If using, add the horseradish.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0A5194AA7E56DD4AAD456DE521F4A6D0', X'9BCA70F6A2FED34EA8978B1837C568E5', X'9BCA70F6A2FED34EA8978B1837C568E5', 3, NULL, NULL, 2, NULL, 0, 'Stir well, or try rolling the drink.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6FB6D76E60D8D547A237B800706F4DE0', X'9BCA70F6A2FED34EA8978B1837C568E5', X'9BCA70F6A2FED34EA8978B1837C568E5', 4, NULL, NULL, 2, NULL, 0, 'Taste and adjust the seasonings to your liking.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1DC8E8414D317F448CA221AD848E1877', X'9BCA70F6A2FED34EA8978B1837C568E5', X'9BCA70F6A2FED34EA8978B1837C568E5', 5, NULL, NULL, 2, NULL, 0, 'Garnish with a lemon wedge and celery stalk or pickle.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6A6CCC413829FC4E874BD92849F564CC', X'9BCA70F6A2FED34EA8978B1837C568E5', X'9BCA70F6A2FED34EA8978B1837C568E5', 6, NULL, NULL, 2, NULL, 0, 'Serve and enjoy.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1DA5D86FA2874940A83FE4602B662F50', X'035265084B51144E96EBA5057213EE4B', X'035265084B51144E96EBA5057213EE4B', 0, NULL, NULL, 2, NULL, 0, 'Preheat an oven to 350 F.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3C35F657C771B94BB2501A627DB740CC', X'035265084B51144E96EBA5057213EE4B', X'035265084B51144E96EBA5057213EE4B', 1, NULL, NULL, 2, NULL, 0, 'Trim the leek: cut off and discard the root end and the dark green stems (reserve the stems to use in broth or stock if that''s how you roll). Cut the remaining leek in half lengthwise, then thinly slice crosswise. Put the sliced leek in a bowl and cover with cool water. Swish around to loosen any dirt or grit. Let sit while you attend to the asparagus.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A74E018F7C13D747853F6164381AF829', X'035265084B51144E96EBA5057213EE4B', X'035265084B51144E96EBA5057213EE4B', 2, NULL, NULL, 2, NULL, 0, 'Trim off and discard the tough ends of the asparagus spears. Slice the spears into 1/2-inch pieces.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1B3F402292FC89439913995BD8DEAA73', X'035265084B51144E96EBA5057213EE4B', X'035265084B51144E96EBA5057213EE4B', 3, NULL, NULL, 2, NULL, 0, 'In a large frying pan over medium-high heat, melt the butter. Lift the sliced leeks out of the water and put in the pan (don''t drain the leeks into a colander—you''ll dump all the dirt back on them!). Sprinkle with 1/2 teaspoon of the salt and cook, stirring frequently, until tender, about 3 minutes.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'30894EB8E846C94F9F89B462883003E1', X'035265084B51144E96EBA5057213EE4B', X'035265084B51144E96EBA5057213EE4B', 4, NULL, NULL, 2, NULL, 0, 'Add the asparagus. Cook, stirring frequently, until just tender, 2 to 3 minutes. Set aside.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5D6D610640DFDD47A61BC9B8F4CCC100', X'035265084B51144E96EBA5057213EE4B', X'035265084B51144E96EBA5057213EE4B', 5, NULL, NULL, 2, NULL, 0, 'In a medium bowl, whisk the eggs and egg yolks together.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B6921847126EDA4C8D2FB12B452207C4', X'035265084B51144E96EBA5057213EE4B', X'035265084B51144E96EBA5057213EE4B', 6, NULL, NULL, 2, NULL, 0, 'Brush some of the egg onto the pie crust just to coat it. Bake for 5 minutes. Let cool to room temperature.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'67A961288E2DD7419EED33C5C07B10CF', X'035265084B51144E96EBA5057213EE4B', X'035265084B51144E96EBA5057213EE4B', 7, NULL, NULL, 2, NULL, 0, 'While the crust bakes, whisk the milk into the eggs. Whisk in the remaining 1/4 teaspoon salt.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4ED5E4961CA6A5488FC548231FD392A6', X'035265084B51144E96EBA5057213EE4B', X'035265084B51144E96EBA5057213EE4B', 8, NULL, NULL, 2, NULL, 0, 'Set the pie crust on a baking sheet for easy handling. Spread the leek and asparagus mixture in the pie crust. Sprinkle with black pepper. Layer on the grated cheese. Pour the egg-milk mixture evenly over everything.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A1706E8DD244184F9B860D395E7F67BC', X'035265084B51144E96EBA5057213EE4B', X'035265084B51144E96EBA5057213EE4B', 9, NULL, NULL, 2, NULL, 0, 'Bake until just set (the very middle can be just a tad jiggly), about 35 minutes.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0454379BB6BB654290F4F016085DD90C', X'035265084B51144E96EBA5057213EE4B', X'035265084B51144E96EBA5057213EE4B', 10, NULL, NULL, 2, NULL, 0, 'Let the quiche sit at least 10 minutes before slicing. Serve warm, at room temperature, or even chilled the next day.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2EA14EC372C3C94B815D66A9FD489436', X'2136208F574DBA44A1CB634295FA7664', X'2136208F574DBA44A1CB634295FA7664', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BC84AF005641FA47A896DCCE0A032A4A', X'2136208F574DBA44A1CB634295FA7664', X'2136208F574DBA44A1CB634295FA7664', 1, NULL, NULL, 2, NULL, 0, 'Fill a highball glass with ice and orange juice.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'74FECED9FBC1044AAEB2BD838DEA8562', X'2136208F574DBA44A1CB634295FA7664', X'2136208F574DBA44A1CB634295FA7664', 2, NULL, NULL, 2, NULL, 0, 'Slowly pour grenadine over the juice. It will sink to the bottom and slowly rise to the top as you drink.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FC9A4D94C122CE4982808A88AC96663D', X'2136208F574DBA44A1CB634295FA7664', X'2136208F574DBA44A1CB634295FA7664', 3, NULL, NULL, 2, NULL, 0, 'Garnish with an orange slice.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FF9963029861C749A91858EC7F290410', X'2136208F574DBA44A1CB634295FA7664', X'2136208F574DBA44A1CB634295FA7664', 4, NULL, NULL, 2, NULL, 0, 'Serve and enjoy!');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9CFAE3F7D0B3284495F5B5A60EAC0A8B', X'AC7518BFECC7454A8F8582663AC4E590', X'AC7518BFECC7454A8F8582663AC4E590', 0, NULL, NULL, 2, NULL, 0, 'To make shortbread tails you will need a 23cm (9`) round tin, a fluted tin gives the shortbread a lovely crimped edge but don''t feel you have to use one. Grease with a little of the butter from the recipe.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E766122A46F4B345859CC6AF6F16ADF2', X'AC7518BFECC7454A8F8582663AC4E590', X'AC7518BFECC7454A8F8582663AC4E590', 1, NULL, NULL, 2, NULL, 0, 'Heat the oven to 325 F/160 C/Gas Mark 3.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6F0AB6185FCC024D8FA2F6EB384E52DF', X'AC7518BFECC7454A8F8582663AC4E590', X'AC7518BFECC7454A8F8582663AC4E590', 2, NULL, NULL, 2, NULL, 0, 'In a large baking bowl, place the butter, the sugar, salt, and the lavender drops.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'93EA8E2CECBB4C48A236722A31E5977A', X'AC7518BFECC7454A8F8582663AC4E590', X'AC7518BFECC7454A8F8582663AC4E590', 3, NULL, NULL, 2, NULL, 0, 'Beat thoroughly using an electric whisk until the mixture is soft, light and creamy.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'27F4861E5C13754AB1ECEFB46D7F5214', X'AC7518BFECC7454A8F8582663AC4E590', X'AC7518BFECC7454A8F8582663AC4E590', 4, NULL, NULL, 2, NULL, 0, 'Add the lemon rind and give the mixture and quick mix.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'54320217EEC0BA4684722E3D5C67D340', X'AC7518BFECC7454A8F8582663AC4E590', X'AC7518BFECC7454A8F8582663AC4E590', 5, NULL, NULL, 2, NULL, 0, 'Sieve in the flour, followed by the cornflour (starch), stir through until completely incorporated.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E11BC69F7F554E47AFE7F909D0D91CEA', X'AC7518BFECC7454A8F8582663AC4E590', X'AC7518BFECC7454A8F8582663AC4E590', 6, NULL, NULL, 2, NULL, 0, 'Press the biscuit mixture into the tin making sure it is into the edges and even on the top.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'17EE51516A6A8F4C91B745C5623BD296', X'AC7518BFECC7454A8F8582663AC4E590', X'AC7518BFECC7454A8F8582663AC4E590', 7, NULL, NULL, 2, NULL, 0, 'Sprinkle with a little caster sugar and bake for 30 to 35 minutes until the shortbread is light, golden brown.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'55FE21A1A604F540A7476A1CD5FAB752', X'AC7518BFECC7454A8F8582663AC4E590', X'AC7518BFECC7454A8F8582663AC4E590', 8, NULL, NULL, 2, NULL, 0, 'Remove the cake from the oven and place onto a cooling tray.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'689D27F7A85C7347B61EA2BEAB230E89', X'AC7518BFECC7454A8F8582663AC4E590', X'AC7518BFECC7454A8F8582663AC4E590', 9, NULL, NULL, 2, NULL, 0, 'Using a sharp knife, mark the shortbread into 12 wedges and leave to cool completely in the tin.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DF004027899FFE4E9CC030B0598303F2', X'AC7518BFECC7454A8F8582663AC4E590', X'AC7518BFECC7454A8F8582663AC4E590', 10, NULL, NULL, 2, NULL, 0, 'Once completely cold, carefully remove the wedges from the tin and store in an airtight tin.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1D7FE5E5A8DBED439C4E2E4E59514129', X'AC7518BFECC7454A8F8582663AC4E590', X'AC7518BFECC7454A8F8582663AC4E590', 11, NULL, NULL, 2, NULL, 0, 'The shortbread keeps well for several days.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'513F2CDAE0957945B7AF4A7E0CC5EA12', X'AC7518BFECC7454A8F8582663AC4E590', X'AC7518BFECC7454A8F8582663AC4E590', 12, NULL, NULL, 2, NULL, 0, 'Serve alongside fresh summer fruits, and whipped cream, a trifle, or put your feet up and enjoy with a nice cup of tea.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D8885FB7E18BFD4EB13D990D8F867C00', X'B8AAC5A93C46FE43B2A9FDC941922ED6', X'B8AAC5A93C46FE43B2A9FDC941922ED6', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
