INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E3E6EA725B662C45A051B7B2B8A1FC26', X'F36DFCC41EA02B42898001A53015602D', X'F36DFCC41EA02B42898001A53015602D', 1, NULL, NULL, 2, NULL, 0, 'Heat the oven to 350 F.');
