INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A0691CCB0FAC0B42A82392E33971EC76', X'F36DFCC41EA02B42898001A53015602D', X'F36DFCC41EA02B42898001A53015602D', 3, NULL, NULL, 2, NULL, 0, 'Divide the almond paste into three portions and take one portion and roll it into a round the size of the cake tin.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E4F2FD0CC09C2D488C7D11CB6622FBAB', X'F36DFCC41EA02B42898001A53015602D', X'F36DFCC41EA02B42898001A53015602D', 4, NULL, NULL, 2, NULL, 0, 'In a large, roomy baking bowl, mix the mixed dried fruits and candied peel with the flour, salt, and spices.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BB34A6C48951F8458A5DFD1562EF18CA', X'F36DFCC41EA02B42898001A53015602D', X'F36DFCC41EA02B42898001A53015602D', 5, NULL, NULL, 2, NULL, 0, 'In another large bowl, cream the butter with the sugar, until light and fluffy.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A2876E267C689A49A26CC2D89AFFCC9C', X'F36DFCC41EA02B42898001A53015602D', X'F36DFCC41EA02B42898001A53015602D', 6, NULL, NULL, 2, NULL, 0, 'Add the beaten egg to the butter mixture a little bit at a time, beating well after each addition — don''t try to rush this process as the batter could curdle. If it does, add a tablespoon of the flour and mix again, this should bring the mixture back together.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'68EB7CE256918843BA3BC88E10DABBC7', X'F36DFCC41EA02B42898001A53015602D', X'F36DFCC41EA02B42898001A53015602D', 7, NULL, NULL, 2, NULL, 0, 'Carefully fold in half the flour and half the fruit into the egg and butter mixture, once incorporated repeat with the remaining flour and fruit.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7D3D000B104D9B40BED9A492D708887E', X'F36DFCC41EA02B42898001A53015602D', X'F36DFCC41EA02B42898001A53015602D', 8, NULL, NULL, 2, NULL, 0, 'Put half of the cake mixture into the tin, smooth and cover with the round of almond paste.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'43E25CECD5FA2E46972663FF1D831C7B', X'F36DFCC41EA02B42898001A53015602D', X'F36DFCC41EA02B42898001A53015602D', 9, NULL, NULL, 2, NULL, 0, 'Put the remaining cake mixture into the tin and smooth the surface carefully.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D48B343FD4BB064785862014F6E84AA8', X'F36DFCC41EA02B42898001A53015602D', X'F36DFCC41EA02B42898001A53015602D', 10, NULL, NULL, 2, NULL, 0, 'Bake in the center of the oven for 1 hour.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EA5A9D18F43B35408EE3BFE214137419', X'F36DFCC41EA02B42898001A53015602D', X'F36DFCC41EA02B42898001A53015602D', 11, NULL, NULL, 2, NULL, 0, 'Lower the heat to 300 F and bake for 1 1/2 hours more or (depending on your oven), until the cake is golden brown. To check if the cake is cooked, insert a skewer into the cake, it should come out clean and dry. If it is, remove from the oven and leave to cool in the tin. If not, cook it a little longer.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'39E94D0B55E9E64085A1D29A9CCB7316', X'F36DFCC41EA02B42898001A53015602D', X'F36DFCC41EA02B42898001A53015602D', 12, NULL, NULL, 2, NULL, 0, 'Roll another third of almond paste, again to the size of the tin. Make the remaining almond paste equally into 11 tiny balls.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8B5CE1997156254E876211E77514D95D', X'F36DFCC41EA02B42898001A53015602D', X'F36DFCC41EA02B42898001A53015602D', 13, NULL, NULL, 2, NULL, 0, 'Once the cake is completely cold, remove from the tin and brush the top of the cake with apricot jam and cover with the disc of almond paste.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DC1668C8931BAF4FB878D935AC63A2F4', X'F36DFCC41EA02B42898001A53015602D', X'F36DFCC41EA02B42898001A53015602D', 14, NULL, NULL, 2, NULL, 0, 'Place the 11 tiny balls evenly around the edge. Brush them all over with a little apricot jam.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A9B1563475F2244BAFDCAC8CFA887286', X'F36DFCC41EA02B42898001A53015602D', X'F36DFCC41EA02B42898001A53015602D', 15, NULL, NULL, 2, NULL, 0, 'Place under a hot grill until lightly browned.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F351A68038D99B42B6185DC1C6351D7E', X'F36DFCC41EA02B42898001A53015602D', X'F36DFCC41EA02B42898001A53015602D', 16, NULL, NULL, 2, NULL, 0, 'Decorate the cake as you wish with tiny Easter eggs or any other Easter theme.');
