INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4973A042DE3A054F9558428A15CB14A3', X'4C0155E5BF8A694399D676C52FE6228C', X'4C0155E5BF8A694399D676C52FE6228C', 1, NULL, NULL, 2, NULL, 0, 'Heat the oven to 425 F/220 C/Gas 7. When the oven is nearly preheated, put the butter in a 2-quart baking dish and put it in the oven to melt. Watch carefully to make sure the butter doesn''t become too brown. It should be hot and begin foamy, but not browned.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8DD5C1E3FB3CDE4A98691F7ED9B2A612', X'4C0155E5BF8A694399D676C52FE6228C', X'4C0155E5BF8A694399D676C52FE6228C', 2, NULL, NULL, 2, NULL, 0, 'In a small bowl, combine the flour, sugar, and salt. Mix well to blend ingredients.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0C97FA66DCBEEC40ADA86C7B0C045601', X'4C0155E5BF8A694399D676C52FE6228C', X'4C0155E5BF8A694399D676C52FE6228C', 3, NULL, NULL, 2, NULL, 0, 'In a medium mixing bowl, whisk the milk and eggs together until well blended.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4E94184C81F7D84D81DCB469E603F4EB', X'4C0155E5BF8A694399D676C52FE6228C', X'4C0155E5BF8A694399D676C52FE6228C', 4, NULL, NULL, 2, NULL, 0, 'Whisk the flour mixture into the milk and egg mixture. Beat with the whisk until the batter is smooth.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'852B6F3B572C5B429E68DF763BABFF30', X'4C0155E5BF8A694399D676C52FE6228C', X'4C0155E5BF8A694399D676C52FE6228C', 5, NULL, NULL, 2, NULL, 0, 'Remove the baking dish with the hot butter from the oven and place it on a rack. Pour the batter into the pan.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B124FDD9D92B3F45AE5E5F777CC96E01', X'4C0155E5BF8A694399D676C52FE6228C', X'4C0155E5BF8A694399D676C52FE6228C', 6, NULL, NULL, 2, NULL, 0, 'Sprinkle blueberries over the batter.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7A603C9143A80C488971243BF1F564F0', X'4C0155E5BF8A694399D676C52FE6228C', X'4C0155E5BF8A694399D676C52FE6228C', 7, NULL, NULL, 2, NULL, 0, 'Bake for 14 to 17 minutes, until the pancake, is puffed and golden brown.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5B00D1386DC1BB4EBE51E1BF556654DD', X'4C0155E5BF8A694399D676C52FE6228C', X'4C0155E5BF8A694399D676C52FE6228C', 8, NULL, NULL, 2, NULL, 0, 'Sift powdered sugar over the pancake and serve with more butter and maple syrup or fruit syrup.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9AF1544B71B68541A69FE5696E67AEF2', X'EF5F84341277644E905EB3CA0A6C084C', X'EF5F84341277644E905EB3CA0A6C084C', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A1CACA7111CD4A46964CE97EDD64D80C', X'EF5F84341277644E905EB3CA0A6C084C', X'EF5F84341277644E905EB3CA0A6C084C', 1, NULL, NULL, 2, NULL, 0, 'Tear the bread into bite-size pieces to equal 6 cups, firmly packed.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'202EDA1372EA734288A50DB42AADB640', X'EF5F84341277644E905EB3CA0A6C084C', X'EF5F84341277644E905EB3CA0A6C084C', 2, NULL, NULL, 2, NULL, 0, 'Combine the eggs, whipping cream, half-and-half, onion, Mexican cheese, Monterey Jack cheese, chilies, and pimiento in a large bowl.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E549F59D5B07D94C9B1E7037B29721E6', X'EF5F84341277644E905EB3CA0A6C084C', X'EF5F84341277644E905EB3CA0A6C084C', 3, NULL, NULL, 2, NULL, 0, 'Gently stir in the bread pieces.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8F4510E0C63D0E449B6FECD0625592FC', X'EF5F84341277644E905EB3CA0A6C084C', X'EF5F84341277644E905EB3CA0A6C084C', 4, NULL, NULL, 2, NULL, 0, 'Pour the bread mixture into lightly greased 9-inch-deep pie plates.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F64FA456FBE8944C992B1ADA7439BCDC', X'EF5F84341277644E905EB3CA0A6C084C', X'EF5F84341277644E905EB3CA0A6C084C', 5, NULL, NULL, 2, NULL, 0, 'Bake at 425 F for 20 to 22 minutes or until browned and set.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'50C297A2B40879499059B51CE3AABD14', X'EF5F84341277644E905EB3CA0A6C084C', X'EF5F84341277644E905EB3CA0A6C084C', 6, NULL, NULL, 2, NULL, 0, 'Serve immediately.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'522E953BAFB7504E915C9B96C6BC29C5', X'6CF43F32093A874D9DC7354557281B1A', X'6CF43F32093A874D9DC7354557281B1A', 0, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 350 F.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B7DE2D60B2BF634C84C451F445A91469', X'6CF43F32093A874D9DC7354557281B1A', X'6CF43F32093A874D9DC7354557281B1A', 1, NULL, NULL, 2, NULL, 0, 'In a large mixing bowl, mix the eggs, cheese, parsley, salt, and pepper. Set aside.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CAD7C1B746A56A48A84921C977557C03', X'6CF43F32093A874D9DC7354557281B1A', X'6CF43F32093A874D9DC7354557281B1A', 2, NULL, NULL, 2, NULL, 0, 'Heat the olive oil in a large, heavy-bottomed pot or Dutch oven over medium heat. Add the lamb and onion and cook, stirring occasionally, until meat is browned and about half cooked about 5 minutes.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'625710104DD82B4791D9F75F373644FC', X'6CF43F32093A874D9DC7354557281B1A', X'6CF43F32093A874D9DC7354557281B1A', 3, NULL, NULL, 2, NULL, 0, 'Stir in the tomatoes and vegetable and continue to cook for another minute.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F24B7CD55CF9C54982DA9D9175B1F227', X'6CF43F32093A874D9DC7354557281B1A', X'6CF43F32093A874D9DC7354557281B1A', 4, NULL, NULL, 2, NULL, 0, 'Transfer the lamb and vegetable mixture to a baking pan (you can also use a large cast-iron skillet) large enough to hold it (and the eggs), and pour the egg mixture evenly over the top.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8D8182DD600D5243B18F7571CBF2F8D6', X'6CF43F32093A874D9DC7354557281B1A', X'6CF43F32093A874D9DC7354557281B1A', 5, NULL, NULL, 2, NULL, 0, 'Shake the pan a bit to distribute the eggs evenly through all the nooks and crannies, then bake just until set, about 10 minutes.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'298A513645A5E04A99A9D42482E8BF01', X'6CF43F32093A874D9DC7354557281B1A', X'6CF43F32093A874D9DC7354557281B1A', 6, NULL, NULL, 2, NULL, 0, 'Serve hot or at room temperature.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D4BAEFCF0A60904C859B6FA4EC29220C', X'229D57C9A76E3F41B55D07837B28869E', X'229D57C9A76E3F41B55D07837B28869E', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'057175643776D04CAFEEE7E3A07D8121', X'229D57C9A76E3F41B55D07837B28869E', X'229D57C9A76E3F41B55D07837B28869E', 1, NULL, NULL, 2, NULL, 0, 'Make the chimichurri sauce first. Whisk together the fresh parsley, cilantro, olive oil, lime juice, garlic, a teaspoon of the salt, and the crushed red pepper flakes in a small bowl. You can also process this sauce in the food processor for a finer minced sauce. You can do this step up to a day ahead of time. The longer it sits, the more flavorful it will get.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9A72424CB705D441909C839F9DEB6156', X'229D57C9A76E3F41B55D07837B28869E', X'229D57C9A76E3F41B55D07837B28869E', 2, NULL, NULL, 2, NULL, 0, 'Generously salt and pepper both sides of the steak.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C1BF87CC2DAEE44ABA582CB93728C9D4', X'229D57C9A76E3F41B55D07837B28869E', X'229D57C9A76E3F41B55D07837B28869E', 3, NULL, NULL, 2, NULL, 0, 'Heat a cast iron skillet or another heavy-bottomed pan on high heat. Once hot, add a tablespoon of butter to the pan and add the steak. Sear on high heat for 3 minutes. Do not move until the three minutes are up. This will guarantee good color and an even sear.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'16CFE77381A16C4B92222FFE9320416C', X'229D57C9A76E3F41B55D07837B28869E', X'229D57C9A76E3F41B55D07837B28869E', 4, NULL, NULL, 2, NULL, 0, 'Flip the steak and add the other tablespoon of butter to the pan and crack the two eggs onto the other side of the pan. Generously season the eggs with salt and pepper too. You can fry them in a different pan if you wish.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'38B4BE42C358594482DADF6C51CEE0C8', X'229D57C9A76E3F41B55D07837B28869E', X'229D57C9A76E3F41B55D07837B28869E', 5, NULL, NULL, 2, NULL, 0, 'Cook the steak and the eggs another 3 minutes for medium rare. You’ll know the eggs are done when the whites are set and the yolks are still jiggly. Heat for longer if you want a more well done steak. You can also flip the eggs if you like them over easy.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FA8BB0A4C0FCE744A8891AA0F489F59F', X'229D57C9A76E3F41B55D07837B28869E', X'229D57C9A76E3F41B55D07837B28869E', 6, NULL, NULL, 2, NULL, 0, 'Top the steak with the chimichurri sauce and serve with a little hot sauce if you like. Wait a few minutes to allow the steak to rest before slicing. Enjoy!');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A45E574EB078F6498C030E73CEF481DB', X'A8A97331F2821D4BA37065CE49463BD1', X'A8A97331F2821D4BA37065CE49463BD1', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6A4E02ED4A50C54B938AA4B160676229', X'A8A97331F2821D4BA37065CE49463BD1', X'A8A97331F2821D4BA37065CE49463BD1', 1, NULL, NULL, 2, NULL, 0, 'Roughly chop the tomatoes, shallot, and chile (if using).');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3E7676D072F7F14081F9D72CDF0BA270', X'A8A97331F2821D4BA37065CE49463BD1', X'A8A97331F2821D4BA37065CE49463BD1', 2, NULL, NULL, 2, NULL, 0, 'In a blender or food processor, whirl them until smooth, at least 1 minute to get everything puréed. Do this in batches if necessary.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'21D4F52D57D6E346A5D158E6DFE18775', X'A8A97331F2821D4BA37065CE49463BD1', X'A8A97331F2821D4BA37065CE49463BD1', 3, NULL, NULL, 2, NULL, 0, 'Run the pureed mixture through a sieve or food mill for a smoother texture.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'256DD4025C5D4748BF8ED4F77145144A', X'A8A97331F2821D4BA37065CE49463BD1', X'A8A97331F2821D4BA37065CE49463BD1', 4, NULL, NULL, 2, NULL, 0, 'Add the lemon juice. Stir to combine, and add salt, pepper, hot sauce or horseradish, and more lemon juice (or anything else!) to taste. Tasting is key to end up with a cocktail that meets your ideal version of a Bloody Mary.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1781FA7D7554BC4E957626B6BD1E259E', X'A8A97331F2821D4BA37065CE49463BD1', X'A8A97331F2821D4BA37065CE49463BD1', 5, NULL, NULL, 2, NULL, 0, 'Cover and chill the spiced-up tomato juice mixture until cold, at least an hour and up to two days.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1338BB689A14434C9C48F5EB192D053E', X'A8A97331F2821D4BA37065CE49463BD1', X'A8A97331F2821D4BA37065CE49463BD1', 6, NULL, NULL, 2, NULL, 0, 'Pour the juice into 2 to 4 glasses, add 2 tablespoons (or more, no judgment here!) vodka or gin into each glass, if you like, and stir to combine. Garnish with celery stalk and/or pickled green beans.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F836EDBDA19DCF449D5623F9D2484CA0', X'E144B9FF8D5C0D41AD7729AF36646F6D', X'E144B9FF8D5C0D41AD7729AF36646F6D', 0, NULL, NULL, 2, NULL, 0, 'Gather your ingredients. Preheat the oven to 375 F.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6054FD07E270A241865294DD39765C36', X'E144B9FF8D5C0D41AD7729AF36646F6D', X'E144B9FF8D5C0D41AD7729AF36646F6D', 1, NULL, NULL, 2, NULL, 0, 'If using fresh chorizo, brown it in a medium sized skillet on high heat for about 5 minutes or until the sausage is cooked through and browned. Stir as you are cooking.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D14C989EE03B9F4985B23C2B070B48B0', X'E144B9FF8D5C0D41AD7729AF36646F6D', X'E144B9FF8D5C0D41AD7729AF36646F6D', 2, NULL, NULL, 2, NULL, 0, 'Spread out the tortilla chips onto a parchment paper lined-baking sheet.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'999DC99312233342A444C08A7181F291', X'E144B9FF8D5C0D41AD7729AF36646F6D', X'E144B9FF8D5C0D41AD7729AF36646F6D', 3, NULL, NULL, 2, NULL, 0, 'Cover the chips with the shredded cheese and the cooked chorizo.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'159E3F8C9DCFAB42B367638CCEBA7412', X'E144B9FF8D5C0D41AD7729AF36646F6D', X'E144B9FF8D5C0D41AD7729AF36646F6D', 4, NULL, NULL, 2, NULL, 0, 'Make 6 little wells in the chips and crack an egg into each one. Sprinkle salt and pepper over the top.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F2F618CDA958D149901766F08B984BEB', X'E144B9FF8D5C0D41AD7729AF36646F6D', X'E144B9FF8D5C0D41AD7729AF36646F6D', 5, NULL, NULL, 2, NULL, 0, 'Place the baking sheet into the oven on the lowest rack. Cook until the egg whites are cooked through and the yolks are to your desired doneness, about 15 minutes.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5818B96EC1B80D42BE37A716F4FA015D', X'E144B9FF8D5C0D41AD7729AF36646F6D', X'E144B9FF8D5C0D41AD7729AF36646F6D', 6, NULL, NULL, 2, NULL, 0, 'Add more cheese to the top if you wish.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'818536B10C4D70428713681C45DD4126', X'E144B9FF8D5C0D41AD7729AF36646F6D', X'E144B9FF8D5C0D41AD7729AF36646F6D', 7, NULL, NULL, 2, NULL, 0, 'Drizzle the taco sauce over the top of the nachos and top with the sliced avocado and cherry tomatoes.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D9A35C6FB2F26B47879AD0BA25B7094B', X'E144B9FF8D5C0D41AD7729AF36646F6D', X'E144B9FF8D5C0D41AD7729AF36646F6D', 8, NULL, NULL, 2, NULL, 0, 'Top with the sour cream, sliced jalapenos, and any of the garnishes that you want. The scallions, cilantro, and lime all add great flavor and color.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E67D244D57946B4687DC76AF774FB29A', X'E144B9FF8D5C0D41AD7729AF36646F6D', X'E144B9FF8D5C0D41AD7729AF36646F6D', 9, NULL, NULL, 2, NULL, 0, 'Serve immediately with more sour cream and taco sauce!');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EBF1B0920FF67F40949AA2039E5CB450', X'B9D316E2AFEFB64A8D3DA4F3468DC1A9', X'B9D316E2AFEFB64A8D3DA4F3468DC1A9', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5F5A90AB4BD8EE4AAC2E8E3D314B7C17', X'B9D316E2AFEFB64A8D3DA4F3468DC1A9', X'B9D316E2AFEFB64A8D3DA4F3468DC1A9', 1, NULL, NULL, 2, NULL, 0, 'You will notice a thin, silvery membrane on the underside of the ribs. If it isn''t removed, it will stay tough and chewy and might prevent some of your dry rub spices from penetrating the meat. To remove the membrane, slide a butter knife under it on one end until you can grasp it. With a paper towel in hand (to keep it from slipping), slowly pull on the membrane to remove it in one piece.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8C1E38CBAA2CEE4085EF718310A9711B', X'B9D316E2AFEFB64A8D3DA4F3468DC1A9', X'B9D316E2AFEFB64A8D3DA4F3468DC1A9', 2, NULL, NULL, 2, NULL, 0, 'In a small bowl, combine the smoked paprika, chili powder, onion powder, dry mustard, garlic powder, salt, black pepper, and cayenne. Blend the spices thoroughly and then rub the mixture all over the ribs.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0FAE663DDCDB824FB495530955900A82', X'B9D316E2AFEFB64A8D3DA4F3468DC1A9', X'B9D316E2AFEFB64A8D3DA4F3468DC1A9', 3, NULL, NULL, 2, NULL, 0, 'Place a trivet in the Instant Pot and add the 1 cup of stock or water and the apple cider vinegar. Stand the ribs on end in the pot, coiling them to fit. Lock the lid in place and make sure the steam release valve is in the sealing position. Choose the pressure cook or manual function (high pressure) and set the time for 25 minutes.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CE45146443A9B54C8AC800681E6F632C', X'B9D316E2AFEFB64A8D3DA4F3468DC1A9', X'B9D316E2AFEFB64A8D3DA4F3468DC1A9', 4, NULL, NULL, 2, NULL, 0, 'When the ribs are done, carefully turn the steam release valve to the venting position for a quick release. If your pressure cooker is not an Instant Pot, follow the manufacturer''s directions for a quick release.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C0FBEB5AE1C20941BB94B9F631A573A3', X'B9D316E2AFEFB64A8D3DA4F3468DC1A9', X'B9D316E2AFEFB64A8D3DA4F3468DC1A9', 5, NULL, NULL, 2, NULL, 0, 'Line a large baking sheet with foil and preheat the broiler (or grill).');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0D630853850F434AA90748EF764DB0F2', X'B9D316E2AFEFB64A8D3DA4F3468DC1A9', X'B9D316E2AFEFB64A8D3DA4F3468DC1A9', 6, NULL, NULL, 2, NULL, 0, 'Remove the ribs to the prepared baking sheet and brush generously with barbecue sauce. Broil or grill the ribs until they are lightly charred.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8FA34D824007904AA844BFE9A043D251', X'B9D316E2AFEFB64A8D3DA4F3468DC1A9', X'B9D316E2AFEFB64A8D3DA4F3468DC1A9', 7, NULL, NULL, 2, NULL, 0, 'Slice the ribs into individual ribs or serving-size portions.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4AAD7C312559194D95E97C8072B1C751', X'B9D316E2AFEFB64A8D3DA4F3468DC1A9', X'B9D316E2AFEFB64A8D3DA4F3468DC1A9', 8, NULL, NULL, 2, NULL, 0, 'Enjoy!');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9FBFB13E13D72A409A845C003DF7D383', X'EBA316999D77C842A2765FA15D8459DF', X'EBA316999D77C842A2765FA15D8459DF', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
