INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C2639A08F66B2343B8F1CA234E86DE5E', X'EBA316999D77C842A2765FA15D8459DF', X'EBA316999D77C842A2765FA15D8459DF', 3, NULL, NULL, 2, NULL, 0, 'Serve and enjoy!');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F35ACE7520621040A26B1D954CEF50CA', X'4B0D7566BD5D4A4CB5AAA48B5338A45A', X'4B0D7566BD5D4A4CB5AAA48B5338A45A', 0, NULL, NULL, 2, NULL, 0, 'Gather your ingredients.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'53A0409E2BC1B04A9016BC648922F07E', X'4B0D7566BD5D4A4CB5AAA48B5338A45A', X'4B0D7566BD5D4A4CB5AAA48B5338A45A', 1, NULL, NULL, 2, NULL, 0, 'Trim off the excess fat from the edge of the pork chop. Pound each pork chop between two sheets of parchment paper to ¼ inch thickness. Salt and pepper both sides of the pork chops. Set aside.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7D0F8B0EEA200841BC8D9802D9209C21', X'4B0D7566BD5D4A4CB5AAA48B5338A45A', X'4B0D7566BD5D4A4CB5AAA48B5338A45A', 2, NULL, NULL, 2, NULL, 0, 'Whisk together the flour, half of the parmesan cheese, and a pinch of the salt and pepper into a shallow bowl.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A43EEF11222E604D8BED99FCE0353ABA', X'4B0D7566BD5D4A4CB5AAA48B5338A45A', X'4B0D7566BD5D4A4CB5AAA48B5338A45A', 3, NULL, NULL, 2, NULL, 0, 'Whisk together the egg, a pinch of the salt and pepper and 2 tablespoons of water into another shallow bowl.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EDFC6B80ED6BA140A2A8FC66B57752F5', X'4B0D7566BD5D4A4CB5AAA48B5338A45A', X'4B0D7566BD5D4A4CB5AAA48B5338A45A', 4, NULL, NULL, 2, NULL, 0, 'Whisk together the remaining parmesan cheese, panko bread crumbs, dried basil, dried oregano and another pinch of salt and pepper in a third shallow bowl.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'12BECAF004533040A1FED12E6C9B9D74', X'4B0D7566BD5D4A4CB5AAA48B5338A45A', X'4B0D7566BD5D4A4CB5AAA48B5338A45A', 5, NULL, NULL, 2, NULL, 0, 'Dip the pork chops into the flour/cheese mixture. Coat both sides and then shake off the excess flour.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BDD41A7E14AE2845999124382D8E1717', X'4B0D7566BD5D4A4CB5AAA48B5338A45A', X'4B0D7566BD5D4A4CB5AAA48B5338A45A', 6, NULL, NULL, 2, NULL, 0, 'Then dip the pork into the egg mixture and flip to coat both sides.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8D947F809551BA4FBC31E2A719016A59', X'4B0D7566BD5D4A4CB5AAA48B5338A45A', X'4B0D7566BD5D4A4CB5AAA48B5338A45A', 7, NULL, NULL, 2, NULL, 0, 'Transfer the pork chop to the breadcrumb mixture and coat both sides.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A48135076B77C841981731B9B14ED0DD', X'4B0D7566BD5D4A4CB5AAA48B5338A45A', X'4B0D7566BD5D4A4CB5AAA48B5338A45A', 8, NULL, NULL, 2, NULL, 0, 'Heat the olive oil and butter in a large skillet on medium heat until the butter has melted. Add the pork chops and cook on medium heat for about 3 minutes or until golden brown. Flip and cook for another 3-4 minutes or until the internal temperature reaches 165F.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A5564F7BD2F71041A1F9C01C4BB53262', X'4B0D7566BD5D4A4CB5AAA48B5338A45A', X'4B0D7566BD5D4A4CB5AAA48B5338A45A', 9, NULL, NULL, 2, NULL, 0, 'Remove from the heat and serve with arugula, more parmesan, chopped parsley and lemon wedges.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'217BEE63C1622B4D925015D540AB11FA', X'8A1BEF9EF253964F8F0D94399ACA855E', X'8A1BEF9EF253964F8F0D94399ACA855E', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C4005B33BD1D1942B5D9695B779341AB', X'8A1BEF9EF253964F8F0D94399ACA855E', X'8A1BEF9EF253964F8F0D94399ACA855E', 1, NULL, NULL, 2, NULL, 0, 'Cut the pork shoulder (aka pork butt or Boston butt) into several large chunks and add it to the slow cooker along with the sliced onion, salt, pepper, and root beer. Place the cover on the slow cooker and cook on the low setting for 8 to 10 hours, or until the pork is very tender. Alternatively, choose the high setting and cook the pork for about 4 to 5 hours.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8B7647EB0486E1459D26B5D02FB8947C', X'8A1BEF9EF253964F8F0D94399ACA855E', X'8A1BEF9EF253964F8F0D94399ACA855E', 2, NULL, NULL, 2, NULL, 0, 'When the pork is ready, drain and discard the liquids. Use two forks or a potato masher to shred the pork. Add the barbecue sauce and stir to blend.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4AE84A668B5F314A8293458BF40D06E9', X'8A1BEF9EF253964F8F0D94399ACA855E', X'8A1BEF9EF253964F8F0D94399ACA855E', 3, NULL, NULL, 2, NULL, 0, 'For a classic pulled pork sandwich, pile the shredded pork on a bun or hoagie roll and top with sliced dill or sour pickles and coleslaw.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DD268AD1501D0C4F8F5A16CFB40D8C0B', X'8A1BEF9EF253964F8F0D94399ACA855E', X'8A1BEF9EF253964F8F0D94399ACA855E', 4, NULL, NULL, 2, NULL, 0, 'Serve pulled pork sandwiches with extra barbecue sauce and coleslaw on the side.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AA8B7D9751E8E64E9F853276DAB23F7D', X'8A1BEF9EF253964F8F0D94399ACA855E', X'8A1BEF9EF253964F8F0D94399ACA855E', 5, NULL, NULL, 2, NULL, 0, 'Alternatively, use the shredded pulled pork to make tacos, or serve it as an entree along with mac and cheese, slaw, and biscuits.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B10F5493EF8BD7458F1FC46ABFA3D388', X'8A1BEF9EF253964F8F0D94399ACA855E', X'8A1BEF9EF253964F8F0D94399ACA855E', 6, NULL, NULL, 2, NULL, 0, 'Enjoy!');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E3938C399862124B993CD317907F372B', X'DFD2B31CF8445D4C8212F5075DC4EFCE', X'DFD2B31CF8445D4C8212F5075DC4EFCE', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'098282A8024DB94D83FEE24C9D5028BF', X'DFD2B31CF8445D4C8212F5075DC4EFCE', X'DFD2B31CF8445D4C8212F5075DC4EFCE', 1, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 400 F or the grill to medium-high (about 400 F). If using frozen shrimp place the package under cold water for 10 minutes to thaw.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3E47825050E1064DA00B6145A62551C5', X'DFD2B31CF8445D4C8212F5075DC4EFCE', X'DFD2B31CF8445D4C8212F5075DC4EFCE', 2, NULL, NULL, 2, NULL, 0, 'Bring 2 quarts of water to a roaring boil and add the potatoes and the corn. Reduce the heat and bring to simmer. Cook for 10 minutes on simmer and drain into a colander. Set aside.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FFE56ACBE4FDDD48BE9B6A28F182154E', X'DFD2B31CF8445D4C8212F5075DC4EFCE', X'DFD2B31CF8445D4C8212F5075DC4EFCE', 3, NULL, NULL, 2, NULL, 0, 'While the potatoes and corn are simmering prepare 4 sets of 12 x 24 inch long aluminum foil sheets. Fold each in half, forming a square. If using industrial aluminum foil simply prepare sheets measuring 12 x 12 inches. Slightly fold up the edges of the sheets.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'30B9D8344BF8E5468FD6B4DC034E3C19', X'DFD2B31CF8445D4C8212F5075DC4EFCE', X'DFD2B31CF8445D4C8212F5075DC4EFCE', 4, NULL, NULL, 2, NULL, 0, 'In a large bowl combine the drained potatoes and corn, the sausage, the thawed shrimp, the onion, and the garlic. Drizzle with the 4 tablespoons of melted butter, season with salt, pepper, and Old Bay seasoning. Mix very well to coat all ingredients.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D1EDF93D3A3F2B4CABCDADCF863B9B75', X'DFD2B31CF8445D4C8212F5075DC4EFCE', X'DFD2B31CF8445D4C8212F5075DC4EFCE', 5, NULL, NULL, 2, NULL, 0, 'Divide the shrimp boil ingredients between the four aluminum foil sheets.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'25E18ECBCC6F22438635732B76384F07', X'DFD2B31CF8445D4C8212F5075DC4EFCE', X'DFD2B31CF8445D4C8212F5075DC4EFCE', 6, NULL, NULL, 2, NULL, 0, 'Neatly fold into packets.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FE473373E10D3C4D815EFB6A24D041E0', X'DFD2B31CF8445D4C8212F5075DC4EFCE', X'DFD2B31CF8445D4C8212F5075DC4EFCE', 7, NULL, NULL, 2, NULL, 0, 'Place them onto a baking sheet and bake for 15 minutes. If grilling, place the packets directly onto the grill surface and cook for 15 minutes, flipping once after about 8 minutes.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8472BF59A0520E4D847D5039296B04CF', X'DFD2B31CF8445D4C8212F5075DC4EFCE', X'DFD2B31CF8445D4C8212F5075DC4EFCE', 8, NULL, NULL, 2, NULL, 0, 'Serve the packets slightly opened up, garnished with chopped parsley, a pinch of Old Bay seasoning and a few lemon wedges. Serve the 1 stick melted butter as a side for optional drizzling over the shrimp boil or dipping.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5D283D59DD6B24439AB8FA3EF2F0DD1D', X'302D67296862554BBE73A7AA866B18D3', X'302D67296862554BBE73A7AA866B18D3', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5D8763D60F6F7F4F99118B2C60D244CC', X'302D67296862554BBE73A7AA866B18D3', X'302D67296862554BBE73A7AA866B18D3', 1, NULL, NULL, 2, NULL, 0, 'Cook the linguine pasta according to the package instructions. Drain, but keep back 2 tablespoons of the cooking water. Toss the pasta in the pan you cooked it in with the reserved cooking water and 1 tablespoon of the olive oil. Keep warm.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2EEED949DCD91B41AA68124C2E1A5DD1', X'302D67296862554BBE73A7AA866B18D3', X'302D67296862554BBE73A7AA866B18D3', 2, NULL, NULL, 2, NULL, 0, 'Make the sauce while the pasta is cooking. Heat the remaining olive oil in a large heavy-bottomed frying pan to hot, but not smoking.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A3445DFECE40E1459873480432542D32', X'302D67296862554BBE73A7AA866B18D3', X'302D67296862554BBE73A7AA866B18D3', 3, NULL, NULL, 2, NULL, 0, 'Add the minced shallot and cook for 1 minute. Add the chili flakes and stir. Lower the heat slightly and add the garlic. Stir well and cook for one minute. Take great care to not burn the garlic or the the dish will taste bitter.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'31B41C85955631499D229CEECABAC764', X'302D67296862554BBE73A7AA866B18D3', X'302D67296862554BBE73A7AA866B18D3', 4, NULL, NULL, 2, NULL, 0, 'Heat a serving bowl - this will ultimately ensure that the dish keeps warm while you enjoy it.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AD865799B1D179489C520FC61ACAC87F', X'302D67296862554BBE73A7AA866B18D3', X'302D67296862554BBE73A7AA866B18D3', 5, NULL, NULL, 2, NULL, 0, 'Add the drained sardines to the frying pan and stir very carefully taking care not to break the sardines up too much. Finally, add the lemon zest and stir again.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'75E03BC9221DA94E9CA3DD97C366834F', X'302D67296862554BBE73A7AA866B18D3', X'302D67296862554BBE73A7AA866B18D3', 6, NULL, NULL, 2, NULL, 0, 'Tip the cooked linguine into the warmed serving bowl, stir the sardine sauce, parsley, and baby kale (the heat of the pasta and the sauce will be enough to cook the tiny leaves) into the linguine. Add salt and pepper to taste. Finish with a drizzle of lemon juice and serve immediately.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'31704574F887B6478847D1F8B99091F4', X'B78FB37F420F9A45859676EE27F0BD83', X'B78FB37F420F9A45859676EE27F0BD83', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D7B9FA686EA6ED40BB41FC42FB70B9EA', X'B78FB37F420F9A45859676EE27F0BD83', X'B78FB37F420F9A45859676EE27F0BD83', 1, NULL, NULL, 2, NULL, 0, 'Remove the roast from the roasting pan and let it rest, covered with foil, on a cutting board in an area that''s warm. If there is a lot of fat left in the pan, pour most of it off—you can refrigerate it in a Mason jar to add concentrated flavor and richness to soups, stocks, chilis, and even pasta. When pouring out the fat, take care not to lose any of the meat juices.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'03CFAC85C4D0EA4390255C61ED02F208', X'B78FB37F420F9A45859676EE27F0BD83', X'B78FB37F420F9A45859676EE27F0BD83', 2, NULL, NULL, 2, NULL, 0, 'Place the roasting pan on the stovetop, across two burners, and add the chopped carrots, onion, and celery.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'14450D51B031EB41B5C8A1C53DC0DC8A', X'B78FB37F420F9A45859676EE27F0BD83', X'B78FB37F420F9A45859676EE27F0BD83', 3, NULL, NULL, 2, NULL, 0, 'Cook on high for a minute, stirring everything around with a wooden spoon until the veggies are a bit browned and most of the liquid has cooked off—but don''t let anything burn.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C2533CB28FA10743BCC0D1C1EC6973B8', X'B78FB37F420F9A45859676EE27F0BD83', X'B78FB37F420F9A45859676EE27F0BD83', 4, NULL, NULL, 2, NULL, 0, 'Pour in about half of the stock and cook for another minute over high heat while scraping all those toasty bits (called fond) away from the bottom of the pan with your wooden spoon.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AFD4EA468ACDA44DA09D1E40A499D616', X'B78FB37F420F9A45859676EE27F0BD83', X'B78FB37F420F9A45859676EE27F0BD83', 5, NULL, NULL, 2, NULL, 0, 'Pour the contents of the roasting pan into a large saucepan along with the remaining stock. Simmer for about 20 minutes or until the liquid has reduced by about a third.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'03AAE16B8E0C284991F4241061D48F9C', X'B78FB37F420F9A45859676EE27F0BD83', X'B78FB37F420F9A45859676EE27F0BD83', 6, NULL, NULL, 2, NULL, 0, 'Pour the liquid through a mesh strainer. For a finer strain, you can line the strainer with cheesecloth. Let it sit for a couple of minutes so that you can skim off any fat that rises to the top.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A6A7EC25E2636A4BBCADA31168D18318', X'B78FB37F420F9A45859676EE27F0BD83', X'B78FB37F420F9A45859676EE27F0BD83', 7, NULL, NULL, 2, NULL, 0, 'By now, your roast will have finished resting and will be ready to carve. If the roast has thrown off any additional juices while it rested, stir these into the sauce.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'62E3A6BCFFE47142832A37BC772EB7AC', X'B78FB37F420F9A45859676EE27F0BD83', X'B78FB37F420F9A45859676EE27F0BD83', 8, NULL, NULL, 2, NULL, 0, 'Add salt and pepper to taste.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5E252E09DBE2234E90127A598BD1162F', X'B78FB37F420F9A45859676EE27F0BD83', X'B78FB37F420F9A45859676EE27F0BD83', 9, NULL, NULL, 2, NULL, 0, 'Each portion of meat should get 1 1/2 to 2 ounces of au jus sauce.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4E4808433DCA7B41BC47D979926EA040', X'B78FB37F420F9A45859676EE27F0BD83', X'B78FB37F420F9A45859676EE27F0BD83', 10, NULL, NULL, 2, NULL, 0, 'Serve warm and enjoy.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3E920CD807EEA445BDF739A25A133779', X'DC3523494EA52849A9EF443E58858FF5', X'DC3523494EA52849A9EF443E58858FF5', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'993B7F297143C243A58329E51D9278EF', X'DC3523494EA52849A9EF443E58858FF5', X'DC3523494EA52849A9EF443E58858FF5', 1, NULL, NULL, 2, NULL, 0, 'Make sure your grill is clean. Rub the cooking grate with a thin layer of oil—1 or 2 teaspoons should be enough. Heat the grill (whether charcoal, gas, or electric) to medium-high. You should be able to hold your hand about 1 inch above the cooking grate for 3 to 4 seconds before pulling it away from the heat. A properly heated grill will help keep the fish from sticking excessively while also cooking it gently and thoroughly without setting it aflame.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D60873FB0F45EE42861E2027B175DD13', X'DC3523494EA52849A9EF443E58858FF5', X'DC3523494EA52849A9EF443E58858FF5', 2, NULL, NULL, 2, NULL, 0, 'While the grill heats, prepare the salmon fillets. Rinse them and pat them dry.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9B359DB7FA9DB942982DBDC2BC7EEFF6', X'DC3523494EA52849A9EF443E58858FF5', X'DC3523494EA52849A9EF443E58858FF5', 3, NULL, NULL, 2, NULL, 0, 'Use tweezers to pull out any pin bones. Pull them out in the direction at which they''re coming out for the smoothest results. Don''t see any pin bones? Run your finger down the `seam` of the fillet—you''ll feel them.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'072662700CF7224999F56DAADA236219', X'DC3523494EA52849A9EF443E58858FF5', X'DC3523494EA52849A9EF443E58858FF5', 4, NULL, NULL, 2, NULL, 0, 'Brush the cooking grate with another 1 to 2 teaspoons of oil. Brush the 4 fillets with the remaining oil and sprinkle with salt and pepper.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0370BE36F29EAE4A91D256EB79524079', X'DC3523494EA52849A9EF443E58858FF5', X'DC3523494EA52849A9EF443E58858FF5', 5, NULL, NULL, 2, NULL, 0, 'Place the salmon, skin-side-down, on the hot grill and cover. Cook, undisturbed, until the salmon just starts to release its fat (opaque mayonnaise-like stuff), or until it reaches 145 F, which takes about 15 to 20 minutes for most 1-inch thick fillets. Allow another 5 to 10 minutes for each extra inch of thickness. Use a spatula to remove the fish from the grill.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DC32A4E693277741861EE2B105A599FF', X'DC3523494EA52849A9EF443E58858FF5', X'DC3523494EA52849A9EF443E58858FF5', 6, NULL, NULL, 2, NULL, 0, 'Serve grilled salmon immediately. Offer lemon wedges so people can spritz their salmon with fresh lemon juice, or serve with the garnish of your choice.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0E009967DE75274E936B5BABEFD8B9FB', X'9CE7F91553CF4342955B6BA9BEBED98C', X'9CE7F91553CF4342955B6BA9BEBED98C', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CFEE0EADF7F8764AA7C1C11EB477360A', X'9CE7F91553CF4342955B6BA9BEBED98C', X'9CE7F91553CF4342955B6BA9BEBED98C', 1, NULL, NULL, 2, NULL, 0, 'In a large resealable food storage bag (plastic), combine the oil, vinegar, sugar, ketchup, onion, Worcestershire sauce, salt, garlic, and Tabasco. Add the chicken and turn to coat thoroughly. Seal the bag and refrigerate for at least 4 hours or overnight, turning occasionally.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4A5082BF1F834441ABA9BE1B03C08419', X'9CE7F91553CF4342955B6BA9BEBED98C', X'9CE7F91553CF4342955B6BA9BEBED98C', 2, NULL, NULL, 2, NULL, 0, 'Pour marinade into a saucepan and bring to a full rolling boil over high heat. Remove from the heat and set aside.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'14A8343161EB0B4F9C920605BCEC298F', X'9CE7F91553CF4342955B6BA9BEBED98C', X'9CE7F91553CF4342955B6BA9BEBED98C', 3, NULL, NULL, 2, NULL, 0, 'Heat the grill to moderate or medium-high heat, about 350 F to 375 F.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2651CC38C35C6E45BEECCD820A7D306D', X'9CE7F91553CF4342955B6BA9BEBED98C', X'9CE7F91553CF4342955B6BA9BEBED98C', 4, NULL, NULL, 2, NULL, 0, 'First, sear the chicken over direct heat for about 2 to 3 minutes per side.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'582CF33C6C334D47A193911E33ED2C77', X'9CE7F91553CF4342955B6BA9BEBED98C', X'9CE7F91553CF4342955B6BA9BEBED98C', 5, NULL, NULL, 2, NULL, 0, 'Continue grilling the chicken leg quarters over indirect heat -- not directly over coals or gas burners -- turning frequently and basting with the reserved boiled marinade, for about 35 to 55 minutes, depending on size.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AE15F069464F5349BA796F3FA630FF5C', X'9CE7F91553CF4342955B6BA9BEBED98C', X'9CE7F91553CF4342955B6BA9BEBED98C', 6, NULL, NULL, 2, NULL, 0, 'The chicken should be fork tender and juices will run clear. The temperature should register at least 165 F on an instant-read thermometer inserted into the thickest part of the meat but not touching bone.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3F1B2014290F07418BE5B313DB83548D', X'9CE7F91553CF4342955B6BA9BEBED98C', X'9CE7F91553CF4342955B6BA9BEBED98C', 7, NULL, NULL, 2, NULL, 0, 'Serve and enjoy!');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'32A10589DCABB14BBD284F308887A02D', X'454578A7241211468556FEEF59E7DF00', X'454578A7241211468556FEEF59E7DF00', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B935DE50D98663479D166DEB48A58C08', X'454578A7241211468556FEEF59E7DF00', X'454578A7241211468556FEEF59E7DF00', 1, NULL, NULL, 2, NULL, 0, 'Build the liquid ingredients in a highball glass over ice cubes. Start by pouring the vodka, and then add the tomato juice, lemon juice, and the dash of Worcheshire sauce.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D40D1A10B48DE64AA4EE55EDDF1730E3', X'454578A7241211468556FEEF59E7DF00', X'454578A7241211468556FEEF59E7DF00', 2, NULL, NULL, 2, NULL, 0, 'Add the celery salt, pepper, and hot sauce to taste. If using, add the horseradish.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EA84285BB1C19F4B8EE871CADF2BE276', X'454578A7241211468556FEEF59E7DF00', X'454578A7241211468556FEEF59E7DF00', 3, NULL, NULL, 2, NULL, 0, 'Stir well, or try rolling the drink.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4655176F5583FA46AD66EF55A87F2921', X'454578A7241211468556FEEF59E7DF00', X'454578A7241211468556FEEF59E7DF00', 4, NULL, NULL, 2, NULL, 0, 'Taste and adjust the seasonings to your liking.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B58E20DDE99EBA4CBDB6061E1519E373', X'454578A7241211468556FEEF59E7DF00', X'454578A7241211468556FEEF59E7DF00', 5, NULL, NULL, 2, NULL, 0, 'Garnish with a lemon wedge and celery stalk or pickle.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FF80099812AB134AAFC704FED3328FBE', X'454578A7241211468556FEEF59E7DF00', X'454578A7241211468556FEEF59E7DF00', 6, NULL, NULL, 2, NULL, 0, 'Serve and enjoy.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'90CD44154BAB29478E048DA61BA628BE', X'55DB7B192AA64346995563985AAC6EB7', X'55DB7B192AA64346995563985AAC6EB7', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3BD0C98F6D4DC84AB5010C97C64A9D1C', X'55DB7B192AA64346995563985AAC6EB7', X'55DB7B192AA64346995563985AAC6EB7', 1, NULL, NULL, 2, NULL, 0, 'Cook the ziti in boiling salted water following the package directions. Drain well and set aside.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6076849EFA1B1B4293E4ECD20C35DE91', X'55DB7B192AA64346995563985AAC6EB7', X'55DB7B192AA64346995563985AAC6EB7', 2, NULL, NULL, 2, NULL, 0, 'Heat the oven to 350 F (180 C/Gas 4). Lightly butter a 2 1/2-quart baking dish.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DEC6F741B703A448A379E94CC3FE974D', X'55DB7B192AA64346995563985AAC6EB7', X'55DB7B192AA64346995563985AAC6EB7', 3, NULL, NULL, 2, NULL, 0, 'While the ziti is cooking, drizzle the olive oil in a large skillet or sauté pan and place it over medium heat. When the oil is hot, add the ground beef, onion, and green bell pepper. Cook, stirring until the onion is tender and the beef is no longer pink.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'836EDF5FFAA8B84585052F195A87561D', X'55DB7B192AA64346995563985AAC6EB7', X'55DB7B192AA64346995563985AAC6EB7', 4, NULL, NULL, 2, NULL, 0, 'Drain off the excess fat and then add the tomatoes, tomato sauce, basil, oregano, garlic powder, salt, and pepper. Bring to a simmer.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B627EDB669DE954286C9887A4B03DFC0', X'55DB7B192AA64346995563985AAC6EB7', X'55DB7B192AA64346995563985AAC6EB7', 5, NULL, NULL, 2, NULL, 0, 'Add the cooked pasta and half of the mozzarella cheese.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4D0485D90DB5134AACDBCB0E91C3B2E2', X'55DB7B192AA64346995563985AAC6EB7', X'55DB7B192AA64346995563985AAC6EB7', 6, NULL, NULL, 2, NULL, 0, 'Transfer the mixture to the prepared baking dish.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1325BEEA0C16004E9128B6651CBAD920', X'55DB7B192AA64346995563985AAC6EB7', X'55DB7B192AA64346995563985AAC6EB7', 7, NULL, NULL, 2, NULL, 0, 'Top the casserole with the remaining mozzarella cheese.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B635136F5E97394C83F4DE2A83E5E1C7', X'55DB7B192AA64346995563985AAC6EB7', X'55DB7B192AA64346995563985AAC6EB7', 8, NULL, NULL, 2, NULL, 0, 'Bake the ziti casserole for 25 to 30 minutes or until hot and bubbly.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C8262851B35B17449C65352A41B1093A', X'41B35484D3369340839E9FED8593709D', X'41B35484D3369340839E9FED8593709D', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FBD035A9EA25CB4F86AD0BB2ABE7FE7D', X'41B35484D3369340839E9FED8593709D', X'41B35484D3369340839E9FED8593709D', 1, NULL, NULL, 2, NULL, 0, 'To make the brine, combine salt, white sugar, and hot water in a large plastic or glass container that you can cover. Stir to mix. Add cold water and cayenne, if using. Continue stirring to dissolve.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'ED3E9738E92FB149B9491686E838AFE6', X'41B35484D3369340839E9FED8593709D', X'41B35484D3369340839E9FED8593709D', 2, NULL, NULL, 2, NULL, 0, 'Add chicken drumsticks, cover, and refrigerate for 4 hours.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3721C16D4C5D2E4283A18A7C4A6DA5BE', X'41B35484D3369340839E9FED8593709D', X'41B35484D3369340839E9FED8593709D', 3, NULL, NULL, 2, NULL, 0, 'Meanwhile, gather the ingredients for the sauce.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CFA3684D8740C945AD096867538B5D8F', X'41B35484D3369340839E9FED8593709D', X'41B35484D3369340839E9FED8593709D', 4, NULL, NULL, 2, NULL, 0, 'Make barbecue sauce by combining ingredients in a medium saucepan.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'70B5C02DAA87F247BF8BAF9290BC7530', X'41B35484D3369340839E9FED8593709D', X'41B35484D3369340839E9FED8593709D', 5, NULL, NULL, 2, NULL, 0, 'Simmer over low heat for 15 minutes, stirring occasionally.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'41F38C36662C6249A6F62F5B8D91D909', X'41B35484D3369340839E9FED8593709D', X'41B35484D3369340839E9FED8593709D', 6, NULL, NULL, 2, NULL, 0, 'Remove from heat and allow to cool for 10 to 15 minutes.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'57D6DFD11079A146B3445DE9146421F0', X'41B35484D3369340839E9FED8593709D', X'41B35484D3369340839E9FED8593709D', 7, NULL, NULL, 2, NULL, 0, 'Preheat grill to a medium-low heat (around 275 F/135 C).');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EA37E1BCBFCEB5459149533FFF98F237', X'41B35484D3369340839E9FED8593709D', X'41B35484D3369340839E9FED8593709D', 8, NULL, NULL, 2, NULL, 0, 'Remove the chicken legs from the brine and discard brine. Rinse chicken thoroughly to remove all traces of the brine from the surface. Pat dry with paper towels.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B246C4FD09DBCC41B1A70D23F4EE0C42', X'41B35484D3369340839E9FED8593709D', X'41B35484D3369340839E9FED8593709D', 9, NULL, NULL, 2, NULL, 0, 'Grill chicken legs for about 30 to 35 minutes, turning often (about every 5 minutes) to make sure all sides are cooked evenly.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'02EFB7FE9CB2BC4E993B13AE964A0DC5', X'41B35484D3369340839E9FED8593709D', X'41B35484D3369340839E9FED8593709D', 10, NULL, NULL, 2, NULL, 0, 'During the last 15 to 20 minutes, start brushing sauce over the surface every 5 minutes, rotating the chicken to coat evenly, until the chicken is done (an internal temperature of should be at least 165 F/75 C, but you may want to get closer to 180 F/82 C), and the sauce has cooked down to a sticky consistency.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'07A89591FBD50341997A0C53821919A4', X'41B35484D3369340839E9FED8593709D', X'41B35484D3369340839E9FED8593709D', 11, NULL, NULL, 2, NULL, 0, 'Remove chicken from heat, place on a large serving platter, and tent loosely with aluminum foil. Let drumsticks rest for 5 to 7 minutes.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'694214A95D73CD4D8167AB3C7E466A6C', X'41B35484D3369340839E9FED8593709D', X'41B35484D3369340839E9FED8593709D', 12, NULL, NULL, 2, NULL, 0, 'Serve and enjoy!');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F9F4C878CEFD994EA7E5CAC24A0CAA98', X'CD176861A3F8BD4CB27364E529FAD312', X'CD176861A3F8BD4CB27364E529FAD312', 0, NULL, NULL, 2, NULL, 0, 'In a large bowl, add the ground lamb, diced onion, minced garlic, chopped parsley, ground cumin, ground coriander, ground cinnamon, salt, and pepper. Mix together until thoroughly combined and form four individual patties.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9F23271B8434064F95768A0746CE4E32', X'CD176861A3F8BD4CB27364E529FAD312', X'CD176861A3F8BD4CB27364E529FAD312', 1, NULL, NULL, 2, NULL, 0, 'Heat up your grill or a grill pan if cooking indoors. Place the burgers on the grill and cook for 4 minutes, flip and cook for an additional 4 minutes. That will give you a medium-rare burger and an internal temperature of about 160 degrees Fahrenheit. Cook for an additional minute or two if you''d like your burgers more well done.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'23EEC2740E75CE44B653BC04CA297099', X'CD176861A3F8BD4CB27364E529FAD312', X'CD176861A3F8BD4CB27364E529FAD312', 2, NULL, NULL, 2, NULL, 0, 'Toast or grill the buns until lightly golden brown (optional) and spread about a tablespoon of labneh on the bottom half of each bun. Place a cooked burger over the labneh, top with the lettuce or green of your choice and sprinkle the crumbled feta cheese over it. top with the remaining half of the bun and serve hot.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5A2775AB1B224B4EA33C67EF0A5F95CD', X'880293BE579D23428C48727DA4650629', X'880293BE579D23428C48727DA4650629', 0, NULL, NULL, 2, NULL, 0, 'In a medium bowl, combine the breadcrumbs, milk, salt, pepper, marjoram, and ketchup; mix well with a spoon and let this mixture stand for 5 minutes.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5CEC7B2AB308624B8AE369A7BF76F3E6', X'880293BE579D23428C48727DA4650629', X'880293BE579D23428C48727DA4650629', 1, NULL, NULL, 2, NULL, 0, 'Add the ground beef, crumbling it with your fingers as you add it; mix gently but thoroughly with your hands until combined.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'057D738313F1C94EAFC7E3DCC684C7C8', X'880293BE579D23428C48727DA4650629', X'880293BE579D23428C48727DA4650629', 2, NULL, NULL, 2, NULL, 0, 'Form the meat mixture into about 10 to 12 mini burgers. At this point, you can cover the burgers and chill them for up to 24 hours.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'71F3A67B23467841AA19395C3D4D78D7', X'880293BE579D23428C48727DA4650629', X'880293BE579D23428C48727DA4650629', 3, NULL, NULL, 2, NULL, 0, 'When you are ready to eat, prepare and preheat a gas or charcoal grill. Grill the burgers on a small rack placed over the regular grill rack, over medium heat for 3 to 4 minutes per side, turning once, until the burgers are 160 F.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5E7923199A416849A97F4BDC9988757E', X'880293BE579D23428C48727DA4650629', X'880293BE579D23428C48727DA4650629', 4, NULL, NULL, 2, NULL, 0, 'Top each mini burger with the American cheese slices and cover the grill for 30 to 40 seconds to let the cheese melt. You can also fry these burgers in a hot pan over medium-high heat, turning once, until done to 160 F.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'092094B781F8394FACD0D66420CCFE70', X'880293BE579D23428C48727DA4650629', X'880293BE579D23428C48727DA4650629', 5, NULL, NULL, 2, NULL, 0, 'Assemble the burgers with the buns, tomatoes, lettuce leaves, ketchup, mustard, and pickles.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BDD8CD5C662A9041A191DFC60D465871', X'F487F5589393844B81929B432D40DA7C', X'F487F5589393844B81929B432D40DA7C', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A23E72DD6D87054283D8B27414D7476A', X'F487F5589393844B81929B432D40DA7C', X'F487F5589393844B81929B432D40DA7C', 1, NULL, NULL, 2, NULL, 0, 'Begin by forming your lamb patties.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C333233D01F6B747AF56DC156F3A45BA', X'F487F5589393844B81929B432D40DA7C', X'F487F5589393844B81929B432D40DA7C', 2, NULL, NULL, 2, NULL, 0, 'You can either cook it on a cast-iron skillet or you can cook it on the grill. Either way, get the heat source blazing hot.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3AE17C721F3A0E46B6281457286AA1BC', X'F487F5589393844B81929B432D40DA7C', X'F487F5589393844B81929B432D40DA7C', 3, NULL, NULL, 2, NULL, 0, 'Add your burger sphere, let it cook for a few minutes until the bottom becomes crispy and cooked about halfway up the meat.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DE7A671F21D9074A80F982127A699DEC', X'F487F5589393844B81929B432D40DA7C', X'F487F5589393844B81929B432D40DA7C', 4, NULL, NULL, 2, NULL, 0, 'At this point, flip the burger gently, and then brown the other side for a minute or two.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E87464FE7BE7574D891CB544BB430A6B', X'F487F5589393844B81929B432D40DA7C', X'F487F5589393844B81929B432D40DA7C', 5, NULL, NULL, 2, NULL, 0, 'Next, you''ll want to take a spatula and smush the burger. The grilled bits will help the burger stay together and will taste amazing. Continue cooking until the meat is cooked through and set aside to let it rest.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4F2FB767C076F143AAD5631C8BA214E4', X'F487F5589393844B81929B432D40DA7C', X'F487F5589393844B81929B432D40DA7C', 6, NULL, NULL, 2, NULL, 0, 'Then, toast your bun. Do this either directly on the grill, in a toaster, or using a broiler.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C35CF41829B7964A83E34850B44026E1', X'F487F5589393844B81929B432D40DA7C', X'F487F5589393844B81929B432D40DA7C', 7, NULL, NULL, 2, NULL, 0, 'Once the bun is crispy and warm, smear half the tzatziki onto both halves. On the bottom half, add the lamb burger, then the roasted tomatoes, then the red onion, and the mint. Serve immediately.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E53D3E9691B8964CABA03347D7CA6565', X'3256E7C6E75635438EC14AC935CF147B', X'3256E7C6E75635438EC14AC935CF147B', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5230139B0F671641940C85EC1E80CD95', X'3256E7C6E75635438EC14AC935CF147B', X'3256E7C6E75635438EC14AC935CF147B', 1, NULL, NULL, 2, NULL, 0, 'The night before you plan to serve your prime rib, mix salt, pepper, and rosemary and rub the meat with it liberally.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2574841B9F278442A118BFCDFBDC7AB1', X'3256E7C6E75635438EC14AC935CF147B', X'3256E7C6E75635438EC14AC935CF147B', 2, NULL, NULL, 2, NULL, 0, 'Cover the meat and place it in the refrigerator overnight.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3A8EEBB90BE7EE40AFF23C86F60B2788', X'3256E7C6E75635438EC14AC935CF147B', X'3256E7C6E75635438EC14AC935CF147B', 3, NULL, NULL, 2, NULL, 0, 'The roast needs to come to room temperature before frying, so remember to remove it from the refrigerator around an hour to an hour and a half before frying.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4871DB4D404B6F4098450E843BF50346', X'3256E7C6E75635438EC14AC935CF147B', X'3256E7C6E75635438EC14AC935CF147B', 4, NULL, NULL, 2, NULL, 0, 'Heat 3 gallons of peanut oil to 360 F.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'12D28850C7FED145B73B8C4711E21D9F', X'3256E7C6E75635438EC14AC935CF147B', X'3256E7C6E75635438EC14AC935CF147B', 5, NULL, NULL, 2, NULL, 0, 'Slowly lower the prime rib into 350 F oil. You can expect the oil to drop in temperature quickly, probably down to approximately 325 F. Turn the heat up on the burner to bring back up to 350 F and then level off temperature.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8B1335EA2648914A877B4795C9D57736', X'3256E7C6E75635438EC14AC935CF147B', X'3256E7C6E75635438EC14AC935CF147B', 6, NULL, NULL, 2, NULL, 0, 'Cook the prime rib for 3 minutes per pound of weight for medium-rare steaks or 4 minutes per pound for medium.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E2658B045A9D224FA6E3BCBA3C068348', X'3256E7C6E75635438EC14AC935CF147B', X'3256E7C6E75635438EC14AC935CF147B', 7, NULL, NULL, 2, NULL, 0, 'Carefully remove from hot oil and let rest for 10 minutes.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DDE7FF4A7B645944AF1CECCE2DEBBD48', X'3256E7C6E75635438EC14AC935CF147B', X'3256E7C6E75635438EC14AC935CF147B', 8, NULL, NULL, 2, NULL, 0, 'Try using sliced bread to drain on versus paper towels.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'74609F498FF37142BB56BBB41CD70BF2', X'3256E7C6E75635438EC14AC935CF147B', X'3256E7C6E75635438EC14AC935CF147B', 9, NULL, NULL, 2, NULL, 0, 'Let rest for a few minutes and then slice.');
