INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'77252A6D623303438C2A50743999E92D', X'857DE1B4C6345843BB2D98FF8F6071D4', X'857DE1B4C6345843BB2D98FF8F6071D4', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 400 degrees F. Spray a 2- or 3-quart baking dish with cooking spray.');
