INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FB0E073C2F51504C9D1151EC7AAA3CDE', X'857DE1B4C6345843BB2D98FF8F6071D4', X'857DE1B4C6345843BB2D98FF8F6071D4', 4, NULL, NULL, 2, NULL, 0, 'Place in the prepared baking dish. Bake 18-22 minutes, until fish flakes easily with a fork.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A80449400EEDA9499E811B2380E27E35', X'793D6D675C4584479E8FAA845841DCA9', X'793D6D675C4584479E8FAA845841DCA9', 0, NULL, NULL, 2, NULL, 0, 'Sauté the onion slices in butter until tender; set aside.');
