INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B9E0E6A095D5E64E81DAB6BAE1F310DC', X'8B72B045FA6A814894A543165F90FDE4', X'8B72B045FA6A814894A543165F90FDE4', 2, NULL, NULL, 2, NULL, 0, 'In a medium bowl, combine the crab meat with the chopped green bell pepper, pimiento, mustard, Worcestershire sauce, salt, pepper, Old Bay, cayenne pepper (if using), and crushed crackers.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'53D0373412542B4D9E601B15762E7745', X'8B72B045FA6A814894A543165F90FDE4', X'8B72B045FA6A814894A543165F90FDE4', 3, NULL, NULL, 2, NULL, 0, 'In a small bowl or cup, combine the egg white, 1 tablespoon of the mayonnaise, and the parsley.');
