INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1AF74C391C9718478842AF4CCA7D443D', X'8B72B045FA6A814894A543165F90FDE4', X'8B72B045FA6A814894A543165F90FDE4', 6, NULL, NULL, 2, NULL, 0, 'Place the flounder in a greased shallow baking dish and top each fillet with even amounts of the crab mixture.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'78F54D2069680E4BBAEE5D77E03E81F2', X'8B72B045FA6A814894A543165F90FDE4', X'8B72B045FA6A814894A543165F90FDE4', 7, NULL, NULL, 2, NULL, 0, 'Drizzle the remaining butter over the top of the stuffed fillets.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6F90CF3153249642BA97D150A4BC3438', X'8B72B045FA6A814894A543165F90FDE4', X'8B72B045FA6A814894A543165F90FDE4', 8, NULL, NULL, 2, NULL, 0, 'Bake the crab-topped flounder in the preheated oven for 15 minutes.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FDB6E3567FFB04449B097D9322F432A7', X'8B72B045FA6A814894A543165F90FDE4', X'8B72B045FA6A814894A543165F90FDE4', 9, NULL, NULL, 2, NULL, 0, 'Combine the egg yolk and the remaining mayonnaise in a small bowl.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'57318CB6BFCE3849BD4E4BC5B0506605', X'8B72B045FA6A814894A543165F90FDE4', X'8B72B045FA6A814894A543165F90FDE4', 10, NULL, NULL, 2, NULL, 0, 'Gently spread the egg mixture on top of each fish portion and sprinkle with paprika.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7F62486AABF4A14FA59B0D0B07536C45', X'8B72B045FA6A814894A543165F90FDE4', X'8B72B045FA6A814894A543165F90FDE4', 11, NULL, NULL, 2, NULL, 0, 'Increase the oven temperature to 450 F (230 C/Gas 8) and bake 6 minutes longer, or until golden and bubbly.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7A59F62D1992B24487387B6455743BFE', X'8B72B045FA6A814894A543165F90FDE4', X'8B72B045FA6A814894A543165F90FDE4', 12, NULL, NULL, 2, NULL, 0, 'Serve and enjoy!');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'93AB62962EB3F440BCFEDF67FF8B99CF', X'6BB18301F2768A4CA11669E7564FE74B', X'6BB18301F2768A4CA11669E7564FE74B', 0, NULL, NULL, 2, NULL, 0, 'Place beans in food processor and process them until they are fairly smooth. Add flour, cornmeal, salsa, cumin and garlic salt. Process until well combined.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2F5BDFDB1922344ABD766B5B1B820E02', X'6BB18301F2768A4CA11669E7564FE74B', X'6BB18301F2768A4CA11669E7564FE74B', 1, NULL, NULL, 2, NULL, 0, 'Spoon the mixture into six balls on a large plate and refrigerate at least 1 hour or up to 4 hours before cooking.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2D7F7585CBF1A34B9E1CA6E62BB5B308', X'6BB18301F2768A4CA11669E7564FE74B', X'6BB18301F2768A4CA11669E7564FE74B', 2, NULL, NULL, 2, NULL, 0, 'Heat barbecue grill or ridged grill pan over medium heat. Coat grill or pan lightly with oil. Form each ball into a 4-inch-wide patty about 1/2-inch thick. Place the patties on the grill or in a pan and then cook until browned and heated through. This should take about 4 to 5 minutes per side.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BC5D7E91B9D21D428D5755CB50FCA24E', X'6BB18301F2768A4CA11669E7564FE74B', X'6BB18301F2768A4CA11669E7564FE74B', 3, NULL, NULL, 2, NULL, 0, 'Serve your black bean cornmeal patties on hamburger buns with your favorite toppings.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AB4602C1D91925499E62DA67A8598955', X'B2FB58BBB4020C4DAAF40D14D18C05A7', X'B2FB58BBB4020C4DAAF40D14D18C05A7', 0, NULL, NULL, 2, NULL, 0, 'Split your bagel down the middle and toast until crispy.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B57796EFD4FA554D8EF9FA30AD034670', X'B2FB58BBB4020C4DAAF40D14D18C05A7', X'B2FB58BBB4020C4DAAF40D14D18C05A7', 1, NULL, NULL, 2, NULL, 0, 'Meanwhile, crack your eggs into a small bowl, add salt and pepper and whisk vigorously.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5DFD0E034C6CDC4881998D2B1D99EE7F', X'B2FB58BBB4020C4DAAF40D14D18C05A7', X'B2FB58BBB4020C4DAAF40D14D18C05A7', 2, NULL, NULL, 2, NULL, 0, 'Heat a small nonstick frying pan over medium heat, add your butter and allow it to melt.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2C5D2F213A5FE042A7C7237E60BF0FB3', X'B2FB58BBB4020C4DAAF40D14D18C05A7', X'B2FB58BBB4020C4DAAF40D14D18C05A7', 3, NULL, NULL, 2, NULL, 0, 'Pour your whisked eggs into the buttered frypan and stir continuously until the eggs have cooked through but are still soft. Add in the dill and set aside.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F7A78DEECF44134B930DB8F8BD2D5092', X'B2FB58BBB4020C4DAAF40D14D18C05A7', X'B2FB58BBB4020C4DAAF40D14D18C05A7', 4, NULL, NULL, 2, NULL, 0, 'Once the bagel is toasted, gently place the soft scrambled eggs on top of the bottom half of the bagel, then add the smoked salmon pieces, the cooked asparagus, and the pickled red onions.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DF5583D09CFCD24C92F85CB7060B3549', X'B2FB58BBB4020C4DAAF40D14D18C05A7', X'B2FB58BBB4020C4DAAF40D14D18C05A7', 5, NULL, NULL, 2, NULL, 0, 'Next, take the remaining top half of the bagel and smear it with cream cheese. Place the creamed cheese bagel half on top of the rest of the sandwich and serve.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'09045D6F1D315B4984E090C74F86F5AD', X'B2FB58BBB4020C4DAAF40D14D18C05A7', X'B2FB58BBB4020C4DAAF40D14D18C05A7', 6, NULL, NULL, 2, NULL, 0, 'Enjoy!');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'54F8CE55E40D20408F3795281744594A', X'397CFD9D6FCAD946B9C59C757DD12B43', X'397CFD9D6FCAD946B9C59C757DD12B43', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'35E5715CC4144D41BD2CE5869378A988', X'397CFD9D6FCAD946B9C59C757DD12B43', X'397CFD9D6FCAD946B9C59C757DD12B43', 1, NULL, NULL, 2, NULL, 0, 'Preheat an oven to 400 F. If you haven''t had the clams shucked, shuck them yourself: insert a blunt knife or screwdriver into the hinge or otherwise between the shells, twist it to break open the shells, remove the top shell, and repeat with remaining clams.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1ADCEBBA66A393428422F47EFE4D653B', X'397CFD9D6FCAD946B9C59C757DD12B43', X'397CFD9D6FCAD946B9C59C757DD12B43', 2, NULL, NULL, 2, NULL, 0, 'Set the shucked clams on the half-shell (be sure to scrape off any clam meat that clings to the other side of the shell into the side you''re cooking—you want all that meat!) on a baking sheet or in a baking dish.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'69B627BA83C96543B8D3A31E01F5DA5B', X'397CFD9D6FCAD946B9C59C757DD12B43', X'397CFD9D6FCAD946B9C59C757DD12B43', 3, NULL, NULL, 2, NULL, 0, 'Peel the garlic and mince it until it forms almost a paste. Mince the parsley leaves as well.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5BC60021CD26BD468152BBE5F2053183', X'397CFD9D6FCAD946B9C59C757DD12B43', X'397CFD9D6FCAD946B9C59C757DD12B43', 4, NULL, NULL, 2, NULL, 0, 'In a small bowl, combine the bread crumbs, garlic, and chile flakes. Mix to combine thoroughly. Drizzle the mixture with the olive oil and combine again. Distribute the stuffing evenly on the clams.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5DF4AC63E394EC4196B9D2E3CA30E866', X'397CFD9D6FCAD946B9C59C757DD12B43', X'397CFD9D6FCAD946B9C59C757DD12B43', 5, NULL, NULL, 2, NULL, 0, 'Bake until the clams are heated through and the breadcrumb topping is browned, 10 to 12 minutes—10 for slightly softer clams, 12 for those who like their cooked seafood really cooked. Serve the clams right away.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'09CC43135C52D04FA7D2C555DF259563', X'3D3B1D18B3441F4E8A79688E8F504EAD', X'3D3B1D18B3441F4E8A79688E8F504EAD', 0, NULL, NULL, 2, NULL, 0, 'Fry 4 slices chopped bacon in a medium pot until soft and golden, about 4 minutes. Add 1 small chopped onion and 2 cloves chopped garlic and continue cooking until the bacon is nicely browned and the onion is tender, about 4 minutes longer.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2E2DDFE010C2AF49BCCB243CBB922EE4', X'3D3B1D18B3441F4E8A79688E8F504EAD', X'3D3B1D18B3441F4E8A79688E8F504EAD', 1, NULL, NULL, 2, NULL, 0, 'Stir in 3 pounds well-scrubbed Manila clams and add 1 cup beer or water or chicken stock. Cover the pot and steam the clams for 3 to 5 minutes, until they open. Discard any clams that do not open.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9C48240F5E8B0F42839303663F2253CD', X'3D3B1D18B3441F4E8A79688E8F504EAD', X'3D3B1D18B3441F4E8A79688E8F504EAD', 2, NULL, NULL, 2, NULL, 0, 'Stir gently to mix with the bacon, garlic, and onion, and serve immediately.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A1F2D61CF3E05441BD9BA47F1113FB6B', X'DC1BB4764BDB054184A066FE85BD1646', X'DC1BB4764BDB054184A066FE85BD1646', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'653415AC8C9C8C40B6A5B4C40DE733A9', X'DC1BB4764BDB054184A066FE85BD1646', X'DC1BB4764BDB054184A066FE85BD1646', 1, NULL, NULL, 2, NULL, 0, 'First, preheat oven to 400 F. Pierce the sweet potatoes and microwave them together until they are soft, about 4 to 6 minutes. Mash them firmly into measuring cups to make 2 cups of mashed sweet potatoes, and then transfer them to a medium-sized mixing bowl.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7E206088C7C0C345BCAA4BB11B9B9A31', X'DC1BB4764BDB054184A066FE85BD1646', X'DC1BB4764BDB054184A066FE85BD1646', 2, NULL, NULL, 2, NULL, 0, 'Meanwhile, in a small saucepan, bring 3/4 cup water and the quinoa to a boil. Reduce to a low simmer and cover, until the water is nearly absorbed and the quinoa appears transparent and is fully cooked? about 8 to 10 minutes. Remove the quinoa from the stove and allow it to sit covered, for about 5 minutes.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3A41622DC9B62949B1B46D311196929E', X'DC1BB4764BDB054184A066FE85BD1646', X'DC1BB4764BDB054184A066FE85BD1646', 3, NULL, NULL, 2, NULL, 0, 'In a separate saucepan, melt 1 tablespoon of the vegan butter substitute or butter. Stir in 1 tablespoon of maple syrup and the cayenne pepper. Add mixture to the mashed sweet potatoes along with the kale, the cooked quinoa, pecans and 1/2 teaspoon of salt and then mix it all together well.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'61D6D58E0B4A1D42A6A05FB5D7564869', X'DC1BB4764BDB054184A066FE85BD1646', X'DC1BB4764BDB054184A066FE85BD1646', 4, NULL, NULL, 2, NULL, 0, 'Form the mixture into four patties; place on a well-oiled baking sheet. Bake for 35 minutes, turning once at the halfway point.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BC6E703DB0087542B044754156D7E0C6', X'DC1BB4764BDB054184A066FE85BD1646', X'DC1BB4764BDB054184A066FE85BD1646', 5, NULL, NULL, 2, NULL, 0, 'When the burgers have around 15 minutes left to bake, make the onion topping. In a small skillet over medium heat, melt the remaining 1 tablespoon vegan butter substitute. Add the onion; sprinkle with salt and fresh ground black pepper to taste. Cook and stir for 12 minutes. Reduce heat to low; add remaining 1 tablespoon of maple syrup. Cook and stir until onions are slightly brown, about 3 more minutes; remove from heat.');
INSERT INTO `VWR INTERNAL Occassionrecipes 2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A2EB2D348BBF4B468FE7050A50A6E6E5', X'DC1BB4764BDB054184A066FE85BD1646', X'DC1BB4764BDB054184A066FE85BD1646', 6, NULL, NULL, 2, NULL, 0, 'Transfer burgers to buns and top each with onion mixture.');
