INSERT INTO `VWR INTERNAL RecipesbyCourse3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F59BA505F1F0474FB6E0F4194A6A1E1F', X'B6821CC0182B534DBE66363DE7669814', X'B6821CC0182B534DBE66363DE7669814', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL RecipesbyCourse3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'042C6980BCCF574A8A1044F5C1B5DFC4', X'B6821CC0182B534DBE66363DE7669814', X'B6821CC0182B534DBE66363DE7669814', 1, NULL, NULL, 2, NULL, 0, 'Prepare the couscous. In a medium-sized saucepan over medium-high heat, heat 2 tablespoons olive oil. Add the couscous and cook, stirring occasionally, for about 3 to 4 minutes or until fragrant and lightly toasted.');
INSERT INTO `VWR INTERNAL RecipesbyCourse3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5E540C2E0402184097F76165704BF60B', X'B6821CC0182B534DBE66363DE7669814', X'B6821CC0182B534DBE66363DE7669814', 2, NULL, NULL, 2, NULL, 0, 'Add the vegetable broth, bring the mixture to a boil, and then turn down the heat and simmer for 10 to 12 minutes or until the liquid is absorbed and the couscous is tender. Set aside.');
INSERT INTO `VWR INTERNAL RecipesbyCourse3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F6ECCDAFEDBAAA49B69E6FDC12CCA53E', X'B6821CC0182B534DBE66363DE7669814', X'B6821CC0182B534DBE66363DE7669814', 3, NULL, NULL, 2, NULL, 0, 'While the couscous simmers, heat the remaining 1 tablespoon olive oil in a saucepan over medium-high heat. Add the garlic, mushrooms, and white wine and cook, stirring often, for about 5 minutes.');
INSERT INTO `VWR INTERNAL RecipesbyCourse3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EC87C424BA5E3A4D81B963F6E1005544', X'B6821CC0182B534DBE66363DE7669814', X'B6821CC0182B534DBE66363DE7669814', 4, NULL, NULL, 2, NULL, 0, 'Add the dried thyme, fresh parsley, and salt, stirring to combine, and cook for about 3 to 4 minutes more, or until most of the moisture from the mushrooms has evaporated.');
INSERT INTO `VWR INTERNAL RecipesbyCourse3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D54002ADF319184CBC11D8D1805D7B99', X'B6821CC0182B534DBE66363DE7669814', X'B6821CC0182B534DBE66363DE7669814', 5, NULL, NULL, 2, NULL, 0, 'Add the cooked Israeli couscous to the mushroom mixture, tossing well to combine. Add salt and pepper to taste.');
INSERT INTO `VWR INTERNAL RecipesbyCourse3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'08B630BDF5E92840AC167D320A7E1A0D', X'E6E182E696CDFB48BA3D8C02CDD2A5E2', X'E6E182E696CDFB48BA3D8C02CDD2A5E2', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL RecipesbyCourse3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DDDC64B37B977E468374254C2C64AF7F', X'E6E182E696CDFB48BA3D8C02CDD2A5E2', X'E6E182E696CDFB48BA3D8C02CDD2A5E2', 1, NULL, NULL, 2, NULL, 0, 'Combine couscous, cucumber, tomatoes, onion, garbanzo beans and mint in a large bowl.');
INSERT INTO `VWR INTERNAL RecipesbyCourse3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FE32DEF5D08E204E8F4B1277A141C4A7', X'E6E182E696CDFB48BA3D8C02CDD2A5E2', X'E6E182E696CDFB48BA3D8C02CDD2A5E2', 2, NULL, NULL, 2, NULL, 0, 'Whisk together lemon juice and olive oil.');
INSERT INTO `VWR INTERNAL RecipesbyCourse3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C947B9408B285A44BEEB8752E19EAA5D', X'E6E182E696CDFB48BA3D8C02CDD2A5E2', X'E6E182E696CDFB48BA3D8C02CDD2A5E2', 3, NULL, NULL, 2, NULL, 0, 'Pour over couscous salad and stir well.');
INSERT INTO `VWR INTERNAL RecipesbyCourse3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'812182A48D5AE34896D4235CF511C92A', X'E6E182E696CDFB48BA3D8C02CDD2A5E2', X'E6E182E696CDFB48BA3D8C02CDD2A5E2', 4, NULL, NULL, 2, NULL, 0, 'Cover and refrigerate for at least 2 hours.');
INSERT INTO `VWR INTERNAL RecipesbyCourse3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E73E52C6C9A78D49B790B43DF154B2DA', X'E6E182E696CDFB48BA3D8C02CDD2A5E2', X'E6E182E696CDFB48BA3D8C02CDD2A5E2', 5, NULL, NULL, 2, NULL, 0, 'Serve and enjoy!');
INSERT INTO `VWR INTERNAL RecipesbyCourse3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'538F6FBABFDEA843A70B72F149A001E7', X'8DC4179955D3C942B64C6F5E92A11046', X'8DC4179955D3C942B64C6F5E92A11046', 0, NULL, NULL, 2, NULL, 0, 'Pour the coconut milk, water, and salt into a medium-sized saucepan. Bring the mixture to a boil over medium-high heat.');
INSERT INTO `VWR INTERNAL RecipesbyCourse3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1A6552085DD88F40A1957EF197394D35', X'8DC4179955D3C942B64C6F5E92A11046', X'8DC4179955D3C942B64C6F5E92A11046', 1, NULL, NULL, 2, NULL, 0, 'Pour the couscous into the coconut milk mixture, remove from the heat, cover the pot with a lid, and allow it to sit for 5 minutes.');
INSERT INTO `VWR INTERNAL RecipesbyCourse3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A9AFDCA5FFCD8F46B66056E7987873C1', X'8DC4179955D3C942B64C6F5E92A11046', X'8DC4179955D3C942B64C6F5E92A11046', 2, NULL, NULL, 2, NULL, 0, 'Add the chopped green onion to the couscous, and fluff with a fork.');
INSERT INTO `VWR INTERNAL RecipesbyCourse3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'19B8A6BBC759564CB06BE6E04BC8A415', X'8DC4179955D3C942B64C6F5E92A11046', X'8DC4179955D3C942B64C6F5E92A11046', 3, NULL, NULL, 2, NULL, 0, 'Serve as a side dish or top with vegetables, meat or fish for a complete entree.');
INSERT INTO `VWR INTERNAL RecipesbyCourse3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AA8E3D31257F47488D2C54266B2007A7', X'68DDC95D43047A49AB06C27585C10A0C', X'68DDC95D43047A49AB06C27585C10A0C', 0, NULL, NULL, 2, NULL, 0, 'Cook the bacon until crisp and browned in a large pan over medium heat. Remove bacon from pan, place on paper towels to drain, then crumble. Drain most of the fat from the pan. Add the garlic to the pan and cook for about 30 seconds.');
INSERT INTO `VWR INTERNAL RecipesbyCourse3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0421683CCD4B5E489ACF58A045D150F0', X'68DDC95D43047A49AB06C27585C10A0C', X'68DDC95D43047A49AB06C27585C10A0C', 1, NULL, NULL, 2, NULL, 0, 'Scrape the garlic and bacon fat remaining in the pan into a small bowl.');
INSERT INTO `VWR INTERNAL RecipesbyCourse3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E20641439D69A14DB8A57F399F3047ED', X'68DDC95D43047A49AB06C27585C10A0C', X'68DDC95D43047A49AB06C27585C10A0C', 2, NULL, NULL, 2, NULL, 0, 'Bring a large pot of water to a boil over high heat. Add salt to flavor, then add the pasta. Stir. Cook until the pasta is just al dente, stirring occasionally.');
INSERT INTO `VWR INTERNAL RecipesbyCourse3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E9391C98E94F204BBE813830D3FB1012', X'68DDC95D43047A49AB06C27585C10A0C', X'68DDC95D43047A49AB06C27585C10A0C', 3, NULL, NULL, 2, NULL, 0, 'Meanwhile, add the eggs, mayonnaise, milk, salt, and pepper to the bacon in the small bowl and beat well with a wire whisk until blended and smooth.');
INSERT INTO `VWR INTERNAL RecipesbyCourse3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CDDD0FBAF8923E4A84E98C4D084C18BD', X'68DDC95D43047A49AB06C27585C10A0C', X'68DDC95D43047A49AB06C27585C10A0C', 4, NULL, NULL, 2, NULL, 0, 'When the pasta is cooked, drain, reserving 1/3 cup cooking water. Return the pasta to the pot immediately.');
INSERT INTO `VWR INTERNAL RecipesbyCourse3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5A5643A39025A541BFACE934B4D28248', X'68DDC95D43047A49AB06C27585C10A0C', X'68DDC95D43047A49AB06C27585C10A0C', 5, NULL, NULL, 2, NULL, 0, 'Off the heat, add the mayonnaise mixture. Toss with tongs until the mixture coats the pasta, adding cooking water as needed to make a smooth sauce.');
INSERT INTO `VWR INTERNAL RecipesbyCourse3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'23CD4335C9603E47BA901CF5BA333D6B', X'68DDC95D43047A49AB06C27585C10A0C', X'68DDC95D43047A49AB06C27585C10A0C', 6, NULL, NULL, 2, NULL, 0, 'Add the Parmesan cheese and toss again and serve immediately.');
INSERT INTO `VWR INTERNAL RecipesbyCourse3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'64576FFAFC32BC46BFBF6D09FA21C2B3', X'6FC6D98898FE3041A8A3DFC6BEA78B01', X'6FC6D98898FE3041A8A3DFC6BEA78B01', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL RecipesbyCourse3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9D813E52B441AF47AB3341B07A9ACE00', X'6FC6D98898FE3041A8A3DFC6BEA78B01', X'6FC6D98898FE3041A8A3DFC6BEA78B01', 1, NULL, NULL, 2, NULL, 0, 'Fill a large soup pot with cold water and add a handful or so of Kosher salt. Stir and taste; it should taste like seawater. Cover the pot and heat the water until it boils.');
INSERT INTO `VWR INTERNAL RecipesbyCourse3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3B5A3EAF976E604393FB301B6566B76B', X'6FC6D98898FE3041A8A3DFC6BEA78B01', X'6FC6D98898FE3041A8A3DFC6BEA78B01', 2, NULL, NULL, 2, NULL, 0, 'Drop the fettuccine into the boiling salted water and cook according to package instructions, about 6 to 9 minutes, or until al dente or tender, but still firm to the bite.');
INSERT INTO `VWR INTERNAL RecipesbyCourse3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8CBFCD43234AB844ACFB9AFD8F003E43', X'6FC6D98898FE3041A8A3DFC6BEA78B01', X'6FC6D98898FE3041A8A3DFC6BEA78B01', 3, NULL, NULL, 2, NULL, 0, 'While the fettuccine cooks, heat the butter and cream in a large sauté pan over medium-low heat until it starts to bubble.');
INSERT INTO `VWR INTERNAL RecipesbyCourse3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BF22D5B8FB2B7449A8E8B24E6D613F4B', X'6FC6D98898FE3041A8A3DFC6BEA78B01', X'6FC6D98898FE3041A8A3DFC6BEA78B01', 4, NULL, NULL, 2, NULL, 0, 'Lower the heat and simmer gently for about 15 minutes or until the sauce has reduced somewhat. Remove from heat.');
