INSERT INTO `VWR INTERNAL RecipesbyCourse3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'26A30312E227A34D97BD38003932ABE6', X'3B96B7AC7CB58C458D6BEA7FAB2890E6', X'3B96B7AC7CB58C458D6BEA7FAB2890E6', 1, NULL, NULL, 2, NULL, 0, 'Sprinkle the chicken cubes with about a third of the seasoning mix and toss to coat.');
