INSERT INTO `VWR INTERNAL RecipesbyCourse3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2D03912ACEF3A646ACDA585073521D28', X'3B96B7AC7CB58C458D6BEA7FAB2890E6', X'3B96B7AC7CB58C458D6BEA7FAB2890E6', 3, NULL, NULL, 2, NULL, 0, 'Begin cooking the pasta.');
