INSERT INTO `VWR INTERNAL RecipesbyCourse3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'217A83D10BA87644B9CF589096650B2F', X'3B96B7AC7CB58C458D6BEA7FAB2890E6', X'3B96B7AC7CB58C458D6BEA7FAB2890E6', 5, NULL, NULL, 2, NULL, 0, 'When the skillet is hot, add 1 tablespoon of the olive oil and swirl to coat the pan.');
INSERT INTO `VWR INTERNAL RecipesbyCourse3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7794327679694842885AD1E9F46E5866', X'3B96B7AC7CB58C458D6BEA7FAB2890E6', X'3B96B7AC7CB58C458D6BEA7FAB2890E6', 6, NULL, NULL, 2, NULL, 0, 'Add the chicken to the skillet and quickly sear it on two sides, about 3 minutes, then add the shrimp and brown each side 30 seconds.');
INSERT INTO `VWR INTERNAL RecipesbyCourse3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'59DBF9A35D7B0D4FA02D86DBC1FA6E5E', X'3B96B7AC7CB58C458D6BEA7FAB2890E6', X'3B96B7AC7CB58C458D6BEA7FAB2890E6', 7, NULL, NULL, 2, NULL, 0, 'Remove the chicken and shrimp to a bowl.');
INSERT INTO `VWR INTERNAL RecipesbyCourse3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'21C9B7D539CB1548A8029E20B955CAD5', X'3B96B7AC7CB58C458D6BEA7FAB2890E6', X'3B96B7AC7CB58C458D6BEA7FAB2890E6', 8, NULL, NULL, 2, NULL, 0, 'Add the remaining 1 tablespoon of olive oil to the pan and toss in the kielbasa sausage, tomatoes, bell pepper, and onion.');
INSERT INTO `VWR INTERNAL RecipesbyCourse3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1ADB07A61DD8454EB1482F0B6077FE0E', X'3B96B7AC7CB58C458D6BEA7FAB2890E6', X'3B96B7AC7CB58C458D6BEA7FAB2890E6', 9, NULL, NULL, 2, NULL, 0, 'Sprinkle the sausage and vegetables with the remaining third of the spice blend and cook over high heat until the vegetables are browned and softened.');
INSERT INTO `VWR INTERNAL RecipesbyCourse3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7015723DC4266A428FCF31AB146D0935', X'3B96B7AC7CB58C458D6BEA7FAB2890E6', X'3B96B7AC7CB58C458D6BEA7FAB2890E6', 10, NULL, NULL, 2, NULL, 0, 'Add half of the chicken stock and continue cooking over high heat until most of the stock has evaporated.');
INSERT INTO `VWR INTERNAL RecipesbyCourse3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EDC8273FCAF1414C9714E1F932A5B903', X'3B96B7AC7CB58C458D6BEA7FAB2890E6', X'3B96B7AC7CB58C458D6BEA7FAB2890E6', 11, NULL, NULL, 2, NULL, 0, 'Add the garlic and wine, stirring for 2 minutes.');
INSERT INTO `VWR INTERNAL RecipesbyCourse3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EEE0330AA44C964AA4ADAB249DB0D027', X'3B96B7AC7CB58C458D6BEA7FAB2890E6', X'3B96B7AC7CB58C458D6BEA7FAB2890E6', 12, NULL, NULL, 2, NULL, 0, 'Add the remaining chicken stock, along with the reserved chicken and shrimp.');
INSERT INTO `VWR INTERNAL RecipesbyCourse3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2446784D5F519245922ADDC0C5D6D9FA', X'3B96B7AC7CB58C458D6BEA7FAB2890E6', X'3B96B7AC7CB58C458D6BEA7FAB2890E6', 13, NULL, NULL, 2, NULL, 0, 'Stir, scraping up the browned bits from the bottom of the skillet, and simmer another 3 minutes or until the liquid is slightly reduced.');
INSERT INTO `VWR INTERNAL RecipesbyCourse3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'14E0CC4B44C4C046A0A87DF3B6EE9A78', X'3B96B7AC7CB58C458D6BEA7FAB2890E6', X'3B96B7AC7CB58C458D6BEA7FAB2890E6', 14, NULL, NULL, 2, NULL, 0, 'Serve the jambalaya over the cooked fettuccine.');
INSERT INTO `VWR INTERNAL RecipesbyCourse3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'17E5D115B1EA0B4FBADFBBA6F2FD6F3A', X'B828E8903E1CB342A53A2AFC6E07F66A', X'B828E8903E1CB342A53A2AFC6E07F66A', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL RecipesbyCourse3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BBD81EA92077D54885DA865AB3BD093B', X'B828E8903E1CB342A53A2AFC6E07F66A', X'B828E8903E1CB342A53A2AFC6E07F66A', 1, NULL, NULL, 2, NULL, 0, 'Remove the skin and bones from the cooked salmon.');
INSERT INTO `VWR INTERNAL RecipesbyCourse3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'56D04A7D6B83AC48A15405080BEAE9BF', X'B828E8903E1CB342A53A2AFC6E07F66A', X'B828E8903E1CB342A53A2AFC6E07F66A', 2, NULL, NULL, 2, NULL, 0, 'Gently break into large pieces. Set aside.');
INSERT INTO `VWR INTERNAL RecipesbyCourse3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'36DF0712AA2F2847ADB76AD751B9FBA3', X'B828E8903E1CB342A53A2AFC6E07F66A', X'B828E8903E1CB342A53A2AFC6E07F66A', 3, NULL, NULL, 2, NULL, 0, 'Bring water to boil in a large pot over high heat. Add some salt to the boiling water, then add the pasta and stir.');
INSERT INTO `VWR INTERNAL RecipesbyCourse3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'21B9A12F193EDF45871480CFE5C48626', X'B828E8903E1CB342A53A2AFC6E07F66A', X'B828E8903E1CB342A53A2AFC6E07F66A', 4, NULL, NULL, 2, NULL, 0, 'When you''ve added the pasta to the water, whisk together the pesto, cream, and sour cream in a large saucepan. Heat this mixture over medium-high heat for 3 to 4 minutes, stirring frequently with a wire whisk.');
INSERT INTO `VWR INTERNAL RecipesbyCourse3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E18BDE7A7C1434488C41A6517241C5D6', X'B828E8903E1CB342A53A2AFC6E07F66A', X'B828E8903E1CB342A53A2AFC6E07F66A', 5, NULL, NULL, 2, NULL, 0, 'Stir the salmon pieces into the cream mixture with a wooden spoon and continue heating over low heat; the mixture will start to simmer.');
INSERT INTO `VWR INTERNAL RecipesbyCourse3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'73D73E612BC399438F482123564A694A', X'B828E8903E1CB342A53A2AFC6E07F66A', X'B828E8903E1CB342A53A2AFC6E07F66A', 6, NULL, NULL, 2, NULL, 0, 'Drain the pasta when it is cooked to al dente.');
INSERT INTO `VWR INTERNAL RecipesbyCourse3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E1354770048A8A488BBDD57F00AB3EE8', X'B828E8903E1CB342A53A2AFC6E07F66A', X'B828E8903E1CB342A53A2AFC6E07F66A', 7, NULL, NULL, 2, NULL, 0, 'Add the drained pasta to the salmon mixture along with half of the Parmesan cheese.');
INSERT INTO `VWR INTERNAL RecipesbyCourse3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6EF6CBA2AE981C499B758AF36EAE36D0', X'B828E8903E1CB342A53A2AFC6E07F66A', X'B828E8903E1CB342A53A2AFC6E07F66A', 8, NULL, NULL, 2, NULL, 0, 'Turn the heat for the saucepan to medium and cook, tossing the mixture gently with tongs until the spaghetti is coated and the mixture is thoroughly heated.');
INSERT INTO `VWR INTERNAL RecipesbyCourse3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3126D23ACE718F4FBC1273983CDF3426', X'B828E8903E1CB342A53A2AFC6E07F66A', X'B828E8903E1CB342A53A2AFC6E07F66A', 9, NULL, NULL, 2, NULL, 0, 'Serve immediately with the remainder of Parmesan cheese.');
INSERT INTO `VWR INTERNAL RecipesbyCourse3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'26FE3BADB7F8474A98E083EFB8B82DB2', X'B828E8903E1CB342A53A2AFC6E07F66A', X'B828E8903E1CB342A53A2AFC6E07F66A', 10, NULL, NULL, 2, NULL, 0, 'Enjoy!');
INSERT INTO `VWR INTERNAL RecipesbyCourse3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'736E3C4AC361F34EA079B7170B9FEDF7', X'CEEBF9B2512CE742A86B99B63093199D', X'CEEBF9B2512CE742A86B99B63093199D', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL RecipesbyCourse3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F1BDCAC6446612409061805EB65EAAAA', X'CEEBF9B2512CE742A86B99B63093199D', X'CEEBF9B2512CE742A86B99B63093199D', 1, NULL, NULL, 2, NULL, 0, 'Fill a 3-quart saucepan about halfway full with water.');
INSERT INTO `VWR INTERNAL RecipesbyCourse3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0FA425F08768FC4B83B0BE4E218091ED', X'CEEBF9B2512CE742A86B99B63093199D', X'CEEBF9B2512CE742A86B99B63093199D', 2, NULL, NULL, 2, NULL, 0, 'Bring water to a boil, and add the pasta. Cook until pasta is al dente and drain.');
INSERT INTO `VWR INTERNAL RecipesbyCourse3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E23F7F7BDC47B14D89F5384A7FB281E3', X'CEEBF9B2512CE742A86B99B63093199D', X'CEEBF9B2512CE742A86B99B63093199D', 3, NULL, NULL, 2, NULL, 0, 'Meanwhile, make the sauce. In a small saucepan over low to medium heat, heat the oil.');
INSERT INTO `VWR INTERNAL RecipesbyCourse3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D0BFEB4C04D3CD418E89A68AFA9ACE22', X'CEEBF9B2512CE742A86B99B63093199D', X'CEEBF9B2512CE742A86B99B63093199D', 4, NULL, NULL, 2, NULL, 0, 'Add the flour and cook for about 2 minutes, stirring constantly with a wire whisk until smooth.');
INSERT INTO `VWR INTERNAL RecipesbyCourse3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0E47DBC5077B2F48ACECA0E8D0BF3357', X'CEEBF9B2512CE742A86B99B63093199D', X'CEEBF9B2512CE742A86B99B63093199D', 5, NULL, NULL, 2, NULL, 0, 'Continuing to stir, add the soymilk and fresh thyme and bring to a simmer for about 4 to 5 minutes. Salt and pepper to taste.');
INSERT INTO `VWR INTERNAL RecipesbyCourse3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E2F0D62731FF7C44A55C559812EE3D6B', X'CEEBF9B2512CE742A86B99B63093199D', X'CEEBF9B2512CE742A86B99B63093199D', 6, NULL, NULL, 2, NULL, 0, 'Serve with the fettuccine and steamed greens, if desired.');
INSERT INTO `VWR INTERNAL RecipesbyCourse3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'56835C0B9C18164188E4224F9F3131BD', X'CEEBF9B2512CE742A86B99B63093199D', X'CEEBF9B2512CE742A86B99B63093199D', 7, NULL, NULL, 2, NULL, 0, 'Enjoy!');
INSERT INTO `VWR INTERNAL RecipesbyCourse3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6039D0E3EC7E2B4993D21DF1CFFCCA5C', X'3EB43F1A657D7544A7E47EF8172E6E58', X'3EB43F1A657D7544A7E47EF8172E6E58', 0, NULL, NULL, 2, NULL, 0, 'In a large blender or food processor, combine the silken tofu, vegan margarine, vegan sour cream, vegan cream cheese, Parmesan cheese substitute and the garlic powder.');
INSERT INTO `VWR INTERNAL RecipesbyCourse3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AFF4D5DBDB9FA842B005DA796AB908A2', X'3EB43F1A657D7544A7E47EF8172E6E58', X'3EB43F1A657D7544A7E47EF8172E6E58', 1, NULL, NULL, 2, NULL, 0, 'Blend until smooth and creamy, then slowly add soy milk a little bit at a time, blending it all together as you go, until you reach a creamy, alfredo-like consistency.');
INSERT INTO `VWR INTERNAL RecipesbyCourse3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1E26B889DFAC1E4093BE7F140943668A', X'3EB43F1A657D7544A7E47EF8172E6E58', X'3EB43F1A657D7544A7E47EF8172E6E58', 2, NULL, NULL, 2, NULL, 0, 'Transfer the sauce mixture to a medium-sized saucepan and season lightly with salt and pepper.');
INSERT INTO `VWR INTERNAL RecipesbyCourse3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5D6C333B0806F146A0FCA085802B382E', X'3EB43F1A657D7544A7E47EF8172E6E58', X'3EB43F1A657D7544A7E47EF8172E6E58', 3, NULL, NULL, 2, NULL, 0, 'Heat the mixture gently over a low or medium heat just until thoroughly heated all the way through (about 6 to 8 minutes), stirring occasionally and being careful that the mixture never comes to a boil. Taste, and adjust seasonings to taste.');
INSERT INTO `VWR INTERNAL RecipesbyCourse3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'04B3232DA38E5747BC3EDD4B937D37BB', X'3EB43F1A657D7544A7E47EF8172E6E58', X'3EB43F1A657D7544A7E47EF8172E6E58', 4, NULL, NULL, 2, NULL, 0, 'A little extra vegan margarine or vegan Parmesan cheese or salt (sea salt or kosher salt is always best) might be needed at this stage.');
INSERT INTO `VWR INTERNAL RecipesbyCourse3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E7AF4A6EB9C4824A942FBC7EAAAF3964', X'3EB43F1A657D7544A7E47EF8172E6E58', X'3EB43F1A657D7544A7E47EF8172E6E58', 5, NULL, NULL, 2, NULL, 0, 'Serve your vegan alfredo sauce over prepared fettuccine pasta, or, use it to make a vegan white pizza or white sauce lasagna.');
INSERT INTO `VWR INTERNAL RecipesbyCourse3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'761AD5F050381F48ABE5EC40846BA8F8', X'28B1A6A8F6BAB44BB2950E87F6B0185D', X'28B1A6A8F6BAB44BB2950E87F6B0185D', 0, NULL, NULL, 2, NULL, 0, 'Cook fettuccine in a pasta pot of boiling salted water (see my article on reduced water pasta) reserving 1/3 cup cooking water, then drain pasta.');
INSERT INTO `VWR INTERNAL RecipesbyCourse3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EC76E09B616E874496628B95060201C2', X'28B1A6A8F6BAB44BB2950E87F6B0185D', X'28B1A6A8F6BAB44BB2950E87F6B0185D', 1, NULL, NULL, 2, NULL, 0, 'While pasta cooks, bring cream and butter to a simmer in a 12-inch heavy skillet over medium-low heat with salt and pepper. Add fettuccine and 3 tablespoons of reserved water, and cheese to sauce and toss. Add a touch more cooking water if sauce seems too thick to you.');
INSERT INTO `VWR INTERNAL RecipesbyCourse3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B95FEB01D91B2240A3C00067F168C4CB', X'28B1A6A8F6BAB44BB2950E87F6B0185D', X'28B1A6A8F6BAB44BB2950E87F6B0185D', 2, NULL, NULL, 2, NULL, 0, 'Serve garnished with chopped parsley and additional cheese on the side.');
INSERT INTO `VWR INTERNAL RecipesbyCourse3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4B56151F03AED24E92835B6F2B69A63A', X'6BFCBC4B593A3C4E9AD9AFE4AAE0C95D', X'6BFCBC4B593A3C4E9AD9AFE4AAE0C95D', 0, NULL, NULL, 2, NULL, 0, 'Bring a large pot of salted water to boil. Cook the gluten-free brown rice noodles until al dente, according to the manufacturer''s instructions. When noodles are just almost done, turn down the heat to medium and add the asparagus spears. Cook until tender-crisp and bright green, about 2 to 3 minutes. Drain pasta and asparagus and set aside.');
INSERT INTO `VWR INTERNAL RecipesbyCourse3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2308BE7B8B6FCD46B6CD6EF9FE4C2BFE', X'6BFCBC4B593A3C4E9AD9AFE4AAE0C95D', X'6BFCBC4B593A3C4E9AD9AFE4AAE0C95D', 1, NULL, NULL, 2, NULL, 0, 'Meanwhile, prepare the chicken. In a small bowl or cup, combine the dried thyme, salt, and pepper. Lightly coat a heavy, large non-stick pan with cooking spray, and heat over medium-high heat. Rub the chicken breasts on all sides with the seasoning mixture, and add the chicken breasts to the pan. Cook for 3 to 4 minutes on each side, or until the breasts are golden brown on both sides. Remove the pan from heat and transfer the chicken to a cutting board.');
INSERT INTO `VWR INTERNAL RecipesbyCourse3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F032AC37FC7F614295A8A695B5363CB1', X'6BFCBC4B593A3C4E9AD9AFE4AAE0C95D', X'6BFCBC4B593A3C4E9AD9AFE4AAE0C95D', 2, NULL, NULL, 2, NULL, 0, 'Cut the chicken breasts lengthwise into pieces about 1 to 2-inch thick, then return the pieces to the pan and return the pan to the heat. Cook until the pieces are just cooked through, just 2 to 3 minutes more. Add the chicken pieces to the pasta-asparagus mixture. In a small cup or bowl, stir together the cornstarch and water until smooth. Set aside. In the same pan you used to cook the chicken, combine the coconut milk, almond milk, nutritional yeast, salt, lemon pepper, garlic powder, and onion powder over medium-high heat. Stir the ingredients together until combined. Cook, stirring often until steam begins to rise from the surface of the liquid, about 3 to 5 minutes. Add the cornstarch mixture and stir well to incorporate. Stir constantly from this point on, cooking until the mixture just begins to thicken, adding more almond milk if necessary. Remove the pan from heat.');
INSERT INTO `VWR INTERNAL RecipesbyCourse3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BD23BAF85348A14E83BDFD0EDDE70B05', X'6BFCBC4B593A3C4E9AD9AFE4AAE0C95D', X'6BFCBC4B593A3C4E9AD9AFE4AAE0C95D', 3, NULL, NULL, 2, NULL, 0, 'Add the pasta, asparagus, chicken, and spinach to the pan, stirring to coat the pasta with the sauce evenly. Portion the pasta onto plates, drizzling any extra sauce over top of each of the servings. Serve immediately with sea salt and freshly ground pepper to taste, if desired.');
INSERT INTO `VWR INTERNAL RecipesbyCourse3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F0A5A150BE8EEA4F9CD312D9A62A168A', X'C5A9A2BFC809CB439A61DB520C030317', X'C5A9A2BFC809CB439A61DB520C030317', 0, NULL, NULL, 2, NULL, 0, 'Place the water, beaten egg, butter, and garlic powder in a food processor and mix at low speed until combined.');
INSERT INTO `VWR INTERNAL RecipesbyCourse3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8940BBC08C3F5940B93E5D0C3647AF02', X'C5A9A2BFC809CB439A61DB520C030317', X'C5A9A2BFC809CB439A61DB520C030317', 1, NULL, NULL, 2, NULL, 0, 'In a large mixing bowl, combine the potato flakes, flour, parsley, and salt.');
INSERT INTO `VWR INTERNAL RecipesbyCourse3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C479CE79F19D9D4EB5230FCFE635E21B', X'C5A9A2BFC809CB439A61DB520C030317', X'C5A9A2BFC809CB439A61DB520C030317', 2, NULL, NULL, 2, NULL, 0, 'Add the potato flake-flour mixture to the food processor and mix at low speed until a dough forms.');
INSERT INTO `VWR INTERNAL RecipesbyCourse3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1D4937D5B1762C4AB78A4F68615B37BD', X'C5A9A2BFC809CB439A61DB520C030317', X'C5A9A2BFC809CB439A61DB520C030317', 3, NULL, NULL, 2, NULL, 0, 'Flour your hands and work surface and gently knead the dough, then roll it out flat. Cut the dough into strips and roll the strips into ropes. Cut the ropes into bite-sized pieces and roll each piece on a gnocchi board or along the tines of the fork to create a ridged pattern.');
INSERT INTO `VWR INTERNAL RecipesbyCourse3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8C20C11A7523DF499AE591E7ED3620DE', X'C5A9A2BFC809CB439A61DB520C030317', X'C5A9A2BFC809CB439A61DB520C030317', 4, NULL, NULL, 2, NULL, 0, 'Boil in salted water for about 1 to 3 minutes (when they float to the surface, they are ready). Drain with a slotted spoon, rather than a colander, because they are a bit delicate. Toss gently in the sauce of your choice.');
INSERT INTO `VWR INTERNAL RecipesbyCourse3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'55684103FF17A54B8E247A308AD776F1', X'A6639EF1331BB743BA02251D99C6E40A', X'A6639EF1331BB743BA02251D99C6E40A', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL RecipesbyCourse3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CC6B1528CEFADC4B8D4B07E1C3DBA27B', X'A6639EF1331BB743BA02251D99C6E40A', X'A6639EF1331BB743BA02251D99C6E40A', 1, NULL, NULL, 2, NULL, 0, 'Soak the crustless bread pieces in 1/4 cup of milk until softened, about 5 to 10 minutes.');
INSERT INTO `VWR INTERNAL RecipesbyCourse3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'16FE961ABB43FC498E1C198C81C39C72', X'A6639EF1331BB743BA02251D99C6E40A', X'A6639EF1331BB743BA02251D99C6E40A', 2, NULL, NULL, 2, NULL, 0, 'Meanwhile, puree the nuts, garlic, Parmigiano, oil, salt, nutmeg, and marjoram (if using) together in a food processor or blender, or with a handheld immersion blender, to form a smooth paste. (If you want to do it old-school, use a mortar and pestle to grind the garlic, nuts, salt, nutmeg, and cheese together to a smooth paste before transferring to a separate bowl to stir in the olive oil.)');
INSERT INTO `VWR INTERNAL RecipesbyCourse3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'65A49BC21D167447BD40A9C9854E849F', X'A6639EF1331BB743BA02251D99C6E40A', X'A6639EF1331BB743BA02251D99C6E40A', 3, NULL, NULL, 2, NULL, 0, 'Add the softened bread to the pureed nut mixture and puree again until smooth and even. Add additional milk, a little bit at a time, as needed to adjust the consistency. It should be about the same thickness as a basil pesto sauce if using on pasta, a bit thinner if using as a spread.');
INSERT INTO `VWR INTERNAL RecipesbyCourse3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FBFD53720E81B64581D4371433EE45FB', X'A6639EF1331BB743BA02251D99C6E40A', X'A6639EF1331BB743BA02251D99C6E40A', 4, NULL, NULL, 2, NULL, 0, 'Enjoy.');
INSERT INTO `VWR INTERNAL RecipesbyCourse3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6640C7CB395C624C8B6C074CC48E8306', X'886CE37255134B499DA982CF6A09DB60', X'886CE37255134B499DA982CF6A09DB60', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL RecipesbyCourse3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E7BF73EC511B26459C41E6F27BE3EC34', X'886CE37255134B499DA982CF6A09DB60', X'886CE37255134B499DA982CF6A09DB60', 1, NULL, NULL, 2, NULL, 0, 'Bring a large, covered pot of water to a rolling boil over high heat, salt it, and add the penne, gnocchi, or other short pasta of choice.');
INSERT INTO `VWR INTERNAL RecipesbyCourse3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'268C07390077094E9E8F11C715D9E2BC', X'886CE37255134B499DA982CF6A09DB60', X'886CE37255134B499DA982CF6A09DB60', 2, NULL, NULL, 2, NULL, 0, 'Meanwhile, heat the tomato sauce and garlic in a large saucepan.');
INSERT INTO `VWR INTERNAL RecipesbyCourse3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'40953A8D9B5FF74EAA9576B41CFF2D60', X'886CE37255134B499DA982CF6A09DB60', X'886CE37255134B499DA982CF6A09DB60', 3, NULL, NULL, 2, NULL, 0, 'When the tomato sauce reaches a simmer, lower the heat to low, stir in the cream, and heat through, being careful not to let the sauce burn.');
INSERT INTO `VWR INTERNAL RecipesbyCourse3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'41E6EF7F600C884AAE6D27F6179A19AA', X'886CE37255134B499DA982CF6A09DB60', X'886CE37255134B499DA982CF6A09DB60', 4, NULL, NULL, 2, NULL, 0, 'Season the sauce to taste with salt and pepper and remove from the heat.');
INSERT INTO `VWR INTERNAL RecipesbyCourse3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'18A21D727429D84BA716A713F1CD91B7', X'886CE37255134B499DA982CF6A09DB60', X'886CE37255134B499DA982CF6A09DB60', 5, NULL, NULL, 2, NULL, 0, 'When the pasta or gnocchi are ready, drain it and transfer it to the saucepan.');
INSERT INTO `VWR INTERNAL RecipesbyCourse3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D77D9BCC1675F541B1D96430C2C1B99C', X'886CE37255134B499DA982CF6A09DB60', X'886CE37255134B499DA982CF6A09DB60', 6, NULL, NULL, 2, NULL, 0, 'Stir to coat it in the sauce and add the freshly grated Parmigiano-Reggiano.');
INSERT INTO `VWR INTERNAL RecipesbyCourse3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D678B02EE7D3D941BDA9790D653D9C27', X'886CE37255134B499DA982CF6A09DB60', X'886CE37255134B499DA982CF6A09DB60', 7, NULL, NULL, 2, NULL, 0, 'Toss to coat pasta evenly.');
INSERT INTO `VWR INTERNAL RecipesbyCourse3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3FD19B27971CA24681E9F45011B153BB', X'886CE37255134B499DA982CF6A09DB60', X'886CE37255134B499DA982CF6A09DB60', 8, NULL, NULL, 2, NULL, 0, 'Garnish with parsley, more freshly grated cheese if desired, and serve immediately.');
INSERT INTO `VWR INTERNAL RecipesbyCourse3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4B89EC6821A595458C8798DB61E1EE3F', X'C394A26109F91044A8DB13168CC65926', X'C394A26109F91044A8DB13168CC65926', 0, NULL, NULL, 2, NULL, 0, 'Bring the milk to a boil, and gradually stir in the semolina, constantly stirring to prevent lumps and keep the mixture from sticking to the pot. The mixture will become quite thick; continue cooking and stirring for about 20 minutes, and remove the pot from the fire.');
INSERT INTO `VWR INTERNAL RecipesbyCourse3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9341CCA527BE374786543402B420A9D7', X'C394A26109F91044A8DB13168CC65926', X'C394A26109F91044A8DB13168CC65926', 1, NULL, NULL, 2, NULL, 0, 'Beat the yolks with a little more milk, and add them to the semolina, together with the cheese, solid butter, and a pinch of salt.');
INSERT INTO `VWR INTERNAL RecipesbyCourse3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'10D732B8D16F734284FA18CF7CE3B960', X'C394A26109F91044A8DB13168CC65926', X'C394A26109F91044A8DB13168CC65926', 2, NULL, NULL, 2, NULL, 0, 'Mix well and spread the mixture a little less than a half an inch thick (1 cm) on your work surface.');
INSERT INTO `VWR INTERNAL RecipesbyCourse3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'295F1A971E24244FA612CA1A02B21FEE', X'C394A26109F91044A8DB13168CC65926', X'C394A26109F91044A8DB13168CC65926', 3, NULL, NULL, 2, NULL, 0, 'Let the semolina cool for 2 hours, and cut it into squares or diamonds. Butter a pan and layer the squares in it, spreading a little more grated cheese between the layers (there should be 3-4). When all is used up, sprinkle the melted butter over the gnocchi, slowly, to allow it to penetrate.');
INSERT INTO `VWR INTERNAL RecipesbyCourse3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'14EC33BC5C65AE40BB73D67320EFB981', X'C394A26109F91044A8DB13168CC65926', X'C394A26109F91044A8DB13168CC65926', 4, NULL, NULL, 2, NULL, 0, 'Bake the gnocchi for 15 minutes in a hot (400 F or 200 C) oven, until golden, and serve at once.');
INSERT INTO `VWR INTERNAL RecipesbyCourse3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C8C96960B12CF742B2A98FD8328A8F6A', X'724DEDCBB0D8334AA2827C6CF338CD4C', X'724DEDCBB0D8334AA2827C6CF338CD4C', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL RecipesbyCourse3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2C763F2BABC6654BB7ED59FB8C6B7395', X'724DEDCBB0D8334AA2827C6CF338CD4C', X'724DEDCBB0D8334AA2827C6CF338CD4C', 1, NULL, NULL, 2, NULL, 0, 'Preheat an oven to 375 F and bring a large pot of water to a boil.');
INSERT INTO `VWR INTERNAL RecipesbyCourse3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'789821E556661443837685CAE8C855C0', X'724DEDCBB0D8334AA2827C6CF338CD4C', X'724DEDCBB0D8334AA2827C6CF338CD4C', 2, NULL, NULL, 2, NULL, 0, 'Trim off and discard the ends of the squash and cut it in half lengthwise. Scoop out and discard the seeds and stringy bits from the middle. Cut off and discard the peel. Chop the trimmed and peeled squash into bite-sized pieces. Spread the pieces on a baking sheet, drizzle with the oil, and toss to coat the squash pieces. Sprinkle with the salt and roast until tender and starting to brown, about 30 minutes. Set aside, but leave the oven on.');
INSERT INTO `VWR INTERNAL RecipesbyCourse3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'44A0C8A6592F054A91DCB58ADC43B4E1', X'724DEDCBB0D8334AA2827C6CF338CD4C', X'724DEDCBB0D8334AA2827C6CF338CD4C', 3, NULL, NULL, 2, NULL, 0, 'Meanwhile, when the water comes to a boil, add enough salt to give the water flavor. Boil the pasta until it is still a tad underdone in the center, drain, and set aside.');
INSERT INTO `VWR INTERNAL RecipesbyCourse3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EB86D7A638A0A94289A5E32F538BA11B', X'724DEDCBB0D8334AA2827C6CF338CD4C', X'724DEDCBB0D8334AA2827C6CF338CD4C', 4, NULL, NULL, 2, NULL, 0, 'In a pot big enough to fit all of the pasta, melt the butter over medium-high heat. Whisk in the flour and cook—continuing to whisk—until the mixture smells like a pie crust (that smell means the flour is cooked), about 3 minutes. Whisk in the milk and cook, stirring with a wooden spoon until smooth and thickened like a mac and cheese sauce. The sauce should coat the back of the spoon and, when you drag your finger through that coating, it should leave a distinct path. That texture means the sauce is properly thickened.');
INSERT INTO `VWR INTERNAL RecipesbyCourse3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'53C0AAD03CBD864985D4FC61BE031F21', X'724DEDCBB0D8334AA2827C6CF338CD4C', X'724DEDCBB0D8334AA2827C6CF338CD4C', 5, NULL, NULL, 2, NULL, 0, 'Remove the pot from the heat, stir in 3/4 of the mozzarella and 1/2 of the Parmesan, as well as the nutmeg and cayenne, if using. Stir until the cheese is fully melted. Once the cheese is melted, add the pasta and roasted squash until everything is coated and combined. Taste and add salt or more spice, if needed.');
INSERT INTO `VWR INTERNAL RecipesbyCourse3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FBCF0089878CC64596B27EF353C99815', X'724DEDCBB0D8334AA2827C6CF338CD4C', X'724DEDCBB0D8334AA2827C6CF338CD4C', 6, NULL, NULL, 2, NULL, 0, 'In a small bowl, combine the remaining cheese and the breadcrumbs. Set aside.');
INSERT INTO `VWR INTERNAL RecipesbyCourse3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7FBD0CA95BB4CC40AB25773594BB9618', X'724DEDCBB0D8334AA2827C6CF338CD4C', X'724DEDCBB0D8334AA2827C6CF338CD4C', 7, NULL, NULL, 2, NULL, 0, 'Thoroughly butter, grease, or oil spray a 2-quart baking dish. Add the pasta-squash mixture. Sprinkle the cheese-breadcrumb mixture on top.');
INSERT INTO `VWR INTERNAL RecipesbyCourse3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4825EFBE6657E14E9DBE7DFF5E092070', X'724DEDCBB0D8334AA2827C6CF338CD4C', X'724DEDCBB0D8334AA2827C6CF338CD4C', 8, NULL, NULL, 2, NULL, 0, 'Bake the dish until hot, bubbling into the center of the dish (not just along the edges), and well-browned on top, about 40 minutes.');
INSERT INTO `VWR INTERNAL RecipesbyCourse3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D7C6CB20EC9DFA4CBB0BFFF1EE2B834B', X'724DEDCBB0D8334AA2827C6CF338CD4C', X'724DEDCBB0D8334AA2827C6CF338CD4C', 9, NULL, NULL, 2, NULL, 0, 'Serve hot and enjoy!');
INSERT INTO `VWR INTERNAL RecipesbyCourse3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'44D0B5E959BF1B429765A0C7BF5F0C5F', X'5706A5B8405F3F4CAD559161CA7569AB', X'5706A5B8405F3F4CAD559161CA7569AB', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL RecipesbyCourse3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'416538999FB01A4DA2E7D9E0F618087A', X'5706A5B8405F3F4CAD559161CA7569AB', X'5706A5B8405F3F4CAD559161CA7569AB', 1, NULL, NULL, 2, NULL, 0, 'Heat the oven to 350 F.');
INSERT INTO `VWR INTERNAL RecipesbyCourse3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DE884C349DBE09419AF7FC0B1F3BC845', X'5706A5B8405F3F4CAD559161CA7569AB', X'5706A5B8405F3F4CAD559161CA7569AB', 2, NULL, NULL, 2, NULL, 0, 'Lightly butter a 2-quart baking dish or spray the dish with nonstick cooking spray.');
INSERT INTO `VWR INTERNAL RecipesbyCourse3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4A2125E629B55B45983F541FA990A376', X'5706A5B8405F3F4CAD559161CA7569AB', X'5706A5B8405F3F4CAD559161CA7569AB', 3, NULL, NULL, 2, NULL, 0, 'Cook the macaroni in boiling salted water following the package directions; drain in a colander and rinse with hot water. Set aside while you prepare the sauce.');
INSERT INTO `VWR INTERNAL RecipesbyCourse3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'898951141E07D04684918EE85B87D7AC', X'5706A5B8405F3F4CAD559161CA7569AB', X'5706A5B8405F3F4CAD559161CA7569AB', 4, NULL, NULL, 2, NULL, 0, 'Meanwhile, in a large saucepan, combine the milk and flour; whisk to blend thoroughly. Stir in the salt, onion powder, and pepper. Cook, stirring, over medium heat until thickened and bubbly. Stir in the chopped ham, green onions if using, and 2 cups of the shredded cheese. Cook until the cheese has melted; remove from the heat.');
INSERT INTO `VWR INTERNAL RecipesbyCourse3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B55567BD8433BB48A9863E86092D6A48', X'5706A5B8405F3F4CAD559161CA7569AB', X'5706A5B8405F3F4CAD559161CA7569AB', 5, NULL, NULL, 2, NULL, 0, 'Add the drained macaroni to the sauce; stir until well blended.');
INSERT INTO `VWR INTERNAL RecipesbyCourse3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F09D96285499B3408489075C3DDBF3B9', X'5706A5B8405F3F4CAD559161CA7569AB', X'5706A5B8405F3F4CAD559161CA7569AB', 6, NULL, NULL, 2, NULL, 0, 'In a small bowl, combine the breadcrumbs with the 2 tablespoons of melted butter; stir until all of the breadcrumbs are thoroughly moistened.');
INSERT INTO `VWR INTERNAL RecipesbyCourse3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'072E004F7EC6F04BB2E80FA9CB938A03', X'5706A5B8405F3F4CAD559161CA7569AB', X'5706A5B8405F3F4CAD559161CA7569AB', 7, NULL, NULL, 2, NULL, 0, 'Spoon the macaroni mixture into the prepared baking dish. Sprinkle with the remaining 1 cup of shredded cheese and then sprinkle the buttered breadcrumbs over the cheese layer.');
INSERT INTO `VWR INTERNAL RecipesbyCourse3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B90115FABACC7B46AEED8BDC9B31FE32', X'5706A5B8405F3F4CAD559161CA7569AB', X'5706A5B8405F3F4CAD559161CA7569AB', 8, NULL, NULL, 2, NULL, 0, 'Bake for 25 to 30 minutes or the topping is browned and the casserole is hot and bubbling around the edges.');
INSERT INTO `VWR INTERNAL RecipesbyCourse3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2B76A5B76705064BA891D682858FC1D3', X'9E4166FF86B0384FB39885E01377EA29', X'9E4166FF86B0384FB39885E01377EA29', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 350 F.');
INSERT INTO `VWR INTERNAL RecipesbyCourse3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EB8573BE63507A448592CCFAEB9A8F74', X'9E4166FF86B0384FB39885E01377EA29', X'9E4166FF86B0384FB39885E01377EA29', 1, NULL, NULL, 2, NULL, 0, 'Bring a pot of water to a boil. Add macaroni noodles and boil for 5 minutes. Drain.');
INSERT INTO `VWR INTERNAL RecipesbyCourse3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1D848B21CFB34F4AB6EFFEED58450908', X'9E4166FF86B0384FB39885E01377EA29', X'9E4166FF86B0384FB39885E01377EA29', 2, NULL, NULL, 2, NULL, 0, 'In a 2-quart baking pan, mix together grated cheddar cheese and noodles.');
INSERT INTO `VWR INTERNAL RecipesbyCourse3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A52A67787A1C5343956F274754E20869', X'9E4166FF86B0384FB39885E01377EA29', X'9E4166FF86B0384FB39885E01377EA29', 3, NULL, NULL, 2, NULL, 0, 'In a deep skillet or pot over medium heat, melt the butter. Whisk in the flour so paste forms, then slowly start whisking in the milk. Whisk constantly until the flour is smoothly blended in. Bring the milk to a boil, then simmer rapidly for 1 minute to thicken.');
INSERT INTO `VWR INTERNAL RecipesbyCourse3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CAF8E64CF9716C4E8BC1984DA2EAF45B', X'9E4166FF86B0384FB39885E01377EA29', X'9E4166FF86B0384FB39885E01377EA29', 4, NULL, NULL, 2, NULL, 0, 'Pour the thickened milk into the noodles. Mix well. If adding a topping of cheese, breadcrumbs, etc to the top of the macaroni and cheese, sprinkle it on top of the noodles.');
INSERT INTO `VWR INTERNAL RecipesbyCourse3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'86160EC97EDE994F8DF9788154B96D63', X'9E4166FF86B0384FB39885E01377EA29', X'9E4166FF86B0384FB39885E01377EA29', 5, NULL, NULL, 2, NULL, 0, 'Bake, uncovered, for 30 minutes. Let the mac and cheese cool for at least ten minutes before serving.');
INSERT INTO `VWR INTERNAL RecipesbyCourse3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'ABE8E138C9326A43A4453765B7CC9A04', X'FC9D657A628070428E417DA2A96B4777', X'FC9D657A628070428E417DA2A96B4777', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL RecipesbyCourse3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C6E8643FB94E274F8F0ED8D2E4B0FFCC', X'FC9D657A628070428E417DA2A96B4777', X'FC9D657A628070428E417DA2A96B4777', 1, NULL, NULL, 2, NULL, 0, 'Grease a 9- x 13-inch casserole or spray it with cooking oil spray. Preheat the oven to 350 F.');
INSERT INTO `VWR INTERNAL RecipesbyCourse3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'260A94B7307F8943A7576DA74025A0AB', X'FC9D657A628070428E417DA2A96B4777', X'FC9D657A628070428E417DA2A96B4777', 2, NULL, NULL, 2, NULL, 0, 'Make the topping. In a small bowl, combine the panko breadcrumbs with the chopped parsley, blue cheese (or Parmesan), and melted butter. Toss to blend the ingredients and set aside.');
INSERT INTO `VWR INTERNAL RecipesbyCourse3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4B2FA5822239324F9DE6A867C7BA2E48', X'FC9D657A628070428E417DA2A96B4777', X'FC9D657A628070428E417DA2A96B4777', 3, NULL, NULL, 2, NULL, 0, 'Place a large saucepan of water over high heat and bring to a bowl. Add the salt and pasta. Reduce the heat to maintain a boil and cook the macaroni following package directions for al dente. Drain the pasta and then transfer it to a large bowl; set aside.');
INSERT INTO `VWR INTERNAL RecipesbyCourse3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BAE7E8C6905F104792DB7F6D3C506FC7', X'FC9D657A628070428E417DA2A96B4777', X'FC9D657A628070428E417DA2A96B4777', 4, NULL, NULL, 2, NULL, 0, 'Prepare the chicken. Melt the butter in a skillet over medium heat. Add the onion and celery; cook, stirring, for 5 minutes, or until the celery is softened and the onion is translucent. Add the garlic and cook for 1 minute longer.');
INSERT INTO `VWR INTERNAL RecipesbyCourse3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F6085315CEE51049BF84EA94D4340BEA', X'FC9D657A628070428E417DA2A96B4777', X'FC9D657A628070428E417DA2A96B4777', 5, NULL, NULL, 2, NULL, 0, 'Add the hot sauce and the shredded chicken to the onion and celery mixture and simmer for about 1 minute longer, or until hot. Transfer to the bowl with the macaroni and set aside.');
INSERT INTO `VWR INTERNAL RecipesbyCourse3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3F76A9E47EC9624E951A9CEC774F0C3C', X'FC9D657A628070428E417DA2A96B4777', X'FC9D657A628070428E417DA2A96B4777', 6, NULL, NULL, 2, NULL, 0, 'Make the cheese sauce. Melt the remaining butter in a saucepan over medium heat (or use the same pan used to cook the macaroni). Add the flour and mustard powder and cook, stirring, for 2 minutes.');
INSERT INTO `VWR INTERNAL RecipesbyCourse3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'36EF669CBD39BB48B93124A3C3F706B0', X'FC9D657A628070428E417DA2A96B4777', X'FC9D657A628070428E417DA2A96B4777', 7, NULL, NULL, 2, NULL, 0, 'Gradually whisk the half-and-half and milk into the flour and butter mixture along with the remaining hot sauce. Cook, stirring, until the sauce is thickened. Add the shredded cheddar and pepper jack cheeses to the sauce along with the sour cream. Stir until the cheese has melted and the mixture is smooth. Taste the sauce and add kosher salt and freshly ground black pepper, to taste.');
INSERT INTO `VWR INTERNAL RecipesbyCourse3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4508EBF7346F6D49BA8B51AA7B287839', X'FC9D657A628070428E417DA2A96B4777', X'FC9D657A628070428E417DA2A96B4777', 8, NULL, NULL, 2, NULL, 0, 'Add the cheese sauce to the bowl with the macaroni and chicken mixture and stir until the ingredients are well combined.');
INSERT INTO `VWR INTERNAL RecipesbyCourse3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2534AEBD57C0264D95B5E851FAD72A22', X'FC9D657A628070428E417DA2A96B4777', X'FC9D657A628070428E417DA2A96B4777', 9, NULL, NULL, 2, NULL, 0, 'Turn the macaroni mixture into the prepared casserole.');
INSERT INTO `VWR INTERNAL RecipesbyCourse3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'585FF10822679744A5B79BDA828B7F09', X'FC9D657A628070428E417DA2A96B4777', X'FC9D657A628070428E417DA2A96B4777', 10, NULL, NULL, 2, NULL, 0, 'Bake, uncovered, for about 25 minutes, or until the breadcrumb topping is browned and the casserole is bubbling around the edges.');
INSERT INTO `VWR INTERNAL RecipesbyCourse3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FA62A04D77A275469EE71F3B4F620BBF', X'FC9D657A628070428E417DA2A96B4777', X'FC9D657A628070428E417DA2A96B4777', 11, NULL, NULL, 2, NULL, 0, 'Serve Buffalo macaroni and cheese with carrot sticks and a garnish of parsley or a drizzle of hot sauce.');
INSERT INTO `VWR INTERNAL RecipesbyCourse3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2FDF8960A98C8340B84894AFC2D29CD3', X'8F8DA0D24B6E06499FA74108FBE5FA85', X'8F8DA0D24B6E06499FA74108FBE5FA85', 0, NULL, NULL, 2, NULL, 0, 'Heat the oven to 350 F (180 C/Gas 4).');
INSERT INTO `VWR INTERNAL RecipesbyCourse3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'14103EE0014C4445AFCD63DBF9F60AF2', X'8F8DA0D24B6E06499FA74108FBE5FA85', X'8F8DA0D24B6E06499FA74108FBE5FA85', 1, NULL, NULL, 2, NULL, 0, 'Butter a 2 1/2-quart baking dish.');
INSERT INTO `VWR INTERNAL RecipesbyCourse3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'959BBAE93AECB64FAE0F07E804F36F55', X'8F8DA0D24B6E06499FA74108FBE5FA85', X'8F8DA0D24B6E06499FA74108FBE5FA85', 2, NULL, NULL, 2, NULL, 0, 'Cook macaroni in boiling salted water following package directions for al dente; drain and transfer to a large bowl.');
INSERT INTO `VWR INTERNAL RecipesbyCourse3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5197BBBCA40F2041B2F114478EDFF579', X'8F8DA0D24B6E06499FA74108FBE5FA85', X'8F8DA0D24B6E06499FA74108FBE5FA85', 3, NULL, NULL, 2, NULL, 0, 'To the macaroni add the condensed cream of mushroom soup, milk, tuna, black pepper, seasoned salt, and 2 cups of the shredded cheese. Mix well and transfer to the prepared baking dish.');
INSERT INTO `VWR INTERNAL RecipesbyCourse3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6FF4DDF304194D45AAC46A54A6C0C3B9', X'8F8DA0D24B6E06499FA74108FBE5FA85', X'8F8DA0D24B6E06499FA74108FBE5FA85', 4, NULL, NULL, 2, NULL, 0, 'Mix the crushed potato chips with the remaining cheese; sprinkle over macaroni mixture.');
INSERT INTO `VWR INTERNAL RecipesbyCourse3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B7A5BE0B013E3A499799D2D69D37F522', X'8F8DA0D24B6E06499FA74108FBE5FA85', X'8F8DA0D24B6E06499FA74108FBE5FA85', 5, NULL, NULL, 2, NULL, 0, 'Bake in the preheated oven for 35 to 45 minutes.');
INSERT INTO `VWR INTERNAL RecipesbyCourse3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BAEEB8E058DA4447B822E99300139A27', X'52D80A160DE03643861646757151D41C', X'52D80A160DE03643861646757151D41C', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL RecipesbyCourse3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'899FF4C28C4AC64F8DA38569DF21C7AE', X'52D80A160DE03643861646757151D41C', X'52D80A160DE03643861646757151D41C', 1, NULL, NULL, 2, NULL, 0, 'Bring a medium pot of salted water to a boil. Add pasta, and cook until al dente, 8 to 9 minutes.');
INSERT INTO `VWR INTERNAL RecipesbyCourse3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'98D88058918DF3449DFE104DF951CB5F', X'52D80A160DE03643861646757151D41C', X'52D80A160DE03643861646757151D41C', 2, NULL, NULL, 2, NULL, 0, 'Reserve 1 cup of the pasta water.');
INSERT INTO `VWR INTERNAL RecipesbyCourse3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6D350BD59A3595459A37C16C8B8CB890', X'52D80A160DE03643861646757151D41C', X'52D80A160DE03643861646757151D41C', 3, NULL, NULL, 2, NULL, 0, 'Then drain, and transfer pasta to a deep bowl; reserve pot.');
INSERT INTO `VWR INTERNAL RecipesbyCourse3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'500AA3EDBC2A5749A8D7562108E94B1F', X'52D80A160DE03643861646757151D41C', X'52D80A160DE03643861646757151D41C', 4, NULL, NULL, 2, NULL, 0, 'Meanwhile, finely chop half of the cilantro. Pick remaining cilantro leaves from stems; discard stems.');
INSERT INTO `VWR INTERNAL RecipesbyCourse3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CD6353238CC07447A0E162F17181232C', X'52D80A160DE03643861646757151D41C', X'52D80A160DE03643861646757151D41C', 5, NULL, NULL, 2, NULL, 0, 'Coarsely shred the cheddar cheese.');
INSERT INTO `VWR INTERNAL RecipesbyCourse3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EE0C3578DC637F45B4ADB71A2A8D0634', X'52D80A160DE03643861646757151D41C', X'52D80A160DE03643861646757151D41C', 6, NULL, NULL, 2, NULL, 0, 'In a small bowl, combine the avocado and lemon juice, and mash with the back of a fork or potato masher until smooth and creamy.');
INSERT INTO `VWR INTERNAL RecipesbyCourse3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'812562A717D8AB4F8822EF6A86156648', X'52D80A160DE03643861646757151D41C', X'52D80A160DE03643861646757151D41C', 7, NULL, NULL, 2, NULL, 0, 'Combine the cream cheese, flour and pasta water in the reserved pot over medium heat. Whisk until blended and smooth, 1 to 2 minutes.');
INSERT INTO `VWR INTERNAL RecipesbyCourse3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6CB04F09FC55B048AC5247E24AA6EE99', X'52D80A160DE03643861646757151D41C', X'52D80A160DE03643861646757151D41C', 8, NULL, NULL, 2, NULL, 0, 'Add the cheese, then stir with a wooden spoon or spatula until cheese melts and sauce thickens, about 1 minute. Season with salt and pepper to taste.');
INSERT INTO `VWR INTERNAL RecipesbyCourse3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D649E0FA29AC3845B22329F74597B831', X'52D80A160DE03643861646757151D41C', X'52D80A160DE03643861646757151D41C', 9, NULL, NULL, 2, NULL, 0, 'Pour cheese sauce over pasta, and stir until well coated.');
INSERT INTO `VWR INTERNAL RecipesbyCourse3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9A668A9BF4A49A45B7FB93C7383F3FFD', X'52D80A160DE03643861646757151D41C', X'52D80A160DE03643861646757151D41C', 10, NULL, NULL, 2, NULL, 0, 'Stir in the avocado puree and chopped cilantro.');
INSERT INTO `VWR INTERNAL RecipesbyCourse3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F1234B331EFF9048802166B679941375', X'52D80A160DE03643861646757151D41C', X'52D80A160DE03643861646757151D41C', 11, NULL, NULL, 2, NULL, 0, 'Garnish with remaining cilantro leaves. Serve immediately.');
INSERT INTO `VWR INTERNAL RecipesbyCourse3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D1BC664E09A66B40B5D00F983F6B2BD1', X'9091EFCBE52342459FEBAE7B8C42C5E1', X'9091EFCBE52342459FEBAE7B8C42C5E1', 0, NULL, NULL, 2, NULL, 0, 'Heat the oven to 350 F (180 C/Gas 4). Butter a 2 1/2- to 3-quart baking dish.');
INSERT INTO `VWR INTERNAL RecipesbyCourse3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C95ECDEB1B9D114CBA4D1CCD62FE6A80', X'9091EFCBE52342459FEBAE7B8C42C5E1', X'9091EFCBE52342459FEBAE7B8C42C5E1', 1, NULL, NULL, 2, NULL, 0, 'Bring a large pot of about 4 quarts of water to a boil over high heat. Add about 1 tablespoon of salt to the water. Add the macaroni, reduce the heat to medium, and continue boiling until al dente** following package directions. Drain the macaroni well.');
INSERT INTO `VWR INTERNAL RecipesbyCourse3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'996FFC6D1D14DD419F15EC3DA6735EDF', X'9091EFCBE52342459FEBAE7B8C42C5E1', X'9091EFCBE52342459FEBAE7B8C42C5E1', 2, NULL, NULL, 2, NULL, 0, 'In a bowl combine the mayonnaise, cream cheese, shredded cheese, drained pimientos, and grated onion. Mix the ingredients together until thoroughly blended. Taste and add salt and freshly ground black pepper, as needed. Add the hot drained macaroni and mix until well blended.');
INSERT INTO `VWR INTERNAL RecipesbyCourse3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A1954A5DF541EB47AE970147F6DCF8F5', X'9091EFCBE52342459FEBAE7B8C42C5E1', X'9091EFCBE52342459FEBAE7B8C42C5E1', 3, NULL, NULL, 2, NULL, 0, 'Spoon the macaroni and pimiento cheese mixture into the prepared baking dish.');
INSERT INTO `VWR INTERNAL RecipesbyCourse3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'12FC465599833C4EB6629D25FD10CA68', X'9091EFCBE52342459FEBAE7B8C42C5E1', X'9091EFCBE52342459FEBAE7B8C42C5E1', 4, NULL, NULL, 2, NULL, 0, 'Sprinkle the French fried onions over the casserole.');
INSERT INTO `VWR INTERNAL RecipesbyCourse3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C7C9F7D776FEF34FB223E6E326C2D43E', X'9091EFCBE52342459FEBAE7B8C42C5E1', X'9091EFCBE52342459FEBAE7B8C42C5E1', 5, NULL, NULL, 2, NULL, 0, 'Bake for about 20 to 25 minutes, or until the macaroni and cheese mixture is hot and bubbling around the edges and the topping is lightly browned and crispy.');
INSERT INTO `VWR INTERNAL RecipesbyCourse3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3CB48688B0BF9245B4010ECDE4CDEE97', X'9091EFCBE52342459FEBAE7B8C42C5E1', X'9091EFCBE52342459FEBAE7B8C42C5E1', 6, NULL, NULL, 2, NULL, 0, 'Serve with a simple tossed green salad or fresh sliced tomatoes, or serve it along with a holiday meal or Sunday dinner.');
INSERT INTO `VWR INTERNAL RecipesbyCourse3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'91D2A6E672F4204A9427D667F76C617D', X'3E0585AEC0112F48ACB3BA90EB5AE261', X'3E0585AEC0112F48ACB3BA90EB5AE261', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL RecipesbyCourse3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'22012D3E6AF38D449F48F80945A706C2', X'3E0585AEC0112F48ACB3BA90EB5AE261', X'3E0585AEC0112F48ACB3BA90EB5AE261', 1, NULL, NULL, 2, NULL, 0, 'Cook the pasta in boiling salted water following the package directions.');
INSERT INTO `VWR INTERNAL RecipesbyCourse3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F4B25DEE45ECBB4DAD8EE2A89EAAA7D0', X'3E0585AEC0112F48ACB3BA90EB5AE261', X'3E0585AEC0112F48ACB3BA90EB5AE261', 2, NULL, NULL, 2, NULL, 0, 'Drain in a colander and rinse under hot water. Set aside.');
INSERT INTO `VWR INTERNAL RecipesbyCourse3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A1ADD287B71FF64C8A66392D57CCE3B4', X'3E0585AEC0112F48ACB3BA90EB5AE261', X'3E0585AEC0112F48ACB3BA90EB5AE261', 3, NULL, NULL, 2, NULL, 0, 'In a large deep skillet or saucepan, heat the vegetable oil and butter over medium heat.');
INSERT INTO `VWR INTERNAL RecipesbyCourse3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B1909AF57463DA46B6A2AEAB05A92FFB', X'3E0585AEC0112F48ACB3BA90EB5AE261', X'3E0585AEC0112F48ACB3BA90EB5AE261', 4, NULL, NULL, 2, NULL, 0, 'Add the onion and bell pepper, if using. Cook, stirring until onion is softened and edges are lightly browned.');
INSERT INTO `VWR INTERNAL RecipesbyCourse3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'ADFB40B6D7032E43B33D8D703AF536BD', X'3E0585AEC0112F48ACB3BA90EB5AE261', X'3E0585AEC0112F48ACB3BA90EB5AE261', 5, NULL, NULL, 2, NULL, 0, 'Add the ground beef and cook, stirring and breaking up, until the beef is cooked through and no longer pink.');
INSERT INTO `VWR INTERNAL RecipesbyCourse3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CB8767B1E2E56B46A3148E4202209D51', X'3E0585AEC0112F48ACB3BA90EB5AE261', X'3E0585AEC0112F48ACB3BA90EB5AE261', 6, NULL, NULL, 2, NULL, 0, 'Add the garlic and garlic powder and cook, stirring for 1 to 2 minutes longer.');
INSERT INTO `VWR INTERNAL RecipesbyCourse3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9C1640372F4BD140B7128A06610203ED', X'3E0585AEC0112F48ACB3BA90EB5AE261', X'3E0585AEC0112F48ACB3BA90EB5AE261', 7, NULL, NULL, 2, NULL, 0, 'Stir in the crushed tomatoes and diced tomatoes, the tomato sauce and paste, sugar, and pepper. Cook, stirring, until the sauce mixture begins to boil. Taste and add salt, as needed. Simmer for 5 to 10 minutes.');
INSERT INTO `VWR INTERNAL RecipesbyCourse3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'19E46EDD11E3E64EB91E2B6197F793B0', X'3E0585AEC0112F48ACB3BA90EB5AE261', X'3E0585AEC0112F48ACB3BA90EB5AE261', 8, NULL, NULL, 2, NULL, 0, 'Add the drained macaroni to the sauce, mix well, and heat through. Serve and enjoy!');
INSERT INTO `VWR INTERNAL RecipesbyCourse3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9DDF7691FD42DD46AEC97886516434DA', X'71DE3B2EC6831C4A86CEDEF8FA3A30A3', X'71DE3B2EC6831C4A86CEDEF8FA3A30A3', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL RecipesbyCourse3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5AAC19744BD373498F181E7892F59389', X'71DE3B2EC6831C4A86CEDEF8FA3A30A3', X'71DE3B2EC6831C4A86CEDEF8FA3A30A3', 1, NULL, NULL, 2, NULL, 0, 'Bring a pot of salted water to a rolling boil and cook the gluten-free pasta according to the manufacturer''s instructions until al dente. Drain and set aside.');
INSERT INTO `VWR INTERNAL RecipesbyCourse3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5509920DED87FB40A607B877ED7B29FF', X'71DE3B2EC6831C4A86CEDEF8FA3A30A3', X'71DE3B2EC6831C4A86CEDEF8FA3A30A3', 2, NULL, NULL, 2, NULL, 0, 'Prepare the sauce. In a small bowl or cup, combine the nutritional yeast, sea salt, onion powder, and garlic powder. Set aside. In a medium-sized saucepan, heat the olive oil or canola oil over medium-low heat. Whisk in the sweet rice flour and continue whisking constantly for 3-5 minutes or until the flour smells toasty but hasn''t browned. In a steady stream, whisk in the almond milk or coconut milk, stirring constantly. Add in the nutritional yeast mixture and the rice vinegar, and cook for three minutes more or until the sauce is slightly thickened. If using, add in the gluten-free vegan cheese shreds and mix until just incorporated.');
INSERT INTO `VWR INTERNAL RecipesbyCourse3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CC625C7CE1E7F64289ECE320AD2713DA', X'71DE3B2EC6831C4A86CEDEF8FA3A30A3', X'71DE3B2EC6831C4A86CEDEF8FA3A30A3', 3, NULL, NULL, 2, NULL, 0, 'Toss the pasta with the sauce and cook for just a minute or two more to reheat the pasta. Serve immediately, serving with freshly ground black pepper and salt to taste.');
INSERT INTO `VWR INTERNAL RecipesbyCourse3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3F549E00B08D2345987778182D5519DD', X'76E9952AEBEB714A9301DFD21C8A7E75', X'76E9952AEBEB714A9301DFD21C8A7E75', 0, NULL, NULL, 2, NULL, 0, 'Poke the potatoes all over with a fork, sticking each potato 8-10 times. Microwave them on high for about 10 minutes, or bake them in a 350F oven for about an hour, or until soft all the way through. Let cool for about 15 minutes.');
INSERT INTO `VWR INTERNAL RecipesbyCourse3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'08ED283F73235440A55FA082164CBA71', X'76E9952AEBEB714A9301DFD21C8A7E75', X'76E9952AEBEB714A9301DFD21C8A7E75', 1, NULL, NULL, 2, NULL, 0, 'Cut the potatoes in half lengthwise and scoop out the flesh with a spoon (use a dish towel to protect your hand if the potatoes are too hot). Discard the skins or reserve for another use, like cheesy potato skins.');
INSERT INTO `VWR INTERNAL RecipesbyCourse3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7D7F1527F628D0479AA05B3E13ABBD79', X'76E9952AEBEB714A9301DFD21C8A7E75', X'76E9952AEBEB714A9301DFD21C8A7E75', 2, NULL, NULL, 2, NULL, 0, 'Pass the scooped potato through a ricer (or mash with a masher or fork) then, using a one-cup measuring cup, measure two lightly packed cups of mashed potato. Reserve any remaining potato another use.');
INSERT INTO `VWR INTERNAL RecipesbyCourse3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E706563B14FCB849BBC1A62EC58605D8', X'76E9952AEBEB714A9301DFD21C8A7E75', X'76E9952AEBEB714A9301DFD21C8A7E75', 3, NULL, NULL, 2, NULL, 0, 'Transfer the two cups of mashed potato to a large bowl. Add the salt and egg yolks and combine, then add the flour and knead by hand until the yolk is completely distributed and the dough is the texture of play-doh. No need to knead for a long time; just incorporating the flour should get you there.');
INSERT INTO `VWR INTERNAL RecipesbyCourse3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E346A99868225645A2D3966AD123B7C9', X'76E9952AEBEB714A9301DFD21C8A7E75', X'76E9952AEBEB714A9301DFD21C8A7E75', 4, NULL, NULL, 2, NULL, 0, 'Divide the dough into four pieces. Roll one of the pieces into a long rope about 1/2-inch thick. Cut the rope into half-inch (gnocchi-sized) nuggets. You can gently roll each nugget between your hand and the tines of a fork to create `gnocchi ridges,` but you can skip this step if you’re short on time. Place the finished gnocchi on a flour-dusted sheet pan. Repeat with the remaining three portions of dough.');
INSERT INTO `VWR INTERNAL RecipesbyCourse3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6E39FA84D6F57245802B1E4A96726B5D', X'76E9952AEBEB714A9301DFD21C8A7E75', X'76E9952AEBEB714A9301DFD21C8A7E75', 5, NULL, NULL, 2, NULL, 0, 'Bring about 4 quarts of water to a rolling boil in a large pot. Add about 2 tablespoons of salt to the boiling water.');
INSERT INTO `VWR INTERNAL RecipesbyCourse3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1E4964F97F1271418151A2C3CF06BC6E', X'76E9952AEBEB714A9301DFD21C8A7E75', X'76E9952AEBEB714A9301DFD21C8A7E75', 6, NULL, NULL, 2, NULL, 0, 'While the water is boiling, prepare the sauce. Add the butter and milk to a large skillet over medium heat and whisk in the packet of cheese sauce. Keep whisking until all the lumps are gone. If the sauce seems a little thin, let it bubble and reduce for a minute or two, but don’t let it get too concentrated or it may be too salty.');
INSERT INTO `VWR INTERNAL RecipesbyCourse3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4A84774CEFE9FC4D9CC2AC900110DBE3', X'76E9952AEBEB714A9301DFD21C8A7E75', X'76E9952AEBEB714A9301DFD21C8A7E75', 7, NULL, NULL, 2, NULL, 0, 'Add the gnocchi to the boiling water and give them a quick stir to prevent clumping. When they begin to float, leave them boiling in the water for another two minutes.');
INSERT INTO `VWR INTERNAL RecipesbyCourse3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5687AA2049660C4085F8D81AF10DAE5D', X'76E9952AEBEB714A9301DFD21C8A7E75', X'76E9952AEBEB714A9301DFD21C8A7E75', 8, NULL, NULL, 2, NULL, 0, 'Remove the gnocchi with a slotted spoon, or drain them in a colander before adding them to the pan with cheese sauce. Stir to coat the gnocchi completely with orange (or white, if that’s your thing) goodness. Remove from heat, and serve immediately.');
INSERT INTO `VWR INTERNAL RecipesbyCourse3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A328B99EF9771040A78045615C05557E', X'92CBEE83DD8A214FA4CFF9341E3060D8', X'92CBEE83DD8A214FA4CFF9341E3060D8', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL RecipesbyCourse3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'139C95245146744B88AB19E159152C68', X'92CBEE83DD8A214FA4CFF9341E3060D8', X'92CBEE83DD8A214FA4CFF9341E3060D8', 1, NULL, NULL, 2, NULL, 0, 'Grease a 2-quart baking dish.');
INSERT INTO `VWR INTERNAL RecipesbyCourse3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3FBB2B439AE2394B8EE89366A57ABD26', X'92CBEE83DD8A214FA4CFF9341E3060D8', X'92CBEE83DD8A214FA4CFF9341E3060D8', 2, NULL, NULL, 2, NULL, 0, 'Heat oven to 350 F/180 C/Gas 4.');
INSERT INTO `VWR INTERNAL RecipesbyCourse3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'648D434E84B32C4E966968C4BC10E8AD', X'92CBEE83DD8A214FA4CFF9341E3060D8', X'92CBEE83DD8A214FA4CFF9341E3060D8', 3, NULL, NULL, 2, NULL, 0, 'Cook the pasta in boiling salted water following package directions. Drain and rinse; set aside.');
INSERT INTO `VWR INTERNAL RecipesbyCourse3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0C3B2B599F6B8F42A667D3E87AAFD28E', X'92CBEE83DD8A214FA4CFF9341E3060D8', X'92CBEE83DD8A214FA4CFF9341E3060D8', 4, NULL, NULL, 2, NULL, 0, 'In a medium saucepan over medium-low heat, melt 4 tablespoons of butter. Add the flour and cook for about 2 minutes, stirring constantly. ?');
INSERT INTO `VWR INTERNAL RecipesbyCourse3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'08E148F367735C4EA310551EE72AB532', X'92CBEE83DD8A214FA4CFF9341E3060D8', X'92CBEE83DD8A214FA4CFF9341E3060D8', 5, NULL, NULL, 2, NULL, 0, 'Gradually add the milk, or half-and-half, and Boursin cheese. Cook, stirring, until hot and thickened.');
INSERT INTO `VWR INTERNAL RecipesbyCourse3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'87B7CFE9321F8D44B51754939D6BB581', X'92CBEE83DD8A214FA4CFF9341E3060D8', X'92CBEE83DD8A214FA4CFF9341E3060D8', 6, NULL, NULL, 2, NULL, 0, 'Add the cheese and peppers. Continue cooking until the cheese has melted, stirring constantly. Add salt and pepper, to taste. If necessary, thin slightly with extra milk.');
INSERT INTO `VWR INTERNAL RecipesbyCourse3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F4C7827F8A8F484BA22D551EECA51324', X'92CBEE83DD8A214FA4CFF9341E3060D8', X'92CBEE83DD8A214FA4CFF9341E3060D8', 7, NULL, NULL, 2, NULL, 0, 'Combine the sauce with the pasta and stir to blend. Spoon into prepared baking dish.');
INSERT INTO `VWR INTERNAL RecipesbyCourse3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4741E5D402159B48B13BFBD2749F4DB2', X'92CBEE83DD8A214FA4CFF9341E3060D8', X'92CBEE83DD8A214FA4CFF9341E3060D8', 8, NULL, NULL, 2, NULL, 0, 'Melt the remaining 2 tablespoons of butter.');
INSERT INTO `VWR INTERNAL RecipesbyCourse3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CBCEB5BF1ACC9F4A8BA2EE0756E376D9', X'92CBEE83DD8A214FA4CFF9341E3060D8', X'92CBEE83DD8A214FA4CFF9341E3060D8', 9, NULL, NULL, 2, NULL, 0, 'Toss melted butter and bread crumbs together until the crumbs are thoroughly coated; sprinkle over the casserole.');
INSERT INTO `VWR INTERNAL RecipesbyCourse3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'57CF238454BB41418FFD2BEF61A46120', X'92CBEE83DD8A214FA4CFF9341E3060D8', X'92CBEE83DD8A214FA4CFF9341E3060D8', 10, NULL, NULL, 2, NULL, 0, 'Bake in the preheated oven for about 25 minutes, or until bubbly and lightly browned.');
INSERT INTO `VWR INTERNAL RecipesbyCourse3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A10450FFC57718419745518D148F5E10', X'92CBEE83DD8A214FA4CFF9341E3060D8', X'92CBEE83DD8A214FA4CFF9341E3060D8', 11, NULL, NULL, 2, NULL, 0, 'Scoop the mac and cheese onto serving plates or bowls and enjoy!');
INSERT INTO `VWR INTERNAL RecipesbyCourse3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E24709A8D33DA140A0D36F1A6E818FE8', X'CB1FA7DD4D15834794495BA5A1C534EF', X'CB1FA7DD4D15834794495BA5A1C534EF', 0, NULL, NULL, 2, NULL, 0, 'Boil the macaroni in abundant salted water just until al dente (7 to 12 minutes, depending on the type of pasta you are using).');
INSERT INTO `VWR INTERNAL RecipesbyCourse3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3B2E54F06C3B8449B2CB5533036ACE59', X'CB1FA7DD4D15834794495BA5A1C534EF', X'CB1FA7DD4D15834794495BA5A1C534EF', 1, NULL, NULL, 2, NULL, 0, 'Drain well, rinse in cold water, drain well again and set aside.');
INSERT INTO `VWR INTERNAL RecipesbyCourse3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CD1E1DE79302FE4DA31EF625917BB8E1', X'CB1FA7DD4D15834794495BA5A1C534EF', X'CB1FA7DD4D15834794495BA5A1C534EF', 2, NULL, NULL, 2, NULL, 0, 'Meanwhile, preheat the oven to 425 F / 220 C and make the besciamella (white sauce):');
INSERT INTO `VWR INTERNAL RecipesbyCourse3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A652570FC50EB041B7C900AC256D5256', X'CB1FA7DD4D15834794495BA5A1C534EF', X'CB1FA7DD4D15834794495BA5A1C534EF', 3, NULL, NULL, 2, NULL, 0, 'Melt the butter in a small saucepan over medium heat.');
INSERT INTO `VWR INTERNAL RecipesbyCourse3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E4831CAA2EF62640BD87942EB9724E13', X'CB1FA7DD4D15834794495BA5A1C534EF', X'CB1FA7DD4D15834794495BA5A1C534EF', 4, NULL, NULL, 2, NULL, 0, 'When it''s melted, whisk in the flour.');
INSERT INTO `VWR INTERNAL RecipesbyCourse3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3B8ECEEC9A4D94499A7A265D7250F1E9', X'CB1FA7DD4D15834794495BA5A1C534EF', X'CB1FA7DD4D15834794495BA5A1C534EF', 5, NULL, NULL, 2, NULL, 0, 'Turn the heat to low and cook, stirring constantly with a wooden spoon to prevent lumps and burning, until the roux (flour-and-butter mixture) is just slightly golden brown and has a nutty scent, 1 to 2 minutes.');
