INSERT INTO `VWR INTERNAL RecipesbyCourse3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3B61B148FC64B34B83DC1D90845F0433', X'CB1FA7DD4D15834794495BA5A1C534EF', X'CB1FA7DD4D15834794495BA5A1C534EF', 7, NULL, NULL, 2, NULL, 0, 'Continue cooking until the mixture starts to thicken, another 1 to 2 minutes.');
